INSERT INTO quranLatin VALUES (4, '1:4', 1, 4, 'm<u>aa</u>likiyawmiddiin\r\r\n');
INSERT INTO quranLatin VALUES (5, '1:5', 1, 5, 'iyy<u>aa</u>kana\'buduwa-iyy<u>aa</u>kanasta\'iin\r\r\n');
