INSERT INTO quranLatin VALUES (8, '2:1', 2, 1, 'alif-l<u>aa</u>m-miim </u>\r\r\n');
INSERT INTO quranLatin VALUES (9, '2:2', 2, 2, '<u>dzaa</u>likalkit<u>aa</u>bu l<u>aa</u> rayba fiihi hudan lilmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (10, '2:3', 2, 3, '<B>al</b>la<u>dz</u>iina yu/minuuna bilghaybi wayuqiimuuna <u>shsh</u>al<u>aa</u>ta wamimm<u>aa</u> razaqn<u>aa</u>hum yunfiquun\r\r\n');
INSERT INTO quranLatin VALUES (11, '2:4', 2, 4, 'walla<u>dz</u>iina yu/minuuna bim<u>aa</u> unzila ilayka wam<u>aa</u> unzila min qablika wabil-<u>aa</u>khirati hum yuuqinuun\r\r\n');
INSERT INTO quranLatin VALUES (12, '2:5', 2, 5, 'ul<u>aa</u>-ika \'al<u>aa</u> hudan min rabbihim waul<u>aa</u>-ika humulmufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (13, '2:6', 2, 6, 'innalla<u>dz</u>iina kafaruu saw<u>aa</u>un \'alayhim a-an<u>dz</u>artahum am lam tun<u>dz</u>irhum l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (14, '2:7', 2, 7, 'khatamall<u>aa</u>hu \'al<u>aa</u> quluubihim wa\'al<u>aa</u> sam\'ihim wa\'al<u>aa</u> ab<u>shaa</u>rihim ghisy<u>aa</u>watun walahum \'a<u>dzaa</u>bun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (15, '2:8', 2, 8, 'wamina nn<u>aa</u>si man yaquulu <u>aa</u>mann<u>aa</u>billahi wabil yaumilaakhiri wamahum bimu\'miniin\r\r\n');
INSERT INTO quranLatin VALUES (16, '2:9', 2, 9, 'yukh<u>aa</u>di\'uunall<u>aa</u>ha walla<u>dz</u>iina <u>aa</u>manuu wam<u>aa</u> yakhda\'uuna ill<u>aa</u> anfusahum wam<u>aa</u>\r\nyasy\'uruun\r\n');
INSERT INTO quranLatin VALUES (17, '2:10', 2, 10, 'fii quluubihim mara<u>dh</u>un faz<u>aa</u>dahumull<u>aa</u>hu mara<u>dh</u>an walahum \'a<u>dzaa</u>bun liimun bim<u>aa</u> k<u>aa</u>nuu yak<u>dz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (18, '2:11', 2, 11, 'wa-i<u>dzaa</u> qiila lahum l<u>aa</u> tufsiduu fii l-ar<u>dh</u>i q<u>aa</u>luu innam<u>aa</u> na<u>h</u>nu mu<u>sh</u>li<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (19, '2:12', 2, 12, 'al<u>aa</u> innahum humu lmufsiduuna wal<u>aa</u>kin l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (20, '2:13', 2, 13, 'wa-i<u>dzaa</u> qiila lahum <u>aa</u>minuu kam<u>aa</u> <u>aa</u>mana nn<u>aa</u>su q<u>aa</u>luu anu/minu kam<u>aa</u> <u>aa</u>mana ssufah<u>aa</u>u l<u>aa</u> innahum humu ssufah<u>aa</u>u wal<u>aa</u>kin l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (21, '2:14', 2, 14, 'wa-i<u>dzaa</u> laquulla<u>dz</u>iina <u>aa</u>manuu q<u>aa</u>luu <u>aa</u>mann<u>aa</u> wa-i<u>dzaa</u> khalaw il<u>aa</u> syay<u>aath</u>iinihim q<u>aa</u>luu inn<u>aa</u> ma\'akum innam<u>aa</u> na<u>h</u>nu mustahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (22, '2:15', 2, 15, '<B>al</b>l<u>aa</u>hu yastahzi-u bihim wayamudduhum fii <u>th</u>ughy<u>aa</u>nihim ya\'mahuun\r\r\n');
INSERT INTO quranLatin VALUES (23, '2:16', 2, 16, 'ul<u>aa</u>-ikalla<u>dz</u>iina isytarawuu <u>dhdh</u>al<u>aa</u>lata bilhud<u>aa</u> fam<u>aa</u> rabi<u>h</u>at tij<u>aa</u>ratuhum wam<u>aa</u> k<u>aa</u>nuu muhtadiin\r\r\n');
INSERT INTO quranLatin VALUES (24, '2:17', 2, 17, 'matsaluhum kamatsalilla<u>dz</u>ii istawqada n<u>aa</u>ran falamm<u>aa</u> a<u>dhaa</u>-at m<u>aa</u> <u>h</u>awlahu <u>dz</u>ahaball<u>aa</u>hu binuurihim watarakahum fii <u>zh</u>ulum<u>aa</u>tin l<u>aa</u> yub<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (25, '2:18', 2, 18, '<u>sh</u>ummun bukmun \'umyun fahum l<u>aa</u> yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (26, '2:19', 2, 19, 'aw ka<u>sh</u>ayyibin mina ssam<u>aa</u>-i fiihi <u>zh</u>ulum<u>aa</u>tun wara\'dun wabarqun yaj\'aluuna a<u>shaa</u>bi\'ahum fii <u>aatsaa</u>nihim mina <u>shsh</u>aw<u>aa</u>\'iqi <u>h</u>a<u>dz</u>ara lmawti wal<u>aa</u>hu mu<u>h</u>ii<u>th</u>un bilk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (27, '2:20', 2, 20, 'yak<u>aa</u>du lbarqu yakh<u>th</u>afu ab<u>shaa</u>rahum kullam<u>aa</u> a<u>dhaa</u>-a lahum masyaw fiihi wa-i<u>dzaa</u> a<u>zh</u>lama \'alayhim q<u>aa</u>muu walaw sy<u>aa</u>-all<u>aa</u>hu la<u>dz</u>ahaba bisam\'ihim wa-ab<u>shaa</u>rihim innall<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (28, '2:21', 2, 21, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su u\'buduu rabbakumulla<u>dz</u>ii khalaqakum walla<u>dz</u>iina min qablikum la\'allakum tattaquun\r\r\n');
INSERT INTO quranLatin VALUES (29, '2:22', 2, 22, '<B>al</b>la<u>dz</u>ii ja\'ala lakumu l-ar<u>dh</u>a fir<u>aa</u>syan wassam<u>aa</u>-a bin<u>aa</u>-an wa-anzala mina ssam<u>aa</u>-i m<u>aa</u>-an fa-akhraja bihi mina tstsamar<u>aa</u>ti rizqan lakum fal<u>aa</u> taj\'aluu lill<u>aa</u>hi and<u>aa</u>dan wa-antum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (30, '2:23', 2, 23, 'wa-in kuntum fii raybin mimm<u>aa</u> nazzaln<u>aa</u> \'al<u>aa</u> \'abdin<u>aa</u> fa/tuu bisuuratin min mitslihi wad\'uu syuhad<u>aa</u>-akum min duunill<u>aa</u>hi in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (31, '2:24', 2, 24, 'fa-in lam taf\'aluu walan taf\'aluu fattaquu nn<u>aa</u>rallatii waquuduh<u>aa</u> nn<u>aa</u>su wal<u>h</u>ij<u>aa</u>ratu u\'iddat lilk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (32, '2:25', 2, 25, 'wabasysyirilla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti anna lahum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kullam<u>aa</u> ruziquu minh<u>aa</u> min tsamaratin rizqan q<u>aa</u>luu h<u>aadzaa</u>lla<u>dz</u>ii ruziqn<u>aa</u> min qablu wautuu bihi mutasy<u>aa</u>bihan walahum fiih<u>aa</u> azw<u>aa</u>jun mu<u>th</u>ahharatun wahum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (33, '2:26', 2, 26, 'innall<u>aa</u>ha l<u>aa</u> yasta<u>h</u>yii an ya<u>dh</u>riba matsalan m<u>aa</u> ba\'uu<u>dh</u>atan fam<u>aa</u> fawqah<u>aa</u> fa-amm<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu faya\'lamuuna annahu l<u>h</u>aqqu min rabbihim wa-amm<u>aa</u>lla<u>dz</u>iina kafaruu fayaquuluuna m<u>aatsaa</u> ar<u>aa</u>dall<u>aa</u>hu bih<u>aadzaa</u> matsalan yu<u>dh</u>illu bihi katsiiran wayahdii bihi katsiiran wam<u>aa</u> yu<u>dh</u>illu bihi ill<u>aa</u> lf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (34, '2:27', 2, 27, '<B>al</b>la<u>dz</u>iina yanqu<u>dh</u>uuna \'ahdall<u>aa</u>hi min ba\'di miits<u>aa</u>qihi wayaq<u>th</u>a\'uuna m<u>aa</u> amarall<u>aa</u>hu bihi an yuu<u>sh</u>ala wayufsiduuna fii l-ar<u>dh</u>i ul<u>aa</u>-ika humu lkh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (35, '2:28', 2, 28, 'kayfa takfuruuna bi<strung>a</strung>ll<u>aa</u>hi wakuntum amw<u>aa</u>tan fa-a<u>h</u>y<u>aa</u>kum tsumma yumiitukum tsumma yu<u>h</u>yiikum tsumma ilayhi turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (36, '2:29', 2, 29, 'huwalla<u>dz</u>ii khalaqa lakum m<u>aa</u> fii l-ar<u>dh</u>i jamii\'an tsumma istaw<u>aa</u> il<u>aa</u> ssam<u>aa</u>-i fasaww<u>aa</u>hunna sab\'a sam<u>aa</u>w<u>aa</u>tin wahuwa bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (37, '2:30', 2, 30, 'wa-i<u>dz</u> q<u>aa</u>la rabbuka lilmal<u>aa</u>-ikati innii j<u>aa</u>\'ilun fii l-ar<u>dh</u>i khaliifatan q<u>aa</u>luu ataj\'alu fiih<u>aa</u> man yufsidu fiih<u>aa</u> wayasfiku ddim<u>aa</u>-a wana<u>h</u>nu nusabbi<u>h</u>u bi<u>h</u>amdika wanuqaddisu laka q<u>aa</u>la innii a\'lamu m<u>aa</u> l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (38, '2:31', 2, 31, 'wa\'allama <u>aa</u>dama l-asm<u>aa</u>-a kullah<u>aa</u> tsumma \'ara<u>dh</u>ahum \'al<u>aa</u> lmal<u>aa</u>-ikati faq<u>aa</u>la anbi-uunii bi-asm<u>aa</u>-i h<u>aa</u>ul<u>aa</u>-i in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (39, '2:32', 2, 32, 'q<u>aa</u>luu sub<u>haa</u>naka l<u>aa</u> \'ilma lan<u>aa</u> ill<u>aa</u> m<u>aa</u> \'allamtan<u>aa</u> innaka anta l\'aliimu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (40, '2:33', 2, 33, 'q<u>aa</u>la y<u>aa</u> <u>aa</u>damu anbi/hum bi-asm<u>aa</u>-ihim falamm<u>aa</u> anba-ahum bi-asm<u>aa</u>-ihim q<u>aa</u>la alam aqul lakum innii a\'lamu ghayba ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wa-a\'lamu m<u>aa</u> tubduuna wam<u>aa</u> kuntum taktumuun\r\r\n');
INSERT INTO quranLatin VALUES (41, '2:34', 2, 34, 'wa-i<u>dz</u> quln<u>aa</u> lilmal<u>aa</u>-ikati usjuduu li-<u>aa</u>dama fasajaduu ill<u>aa</u> ibliisa ab<u>aa</u> wastakbara wak<u>aa</u>na mina lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (42, '2:35', 2, 35, 'waquln<u>aa</u> y<u>aa</u> <u>aa</u>damu uskun anta wazawjuka ljannata wakul<u>aa</u> minh<u>aa</u> raghadan <u>h</u>aytsu syi/tum<u>aa</u> wal<u>aa</u> taqrab<u>aa</u> h<u>aadz</u>ihi sysyajarata fatakuun<u>aa</u> mina <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (43, '2:36', 2, 36, 'fa-azallahum<u>aa</u> sysyay<u>th</u><u>aa</u>nu \'anh<u>aa</u> fa-akhrajahum<u>aa</u> mimm<u>aa</u> k<u>aa</u>n<u>aa</u> fiihi waquln<u>aa</u> ihbi<u>th</u>uu ba\'<u>dh</u>ukum liba\'<u>dh</u>in \'aduwwun walakum fii l-ar<u>dh</u>i mustaqarrun wamat<u>aa</u>\'un il<u>aa</u> <u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (44, '2:37', 2, 37, 'fatalaqq<u>aa</u> <u>aa</u>damu min rabbihi kalim<u>aa</u>tin fat<u>aa</u>ba \'alayhi innahu huwa ttaww<u>aa</u>bu rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (45, '2:38', 2, 38, 'quln<u>aa</u> ihbi<u>th</u>uu minh<u>aa</u> jamii\'an fa-imm<u>aa</u> ya/tiyannakum minnii hudan faman tabi\'a hud<u>aa</u>ya fal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (46, '2:39', 2, 39, 'walla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (47, '2:40', 2, 40, 'y<u>aa</u> banii isr<u>aa</u>-iila u<u>dz</u>kuruu ni\'matiyallatii an\'amtu \'alaykum wa-awfuu bi\'ahdii uufi bi\'ahdikum wa-iyy<u>aa</u>ya farhabuun\r\r\n');
INSERT INTO quranLatin VALUES (48, '2:41', 2, 41, 'wa<u>aa</u>minuu bim<u>aa</u> anzaltu mu<u>sh</u>addiqan lim<u>aa</u> ma\'akum wal<u>aa</u> takuunuu awwala k<u>aa</u>firin bihi wal<u>aa</u> tasytaruu bi-<u>aa</u>y<u>aa</u>tii tsamanan qaliilan wa-iyy<u>aa</u>ya fattaquun\r\r\n');
INSERT INTO quranLatin VALUES (49, '2:42', 2, 42, 'wal<u>aa</u> talbisuu l<u>h</u>aqqa bilb<u>aath</u>ili wataktumuu l<u>h</u>aqqa wa-antum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (50, '2:43', 2, 43, 'wa-aqiimuu <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tuu zzak<u>aa</u>ta warka\'uu ma\'a rr<u>aa</u>ki\'iin\r\r\n');
INSERT INTO quranLatin VALUES (51, '2:44', 2, 44, 'ata/muruuna nn<u>aa</u>sa bilbirri watansawna anfusakum wa-antum tatluuna lkit<u>aa</u>ba afal<u>aa</u> ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (52, '2:45', 2, 45, 'wasta\'iinuu bi<u>shsh</u>abri wa<u>shsh</u>al<u>aa</u>ti wa-innah<u>aa</u> lakabiiratun ill<u>aa</u> \'al<u>aa</u> lkh<u>aa</u>syi\'iin\r\r\n');
INSERT INTO quranLatin VALUES (53, '2:46', 2, 46, '<B>al</b>la<u>dz</u>iina ya<u>zh</u>unnuuna annahum mul<u>aa</u>quu rabbihim wa-annahum ilayhi r<u>aa</u>ji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (54, '2:47', 2, 47, 'y<u>aa</u> banii isr<u>aa</u>-iila u<u>dz</u>kuruu ni\'matiyallatii an\'amtu \'alaykum wa-annii fa<u>dhdh</u>altukum \'al<u>aa</u> l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (55, '2:48', 2, 48, 'wattaquu yawman l<u>aa</u> tajzii nafsun \'an nafsin syay-an wal<u>aa</u> yuqbalu minh<u>aa</u> syaf<u>aa</u>\'atun wal<u>aa</u> yu/kha<u>dz</u>u minh<u>aa</u> \'adlun wal<u>aa</u> hum yun<u>sh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (56, '2:49', 2, 49, 'wa-i<u>dz</u> najjayn<u>aa</u>kum min <u>aa</u>li fir\'awna yasuumuunakum suu-a l\'a<u>dzaa</u>bi yu<u>dz</u>abbi<u>h</u>uuna abn<u>aa</u>-akum wayasta<u>h</u>yuuna nis<u>aa</u>-akum wafii <u>dzaa</u>likum bal<u>aa</u>un min rabbikum \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (57, '2:50', 2, 50, 'wa-i<u>dz</u> faraqn<u>aa</u> bikumu lba<u>h</u>ra fa-anjayn<u>aa</u>kum wa-aghraqn<u>aa</u> <u>aa</u>la fir\'awna wa-antum tan<u>zh</u>uruun\r\r\n');
INSERT INTO quranLatin VALUES (58, '2:51', 2, 51, 'wa-i<u>dz</u> w<u>aa</u>\'adn<u>aa</u> muus<u>aa</u> arba\'iina laylatan tsumma ittakha<u>dz</u>tumu l\'ijla min ba\'dihi wa-antum <u>zhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (59, '2:52', 2, 52, 'tsumma \'afawn<u>aa</u> \'ankum min ba\'di <u>dzaa</u>lika la\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (60, '2:53', 2, 53, 'wa-i<u>dz</u> <u>aa</u>tayn<u>aa</u> muus<u>aa</u> lkit<u>aa</u>ba walfurq<u>aa</u>na la\'allakum tahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (61, '2:54', 2, 54, 'wa-i<u>dz</u> q<u>aa</u>la muus<u>aa</u> liqawmihi y<u>aa</u> qawmi innakum <u>zh</u>alamtum anfusakum bittikh<u>aadz</u>ikumu l\'ijla fatuubuu il<u>aa</u> b<u>aa</u>ri-ikum faqtuluu anfusakum <u>dzaa</u>likum khayrun lakum \'inda b<u>aa</u>ri-ikum fat<u>aa</u>ba \'alaykum innahu huwa ttaww<u>aa</u>bu rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (62, '2:55', 2, 55, 'wa-i<u>dz</u> qultum y<u>aa</u> muus<u>aa</u> lan nu/mina laka <u>h</u>att<u>aa</u> nar<u>aa</u>ll<u>aa</u>ha jahratan fa-akha<u>dz</u>atkumu <u>shshaa</u>\'iqatu wa-antum tan<u>zh</u>uruun\r\r\n');
INSERT INTO quranLatin VALUES (63, '2:56', 2, 56, 'tsumma ba\'atsn<u>aa</u>kum min ba\'di mawtikum la\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (64, '2:57', 2, 57, 'wa<u>zh</u>allaln<u>aa</u> \'alaykumu lgham<u>aa</u>ma wa-anzaln<u>aa</u> \'alaykumu lmanna wassalw<u>aa</u> kuluu min <u>th</u>ayyib<u>aa</u>ti m<u>aa</u> razaqn<u>aa</u>kum wam<u>aa</u> <u>zh</u>alamuun<u>aa</u> wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (65, '2:58', 2, 58, 'wa-i<u>dz</u> quln<u>aa</u> udkhuluu h<u>aadz</u>ihi lqaryata fakuluu minh<u>aa</u> <u>h</u>aytsu syi/tum raghadan wadkhuluu lb<u>aa</u>ba sujjadan waquuluu <u>h</u>i<u>ththh</u>atun naghfir lakum kha<u>th</u><u>aa</u>y<u>aa</u>kum wasanaziidu lmu<u>h</u>siniin\r\r\n');
