INSERT INTO quranLatin VALUES (68, '2:61', 2, 61, 'wa-i<u>dz</u> qultum y<u>aa</u> muus<u>aa</u> lan na<u>sh</u>bira \'al<u>aa</u> <u>th</u>a\'<u>aa</u>min w<u>aah</u>idin fad\'u lan<u>aa</u> rabbaka yukhrij lan<u>aa</u> mimm<u>aa</u> tunbitu l-ar<u>dh</u>u min baqlih<u>aa</u> waqitsts<u>aa</u>-ih<u>aa</u> wafuumih<u>aa</u> wa\'adasih<u>aa</u> waba<u>sh</u>alih<u>aa</u> q<u>aa</u>la atastabdiluunalla<u>dz</u>ii huwa adn<u>aa</u> billa<u>dz</u>ii huwa khayrun ihbi<u>th</u>uu mi<u>sh</u>ran fa-inna lakum m<u>aa</u> sa-altum wa<u>dh</u>uribat \'alayhimu <u>dzdz</u>illatu walmaskanatu wab<u>aa</u>uu bigha<u>dh</u>abin minall<u>aa</u>hi <u>dzaa</u>lika bi-annahum k<u>aa</u>nuu yakfuruuna bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi wayaqtuluuna nnabiyyiina bighayri l<u>h</u>aqqi <u>dzaa</u>lika bim<u>aa</u> \'a<u>sh</u>aw wak<u>aa</u>nuu ya\'taduun\r\r\n');
