INSERT INTO quranLatin VALUES (70, '2:63', 2, 63, 'wa-i<u>dz</u> akha<u>dz</u>n<u>aa</u> miits<u>aa</u>qakum warafa\'n<u>aa</u> fawqakumu <u>ththh</u>uura khu<u>dz</u>uu m<u>aa</u> <u>aa</u>tayn<u>aa</u>kum biquwwatin wa<u>dz</u>kuruu m<u>aa</u> fiihi la\'allakum tattaquun\r\r\n');
