INSERT INTO quranLatin VALUES (92, '2:85', 2, 85, 'tsumma antum h<u>aa</u>ul<u>aa</u>-i taqtuluuna anfusakum watukhrijuuna fariiqan minkum min diy<u>aa</u>rihim ta<u>zhaa</u>haruuna \'alayhim bil-itsmi wal\'udw<u>aa</u>ni wa-in ya/tuukum us<u>aa</u>r<u>aa</u> tuf<u>aa</u>duuhum wahuwa mu<u>h</u>arramun \'alaykum ikhr<u>aa</u>juhum afatu/minuuna biba\'<u>dh</u>i lkit<u>aa</u>bi watakfuruuna biba\'<u>dh</u>in fam<u>aa</u> jaz<u>aa</u>u man yaf\'alu <u>dzaa</u>lika minkum ill<u>aa</u> khizyun fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wayawma lqiy<u>aa</u>mati yuradduuna il<u>aa</u> asyaddi l\'a<u>dzaa</u>bi wam<u>aa</u>ll<u>aa</u>hu bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun\r\r\n');
