INSERT INTO quranLatin VALUES (94, '2:87', 2, 87, 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> lkit<u>aa</u>ba waqaffayn<u>aa</u> min ba\'dihi birrusuli wa<u>aa</u>tayn<u>aa</u> \'iis<u>aa</u> ibna maryama lbayyin<u>aa</u>ti wa-ayyadn<u>aa</u>hu biruu<u>h</u>i lqudusi afakullam<u>aa</u> j<u>aa</u>-akum rasuulun bim<u>aa</u> l<u>aa</u> tahw<u>aa</u> anfusukumu istakbartum fafariiqan ka<u>dzdz</u>abtum wafariiqan taqtuluun\r\r\n');
