INSERT INTO quranLatin VALUES (98, '2:91', 2, 91, 'wa-i<u>dzaa</u> qiila lahum <u>aa</u>minuu bim<u>aa</u> anzalall<u>aa</u>hu q<u>aa</u>luu nu/minu bim<u>aa</u> unzila \'alayn<u>aa</u> wayakfuruuna bim<u>aa</u> war<u>aa</u>-ahu wahuwa l<u>h</u>aqqu mu<u>sh</u>addiqan lim<u>aa</u> ma\'ahum qul falima taqtuluuna anbiy<u>aa</u>-all<u>aa</u>hi min qablu in kuntum mu/miniin\r\r\n');
