INSERT INTO quranLatin VALUES (107, '2:100', 2, 100, 'awa kullam<u>aa</u> \'<u>aa</u>haduu \'ahdan naba<u>dz</u>ahu fariiqun minhum bal aktsaruhum l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (108, '2:101', 2, 101, 'walamm<u>aa</u> j<u>aa</u>-ahum rasuulun min \'indill<u>aa</u>hi mu<u>sh</u>addiqun lim<u>aa</u> ma\'ahum naba<u>dz</u>a fariiqun minalla<u>dz</u>iina uutuu lkit<u>aa</u>ba kit<u>aa</u>ball<u>aa</u>hi war<u>aa</u>-a <u>zh</u>uhuurihim ka-annahum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (109, '2:102', 2, 102, 'wattaba\'uu m<u>aa</u> tatluu sysyay<u>aath</u>iinu \'al<u>aa</u> mulki sulaym<u>aa</u>na wam<u>aa</u> kafara sulaym<u>aa</u>nu wal<u>aa</u>kinna sysyay<u>aath</u>iina kafaruu yu\'allimuuna nn<u>aa</u>sa ssi<u>h</u>ra wam<u>aa</u> unzila \'al<u>aa</u> lmalakayni bib<u>aa</u>bila h<u>aa</u>ruuta wam<u>aa</u>ruuta wam<u>aa</u> yu\'allim<u>aa</u>ni min a<u>h</u>adin <u>h</u>att<u>aa</u> yaquul<u>aa</u> innam<u>aa</u> na<u>h</u>nu fitnatun fal<u>aa</u> takfur fayata\'allamuuna minhum<u>aa</u> m<u>aa</u> yufarriquuna bihi bayna lmar-i wazawjihi wam<u>aa</u> hum bi<u>daa</u>rriina bihi min a<u>h</u>adin ill<u>aa</u> bi-i<u>dz</u>nill<u>aa</u>hi wayata\'allamuuna m<u>aa</u> ya<u>dh</u>urruhum wal<u>aa</u> yanfa\'uhum walaqad \'alimuu lamani isytar<u>aa</u>hu m<u>aa</u> lahu fii l-<u>aa</u>khirati min khal<u>aa</u>qin walabi/sa m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (110, '2:103', 2, 103, 'walaw annahum <u>aa</u>manuu wattaqaw lamatsuubatun min \'indill<u>aa</u>hi khayrun law k<u>aa</u>nuu ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (111, '2:104', 2, 104, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> taquuluu r<u>aa</u>\'in<u>aa</u> waquuluu un<u>zh</u>urn<u>aa</u> wasma\'uu walilk<u>aa</u>firiina \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (112, '2:105', 2, 105, 'm<u>aa</u> yawaddulla<u>dz</u>iina kafaruu min ahli lkit<u>aa</u>bi wal<u>aa</u> lmusyrikiina an yunazzala \'alaykum min khayrin min rabbikum wal<u>aa</u>hu yakhta<u>shsh</u>u bira<u>h</u>matihi man yasy<u>aa</u>u wal<u>aa</u>hu <u>dz</u>uu lfa<u>dh</u>li l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (113, '2:106', 2, 106, 'm<u>aa</u> nansakh min <u>aa</u>yatin aw nunsih<u>aa</u> na/ti bikhayrin minh<u>aa</u> aw mitslih<u>aa</u> alam ta\'lam annall<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (114, '2:107', 2, 107, 'alam ta\'lam annall<u>aa</u>ha lahu mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wam<u>aa</u> lakum min duunill<u>aa</u>hi min waliyyin wal<u>aa</u> na<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (115, '2:108', 2, 108, 'am turiiduuna an tas-aluu rasuulakum kam<u>aa</u> su-ila muus<u>aa</u> min qablu waman yatabaddali lkufra bil-iim<u>aa</u>ni faqad <u>dh</u>alla saw<u>aa</u>-a ssabiil\r\r\n');
INSERT INTO quranLatin VALUES (116, '2:109', 2, 109, 'wadda katsiirun min ahli lkit<u>aa</u>bi law yarudduunakum min ba\'di iim<u>aa</u>nikum kuff<u>aa</u>ran <u>h</u>asadan min \'indi anfusihim min ba\'di m<u>aa</u> tabayyana lahumu l<u>h</u>aqqu fa\'fuu wa<u>sh</u>fa<u>h</u>uu <u>h</u>att<u>aa</u> ya/tiyall<u>aa</u>hu bi-amrihi innall<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (117, '2:110', 2, 110, 'wa-aqiimuu <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tuu zzak<u>aa</u>ta wam<u>aa</u> tuqaddimuu li-anfusikum min khayrin tajiduuhu \'indall<u>aa</u>hi innall<u>aa</u>ha bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (118, '2:111', 2, 111, 'waq<u>aa</u>luu lan yadkhula ljannata ill<u>aa</u> man k<u>aa</u>na huudan aw na<u>shaa</u>r<u>aa</u> tilka am<u>aa</u>niyyuhum qul h<u>aa</u>tuu burh<u>aa</u>nakum in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (119, '2:112', 2, 112, 'bal<u>aa</u> man aslama wajhahu lill<u>aa</u>hi wahuwa mu<u>h</u>sinun falahu ajruhu \'inda rabbihi wal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (120, '2:113', 2, 113, 'waq<u>aa</u>lati lyahuudu laysati nna<u>shaa</u>r<u>aa</u> \'al<u>aa</u> syay-in waq<u>aa</u>lati nna<u>shaa</u>r<u>aa</u> laysati lyahuudu \'al<u>aa</u> syay-in wahum yatluuna lkit<u>aa</u>ba ka<u>dzaa</u>lika q<u>aa</u>lalla<u>dz</u>iina l<u>aa</u> ya\'lamuuna mitsla qawlihim fal<u>aa</u>hu ya<u>h</u>kumu baynahum yawma lqiy<u>aa</u>mati fiim<u>aa</u> k<u>aa</u>nuu fiihi yakhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (121, '2:114', 2, 114, 'waman a<u>zh</u>lamu mimman mana\'a mas<u>aa</u>jidall<u>aa</u>hi an yu<u>dz</u>kara fiih<u>aa</u> ismuhu wasa\'<u>aa</u> fii khar<u>aa</u>bih<u>aa</u> ul<u>aa</u>-ika m<u>aa</u> k<u>aa</u>na lahum an yadkhuluuh<u>aa</u> ill<u>aa</u> kh<u>aa</u>-ifiina lahum fii dduny<u>aa</u> khizyun walahum fii l-<u>aa</u>khirati \'a<u>dzaa</u>bun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (122, '2:115', 2, 115, 'walill<u>aa</u>hi lmasyriqu walmaghribu fa-aynam<u>aa</u> tuwalluu fatsamma wajhull<u>aa</u>hi innall<u>aa</u>ha w<u>aa</u>si\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (123, '2:116', 2, 116, 'waq<u>aa</u>luu itakha<u>dz</u>all<u>aa</u>hu waladan sub<u>haa</u>nahu bal lahu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i kullun lahu q<u>aa</u>nituun\r\r\n');
INSERT INTO quranLatin VALUES (124, '2:117', 2, 117, 'badii\'u ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wa-i<u>dzaa</u> qa<u>daa</u> amran fa-innam<u>aa</u> yaquulu lahu kun fayakuun\r\r\n');
INSERT INTO quranLatin VALUES (125, '2:118', 2, 118, 'waq<u>aa</u>lalla<u>dz</u>iina l<u>aa</u> ya\'lamuuna lawl<u>aa</u> yukallimun<u>aa</u>ll<u>aa</u>hu aw ta/tiin<u>aa</u> <u>aa</u>yatun ka<u>dzaa</u>lika q<u>aa</u>lalla<u>dz</u>iina min qablihim mitsla qawlihim tasy<u>aa</u>bahat quluubuhum qad bayyann<u>aa</u> l-<u>aa</u>y<u>aa</u>ti liqawmin yuuqinuun\r\r\n');
INSERT INTO quranLatin VALUES (126, '2:119', 2, 119, 'inn<u>aa</u> arsaln<u>aa</u>ka bil<u>h</u>aqqi basyiiran wana<u>dz</u>iiran wal<u>aa</u> tus-alu \'an a<u>sh</u>-<u>haa</u>bi lja<u>h</u>iim\r\n');
INSERT INTO quranLatin VALUES (127, '2:120', 2, 120, 'walan tar<u>daa</u> \'anka lyahuudu wal<u>aa</u> nna<u>shaa</u>r<u>aa</u> <u>h</u>att<u>aa</u> tattabi\'a millatahum qul inna hud<u>aa</u>ll<u>aa</u>hi huwa lhud<u>aa</u> wala-ini ittaba\'ta ahw<u>aa</u>-ahum ba\'dalla<u>dz</u>ii j<u>aa</u>-aka mina l\'ilmi m<u>aa</u> laka minall<u>aa</u>hi min waliyyin wal<u>aa</u> na<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (128, '2:121', 2, 121, '<B>al</b>la<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu lkit<u>aa</u>ba yatluunahu <u>h</u>aqqa til<u>aa</u>watihi ul<u>aa</u>-ika yu/minuuna bihi waman yakfur bihi faul<u>aa</u>-ika humu lkh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (129, '2:122', 2, 122, 'y<u>aa</u> banii isr<u>aa</u>-iila u<u>dz</u>kuruu ni\'matiyallatii an\'amtu \'alaykum wa-annii fa<u>dhdh</u>altukum \'al<u>aa</u> l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (130, '2:123', 2, 123, 'wattaquu yawman l<u>aa</u> tajzii nafsun \'an nafsin syay-an wal<u>aa</u> yuqbalu minh<u>aa</u> \'adlun wal<u>aa</u> tanfa\'uh<u>aa</u> syaf<u>aa</u>\'atun wal<u>aa</u> hum yun<u>sh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (131, '2:124', 2, 124, 'wa-i<u>dz</u>i ibtal<u>aa</u> ibr<u>aa</u>hiima rabbuhu bikalim<u>aa</u>tin fa-atammahunna q<u>aa</u>la innii j<u>aa</u>\'iluka linn<u>aa</u>si im<u>aa</u>man q<u>aa</u>la wamin <u>dz</u>urriyyatii q<u>aa</u>la l<u>aa</u> yan<u>aa</u>lu \'ahdii <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (132, '2:125', 2, 125, 'wa-i<u>dz</u> ja\'aln<u>aa</u> lbayta mats<u>aa</u>batan linn<u>aa</u>si wa-amnan wattakhi<u>dz</u>uu min maq<u>aa</u>mi ibr<u>aa</u>hiima mu<u>sh</u>allan wa\'ahidn<u>aa</u> il<u>aa</u> ibr<u>aa</u>hiima wa-ism<u>aa</u>\'iila an <u>th</u>ahhir<u>aa</u> baytiya li<u>ththaa</u>-ifiina wal\'<u>aa</u>kifiina warrukka\'i ssujuud\r\r\n');
INSERT INTO quranLatin VALUES (133, '2:126', 2, 126, 'wa-i<u>dz</u> q<u>aa</u>la ibr<u>aa</u>hiimu rabbi ij\'al h<u>aadzaa</u> baladan <u>aa</u>minan warzuq ahlahu mina tstsamar<u>aa</u>ti man <u>aa</u>mana minhum bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri q<u>aa</u>la waman kafara faumatti\'uhu qaliilan tsumma a<u>dth</u>arruhu il<u>aa</u> \'a<u>dzaa</u>bi nn<u>aa</u>ri wabi/sa lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (134, '2:127', 2, 127, 'wa-i<u>dz</u> yarfa\'u ibr<u>aa</u>hiimu lqaw<u>aa</u>\'ida mina lbayti wa-ism<u>aa</u>\'iilu rabban<u>aa</u> taqabbal minn<u>aa</u> innaka anta ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (135, '2:128', 2, 128, 'rabban<u>aa</u> waj\'aln<u>aa</u> muslimayni laka wamin <u>dz</u>urriyyatin<u>aa</u> ummatan muslimatan laka wa-arin<u>aa</u> man<u>aa</u>sikan<u>aa</u> watub \'alayn<u>aa</u> innaka anta ttaww<u>aa</u>bu rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (136, '2:129', 2, 129, 'rabban<u>aa</u> wab\'ats fiihim rasuulan minhum yatluu \'alayhim <u>aa</u>y<u>aa</u>tika wayu\'allimuhumu lkit<u>aa</u>ba wal<u>h</u>ikmata wayuzakkiihim innaka anta l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (137, '2:130', 2, 130, 'waman yarghabu \'an millati ibr<u>aa</u>hiima ill<u>aa</u> man safiha nafsahu walaqadi i<u>sth</u>afayn<u>aa</u>hu fii dduny<u>aa</u> wa-innahu fii l-<u>aa</u>khirati lamina <u>shshaa</u>li<u>h</u>iin\r\n');
INSERT INTO quranLatin VALUES (138, '2:131', 2, 131, 'i<u>dz</u> q<u>aa</u>la lahu rabbuhu aslim q<u>aa</u>la aslamtu lirabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (139, '2:132', 2, 132, 'wawa<u>shshaa</u> bih<u>aa</u> ibr<u>aa</u>hiimu baniihi waya\'quubu y<u>aa</u> baniyya innall<u>aa</u>ha i<u>sth</u>af<u>aa</u> lakumu ddiina fal<u>aa</u> tamuutunna ill<u>aa</u> wa-antum muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (140, '2:133', 2, 133, 'am kuntum syuhad<u>aa</u>-a i<u>dz</u> <u>h</u>a<u>dh</u>ara ya\'quuba lmawtu i<u>dz</u> q<u>aa</u>la libaniihi m<u>aa</u> ta\'buduuna min ba\'dii q<u>aa</u>luu na\'budu il<u>aa</u>haka wa-il<u>aa</u>ha <u>aa</u>b<u>aa</u>-ika ibr<u>aa</u>hiima wa-ism<u>aa</u>\'iila wa-is<u>haa</u>qa il<u>aa</u>han w<u>aah</u>idan wana<u>h</u>nu lahu muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (141, '2:134', 2, 134, 'tilka ummatun qad khalat lah<u>aa</u> m<u>aa</u> kasabat walakum m<u>aa</u> kasabtum wal<u>aa</u> tus-aluuna \'amm<u>aa</u> k<u>aa</u>nu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (142, '2:135', 2, 135, 'waq<u>aa</u>luu kuunuu huudan aw na<u>shaa</u>r<u>aa</u> tahtaduu qul bal millata ibr<u>aa</u>hiima <u>h</u>aniifan wam<u>aa</u> k<u>aa</u>na mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (143, '2:136', 2, 136, 'quuluu <u>aa</u>mann<u>aa</u> bil<u>aa</u>hi wam<u>aa</u> unzila ilayn<u>aa</u> wam<u>aa</u> unzila il<u>aa</u> ibr<u>aa</u>hiima wa-ism<u>aa</u>\'iila wa-is<u>haa</u>qa waya\'quuba wal-asb<u>aath</u>i wam<u>aa</u> uutiya muus<u>aa</u> wa\'iis<u>aa</u> wam<u>aa</u> uutiya nnabiyyuuna min rabbihim l<u>aa</u> nufarriqu bayna a<u>h</u>adin minhum wana<u>h</u>nu lahu muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (144, '2:137', 2, 137, 'fa-in <u>aa</u>manuu bimitsli m<u>aa</u> <u>aa</u>mantum bihi faqadi ihtadaw wa-in tawallaw fa-innam<u>aa</u> hum fii syiq<u>aa</u>qin fasayakfiikahumull<u>aa</u>hu wahuwa ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (145, '2:138', 2, 138, '<u>sh</u>ibghatall<u>aa</u>hi waman a<u>h</u>sanu minall<u>aa</u>hi <u>sh</u>ibghatan wana<u>h</u>nu lahu \'<u>aa</u>biduun\r \r\r\n');
INSERT INTO quranLatin VALUES (146, '2:139', 2, 139, 'qul atu<u>haa</u>jjuunan<u>aa</u> fiill<u>aa</u>hi wahuwa rabbun<u>aa</u> warabbukum walan<u>aa</u> a\'m<u>aa</u>lun<u>aa</u> walakum a\'m<u>aa</u>lukum wana<u>h</u>nu lahu mukhli<u>sh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (147, '2:140', 2, 140, 'am taquuluuna inna ibr<u>aa</u>hiima wa-ism<u>aa</u>\'iila wa-is<u>haa</u>qa waya\'quuba wal-asb<u>aath</u>a k<u>aa</u>nuu huudan aw na<u>shaa</u>r<u>aa</u> qul a-antum a\'lamu amill<u>aa</u>hu waman a<u>zh</u>lamu mimman katama syah<u>aa</u>datan \'indahu minall<u>aa</u>hi wam<u>aa</u>ll<u>aa</u>hu bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (148, '2:141', 2, 141, 'tilka ummatun qad khalat lah<u>aa</u> m<u>aa</u> kasabat walakum m<u>aa</u> kasabtum wal<u>aa</u> tus-aluuna \'amm<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (149, '2:142', 2, 142, 'sayaquulu ssufah<u>aa</u>u mina nn<u>aa</u>si m<u>aa</u> wall<u>aa</u>hum \'an qiblatihimullatii k<u>aa</u>nuu \'alayh<u>aa</u> qul lill<u>aa</u>hi lmasyriqu walmaghribu yahdii man yasy<u>aa</u>u il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (150, '2:143', 2, 143, 'waka<u>dzaa</u>lika ja\'aln<u>aa</u>kum ummatan wasa<u>th</u>an litakuunuu syuhad<u>aa</u>-a \'al<u>aa</u> nn<u>aa</u>si wayakuuna rrasuulu \'alaykum syahiidan wam<u>aa</u> ja\'aln<u>aa</u> lqiblatallatii kunta \'alayh<u>aa</u> ill<u>aa</u> lina\'lama man yattabi\'u rrasuula mimman yanqalibu \'al<u>aa</u> \'aqibayhi wa-in k<u>aa</u>nat lakabiiratan ill<u>aa</u> \'al<u>aa</u>lla<u>dz</u>iina had<u>aa</u>ll<u>aa</u>hu wam<u>aa</u> k<u>aa</u>nall<u>aa</u>hu liyu<u>dh</u>ii\'a iim<u>aa</u>nakum innall<u>aa</u>ha binn<u>aa</u>si larauufun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (151, '2:144', 2, 144, 'qad nar<u>aa</u> taqalluba wajhika fii ssam<u>aa</u>-i falanuwalliyannaka qiblatan tar<u>daa</u>h<u>aa</u> fawalli wajhaka sya<u>th</u>ra lmasjidi l<u>h</u>ar<u>aa</u>mi wa<u>h</u>aytsu m<u>aa</u> kuntum fawalluu wujuuhakum sya<u>th</u>rahu wa-innalla<u>dz</u>iina uutuu lkit<u>aa</u>ba laya\'lamuuna annahu l<u>h</u>aqqu min rabbihim wam<u>aa</u>ll<u>aa</u>hu bigh<u>aa</u>filin \'amm<u>aa</u> ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (152, '2:145', 2, 145, 'wala-in ataytalla<u>dz</u>iina uutuu lkit<u>aa</u>ba bikulli <u>aa</u>yatin m<u>aa</u> tabi\'uu qiblataka wam<u>aa</u> anta bit<u>aa</u>bi\'in qiblatahum wam<u>aa</u> ba\'<u>dh</u>uhum bit<u>aa</u>bi\'in qiblata ba\'<u>dh</u>in wala-ini ittaba\'ta ahw<u>aa</u>-ahum min ba\'di m<u>aa</u> j<u>aa</u>-aka mina l\'ilmi innaka i<u>dz</u>an lamina <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (153, '2:146', 2, 146, '<B>al</b>la<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu lkit<u>aa</u>ba ya\'rifuunahu kam<u>aa</u> ya\'rifuuna abn<u>aa</u>-ahum wa-inna fariiqan minhum layaktumuuna l<u>h</u>aqqa wahum ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (154, '2:147', 2, 147, 'al<u>h</u>aqqu min rabbika fal<u>aa</u> takuunanna mina lmumtariin\r\r\n');
INSERT INTO quranLatin VALUES (155, '2:148', 2, 148, 'walikullin wijhatun huwa muwalliih<u>aa</u> fastabiquu lkhayr<u>aa</u>ti aynam<u>aa</u> takuunuu ya/ti bikumull<u>aa</u>hu jamii\'an innall<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (156, '2:149', 2, 149, 'wamin <u>h</u>aytsu kharajta fawalli wajhaka sya<u>th</u>ra lmasjidi l<u>h</u>ar<u>aa</u>mi wa-innahu lal<u>h</u>aqqu min rabbika wam<u>aa</u>ll<u>aa</u>hu bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (157, '2:150', 2, 150, 'wamin <u>h</u>aytsu kharajta fawalli wajhaka sya<u>th</u>ra lmasjidi l<u>h</u>ar<u>aa</u>mi wa<u>h</u>aytsu m<u>aa</u> kuntum fawalluu wujuuhakum sya<u>th</u>rahu li-all<u>aa</u> yakuuna linn<u>aa</u>si \'alaykum <u>h</u>ujjatun ill<u>aa</u>lla<u>dz</u>iina <u>zh</u>alamuu minhum fal<u>aa</u> takhsyawhum wakhsyawnii wali-utimma ni\'matii \'alaykum wala\'allakum tahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (158, '2:151', 2, 151, 'kam<u>aa</u> arsaln<u>aa</u> fiikum rasuulan minkum yatluu \'alaykum <u>aa</u>y<u>aa</u>tin<u>aa</u> wayuzakkiikum wayu\'allimukumu lkit<u>aa</u>ba wal<u>h</u>ikmata wayu\'allimukum m<u>aa</u> lam takuunuu ta\'lamuun\r\r\n');
