INSERT INTO quranLatin VALUES (163, '2:156', 2, 156, '<B>al</b>la<u>dz</u>iina i<u>dzaa</u> a<u>shaa</u>bat-hum mu<u>sh</u>iibatun q<u>aa</u>luu inn<u>aa</u> lill<u>aa</u>hi wa-inn<u>aa</u> ilayhi r<u>aa</u>ji\'uun\r\r\n');
