INSERT INTO quranLatin VALUES (165, '2:158', 2, 158, 'inna <u>shsh</u>af<u>aa</u> walmarwata min sya\'<u>aa</u>-irill<u>aa</u>hi faman <u>h</u>ajja lbayta awi i\'tamara fal<u>aa</u> jun<u>aah</u>a \'alayhi an ya<u>ththh</u>awwafa bihim<u>aa</u> waman ta<u>th</u>awwa\'a khayran fa-innall<u>aa</u>ha sy<u>aa</u>kirun \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (166, '2:159', 2, 159, 'innalla<u>dz</u>iina yaktumuuna m<u>aa</u> anzaln<u>aa</u> mina lbayyin<u>aa</u>ti walhud<u>aa</u> min ba\'di m<u>aa</u> bayyann<u>aa</u>hu linn<u>aa</u>si fii lkit<u>aa</u>bi ul<u>aa</u>-ika yal\'anuhumull<u>aa</u>hu wayal\'anuhumull<u>aa</u>\'inuun\r\r\n');
