INSERT INTO quranLatin VALUES (171, '2:164', 2, 164, 'inna fii khalqi ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wakhtil<u>aa</u>fillayli wannah<u>aa</u>ri walfulkillatii tajrii fii lba<u>h</u>ri bim<u>aa</u> yanfa\'u nn<u>aa</u>sa wam<u>aa</u> anzalall<u>aa</u>hu mina ssam<u>aa</u>-i min m<u>aa</u>-in fa-a<u>h</u>y<u>aa</u> bihi l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> wabatstsa fiih<u>aa</u> min kulli d<u>aa</u>bbatin wata<u>sh</u>riifi rriy<u>aah</u>i wassa<u>haa</u>bi lmusakhkhari bayna ssam<u>aa</u>-i wal-ar<u>dh</u>i la<u>aa</u>y<u>aa</u>tin liqawmin ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (172, '2:165', 2, 165, 'wamina nn<u>aa</u>si man yattakhi<u>dz</u>u min duunill<u>aa</u>hi and<u>aa</u>dan yu<u>h</u>ibbuunahum ka<u>h</u>ubbill<u>aa</u>hi walla<u>dz</u>iina <u>aa</u>manuu asyaddu <u>h</u>ubban lill<u>aa</u>hi walaw yar<u>aa</u>lla<u>dz</u>iina <u>zh</u>alamuu i<u>dz</u> yarawna l\'a<u>dzaa</u>ba anna lquwwata lill<u>aa</u>hi jamii\'an wa-annall<u>aa</u>ha syadiidu l\'a<u>dzaa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (173, '2:166', 2, 166, 'i<u>dz</u> tabarra-alla<u>dz</u>iina ittubi\'uu minalla<u>dz</u>iina ittaba\'uu wara-awuu l\'a<u>dzaa</u>ba wataqa<u>ththh</u>a\'at bihimu l-asb<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (174, '2:167', 2, 167, 'waq<u>aa</u>lalla<u>dz</u>iina ittaba\'uu law anna lan<u>aa</u> karratan fanatabarra-a minhum kam<u>aa</u> tabarrauu minn<u>aa</u> ka<u>dzaa</u>lika yuriihimull<u>aa</u>hu a\'m<u>aa</u>lahum <u>h</u>asar<u>aa</u>tin \'alayhim wam<u>aa</u> hum bikh<u>aa</u>rijiina mina nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (175, '2:168', 2, 168, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su kuluu mimm<u>aa</u> fii l-ar<u>dh</u>i <u>h</u>al<u>aa</u>lan <u>th</u>ayyiban wal<u>aa</u> tattabi\'uu khu<u>th</u>uw<u>aa</u>ti sysyay<u>th</u><u>aa</u>ni innahu lakum \'aduwwun mubiin\r\r\n');
