INSERT INTO quranLatin VALUES (177, '2:170', 2, 170, 'wa-i<u>dzaa</u> qiila lahumu ittabi\'uu m<u>aa</u> anzalall<u>aa</u>hu q<u>aa</u>luu bal nattabi\'u m<u>aa</u> lfayn<u>aa</u> \'alayhi <u>aa</u>b<u>aa</u>-an<u>aa</u> awa law k<u>aa</u>na <u>aa</u>b<u>aa</u>uhum l<u>aa</u> ya\'qiluuna syay-an wal<u>aa</u> yahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (178, '2:171', 2, 171, 'wamatsalulla<u>dz</u>iina kafaruu kamatsalilla<u>dz</u>ii yan\'iqu bim<u>aa</u> l<u>aa</u> yasma\'u ill<u>aa</u> du\'<u>aa</u>-an wanid<u>aa</u>-an <u>sh</u>ummun bukmun \'umyun fahum l<u>aa</u> ya\'qiluun\r\r\n');
