INSERT INTO quranLatin VALUES (180, '2:173', 2, 173, 'innam<u>aa</u> <u>h</u>arrama \'alaykumu lmaytata waddama wala<u>h</u>ma lkhinziiri wam<u>aa</u> uhilla bihi lighayrill<u>aa</u>hi famani i<u>dth</u>urra ghayra b<u>aa</u>ghin wal<u>aa</u> \'<u>aa</u>din fal<u>aa</u> itsma \'alayhi innall<u>aa</u>ha ghafuurun ra<u>h</u>iim \r\r\n');
INSERT INTO quranLatin VALUES (181, '2:174', 2, 174, 'innalla<u>dz</u>iina yaktumuuna m<u>aa</u> anzalall<u>aa</u>hu mina lkit<u>aa</u>bi wayasytaruuna bihi tsamanan qaliilan ul<u>aa</u>-ika m<u>aa</u> ya/kuluuna fii bu<u>th</u>uunihim ill<u>aa</u> nn<u>aa</u>ra wal<u>aa</u> yukallimuhumull<u>aa</u>hu yawma lqiy<u>aa</u>mati wal<u>aa</u> yuzakkiihim walahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (182, '2:175', 2, 175, 'ul<u>aa</u>-ikalla<u>dz</u>iina isytarawuu <u>dhdh</u>al<u>aa</u>lata bilhud<u>aa</u> wal\'a<u>dzaa</u>ba bilmaghfirati fam<u>aa</u> a<u>sh</u>barahum \'al<u>aa</u> nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (183, '2:176', 2, 176, '<u>dzaa</u>lika bi-annall<u>aa</u>ha nazzala lkit<u>aa</u>ba bil<u>h</u>aqqi wa-innalla<u>dz</u>iina ikhtalafuu fii lkit<u>aa</u>bi lafii syiq<u>aa</u>qin ba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (184, '2:177', 2, 177, 'laysa lbirra an tuwalluu wujuuhakum qibala lmasyriqi walmaghribi wal<u>aa</u>kinna lbirra man <u>aa</u>mana bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri walmal<u>aa</u>-ikati walkit<u>aa</u>bi wannabiyyiina wa<u>aa</u>t<u>aa</u>\r lm<u>aa</u>la \'al<u>aa</u> <u>h</u>ubbihi <u>dz</u>awii lqurb<u>aa</u> walyat<u>aa</u>m<u>aa</u> walmas<u>aa</u>kiina wabna lssabiili wass<u>aa</u>-iliina wafii rriq<u>aa</u>bi wa-aq<u>aa</u>ma <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>t<u>aa</u> zzak<u>aa</u>ta walmuufuuna bi\'ahdihim i<u>dzaa</u> \'<u>aa</u>haduu wa<u>shshaa</u>biriina fii lba/s<u>aa</u>-i wa<u>dhdh</u>arr<u>aa</u>-i wa<u>h</u>iina lba/si ul<u>aa</u>-ikalla<u>dz</u>iina <u>sh</u>adaquu waul<u>aa</u>-ika humu lmuttaquun\r\r\n');
INSERT INTO quranLatin VALUES (185, '2:178', 2, 178, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu kutiba \'alaykumu lqi<u>sas</u>u fii lqatl<u>aa</u> l<u>h</u>urru bil<u>h</u>urri wal\'abdu bil\'abdi walunts<u>aa</u> bilunts<u>aa</u> faman \'ufiya lahu min akhiihi syay-un fattib<u>aa</u>\'un bilma\'ruufi wa-ad<u>aa</u>un ilayhi bi-i<u>h</u>s<u>aa</u>nin <u>dzaa</u>lika takhfiifun min rabbikum wara<u>h</u>matun famani i\'tad<u>aa</u> ba\'da <u>dzaa</u>lika falahu \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (186, '2:179', 2, 179, 'walakum fii lqi<u>sas</u>i <u>h</u>ay<u>aa</u>tun y<u>aa</u> ulii l-alb<u>aa</u>bi la\'allakum tattaquun\r\n');
INSERT INTO quranLatin VALUES (187, '2:180', 2, 180, 'kutiba \'alaykum i<u>dzaa</u> <u>h</u>a<u>dh</u>ara a<u>h</u>adakumu lmawtu in taraka khayran lwa<u>sh</u>iyyatu lilw<u>aa</u>lidayni wal-aqrabiina bilma\'ruufi <u>h</u>aqqan \'al<u>aa</u> lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (188, '2:181', 2, 181, 'faman baddalahu ba\'da m<u>aa</u> sami\'ahu fa-innam<u>aa</u> itsmuhu \'al<u>aa</u>lla<u>dz</u>iina yubaddiluunahu innall<u>aa</u>ha samii\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (189, '2:182', 2, 182, 'faman kh<u>aa</u>fa min muu<u>sh</u>in janafan aw itsman fa-a<u>sh</u>la<u>h</u>a baynahum fal<u>aa</u> itsma \'alayhi innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (190, '2:183', 2, 183, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu kutiba \'alaykumu <u>shsh</u>iy<u>aa</u>mu kam<u>aa</u> kutiba \'al<u>aa</u>lla<u>dz</u>iina min qablikum la\'allakum tattaquun\r\r\n');
INSERT INTO quranLatin VALUES (191, '2:184', 2, 184, 'ayy<u>aa</u>man ma\'duud<u>aa</u>tin faman k<u>aa</u>na minkum marii<u>dh</u>an aw \'al<u>aa</u> safarin fa\'iddatun min ayy<u>aa</u>min ukhara wa\'al<u>aa</u>lla<u>dz</u>iina yu<u>th</u>iiquunahu fidyatun <u>th</u>a\'<u>aa</u>mu miskiinin faman ta<u>th</u>awwa\'a khayran fahuwa khayrun lahu wa-an ta<u>sh</u>uumuu khayrun lakum in kuntum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (192, '2:185', 2, 185, 'syahru rama<u>daa</u>nalla<u>dz</u>ii unzila fiihi lqur-<u>aa</u>nu hudan linn<u>aa</u>si wabayyin<u>aa</u>tin mina lhud<u>aa</u> walfurq<u>aa</u>ni faman syahida minkumu sysyahra falya<u>sh</u>umhu waman k<u>aa</u>na marii<u>dh</u>an aw \'al<u>aa</u> safarin fa\'iddatun min ayy<u>aa</u>min ukhara yuriidull<u>aa</u>hu bikumu lyusra wal<u>aa</u> yuriidu bikumu l\'usra walitukmiluu l\'iddata walitukabbiruull<u>aa</u>ha \'al<u>aa</u> m<u>aa</u> had<u>aa</u>kum wala\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (193, '2:186', 2, 186, 'wa-i<u>dzaa</u> sa-alaka \'ib<u>aa</u>dii \'annii fa-innii qariibun ujiibu da\'wata dd<u>aa</u>\'i i<u>dzaa</u> da\'<u>aa</u>ni falyastajiibuu lii walyu/minuu bii la\'allahum yarsyuduun\r\r\n');
INSERT INTO quranLatin VALUES (194, '2:187', 2, 187, 'u<u>h</u>illa lakum laylata <u>shsh</u>iy<u>aa</u>mi rrafatsu il<u>aa</u> nis<u>aa</u>-ikum hunna lib<u>aa</u>sun lakum wa-antum lib<u>aa</u>sun lahunna \'alimall<u>aa</u>hu annakum kuntum takht<u>aa</u>nuuna anfusakum fat<u>aa</u>ba \'alaykum wa\'af<u>aa</u> \'ankum fal-<u>aa</u>na b<u>aa</u>syiruuhunna wabtaghuu m<u>aa</u> kataball<u>aa</u>hu lakum wakuluu wasyrabuu <u>h</u>att<u>aa</u> yatabayyana lakumu lkhay<u>th</u>u l-abya<u>dh</u>u mina lkhay<u>th</u>i l-aswadi mina lfajri tsumma atimmuu <u>shsh</u>iy<u>aa</u>ma il<u>aa</u>llayli wal<u>aa</u> tub<u>aa</u>syiruuhunna wa-antum \'<u>aa</u>kifuuna fii lmas<u>aa</u>jidi tilka <u>h</u>uduudull<u>aa</u>hi fal<u>aa</u> taqrabuuh<u>aa</u> ka<u>dzaa</u>lika yubayyinull<u>aa</u>hu <u>aa</u>y<u>aa</u>tihi linn<u>aa</u>si la\'allahum yattaquun\r\r\n');
INSERT INTO quranLatin VALUES (195, '2:188', 2, 188, 'wal<u>aa</u> ta/kuluu amw<u>aa</u>lakum baynakum bilb<u>aath</u>ili watudluu bih<u>aa</u> il<u>aa</u> l<u>h</u>ukk<u>aa</u>mi lita/kuluu fariiqan min amw<u>aa</u>li nn<u>aa</u>si bil-itsmi wa-antum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (196, '2:189', 2, 189, 'yas-aluunaka \'ani l-ahillati qul hiya maw<u>aa</u>qiitu linn<u>aa</u>si wal<u>h</u>ajji walaysa lbirru bi-an ta/tuu lbuyuuta min <u>zh</u>uhuurih<u>aa</u> wal<u>aa</u>kinna lbirra mani ittaq<u>aa</u> wa/tuu lbuyuuta min abw<u>aa</u>bih<u>aa</u> wattaquull<u>aa</u>ha la\'allakum tufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (197, '2:190', 2, 190, 'waq<u>aa</u>tiluu fii sabiilill<u>aa</u>hilla<u>dz</u>iina yuq<u>aa</u>tiluunakum wal<u>aa</u> ta\'taduu innall<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu lmu\'tadiin\r\r\n');
INSERT INTO quranLatin VALUES (198, '2:191', 2, 191, 'waqtuluuhum <u>h</u>aytsu tsaqiftumuuhum wa-akhrijuuhum min <u>h</u>aytsu akhrajuukum walfitnatu asyaddu mina lqatli wal<u>aa</u> tuq<u>aa</u>tiluuhum \'inda lmasjidi l<u>h</u>ar<u>aa</u>mi <u>h</u>att<u>aa</u> yuq<u>aa</u>tiluukum fiihi fa-in q<u>aa</u>taluukum faqtuluuhum ka<u>dzaa</u>lika jaz<u>aa</u>u lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (199, '2:192', 2, 192, 'fa-ini intahaw fa-innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (200, '2:193', 2, 193, 'waq<u>aa</u>tiluuhum <u>h</u>att<u>aa</u> l<u>aa</u> takuuna fitnatun wayakuuna ddiinu lill<u>aa</u>hi fa-ini intahaw fal<u>aa</u> \'udw<u>aa</u>na ill<u>aa</u> \'al<u>aa</u> <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (201, '2:194', 2, 194, 'asysyahru l<u>h</u>ar<u>aa</u>mu bisysyahri l<u>h</u>ar<u>aa</u>mi wal<u>h</u>urum<u>aa</u>tu qi<u>sas</u>un famani i\'tad<u>aa</u> \'alaykum fa\'taduu \'alayhi bimitsli m<u>aa</u> i\'tad<u>aa</u> \'alaykum wattaquull<u>aa</u>ha wa\'lamuu annall<u>aa</u>ha ma\'a lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (202, '2:195', 2, 195, 'wa-anfiquu fii sabiilill<u>aa</u>hi wal<u>aa</u> tulquu bi-aydiikum il<u>aa</u> ttahlukati wa-a<u>h</u>sinuu innall<u>aa</u>ha yu<u>h</u>ibbu lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (203, '2:196', 2, 196, 'wa-atimmuu l<u>h</u>ajja wal\'umrata lill<u>aa</u>hi fa-in u<u>hs</u>irtum fam<u>aa</u> istaysara mina lhadyi wal<u>aa</u> ta<u>h</u>liquu ruuusakum <u>h</u>att<u>aa</u> yablugha lhadyu ma<u>h</u>illahu faman k<u>aa</u>na minkum marii<u>dh</u>an aw bihi a<u>dz</u>an min ra/sihi fafidyatun min <u>sh</u>iy<u>aa</u>min aw <u>sh</u>adaqatin aw nusukin fa-i<u>dzaa</u> amintum faman tamatta\'a bil\'umrati il<u>aa</u> l<u>h</u>ajji fam<u>aa</u> istaysara mina lhadyi faman lam yajid fa<u>sh</u>iy<u>aa</u>mu tsal<u>aa</u>tsati ayy<u>aa</u>min fii l<u>h</u>ajji wasab\'atin i<u>dzaa</u> raja\'tum tilka \'asyaratun k<u>aa</u>milatun <u>dzaa</u>lika liman lam yakun ahluhu <u>had</u>irii lmasjidi l<u>h</u>ar<u>aa</u>mi wattaquull<u>aa</u>ha wa\'lamuu annall<u>aa</u>ha syadiidu l\'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (204, '2:197', 2, 197, 'al<u>h</u>ajju asyhurun ma\'luum<u>aa</u>tun faman fara<u>dh</u>a fiihinna l<u>h</u>ajja fal<u>aa</u> rafatsa wal<u>aa</u> fusuuqa wal<u>aa</u> jid<u>aa</u>la fii l<u>h</u>ajji wam<u>aa</u> taf\'aluu min khayrin ya\'lamhull<u>aa</u>hu watazawwaduu fa-inna khayra zz<u>aa</u>di ttaqw<u>aa</u> wattaquuni y<u>aa</u> ulii l-alb<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (205, '2:198', 2, 198, 'laysa \'alaykum jun<u>aah</u>un an tabtaghuu fa<u>dh</u>lan min rabbikum fa-i<u>dzaa</u> afa<u>dh</u>tum min \'araf<u>aa</u>tin fa<u>dz</u>kuruull<u>aa</u>ha \'inda lmasy\'ari l<u>h</u>ar<u>aa</u>mi wa<u>dz</u>kuruuhu kam<u>aa</u> had<u>aa</u>kum wa-in kuntum min qablihi lamina <u>dhdhaa</u>lliin\r \r\r\n');
