INSERT INTO quranLatin VALUES (220, '2:213', 2, 213, 'k<u>aa</u>na nn<u>aa</u>su ummatan w<u>aah</u>idatan faba\'atsall<u>aa</u>hu nnabiyyiina mubasysyiriina wamun<u>dz</u>iriina wa-anzala ma\'ahumu lkit<u>aa</u>ba bil<u>h</u>aqqi liya<u>h</u>kuma bayna nn<u>aa</u>si fiim<u>aa</u> ikhtalafuu fiihi wam<u>aa</u> ikhtalafa fiihi ill<u>aa</u>lla<u>dz</u>iina uutuuhu min ba\'di m<u>aa</u> j<u>aa</u>-at-humu lbayyin<u>aa</u>tu baghyan baynahum fahad<u>aa</u>ll<u>aa</u>hulla<u>dz</u>iina <u>aa</u>manuu lim<u>aa</u> ikhtalafuu fiihi mina l<u>h</u>aqqi bi-i<u>dz</u>nihi wal<u>aa</u>hu yahdii man yasy<u>aa</u>u il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
