INSERT INTO quranLatin VALUES (224, '2:217', 2, 217, 'yas-aluunaka \'ani sysyahri l<u>h</u>ar<u>aa</u>mi qit<u>aa</u>lin fiihi qul qit<u>aa</u>lun fiihi kabiirun wa<u>sh</u>addun \'an sabiilill<u>aa</u>hi wakufrun bihi walmasjidi l<u>h</u>ar<u>aa</u>mi wa-ikhr<u>aa</u>ju ahlihi minhu akbaru \'indall<u>aa</u>hi walfitnatu akbaru mina lqatli wal<u>aa</u> yaz<u>aa</u>luuna yuq<u>aa</u>tiluunakum <u>h</u>att<u>aa</u> yarudduukum \'an diinikum ini ista<u>th</u><u>aa</u>\'uu waman yartadid minkum \'an diinihi fayamut wahuwa k<u>aa</u>firun faul<u>aa</u>-ika <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum fii dduny<u>aa</u> wal-<u>aa</u>khirati waul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
