INSERT INTO quranLatin VALUES (269, '2:262', 2, 262, '<B>al</b>la<u>dz</u>iina yunfiquuna amw<u>aa</u>lahum fii sabiilill<u>aa</u>hi tsumma l<u>aa</u> yutbi\'uuna m<u>aa</u> anfaquu mannan wal<u>aa</u> a<u>dz</u>an lahum ajruhum \'inda rabbihim wal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (270, '2:263', 2, 263, 'qawlun ma\'ruufun wamaghfiratun khayrun min <u>sh</u>adaqatin yatba\'uh<u>aa</u> a<u>dz</u>an wal<u>aa</u>hu ghaniyyun <u>h</u>aliim\r\r\n');
INSERT INTO quranLatin VALUES (271, '2:264', 2, 264, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tub<u>th</u>iluu <u>sh</u>adaq<u>aa</u>tikum bilmanni wal-a<u>dzaa</u> kalla<u>dz</u>ii yunfiqu m<u>aa</u>lahu ri-<u>aa</u>-a nn<u>aa</u>si wal<u>aa</u> yu/minu bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri famatsaluhu kamatsali <u>sh</u>afw<u>aa</u>nin \'alayhi tur<u>aa</u>bun fa-a<u>shaa</u>bahu w<u>aa</u>bilun fatarakahu <u>sh</u>aldan l<u>aa</u> yaqdiruuna \'al<u>aa</u> syay-in mimm<u>aa</u> kasabuu wal<u>aa</u>hu l<u>aa</u> yahdii lqawma lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (272, '2:265', 2, 265, 'wamatsalulla<u>dz</u>iina yunfiquuna amw<u>aa</u>lahumu ibtigh<u>aa</u>-a mar<u>daa</u>till<u>aa</u>hi watatsbiitan min\r anfusihim kamatsali jannatin birabwatin a<u>shaa</u>bah<u>aa</u> w<u>aa</u>bilun fa<u>aa</u>tat ukulah<u>aa</u> <u>dh</u>i\'fayni fa-in lam yu<u>sh</u>ibh<u>aa</u> w<u>aa</u>bilun fa<u>th</u>allun wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (273, '2:266', 2, 266, 'ayawaddu a<u>h</u>adukum an takuuna lahu jannatun min nakhiilin wa-a\'n<u>aa</u>bin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru lahu fiih<u>aa</u> min kulli tstsamar<u>aa</u>ti wa-a<u>shaa</u>bahu lkibaru walahu <u>dz</u>urriyyatun <u>dh</u>u\'af<u>aa</u>u fa-a<u>shaa</u>bah<u>aa</u> i\'<u>shaa</u>run fiihi n<u>aa</u>run fa<u>h</u>taraqat ka<u>dzaa</u>lika yubayyinull<u>aa</u>hu lakumu l-<u>aa</u>y<u>aa</u>ti\r la\'allakum tatafakkaruun\r\r\n');
INSERT INTO quranLatin VALUES (274, '2:267', 2, 267, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu anfiquu min <u>th</u>ayyib<u>aa</u>ti m<u>aa</u> kasabtum wamimm<u>aa</u> akhrajn<u>aa</u> lakum mina l-ar<u>dh</u>i wal<u>aa</u> tayammamuu lkhabiitsa minhu tunfiquuna walastum bi-<u>aa</u>khi<u>dz</u>iihi ill<u>aa</u> an tughmi<u>dh</u>uu fiihi wa\'lamuu annall<u>aa</u>ha ghaniyyun <u>h</u>amiid\r \r\r\n');
INSERT INTO quranLatin VALUES (275, '2:268', 2, 268, 'asysyay<u>th</u><u>aa</u>nu ya\'idukumu lfaqra waya/murukum bilfa<u>h</u>sy<u>aa</u>-i wal<u>aa</u>hu ya\'idukum maghfiratan minhu wafa<u>dh</u>lan wal<u>aa</u>hu w<u>aa</u>si\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (276, '2:269', 2, 269, 'yu/tii l<u>h</u>ikmata man yasy<u>aa</u>u waman yu/ta l<u>h</u>ikmata faqad uutiya khayran katsiiran wam<u>aa</u> ya<u>dzdz</u>akkaru ill<u>aa</u> uluu l-alb<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (277, '2:270', 2, 270, 'wam<u>aa</u> anfaqtum min nafaqatin aw na<u>dz</u>artum min na<u>dz</u>rin fa-innall<u>aa</u>ha ya\'lamuhu wam<u>aa</u> li<u>zhzhaa</u>limiina min an<u>shaa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (278, '2:271', 2, 271, 'in tubduu <u>shsh</u>adaq<u>aa</u>ti fani\'imm<u>aa</u> hiya wa-in tukhfuuh<u>aa</u> watu/tuuh<u>aa</u> lfuqar<u>aa</u>-a fahuwa khayrun lakum wayukaffiru \'ankum min sayyi-<u>aa</u>tikum wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna khabiir\r\r\n');
INSERT INTO quranLatin VALUES (279, '2:272', 2, 272, 'laysa \'alayka hud<u>aa</u>hum wal<u>aa</u>kinnall<u>aa</u>ha yahdii man yasy<u>aa</u>u wam<u>aa</u> tunfiquu min khayrin fali-anfusikum wam<u>aa</u> tunfiquuna ill<u>aa</u> ibtigh<u>aa</u>-a wajhill<u>aa</u>hi wam<u>aa</u> tunfiquu min khayrin yuwaffa ilaykum wa-antum l<u>aa</u> tu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (280, '2:273', 2, 273, 'lilfuqar<u>aa</u>-illa<u>dz</u>iina u<u>hs</u>iruu fii sabiilill<u>aa</u>hi l<u>aa</u> yasta<u>th</u>ii\'uuna <u>dh</u>arban fii l-ar<u>dh</u>i ya<u>h</u>sabuhumu lj<u>aa</u>hilu aghniy<u>aa</u>-a mina tta\'affufi ta\'rifuhum bisiim<u>aa</u>hum l<u>aa</u> yas-aluuna nn<u>aa</u>sa il<u>haa</u>fan wam<u>aa</u> tunfiquu min khayrin fa-innall<u>aa</u>ha bihi \'aliim\r\n');
INSERT INTO quranLatin VALUES (281, '2:274', 2, 274, '<B>al</b>la<u>dz</u>iina yunfiquuna amw<u>aa</u>lahum billayli wannah<u>aa</u>ri sirran wa\'al<u>aa</u>niyatan falahum ajruhum \'inda rabbihim wal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun\r\n');
INSERT INTO quranLatin VALUES (282, '2:275', 2, 275, '<B>al</b>la<u>dz</u>iina ya/kuluuna rrib<u>aa</u> l<u>aa</u> yaquumuuna ill<u>aa</u> kam<u>aa</u> yaquumulla<u>dz</u>ii yatakhabba<u>th</u>uhu sysyay<u>th</u><u>aa</u>nu mina lmassi <u>dzaa</u>lika bi-annahum q<u>aa</u>luu innam<u>aa</u> lbay\'u mitslu rrib<u>aa</u>\r wa-a<u>h</u>allall<u>aa</u>hu lbay\'a wa<u>h</u>arrama rrib<u>aa</u> faman j<u>aa</u>-ahu maw\'i<u>zh</u>atun min rabbihi fantah<u>aa</u> falahu m<u>aa</u> salafa wa-amruhu il<u>aa</u>ll<u>aa</u>hi waman \'<u>aa</u>da faul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (283, '2:276', 2, 276, 'yam<u>h</u>aqull<u>aa</u>hu rrib<u>aa</u> wayurbii <u>shsh</u>adaq<u>aa</u>ti wal<u>aa</u>hu l<u>aa</u> yu<u>h</u>ibbu kulla kaff<u>aa</u>rin atsiim\r\r\n');
INSERT INTO quranLatin VALUES (284, '2:277', 2, 277, 'innalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti wa-aq<u>aa</u>muu <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tawuu zzak<u>aa</u>ta lahum ajruhum \'inda rabbihim wal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (285, '2:278', 2, 278, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu ittaquull<u>aa</u>ha wa<u>dz</u>aruu m<u>aa</u> baqiya mina rrib<u>aa</u> in kuntum mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (286, '2:279', 2, 279, 'fa-in lam taf\'aluu fa/<u>dz</u>anuu bi<u>h</u>arbin minall<u>aa</u>hi warasuulihi wa-in tubtum falakum ruuusu amw<u>aa</u>likum l<u>aa</u> ta<u>zh</u>limuuna wal<u>aa</u> tu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (287, '2:280', 2, 280, 'wa-in k<u>aa</u>na <u>dz</u>uu \'usratin fana<u>zh</u>iratun il<u>aa</u> maysaratin wa-an ta<u>sh</u>addaquu khayrun lakum in kuntum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (288, '2:281', 2, 281, 'wattaquu yawman turja\'uuna fiihi il<u>aa</u>ll<u>aa</u>hi tsumma tuwaff<u>aa</u> kullu nafsin m<u>aa</u> kasabat wahum l<u>aa</u> yu<u>zh</u>lamuun  \r\n');
INSERT INTO quranLatin VALUES (289, '2:282', 2, 282, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> tad<u>aa</u>yantum bidaynin il<u>aa</u> ajalin musamman faktubuuhu walyaktub baynakum k<u>aa</u>tibun bil\'adli wal<u>aa</u> ya/ba k<u>aa</u>tibun an yaktuba kam<u>aa</u> \'allamahull<u>aa</u>hu falyaktub walyumlililla<u>dz</u>ii \'alayhi l<u>h</u>aqqu walyattaqill<u>aa</u>ha rabbahu wal<u>aa</u> yabkhas minhu syay-an fa-in k<u>aa</u>nalla<u>dz</u>ii \'alayhi l<u>h</u>aqqu safiihan aw <u>dh</u>a\'iifan aw l<u>aa</u> yasta<u>th</u>ii\'u an yumilla huwa falyumlil waliyyuhu bil\'adli wastasyhiduu syahiidayni min rij<u>aa</u>likum fa-in lam yakuun<u>aa</u> rajulayni farajulun wamra-at<u>aa</u>ni mimman tar<u>dh</u>awna mina sysyuhad<u>aa</u>-i an ta<u>dh</u>illa i<u>h</u>d<u>aa</u>hum<u>aa</u> fatu<u>dz</u>akkira i<u>h</u>d<u>aa</u>hum<u>aa</u> l-ukhr<u>aa</u> wal<u>aa</u> ya/ba sysyuhad<u>aa</u>u i<u>dzaa</u> m<u>aa</u> du\'uu wal<u>aa</u> tas-amuu an taktubuuhu <u>sh</u>aghiiran aw kabiiran il<u>aa</u> ajalihi <u>dzaa</u>likum aqsa<u>th</u>u \'indall<u>aa</u>hi wa-aqwamu lisysyah<u>aa</u>dati wa-adn<u>aa</u>ll<u>aa</u> tart<u>aa</u>buu ill<u>aa</u> an takuuna tij<u>aa</u>ratan <u>had</u>iratan tudiiruunah<u>aa</u> baynakum falaysa \'alaykum jun<u>aah</u>unll<u>aa</u> taktubuuh<u>aa</u> wa-asyhiduu i<u>dzaa</u> tab<u>aa</u>ya\'tum wal<u>aa</u> yu<u>daa</u>rra k<u>aa</u>tibun wal<u>aa</u> syahiidun wa-in taf\'aluu fa-innahu fusuuqun bikum wattaquull<u>aa</u>ha wayu\'allimukumull<u>aa</u>hu wal<u>aa</u>hu bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (290, '2:283', 2, 283, 'wa-in kuntum \'al<u>aa</u> safarin walam tajiduu k<u>aa</u>tiban farih<u>aa</u>nun maqbuu<u>dh</u>atun fa-in amina ba\'<u>dh</u>ukum ba\'<u>dh</u>an falyu-addilla<u>dz</u>ii<u> </u>i/tumina am<u>aa</u>natahu walyattaqill<u>aa</u>ha rabbahu wal<u>aa</u> taktumuu sysyah<u>aa</u>data waman yaktumh<u>aa</u> fa-innahu <u>aa</u>tsimun qalbuhu wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (291, '2:284', 2, 284, 'lill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wa-in tubduu m<u>aa</u> fii anfusikum aw tukhfuuhu yu<u>haa</u>sibkum bihill<u>aa</u>hu fayaghfiru liman yasy<u>aa</u>u wayu\'a<u>dzdz</u>ibu man yasy<u>aa</u>u wal<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (292, '2:285', 2, 285, '<u>aa</u>mana rrasuulu bim<u>aa</u> unzila ilayhi min rabbihi walmu/minuuna kullun <u>aa</u>mana bil<u>aa</u>hi wamal<u>aa</u>-ikatihi wakutubihi warusulihi l<u>aa</u> nufarriqu bayna a<u>h</u>adin min rusulihi waq<u>aa</u>luu sami\'n<u>aa</u> wa-a<u>th</u>a\'n<u>aa</u> ghufr<u>aa</u>naka rabban<u>aa</u> wa-ilayka lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (293, '2:286', 2, 286, 'l<u>aa</u> yukallifull<u>aa</u>hu nafsan ill<u>aa</u> wus\'ah<u>aa</u> lah<u>aa</u> m<u>aa</u> kasabat wa\'alayh<u>aa</u> m<u>aa</u> iktasabat rabban<u>aa</u> l<u>aa</u> tu-<u>aa</u>khi<u>dz</u>n<u>aa</u> in nasiin<u>aa</u> aw akh<u>th</u>a/n<u>aa</u> rabban<u>aa</u> wal<u>aa</u> ta<u>h</u>mil \'alayn<u>aa</u> i<u>sh</u>ran kam<u>aa</u> <u>h</u>amaltahu \'al<u>aa</u>lla<u>dz</u>iina min qablin<u>aa</u> rabban<u>aa</u> wal<u>aa</u> tu<u>h</u>ammiln<u>aa</u> m<u>aa</u> l<u>aa</u> <u>th</u><u>aa</u>qata lan<u>aa</u> bihi wa\'fu \'ann<u>aa</u> waghfir lan<u>aa</u> war<u>h</u>amn<u>aa</u> anta mawl<u>aa</u>n<u>aa</u> fan<u>sh</u>urn<u>aa</u> \'al<u>aa</u> lqawmi lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (294, '3:1', 3, 1, 'alif-l<u>aa</u>m-miim</u>\r\r\n');
INSERT INTO quranLatin VALUES (295, '3:2', 3, 2, '<B>al</b>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa l<u>h</u>ayyu lqayyuum\r\r\n');
INSERT INTO quranLatin VALUES (296, '3:3', 3, 3, 'nazzala \'alayka lkit<u>aa</u>ba bil<u>h</u>aqqi mu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi wa-anzala ttawr<u>aa</u>ta wal-injiil\r\r\n');
INSERT INTO quranLatin VALUES (297, '3:4', 3, 4, 'min qablu hudan linn<u>aa</u>si wa-anzala lfurq<u>aa</u>na innalla<u>dz</u>iina kafaruu bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi lahum \'a<u>dzaa</u>bun syadiidun wal<u>aa</u>hu \'aziizun <u>dz</u>uu intiq<u>aa</u>m\r\r\n');
INSERT INTO quranLatin VALUES (298, '3:5', 3, 5, 'innall<u>aa</u>ha l<u>aa</u> yakhf<u>aa</u> \'alayhi syay-un fii l-ar<u>dh</u>i wal<u>aa</u> fii ssam<u>aa</u>/-<b>i </b>\r\r\n');
INSERT INTO quranLatin VALUES (299, '3:6', 3, 6, 'huwalla<u>dz</u>ii yu<u>sh</u>awwirukum fii l-ar<u>haa</u>mi kayfa yasy<u>aa</u>u l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (300, '3:7', 3, 7, 'huwalla<u>dz</u>ii anzala \'alayka lkit<u>aa</u>ba minhu <u>aa</u>y<u>aa</u>tun mu<u>h</u>kam<u>aa</u>tun hunna ummu lkit<u>aa</u>bi waukharu mutasy<u>aa</u>bih<u>aa</u>tun fa-amm<u>aa</u>lla<u>dz</u>iina fii quluubihim zayghun fayattabi\'uuna m<u>aa</u> tasy<u>aa</u>baha minhu ibtigh<u>aa</u>-a lfitnati wabtigh<u>aa</u>-a ta/wiilihi wam<u>aa</u> ya\'lamu ta/wiilahu ill<u>aa</u>ll<u>aa</u>hu warr<u>aa</u>sikhuuna fii l\'ilmi yaquuluuna <u>aa</u>mann<u>aa</u> bihi kullun min \'indi rabbin<u>aa</u> wam<u>aa</u> ya<u>dzdz</u>akkaru ill<u>aa</u> uluu l-alb<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (301, '3:8', 3, 8, 'rabban<u>aa</u> l<u>aa</u> tuzigh quluuban<u>aa</u> ba\'da i<u>dz</u> hadaytan<u>aa</u> wahab lan<u>aa</u> min ladunka ra<u>h</u>matan innaka anta lwahh<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (302, '3:9', 3, 9, 'rabban<u>aa</u> innaka j<u>aa</u>mi\'u nn<u>aa</u>si liyawmin l<u>aa</u> rayba fiihi innall<u>aa</u>ha l<u>aa</u> yukhlifu lmii\'<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (303, '3:10', 3, 10, 'innalla<u>dz</u>iina kafaruu lan tughniya \'anhum amw<u>aa</u>luhum wal<u>aa</u> awl<u>aa</u>duhum minall<u>aa</u>hi syay-an waul<u>aa</u>-ika hum waquudu nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (304, '3:11', 3, 11, 'kada/bi <u>aa</u>li fir\'awna walla<u>dz</u>iina min qablihim ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fa-akha<u>dz</u>ahumull<u>aa</u>hu bi<u>dz</u>unuubihim wal<u>aa</u>hu syadiidu l\'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (305, '3:12', 3, 12, 'qul lilla<u>dz</u>iina kafaruu satughlabuuna watu<u>h</u>syaruuna il<u>aa</u> jahannama wabi/sa lmih<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (306, '3:13', 3, 13, 'qad k<u>aa</u>na lakum <u>aa</u>yatun fii fi-atayni iltaqat<u>aa</u> fi-atun tuq<u>aa</u>tilu fii sabiilill<u>aa</u>hi waukhr<u>aa</u> k<u>aa</u>firatun yarawnahum mitslayhim ra/ya l\'ayni wal<u>aa</u>hu yu-ayyidu bina<u>sh</u>rihi man yasy<u>aa</u>u inna fii <u>dzaa</u>lika la\'ibratan li-ulii l-ab<u>shaa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (307, '3:14', 3, 14, 'zuyyina linn<u>aa</u>si <u>h</u>ubbu sysyahaw<u>aa</u>ti mina nnis<u>aa</u>-i walbaniina walqan<u>aath</u>iiri lmuqan<u>th</u>arati mina <u>dzdz</u>ahabi walfi<u>dhdh</u>ati walkhayli lmusawwamati wal-an\'<u>aa</u>mi wal<u>h</u>artsi <u>dzaa</u>lika mat<u>aa</u>\'u l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wal<u>aa</u>hu \'indahu <u>h</u>usnu lma<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (308, '3:15', 3, 15, 'qul a-unabbi-ukum bikhayrin min <u>dzaa</u>likum lilla<u>dz</u>iina ittaqaw \'inda rabbihim jann<u>aa</u>tun tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wa-azw<u>aa</u>jun mu<u>th</u>ahharatun wari<u>dh</u>w<u>aa</u>nun minall<u>aa</u>hi wal<u>aa</u>hu ba<u>sh</u>iirun bil\'ib<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (309, '3:16', 3, 16, '<B>al</b>la<u>dz</u>iina yaquuluuna rabban<u>aa</u> innan<u>aa</u> <u>aa</u>mann<u>aa</u> faghfir lan<u>aa</u> <u>dz</u>unuuban<u>aa</u> waqin<u>aa</u> \'a<u>dzaa</u>ba nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (310, '3:17', 3, 17, 'a<u>shshaa</u>biriina wa<u>shshaa</u>diqiina walq<u>aa</u>nitiina walmunfiqiina walmustaghfiriina bil-as<u>haa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (311, '3:18', 3, 18, 'syahidall<u>aa</u>hu annahu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa walmal<u>aa</u>-ikatu wauluu l\'ilmi q<u>aa</u>-iman bilqis<u>th</u>i l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (312, '3:19', 3, 19, 'inna ddiina \'indall<u>aa</u>hi l-isl<u>aa</u>mu wam<u>aa</u> ikhtalafalla<u>dz</u>iina uutuu lkit<u>aa</u>ba ill<u>aa</u> min ba\'di m<u>aa</u> j<u>aa</u>-ahumu l\'ilmu baghyan baynahum waman yakfur bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi fa-innall<u>aa</u>ha sarii\'u l<u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (313, '3:20', 3, 20, 'fa-in <u>haa</u>jjuuka faqul aslamtu wajhiya lill<u>aa</u>hi wamani ittaba\'ani waqul lilla<u>dz</u>iina uutuu lkit<u>aa</u>ba wal<b>-</b>ummiyyiina a-aslamtum fa-in aslamuu faqadi ihtadaw wa-in tawallaw fa-innam<u>aa</u> \'alayka lbal<u>aa</u>ghu wal<u>aa</u>hu ba<u>sh</u>iirun bil\'ib<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (314, '3:21', 3, 21, 'innalla<u>dz</u>iina yakfuruuna bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi wayaqtuluuna nnabiyyiina bighayri <u>h</u>aqqin wayaqtuluunalla<u>dz</u>iina ya/muruuna bilqis<u>th</u>i mina nn<u>aa</u>si fabasysyirhum bi\'a<u>dzaa</u>bin liim\r\r\n');
INSERT INTO quranLatin VALUES (315, '3:22', 3, 22, 'ul<u>aa</u>-ikalla<u>dz</u>iina <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum fii dduny<u>aa</u> wal-<u>aa</u>khirati wam<u>aa</u> lahum min n<u>aas</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (316, '3:23', 3, 23, 'alam tara il<u>aa</u>lla<u>dz</u>iina uutuu na<u>sh</u>iiban mina lkit<u>aa</u>bi yud\'awna il<u>aa</u> kit<u>aa</u>bill<u>aa</u>hi liya<u>h</u>kuma baynahum tsumma yatawall<u>aa</u> fariiqun minhum wahum mu\'ri<u>dh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (317, '3:24', 3, 24, '<u>dzaa</u>lika bi-annahum q<u>aa</u>luu lan tamassan<u>aa</u> nn<u>aa</u>ru ill<u>aa</u> ayy<u>aa</u>man ma\'duud<u>aa</u>tin wagharrahum fii diinihim m<u>aa</u> k<u>aa</u>nuu yaftaruun\r\r\n');
INSERT INTO quranLatin VALUES (318, '3:25', 3, 25, 'fakayfa i<u>dzaa</u> jama\'n<u>aa</u>hum liyawmin l<u>aa</u> rayba fiihi wawuffiyat kullu nafsin m<u>aa</u> kasabat wahum l<u>aa</u> yu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (319, '3:26', 3, 26, 'qulill<u>aa</u>humma m<u>aa</u>lika lmulki tu/tii lmulka man tasy<u>aa</u>u watanzi\'u lmulka mimman tasy<u>aa</u>u watu\'izzu man tasy<u>aa</u>u watu<u>dz</u>illu man tasy<u>aa</u>u biyadika lkhayru innaka \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (320, '3:27', 3, 27, 'tuulijullayla fii nnah<u>aa</u>ri watuuliju nnah<u>aa</u>ra fiillayli watukhriju l<u>h</u>ayya mina lmayyiti watukhriju lmayyita mina l<u>h</u>ayyi watarzuqu man tasy<u>aa</u>u bighayri <u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (321, '3:28', 3, 28, 'l<u>aa</u> yattakhi<u>dz</u>i lmu/minuuna lk<u>aa</u>firiina awliy<u>aa</u>-a min duuni lmu/miniina waman yaf\'al <u>dzaa</u>lika falaysa minall<u>aa</u>hi fii syay-in ill<u>aa</u> an tattaquu minhum tuq<u>aa</u>tan wayu<u>h</u>a<u>dzdz</u>irukumull<u>aa</u>hu nafsahu wa-il<u>aa</u>ll<u>aa</u>hi lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (322, '3:29', 3, 29, 'qul in tukhfuu m<u>aa</u> fii <u>sh</u>uduurikum aw tubduuhu ya\'lamhull<u>aa</u>hu waya\'lamu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wal<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (323, '3:30', 3, 30, 'yawma tajidu kullu nafsin m<u>aa</u> \'amilat min khayrin mu<u>hd</u>aran wam<u>aa</u> \'amilat min suu-in tawaddu law anna baynah<u>aa</u> wabaynahu amadan ba\'iidan wayu<u>h</u>a<u>dzdz</u>irukumull<u>aa</u>hu nafsahu wal<u>aa</u>hu rauufun bil\'ib<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (324, '3:31', 3, 31, 'qul in kuntum tu<u>h</u>ibbuunall<u>aa</u>ha fattabi\'uunii yu<u>h</u>bibkumull<u>aa</u>hu wayaghfir lakum <u>dz</u>unuubakum wal<u>aa</u>hu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (325, '3:32', 3, 32, 'qul a<u>th</u>ii\'uull<u>aa</u>ha warrasuula fa-in tawallaw fa-innall<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (326, '3:33', 3, 33, 'innall<u>aa</u>ha i<u>sth</u>af<u>aa</u> <u>aa</u>dama wanuu<u>h</u>an wa<u>aa</u>la ibr<u>aa</u>hiima wa<u>aa</u>la \'imr<u>aa</u>na \'al<u>aa</u> l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (327, '3:34', 3, 34, '<u>dz</u>urriyyatan ba\'<u>dh</u>uh<u>aa</u> min ba\'<u>dh</u>in wal<u>aa</u>hu samii\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (328, '3:35', 3, 35, 'i<u>dz</u> q<u>aa</u>lati imra-atu \'imr<u>aa</u>na rabbi innii na<u>dz</u>artu laka m<u>aa</u> fii ba<u>th</u>nii mu<u>h</u>arraran fataqabbal minnii innaka anta ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (329, '3:36', 3, 36, 'falamm<u>aa</u> wa<u>dh</u>a\'at-h<u>aa</u> q<u>aa</u>lat rabbi innii wa<u>dh</u>a\'tuh<u>aa</u> unts<u>aa</u> wal<u>aa</u>hu a\'lamu bim<u>aa</u> wa<u>dh</u>a\'at walaysa <u>dzdz</u>akaru kalunts<u>aa</u> wa-innii sammaytuh<u>aa</u> maryama wa-innii u\'ii<u>dz</u>uh<u>aa</u> bika wa<u>dz</u>urriyyatah<u>aa</u> mina sysyay<u>th</u><u>aa</u>ni rrajiim\r\r\n');
INSERT INTO quranLatin VALUES (330, '3:37', 3, 37, 'fataqabbalah<u>aa</u> rabbuh<u>aa</u> biqabuulin <u>h</u>asanin wa-anbatah<u>aa</u> nab<u>aa</u>tan <u>h</u>asanan wakaffalah<u>aa</u> zakariyy<u>aa</u> kullam<u>aa</u> dakhala \'alayh<u>aa</u> zakariyy<u>aa</u> lmi<u>h</u>r<u>aa</u>ba wajada \'indah<u>aa</u> rizqan q<u>aa</u>la y<u>aa</u> maryamu ann<u>aa</u> laki h<u>aadzaa</u> q<u>aa</u>lat huwa min \'indill<u>aa</u>hi innall<u>aa</u>ha yarzuqu man yasy<u>aa</u>u bighayri <u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (331, '3:38', 3, 38, 'hun<u>aa</u>lika da\'<u>aa</u> zakariyy<u>aa</u> rabbahu q<u>aa</u>la rabbi hab lii min ladunka <u>dz</u>urriyyatan <u>th</u>ayyibatan innaka samii\'u ddu\'<u>aa</u>/-\r\r\n');
INSERT INTO quranLatin VALUES (332, '3:39', 3, 39, 'fan<u>aa</u>dat-hu lmal<u>aa</u>-ikatu wahuwa q<u>aa</u>-imun yu<u>sh</u>allii fii lmi<u>h</u>r<u>aa</u>bi annall<u>aa</u>ha yubasysyiruka biya<u>h</u>y<u>aa</u> mu<u>sh</u>addiqan bikalimatin minall<u>aa</u>hi wasayyidan wa<u>h</u>a<u>sh</u>uuran wanabiyyan mina <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (333, '3:40', 3, 40, 'q<u>aa</u>la rabbi ann<u>aa</u> yakuunu lii ghul<u>aa</u>mun waqad balaghaniya lkibaru wamra-atii \'<u>aa</u>qirun q<u>aa</u>la ka<u>dzaa</u>likall<u>aa</u>hu yaf\'alu m<u>aa</u> yasy<u>aa</u>/\r\r\n');
INSERT INTO quranLatin VALUES (334, '3:41', 3, 41, 'q<u>aa</u>la rabbi ij\'al lii <u>aa</u>yatan q<u>aa</u>la <u>aa</u>yatukall<u>aa</u> tukallima nn<u>aa</u>sa tsal<u>aa</u>tsata ayy<u>aa</u>min ill<u>aa</u> ramzan wa<u>dz</u>kur rabbaka katsiiran wasabbi<u>h</u> bil\'asyiyyi wal-ibk<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (335, '3:42', 3, 42, 'wa-i<u>dz</u> q<u>aa</u>lati lmal<u>aa</u>-ikatu y<u>aa</u> maryamu innall<u>aa</u>ha i<u>sth</u>af<u>aa</u>ki wa<u>th</u>ahharaki wa<u>sth</u>af<u>aa</u>ki \'al<u>aa</u> nis<u>aa</u>-i l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (336, '3:43', 3, 43, 'y<u>aa</u> maryamu uqnutii lirabbiki wasjudii warka\'ii ma\'a rr<u>aa</u>ki\'iin\r\r\n');
INSERT INTO quranLatin VALUES (337, '3:44', 3, 44, '<u>dzaa</u>lika min anb<u>aa</u>-i lghaybi nuu<u>h</u>iihi ilayka wam<u>aa</u> kunta ladayhim i<u>dz</u> yulquuna aql<u>aa</u>mahum ayyuhum yakfulu maryama wam<u>aa</u> kunta ladayhim i<u>dz</u> yakhta<u>sh</u>imuun\r\r\n');
INSERT INTO quranLatin VALUES (338, '3:45', 3, 45, 'i<u>dz</u> q<u>aa</u>lati lmal<u>aa</u>-ikatu y<u>aa</u> maryamu innall<u>aa</u>ha yubasysyiruki bikalimatin minhu ismuhu lmasii<u>h</u>u \'iis<u>aa</u> ibnu maryama wajiihan fii dduny<u>aa</u> wal-<u>aa</u>khirati wamina lmuqarrabiin\r\r\n');
INSERT INTO quranLatin VALUES (339, '3:46', 3, 46, 'wayukallimu nn<u>aa</u>sa fii lmahdi wakahlan wamina <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (340, '3:47', 3, 47, 'q<u>aa</u>lat rabbi ann<u>aa</u> yakuunu lii waladun walam yamsasnii basyarun q<u>aa</u>la ka<u>dzaa</u>likill<u>aa</u>hu yakhluqu m<u>aa</u> yasy<u>aa</u>u i<u>dzaa</u> qa<u>daa</u> amran fa-innam<u>aa</u> yaquulu lahu kun fayakuun\r\r\n');
INSERT INTO quranLatin VALUES (341, '3:48', 3, 48, 'wayu\'allimuhu lkit<u>aa</u>ba wal<u>h</u>ikmata wattawr<u>aa</u>ta wal-injiil\r\r\n');
INSERT INTO quranLatin VALUES (342, '3:49', 3, 49, 'warasuulan il<u>aa</u> banii isr<u>aa</u>-iila annii qad ji/tukum bi-<u>aa</u>yatin min rabbikum annii akhluqu lakum mina <u>ththh</u>iini kahay-ati <u>ththh</u>ayri fa-anfukhu fiihi fayakuunu <u>th</u>ayran bi-i<u>dz</u>nill<u>aa</u>hi waubri-u l-akmaha wal-abra<u>sh</u>a wau<u>h</u>yii lmawt<u>aa</u> bi-i<u>dz</u>nill<u>aa</u>hi wa-unabbi-ukum bim<u>aa</u> ta/kuluuna wam<u>aa</u> taddakhiruuna fii buyuutikum inna fii <u>dzaa</u>lika la<u>aa</u>yatan lakum in kuntum mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (343, '3:50', 3, 50, 'wamu<u>sh</u>addiqan lim<u>aa</u> bayna yadayya mina ttawr<u>aa</u>ti wali-u<u>h</u>illa lakum ba\'<u>dh</u>alla<u>dz</u>ii <u>h</u>urrima \'alaykum waji/tukum bi-<u>aa</u>yatin min rabbikum fattaquull<u>aa</u>ha wa-a<u>th</u>ii\'uun\r\r\n');
INSERT INTO quranLatin VALUES (344, '3:51', 3, 51, 'innall<u>aa</u>ha rabbii warabbukum fa\'buduuhu h<u>aadzaa</u> <u>sh</u>ir<u>aath</u>un mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (345, '3:52', 3, 52, 'falamm<u>aa</u> a<u>h</u>assa \'iis<u>aa</u> minhumu lkufra q<u>aa</u>la man an<u>shaa</u>rii il<u>aa</u>ll<u>aa</u>hi q<u>aa</u>la l<u>h</u>aw<u>aa</u>riyyuuna na<u>h</u>nu an<u>shaa</u>rull<u>aa</u>hi <u>aa</u>mann<u>aa</u> bil<u>aa</u>hi wasyhad bi-ann<u>aa</u> muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (346, '3:53', 3, 53, 'rabban<u>aa</u> <u>aa</u>mann<u>aa</u> bim<u>aa</u> anzalta wattaba\'n<u>aa</u> rrasuula faktubn<u>aa</u> ma\'a sysy<u>aa</u>hidiin\r\r\n');
INSERT INTO quranLatin VALUES (347, '3:54', 3, 54, 'wamakaruu wamakarall<u>aa</u>hu wal<u>aa</u>hu khayru lm<u>aa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (348, '3:55', 3, 55, 'i<u>dz</u> q<u>aa</u>lall<u>aa</u>hu y<u>aa</u> \'iis<u>aa</u> innii mutawaffiika war<u>aa</u>fi\'uka ilayya wamu<u>th</u>ahhiruka minalla<u>dz</u>iina kafaruu waj<u>aa</u>\'ilulla<u>dz</u>iina ittaba\'uuka fawqalla<u>dz</u>iina kafaruu il<u>aa</u> yawmi lqiy<u>aa</u>mati tsumma ilayya marji\'ukum fa-a<u>h</u>kumu baynakum fiim<u>aa</u> kuntum fiihi takhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (349, '3:56', 3, 56, 'fa-amm<u>aa</u>lla<u>dz</u>iina kafaruu fau\'a<u>dzdz</u>ibuhum \'a<u>dzaa</u>ban syadiidan fii dduny<u>aa</u> wal-<u>aa</u>khirati wam<u>aa</u> lahum min n<u>aas</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (350, '3:57', 3, 57, 'wa-amm<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti fayuwaffiihim ujuurahum wal<u>aa</u>hu l<u>aa</u> yu<u>h</u>ibbu <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (351, '3:58', 3, 58, '<u>dzaa</u>lika natluuhu \'alayka mina l-<u>aa</u>y<u>aa</u>ti wa<u>dzdz</u>ikri l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (352, '3:59', 3, 59, 'inna matsala \'iis<u>aa</u> \'indall<u>aa</u>hi kamatsali <u>aa</u>dama khalaqahu min tur<u>aa</u>bin tsumma q<u>aa</u>la lahu kun fayakuun\r\r\n');
INSERT INTO quranLatin VALUES (353, '3:60', 3, 60, 'al<u>h</u>aqqu min rabbika fal<u>aa</u> takun mina lmumtariin\r\r\n');
INSERT INTO quranLatin VALUES (354, '3:61', 3, 61, 'faman <u>haa</u>jjaka fiihi min ba\'di m<u>aa</u> j<u>aa</u>-aka mina l\'ilmi faqul ta\'<u>aa</u>law nad\'u abn<u>aa</u>-an<u>aa</u> wa-abn<u>aa</u>-akum wanis<u>aa</u>-an<u>aa</u> wanis<u>aa</u>-akum wa-anfusan<u>aa</u> wa-anfusakum tsumma nabtahil fanaj\'al la\'natall<u>aa</u>hi \'al<u>aa</u> lk<u>aadz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (355, '3:62', 3, 62, 'inna h<u>aadzaa</u> lahuwa lqa<u>sh</u>a<u>sh</u>u l<u>h</u>aqqu wam<u>aa</u> min il<u>aa</u>hin ill<u>aa</u>ll<u>aa</u>hu wa-innall<u>aa</u>ha lahuwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (356, '3:63', 3, 63, 'fa-in tawallaw fa-innall<u>aa</u>ha \'aliimun bilmufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (357, '3:64', 3, 64, 'qul y<u>aa</u> ahla lkit<u>aa</u>bi ta\'<u>aa</u>law il<u>aa</u> kalimatin saw<u>aa</u>-in baynan<u>aa</u> wabaynakumll<u>aa</u> na\'buda ill<u>aa</u>ll<u>aa</u>ha wal<u>aa</u> nusyrika bihi syay-an wal<u>aa</u> yattakhi<u>dz</u>a ba\'<u>dh</u>un<u>aa</u> ba\'<u>dh</u>an arb<u>aa</u>ban min duunill<u>aa</u>hi fa-in tawallaw faquuluu isyhaduu bi-ann<u>aa</u> muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (358, '3:65', 3, 65, 'y<u>aa</u> ahla lkit<u>aa</u>bi lima tu<u>haa</u>jjuuna fii ibr<u>aa</u>hiima wam<u>aa</u> unzilati ttawr<u>aa</u>tu wal-injiilu ill<u>aa</u> min ba\'dihi afal<u>aa</u> ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (359, '3:66', 3, 66, 'h<u>aa</u> antum h<u>aa</u>ul<u>aa</u>-i <u>haa</u>jajtum fiim<u>aa</u> lakum bihi \'ilmun falima tu<u>haa</u>jjuuna fiim<u>aa</u> laysa lakum bihi \'ilmun wal<u>aa</u>hu ya\'lamu wa-antum l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (360, '3:67', 3, 67, 'm<u>aa</u> k<u>aa</u>na ibr<u>aa</u>hiimu yahuudiyyan wal<u>aa</u> na<u>sh</u>r<u>aa</u>niyyan wal<u>aa</u>kin k<u>aa</u>na <u>h</u>aniifan musliman wam<u>aa</u> k<u>aa</u>na mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (361, '3:68', 3, 68, 'inna awl<u>aa</u> nn<u>aa</u>si bi-ibr<u>aa</u>hiima lalla<u>dz</u>iina ittaba\'uuhu wah<u>aadzaa</u> nnabiyyu walla<u>dz</u>iina <u>aa</u>manuu wal<u>aa</u>hu waliyyu lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (362, '3:69', 3, 69, 'waddat <u>th</u><u>aa</u>-ifatun min ahli lkit<u>aa</u>bi law yu<u>dh</u>illuunakum wam<u>aa</u> yu<u>dh</u>illuuna ill<u>aa</u> anfusahum wam<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (363, '3:70', 3, 70, 'y<u>aa</u> ahla lkit<u>aa</u>bi lima takfuruuna bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi wa-antum tasyhaduun\r\r\n');
INSERT INTO quranLatin VALUES (364, '3:71', 3, 71, 'y<u>aa</u> ahla lkit<u>aa</u>bi lima talbisuuna l<u>h</u>aqqa bilb<u>aath</u>ili wataktumuuna l<u>h</u>aqqa wa-antum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (365, '3:72', 3, 72, 'waq<u>aa</u>lat <u>th</u><u>aa</u>-ifatun min ahli lkit<u>aa</u>bi <u>aa</u>minuu billa<u>dz</u>ii unzila \'al<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu wajha nnah<u>aa</u>ri wakfuruu <u>aa</u>khirahu la\'allahum yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (366, '3:73', 3, 73, 'wal<u>aa</u> tu/minuu ill<u>aa</u> liman tabi\'a diinakum qul inna lhud<u>aa</u> hud<u>aa</u>ll<u>aa</u>hi an yu/t<u>aa</u> a<u>h</u>adun mitsla m<u>aa</u> uutiitum aw yu<u>haa</u>jjuukum \'inda rabbikum qul inna lfa<u>dh</u>la biyadill<u>aa</u>hi yu/tiihi man yasy<u>aa</u>u wal<u>aa</u>hu w<u>aa</u>si\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (367, '3:74', 3, 74, 'yakhta<u>shsh</u>u bira<u>h</u>matihi man yasy<u>aa</u>u wal<u>aa</u>hu <u>dz</u>uu lfa<u>dh</u>li l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (368, '3:75', 3, 75, 'wamin ahli lkit<u>aa</u>bi man in ta/manhu biqin<u>th</u><u>aa</u>rin yu-addihi ilayka waminhum man in ta/manhu bidiin<u>aa</u>rin l<u>aa</u> yu-addihi ilayka ill<u>aa</u> m<u>aa</u> dumta \'alayhi q<u>aa</u>-iman <u>dzaa</u>lika bi-annahum q<u>aa</u>luu laysa \'alayn<u>aa</u> fii l-ummiyyiina sabiilun wayaquuluuna \'al<u>aa</u>ll<u>aa</u>hi lka<u>dz</u>iba wahum ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (369, '3:76', 3, 76, 'bal<u>aa</u> man awf<u>aa</u> bi\'ahdihi wattaq<u>aa</u> fa-innall<u>aa</u>ha yu<u>h</u>ibbu lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (370, '3:77', 3, 77, 'innalla<u>dz</u>iina yasytaruuna bi\'ahdill<u>aa</u>hi wa-aym<u>aa</u>nihim tsamanan qaliilan ul<u>aa</u>-ika l<u>aa</u> khal<u>aa</u>qa lahum fii l-<u>aa</u>khirati wal<u>aa</u> yukallimuhumull<u>aa</u>hu wal<u>aa</u> yan<u>zh</u>uru ilayhim yawma lqiy<u>aa</u>mati wal<u>aa</u> yuzakkiihim walahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (371, '3:78', 3, 78, 'wa-inna minhum lafariiqan yalwuuna lsinatahum bilkit<u>aa</u>bi lita<u>h</u>sabuuhu mina lkit<u>aa</u>bi wam<u>aa</u> huwa mina lkit<u>aa</u>bi wayaquuluuna huwa min \'indill<u>aa</u>hi wam<u>aa</u> huwa min \'indill<u>aa</u>hi wayaquuluuna \'al<u>aa</u>ll<u>aa</u>hi lka<u>dz</u>iba wahum ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (372, '3:79', 3, 79, 'm<u>aa</u> k<u>aa</u>na libasyarin an yu/tiyahull<u>aa</u>hu lkit<u>aa</u>ba wal<u>h</u>ukma wannubuwwata tsumma yaquula linn<u>aa</u>si kuunuu \'ib<u>aa</u>dan lii min duunill<u>aa</u>hi wal<u>aa</u>kin kuunuu rabb<u>aa</u>niyyiina bim<u>aa</u> kuntum tu\'allimuuna lkit<u>aa</u>ba wabim<u>aa</u> kuntum tadrusuun\r\r\n');
INSERT INTO quranLatin VALUES (373, '3:80', 3, 80, 'wal<u>aa</u> ya/murakum an tattakhi<u>dz</u>uu lmal<u>aa</u>-ikata wannabiyyiina arb<u>aa</u>ban aya/murukum bilkufri ba\'da i<u>dz</u> antum muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (374, '3:81', 3, 81, 'wa-i<u>dz</u> akha<u>dz</u>all<u>aa</u>hu miits<u>aa</u>qa nnabiyyiina lam<u>aa</u> <u>aa</u>taytukum min kit<u>aa</u>bin wa<u>h</u>ikmatin tsumma j<u>aa</u>-akum rasuulun mu<u>sh</u>addiqun lim<u>aa</u> ma\'akum latu/minunna bihi walatan<u>sh</u>urunnahu q<u>aa</u>la a-aqrartum wa-akha<u>dz</u>tum \'al<u>aa</u> <u>dzaa</u>likum i<u>sh</u>rii q<u>aa</u>luu aqrarn<u>aa</u> q<u>aa</u>la fasyhaduu wa-an<u>aa</u> ma\'akum mina sysy<u>aa</u>hidiin\r\r\n');
INSERT INTO quranLatin VALUES (375, '3:82', 3, 82, 'faman tawall<u>aa</u> ba\'da <u>dzaa</u>lika faul<u>aa</u>-ika humu lf<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (376, '3:83', 3, 83, 'afaghayra diinill<u>aa</u>hi yabghuuna walahu aslama man fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i <u>th</u>aw\'an wakarhan wa-ilayhi yurja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (377, '3:84', 3, 84, 'qul <u>aa</u>mann<u>aa</u> bil<u>aa</u>hi wam<u>aa</u> unzila \'alayn<u>aa</u> wam<u>aa</u> unzila \'al<u>aa</u> ibr<u>aa</u>hiima wa-ism<u>aa</u>\'iila wa-is<u>haa</u>qa waya\'quuba wal-asb<u>aath</u>i wam<u>aa</u> uutiya muus<u>aa</u> wa\'iis<u>aa</u> wannabiyyuuna min rabbihim l<u>aa</u> nufarriqu bayna a<u>h</u>adin minhum wana<u>h</u>nu lahu muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (378, '3:85', 3, 85, 'waman yabtaghi ghayra l-isl<u>aa</u>mi diinan falan yuqbala minhu wahuwa fii l-<u>aa</u>khirati mina lkh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (379, '3:86', 3, 86, 'kayfa yahdiill<u>aa</u>hu qawman kafaruu ba\'da iim<u>aa</u>nihim wasyahiduu anna rrasuula <u>h</u>aqqun waj<u>aa</u>-ahumu lbayyin<u>aa</u>tu wal<u>aa</u>hu l<u>aa</u> yahdii lqawma <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (380, '3:87', 3, 87, 'ul<u>aa</u>-ika jaz<u>aa</u>uhum anna \'alayhim la\'natall<u>aa</u>hi walmal<u>aa</u>-ikati wann<u>aa</u>si ajma\'iin\r\r\n');
INSERT INTO quranLatin VALUES (381, '3:88', 3, 88, 'kh<u>aa</u>lidiina fiih<u>aa</u> l<u>aa</u> yukhaffafu \'anhumu l\'a<u>dzaa</u>bu wal<u>aa</u> hum yun<u>zh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (382, '3:89', 3, 89, 'ill<u>aa</u>lla<u>dz</u>iina t<u>aa</u>buu min ba\'di <u>dzaa</u>lika wa-a<u>sh</u>la<u>h</u>uu fa-innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (383, '3:90', 3, 90, 'innalla<u>dz</u>iina kafaruu ba\'da iim<u>aa</u>nihim tsumma izd<u>aa</u>duu kufran lan tuqbala tawbatuhum waul<u>aa</u>-ika humu <u>dhdhaa</u>lluun\r\r\n');
INSERT INTO quranLatin VALUES (384, '3:91', 3, 91, 'innalla<u>dz</u>iina kafaruu wam<u>aa</u>tuu wahum kuff<u>aa</u>run falan yuqbala min a<u>h</u>adihim milu l-ar<u>dh</u>i <u>dz</u>ahaban walawi iftad<u>aa</u> bihi ul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun liimun wam<u>aa</u> lahum min n<u>aas</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (385, '3:92', 3, 92, 'lan tan<u>aa</u>luu lbirra <u>h</u>att<u>aa</u> tunfiquu mimm<u>aa</u> tu<u>h</u>ibbuuna wam<u>aa</u> tunfiquu min syay-in fa-innall<u>aa</u>ha bihi \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (386, '3:93', 3, 93, 'kullu <u>ththh</u>a\'<u>aa</u>mi k<u>aa</u>na <u>h</u>illan libanii isr<u>aa</u>-iila ill<u>aa</u> m<u>aa</u> <u>h</u>arrama isr<u>aa</u>-iilu \'al<u>aa</u> nafsihi min qabli an tunazzala ttawr<u>aa</u>tu qul fa/tuu bittawr<u>aa</u>ti fatluuh<u>aa</u> in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (387, '3:94', 3, 94, 'famani iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi lka<u>dz</u>iba min ba\'di <u>dzaa</u>lika faul<u>aa</u>-ika humu <u>zhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (388, '3:95', 3, 95, 'qul <u>sh</u>adaqall<u>aa</u>hu fattabi\'uu millata ibr<u>aa</u>hiima <u>h</u>aniifan wam<u>aa</u> k<u>aa</u>na mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (389, '3:96', 3, 96, 'inna awwala baytin wu<u>dh</u>i\'a linn<u>aa</u>si lalla<u>dz</u>ii bibakkata mub<u>aa</u>rakan wahudan lil\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (390, '3:97', 3, 97, 'fiihi <u>aa</u>y<u>aa</u>tun bayyin<u>aa</u>tun maq<u>aa</u>mu ibr<u>aa</u>hiima waman dakhalahu k<u>aa</u>na <u>aa</u>minan walill<u>aa</u>hi \'al<u>aa</u> nn<u>aa</u>si <u>h</u>ijju lbayti mani ista<u>th</u><u>aa</u>\'a ilayhi sabiilan waman kafara fa-innall<u>aa</u>ha ghaniyyun \'ani l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (391, '3:98', 3, 98, 'qul y<u>aa</u> ahla lkit<u>aa</u>bi lima takfuruuna bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi wal<u>aa</u>hu syahiidun \'al<u>aa</u> m<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (392, '3:99', 3, 99, 'qul y<u>aa</u> ahla lkit<u>aa</u>bi lima ta<u>sh</u>udduuna \'an sabiilill<u>aa</u>hi man <u>aa</u>mana tabghuunah<u>aa</u> \'iwajan wa-antum syuhad<u>aa</u>u wam<u>aa</u>ll<u>aa</u>hu bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (393, '3:100', 3, 100, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu in tu<u>th</u>ii\'uu fariiqan minalla<u>dz</u>iina uutuu lkit<u>aa</u>ba yarudduukum ba\'da iim<u>aa</u>nikum k<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (394, '3:101', 3, 101, 'wakayfa takfuruuna wa-antum tutl<u>aa</u> \'alaykum <u>aa</u>y<u>aa</u>tull<u>aa</u>hi wafiikum rasuuluhu waman ya\'ta<u>sh</u>im bil<u>aa</u>hi faqad hudiya il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (395, '3:102', 3, 102, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu ittaquull<u>aa</u>ha <u>h</u>aqqa tuq<u>aa</u>tihi wal<u>aa</u> tamuutunna ill<u>aa</u> wa-antum muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (396, '3:103', 3, 103, 'wa\'ta<u>sh</u>imuu bi<u>h</u>ablill<u>aa</u>hi jamii\'an wal<u>aa</u> tafarraquu wa<u>dz</u>kuruu ni\'matall<u>aa</u>hi \'alaykum i<u>dz</u> kuntum a\'d<u>aa</u>-an fa-allafa bayna quluubikum fa-a<u>sh</u>ba<u>h</u>tum bini\'matihi ikhw<u>aa</u>nan wakuntum \'al<u>aa</u> syaf<u>aa</u> <u>h</u>ufratin mina nn<u>aa</u>ri fa-anqa<u>dz</u>akum minh<u>aa</u> ka<u>dzaa</u>lika yubayyinull<u>aa</u>hu lakum <u>aa</u>y<u>aa</u>tihi la\'allakum tahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (397, '3:104', 3, 104, 'waltakun minkum ummatun yad\'uuna il<u>aa</u> lkhayri waya/muruuna bilma\'ruufi wayanhawna \'ani lmunkari waul<u>aa</u>-ika humu lmufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (398, '3:105', 3, 105, 'wal<u>aa</u> takuunuu kalla<u>dz</u>iina tafarraquu wakhtalafuu min ba\'di m<u>aa</u> j<u>aa</u>-ahumu lbayyin<u>aa</u>tu waul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (399, '3:106', 3, 106, 'yawma tabya<u>dhdh</u>u wujuuhun wataswaddu wujuuhun fa-amm<u>aa</u>lla<u>dz</u>iina iswaddat wujuuhuhum akafartum ba\'da iim<u>aa</u>nikum fa<u>dz</u>uuquu l\'a<u>dzaa</u>ba bim<u>aa</u> kuntum takfuruun\r\r\n');
INSERT INTO quranLatin VALUES (400, '3:107', 3, 107, 'wa-amm<u>aa</u>lla<u>dz</u>iina ibya<u>dhdh</u>at wujuuhuhum fafii ra<u>h</u>matill<u>aa</u>hi hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (401, '3:108', 3, 108, 'tilka <u>aa</u>y<u>aa</u>tull<u>aa</u>hi natluuh<u>aa</u> \'alayka bil<u>h</u>aqqi wam<u>aa</u>ll<u>aa</u>hu yuriidu <u>zh</u>ulman lil\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (402, '3:109', 3, 109, 'walill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wa-il<u>aa</u>ll<u>aa</u>hi turja\'u l-umuur\r\r\n');
INSERT INTO quranLatin VALUES (403, '3:110', 3, 110, 'kuntum khayra ummatin ukhrijat linn<u>aa</u>si ta/muruuna bilma\'ruufi watanhawna \'ani lmunkari watu/minuuna bil<u>aa</u>hi walaw <u>aa</u>mana ahlu lkit<u>aa</u>bi lak<u>aa</u>na khayran lahum minhumu lmu/minuuna wa-aktsaruhumu lf<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (404, '3:111', 3, 111, 'lan ya<u>dh</u>urruukum ill<u>aa</u> a<u>dz</u>an wa-in yuq<u>aa</u>tiluukum yuwalluukumu l-adb<u>aa</u>ra tsumma l<u>aa</u> yun<u>sh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (405, '3:112', 3, 112, '<u>dh</u>uribat \'alayhimu <u>dzdz</u>illatu ayna m<u>aa</u> tsuqifuu ill<u>aa</u> bi<u>h</u>ablin minall<u>aa</u>hi wa<u>h</u>ablin mina nn<u>aa</u>si wab<u>aa</u>uu bigha<u>dh</u>abin minall<u>aa</u>hi wa<u>dh</u>uribat \'alayhimu lmaskanatu <u>dzaa</u>lika bi-annahum k<u>aa</u>nuu yakfuruuna bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi wayaqtuluuna l-anbiy<u>aa</u>-a bighayri <u>h</u>aqqin <u>dzaa</u>lika bim<u>aa</u> \'a<u>sh</u>aw wak<u>aa</u>nuu ya\'taduun\r\r\n');
INSERT INTO quranLatin VALUES (406, '3:113', 3, 113, 'laysuu saw<u>aa</u>-an min ahli lkit<u>aa</u>bi ummatun q<u>aa</u>-imatun yatluuna <u>aa</u>y<u>aa</u>till<u>aa</u>hi <u>aa</u>n<u>aa</u>-allayli wahum yasjuduun\r\r\n');
INSERT INTO quranLatin VALUES (407, '3:114', 3, 114, 'yu/minuuna bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri waya/muruuna bilma\'ruufi wayanhawna \'ani lmunkari wayus<u>aa</u>ri\'uuna fii lkhayr<u>aa</u>ti waul<u>aa</u>-ika mina <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (408, '3:115', 3, 115, 'wam<u>aa</u> yaf\'aluu min khayrin falan yukfaruuhu wal<u>aa</u>hu \'aliimun bilmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (409, '3:116', 3, 116, 'innalla<u>dz</u>iina kafaruu lan tughniya \'anhum amw<u>aa</u>luhum wal<u>aa</u> awl<u>aa</u>duhum minall<u>aa</u>hi syay-an waul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (410, '3:117', 3, 117, 'matsalu m<u>aa</u> yunfiquuna fii h<u>aadz</u>ihi l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> kamatsali rii<u>h</u>in fiih<u>aa</u> <u>sh</u>irrun a<u>shaa</u>bat <u>h</u>artsa qawmin <u>zh</u>alamuu anfusahum fa-ahlakat-hu wam<u>aa</u> <u>zh</u>alamahumull<u>aa</u>hu wal<u>aa</u>kin anfusahum ya<u>zh</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (411, '3:118', 3, 118, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattakhi<u>dz</u>uu bi<u>th</u><u>aa</u>natan min duunikum l<u>aa</u> ya/luunakum khab<u>aa</u>lan wadduu m<u>aa</u> \'anittum qad badati lbagh<u>daa</u>u min afw<u>aa</u>hihim wam<u>aa</u> tukhfii <u>sh</u>uduuruhum akbaru qad bayyann<u>aa</u> lakumu l-<u>aa</u>y<u>aa</u>ti in kuntum ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (412, '3:119', 3, 119, 'h<u>aa</u> antum ul<u>aa</u>-i tu<u>h</u>ibbuunahum wal<u>aa</u> yu<u>h</u>ibbuunakum watu/minuuna bilkit<u>aa</u>bi kullihi wa-i<u>dzaa</u> laquukum q<u>aa</u>luu <u>aa</u>mann<u>aa</u> wa-i<u>dzaa</u> khalaw \'a<u>dhdh</u>uu \'alaykumu l-an<u>aa</u>mila mina lghay<u>zh</u>i qul muutuu bighay<u>zh</u>ikum innall<u>aa</u>ha \'aliimun bi<u>dzaa</u>ti <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (413, '3:120', 3, 120, 'in tamsaskum <u>h</u>asanatun tasu/hum wa-in tu<u>sh</u>ibkum sayyi-atun yafra<u>h</u>uu bih<u>aa</u> wa-in ta<u>sh</u>biruu watattaquu l<u>aa</u> ya<u>dh</u>urrukum kayduhum syay-an innall<u>aa</u>ha bim<u>aa</u> ya\'maluuna mu<u>h</u>ii<u>th</u>\r\r\n');
INSERT INTO quranLatin VALUES (414, '3:121', 3, 121, 'wa-i<u>dz</u> ghadawta min ahlika tubawwi-u lmu/miniina maq<u>aa</u>\'ida lilqit<u>aa</u>li wal<u>aa</u>hu samii\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (415, '3:122', 3, 122, 'i<u>dz</u> hammat <u>th</u><u>aa</u>-ifat<u>aa</u>ni minkum an tafsyal<u>aa</u> wal<u>aa</u>hu waliyyuhum<u>aa</u> wa\'al<u>aa</u>ll<u>aa</u>hi falyatawakkali lmu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (416, '3:123', 3, 123, 'walaqad na<u>sh</u>arakumull<u>aa</u>hu bibadrin wa-antum a<u>dz</u>illatun fattaquull<u>aa</u>ha la\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (417, '3:124', 3, 124, 'i<u>dz</u> taquulu lilmu/miniina alan yakfiyakum an yumiddakum rabbukum bitsal<u>aa</u>tsati <u>aa</u>l<u>aa</u>fin mina almal<u>aa</u>-ikati munzaliin\r\r\n');
INSERT INTO quranLatin VALUES (418, '3:125', 3, 125, 'bal<u>aa</u> in ta<u>sh</u>biruu watattaquu waya/tuukum min fawrihim h<u>aadzaa</u> yumdidkum rabbukum bikhamsati <u>aa</u>l<u>aa</u>fin mina lmal<u>aa</u>-ikati musawwimiin\r\r\n');
INSERT INTO quranLatin VALUES (419, '3:126', 3, 126, 'wam<u>aa</u> ja\'alahull<u>aa</u>hu ill<u>aa</u> busyr<u>aa</u> lakum walita<u>th</u>ma-inna quluubukum bihi wam<u>aa</u> nna<u>sh</u>ru ill<u>aa</u> min \'indill<u>aa</u>hi l\'aziizi l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (420, '3:127', 3, 127, 'liyaq<u>th</u>a\'a <u>th</u>arafan minalla<u>dz</u>iina kafaruu aw yakbitahum fayanqalibuu kh<u>aa</u>-ibiin\r\r\n');
INSERT INTO quranLatin VALUES (421, '3:128', 3, 128, 'laysa laka mina l-amri syay-un aw yatuuba \'alayhim aw yu\'a<u>dzdz</u>ibahum fa-innahum <u>zhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (422, '3:129', 3, 129, 'walill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i yaghfiru liman yasy<u>aa</u>u wayu\'a<u>dzdz</u>ibu man yasy<u>aa</u>u wal<u>aa</u>hu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (423, '3:130', 3, 130, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> ta/kuluu rrib<u>aa</u> a<u>dh</u>\'<u>aa</u>fan mu<u>daa</u>\'afatan wattaquull<u>aa</u>ha la\'allakum tufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (424, '3:131', 3, 131, 'wattaquu nn<u>aa</u>rallatii u\'iddat lilk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (425, '3:132', 3, 132, 'wa-a<u>th</u>ii\'uull<u>aa</u>ha warrasuula la\'allakum tur<u>h</u>amuun\r\r\n');
INSERT INTO quranLatin VALUES (426, '3:133', 3, 133, 'was<u>aa</u>ri\'uu il<u>aa</u> maghfiratin min rabbikum wajannatin \'ar<u>dh</u>uh<u>aa</u> ssam<u>aa</u>w<u>aa</u>tu wal-ar<u>dh</u>u u\'iddat lilmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (427, '3:134', 3, 134, '<B>al</b>la<u>dz</u>iina yunfiquuna fii ssarr<u>aa</u>-i wa<u>dhdh</u>arr<u>aa</u>-i walk<u>aats</u>imiina lghay<u>zh</u>a wal\'<u>aa</u>fiina \'ani nn<u>aa</u>si wal<u>aa</u>hu yu<u>h</u>ibbu lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (428, '3:135', 3, 135, 'walla<u>dz</u>iina i<u>dzaa</u> fa\'aluu f<u>aah</u>isyatan aw <u>zh</u>alamuu anfusahum <u>dz</u>akaruull<u>aa</u>ha fastaghfaruu li<u>dz</u>unuubihim waman yaghfiru <u>dzdz</u>unuuba ill<u>aa</u>ll<u>aa</u>hu walam yu<u>sh</u>irruu \'al<u>aa</u> m<u>aa</u> fa\'aluu wahum ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (429, '3:136', 3, 136, 'ul<u>aa</u>-ika jaz<u>aa</u>uhum maghfiratun min rabbihim wajann<u>aa</u>tun tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wani\'ma ajru l\'<u>aa</u>miliin\r\r\n');
INSERT INTO quranLatin VALUES (430, '3:137', 3, 137, 'qad khalat min qablikum sunanun fasiiruu fii l-ar<u>dh</u>i fan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu lmuka<u>dzdz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (431, '3:138', 3, 138, 'h<u>aadzaa</u> bay<u>aa</u>nun linn<u>aa</u>si wahudan wamaw\'i<u>zh</u>atun lilmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (432, '3:139', 3, 139, 'wal<u>aa</u> tahinuu wal<u>aa</u> ta<u>h</u>zanuu wa-antumu l-a\'lawna in kuntum mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (433, '3:140', 3, 140, 'in yamsaskum qar<u>h</u>un faqad massa lqawma qar<u>h</u>un mitsluhu watilka l-ayy<u>aa</u>mu nud<u>aa</u>wiluh<u>aa</u> bayna nn<u>aa</u>si waliya\'lamall<u>aa</u>hulla<u>dz</u>iina <u>aa</u>manuu wayattakhi<u>dz</u>a minkum syuhad<u>aa</u>-a wal<u>aa</u>hu l<u>aa</u> yu<u>h</u>ibbu <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (434, '3:141', 3, 141, 'waliyuma<u>hh</u>i<u>sh</u>all<u>aa</u>hulla<u>dz</u>iina <u>aa</u>manuu wayam<u>h</u>aqa lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (435, '3:142', 3, 142, 'am <u>h</u>asibtum an tadkhuluu ljannata walamm<u>aa</u> ya\'lamill<u>aa</u>hulla<u>dz</u>iina j<u>aa</u>haduu minkum waya\'lama <u>shshaa</u>biriin\r\r\n');
INSERT INTO quranLatin VALUES (436, '3:143', 3, 143, 'walaqad kuntum tamannawna lmawta min qabli an talqawhu faqad ra-aytumuuhu wa-antum tan<u>zh</u>uruun\r\r\n');
INSERT INTO quranLatin VALUES (437, '3:144', 3, 144, 'wam<u>aa</u> mu<u>h</u>ammadun ill<u>aa</u> rasuulun qad khalat min qablihi rrusulu afa-in m<u>aa</u>ta aw qutila inqalabtum \'al<u>aa</u> a\'q<u>aa</u>bikum waman yanqalib \'al<u>aa</u> \'aqibayhi falan ya<u>dh</u>urrall<u>aa</u>ha syay-an wasayajziill<u>aa</u>hu sysy<u>aa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (438, '3:145', 3, 145, 'wam<u>aa</u> k<u>aa</u>na linafsin an tamuuta ill<u>aa</u> bi-i<u>dz</u>nill<u>aa</u>hi kit<u>aa</u>ban mu-ajjalan waman yurid tsaw<u>aa</u>ba dduny<u>aa</u> nu/tihi minh<u>aa</u> waman yurid tsaw<u>aa</u>ba l-<u>aa</u>khirati nu/tihi minh<u>aa</u> wasanajzii sysy<u>aa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (439, '3:146', 3, 146, 'waka-ayyin min nabiyyin q<u>aa</u>tala ma\'ahu ribbiyyuuna katsiirun fam<u>aa</u> wahanuu lim<u>aa</u> a<u>shaa</u>bahum fii sabiilill<u>aa</u>hi wam<u>aa</u> <u>dh</u>a\'ufuu wam<u>aa</u> istak<u>aa</u>nuu wal<u>aa</u>hu yu<u>h</u>ibbu <u>shshaa</u>biriin\r\r\n');
INSERT INTO quranLatin VALUES (440, '3:147', 3, 147, 'wam<u>aa</u> k<u>aa</u>na qawlahum ill<u>aa</u> an q<u>aa</u>luu rabban<u>aa</u> ighfir lan<u>aa</u> <u>dz</u>unuuban<u>aa</u> wa-isr<u>aa</u>fan<u>aa</u> fii amrin<u>aa</u> watsabbit aqd<u>aa</u>man<u>aa</u> wan<u>sh</u>urn<u>aa</u> \'al<u>aa</u> lqawmi lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (441, '3:148', 3, 148, 'fa<u>aa</u>t<u>aa</u>humull<u>aa</u>hu tsaw<u>aa</u>ba dduny<u>aa</u> wa<u>h</u>usna tsaw<u>aa</u>bi l-<u>aa</u>khirati wal<u>aa</u>hu yu<u>h</u>ibbu lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (442, '3:149', 3, 149, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu in tu<u>th</u>ii\'uulla<u>dz</u>iina kafaruu yarudduukum \'al<u>aa</u> a\'q<u>aa</u>bikum fatanqalibuu kh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (443, '3:150', 3, 150, 'balill<u>aa</u>hu mawl<u>aa</u>kum wahuwa khayru nn<u>aas</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (444, '3:151', 3, 151, 'sanulqii fii quluubilla<u>dz</u>iina kafaruu rru\'ba bim<u>aa</u> asyrakuu bil<u>aa</u>hi m<u>aa</u> lam yunazzil bihi sul<u>th</u><u>aa</u>nan wama/w<u>aa</u>humu nn<u>aa</u>ru wabi/sa matsw<u>aa</u> <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (445, '3:152', 3, 152, 'walaqad <u>sh</u>adaqakumull<u>aa</u>hu wa\'dahu i<u>dz</u> ta<u>h</u>ussuunahum bi-i<u>dz</u>nihi <u>h</u>att<u>aa</u> i<u>dzaa</u> fasyiltum watan<u>aa</u>za\'tum fii l-amri wa\'a<u>sh</u>aytum min ba\'di m<u>aa</u> ar<u>aa</u>kum m<u>aa</u> tu<u>h</u>ibbuuna minkum man yuriidu dduny<u>aa</u> waminkum man yuriidu l-<u>aa</u>khirata tsumma <u>sh</u>arafakum \'anhum liyabtaliyakum walaqad \'af<u>aa</u> \'ankum wal<u>aa</u>hu <u>dz</u>uu fa<u>dh</u>lin \'al<u>aa</u> lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (446, '3:153', 3, 153, 'i<u>dz</u> tu<u>sh</u>\'iduuna wal<u>aa</u> talwuuna \'al<u>aa</u> a<u>h</u>adin warrasuulu yad\'uukum fii ukhr<u>aa</u>kum fa-ats<u>aa</u>bakum ghamman bighammin likay l<u>aa</u> ta<u>h</u>zanuu \'al<u>aa</u> m<u>aa</u> f<u>aa</u>takum wal<u>aa</u> m<u>aa</u> a<u>shaa</u>bakum wal<u>aa</u>hu khabiirun bim<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (447, '3:154', 3, 154, 'tsumma anzala \'alaykum min ba\'di lghammi amanatan nu\'<u>aa</u>san yaghsy<u>aa</u> <u>th</u><u>aa</u>-ifatan minkum wa<u>th</u><u>aa</u>-ifatun qad ahammat-hum anfusuhum ya<u>zh</u>unnuuna bil<u>aa</u>hi ghayra l<u>h</u>aqqi <u>zh</u>anna lj<u>aa</u>hiliyyati yaquuluuna hal lan<u>aa</u> mina l-amri min syay-in qul inna l-amra kullahu lill<u>aa</u>hi yukhfuuna fii anfusihim m<u>aa</u> l<u>aa</u> yubduuna laka yaquuluuna law k<u>aa</u>na lan<u>aa</u> mina l-amri syay-un m<u>aa</u> qutiln<u>aa</u> h<u>aa</u>hun<u>aa</u> qul law kuntum fii buyuutikum labarazalla<u>dz</u>iina kutiba \'alayhimu lqatlu il<u>aa</u> ma<u>daa</u>ji\'ihim waliyabtaliyall<u>aa</u>hu m<u>aa</u> fii <u>sh</u>uduurikum waliyuma<u>hh</u>i<u>sh</u>a m<u>aa</u> fii quluubikum wal<u>aa</u>hu \'aliimun bi<u>dzaa</u>ti <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (448, '3:155', 3, 155, 'innalla<u>dz</u>iina tawallaw minkum yawma iltaq<u>aa</u> ljam\'<u>aa</u>ni innam<u>aa</u> istazallahumu sysyay<u>th</u><u>aa</u>nu biba\'<u>dh</u>i m<u>aa</u> kasabuu walaqad \'af<u>aa</u>ll<u>aa</u>hu \'anhum innall<u>aa</u>ha ghafuurun <u>h</u>aliim\r\r\n');
INSERT INTO quranLatin VALUES (449, '3:156', 3, 156, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> takuunuu kalla<u>dz</u>iina kafaruu waq<u>aa</u>luu li-ikhw<u>aa</u>nihim i<u>dzaa</u> <u>dh</u>arabuu fii l-ar<u>dh</u>i aw k<u>aa</u>nuu ghuzzan law k<u>aa</u>nuu \'indan<u>aa</u> m<u>aa</u> m<u>aa</u>tuu wam<u>aa</u> qutiluu liyaj\'alall<u>aa</u>hu <u>dzaa</u>lika <u>h</u>asratan fii quluubihim wal<u>aa</u>hu yu<u>h</u>yii wayumiitu wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (450, '3:157', 3, 157, 'wala-in qutiltum fii sabiilill<u>aa</u>hi aw muttum lamaghfiratun minall<u>aa</u>hi wara<u>h</u>matun khayrun mimm<u>aa</u> yajma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (451, '3:158', 3, 158, 'wala-in muttum aw qutiltum la-il<u>aa</u>ll<u>aa</u>hi tu<u>h</u>syaruun\r\r\n');
INSERT INTO quranLatin VALUES (452, '3:159', 3, 159, 'fabim<u>aa</u> ra<u>h</u>matin minall<u>aa</u>hi linta lahum walaw kunta fa<u>zhzh</u>an ghalii<u>zh</u>a lqalbi lanfa<u>dhdh</u>uu min <u>h</u>awlika fa\'fu \'anhum wastaghfir lahum wasy<u>aa</u>wirhum fii l-amri fa-i<u>dzaa</u> \'azamta fatawakkal \'al<u>aa</u>ll<u>aa</u>hi innall<u>aa</u>ha yu<u>h</u>ibbu lmutawakkiliin\r\r\n');
INSERT INTO quranLatin VALUES (453, '3:160', 3, 160, 'in yan<u>sh</u>urkumull<u>aa</u>hu fal<u>aa</u> gh<u>aa</u>liba lakum wa-in yakh<u>dz</u>ulkum faman <u>dzaa</u>lla<u>dz</u>ii yan<u>sh</u>urukum min ba\'dihi wa\'al<u>aa</u>ll<u>aa</u>hi falyatawakkali lmu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (454, '3:161', 3, 161, 'wam<u>aa</u> k<u>aa</u>na linabiyyin an yaghulla waman yaghlul ya/ti bim<u>aa</u> ghalla yawma lqiy<u>aa</u>mati tsumma tuwaff<u>aa</u> kullu nafsin m<u>aa</u> kasabat wahum l<u>aa</u> yu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (455, '3:162', 3, 162, 'afamani ittaba\'a ri<u>dh</u>w<u>aa</u>nall<u>aa</u>hi kaman b<u>aa</u>-a bisakha<u>th</u>in minall<u>aa</u>hi wama/w<u>aa</u>hu jahannamu wabi/sa lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (456, '3:163', 3, 163, 'hum daraj<u>aa</u>tun \'indall<u>aa</u>hi wal<u>aa</u>hu ba<u>sh</u>iirun bim<u>aa</u> ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (457, '3:164', 3, 164, 'laqad mannall<u>aa</u>hu \'al<u>aa</u> lmu/miniina i<u>dz</u> ba\'atsa fiihim rasuulan min anfusihim yatluu \'alayhim <u>aa</u>y<u>aa</u>tihi wayuzakkiihim wayu\'allimuhumu lkit<u>aa</u>ba wal<u>h</u>ikmata wa-in k<u>aa</u>nuu min qablu lafii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (458, '3:165', 3, 165, 'awa lamm<u>aa</u> a<u>shaa</u>batkum mu<u>sh</u>iibatun qad a<u>sh</u>abtum mitslayh<u>aa</u> qultum ann<u>aa</u> h<u>aadzaa</u> qul huwa min \'indi anfusikum innall<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (459, '3:166', 3, 166, 'wam<u>aa</u> a<u>shaa</u>bakum yawma iltaq<u>aa</u> ljam\'<u>aa</u>ni fabi-i<u>dz</u>nill<u>aa</u>hi waliya\'lama lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (460, '3:167', 3, 167, 'waliya\'lamalla<u>dz</u>iina n<u>aa</u>faquu waqiila lahum ta\'<u>aa</u>law q<u>aa</u>tiluu fii sabiilill<u>aa</u>hi awi idfa\'uu q<u>aa</u>luu law na\'lamu qit<u>aa</u>lan lattaba\'n<u>aa</u>kum hum lilkufri yawma-i<u>dz</u>in aqrabu minhum lil-iim<u>aa</u>ni yaquuluuna bi-afw<u>aa</u>hihim m<u>aa</u> laysa fii quluubihim wal<u>aa</u>hu a\'lamu bim<u>aa</u> yaktumuun\r\r\n');
INSERT INTO quranLatin VALUES (461, '3:168', 3, 168, '<B>al</b>la<u>dz</u>iina q<u>aa</u>luu li-ikhw<u>aa</u>nihim waqa\'aduu law a<u>th</u><u>aa</u>\'uun<u>aa</u> m<u>aa</u> qutiluu qul fadrauu \'an anfusikumu lmawta in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (462, '3:169', 3, 169, 'wal<u>aa</u> ta<u>h</u>sabannalla<u>dz</u>iina qutiluu fii sabiilill<u>aa</u>hi amw<u>aa</u>tan bal a<u>h</u>y<u>aa</u>un \'inda rabbihim yurzaquun\r\r\n');
INSERT INTO quranLatin VALUES (463, '3:170', 3, 170, 'fari<u>h</u>iina bim<u>aa</u> <u>aa</u>t<u>aa</u>humull<u>aa</u>hu min fa<u>dh</u>lihi wayastabsyiruuna billa<u>dz</u>iina lam yal<u>h</u>aquu bihim min khalfihimll<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (464, '3:171', 3, 171, 'yastabsyiruuna bini\'matin minall<u>aa</u>hi wafa<u>dh</u>lin wa-annall<u>aa</u>ha l<u>aa</u> yu<u>dh</u>ii\'u ajra lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (465, '3:172', 3, 172, '<B>al</b>la<u>dz</u>iina istaj<u>aa</u>buu lill<u>aa</u>hi warrasuuli min ba\'di m<u>aa</u> a<u>shaa</u>bahumu lqar<u>h</u>u lilla<u>dz</u>iina a<u>h</u>sanuu minhum wattaqaw ajrun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (466, '3:173', 3, 173, '<B>al</b>la<u>dz</u>iina q<u>aa</u>la lahumu nn<u>aa</u>su inna nn<u>aa</u>sa qad jama\'uu lakum fakhsyawhum faz<u>aa</u>dahum iim<u>aa</u>nan waq<u>aa</u>luu <u>h</u>asbun<u>aa</u>ll<u>aa</u>hu wani\'ma lwakiil\r\r\n');
INSERT INTO quranLatin VALUES (467, '3:174', 3, 174, 'fanqalabuu bini\'matin minall<u>aa</u>hi wafa<u>dh</u>lin lam yamsas-hum suu-un wattaba\'uu ri<u>dh</u>w<u>aa</u>nall<u>aa</u>hi wal<u>aa</u>hu <u>dz</u>uu fa<u>dh</u>lin \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (468, '3:175', 3, 175, 'innam<u>aa</u> <u>dzaa</u>likumu sysyay<u>th</u><u>aa</u>nu yukhawwifu awliy<u>aa</u>-ahu fal<u>aa</u> takh<u>aa</u>fuuhum wakh<u>aa</u>fuuni in kuntum mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (469, '3:176', 3, 176, 'wal<u>aa</u> ya<u>h</u>zunkalla<u>dz</u>iina yus<u>aa</u>ri\'uuna fii lkufri innahum lan ya<u>dh</u>urruull<u>aa</u>ha syay-an yuriidull<u>aa</u>hull<u>aa</u> yaj\'ala lahum <u>h</u>a<u>zhzh</u>an fii l-<u>aa</u>khirati walahum \'a<u>dzaa</u>bun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (470, '3:177', 3, 177, 'innalla<u>dz</u>iina isytarawuu lkufra bil-iim<u>aa</u>ni lan ya<u>dh</u>urruull<u>aa</u>ha syay-an walahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (471, '3:178', 3, 178, 'wal<u>aa</u> ya<u>h</u>sabannalla<u>dz</u>iina kafaruu annam<u>aa</u> numlii lahum khayrun li-anfusihim innam<u>aa</u> numlii lahum liyazd<u>aa</u>duu itsman walahum \'a<u>dzaa</u>bun muhiin\r\r\n');
INSERT INTO quranLatin VALUES (472, '3:179', 3, 179, 'm<u>aa</u> k<u>aa</u>nall<u>aa</u>hu liya<u>dz</u>ara lmu/miniina \'al<u>aa</u> m<u>aa</u> antum \'alayhi <u>h</u>att<u>aa</u> yamiiza lkhabiitsa mina <u>ththh</u>ayyibi wam<u>aa</u> k<u>aa</u>nall<u>aa</u>hu liyu<u>th</u>li\'akum \'al<u>aa</u> lghaybi wal<u>aa</u>kinnall<u>aa</u>ha yajtabii min rusulihi man yasy<u>aa</u>u fa<u>aa</u>minuu bil<u>aa</u>hi warusulihi wa-in tu/minuu watattaquu falakum ajrun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (473, '3:180', 3, 180, 'wal<u>aa</u> ya<u>h</u>sabannalla<u>dz</u>iina yabkhaluuna bim<u>aa</u> <u>aa</u>t<u>aa</u>hummull<u>aa</u>hu min fa<u>dh</u>lihi huwa khayran lahum bal huwa syarrun lahum sayu<u>th</u>awwaquuna m<u>aa</u> bakhiluu bihi yawma lqiy<u>aa</u>mati walill<u>aa</u>hi miir<u>aa</u>tsu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna khabiir\r\r\n');
INSERT INTO quranLatin VALUES (474, '3:181', 3, 181, 'laqad sami\'all<u>aa</u>hu qawlalla<u>dz</u>iina q<u>aa</u>luu innall<u>aa</u>ha faqiirun wana<u>h</u>nu aghniy<u>aa</u>un sanaktubu m<u>aa</u> q<u>aa</u>luu waqatlahumu l-anbiy<u>aa</u>-a bighayri <u>h</u>aqqin wanaquulu <u>dz</u>uuquu \'a<u>dzaa</u>ba l<u>h</u>ariiq\r\r\n');
INSERT INTO quranLatin VALUES (475, '3:182', 3, 182, '<u>dzaa</u>lika bim<u>aa</u> qaddamat aydiikum wa-annall<u>aa</u>ha laysa bi<u>zh</u>all<u>aa</u>min lil\'abiid\r\r\n');
INSERT INTO quranLatin VALUES (476, '3:183', 3, 183, '<B>al</b>la<u>dz</u>iina q<u>aa</u>luu innall<u>aa</u>ha \'ahida ilayn<u>aa</u>ll<u>aa</u> nu/mina lirasuulin <u>h</u>att<u>aa</u> ya/tiyan<u>aa</u> biqurb<u>aa</u>nin ta/kuluhu nn<u>aa</u>ru qul qad j<u>aa</u>-akum rusulun min qablii bilbayyin<u>aa</u>ti wabilla<u>dz</u>ii qultum falima qataltumuuhum in kutum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (477, '3:184', 3, 184, 'fa-in ka<u>dzdz</u>abuuka faqad ku<u>dzdz</u>iba rusulun min qablika j<u>aa</u>uu bilbayyin<u>aa</u>ti wazzuburi walkit<u>aa</u>bi lmuniir\r\r\n');
INSERT INTO quranLatin VALUES (478, '3:185', 3, 185, 'kullu nafsin <u>dzaa</u>-iqatu lmawti wa-innam<u>aa</u> tuwaffawna ujuurakum yawma lqiy<u>aa</u>mati faman zu<u>h</u>zi<u>h</u>a \'ani nn<u>aa</u>ri waudkhila ljannata faqad f<u>aa</u>za wam<u>aa</u> l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> ill<u>aa</u> mat<u>aa</u>\'u lghuruur\r\r\n');
INSERT INTO quranLatin VALUES (479, '3:186', 3, 186, 'latublawunna fii amw<u>aa</u>likum wa-anfusikum walatasma\'unna minalla<u>dz</u>iina uutuu lkit<u>aa</u>ba min qablikum waminalla<u>dz</u>iina asyrakuu a<u>dz</u>an katsiiran wa-in ta<u>sh</u>biruu watattaquu fa-inna <u>dzaa</u>lika min \'azmi l-umuur\r\r\n');
INSERT INTO quranLatin VALUES (480, '3:187', 3, 187, 'wa-i<u>dz</u> akha<u>dz</u>all<u>aa</u>hu miits<u>aa</u>qalla<u>dz</u>iina uutuu lkit<u>aa</u>ba latubayyinunnahu linn<u>aa</u>si wal<u>aa</u> taktumuunahu fanaba<u>dz</u>uuhu war<u>aa</u>-a <u>zh</u>uhuurihim wasytaraw bihi tsamanan qaliilan fabi/sa m<u>aa</u> yasytaruun\r\r\n');
INSERT INTO quranLatin VALUES (481, '3:188', 3, 188, 'l<u>aa</u> ta<u>h</u>sabannalla<u>dz</u>iina yafra<u>h</u>uuna bim<u>aa</u> ataw wayu<u>h</u>ibbuuna an yu<u>h</u>maduu bim<u>aa</u> lam yaf\'aluu fal<u>aa</u> ta<u>h</u>sabannahum bimaf<u>aa</u>zatin mina l\'a<u>dzaa</u>bi walahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (482, '3:189', 3, 189, 'walill<u>aa</u>hi mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wal<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (483, '3:190', 3, 190, 'inna fii khalqi ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wakhtil<u>aa</u>fillayli wannah<u>aa</u>ri la<u>aa</u>y<u>aa</u>tin li-ulii l-alb<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (484, '3:191', 3, 191, '<B>al</b>la<u>dz</u>iina ya<u>dz</u>kuruunall<u>aa</u>ha qiy<u>aa</u>man waqu\'uudan wa\'al<u>aa</u> junuubihim wayatafakkaruuna fii khalqi ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i rabban<u>aa</u> m<u>aa</u> khalaqta h<u>aadzaa</u> b<u>aath</u>ilan sub<u>haa</u>naka faqin<u>aa</u> \'a<u>dzaa</u>ba nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (485, '3:192', 3, 192, 'rabban<u>aa</u> innaka man tudkhili nn<u>aa</u>ra faqad akhzaytahu wam<u>aa</u> li<u>zhzhaa</u>limiina min an<u>shaa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (486, '3:193', 3, 193, 'rabban<u>aa</u> innan<u>aa</u> sami\'n<u>aa</u> mun<u>aa</u>diyan yun<u>aa</u>dii lil-iim<u>aa</u>ni an <u>aa</u>minuu birabbikum fa<u>aa</u>mann<u>aa</u> rabban<u>aa</u> faghfir lan<u>aa</u> <u>dz</u>unuuban<u>aa</u> wakaffir \'ann<u>aa</u> sayyi-<u>aa</u>tin<u>aa</u> watawaffan<u>aa</u> ma\'a l-abr<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (487, '3:194', 3, 194, 'rabban<u>aa</u> wa<u>aa</u>tin<u>aa</u> m<u>aa</u> wa\'adtan<u>aa</u> \'al<u>aa</u> rusulika wal<u>aa</u> tukhzin<u>aa</u> yawma lqiy<u>aa</u>mati innaka l<u>aa</u> tukhlifu lmii\'<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (488, '3:195', 3, 195, 'fastaj<u>aa</u>ba lahum rabbuhum annii l<u>aa</u> u<u>dh</u>ii\'u \'amala \'<u>aa</u>milin minkum min <u>dz</u>akarin aw unts<u>aa</u> ba\'<u>dh</u>ukum min ba\'<u>dh</u>in falla<u>dz</u>iina h<u>aa</u>jaruu waukhrijuu min diy<u>aa</u>rihim wauu<u>dz</u>uu fii sabiilii waq<u>aa</u>taluu waqutiluu laukaffiranna \'anhum sayyi-<u>aa</u>tihim walaudkhilannahum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru tsaw<u>aa</u>ban min \'indill<u>aa</u>hi wal<u>aa</u>hu \'indahu <u>h</u>usnu tstsaw<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (489, '3:196', 3, 196, 'l<u>aa</u> yaghurrannaka taqallubulla<u>dz</u>iina kafaruu fii lbil<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (490, '3:197', 3, 197, 'mat<u>aa</u>\'un qaliilun tsumma ma/w<u>aa</u>hum jahannamu wabi/sa lmih<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (491, '3:198', 3, 198, 'l<u>aa</u>kinilla<u>dz</u>iina ittaqaw rabbahum lahum jann<u>aa</u>tun tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> nuzulan min \'indill<u>aa</u>hi wam<u>aa</u> \'indall<u>aa</u>hi khayrun lil-abr<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (492, '3:199', 3, 199, 'wa-inna min ahli lkit<u>aa</u>bi laman yu/minu bil<u>aa</u>hi wam<u>aa</u> unzila ilaykum wam<u>aa</u> unzila ilayhim kh<u>aa</u>syi\'iina lill<u>aa</u>hi l<u>aa</u> yasytaruuna bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi tsamanan qaliilan ul<u>aa</u>-ika lahum ajruhum \'inda rabbihim innall<u>aa</u>ha sarii\'u l<u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (493, '3:200', 3, 200, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu i<u>sh</u>biruu wa<u>shaa</u>biruu war<u>aa</u>bi<u>th</u>uu wattaquull<u>aa</u>ha la\'allakum tufli<u>h</u>uun<dir>\r\r\n');
INSERT INTO quranLatin VALUES (494, '4:1', 4, 1, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su ittaquu rabbakumulla<u>dz</u>ii khalaqakum min nafsin w<u>aah</u>idatin wakhalaqa minh<u>aa</u> zawjah<u>aa</u> wabatstsa minhum<u>aa</u> rij<u>aa</u>lan katsiiran wanis<u>aa</u>-an wattaquull<u>aa</u>halla<u>dz</u>ii tas<u>aa</u>-aluuna bihi wal-ar<u>haa</u>ma innall<u>aa</u>ha k<u>aa</u>na \'alaykum raqiib\r\r\n');
INSERT INTO quranLatin VALUES (495, '4:2', 4, 2, 'wa<u>aa</u>tuu lyat<u>aa</u>m<u>aa</u> amw<u>aa</u>lahum wal<u>aa</u> tatabaddaluu lkhabiitsa bi<u>ththh</u>ayyibi wal<u>aa</u> ta/kuluu amw<u>aa</u>lahum il<u>aa</u> amw<u>aa</u>likum innahu k<u>aa</u>na <u>h</u>uuban kabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (496, '4:3', 4, 3, 'wa-in khiftumll<u>aa</u> tuqsi<u>th</u>uu fii lyat<u>aa</u>m<u>aa</u> fanki<u>h</u>uu m<u>aa</u> <u>th</u><u>aa</u>ba lakum mina nnis<u>aa</u>-i matsn<u>aa</u> watsul<u>aa</u>tsa warub<u>aa</u>\'a fa-in khiftumll<u>aa</u> ta\'diluu faw<u>aah</u>idatan aw m<u>aa</u> malakat aym<u>aa</u>nukum <u>dzaa</u>lika adn<u>aa</u>ll<u>aa</u> ta\'uuluu</u>\r\r\n');
INSERT INTO quranLatin VALUES (497, '4:4', 4, 4, 'wa<u>aa</u>tuu nnis<u>aa</u>-a <u>sh</u>aduq<u>aa</u>tihinna ni<u>h</u>latan fa-in <u>th</u>ibna lakum \'an syay-in minhu nafsan fakuluuhu hanii-an marii-<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (498, '4:5', 4, 5, 'wal<u>aa</u> tu/tuu ssufah<u>aa</u>-a amw<u>aa</u>lakumullatii ja\'alall<u>aa</u>hu lakum qiy<u>aa</u>man warzuquuhum fiih<u>aa</u> waksuuhum waquuluu lahum qawlan ma\'ruuf<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (499, '4:6', 4, 6, 'wabtaluu lyat<u>aa</u>m<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> balaghuu nnik<u>aah</u>a fa-in <u>aa</u>nastum minhum rusydan fadfa\'uu ilayhim amw<u>aa</u>lahum wal<u>aa</u> ta/kuluuh<u>aa</u> isr<u>aa</u>fan wabid<u>aa</u>ran an yakbaruu waman k<u>aa</u>na ghaniyyan falyasta\'fif waman k<u>aa</u>na faqiiran falya/kul bilma\'ruufi fa-i<u>dzaa</u> dafa\'tum ilayhim amw<u>aa</u>lahum fa-asyhiduu \'alayhim wakaf<u>aa</u> bil<u>aa</u>hi <u>h</u>asiib<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (500, '4:7', 4, 7, 'lirrij<u>aa</u>li na<u>sh</u>iibun mimm<u>aa</u> taraka lw<u>aa</u>lid<u>aa</u>ni wal-aqrabuuna walinnis<u>aa</u>-i na<u>sh</u>iibun mimm<u>aa</u> taraka lw<u>aa</u>lid<u>aa</u>ni wal-aqrabuuna mimm<u>aa</u> qalla minhu aw katsura na<u>sh</u>iiban mafruu<u>daa</u>\r\r\n');
INSERT INTO quranLatin VALUES (501, '4:8', 4, 8, 'wa-i<u>dzaa</u> <u>h</u>a<u>dh</u>ara lqismata uluu lqurb<u>aa</u> walyat<u>aa</u>m<u>aa</u> walmas<u>aa</u>kiinu farzuquuhum minhu waquuluu lahum qawlan ma\'ruuf<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (502, '4:9', 4, 9, 'walyakhsyalla<u>dz</u>iina law tarakuu min khalfihim <u>dz</u>urriyyatan <u>dh</u>i\'<u>aa</u>fan kh<u>aa</u>fuu \'alayhim falyattaquull<u>aa</u>ha walyaquuluu qawlan sadiid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (503, '4:10', 4, 10, 'innalla<u>dz</u>iina ya/kuluuna amw<u>aa</u>la lyat<u>aa</u>m<u>aa</u> <u>zh</u>ulman innam<u>aa</u> ya/kuluuna fii bu<u>th</u>uunihim n<u>aa</u>ran wasaya<u>sh</u>lawna sa\'iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (504, '4:11', 4, 11, 'yuu<u>sh</u>iikumull<u>aa</u>hu fii awl<u>aa</u>dikum li<u>dzdz</u>akari mitslu <u>h</u>a<u>zhzh</u>i luntsayayni fa-in kunna nis<u>aa</u>-an fawqa itsnatayni falahunna tsuluts<u>aa</u> m<u>aa</u> taraka wa-in k<u>aa</u>nat w<u>aah</u>idatan falah<u>aa</u> nni<u>sh</u>fu wali-abawayhi likulli w<u>aah</u>idin minhum<u>aa</u> ssudusu mimm<u>aa</u> taraka in k<u>aa</u>na lahu waladun fa-in lam yakun lahu waladun wawaritsahu abaw<u>aa</u>hu fali-ummihi tstsulutsu fa-in k<u>aa</u>na lahu ikhwatun fali-ummihi ssudusu min ba\'di wa<u>sh</u>iyyatin yuu<u>sh</u>ii bih<u>aa</u> aw daynin <u>aa</u>b<u>aa</u>ukum wa-abn<u>aa</u>ukum l<u>aa</u> tadruuna ayyuhum aqrabu lakum naf\'an farii<u>dh</u>atan minall<u>aa</u>hi innall<u>aa</u>ha k<u>aa</u>na \'aliiman <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (505, '4:12', 4, 12, 'walakum ni<u>sh</u>fu m<u>aa</u> taraka azw<u>aa</u>jukum in lam yakun lahunna waladun fa-in k<u>aa</u>na lahunna waladun falakumu rrubu\'u mimm<u>aa</u> tarakna min ba\'di wa<u>sh</u>iyyatin yuu<u>sh</u>iina bih<u>aa</u> aw daynin walahunna rrubu\'u mimm<u>aa</u> taraktum in lam yakun lakum waladun fa-in k<u>aa</u>na lakum waladun falahunna tstsumunu mimm<u>aa</u> taraktum min ba\'di wa<u>sh</u>iyyatin tuu<u>sh</u>uuna bih<u>aa</u> aw daynin wa-in k<u>aa</u>na rajulun yuuratsu kal<u>aa</u>latan awi imra-atun walahu akhun aw ukhtun falikulli w<u>aah</u>idin minhum<u>aa</u> ssudusu fa-in k<u>aa</u>nuu aktsara min <u>dzaa</u>lika fahum syurak<u>aa</u>u fii tstsulutsi min ba\'di wa<u>sh</u>iyyatin yuu<u>shaa</u> bih<u>aa</u> aw daynin ghayra mu<u>daa</u>rrin wa<u>sh</u>iyyatan minall<u>aa</u>hi wal<u>aa</u>hu \'aliimun <u>h</u>aliim\r\r\n');
INSERT INTO quranLatin VALUES (506, '4:13', 4, 13, 'tilka <u>h</u>uduudull<u>aa</u>hi waman yu<u>th</u>i\'ill<u>aa</u>ha warasuulahu yudkhilhu jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wa<u>dzaa</u>lika lfawzu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (507, '4:14', 4, 14, 'waman ya\'<u>sh</u>ill<u>aa</u>ha warasuulahu wayata\'adda <u>h</u>uduudahu yudkhilhu n<u>aa</u>ran kh<u>aa</u>lidan fiih<u>aa</u> walahu \'a<u>dzaa</u>bun muhiin\r\r\n');
INSERT INTO quranLatin VALUES (508, '4:15', 4, 15, 'wall<u>aa</u>tii ya/tiina lf<u>aah</u>isyata min nis<u>aa</u>-ikum fastasyhiduu \'alayhinna arba\'atan minkum fa-in syahiduu fa-amsikuuhunna fii lbuyuuti <u>h</u>att<u>aa</u> yatawaff<u>aa</u>hunna lmawtu aw yaj\'alall<u>aa</u>hu lahunna sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (509, '4:16', 4, 16, 'walla<u>dzaa</u>ni ya/tiy<u>aa</u>nih<u>aa</u> minkum fa<u>aadz</u>uuhum<u>aa</u> fa-in t<u>aa</u>b<u>aa</u> wa-a<u>sh</u>la<u>haa</u> fa-a\'ri<u>dh</u>uu \'anhum<u>aa</u> innall<u>aa</u>ha k<u>aa</u>na taww<u>aa</u>ban ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (510, '4:17', 4, 17, 'innam<u>aa</u> ttawbatu \'al<u>aa</u>ll<u>aa</u>hi lilla<u>dz</u>iina ya\'maluuna ssuu-a bijah<u>aa</u>latin tsumma yatuubuuna min qariibin faul<u>aa</u>-ika yatuubull<u>aa</u>hu \'alayhim wak<u>aa</u>nall<u>aa</u>hu \'aliiman <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (511, '4:18', 4, 18, 'walaysati ttawbatu lilla<u>dz</u>iina ya\'maluuna ssayyi-<u>aa</u>ti <u>h</u>att<u>aa</u> i<u>dzaa</u> <u>h</u>a<u>dh</u>ara a<u>h</u>adahumu lmawtu q<u>aa</u>la innii tubtu l-<u>aa</u>na wal<u>aa</u>lla<u>dz</u>iina yamuutuuna wahum kuff<u>aa</u>run ul<u>aa</u>-ika a\'tadn<u>aa</u> lahum \'a<u>dzaa</u>ban liim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (512, '4:19', 4, 19, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> ya<u>h</u>illu lakum an taritsuu nnis<u>aa</u>-a karhan wal<u>aa</u> ta\'<u>dh</u>uluuhunna lita<u>dz</u>habuu biba\'<u>dh</u>i m<u>aa</u> <u>aa</u>taytumuuhunna ill<u>aa</u> an ya/tiina bif<u>aah</u>isyatin mubayyinatin wa\'<u>aa</u>syiruuhunna bilma\'ruufi fa-in karihtumuuhunna fa\'as<u>aa</u> an takrahuu syay-an wayaj\'alall<u>aa</u>hu fiihi khayran katsiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (513, '4:20', 4, 20, 'wa-in aradtumu istibd<u>aa</u>la zawjin mak<u>aa</u>na zawjin wa<u>aa</u>taytum i<u>h</u>d<u>aa</u>hunna qin<u>th</u><u>aa</u>ran fal<u>aa</u> ta/khu<u>dz</u>uu minhu syay-an ata/khu<u>dz</u>uunahu buht<u>aa</u>nan wa-itsman mubiin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (514, '4:21', 4, 21, 'wakayfa ta/khu<u>dz</u>uunahu waqad af<u>daa</u> ba\'<u>dh</u>ukum il<u>aa</u> ba\'<u>dh</u>in wa-akha<u>dz</u>na minkum miits<u>aa</u>qan ghalii<u>zhaa</u>\r\r\n');
INSERT INTO quranLatin VALUES (515, '4:22', 4, 22, 'wal<u>aa</u> tanki<u>h</u>uu m<u>aa</u> naka<u>h</u>a <u>aa</u>b<u>aa</u>ukum mina nnis<u>aa</u>-i ill<u>aa</u> m<u>aa</u> qad salafa innahu k<u>aa</u>na f<u>aah</u>isyatan wamaqtan was<u>aa</u>-a sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (516, '4:23', 4, 23, '<u>h</u>urrimat \'alaykum ummah<u>aa</u>tukum waban<u>aa</u>tukum wa-akhaw<u>aa</u>tukum wa\'amm<u>aa</u>tukum wakh<u>aa</u>l<u>aa</u>tukum waban<u>aa</u>tu l-akhi waban<u>aa</u>tu l-ukhti waummah<u>aa</u>tukumull<u>aa</u>tii ar<u>dh</u>a\'nakum wa-akhaw<u>aa</u>tukum mina rra<u>daa</u>\'ati waummah<u>aa</u>tu nis<u>aa</u>-ikum warab<u>aa</u>-ibukumull<u>aa</u>tii fii <u>h</u>ujuurikum min nis<u>aa</u>-ikumull<u>aa</u>tii dakhaltum bihinna fa-in lam takuunuu dakhaltum bihinna fal<u>aa</u> jun<u>aah</u>a \'alaykum wa<u>h</u>al<u>aa</u>-ilu abn<u>aa</u>-ikumulla<u>dz</u>iina min a<u>sh</u>l<u>aa</u>bikum wa-an tajma\'uu bayna l-ukhtayni ill<u>aa</u> m<u>aa</u> qad salafa innall<u>aa</u>ha k<u>aa</u>na ghafuuran ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (517, '4:24', 4, 24, 'walmu<u>hs</u>an<u>aa</u>tu mina nnis<u>aa</u>-i ill<u>aa</u> m<u>aa</u> malakat aym<u>aa</u>nukum kit<u>aa</u>ball<u>aa</u>hi \'alaykum wau<u>h</u>illa lakum m<u>aa</u> war<u>aa</u>-a <u>dzaa</u>likum an tabtaghuu bi-amw<u>aa</u>likum mu<u>hs</u>iniina ghayra mus<u>aa</u>fi<u>h</u>iina fam<u>aa</u> istamta\'tum bihi minhunna fa<u>aa</u>tuuhunna ujuurahunna farii<u>dh</u>atan wal<u>aa</u> jun<u>aah</u>a \'alaykum fiim<u>aa</u> tar<u>aad</u>aytum bihi min ba\'di lfarii<u>dh</u>ati innall<u>aa</u>ha k<u>aa</u>na \'aliiman <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (518, '4:25', 4, 25, 'waman lam yasta<u>th</u>i\'minkum <u>th</u>awlan an yanki<u>h</u>a lmu<u>hs</u>an<u>aa</u>ti lmu/min<u>aa</u>ti famin m<u>aa</u> malakat aym<u>aa</u>nukum min fatay<u>aa</u>tikumu lmu/min<u>aa</u>ti wal<u>aa</u>hu a\'lamu bi-iim<u>aa</u>nikum ba\'<u>dh</u>ukum min ba\'<u>dh</u>in fanki<u>h</u>uuhunna bi-i<u>dz</u>ni ahlihinna wa<u>aa</u>tuuhunna ujuurahunna bilma\'ruufi mu<u>hs</u>an<u>aa</u>tin ghayra mas<u>aa</u>fi<u>haa</u>tin wal<u>aa</u> muttakhi<u>dzaa</u>ti akhd<u>aa</u>nin fa-i<u>dzaa</u> u<u>hs</u>inna fa-in atayna bif<u>aah</u>isyatin fa\'alayhinna ni<u>sh</u>fu m<u>aa</u> \'al<u>aa</u> lmu<u>hs</u>an<u>aa</u>ti mina l\'a<u>dzaa</u>bi <u>dzaa</u>lika liman khasyiya l\'anata minkum wa-an ta<u>sh</u>biruu khayrun lakum wal<u>aa</u>hu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (519, '4:26', 4, 26, 'yuriidull<u>aa</u>hu liyubayyina lakum wayahdiyakum sunanalla<u>dz</u>iina min qablikum wayatuuba \'alaykum wal<u>aa</u>hu \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (520, '4:27', 4, 27, 'wal<u>aa</u>hu yuriidu an yatuuba \'alaykum wayuriidulla<u>dz</u>iina yattabi\'uuna sysyahaw<u>aa</u>ti an tamiiluu maylan \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (521, '4:28', 4, 28, 'yuriidull<u>aa</u>hu an yukhaffifa \'ankum wakhuliqa l-ins<u>aa</u>nu <u>dh</u>a\'iif<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (522, '4:29', 4, 29, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> ta/kuluu amw<u>aa</u>lakum baynakum bilb<u>aath</u>ili ill<u>aa</u> an takuuna tij<u>aa</u>ratan \'an tar<u>aad</u>in minkum wal<u>aa</u> taqtuluu anfusakum innall<u>aa</u>ha k<u>aa</u>na bikum ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (523, '4:30', 4, 30, 'waman yaf\'al <u>dzaa</u>lika \'udw<u>aa</u>nan wa<u>zh</u>ulman fasawfa nu<u>sh</u>liihi n<u>aa</u>ran wak<u>aa</u>na <u>dzaa</u>lika \'al<u>aa</u>ll<u>aa</u>hi yasiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (524, '4:31', 4, 31, 'in tajtanibuu kab<u>aa</u>-ira m<u>aa</u> tunhawna \'anhu nukaffir \'ankum sayyi-<u>aa</u>tikum wanudkhilkum mudkhalan kariim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (525, '4:32', 4, 32, 'wal<u>aa</u> tatamannaw m<u>aa</u> fa<u>dhdh</u>alall<u>aa</u>hu bihi ba\'<u>dh</u>akum \'al<u>aa</u> ba\'<u>dh</u>in lirrij<u>aa</u>li na<u>sh</u>iibun mimm<u>aa</u> iktasabuu walinnis<u>aa</u>-i na<u>sh</u>iibun mimm<u>aa</u> iktasabna was-aluull<u>aa</u>ha min fa<u>dh</u>lihi innall<u>aa</u>ha k<u>aa</u>na bikulli syay-in \'aliim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (526, '4:33', 4, 33, 'walikullin ja\'aln<u>aa</u> maw<u>aa</u>liya mimm<u>aa</u> taraka lw<u>aa</u>lid<u>aa</u>ni wal-aqrabuuna walla<u>dz</u>iina \'aqadat aym<u>aa</u>nukum fa<u>aa</u>tuuhum na<u>sh</u>iibahum innall<u>aa</u>ha k<u>aa</u>na \'al<u>aa</u> kulli syay-in syahiid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (527, '4:34', 4, 34, 'arrij<u>aa</u>lu qaww<u>aa</u>muuna \'al<u>aa</u> nnis<u>aa</u>-i bim<u>aa</u> fa<u>dhdh</u>alall<u>aa</u>hu ba\'<u>dh</u>ahum \'al<u>aa</u> ba\'<u>dh</u>in wabim<u>aa</u> anfaquu min amw<u>aa</u>lihim fa<u>shshaa</u>li<u>haa</u>tu q<u>aa</u>nit<u>aa</u>tun <u>haa</u>fi<u>zhaa</u>tun lilghaybi bim<u>aa</u> <u>h</u>afi<u>zh</u>all<u>aa</u>hu wall<u>aa</u>tii takh<u>aa</u>fuuna nusyuuzahunna fa\'i<u>zh</u>uuhunna wahjuruuhunna fii lma<u>daa</u>ji\'i wa<u>dh</u>ribuuhunna fa-in a<u>th</u>a\'nakum fal<u>aa</u> tabghuu \'alayhinna sabiilan innall<u>aa</u>ha k<u>aa</u>na \'aliyyan kabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (528, '4:35', 4, 35, 'wa-in khiftum syiq<u>aa</u>qa baynihim<u>aa</u> fab\'atsuu <u>h</u>akaman min ahlihi wa<u>h</u>akaman min ahlih<u>aa</u> in yuriid<u>aa</u> i<u>sh</u>l<u>aah</u>an yuwaffiqill<u>aa</u>hu baynahum<u>aa</u> innall<u>aa</u>ha k<u>aa</u>na \'aliiman khabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (529, '4:36', 4, 36, 'wa\'buduull<u>aa</u>ha wal<u>aa</u> tusyrikuu bihi syay-an wabilw<u>aa</u>lidayni i<u>h</u>s<u>aa</u>nan wabi<u>dz</u>ii lqurb<u>aa</u> walyat<u>aa</u>m<u>aa</u> walmas<u>aa</u>kiini walj<u>aa</u>ri <u>dz</u>ii lqurb<u>aa</u> walj<u>aa</u>ri ljunubi wa<u>ssah</u>ibi biljanbi wabni ssabiili wam<u>aa</u> malakat aym<u>aa</u>nukum innall<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu man k<u>aa</u>na mukht<u>aa</u>lan fakhuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (530, '4:37', 4, 37, '<B>al</b>la<u>dz</u>iina yabkhaluuna waya/muruuna nn<u>aa</u>sa bilbukhli wayaktumuuna m<u>aa</u> <u>aa</u>t<u>aa</u>humull<u>aa</u>hu min fa<u>dh</u>lihi wa-a\'tadn<u>aa</u> lilk<u>aa</u>firiina \'a<u>dzaa</u>ban muhiin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (531, '4:38', 4, 38, 'walla<u>dz</u>iina yunfiquuna amw<u>aa</u>lahum ri-<u>aa</u>-a nn<u>aa</u>si wal<u>aa</u> yu/minuuna bil<u>aa</u>hi wal<u>aa</u> bilyawmi l-<u>aa</u>khiri waman yakuni sysyay<u>th</u><u>aa</u>nu lahu qariinan fas<u>aa</u>-a qariin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (532, '4:39', 4, 39, 'wam<u>aatsaa</u> \'alayhim law <u>aa</u>manuu bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri wa-anfaquu mimm<u>aa</u> razaqahumull<u>aa</u>hu wak<u>aa</u>nall<u>aa</u>hu bihim \'aliim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (533, '4:40', 4, 40, 'innall<u>aa</u>ha l<u>aa</u> ya<u>zh</u>limu mitsq<u>aa</u>la <u>dz</u>arratin wa-in taku <u>h</u>asanatan yu<u>daa</u>\'ifh<u>aa</u> wayu/ti min ladunhu ajran \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (534, '4:41', 4, 41, 'fakayfa i<u>dzaa</u> ji/n<u>aa</u> min kulli ummatin bisyahiidin waji/n<u>aa</u> bika \'al<u>aa</u> h<u>aa</u>ul<u>aa</u>-i syahiid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (535, '4:42', 4, 42, 'yawma-i<u>dz</u>in yawaddulla<u>dz</u>iina kafaruu wa\'a<u>sh</u>awuu rrasuula law tusaww<u>aa</u> bihimu l-ar<u>dh</u>u wal<u>aa</u> yaktumuunall<u>aa</u>ha <u>h</u>adiits<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (536, '4:43', 4, 43, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> taqrabuu <u>shsh</u>al<u>aa</u>ta wa-antum suk<u>aa</u>r<u>aa</u> <u>h</u>att<u>aa</u> ta\'lamuu m<u>aa</u> taquuluuna wal<u>aa</u> junuban ill<u>aa</u> \'<u>aa</u>birii sabiilin <u>h</u>att<u>aa</u> taghtasiluu wa-in kuntum mar<u>daa</u> aw \'al<u>aa</u> safarin aw j<u>aa</u>-a a<u>h</u>adun minkum mina lgh<u>aa</u>-i<u>th</u>i aw l<u>aa</u>mastumu nnis<u>aa</u>-a falam tajiduu m<u>aa</u>-an fatayammamuu <u>sh</u>a\'iidan <u>th</u>ayyiban famsa<u>h</u>uu biwujuuhikum wa-aydiikum innall<u>aa</u>ha k<u>aa</u>na \'afuwwan ghafuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (537, '4:44', 4, 44, 'alam tara il<u>aa</u>lla<u>dz</u>iina uutuu na<u>sh</u>iiban mina lkit<u>aa</u>bi yasytaruuna <u>dhdh</u>al<u>aa</u>lata wayuriiduuna an ta<u>dh</u>illuu ssabiil\r\r\n');
INSERT INTO quranLatin VALUES (538, '4:45', 4, 45, 'wal<u>aa</u>hu a\'lamu bi-a\'d<u>aa</u>-ikum wakaf<u>aa</u> bil<u>aa</u>hi waliyyan wakaf<u>aa</u> bil<u>aa</u>hi na<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (539, '4:46', 4, 46, 'minalla<u>dz</u>iina h<u>aa</u>duu yu<u>h</u>arrifuuna lkalima \'an maw<u>aad</u>i\'ihi wayaquuluuna sami\'n<u>aa</u> wa\'a<u>sh</u>ayn<u>aa</u> wasma\'ghayra musma\'in war<u>aa</u>\'in<u>aa</u> layyan bi-alsinatihim wa<u>th</u>a\'nan fii ddiini walaw annahum q<u>aa</u>luu sami\'n<u>aa</u> wa-a<u>th</u>a\'n<u>aa</u> wasma\'wan<u>zh</u>urn<u>aa</u> lak<u>aa</u>na khayran lahum wa-aqwama wal<u>aa</u>kin la\'anahumull<u>aa</u>hu bikufrihim fal<u>aa</u> yu/minuuna ill<u>aa</u> qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (540, '4:47', 4, 47, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina uutuu lkit<u>aa</u>ba <u>aa</u>minuu bim<u>aa</u> nazzaln<u>aa</u> mu<u>sh</u>addiqan lim<u>aa</u> ma\'akum min qabli an na<u>th</u>misa wujuuhan fanaruddah<u>aa</u> \'al<u>aa</u> adb<u>aa</u>rih<u>aa</u> aw nal\'anahum kam<u>aa</u> la\'ann<u>aa</u> a<u>sh</u>-<u>haa</u>ba ssabti wak<u>aa</u>na amrull<u>aa</u>hi maf\'uul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (541, '4:48', 4, 48, 'innall<u>aa</u>ha l<u>aa</u> yaghfiru an yusyraka bihi wayaghfiru m<u>aa</u> duuna <u>dzaa</u>lika liman yasy<u>aa</u>u waman yusyrik bil<u>aa</u>hi faqadi iftar<u>aa</u> itsman \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (542, '4:49', 4, 49, 'alam tara il<u>aa</u>lla<u>dz</u>iina yuzakkuuna anfusahum balill<u>aa</u>hu yuzakkii man yasy<u>aa</u>u wal<u>aa</u> yu<u>zh</u>lamuuna fatiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (543, '4:50', 4, 50, 'un<u>zh</u>ur kayfa yaftaruuna \'al<u>aa</u>ll<u>aa</u>hi lka<u>dz</u>iba wakaf<u>aa</u> bihi itsman mubiin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (544, '4:51', 4, 51, 'alam tara il<u>aa</u>lla<u>dz</u>iina uutuu na<u>sh</u>iiban mina lkit<u>aa</u>bi yu/minuuna biljibti wa<u>ththaa</u>ghuuti wayaquuluuna lilla<u>dz</u>iina kafaruu h<u>aa</u>ul<u>aa</u>-i ahd<u>aa</u> minalla<u>dz</u>iina <u>aa</u>manuu sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (545, '4:52', 4, 52, 'ul<u>aa</u>-ikalla<u>dz</u>iina la\'anahumull<u>aa</u>hu waman yal\'anill<u>aa</u>hu falan tajida lahu na<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (546, '4:53', 4, 53, 'am lahum na<u>sh</u>iibun mina lmulki fa-i<u>dz</u>an l<u>aa</u> yu/tuuna nn<u>aa</u>sa naqiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (547, '4:54', 4, 54, 'am ya<u>h</u>suduuna nn<u>aa</u>sa \'al<u>aa</u> m<u>aa</u> <u>aa</u>t<u>aa</u>humull<u>aa</u>hu min fa<u>dh</u>lihi faqad <u>aa</u>tayn<u>aa</u> <u>aa</u>la ibr<u>aa</u>hiima lkit<u>aa</u>ba wal<u>h</u>ikmata wa<u>aa</u>tayn<u>aa</u>hum mulkan \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (548, '4:55', 4, 55, 'faminhum man <u>aa</u>mana bihi waminhum man <u>sh</u>adda \'anhu wakaf<u>aa</u> bijahannama sa\'iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (549, '4:56', 4, 56, 'innalla<u>dz</u>iina kafaruu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> sawfa nu<u>sh</u>liihim n<u>aa</u>ran kullam<u>aa</u> na<u>dh</u>ijat juluuduhum baddaln<u>aa</u>hum juluudan ghayrah<u>aa</u> liya<u>dz</u>uuquu l\'a<u>dzaa</u>ba innall<u>aa</u>ha k<u>aa</u>na \'aziizan <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (550, '4:57', 4, 57, 'walla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti sanudkhiluhum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> abadan lahum fiih<u>aa</u> azw<u>aa</u>jun mu<u>th</u>ahharatun wanudkhiluhum <u>zh</u>illan <u>zh</u>aliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (551, '4:58', 4, 58, 'innall<u>aa</u>ha ya/murukum an tu-adduu l-am<u>aa</u>n<u>aa</u>ti il<u>aa</u> ahlih<u>aa</u> wa-i<u>dzaa</u> <u>h</u>akamtum bayna nn<u>aa</u>si an ta<u>h</u>kumuu bil\'adli innall<u>aa</u>ha ni\'imm<u>aa</u> ya\'i<u>zh</u>ukum bihi innall<u>aa</u>ha k<u>aa</u>na samii\'an ba<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (552, '4:59', 4, 59, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu a<u>th</u>ii\'uull<u>aa</u>ha wa-a<u>th</u>ii\'uu rrasuula waulii l-amri minkum fa-in tan<u>aa</u>za\'tum fii syay-in farudduuhu il<u>aa</u>ll<u>aa</u>hi warrasuuli in kuntum tu/minuuna bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri <u>dzaa</u>lika khayrun wa-a<u>h</u>sanu ta/wiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (553, '4:60', 4, 60, 'alam tara il<u>aa</u>lla<u>dz</u>iina yaz\'umuuna annahum <u>aa</u>manuu bim<u>aa</u> unzila ilayka wam<u>aa</u> unzila min qablika yuriiduuna an yata<u>haa</u>kamuu il<u>aa</u> <u>ththaa</u>ghuuti waqad umiruu an yakfuruu bihi wayuriidu sysyay<u>th</u><u>aa</u>nu an yu<u>dh</u>illahum <u>dh</u>al<u>aa</u>lan ba\'iid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (554, '4:61', 4, 61, 'wa-i<u>dzaa</u> qiila lahum ta\'<u>aa</u>law il<u>aa</u> m<u>aa</u> anzalall<u>aa</u>hu wa-il<u>aa</u> rrasuuli ra-ayta lmun<u>aa</u>fiqiina ya<u>sh</u>udduuna \'anka <u>sh</u>uduud<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (555, '4:62', 4, 62, 'fakayfa i<u>dzaa</u> a<u>shaa</u>bat-hum mu<u>sh</u>iibatun bim<u>aa</u> qaddamat aydiihim tsumma j<u>aa</u>uuka ya<u>h</u>lifuuna bil<u>aa</u>hi in aradn<u>aa</u> ill<u>aa</u> i<u>h</u>s<u>aa</u>nan watawfiiq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (556, '4:63', 4, 63, 'ul<u>aa</u>-ikalla<u>dz</u>iina ya\'lamull<u>aa</u>hu m<u>aa</u> fii quluubihim fa-a\'ri<u>dh</u> \'anhum wa\'i<u>zh</u>hum waqul lahum fii anfusihim qawlan baliigh<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (557, '4:64', 4, 64, 'wam<u>aa</u> arsaln<u>aa</u> min rasuulin ill<u>aa</u> liyu<u>th</u><u>aa</u>\'a bi-i<u>dz</u>nill<u>aa</u>hi walaw annahum i<u>dz</u> <u>zh</u>alamuu anfusahum j<u>aa</u>uuka fastaghfaruull<u>aa</u>ha wastaghfara lahumu rrasuulu lawajaduull<u>aa</u>ha taww<u>aa</u>ban ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (558, '4:65', 4, 65, 'fal<u>aa</u> warabbika l<u>aa</u> yu/minuuna <u>h</u>att<u>aa</u> yu<u>h</u>akkimuuka fiim<u>aa</u> syajara baynahum tsumma l<u>aa</u> yajiduu fii anfusihim <u>h</u>arajan mimm<u>aa</u> qa<u>dh</u>ayta wayusallimuu tasliim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (559, '4:66', 4, 66, 'walaw ann<u>aa</u> katabn<u>aa</u> \'alayhim ani uqtuluu anfusakum awi ukhrujuu min diy<u>aa</u>rikum m<u>aa</u> fa\'aluuhu ill<u>aa</u> qaliilun minhum walaw annahum fa\'aluu m<u>aa</u> yuu\'a<u>zh</u>uuna bihi lak<u>aa</u>na khayran lahum wa-asyadda tatsbiit<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (560, '4:67', 4, 67, 'wa-i<u>dz</u>an la<u>aa</u>tayn<u>aa</u>hum min ladunn<u>aa</u> ajran \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (561, '4:68', 4, 68, 'walahadayn<u>aa</u>hum <u>sh</u>ir<u>aath</u>an mustaqiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (562, '4:69', 4, 69, 'waman yu<u>th</u>i\'ill<u>aa</u>ha warrasuula faul<u>aa</u>-ika ma\'alla<u>dz</u>iina an\'amall<u>aa</u>hu \'alayhim mina nnabiyyiina wa<u>shsh</u>iddiiqiina wasysyuhad<u>aa</u>-i wa<u>shshaa</u>li<u>h</u>iina wa<u>h</u>asuna ul<u>aa</u>-ika rafiiq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (563, '4:70', 4, 70, '<u>dzaa</u>lika lfa<u>dh</u>lu minall<u>aa</u>hi wakaf<u>aa</u> bil<u>aa</u>hi \'aliim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (564, '4:71', 4, 71, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu khu<u>dz</u>uu <u>h</u>i<u>dz</u>rakum fanfiruu tsub<u>aa</u>tin awi infiruu jamii\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (565, '4:72', 4, 72, 'wa-inna minkum laman layuba<u>ththh</u>i-anna fa-in a<u>shaa</u>batkum mu<u>sh</u>iibatun q<u>aa</u>la qad an\'amall<u>aa</u>hu \'alayya i<u>dz</u> lam akun ma\'ahum syahiid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (566, '4:73', 4, 73, 'wala-in a<u>shaa</u>bakum fa<u>dh</u>lun minall<u>aa</u>hi layaquulanna ka-an lam takun baynakum wabaynahu mawaddatun y<u>aa</u> laytanii kuntu ma\'ahum fa-afuuza fawzan \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (567, '4:74', 4, 74, 'falyuq<u>aa</u>til fii sabiilill<u>aa</u>hilla<u>dz</u>iina yasyruuna l<u>h</u>ay<u>aa</u>ta dduny<u>aa</u> bil-<u>aa</u>khirati waman yuq<u>aa</u>til fii sabiilill<u>aa</u>hi fayuqtal aw yaghlib fasawfa nu/tiihi ajran \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (568, '4:75', 4, 75, 'wam<u>aa</u> lakum l<u>aa</u> tuq<u>aa</u>tiluuna fii sabiilill<u>aa</u>hi walmusta<u>dh</u>\'afiina mina rrij<u>aa</u>li wannis<u>aa</u>-i walwild<u>aa</u>nilla<u>dz</u>iina yaquuluuna rabban<u>aa</u> akhrijn<u>aa</u> min h<u>aadz</u>ihi lqaryati <u>zhzhaa</u>limi ahluh<u>aa</u> waj\'al lan<u>aa</u> min ladunka waliyyan waj\'al lan<u>aa</u> min ladunka na<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (569, '4:76', 4, 76, '<B>al</b>la<u>dz</u>iina <u>aa</u>manuu yuq<u>aa</u>tiluuna fii sabiilill<u>aa</u>hi walla<u>dz</u>iina kafaruu yuq<u>aa</u>tiluuna fii sabiili <u>ththaa</u>ghuuti faq<u>aa</u>tiluu awliy<u>aa</u>-a sysyay<u>th</u><u>aa</u>ni inna kayda sysyay<u>th</u><u>aa</u>ni k<u>aa</u>na <u>dh</u>a\'iif<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (570, '4:77', 4, 77, 'alam tara il<u>aa</u>lla<u>dz</u>iina qiila lahum kuffuu aydiyakum wa-aqiimuu <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tuu zzak<u>aa</u>ta falamm<u>aa</u> kutiba \'alayhimu lqit<u>aa</u>lu i<u>dzaa</u> fariiqun minhum yakhsyawna nn<u>aa</u>sa kakhasyyatill<u>aa</u>hi aw asyadda khasyyatan waq<u>aa</u>luu rabban<u>aa</u> lima katabta \'alayn<u>aa</u> lqit<u>aa</u>la lawl<u>aa</u> akhkhartan<u>aa</u> il<u>aa</u> ajalin qariibin qul mat<u>aa</u>\'u dduny<u>aa</u> qaliilun wal-<u>aa</u>khiratu khayrun limani ittaq<u>aa</u> wal<u>aa</u> tu<u>zh</u>lamuuna fatiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (571, '4:78', 4, 78, 'aynam<u>aa</u> takuunuu yudrikkumu lmawtu walaw kuntum fii buruujin musyayyadatin wa-in tu<u>sh</u>ibhum <u>h</u>asanatun yaquuluu h<u>aadz</u>ihi min \'indill<u>aa</u>hi wa-in tu<u>sh</u>ibhum sayyi-atun yaquuluu h<u>aadz</u>ihi min \'indika qul kullun min \'indill<u>aa</u>hi fam<u>aa</u>li h<u>aa</u>ul<u>aa</u>-i lqawmi l<u>aa</u> yak<u>aa</u>duuna yafqahuuna <u>h</u>adiits<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (572, '4:79', 4, 79, 'm<u>aa</u> a<u>shaa</u>baka min <u>h</u>asanatin faminall<u>aa</u>hi wam<u>aa</u> a<u>shaa</u>baka min sayyi-atin famin nafsika wa-arsaln<u>aa</u>ka linn<u>aa</u>si rasuulan wakaf<u>aa</u> bil<u>aa</u>hi syahiidan</u>\r\r\n');
INSERT INTO quranLatin VALUES (573, '4:80', 4, 80, 'man yu<u>th</u>i\'i rrasuula faqad a<u>th</u><u>aa</u>\'all<u>aa</u>ha waman tawall<u>aa</u> fam<u>aa</u> arsaln<u>aa</u>ka \'alayhim <u>h</u>afii<u>zhaa</u>\r\r\n');
INSERT INTO quranLatin VALUES (574, '4:81', 4, 81, 'wayaquuluuna <u>th</u><u>aa</u>\'atun fa-i<u>dzaa</u> barazuu min \'indika bayyata <u>th</u><u>aa</u>-ifatun minhum ghayralla<u>dz</u>ii taquulu wal<u>aa</u>hu yaktubu m<u>aa</u> yubayyituuna fa-a\'ri<u>dh</u> \'anhum watawakkal \'al<u>aa</u>ll<u>aa</u>hi wakaf<u>aa</u> bil<u>aa</u>hi wakiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (575, '4:82', 4, 82, 'afal<u>aa</u> yatadabbaruuna lqur-<u>aa</u>na walaw k<u>aa</u>na min \'indi ghayrill<u>aa</u>hi lawajaduu fiihi ikhtil<u>aa</u>fan katsiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (576, '4:83', 4, 83, 'wa-i<u>dzaa</u> j<u>aa</u>-ahum amrun mina l-amni awi lkhawfi a<u>dzaa</u>\'uu bihi walaw radduuhu il<u>aa</u> rrasuuli wa-il<u>aa</u> ulii l-amri minhum la\'alimahulla<u>dz</u>iina yastanbi<u>th</u>uunahu minhum walawl<u>aa</u> fa<u>dh</u>lull<u>aa</u>hi \'alaykum wara<u>h</u>matuhu lattaba\'tumu sysyay<u>th</u><u>aa</u>na ill<u>aa</u> qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (577, '4:84', 4, 84, 'faq<u>aa</u>til fii sabiilill<u>aa</u>hi l<u>aa</u> tukallafu ill<u>aa</u> nafsaka wa<u>h</u>arri<u>dh</u>i lmu/miniina \'as<u>aa</u>ll<u>aa</u>hu an yakuffa ba/salla<u>dz</u>iina kafaruu wal<u>aa</u>hu asyaddu ba/san wa-asyaddu tankiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (578, '4:85', 4, 85, 'man yasyfa\'syaf<u>aa</u>\'atan <u>h</u>asanatan yakun lahu na<u>sh</u>iibun minh<u>aa</u> waman yasyfa\'syaf<u>aa</u>\'atan sayyi-atan yakun lahu kiflun minh<u>aa</u> wak<u>aa</u>nall<u>aa</u>hu \'al<u>aa</u> kulli syay-in muqiit<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (579, '4:86', 4, 86, 'wa-i<u>dzaa</u> <u>h</u>uyyiitum bita<u>h</u>iyyatin fa<u>h</u>ayyuu bi-a<u>h</u>sana minh<u>aa</u> aw rudduuh<u>aa</u> innall<u>aa</u>ha k<u>aa</u>na \'al<u>aa</u> kulli syay-in <u>h</u>asiib<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (580, '4:87', 4, 87, '<B>al</b>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa layajma\'annakum il<u>aa</u> yawmi lqiy<u>aa</u>mati l<u>aa</u> rayba fiihi waman a<u>sh</u>daqu minall<u>aa</u>hi <u>h</u>adiits<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (581, '4:88', 4, 88, 'fam<u>aa</u> lakum fii lmun<u>aa</u>fiqiina fi-atayni wal<u>aa</u>hu arkasahum bim<u>aa</u> kasabuu aturiiduuna an tahduu man a<u>dh</u>allall<u>aa</u>hu waman yu<u>dh</u>lilill<u>aa</u>hu falan tajida lahu sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (582, '4:89', 4, 89, 'wadduu law takfuruuna kam<u>aa</u> kafaruu fatakuunuuna saw<u>aa</u>-an fal<u>aa</u> tattakhi<u>dz</u>uu minhum awliy<u>aa</u>-a <u>h</u>att<u>aa</u> yuh<u>aa</u>jiruu fii sabiilill<u>aa</u>hi fa-in tawallaw fakhu<u>dz</u>uuhum waqtuluuhum <u>h</u>aytsu wajadtumuuhum wal<u>aa</u> tattakhi<u>dz</u>uu minhum waliyyan wal<u>aa</u> na<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (583, '4:90', 4, 90, 'ill<u>aa</u>lla<u>dz</u>iina ya<u>sh</u>iluuna il<u>aa</u> qawmin baynakum wabaynahum miits<u>aa</u>qun aw j<u>aa</u>uukum <u>h</u>a<u>sh</u>irat <u>sh</u>uduuruhum an yuq<u>aa</u>tiluukum aw yuq<u>aa</u>tiluu qawmahum walaw sy<u>aa</u>-all<u>aa</u>hu lasalla<u>th</u>ahum \'alaykum falaq<u>aa</u>taluukum fa-ini i\'tazaluukum falam yuq<u>aa</u>tiluukum wa-alqaw ilaykumu ssalama fam<u>aa</u> ja\'alall<u>aa</u>hu lakum \'alayhim sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (584, '4:91', 4, 91, 'satajiduuna <u>aa</u>khariina yuriiduuna an ya/manuukum waya/manuu qawmahum kulla m<u>aa</u> rudduu il<u>aa</u> lfitnati urkisuu fiih<u>aa</u> fa-in lam ya\'taziluukum wayulquu ilaykumu ssalama wayakuffuu aydiyahum fakhu<u>dz</u>uuhum waqtuluuhum <u>h</u>aytsu tsaqiftumuuhum waul<u>aa</u>-ikum ja\'aln<u>aa</u> lakum \'alayhim sul<u>th</u><u>aa</u>nan mubiin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (585, '4:92', 4, 92, 'wam<u>aa</u> k<u>aa</u>na limu/minin an yaqtula mu/minan ill<u>aa</u> kha<u>th</u>a-an waman qatala mu/minan kha<u>th</u>a-an fata<u>h</u>riiru raqabatin mu/minatin wadiyatun musallamatun il<u>aa</u> ahlihi ill<u>aa</u> an ya<u>shsh</u>addaquu fa-in k<u>aa</u>na min qawmin \'aduwwin lakum wahuwa mu/minun fata<u>h</u>riiru raqabatin mu/minatin wa-in k<u>aa</u>na min qawmin baynakum wabaynahum miits<u>aa</u>qun fadiyatun musallamatun il<u>aa</u> ahlihi wata<u>h</u>riiru raqabatin mu/minatin faman lam yajid fa<u>sh</u>iy<u>aa</u>mu syahrayni mutat<u>aa</u>bi\'ayni tawbatan minall<u>aa</u>hi wak<u>aa</u>nall<u>aa</u>hu \'aliiman <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (586, '4:93', 4, 93, 'waman yaqtul mu/minan muta\'ammidan fajaz<u>aa</u>uhu jahannamu kh<u>aa</u>lidan fiih<u>aa</u> wagha<u>dh</u>iball<u>aa</u>hu \'alayhi wala\'anahu wa-a\'adda lahu \'a<u>dzaa</u>ban \'a<u>zh</u>iim<u>aa</u><b>n </b>\r\r\n');
INSERT INTO quranLatin VALUES (587, '4:94', 4, 94, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> <u>dh</u>arabtum fii sabiilill<u>aa</u>hi fatabayyanuu wal<u>aa</u> taquuluu liman lq<u>aa</u> ilaykumu ssal<u>aa</u>ma lasta mu/minan tabtaghuuna \'ara<u>dh</u>a l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> fa\'indall<u>aa</u>hi magh<u>aa</u>nimu katsiiratun ka<u>dzaa</u>lika kuntum min qablu famannall<u>aa</u>hu \'alaykum fatabayyanuu innall<u>aa</u>ha k<u>aa</u>na bim<u>aa</u> ta\'maluuna khabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (588, '4:95', 4, 95, 'l<u>aa</u> yastawii lq<u>aa</u>\'iduuna mina lmu/miniina ghayru ulii <u>dhdh</u>arari walmuj<u>aa</u>hiduuna fii sabiilill<u>aa</u>hi bi-amw<u>aa</u>lihim wa-anfusihim fa<u>dhdh</u>alall<u>aa</u>hu lmuj<u>aa</u>hidiina bi-amw<u>aa</u>lihim wa-anfusihim \'al<u>aa</u> lq<u>aa</u>\'idiina darajatan wakullan wa\'adall<u>aa</u>hu l<u>h</u>usn<u>aa</u> wafa<u>dhdh</u>alall<u>aa</u>hu lmuj<u>aa</u>hidiina \'al<u>aa</u> lq<u>aa</u>\'idiina ajran \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (589, '4:96', 4, 96, 'daraj<u>aa</u>tin minhu wamaghfiratan wara<u>h</u>matan wak<u>aa</u>nall<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (590, '4:97', 4, 97, 'innalla<u>dz</u>iina tawaff<u>aa</u>humu almal<u>aa</u>-ikatu <u>zhaa</u>limii anfusihim q<u>aa</u>luu fiima kuntum q<u>aa</u>luu kunn<u>aa</u> musta<u>dh</u>\'afiina fii al-ar<u>dh</u>i q<u>aa</u>luu alam takun ar<u>dh</u>ull<u>aa</u>hi w<u>aa</u>si\'atan fatuh<u>aa</u>jiruu fiih<u>aa</u> faul<u>aa</u>-ika ma/w<u>aa</u>hum jahannamu was<u>aa</u>-at ma<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (591, '4:98', 4, 98, 'ill<u>aa</u> lmusta<u>dh</u>\'afiina mina rrij<u>aa</u>li wannis<u>aa</u>-i walwild<u>aa</u>ni l<u>aa</u> yasta<u>th</u>ii\'uuna <u>h</u>iilatan wal<u>aa</u> yahtaduuna sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (592, '4:99', 4, 99, 'faul<u>aa</u>-ika \'as<u>aa</u>ll<u>aa</u>hu an ya\'fuwa \'anhum wak<u>aa</u>nall<u>aa</u>hu \'afuwwan ghafuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (593, '4:100', 4, 100, 'waman yuh<u>aa</u>jir fii sabiilill<u>aa</u>hi yajid fii l-ar<u>dh</u>i mur<u>aa</u>ghaman katsiiran wasa\'atan waman yakhruj min baytihi muh<u>aa</u>jiran il<u>aa</u>ll<u>aa</u>hi warasuulihi tsumma yudrik-hu lmawtu faqad waqa\'a ajruhu \'al<u>aa</u>ll<u>aa</u>hi wak<u>aa</u>nall<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (594, '4:101', 4, 101, 'wa-i<u>dzaa</u> <u>dh</u>arabtum fii l-ar<u>dh</u>i falaysa \'alaykum jun<u>aah</u>un an taq<u>sh</u>uruu mina <u>shsh</u>al<u>aa</u>ti in khiftum an yaftinakumulla<u>dz</u>iina kafaruu inna lk<u>aa</u>firiina k<u>aa</u>nuu lakum \'aduwwan mubiin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (595, '4:102', 4, 102, 'wa-i<u>dzaa</u> kunta fiihim fa-aqamta lahumu <u>shsh</u>al<u>aa</u>ta faltaqum <u>th</u><u>aa</u>-ifatun minhum ma\'aka walya/khu<u>dz</u>uu asli<u>h</u>atahum fa-i<u>dzaa</u> sajaduu falyakuunuu min war<u>aa</u>-ikum walta/ti <u>th</u><u>aa</u>-ifatun ukhr<u>aa</u> lam yu<u>sh</u>alluu falyu<u>sh</u>alluu ma\'aka walya/khu<u>dz</u>uu <u>h</u>i<u>dz</u>rahum wa-asli<u>h</u>atahum waddalla<u>dz</u>iina kafaruu law taghfuluuna \'an asli<u>h</u>atikum wa-amti\'atikum fayamiiluuna \'alaykum maylatan w<u>aah</u>idatan wal<u>aa</u> jun<u>aah</u>a \'alaykum in k<u>aa</u>na bikum a<u>dz</u>an min ma<u>th</u>arin aw kuntum mar<u>daa</u> an ta<u>dh</u>a\'uu asli<u>h</u>atakum wakhu<u>dz</u>uu <u>h</u>i<u>dz</u>rakum innall<u>aa</u>ha a\'adda lilk<u>aa</u>firiina \'a<u>dzaa</u>ban muhiin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (596, '4:103', 4, 103, 'fa-i<u>dzaa</u> qa<u>dh</u>aytumu <u>shsh</u>al<u>aa</u>ta fa<u>dz</u>kuruull<u>aa</u>ha qiy<u>aa</u>man waqu\'uudan wa\'al<u>aa</u> junuubikum fa-i<u>dzaa</u> i<u>th</u>ma/nantum fa-aqiimuu <u>shsh</u>al<u>aa</u>ta inna <u>shsh</u>al<u>aa</u>ta k<u>aa</u>nat \'al<u>aa</u> lmu/miniina kit<u>aa</u>ban mawquut<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (597, '4:104', 4, 104, 'wal<u>aa</u> tahinuu fii ibtigh<u>aa</u>-i lqawmi in takuunuu ta/lamuuna fa-innahum ya/lamuuna kam<u>aa</u> ta/lamuuna watarjuuna minall<u>aa</u>hi m<u>aa</u> l<u>aa</u> yarjuuna wak<u>aa</u>nall<u>aa</u>hu \'aliiman <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (598, '4:105', 4, 105, 'inn<u>aa</u> anzaln<u>aa</u> ilayka lkit<u>aa</u>ba bil<u>h</u>aqqi lita<u>h</u>kuma bayna nn<u>aa</u>si bim<u>aa</u> ar<u>aa</u>kall<u>aa</u>hu wal<u>aa</u> takun lilkh<u>aa</u>-iniina kha<u>sh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (599, '4:106', 4, 106, 'wastaghfirill<u>aa</u>ha innall<u>aa</u>ha k<u>aa</u>na ghafuuran ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (600, '4:107', 4, 107, 'wal<u>aa</u> tuj<u>aa</u>dil \'anilla<u>dz</u>iina yakht<u>aa</u>nuuna anfusahum innall<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu man k<u>aa</u>na khaww<u>aa</u>nan atsiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (601, '4:108', 4, 108, 'yastakhfuuna mina nn<u>aa</u>si wal<u>aa</u> yastakhfuuna minall<u>aa</u>hi wahuwa ma\'ahum i<u>dz</u> yubayyituuna m<u>aa</u> l<u>aa</u> yar<u>daa</u> mina lqawli wak<u>aa</u>nall<u>aa</u>hu bim<u>aa</u> ya\'maluuna mu<u>h</u>ii<u>th</u><u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (602, '4:109', 4, 109, 'h<u>aa</u>-antum h<u>aa</u>ul<u>aa</u>-i j<u>aa</u>daltum \'anhum fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> faman yuj<u>aa</u>dilull<u>aa</u>ha \'anhum yawma lqiy<u>aa</u>mati am man yakuunu \'alayhim wakiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (603, '4:110', 4, 110, 'waman ya\'mal suu-an aw ya<u>zh</u>lim nafsahu tsumma yastaghfirill<u>aa</u>ha yajidill<u>aa</u>ha ghafuuran ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (604, '4:111', 4, 111, 'waman yaksib itsman fa-innam<u>aa</u> yaksibuhu \'al<u>aa</u> nafsihi wak<u>aa</u>nall<u>aa</u>hu \'aliiman <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (605, '4:112', 4, 112, 'waman yaksib kha<u>th</u>ii-atan aw itsman tsumma yarmi bihi barii-an faqadi i<u>h</u>tamala buht<u>aa</u>nan wa-itsman mubiin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (606, '4:113', 4, 113, 'walawl<u>aa</u> fa<u>dh</u>lull<u>aa</u>hi \'alayka wara<u>h</u>matuhu lahammat <u>th</u><u>aa</u>-ifatun minhum an yu<u>dh</u>illuuka wam<u>aa</u> yu<u>dh</u>illuuna ill<u>aa</u> anfusahum wam<u>aa</u> ya<u>dh</u>urruunaka min syay-in wa-anzalall<u>aa</u>hu \'alayka lkit<u>aa</u>ba wal<u>h</u>ikmata wa\'allamaka m<u>aa</u> lam takun ta\'lamu wak<u>aa</u>na fa<u>dh</u>lull<u>aa</u>hi \'alayka \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (607, '4:114', 4, 114, 'l<u>aa</u> khayra fii katsiirin min najw<u>aa</u>hum ill<u>aa</u> man amara bi<u>sh</u>adaqatin aw ma\'ruufin aw i<u>sh</u>l<u>aah</u>in bayna nn<u>aa</u>si waman yaf\'al <u>dzaa</u>lika ibtigh<u>aa</u>-a mar<u>daa</u>till<u>aa</u>hi fasawfa nu/tiihi ajran \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (608, '4:115', 4, 115, 'waman yusy<u>aa</u>qiqi rrasuula min ba\'di m<u>aa</u> tabayyana lahu lhud<u>aa</u> wayattabi\'ghayra sabiili lmu/miniina nuwallihi m<u>aa</u> tawall<u>aa</u> wanu<u>sh</u>lihi jahannama was<u>aa</u>-at ma<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (609, '4:116', 4, 116, 'innall<u>aa</u>ha l<u>aa</u> yaghfiru an yusyraka bihi wayaghfiru m<u>aa</u> duuna <u>dzaa</u>lika liman yasy<u>aa</u>u waman yusyrik bil<u>aa</u>hi faqad <u>dh</u>alla <u>dh</u>al<u>aa</u>lan ba\'iid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (610, '4:117', 4, 117, 'in yad\'uuna min duunihi ill<u>aa</u> in<u>aa</u>tsan wa-in yad\'uuna ill<u>aa</u> syay<u>th</u><u>aa</u>nan mariid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (611, '4:118', 4, 118, 'la\'anahull<u>aa</u>hu waq<u>aa</u>la la-attakhi<u>dz</u>anna min \'ib<u>aa</u>dika na<u>sh</u>iiban mafruu<u>daa</u>\r\r\n');
INSERT INTO quranLatin VALUES (612, '4:119', 4, 119, 'walau<u>dh</u>illannahum walaumanniyannahum wala<u>aa</u>murannahum falayubattikunna <u>aatsaa</u>na l-an\'<u>aa</u>mi wala<u>aa</u>murannahum falayughayyirunna khalqall<u>aa</u>hi waman yattakhi<u>dz</u>i sysyay<u>th</u><u>aa</u>na waliyyan min duunill<u>aa</u>hi faqad khasira khusr<u>aa</u>nan mubiin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (613, '4:120', 4, 120, 'ya\'iduhum wayumanniihim wam<u>aa</u> ya\'iduhumu sysyay<u>th</u><u>aa</u>nu ill<u>aa</u> ghuruur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (614, '4:121', 4, 121, 'ul<u>aa</u>-ika ma/w<u>aa</u>hum jahannamu wal<u>aa</u> yajiduuna \'anh<u>aa</u> ma<u>h</u>ii<u>shaa</u>\r\r\n');
INSERT INTO quranLatin VALUES (615, '4:122', 4, 122, 'walla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti sanudkhiluhum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> abadan wa\'dall<u>aa</u>hi <u>h</u>aqqan waman a<u>sh</u>daqu minall<u>aa</u>hi qiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (616, '4:123', 4, 123, 'laysa bi-am<u>aa</u>niyyikum wal<u>aa</u> am<u>aa</u>niyyi ahli lkit<u>aa</u>bi man ya\'mal suu-an yujza bihi wal<u>aa</u> yajid lahu min duunill<u>aa</u>hi waliyyan wal<u>aa</u> na<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (617, '4:124', 4, 124, 'waman ya\'mal mina <u>shshaa</u>li<u>haa</u>ti min <u>dz</u>akarin aw unts<u>aa</u> wahuwa mu/minun faul<u>aa</u>-ika yadkhuluuna ljannata wal<u>aa</u> yu<u>zh</u>lamuuna naqiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (618, '4:125', 4, 125, 'waman a<u>h</u>sanu diinan mimman aslama wajhahu lill<u>aa</u>hi wahuwa mu<u>h</u>sinun wattaba\'a millata ibr<u>aa</u>hiima <u>h</u>aniifan wattakha<u>dz</u>all<u>aa</u>hu ibr<u>aa</u>hiima khaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (619, '4:126', 4, 126, 'walill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wak<u>aa</u>nall<u>aa</u>hu bikulli syay-in mu<u>h</u>ii<u>th</u><u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (620, '4:127', 4, 127, 'wayastaftuunaka fii nnis<u>aa</u>-i qulill<u>aa</u>hu yuftiikum fiihinna wam<u>aa</u> yutl<u>aa</u> \'alaykum fii lkit<u>aa</u>bi fii yat<u>aa</u>m<u>aa</u> nnis<u>aa</u>-ill<u>aa</u>tii l<u>aa</u> tu/tuunahunna m<u>aa</u> kutiba lahunna watarghabuuna an tanki<u>h</u>uuhunna walmusta<u>dh</u>\'afiina mina lwild<u>aa</u>ni wa-an taquumuu lilyat<u>aa</u>m<u>aa</u> bilqis<u>th</u>i wam<u>aa</u> taf\'aluu min khayrin fa-innall<u>aa</u>ha k<u>aa</u>na bihi \'aliim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (621, '4:128', 4, 128, 'wa-ini imra-atun kh<u>aa</u>fat min ba\'lih<u>aa</u> nusyuuzan aw i\'r<u>aad</u>an fal<u>aa</u> jun<u>aah</u>a \'alayhim<u>aa</u> an yu<u>sh</u>li<u>haa</u> baynahum<u>aa</u> <u>sh</u>ul<u>h</u>an wa<u>shsh</u>ul<u>h</u>u khayrun wau<u>hd</u>irati l-anfusu sysyu<u>hh</u>a wa-in tu<u>h</u>sinuu watattaquu fa-innall<u>aa</u>ha k<u>aa</u>na bim<u>aa</u> ta\'maluuna khabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (622, '4:129', 4, 129, 'walan tasta<u>th</u>ii\'uu an ta\'diluu bayna nnis<u>aa</u>-i walaw <u>h</u>ara<u>sh</u>tum fal<u>aa</u> tamiiluu kulla lmayli fata<u>dz</u>aruuh<u>aa</u> kalmu\'allaqati wa-in tu<u>sh</u>li<u>h</u>uu watattaquu fa-innall<u>aa</u>ha k<u>aa</u>na ghafuuran ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (623, '4:130', 4, 130, 'wa-in yatafarraq<u>aa</u> yughnill<u>aa</u>hu kullan min sa\'atihi wak<u>aa</u>nall<u>aa</u>hu w<u>aa</u>si\'an <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (624, '4:131', 4, 131, 'walill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i walaqad wa<u>shsh</u>ayn<u>aa</u>lla<u>dz</u>iina uutuu lkit<u>aa</u>ba min qablikum wa-iyy<u>aa</u>kum ani ittaquull<u>aa</u>ha wa-in takfuruu fa-inna lill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wak<u>aa</u>nall<u>aa</u>hu ghaniyyan <u>h</u>amiid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (625, '4:132', 4, 132, 'walill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wakaf<u>aa</u> bil<u>aa</u>hi wakiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (626, '4:133', 4, 133, 'in yasya/ yu<u>dz</u>hibkum ayyuh<u>aa</u> nn<u>aa</u>su waya/ti bi-<u>aa</u>khariina wak<u>aa</u>nall<u>aa</u>hu \'al<u>aa</u> <u>dzaa</u>lika qadiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (627, '4:134', 4, 134, 'man k<u>aa</u>na yuriidu tsaw<u>aa</u>ba dduny<u>aa</u> fa\'indall<u>aa</u>hi tsaw<u>aa</u>bu dduny<u>aa</u> wal-<u>aa</u>khirati wak<u>aa</u>nall<u>aa</u>hu samii\'an ba<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (628, '4:135', 4, 135, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu kuunuu qaww<u>aa</u>miina bilqis<u>th</u>i syuhad<u>aa</u>-a lill<u>aa</u>hi walaw \'al<u>aa</u> anfusikum awi lw<u>aa</u>lidayni wal-aqrabiina in yakun ghaniyyan aw faqiiran fal<u>aa</u>hu awl<u>aa</u> bihim<u>aa</u> fal<u>aa</u> tattabi\'uu lhaw<u>aa</u> an ta\'diluu wa-in talwuu aw tu\'ri<u>dh</u>uu fa-innall<u>aa</u>ha k<u>aa</u>na bim<u>aa</u> ta\'maluuna khabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (629, '4:136', 4, 136, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu <u>aa</u>minuu bil<u>aa</u>hi warasuulihi walkit<u>aa</u>billa<u>dz</u>ii nazzala \'al<u>aa</u> rasuulihi walkit<u>aa</u>billa<u>dz</u>ii anzala min qablu waman yakfur bil<u>aa</u>hi wamal<u>aa</u>-ikatihi wakutubihi warusulihi walyawmi l-<u>aa</u>khiri faqad <u>dh</u>alla <u>dh</u>al<u>aa</u>lan ba\'iid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (630, '4:137', 4, 137, 'innalla<u>dz</u>iina <u>aa</u>manuu tsumma kafaruu tsumma <u>aa</u>manuu tsumma kafaruu tsumma izd<u>aa</u>duu kufran lam yakunill<u>aa</u>hu liyaghfira lahum wal<u>aa</u> liyahdiyahum sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (631, '4:138', 4, 138, 'basysyiri lmun<u>aa</u>fiqiina bi-anna lahum \'a<u>dzaa</u>ban liim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (632, '4:139', 4, 139, '<B>al</b>la<u>dz</u>iina yattakhi<u>dz</u>uuna lk<u>aa</u>firiina awliy<u>aa</u>-a min duuni lmu/miniina ayabtaghuuna \'indahumu l\'izzata fa-inna l\'izzata lill<u>aa</u>hi jamii\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (633, '4:140', 4, 140, 'waqad nazzala \'alaykum fii lkit<u>aa</u>bi an i<u>dzaa</u> sami\'tum <u>aa</u>y<u>aa</u>till<u>aa</u>hi yukfaru bih<u>aa</u> wayustahzau bih<u>aa</u> fal<u>aa</u> taq\'uduu ma\'ahum <u>h</u>att<u>aa</u> yakhuu<u>dh</u>uu fii <u>h</u>adiitsin ghayrihi innakum i<u>dz</u>an mitsluhum innall<u>aa</u>ha j<u>aa</u>mi\'u lmun<u>aa</u>fiqiina walk<u>aa</u>firiina fii jahannama jamii\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (634, '4:141', 4, 141, '<B>al</b>la<u>dz</u>iina yatarabba<u>sh</u>uuna bikum fa-in k<u>aa</u>na lakum fat<u>h</u>un minall<u>aa</u>hi q<u>aa</u>luu alam nakun ma\'akum wa-in k<u>aa</u>na lilk<u>aa</u>firiina na<u>sh</u>iibun q<u>aa</u>luu alam nasta<u>h</u>wi<u>dz</u> \'alaykum wanamna\'kum mina almu/miniina fal<u>aa</u>hu ya<u>h</u>kumu baynakum yawma alqiy<u>aa</u>mati walan yaj\'alall<u>aa</u>hu lilk<u>aa</u>firiina \'al<u>aa</u> almu/miniina sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (635, '4:142', 4, 142, 'inna lmun<u>aa</u>fiqiina yukh<u>aa</u>di\'uunall<u>aa</u>ha wahuwa kh<u>aa</u>di\'uhum wa-i<u>dzaa</u> q<u>aa</u>muu il<u>aa</u> <u>shsh</u>al<u>aa</u>ti q<u>aa</u>muu kus<u>aa</u>l<u>aa</u> yur<u>aa</u>uuna nn<u>aa</u>sa wal<u>aa</u> ya<u>dz</u>kuruunall<u>aa</u>ha ill<u>aa</u> qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (636, '4:143', 4, 143, 'mu<u>dz</u>ab<u>dz</u>abiina bayna <u>dzaa</u>lika l<u>aa</u> il<u>aa</u> h<u>aa</u>ul<u>aa</u>-i wal<u>aa</u> il<u>aa</u> h<u>aa</u>ul<u>aa</u>-i waman yu<u>dh</u>lilill<u>aa</u>hu falan tajida lahu sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (637, '4:144', 4, 144, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattakhi<u>dz</u>uu lk<u>aa</u>firiina awliy<u>aa</u>-a min duuni lmu/miniina aturiiduuna an taj\'aluu lill<u>aa</u>hi \'alaykum sul<u>th</u><u>aa</u>nan mubiin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (638, '4:145', 4, 145, 'inna lmun<u>aa</u>fiqiina fii ddarki l-asfali mina nn<u>aa</u>ri walan tajida lahum na<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (639, '4:146', 4, 146, 'ill<u>aa</u>lla<u>dz</u>iina t<u>aa</u>buu wa-a<u>sh</u>la<u>h</u>uu wa\'ta<u>sh</u>amuu bil<u>aa</u>hi wa-akhla<u>sh</u>uu diinahum lill<u>aa</u>hi faul<u>aa</u>-ika ma\'a lmu/miniina wasawfa yu/till<u>aa</u>hu lmu/miniina ajran \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (640, '4:147', 4, 147, 'm<u>aa</u> yaf\'alull<u>aa</u>hu bi\'a<u>dzaa</u>bikum in syakartum wa<u>aa</u>mantum wak<u>aa</u>nall<u>aa</u>hu sy<u>aa</u>kiran \'aliim<u>aa</u>\r\r\n');
