INSERT INTO quranLatin VALUES (646, '4:153', 4, 153, 'yas-aluka ahlu lkit<u>aa</u>bi an tunazzila \'alayhim kit<u>aa</u>ban mina ssam<u>aa</u>-i faqad sa-aluu muus<u>aa</u> akbara min <u>dzaa</u>lika faq<u>aa</u>luu arin<u>aa</u>ll<u>aa</u>ha jahratan fa-akha<u>dz</u>at-humu <u>shshaa</u>\'iqatu bi<u>zh</u>ulmihim tsumma ittakha<u>dz</u>uu l\'ijla min ba\'di m<u>aa</u> j<u>aa</u>-at-humu lbayyin<u>aa</u>tu fa\'afawn<u>aa</u> \'an <u>dzaa</u>lika wa<u>aa</u>tayn<u>aa</u> muus<u>aa</u> sul<u>th</u><u>aa</u>nan mubiin<u>aa</u>\r\r\n');
