INSERT INTO quranLatin VALUES (649, '4:156', 4, 156, 'wabikufrihim waqawlihim \'al<u>aa</u> maryama buht<u>aa</u>nan \'a<u>zh</u>iim<u>aa</u>\r\r\n');
