INSERT INTO quranLatin VALUES (655, '4:162', 4, 162, 'l<u>aa</u>kini rr<u>aa</u>sikhuuna fii l\'ilmi minhum walmu/minuuna yu/minuuna bim<u>aa</u> unzila ilayka wam<u>aa</u> unzila min qablika walmuqiimiina <u>shsh</u>al<u>aa</u>ta walmu/tuuna zzak<u>aa</u>ta walmu/minuuna bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri ul<u>aa</u>-ika sanu/tiihim ajran \'a<u>zh</u>iim<u>aa</u>\r\r\n');
