INSERT INTO quranLatin VALUES (664, '4:171', 4, 171, 'y<u>aa</u> ahla lkit<u>aa</u>bi l<u>aa</u> taghluu fii diinikum wal<u>aa</u> taquuluu \'al<u>aa</u>ll<u>aa</u>hi ill<u>aa</u> l<u>h</u>aqqa innam<u>aa</u> lmasii<u>h</u>u \'iis<u>aa</u> ibnu maryama rasuulull<u>aa</u>hi wakalimatuhu lq<u>aa</u>h<u>aa</u> il<u>aa</u> maryama waruu<u>h</u>un minhu fa<u>aa</u>minuu bil<u>aa</u>hi warusulihi wal<u>aa</u> taquuluu tsal<u>aa</u>tsatun intahuu khayran lakum innam<u>aa</u>ll<u>aa</u>hu il<u>aa</u>hun w<u>aah</u>idun sub<u>haa</u>nahu an yakuuna lahu waladun lahu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wakaf<u>aa</u> bil<u>aa</u>hi wakiil<u>aa</u>\r\r\n');
