INSERT INTO quranLatin VALUES (669, '4:176', 4, 176, 'yastaftuunaka qulill<u>aa</u>hu yuftiikum fii lkal<u>aa</u>lati ini imruun halaka laysa lahu waladun walahu ukhtun falah<u>aa</u> ni<u>sh</u>fu m<u>aa</u> taraka wahuwa yaritsuh<u>aa</u> in lam yakun lah<u>aa</u> waladun fa-in k<u>aa</u>nat<u>aa</u> itsnatayni falahum<u>aa</u> tstsuluts<u>aa</u>ni mimm<u>aa</u> taraka wa-in k<u>aa</u>nuu ikhwatan rij<u>aa</u>lan wanis<u>aa</u>-an fali<u>dzdz</u>akari mitslu <u>h</u>a<u>zhzh</u>i luntsayayni yubayyinull<u>aa</u>hu lakum an ta<u>dh</u>illuu wal<u>aa</u>hu bikulli syay-in \'aliim<dir>\r\r\n');
INSERT INTO quranLatin VALUES (670, '5:1', 5, 1, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu awfuu bil\'uquudi u<u>h</u>illat lakum bahiimatu l-an\'<u>aa</u>mi ill<u>aa</u> m<u>aa</u> yutl<u>aa</u> \'alaykum ghayra mu<u>h</u>illii <u>shsh</u>aydi wa-antum <u>h</u>urumun innall<u>aa</u>ha ya<u>h</u>kumu m<u>aa</u> yuriid\r\r\n');
INSERT INTO quranLatin VALUES (671, '5:2', 5, 2, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tu<u>h</u>illuu sya\'<u>aa</u>-irall<u>aa</u>hi wal<u>aa</u> sysyahra l<u>h</u>ar<u>aa</u>ma wal<u>aa</u> lhadya wal<u>aa</u> lqal<u>aa</u>-ida wal<u>aa</u> <u>aa</u>mmiina lbayta l<u>h</u>ar<u>aa</u>ma yabtaghuuna fa<u>dh</u>lan min rabbihim wari<u>dh</u>w<u>aa</u>nan wa-i<u>dzaa</u> <u>h</u>alaltum fa<u>staa</u>duu wal<u>aa</u> yajrimannakum syana<u>aa</u>nu qawmin an <u>sh</u>adduukum \'ani lmasjidi l<u>h</u>ar<u>aa</u>mi an ta\'taduu wata\'<u>aa</u>wanuu \'al<u>aa</u> lbirri wattaqw<u>aa</u> wal<u>aa</u> ta\'<u>aa</u>wanuu \'al<u>aa</u> l-itsmi wal\'udw<u>aa</u>ni wattaquull<u>aa</u>ha innall<u>aa</u>ha syadiidu l\'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (672, '5:3', 5, 3, '<u>h</u>urrimat \'alaykumu lmaytatu waddamu wala<u>h</u>mu lkhinziiri wam<u>aa</u> uhilla lighayrill<u>aa</u>hi bihi walmunkhaniqatu walmawquu<u>dz</u>atu walmutaraddiyatu wanna<u>th</u>ii<u>h</u>atu wam<u>aa</u> akala ssabu\'u ill<u>aa</u> m<u>aa ts</u>akkaytum wam<u>aa</u> <u>dz</u>ubi<u>h</u>a \'al<u>aa</u> nnu<u>sh</u>ubi wa-an tastaqsimuu bil-azl<u>aa</u>mi <u>dzaa</u>likum fisqun lyawma ya-isalla<u>dz</u>iina kafaruu min diinikum fal<u>aa</u> takhsyawhum wakhsyawni lyawma akmaltu lakum diinakum wa-atmamtu \'alaykum ni\'matii wara<u>dh</u>iitu lakumu l-isl<u>aa</u>ma diinan famani i<u>dth</u>urra fii makhma<u>sh</u>atin ghayra mutaj<u>aa</u>nifin li-itsmin fa-innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (673, '5:4', 5, 4, 'yas-aluunaka m<u>aatsaa</u> u<u>h</u>illa lahum qul u<u>h</u>illa lakumu <u>ththh</u>ayyib<u>aa</u>tu wam<u>aa</u> \'allamtum mina ljaw<u>aa</u>ri<u>h</u>i mukallibiina tu\'allimuunahunna mimm<u>aa</u> \'allamakumull<u>aa</u>hu fakuluu mimm<u>aa</u> amsakna \'alaykum wa<u>dz</u>kuruu ismall<u>aa</u>hi \'alayhi wattaquull<u>aa</u>ha innall<u>aa</u>ha sarii\'u l<u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (674, '5:5', 5, 5, 'alyawma u<u>h</u>illa lakumu <u>ththh</u>ayyib<u>aa</u>tu wa<u>th</u>a\'<u>aa</u>mulla<u>dz</u>iina uutuu lkit<u>aa</u>ba <u>h</u>illun lakum wa<u>th</u>a\'<u>aa</u>mukum <u>h</u>illun lahum walmu<u>hs</u>an<u>aa</u>tu mina lmu/min<u>aa</u>ti walmu<u>hs</u>an<u>aa</u>tu minalla<u>dz</u>iina uutuu lkit<u>aa</u>ba min qablikum i<u>dzaa</u> <u>aa</u>taytumuuhunna ujuurahunna mu<u>hs</u>iniina ghayra mus<u>aa</u>fi<u>h</u>iina wal<u>aa</u> muttakhi<u>dz</u>ii akhd<u>aa</u>nin waman yakfur bil-iim<u>aa</u>ni faqad <u>h</u>abi<u>th</u>a \'amaluhu wahuwa fii l-<u>aa</u>khirati mina lkh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (675, '5:6', 5, 6, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> qumtum il<u>aa</u> <u>shsh</u>al<u>aa</u>ti faghsiluu wujuuhakum wa-aydiyakum il<u>aa</u> lmar<u>aa</u>fiqi wamsa<u>h</u>uu biruuusikum wa-arjulakum il<u>aa</u> lka\'bayni wa-in kuntum junuban fa<u>ththh</u>ahharuu wa-in kuntum mar<u>daa</u> aw \'al<u>aa</u> safarin aw j<u>aa</u>-a a<u>h</u>adun minkum mina lgh<u>aa</u>-i<u>th</u>i aw l<u>aa</u>mastumu nnis<u>aa</u>-a falam tajiduu m<u>aa</u>-an fatayammamuu <u>sh</u>a\'iidan <u>th</u>ayyiban famsa<u>h</u>uu biwujuuhikum wa-aydiikum minhu m<u>aa</u> yuriidull<u>aa</u>hu liyaj\'ala \'alaykum min <u>h</u>arajin wal<u>aa</u>kin yuriidu liyu<u>th</u>ahhirakum waliyutimma ni\'matahu \'alaykum la\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (676, '5:7', 5, 7, 'wa<u>dz</u>kuruu ni\'matall<u>aa</u>hi \'alaykum wamiits<u>aa</u>qahulla<u>dz</u>ii w<u>aa</u>tsaqakum bihi i<u>dz</u> qultum sami\'n<u>aa</u> wa-a<u>th</u>a\'n<u>aa</u> wattaquull<u>aa</u>ha innall<u>aa</u>ha \'aliimun bi<u>dzaa</u>ti <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (677, '5:8', 5, 8, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu kuunuu qaww<u>aa</u>miina lill<u>aa</u>hi syuhad<u>aa</u>-a bilqis<u>th</u>i wal<u>aa</u> yajrimannakum syana<u>aa</u>nu qawmin \'al<u>aa</u>ll<u>aa</u> ta\'diluu i\'diluu huwa aqrabu littaqw<u>aa</u> wattaquull<u>aa</u>ha innall<u>aa</u>ha khabiirun bim<u>aa</u> ta\'maluun\r\r\n');
