INSERT INTO quranLatin VALUES (679, '5:10', 5, 10, 'walla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu lja<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (680, '5:11', 5, 11, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu u<u>dz</u>kuruu ni\'matall<u>aa</u>hi \'alaykum i<u>dz</u> hamma qawmun an yabsu<u>th</u>uu ilaykum aydiyahum fakaffa aydiyahum \'ankum wattaquull<u>aa</u>ha wa\'al<u>aa</u>ll<u>aa</u>hi falyatawakkali lmu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (681, '5:12', 5, 12, 'walaqad akha<u>dz</u>all<u>aa</u>hu miits<u>aa</u>qa banii isr<u>aa</u>-iila waba\'atsn<u>aa</u> minhumu itsnay \'asyara naqiiban waq<u>aa</u>lall<u>aa</u>hu innii ma\'akum la-in aqamtumu <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>taytumu zzak<u>aa</u>ta wa<u>aa</u>mantum birusulii wa\'azzartumuuhum wa-aqra<u>dh</u>tumull<u>aa</u>ha qar<u>dh</u>an <u>h</u>asanan laukaffiranna \'ankum sayyi-<u>aa</u>tikum walaudkhilannakum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru faman kafara ba\'da <u>dzaa</u>lika minkum faqad <u>dh</u>alla saw<u>aa</u>-a ssabiil\r\r\n');
INSERT INTO quranLatin VALUES (682, '5:13', 5, 13, 'fabim<u>aa</u> naq<u>dh</u>ihim miits<u>aa</u>qahum la\'ann<u>aa</u>hum waja\'aln<u>aa</u> quluubahum q<u>aa</u>siyatan yu<u>h</u>arrifuuna lkalima \'an maw<u>aad</u>i\'ihi wanasuu <u>h</u>a<u>zhzh</u>an mimm<u>aa</u> <u>dz</u>ukkiruu bihi wal<u>aa</u> taz<u>aa</u>lu ta<u>ththh</u>ali\'u \'al<u>aa</u> kh<u>aa</u>-inatin minhum ill<u>aa</u> qaliilan minhum fa\'fu \'anhum wa<u>sh</u>fa<u>h</u> innall<u>aa</u>ha yu<u>h</u>ibbu lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (683, '5:14', 5, 14, 'waminalla<u>dz</u>iina q<u>aa</u>luu inn<u>aa</u> na<u>shaa</u>r<u>aa</u> akha<u>dz</u>n<u>aa</u> miits<u>aa</u>qahum fanasuu <u>h</u>a<u>zhzh</u>an mimm<u>aa</u> <u>dz</u>ukkiruu bihi fa-aghrayn<u>aa</u> baynahumu l\'ad<u>aa</u>wata walbagh<u>dhaa</u>-a il<u>aa</u> yawmi lqiy<u>aa</u>mati wasawfa yunabbi-uhumull<u>aa</u>hu bim<u>aa</u> k<u>aa</u>nuu ya<u>sh</u>na\'uun\r\r\n');
INSERT INTO quranLatin VALUES (684, '5:15', 5, 15, 'y<u>aa</u> ahla lkit<u>aa</u>bi qad j<u>aa</u>-akum rasuulun<u>aa</u> yubayyinu lakum katsiiran mimm<u>aa</u> kuntum tukhfuuna mina lkit<u>aa</u>bi waya\'fuu \'an katsiirin qad j<u>aa</u>-akum minall<u>aa</u>hi nuurun wakit<u>aa</u>bun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (685, '5:16', 5, 16, 'yahdii bihill<u>aa</u>hu mani ittaba\'a ri<u>dh</u>w<u>aa</u>nahu subula ssal<u>aa</u>mi wayukhrijuhum mina <u>zhzh</u>ulum<u>aa</u>ti il<u>aa</u> nnuuri bi-i<u>dz</u>nihi wayahdiihim il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (686, '5:17', 5, 17, 'laqad kafaralla<u>dz</u>iina q<u>aa</u>luu innall<u>aa</u>ha huwa lmasii<u>h</u>u ibnu maryama qul faman yamliku minall<u>aa</u>hi syay-an in ar<u>aa</u>da an yuhlika lmasii<u>h</u>a ibna maryama waummahu waman fii l-ar<u>dh</u>i jamii\'an walill<u>aa</u>hi mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wam<u>aa</u> baynahum<u>aa</u> yakhluqu m<u>aa</u> yasy<u>aa</u>u wal<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (687, '5:18', 5, 18, 'waq<u>aa</u>lati lyahuudu wanna<u>shaa</u>r<u>aa</u> na<u>h</u>nu abn<u>aa</u>ull<u>aa</u>hi wa-a<u>h</u>ibb<u>aa</u>uhu qul falima yu\'a<u>dzdz</u>ibukum bi<u>dz</u>unuubikum bal antum basyarun mimman khalaqa yaghfiru liman yasy<u>aa</u>u wayu\'a<u>dzdz</u>ibu man yasy<u>aa</u>u walill<u>aa</u>hi mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wam<u>aa</u> baynahum<u>aa</u> wa-ilayhi lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (688, '5:19', 5, 19, 'y<u>aa</u> ahla lkit<u>aa</u>bi qad j<u>aa</u>-akum rasuulun<u>aa</u> yubayyinu lakum \'al<u>aa</u> fatratin mina rrusuli an taquuluu m<u>aa</u> j<u>aa</u>-an<u>aa</u> min basyiirin wal<u>aa</u> na<u>dz</u>iirin faqad j<u>aa</u>-akum basyiirun wana<u>dz</u>iirun wal<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (689, '5:20', 5, 20, 'wa-i<u>dz</u> q<u>aa</u>la muus<u>aa</u> liqawmihi y<u>aa</u> qawmi u<u>dz</u>kuruu ni\'matall<u>aa</u>hi \'alaykum i<u>dz</u> ja\'ala fiikum anbiy<u>aa</u>-a waja\'alakum muluukan wa<u>aa</u>t<u>aa</u>kum m<u>aa</u> lam yu/ti a<u>h</u>adan mina l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (690, '5:21', 5, 21, 'y<u>aa</u> qawmi udkhuluu l-ar<u>dh</u>a lmuqaddasatallatii kataball<u>aa</u>hu lakum wal<u>aa</u> tartadduu \'al<u>aa</u> adb<u>aa</u>rikum fatanqalibuu kh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (691, '5:22', 5, 22, 'q<u>aa</u>luu y<u>aa</u> muus<u>aa</u> inna fiih<u>aa</u> qawman jabb<u>aa</u>riina wa-inn<u>aa</u> lan nadkhulah<u>aa</u> <u>h</u>att<u>aa</u> yakhrujuu minh<u>aa</u> fa-in yakhrujuu minh<u>aa</u> fa-inn<u>aa</u> d<u>aa</u>khiluun\r\r\n');
INSERT INTO quranLatin VALUES (692, '5:23', 5, 23, 'q<u>aa</u>la rajul<u>aa</u>ni minalla<u>dz</u>iina yakh<u>aa</u>fuuna an\'amall<u>aa</u>hu \'alayhim<u>aa</u> udkhuluu \'alayhimu lb<u>aa</u>ba fa-i<u>dzaa</u> dakhaltumuuhu fa-innakum gh<u>aa</u>libuuna wa\'al<u>aa</u>ll<u>aa</u>hi fatawakkaluu in kuntum mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (693, '5:24', 5, 24, 'q<u>aa</u>luu y<u>aa</u> muus<u>aa</u> inn<u>aa</u> lan nadkhulah<u>aa</u> abadan m<u>aa</u> d<u>aa</u>muu fiih<u>aa</u> fa-i<u>dz</u>hab anta warabbuka faq<u>aa</u>til<u>aa</u> inn<u>aa</u> h<u>aa</u>hun<u>aa</u> q<u>aa</u>\'iduun\r\r\n');
INSERT INTO quranLatin VALUES (694, '5:25', 5, 25, 'q<u>aa</u>la rabbi innii l<u>aa</u> amliku ill<u>aa</u> nafsii wa-akhii fafruq baynan<u>aa</u> wabayna lqawmi lf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (695, '5:26', 5, 26, 'q<u>aa</u>la fa-innah<u>aa</u> mu<u>h</u>arramatun \'alayhim arba\'iina sanatan yatiihuuna fii l-ar<u>dh</u>i fal<u>aa</u> ta/sa \'al<u>aa</u> lqawmi lf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (696, '5:27', 5, 27, 'watlu \'alayhim naba-a ibnay <u>aa</u>dama bil<u>h</u>aqqi i<u>dz</u> qarrab<u>aa</u> qurb<u>aa</u>nan fatuqubbila min a<u>h</u>adihim<u>aa</u> walam yutaqabbal mina l-<u>aa</u>khari q<u>aa</u>la la-aqtulannaka q<u>aa</u>la innam<u>aa</u> yataqabbalull<u>aa</u>hu mina lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (697, '5:28', 5, 28, 'la-in basa<u>th</u>ta ilayya yadaka litaqtulanii m<u>aa</u> an<u>aa</u> bib<u>aa</u>si<u>th</u>in yadiya ilayka li-aqtulaka innii akh<u>aa</u>full<u>aa</u>ha rabba l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (698, '5:29', 5, 29, 'innii uriidu an tabuu-a bi-itsmii wa-itsmika fatakuuna min a<u>sh</u>-<u>haa</u>bi nn<u>aa</u>ri wa<u>dzaa</u>lika jaz<u>aa</u>u <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (699, '5:30', 5, 30, 'fa<u>th</u>awwa\'at lahu nafsuhu qatla akhiihi faqatalahu fa-a<u>sh</u>ba<u>h</u>a mina lkh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (700, '5:31', 5, 31, 'faba\'atsall<u>aa</u>hu ghur<u>aa</u>ban yab<u>h</u>atsu fii l-ar<u>dh</u>i liyuriyahu kayfa yuw<u>aa</u>rii saw-ata akhiihi q<u>aa</u>la y<u>aa</u> waylat<u>aa</u> a\'ajaztu an akuuna mitsla h<u>aadzaa</u> lghur<u>aa</u>bi fauw<u>aa</u>riya saw-ata akhii fa-a<u>sh</u>ba<u>h</u>a mina nn<u>aa</u>dimiin\r\r\n');
INSERT INTO quranLatin VALUES (701, '5:32', 5, 32, 'min ajli <u>dzaa</u>lika katabn<u>aa</u> \'al<u>aa</u> banii isr<u>aa</u>-iila annahu man qatala nafsan bighayri nafsin aw fas<u>aa</u>din fii l-ar<u>dh</u>i faka-annam<u>aa</u> qatala nn<u>aa</u>sa jamii\'an waman a<u>h</u>y<u>aa</u>h<u>aa</u> faka-annam<u>aa</u> a<u>h</u>y<u>aa</u> nn<u>aa</u>sa jamii\'an walaqad j<u>aa</u>-at-hum rusulun<u>aa</u> bilbayyin<u>aa</u>ti tsumma inna katsiiran minhum ba\'da <u>dzaa</u>lika fii l-ar<u>dh</u>i lamusrifuun\r\r\n');
INSERT INTO quranLatin VALUES (702, '5:33', 5, 33, 'innam<u>aa</u> jaz<u>aa</u>ulla<u>dz</u>iina yu<u>haa</u>ribuunall<u>aa</u>ha warasuulahu wayas\'awna fii l-ar<u>dh</u>i fas<u>aa</u>dan an yuqattaluu aw yu<u>sh</u>allabuu aw tuqa<u>ththh</u>a\'a aydiihim wa-arjuluhum min khil<u>aa</u>fin aw yunfaw mina l-ar<u>dh</u>i <u>dzaa</u>lika lahum khizyun fii dduny<u>aa</u> walahum fii l-<u>aa</u>khirati \'a<u>dzaa</u>bun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (703, '5:34', 5, 34, 'ill<u>aa</u>lla<u>dz</u>iina t<u>aa</u>buu min qabli an taqdiruu \'alayhim fa\'lamuu annall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (704, '5:35', 5, 35, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu ittaquull<u>aa</u>ha wabtaghuu ilayhi lwasiilata waj<u>aa</u>hiduu fii sabiilihi la\'allakum tufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (705, '5:36', 5, 36, 'innalla<u>dz</u>iina kafaruu law anna lahum m<u>aa</u> fii l-ar<u>dh</u>i jamii\'an wamitslahu ma\'ahu liyaftaduu bihi min \'a<u>dzaa</u>bi yawmi lqiy<u>aa</u>mati m<u>aa</u> tuqubbila minhum walahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (706, '5:37', 5, 37, 'yuriiduuna an yakhrujuu mina nn<u>aa</u>ri wam<u>aa</u> hum bikh<u>aa</u>rijiina minh<u>aa</u> walahum \'a<u>dzaa</u>bun muqiim\r\r\n');
INSERT INTO quranLatin VALUES (707, '5:38', 5, 38, 'wass<u>aa</u>riqu wass<u>aa</u>riqatu faq<u>th</u>a\'uu aydiyahum<u>aa</u> jaz<u>aa</u>-an bim<u>aa</u> kasab<u>aa</u> nak<u>aa</u>lan minall<u>aa</u>hi wal<u>aa</u>hu \'aziizun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (708, '5:39', 5, 39, 'faman t<u>aa</u>ba min ba\'di <u>zh</u>ulmihi wa-a<u>sh</u>la<u>h</u>a fa-innall<u>aa</u>ha yatuubu \'alayhi innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (709, '5:40', 5, 40, 'alam ta\'lam annall<u>aa</u>ha lahu mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i yu\'a<u>dzdz</u>ibu man yasy<u>aa</u>u wayaghfiru liman yasy<u>aa</u>u wal<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (710, '5:41', 5, 41, 'y<u>aa</u> ayyuh<u>aa</u> rrasuulu l<u>aa</u> ya<u>h</u>zunkalla<u>dz</u>iina yus<u>aa</u>ri\'uuna fii lkufri minalla<u>dz</u>iina q<u>aa</u>luu <u>aa</u>mann<u>aa</u> bi-afw<u>aa</u>hihim walam tu/min quluubuhum waminalla<u>dz</u>iina h<u>aa</u>duu samm<u>aa</u>\'uuna lilka<u>dz</u>ibi samm<u>aa</u>\'uuna liqawmin <u>aa</u>khariina lam ya/tuuka yu<u>h</u>arrifuuna lkalima min ba\'di maw<u>aad</u>i\'ihi yaquuluuna in uutiitum h<u>aadzaa</u> fakhu<u>dz</u>uuhu wa-in lam tu/tawhu fa<u>hts</u>aruu waman yuridill<u>aa</u>hu fitnatahu falan tamlika lahu minall<u>aa</u>hi syay-an ul<u>aa</u>-ikalla<u>dz</u>iina lam yuridill<u>aa</u>hu an yu<u>th</u>ahhira quluubahum lahum fii dduny<u>aa</u> khizyun walahum fii l-<u>aa</u>khirati \'a<u>dzaa</u>bun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (711, '5:42', 5, 42, 'samm<u>aa</u>\'uuna lilka<u>dz</u>ibi akk<u>aa</u>luuna lissu<u>h</u>ti fa-in j<u>aa</u>uuka fa<u>h</u>kum baynahum aw a\'ri<u>dh</u> \'anhum wa-in tu\'ri<u>dh</u> \'anhum falan ya<u>dh</u>urruuka syay-an wa-in <u>h</u>akamta fa<u>h</u>kum baynahum bilqis<u>th</u>i innall<u>aa</u>ha yu<u>h</u>ibbu lmuqsi<u>th</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (712, '5:43', 5, 43, 'wakayfa yu<u>h</u>akkimuunaka wa\'indahumu ttawr<u>aa</u>tu fiih<u>aa</u> <u>h</u>ukmull<u>aa</u>hi tsumma yatawallawna min ba\'di <u>dzaa</u>lika wam<u>aa</u> ul<u>aa</u>-ika bilmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (713, '5:44', 5, 44, 'inn<u>aa</u> anzaln<u>aa</u> ttawr<u>aa</u>ta fiih<u>aa</u> hudan wanuurun ya<u>h</u>kumu bih<u>aa</u> nnabiyyuunalla<u>dz</u>iina aslamuu lilla<u>dz</u>iina h<u>aa</u>duu warrabb<u>aa</u>niyyuuna wal-a<u>h</u>b<u>aa</u>ru bim<u>aa</u> istu<u>h</u>fi<u>zh</u>uu min kit<u>aa</u>bill<u>aa</u>hi wak<u>aa</u>nuu \'alayhi syuhad<u>aa</u>-a fal<u>aa</u> takhsyawuu nn<u>aa</u>sa wakhsyawni wal<u>aa</u> tasytaruu bi-<u>aa</u>y<u>aa</u>tii tsamanan qaliilan waman lam ya<u>h</u>kum bim<u>aa</u> anzalall<u>aa</u>hu faul<u>aa</u>-ika humu lk<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (714, '5:45', 5, 45, 'wakatabn<u>aa</u> \'alayhim fiih<u>aa</u> anna nnafsa binnafsi wal\'ayna bil\'ayni wal-anfa bil-anfi walu<u>dz</u>una bilu<u>dz</u>uni wassinna bissinni waljuruu<u>h</u>a qi<u>sas</u>un faman ta<u>sh</u>addaqa bihi fahuwa kaff<u>aa</u>ratun lahu waman lam ya<u>h</u>kum bim<u>aa</u> anzalall<u>aa</u>hu faul<u>aa</u>-ika humu <u>zhzhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (715, '5:46', 5, 46, 'waqaffayn<u>aa</u> \'al<u>aa</u> <u>aa</u>ts<u>aa</u>rihim bi\'iis<u>aa</u> ibni maryama mu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi mina ttawr<u>aa</u>ti wa<u>aa</u>tayn<u>aa</u>hu l-injiila fiihi hudan wanuurun wamu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi mina ttawr<u>aa</u>ti wahudan wamaw\'i<u>zh</u>atan lilmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (716, '5:47', 5, 47, 'walya<u>h</u>kum ahlu l-injiili bim<u>aa</u> anzalall<u>aa</u>hu fiihi waman lam ya<u>h</u>kum bim<u>aa</u> anzalall<u>aa</u>hu faul<u>aa</u>-ika humu lf<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (717, '5:48', 5, 48, 'wa-anzaln<u>aa</u> ilayka lkit<u>aa</u>ba bil<u>h</u>aqqi mu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi mina lkit<u>aa</u>bi wamuhayminan \'alayhi fa<u>h</u>kum baynahum bim<u>aa</u> anzalall<u>aa</u>hu wal<u>aa</u> tattabi\'ahw<u>aa</u>-ahum \'amm<u>aa</u> j<u>aa</u>-aka mina l<u>h</u>aqqi likullin ja\'aln<u>aa</u> minkum syir\'atan waminh<u>aa</u>jan walaw sy<u>aa</u>-all<u>aa</u>hu laja\'alakum ummatan w<u>aah</u>idatan wal<u>aa</u>kin liyabluwakum fiim<u>aa</u> <u>aa</u>t<u>aa</u>kum fastabiquu lkhayr<u>aa</u>ti il<u>aa</u>ll<u>aa</u>hi marji\'ukum jamii\'an fayunabbi-ukum bim<u>aa</u> kuntum fiihi takhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (718, '5:49', 5, 49, 'wa-ani u<u>h</u>kum baynahum bim<u>aa</u> anzalall<u>aa</u>hu wal<u>aa</u> tattabi\'ahw<u>aa</u>-ahum wa<u>hts</u>arhum an yaftinuuka \'an ba\'<u>dh</u>i m<u>aa</u> anzalall<u>aa</u>hu ilayka fa-in tawallaw fa\'lam annam<u>aa</u> yuriidull<u>aa</u>hu an yu<u>sh</u>iibahum biba\'<u>dh</u>i <u>dz</u>unuubihim wa-inna katsiiran mina nn<u>aa</u>si laf<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (719, '5:50', 5, 50, 'afa<u>h</u>ukma lj<u>aa</u>hiliyyati yabghuuna waman a<u>h</u>sanu minall<u>aa</u>hu <u>h</u>ukman liqawmin yuuqinuun\r\r\n');
INSERT INTO quranLatin VALUES (720, '5:51', 5, 51, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattakhi<u>dz</u>uu lyahuuda wanna<u>shaa</u>r<u>aa</u> awliy<u>aa</u>-a ba\'<u>dh</u>uhum awliy<u>aa</u>u ba\'<u>dh</u>in waman yatawallahum minkum fa-innahu minhum innall<u>aa</u>ha l<u>aa</u> yahdii lqawma <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (721, '5:52', 5, 52, 'fatar<u>aa</u>lla<u>dz</u>iina fii quluubihim mara<u>dh</u>un yus<u>aa</u>ri\'uuna fiihim yaquuluuna nakhsy<u>aa</u> an tu<u>sh</u>iiban<u>aa</u> d<u>aa</u>-iratun fa\'as<u>aa</u>ll<u>aa</u>hu an ya/tiya bilfat<u>h</u>i aw amrin min \'indihi fayu<u>sh</u>bi<u>h</u>uu \'al<u>aa</u> m<u>aa</u> asarruu fii anfusihim n<u>aa</u>dimiin\r\r\n');
INSERT INTO quranLatin VALUES (722, '5:53', 5, 53, 'wayaquululla<u>dz</u>iina <u>aa</u>manuu ah<u>aa</u>ul<u>aa</u>-illa<u>dz</u>iina aqsamuu bil<u>aa</u>hi jahda aym<u>aa</u>nihim innahum lama\'akum <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum fa-a<u>sh</u>ba<u>h</u>uu kh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (723, '5:54', 5, 54, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu man yartadda minkum \'an diinihi fasawfa ya/tiill<u>aa</u>hu biqawmin yu<u>h</u>ibbuhum wayu<u>h</u>ibbuunahu a<u>dz</u>illatin \'al<u>aa</u> lmu/miniina a\'izzatin \'al<u>aa</u> lk<u>aa</u>firiina yuj<u>aa</u>hiduuna fii sabiilill<u>aa</u>hi wal<u>aa</u> yakh<u>aa</u>fuuna lawmata l<u>aa</u>-imin <u>dzaa</u>lika fa<u>dh</u>lull<u>aa</u>hi yu/tiihi man yasy<u>aa</u>u wal<u>aa</u>hu w<u>aa</u>si\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (724, '5:55', 5, 55, 'innam<u>aa</u> waliyyukumull<u>aa</u>hu warasuuluhu walla<u>dz</u>iina <u>aa</u>manuulla<u>dz</u>iina yuqiimuuna <u>shsh</u>al<u>aa</u>ta wayu/tuuna zzak<u>aa</u>ta wahum r<u>aa</u>ki\'uun\r\r\n');
INSERT INTO quranLatin VALUES (725, '5:56', 5, 56, 'waman yatawallall<u>aa</u>ha warasuulahu walla<u>dz</u>iina <u>aa</u>manuu fa-inna <u>h</u>izball<u>aa</u>hi humu lgh<u>aa</u>libuun\r\r\n');
INSERT INTO quranLatin VALUES (726, '5:57', 5, 57, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattakhi<u>dz</u>uulla<u>dz</u>iina ittakha<u>dz</u>uu diinakum huzuwan wala\'iban minalla<u>dz</u>iina uutuu lkit<u>aa</u>ba min qablikum walkuff<u>aa</u>ra awliy<u>aa</u>-a wattaquull<u>aa</u>ha in kuntum mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (727, '5:58', 5, 58, 'wa-i<u>dzaa</u> n<u>aa</u>daytum il<u>aa</u> <u>shsh</u>al<u>aa</u>ti ittakha<u>dz</u>uuh<u>aa</u> huzuwan wala\'iban <u>dzaa</u>lika bi-annahum qawmun l<u>aa</u> ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (728, '5:59', 5, 59, 'qul y<u>aa</u> ahla lkit<u>aa</u>bi hal tanqimuuna minn<u>aa</u> ill<u>aa</u> an <u>aa</u>mann<u>aa</u> bil<u>aa</u>hi wam<u>aa</u> unzila ilayn<u>aa</u> wam<u>aa</u> unzila min qablu wa-anna aktsarakum f<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (729, '5:60', 5, 60, 'qul hal unabbi-ukum bisyarrin min <u>dzaa</u>lika matsuubatan \'indall<u>aa</u>hi man la\'anahull<u>aa</u>hu wagha<u>dh</u>iba \'alayhi waja\'ala minhumu lqiradata walkhan<u>aa</u>ziira wa\'abada <u>ththaa</u>ghuuti ul<u>aa</u>-ika syarrun mak<u>aa</u>nan wa-a<u>dh</u>allu \'an saw<u>aa</u>-i ssabiil\r\r\n');
INSERT INTO quranLatin VALUES (730, '5:61', 5, 61, 'wa-i<u>dzaa</u> j<u>aa</u>uukum q<u>aa</u>luu <u>aa</u>mann<u>aa</u> waqad dakhaluu bilkufri wahum qad kharajuu bihi wal<u>aa</u>hu a\'lamu bim<u>aa</u> k<u>aa</u>nuu yaktumuun\r\r\n');
INSERT INTO quranLatin VALUES (731, '5:62', 5, 62, 'watar<u>aa</u> katsiiran minhum yus<u>aa</u>ri\'uuna fii l-itsmi wal\'udw<u>aa</u>ni wa-aklihimu ssu<u>h</u>ta labi/sa m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (732, '5:63', 5, 63, 'lawl<u>aa</u> yanh<u>aa</u>humu rrabb<u>aa</u>niyyuuna wal-a<u>h</u>b<u>aa</u>ru \'an qawlihimu l-itsma wa-aklihimu ssu<u>h</u>ta labi/sa m<u>aa</u> k<u>aa</u>nuu ya<u>sh</u>na\'uun\r\r\n');
INSERT INTO quranLatin VALUES (733, '5:64', 5, 64, 'waq<u>aa</u>lati lyahuudu yadull<u>aa</u>hi maghluulatun ghullat aydiihim walu\'inuu bim<u>aa</u> q<u>aa</u>luu bal yad<u>aa</u>hu mabsuu<u>th</u>at<u>aa</u>ni yunfiqu kayfa yasy<u>aa</u>u walayaziidanna katsiiran minhum m<u>aa</u> unzila ilayka min rabbika <u>th</u>ughy<u>aa</u>nan wakufran wa-alqayn<u>aa</u> baynahumu l\'ad<u>aa</u>wata walbagh<u>dhaa</u>-a il<u>aa</u> yawmi lqiy<u>aa</u>mati kullam<u>aa</u> awqaduu n<u>aa</u>ran lil<u>h</u>arbi a<u>th</u>fa-ah<u>aa</u>ll<u>aa</u>hu wayas\'awna fii l-ar<u>dh</u>i fas<u>aa</u>dan wal<u>aa</u>hu l<u>aa</u> yu<u>h</u>ibbu lmufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (734, '5:65', 5, 65, 'walaw anna ahla lkit<u>aa</u>bi <u>aa</u>manuu wattaqaw lakaffarn<u>aa</u> \'anhum sayyi-<u>aa</u>tihim wala-adkhaln<u>aa</u>hum jann<u>aa</u>ti nna\'iim\r\r\n');
INSERT INTO quranLatin VALUES (735, '5:66', 5, 66, 'walaw annahum aq<u>aa</u>muu ttawr<u>aa</u>ta wal-injiila wam<u>aa</u> unzila ilayhim min rabbihim la-akaluu min fawqihim wamin ta<u>h</u>ti arjulihim minhum ummatun muqta<u>sh</u>idatun wakatsiirun minhum s<u>aa</u>-a m<u>aa</u> ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (736, '5:67', 5, 67, 'y<u>aa</u> ayyuh<u>aa</u> rrasuulu balligh m<u>aa</u> unzila ilayka min rabbika wa-in lam taf\'al fam<u>aa</u> ballaghta ris<u>aa</u>latahu wal<u>aa</u>hu ya\'<u>sh</u>imuka mina nn<u>aa</u>si innall<u>aa</u>ha l<u>aa</u> yahdii lqawma lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (737, '5:68', 5, 68, 'qul y<u>aa</u> ahla lkit<u>aa</u>bi lastum \'al<u>aa</u> syay-in <u>h</u>att<u>aa</u> tuqiimuu ttawr<u>aa</u>ta wal-injiila wam<u>aa</u> unzila ilaykum min rabbikum walayaziidanna katsiiran minhum m<u>aa</u> unzila ilayka min rabbika <u>th</u>ughy<u>aa</u>nan wakufran fal<u>aa</u> ta/sa \'al<u>aa</u> lqawmi lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (738, '5:69', 5, 69, 'innalla<u>dz</u>iina <u>aa</u>manuu walla<u>dz</u>iina h<u>aa</u>duu wa<u>shshaa</u>bi-uuna wanna<u>shaa</u>r<u>aa</u> man <u>aa</u>mana bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri wa\'amila <u>shaa</u>li<u>h</u>an fal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (739, '5:70', 5, 70, 'laqad akha<u>dz</u>n<u>aa</u> miits<u>aa</u>qa banii isr<u>aa</u>-iila wa-arsaln<u>aa</u> ilayhim rusulan kullam<u>aa</u> j<u>aa</u>-ahum rasuulun bim<u>aa</u> l<u>aa</u> tahw<u>aa</u> anfusuhum fariiqan ka<u>dzdz</u>abuu wafariiqan yaqtuluun\r\r\n');
INSERT INTO quranLatin VALUES (740, '5:71', 5, 71, 'wa<u>h</u>asibuull<u>aa</u> takuuna fitnatun fa\'amuu wa<u>sh</u>ammuu tsumma t<u>aa</u>ball<u>aa</u>hu \'alayhim tsumma \'amuu wa<u>sh</u>ammuu katsiirun minhum wal<u>aa</u>hu ba<u>sh</u>iirun bim<u>aa</u> ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (741, '5:72', 5, 72, 'laqad kafaralla<u>dz</u>iina q<u>aa</u>luu innall<u>aa</u>ha huwa lmasii<u>h</u>u ibnu maryama waq<u>aa</u>la lmasii<u>h</u>u y<u>aa</u> banii isr<u>aa</u>-iila u\'buduull<u>aa</u>ha rabbii warabbakum innahu man yusyrik bil<u>aa</u>hi faqad <u>h</u>arramall<u>aa</u>hu \'alayhi ljannata wama/w<u>aa</u>hu nn<u>aa</u>ru wam<u>aa</u> li<u>zhzhaa</u>limiina min an<u>shaa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (742, '5:73', 5, 73, 'laqad kafaralla<u>dz</u>iina q<u>aa</u>luu innall<u>aa</u>ha ts<u>aa</u>litsu tsal<u>aa</u>tsatin wam<u>aa</u> min il<u>aa</u>hin ill<u>aa</u> il<u>aa</u>hun w<u>aah</u>idun wa-in lam yantahuu \'amm<u>aa</u> yaquuluuna layamassannalla<u>dz</u>iina kafaruu minhum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (743, '5:74', 5, 74, 'afal<u>aa</u> yatuubuuna il<u>aa</u>ll<u>aa</u>hi wayastaghfiruunahu wal<u>aa</u>hu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (744, '5:75', 5, 75, 'm<u>aa</u> lmasii<u>h</u>u ibnu maryama ill<u>aa</u> rasuulun qad khalat min qablihi rrusulu waummuhu <u>sh</u>iddiiqatun k<u>aa</u>n<u>aa</u> ya/kul<u>aa</u>ni <u>ththh</u>a\'<u>aa</u>ma un<u>zh</u>ur kayfa nubayyinu lahumu l-<u>aa</u>y<u>aa</u>ti tsumma un<u>zh</u>ur ann<u>aa</u> yu/fakuun\r\r\n');
INSERT INTO quranLatin VALUES (745, '5:76', 5, 76, 'qul ata\'buduuna min duunill<u>aa</u>hi m<u>aa</u> l<u>aa</u> yamliku lakum <u>dh</u>arran wal<u>aa</u> naf\'an wal<u>aa</u>hu huwa ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (746, '5:77', 5, 77, 'qul y<u>aa</u> ahla lkit<u>aa</u>bi l<u>aa</u> taghluu fii diinikum ghayra l<u>h</u>aqqi wal<u>aa </u>tattabi\'uu ahw<u>aa</u>-a qawmin qad <u>dh</u>alluu min qablu wa-a<u>dh</u>alluu katsiiran wa<u>dh</u>alluu \'an saw<u>aa</u>-i ssabiil\r\r\n');
INSERT INTO quranLatin VALUES (747, '5:78', 5, 78, 'lu\'inalla<u>dz</u>iina kafaruu min banii isr<u>aa</u>-iila \'al<u>aa</u> lis<u>aa</u>ni d<u>aa</u>wuuda wa\'iis<u>aa</u> ibni maryama <u>dzaa</u>lika bim<u>aa</u> \'a<u>sh</u>aw wak<u>aa</u>nuu ya\'taduun\r\r\n');
INSERT INTO quranLatin VALUES (748, '5:79', 5, 79, 'k<u>aa</u>nuu l<u>aa</u> yatan<u>aa</u>hawna \'an munkarin fa\'aluuhu labi/sa m<u>aa</u> k<u>aa</u>nuu yaf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (749, '5:80', 5, 80, 'tar<u>aa</u> katsiiran minhum yatawallawnalla<u>dz</u>iina kafaruu labi/sa m<u>aa </u>qaddamat lahum anfusuhum an sakhi<u>th</u>all<u>aa</u>hu \'alayhim wafii l\'a<u>dzaa</u>bi hum kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (750, '5:81', 5, 81, 'walaw k<u>aa</u>nuu yu/minuuna bil<u>aa</u>hi wannabiyyi wam<u>aa</u> unzila ilayhi m<u>aa</u> ittakha<u>dz</u>uuhum awliy<u>aa</u>-a wal<u>aa</u>kinna katsiiran minhum f<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (751, '5:82', 5, 82, 'latajidanna asyadda nn<u>aa</u>si \'ad<u>aa</u>watan lilla<u>dz</u>iina <u>aa</u>manuu lyahuuda walla<u>dz</u>iina asyrakuu walatajidanna aqrabahum mawaddatan lilla<u>dz</u>iina <u>aa</u>manuulla<u>dz</u>iina q<u>aa</u>luu inn<u>aa</u> na<u>shaa</u>r<u>aa</u> <u>dzaa</u>lika bi-anna minhum qissiisiina waruhb<u>aa</u>nan wa-annahum l<u>aa</u> yastakbiruun\r\r\n');
INSERT INTO quranLatin VALUES (752, '5:83', 5, 83, 'wa-i<u>dzaa</u> sami\'uu m<u>aa</u> unzila il<u>aa</u> rrasuuli tar<u>aa</u> a\'yunahum tafii<u>dh</u>u mina ddam\'i mimm<u>aa</u> \'arafuu mina l<u>h</u>aqqi yaquuluuna rabban<u>aa</u> <u>aa</u>mann<u>aa</u> faktubn<u>aa</u> ma\'a sysy<u>aa</u>hidiin\r\r\n');
INSERT INTO quranLatin VALUES (753, '5:84', 5, 84, 'wam<u>aa</u> lan<u>aa</u> l<u>aa</u> nu/minu bil<u>aa</u>hi wam<u>aa</u> j<u>aa</u>-an<u>aa</u> mina l<u>h</u>aqqi wana<u>th</u>ma\'u an yudkhilan<u>aa</u> rabbun<u>aa</u> ma\'a lqawmi <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (754, '5:85', 5, 85, 'fa-ats<u>aa</u>bahumull<u>aa</u>hu bim<u>aa</u> q<u>aa</u>luu jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wa<u>dzaa</u>lika jaz<u>aa</u>u lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (755, '5:86', 5, 86, 'walla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu lja<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (756, '5:87', 5, 87, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tu<u>h</u>arrimuu <u>th</u>ayyib<u>aa</u>ti m<u>aa</u> a<u>h</u>allall<u>aa</u>hu lakum wal<u>aa</u> ta\'taduu innall<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu lmu\'tadiin\r\r\n');
INSERT INTO quranLatin VALUES (757, '5:88', 5, 88, 'wakuluu mimm<u>aa</u> razaqakumull<u>aa</u>hu <u>h</u>al<u>aa</u>lan <u>th</u>ayyiban wattaquull<u>aa</u>halla<u>dz</u>ii antum bihi mu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (758, '5:89', 5, 89, 'l<u>aa</u> yu-<u>aa</u>khi<u>dz</u>ukumull<u>aa</u>hu billaghwi fii aym<u>aa</u>nikum wal<u>aa</u>kin yu-<u>aa</u>khi<u>dz</u>ukum bim<u>aa</u> \'aqqadtumu l-aym<u>aa</u>na fakaff<u>aa</u>ratuhu i<u>th</u>\'<u>aa</u>mu \'asyarati mas<u>aa</u>kiina min awsa<u>th</u>i m<u>aa</u> tu<u>th</u>\'imuuna ahliikum aw kiswatuhum aw ta<u>h</u>riiru raqabatin faman lam yajid fa<u>sh</u>iy<u>aa</u>mu tsal<u>aa</u>tsati ayy<u>aa</u>min <u>dzaa</u>lika kaff<u>aa</u>ratu aym<u>aa</u>nikum i<u>dzaa</u> <u>h</u>alaftum wa<u>h</u>fa<u>zh</u>uu aym<u>aa</u>nakum ka<u>dzaa</u>lika yubayyinull<u>aa</u>hu lakum <u>aa</u>y<u>aa</u>tihi la\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (759, '5:90', 5, 90, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu innam<u>aa</u> lkhamru walmaysiru wal-an<u>shaa</u>bu wal-azl<u>aa</u>mu rijsun min \'amali sysyay<u>th</u><u>aa</u>ni fajtanibuuhu la\'allakum tufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (760, '5:91', 5, 91, 'innam<u>aa</u> yuriidu sysyay<u>th</u><u>aa</u>nu an yuuqi\'a baynakumu l\'ad<u>aa</u>wata walbagh<u>dhaa</u>-a fii lkhamri walmaysiri waya<u>sh</u>uddakum \'an <u>dz</u>ikrill<u>aa</u>hi wa\'ani <u>shsh</u>al<u>aa</u>ti fahal antum muntahuun\r\r\n');
INSERT INTO quranLatin VALUES (761, '5:92', 5, 92, 'wa-a<u>th</u>ii\'uull<u>aa</u>ha wa-a<u>th</u>ii\'uu rrasuula wa<u>hts</u>aruu fa-in tawallaytum fa\'lamuu annam<u>aa</u> \'al<u>aa</u> rasuulin<u>aa</u> lbal<u>aa</u>ghu lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (762, '5:93', 5, 93, 'laysa \'al<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti jun<u>aah</u>un fiim<u>aa</u> <u>th</u>a\'imuu i<u>dzaa</u> m<u>aa</u> ittaqaw wa<u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti tsumma ittaqaw wa<u>aa</u>manuu tsumma ittaqaw wa-a<u>h</u>sanuu wal<u>aa</u>hu yu<u>h</u>ibbu lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (763, '5:94', 5, 94, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu layabluwannakumull<u>aa</u>hu bisyay-in mina <u>shsh</u>aydi tan<u>aa</u>luhu aydiikum warim<u>aah</u>ukum liya\'lamall<u>aa</u>hu man yakh<u>aa</u>fuhu bilghaybi famani i\'tad<u>aa</u> ba\'da <u>dzaa</u>lika falahu \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (764, '5:95', 5, 95, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> taqtuluu <u>shsh</u>ayda wa-antum <u>h</u>urumun waman qatalahu minkum muta\'ammidan fajaz<u>aa</u>un mitslu m<u>aa</u> qatala mina nna\'ami ya<u>h</u>kumu bihi <u>dz</u>aw<u>aa</u> \'adlin minkum hadyan b<u>aa</u>ligha lka\'bati aw kaff<u>aa</u>ratun <u>th</u>a\'<u>aa</u>mu mas<u>aa</u>kiina aw \'adlu <u>dzaa</u>lika <u>sh</u>iy<u>aa</u>man liya<u>dz</u>uuqa wab<u>aa</u>la amrihi \'af<u>aa</u>ll<u>aa</u>hu \'amm<u>aa</u> salafa waman \'<u>aa</u>da fayantaqimull<u>aa</u>hu minhu wal<u>aa</u>hu \'aziizun <u>dz</u>uu intiq<u>aa</u>m\r\r\n');
INSERT INTO quranLatin VALUES (765, '5:96', 5, 96, 'u<u>h</u>illa lakum <u>sh</u>aydu lba<u>h</u>ri wa<u>th</u>a\'<u>aa</u>muhu mat<u>aa</u>\'an lakum walissayy<u>aa</u>rati wa<u>h</u>urrima \'alaykum <u>sh</u>aydu lbarri m<u>aa</u> dumtum <u>h</u>uruman wattaquull<u>aa</u>halla<u>dz</u>ii ilayhi tu<u>h</u>syaruun\r\r\n');
INSERT INTO quranLatin VALUES (766, '5:97', 5, 97, 'ja\'alall<u>aa</u>hu lka\'bata lbayta l<u>h</u>ar<u>aa</u>ma qiy<u>aa</u>man linn<u>aa</u>si wasysyahra l<u>h</u>ar<u>aa</u>ma walhadya walqal<u>aa</u>-ida <u>dzaa</u>lika lita\'lamuu annall<u>aa</u>ha ya\'lamu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wa-annall<u>aa</u>ha bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (767, '5:98', 5, 98, 'i\'lamuu annall<u>aa</u>ha syadiidu l\'iq<u>aa</u>bi wa-annall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (768, '5:99', 5, 99, 'm<u>aa</u> \'al<u>aa</u> rrasuuli ill<u>aa</u> lbal<u>aa</u>ghu wal<u>aa</u>hu ya\'lamu m<u>aa</u> tubduuna wam<u>aa</u> taktumuun\r\r\n');
INSERT INTO quranLatin VALUES (769, '5:100', 5, 100, 'qul l<u>aa</u> yastawii lkhabiitsu wa<u>ththh</u>ayyibu walaw a\'jabaka katsratu lkhabiitsi fattaquull<u>aa</u>ha y<u>aa</u> ulii l-alb<u>aa</u>bi la\'allakum tufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (770, '5:101', 5, 101, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tas-aluu \'an asyy<u>aa</u>-a in tubda lakum tasu/kum wa-in tas-aluu \'anh<u>aa</u> <u>h</u>iina yunazzalu lqur-<u>aa</u>nu tubda lakum \'af<u>aa</u>ll<u>aa</u>hu \'anh<u>aa</u> wal<u>aa</u>hu ghafuurun <u>h</u>aliim\r\r\n');
INSERT INTO quranLatin VALUES (771, '5:102', 5, 102, 'qad sa-alah<u>aa</u> qawmun min qablikum tsumma a<u>sh</u>ba<u>h</u>uu bih<u>aa</u> k<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (772, '5:103', 5, 103, 'm<u>aa</u> ja\'alall<u>aa</u>hu min ba<u>h</u>iiratin wal<u>aa</u> s<u>aa</u>-ibatin wal<u>aa</u> wa<u>sh</u>iilatin wal<u>aa</u> <u>haa</u>min wal<u>aa</u>kinnalla<u>dz</u>iina kafaruu yaftaruuna \'al<u>aa</u>ll<u>aa</u>hi lka<u>dz</u>iba wa-aktsaruhum l<u>aa</u> ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (773, '5:104', 5, 104, 'wa-i<u>dzaa</u> qiila lahum ta\'<u>aa</u>law il<u>aa</u> m<u>aa</u> anzalall<u>aa</u>hu wa-il<u>aa</u> rrasuuli q<u>aa</u>luu <u>h</u>asbun<u>aa</u> m<u>aa</u> wajadn<u>aa</u> \'alayhi <u>aa</u>b<u>aa</u>-an<u>aa</u> awa law k<u>aa</u>na <u>aa</u>b<u>aa</u>uhum l<u>aa</u> ya\'lamuuna syay-an wal<u>aa</u> yahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (774, '5:105', 5, 105, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu \'alaykum anfusakum l<u>aa</u> ya<u>dh</u>urrukum man <u>dh</u>alla i<u>dz</u>a ihtadaytum il<u>aa</u>ll<u>aa</u>hi marji\'ukum jamii\'an fayunabbi-ukum bim<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (775, '5:106', 5, 106, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu syah<u>aa</u>datu baynikum i<u>dzaa</u> <u>h</u>a<u>dh</u>ara a<u>h</u>adakumu lmawtu <u>h</u>iina lwa<u>sh</u>iyyati itsn<u>aa</u>ni <u>dz</u>aw<u>aa</u> \'adlin minkum aw <u>aa</u>khar<u>aa</u>ni min ghayrikum in antum <u>dh</u>arabtum fii l-ar<u>dh</u>i fa-a<u>shaa</u>batkum mu<u>sh</u>iibatu lmawti ta<u>h</u>bisuunahum<u>aa</u> min ba\'di <u>shsh</u>al<u>aa</u>ti fayuqsim<u>aa</u>ni bil<u>aa</u>hi ini irtabtum l<u>aa</u> nasytarii bihi tsamanan walaw k<u>aa</u>na <u>dzaa</u> qurb<u>aa</u> wal<u>aa</u> naktumu syah<u>aa</u>datall<u>aa</u>hi inn<u>aa</u> i<u>dz</u>an lamina l-<u>aa</u>tsimiin\r\r\n');
INSERT INTO quranLatin VALUES (776, '5:107', 5, 107, 'fa-in \'utsira \'al<u>aa</u> annahum<u>aa</u> ista<u>h</u>aqq<u>aa</u> itsman fa<u>aa</u>khara<u>n</u>i yaquum<u>aa</u>ni maq<u>aa</u>mahum<u>aa</u> minalla<u>dz</u>iina ista<u>h</u>aqqa \'alayhimu l-awlay<u>aa</u>ni fayuqsim<u>aa</u>ni bil<u>aa</u>hi lasyah<u>aa</u>datun<u>aa</u> a<u>h</u>aqqu min syah<u>aa</u>datihim<u>aa</u> wam<u>aa</u> i\'tadayn<u>aa</u> inn<u>aa</u> i<u>dz</u>an lamina <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (777, '5:108', 5, 108, '<u>dzaa</u>lika adn<u>aa</u> an ya/tuu bisysyah<u>aa</u>dati \'al<u>aa</u> wajhih<u>aa</u> aw yakh<u>aa</u>fuu an turadda aym<u>aa</u>nun ba\'da aym<u>aa</u>nihim wattaquull<u>aa</u>ha wasma\'uu wal<u>aa</u>hu l<u>aa</u> yahdii lqawma lf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (778, '5:109', 5, 109, 'yawma yajma\'ull<u>aa</u>hu rrusula fayaquulu m<u>aatsaa</u> ujibtum q<u>aa</u>luu l<u>aa</u> \'ilma lan<u>aa</u> innaka anta \'all<u>aa</u>mu lghuyuub\r\r\n');
INSERT INTO quranLatin VALUES (779, '5:110', 5, 110, 'i<u>dz</u> q<u>aa</u>lall<u>aa</u>hu y<u>aa</u> \'iis<u>aa</u> ibna maryama u<u>dz</u>kur ni\'matii \'alayka wa\'al<u>aa</u> w<u>aa</u>lidatika i<u>dz</u> ayyadtuka biruu<u>h</u>i lqudusi tukallimu nn<u>aa</u>sa fii lmahdi wakahlan wa-i<u>dz</u> \'allamtuka lkit<u>aa</u>ba wal<u>h</u>ikmata wattawr<u>aa</u>ta wal-injiila wa-i<u>dz</u> takhluqu mina <u>ththh</u>iini kahay-ati <u>ththh</u>ayri bi-i<u>dz</u>nii fatanfukhu fiih<u>aa</u> fatakuunu <u>th</u>ayran bi-i<u>dz</u>nii watubri-u l-akmaha wal-abra<u>sh</u>a bi-i<u>dz</u>nii wa-i<u>dz</u> tukhriju lmawt<u>aa</u> bi-i<u>dz</u>nii wa-i<u>dz</u> kafaftu banii isr<u>aa</u>-iila \'anka i<u>dz</u> ji/tahum bilbayyin<u>aa</u>ti faq<u>aa</u>lalla<u>dz</u>iina kafaruu minhum in h<u>aadzaa</u> ill<u>aa</u> si<u>h</u>run mubiin\r\r\n');
INSERT INTO quranLatin VALUES (780, '5:111', 5, 111, 'wa-i<u>dz</u> aw<u>h</u>aytu il<u>aa</u> l<u>h</u>aw<u>aa</u>riyyiina an <u>aa</u>minuu bii wabirasuulii q<u>aa</u>luu <u>aa</u>mann<u>aa</u> wasyhad bi-annan<u>aa</u> muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (781, '5:112', 5, 112, 'i<u>dz</u> q<u>aa</u>la l<u>h</u>aw<u>aa</u>riyyuuna y<u>aa</u> \'iis<u>aa</u> ibna maryama hal yasta<u>th</u>ii\'u rabbuka an yunazzila \'alayn<u>aa</u> m<u>aa</u>-idatan mina ssam<u>aa</u>-i q<u>aa</u>la ittaquull<u>aa</u>ha in kuntum mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (782, '5:113', 5, 113, 'q<u>aa</u>luu nuriidu an na/kula minh<u>aa</u> wata<u>th</u>ma-inna quluubun<u>aa</u> wana\'lama an qad <u>sh</u>adaqtan<u>aa</u> wanakuuna \'alayh<u>aa</u> mina sysy<u>aa</u>hidiin\r\r\n');
INSERT INTO quranLatin VALUES (783, '5:114', 5, 114, 'q<u>aa</u>la \'iis<u>aa</u> ibnu maryamall<u>aa</u>humma rabban<u>aa</u> anzil \'alayn<u>aa</u> m<u>aa</u>-idatan mina ssam<u>aa</u>-i takuunu lan<u>aa</u> \'iidan li-awwalin<u>aa</u> wa<u>aa</u>khirin<u>aa</u> wa<u>aa</u>yatan minka warzuqn<u>aa</u> wa-anta khayru rr<u>aa</u>ziqiin\r\r\n');
INSERT INTO quranLatin VALUES (784, '5:115', 5, 115, 'q<u>aa</u>lall<u>aa</u>hu innii munazziluh<u>aa</u> \'alaykum faman yakfur ba\'du minkum fa-innii u\'a<u>dzdz</u>ibuhu \'a<u>dzaa</u>ban l<u>aa</u> u\'a<u>dzdz</u>ibuhu a<u>h</u>adan mina l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (785, '5:116', 5, 116, 'wa-i<u>dz</u> q<u>aa</u>lall<u>aa</u>hu y<u>aa</u> \'iis<u>aa</u> ibna maryama a-anta qulta linn<u>aa</u>si ittakhi<u>dz</u>uunii waummiya il<u>aa</u>hayni min duunill<u>aa</u>hi q<u>aa</u>la sub<u>haa</u>naka m<u>aa</u> yakuunu lii an aquula m<u>aa</u> laysa lii bi<u>h</u>aqqin in kuntu qultuhu faqad \'alimtahu ta\'lamu m<u>aa</u> fii nafsii wal<u>aa</u> a\'lamu m<u>aa</u> fii nafsika innaka anta \'all<u>aa</u>mu lghuyuub\r\r\n');
INSERT INTO quranLatin VALUES (786, '5:117', 5, 117, 'm<u>aa</u> qultu lahum ill<u>aa</u> m<u>aa</u> amartanii bihi ani u\'buduull<u>aa</u>ha rabbii warabbakum wakuntu \'alayhim syahiidan m<u>aa</u> dumtu fiihim falamm<u>aa</u> tawaffaytanii kunta anta rraqiiba \'alayhim wa-anta \'al<u>aa</u> kulli syay-in syahiid\r\r\n');
INSERT INTO quranLatin VALUES (787, '5:118', 5, 118, 'in tu\'a<u>dzdz</u>ibhum fa-innahum \'ib<u>aa</u>duka wa-in taghfir lahum fa-innaka anta l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (788, '5:119', 5, 119, 'q<u>aa</u>lall<u>aa</u>hu h<u>aadzaa</u> yawmu yanfa\'u <u>shshaa</u>diqiina <u>sh</u>idquhum lahum jann<u>aa</u>tun tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> abadan ra<u>dh</u>iyall<u>aa</u>hu \'anhum wara<u>dh</u>uu \'anhu <u>dzaa</u>lika lfawzu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (789, '5:120', 5, 120, 'lill<u>aa</u>hi mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wam<u>aa</u> fiihinna wahuwa \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (790, '6:1', 6, 1, 'al<u>h</u>amdu lill<u>aa</u>hilla<u>dz</u>ii khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a waja\'ala <u>zhzh</u>ulum<u>aa</u>ti wannuura tsummalla<u>dz</u>iina kafaruu birabbihim ya\'diluun\r\r\n');
INSERT INTO quranLatin VALUES (791, '6:2', 6, 2, 'huwalla<u>dz</u>ii khalaqakum min <u>th</u>iinin tsumma qa<u>daa</u> ajalan wa-ajalun musamman \'indahu tsumma antum tamtaruun\r\r\n');
INSERT INTO quranLatin VALUES (792, '6:3', 6, 3, 'wahuwall<u>aa</u>hu fii ssam<u>aa</u>w<u>aa</u>ti wafii l-ar<u>dh</u>i ya\'lamu sirrakum wajahrakum waya\'lamu m<u>aa</u> taksibuun\r\r\n');
INSERT INTO quranLatin VALUES (793, '6:4', 6, 4, 'wam<u>aa</u> ta/tiihim min <u>aa</u>yatin min <u>aa</u>y<u>aa</u>ti rabbihim ill<u>aa</u> k<u>aa</u>nuu \'anh<u>aa</u> mu\'ri<u>dh</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (794, '6:5', 6, 5, 'faqad ka<u>dzdz</u>abuu bil<u>h</u>aqqi lamm<u>aa</u> j<u>aa</u>-ahum fasawfa ya/tiihim anb<u>aa</u>u m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (795, '6:6', 6, 6, 'alam yaraw kam ahlakn<u>aa</u> min qablihim min qarnin makkann<u>aa</u>hum fii l-ar<u>dh</u>i m<u>aa</u> lam numakkin lakum wa-arsaln<u>aa</u> ssam<u>aa</u>-a \'alayhim midr<u>aa</u>ran waja\'aln<u>aa</u> l-anh<u>aa</u>ra tajrii min ta<u>h</u>tihim fa-ahlakn<u>aa</u>hum bi<u>dz</u>unuubihim wa-ansya/n<u>aa</u> min ba\'dihim qarnan <u>aa</u>khariin\r\r\n');
INSERT INTO quranLatin VALUES (796, '6:7', 6, 7, 'walaw nazzaln<u>aa</u> \'alayka kit<u>aa</u>ban fii qir<u>th</u><u>aa</u>sin falamasuuhu bi-aydiihim laq<u>aa</u>lalla<u>dz</u>iina kafaruu in h<u>aadzaa</u> ill<u>aa</u> si<u>h</u>run mubiin\r\r\n');
INSERT INTO quranLatin VALUES (797, '6:8', 6, 8, 'waq<u>aa</u>luu lawl<u>aa</u> unzila \'alayhi malakun walaw anzaln<u>aa</u> malakan laqu<u>dh</u>iya l-amru tsumma l<u>aa</u> yun<u>zh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (798, '6:9', 6, 9, 'walaw ja\'aln<u>aa</u>hu malakan laja\'aln<u>aa</u>hu rajulan walalabasn<u>aa</u> \'alayhim m<u>aa</u> yalbisuun\r\r\n');
INSERT INTO quranLatin VALUES (799, '6:10', 6, 10, 'walaqadi istuhzi-a birusulin min qablika fa<u>haa</u>qa billa<u>dz</u>iina sakhiruu minhum m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (800, '6:11', 6, 11, 'qul siiruu fii l-ar<u>dh</u>i tsumma un<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu lmuka<u>dzdz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (801, '6:12', 6, 12, 'qul liman m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i qul lill<u>aa</u>hi kataba \'al<u>aa</u> nafsihi rra<u>h</u>mata layajma\'annakum il<u>aa</u> yawmi lqiy<u>aa</u>mati l<u>aa</u> rayba fiihilla<u>dz</u>iina khasiruu anfusahum fahum l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (802, '6:13', 6, 13, 'walahu m<u>aa</u> sakana fiillayli wannah<u>aa</u>ri wahuwa ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (803, '6:14', 6, 14, 'qul aghayrall<u>aa</u>hi attakhi<u>dz</u>u waliyyan f<u>aath</u>iri ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wahuwa yu<u>th</u>\'imu wal<u>aa</u> yu<u>th</u>\'amu qul innii umirtu an akuuna awwala man aslama wal<u>aa</u> takuunanna mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (804, '6:15', 6, 15, 'qul innii akh<u>aa</u>fu in \'a<u>sh</u>aytu rabbii \'a<u>dzaa</u>ba yawmin \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (805, '6:16', 6, 16, 'man yu<u>sh</u>raf \'anhu yawma-i<u>dz</u>in faqad ra<u>h</u>imahu wa<u>dzaa</u>lika lfawzu lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (806, '6:17', 6, 17, 'wa-in yamsaskall<u>aa</u>hu bi<u>dh</u>urrin fal<u>aa</u> k<u>aa</u>syifa lahu ill<u>aa</u> huwa wa-in yamsaska bikhayrin fahuwa \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (807, '6:18', 6, 18, 'wahuwa lq<u>aa</u>hiru fawqa \'ib<u>aa</u>dihi wahuwa l<u>h</u>akiimu lkhabiir\r\r\n');
INSERT INTO quranLatin VALUES (808, '6:19', 6, 19, 'qul ayyu syay-in akbaru syah<u>aa</u>datan qulill<u>aa</u>hu syahiidun baynii wabaynakum wauu<u>h</u>iya ilayya h<u>aadzaa</u> lqur-<u>aa</u>nu li-un<u>dz</u>irakum bihi waman balagha a-innakum latasyhaduuna anna ma\'all<u>aa</u>hi <u>aa</u>lihatan ukhr<u>aa</u> qul l<u>aa</u> asyhadu qul innam<u>aa</u> huwa il<u>aa</u>hun w<u>aah</u>idun wa-innanii barii-un mimm<u>aa</u> tusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (809, '6:20', 6, 20, '<B>al</b>la<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu lkit<u>aa</u>ba ya\'rifuunahu kam<u>aa</u> ya\'rifuuna abn<u>aa</u>-ahumlla<u>dz</u>iina khasiruu anfusahum fahum l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (810, '6:21', 6, 21, 'waman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>iban aw ka<u>dzdz</u>aba bi-<u>aa</u>y<u>aa</u>tihi innahu l<u>aa</u> yufli<u>h</u>u <u>zhzhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (811, '6:22', 6, 22, 'wayawma na<u>h</u>syuruhum jamii\'an tsumma naquulu lilla<u>dz</u>iina asyrakuu ayna syurak<u>aa</u>ukumulla<u>dz</u>iina kuntum taz\'umuun\r\r\n');
INSERT INTO quranLatin VALUES (812, '6:23', 6, 23, 'tsumma lam takun fitnatuhum ill<u>aa</u> an q<u>aa</u>luu wal<u>aa</u>hi rabbin<u>aa</u> m<u>aa</u> kunn<u>aa</u> musyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (813, '6:24', 6, 24, 'un<u>zh</u>ur kayfa ka<u>dz</u>abuu \'al<u>aa</u> anfusihim wa<u>dh</u>alla \'anhum m<u>aa</u> k<u>aa</u>nuu yaftaruun<b>a </b>\r\r\n');
INSERT INTO quranLatin VALUES (814, '6:25', 6, 25, 'waminhum man yastami\'u ilayka waja\'aln<u>aa</u> \'al<u>aa</u> quluubihim akinnatan an yafqahuuhu wafii <u>aatsaa</u>nihim waqran wa-in yaraw kulla <u>aa</u>yatin l<u>aa</u> yu/minuu bih<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>uuka yuj<u>aa</u>diluunaka yaquululla<u>dz</u>iina kafaruu in h<u>aadzaa</u> ill<u>aa</u> as<u>aath</u>iiru l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (815, '6:26', 6, 26, 'wahum yanhawna \'anhu wayan-awna \'anhu wa-in yuhlikuuna ill<u>aa</u> anfusahum wam<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (816, '6:27', 6, 27, 'walaw tar<u>aa</u> i<u>dz</u> wuqifuu \'al<u>aa</u> nn<u>aa</u>ri faq<u>aa</u>luu y<u>aa</u> laytan<u>aa</u> nuraddu wal<u>aa</u> nuka<u>dzdz</u>iba bi-<u>aa</u>y<u>aa</u>ti rabbin<u>aa</u> wanakuuna mina lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (817, '6:28', 6, 28, 'bal bad<u>aa</u> lahum m<u>aa</u> k<u>aa</u>nuu yukhfuuna min qablu walaw rudduu la\'<u>aa</u>duu lim<u>aa</u> nuhuu \'anhu wa-innahum lak<u>aadz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (818, '6:29', 6, 29, 'waq<u>aa</u>luu in hiya ill<u>aa</u> <u>h</u>ay<u>aa</u>tun<u>aa</u> dduny<u>aa</u> wam<u>aa</u> na<u>h</u>nu bimab\'uutsiin\r\r\n');
INSERT INTO quranLatin VALUES (819, '6:30', 6, 30, 'walaw tar<u>aa</u> i<u>dz</u> wuqifuu \'al<u>aa</u> rabbihim q<u>aa</u>la alaysa h<u>aadzaa</u> bil<u>h</u>aqqi q<u>aa</u>luu bal<u>aa</u> warabbin<u>aa</u> q<u>aa</u>la fa<u>dz</u>uuquu al\'a<u>dzaa</u>ba bim<u>aa</u> kuntum takfuruun\r\r\n');
INSERT INTO quranLatin VALUES (820, '6:31', 6, 31, 'qad khasiralla<u>dz</u>iina ka<u>dzdz</u>abuu biliq<u>aa</u>-ill<u>aa</u>hi <u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-at-humu ss<u>aa</u>\'atu baghtatan q<u>aa</u>luu y<u>aa</u> <u>h</u>asratan<u>aa</u> \'al<u>aa</u> m<u>aa</u> farra<u>th</u>n<u>aa</u> fiih<u>aa</u> wahum ya<u>h</u>miluuna awz<u>aa</u>rahum \'al<u>aa</u> <u>zh</u>uhuurihim l<u>aa</u> s<u>aa</u>-a m<u>aa</u> yaziruun\r\r\n');
INSERT INTO quranLatin VALUES (821, '6:32', 6, 32, 'wam<u>aa</u> l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> ill<u>aa</u> la\'ibun walahwun waladd<u>aa</u>ru l-<u>aa</u>khirati khayrun lilla<u>dz</u>iina yattaquuna afal<u>aa</u> ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (822, '6:33', 6, 33, 'qad na\'lamu innahu laya<u>h</u>zunukalla<u>dz</u>ii yaquuluuna fa-innahum l<u>aa</u> yuka<u>dzdz</u>ibuunaka wal<u>aa</u>kinna <u>zhzhaa</u>limiina bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi yaj<u>h</u>aduun\r\r\n');
INSERT INTO quranLatin VALUES (823, '6:34', 6, 34, 'walaqad ku<u>dzdz</u>ibat rusulun min qablika fa<u>sh</u>abaruu \'al<u>aa</u> m<u>aa</u> ku<u>dzdz</u>ibuu wauu<u>dz</u>uu <u>h</u>att<u>aa</u> at<u>aa</u>hum na<u>sh</u>run<u>aa</u> wal<u>aa</u> mubaddila likalim<u>aa</u>till<u>aa</u>hi walaqad j<u>aa</u>-aka min naba-i lmursaliin\r\r\n');
INSERT INTO quranLatin VALUES (824, '6:35', 6, 35, 'wa-in k<u>aa</u>na kabura \'alayka i\'r<u>aad</u>uhum fa-ini ista<u>th</u>a\'ta an tabtaghiya nafaqan fii l-ar<u>dh</u>i aw sullaman fii ssam<u>aa</u>-i fata/tiyahum bi-<u>aa</u>yatin walaw sy<u>aa</u>-all<u>aa</u>hu lajama\'ahum \'al<u>aa</u> lhud<u>aa</u> fal<u>aa</u> takuunanna mina lj<u>aa</u>hiliin\r\r\n');
INSERT INTO quranLatin VALUES (825, '6:36', 6, 36, 'innam<u>aa</u> yastajiibulla<u>dz</u>iina yasma\'uuna walmawt<u>aa</u> yab\'atsuhumull<u>aa</u>hu tsumma ilayhi yurja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (826, '6:37', 6, 37, 'waq<u>aa</u>luu lawl<u>aa</u> nuzzila \'alayhi <u>aa</u>yatun min rabbihi qul innall<u>aa</u>ha q<u>aa</u>dirun \'al<u>aa</u> an yunazzila <u>aa</u>yatan wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (827, '6:38', 6, 38, 'wam<u>aa</u> min d<u>aa</u>bbatin fii l-ar<u>dh</u>i wal<u>aa</u> <u>th</u><u>aa</u>-irin ya<u>th</u>iiru bijan<u>aah</u>ayhi ill<u>aa</u> umamun amts<u>aa</u>lukum m<u>aa</u> farra<u>th</u>n<u>aa</u> fii lkit<u>aa</u>bi min syay-in tsumma il<u>aa</u> rabbihim yu<u>h</u>syaruun\r\r\n');
INSERT INTO quranLatin VALUES (828, '6:39', 6, 39, 'walla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> <u>sh</u>ummun wabukmun fii <u>zhzh</u>ulum<u>aa</u>ti man yasya-ill<u>aa</u>hu yu<u>dh</u>lilhu waman yasya/ yaj\'alhu \'al<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (829, '6:40', 6, 40, 'qul ara-aytakum in at<u>aa</u>kum \'a<u>dzaa</u>bull<u>aa</u>hi aw atatkumu ss<u>aa</u>\'atu aghayrall<u>aa</u>hi tad\'uuna in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (830, '6:41', 6, 41, 'bal iyy<u>aa</u>hu tad\'uuna fayaksyifu m<u>aa</u> tad\'uuna ilayhi in sy<u>aa</u>-a watansawna m<u>aa</u> tusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (831, '6:42', 6, 42, 'walaqad arsaln<u>aa</u> il<u>aa</u> umamin min qablika fa-akha<u>dz</u>n<u>aa</u>hum bilba/s<u>aa</u>-i wa<u>dhdh</u>arr<u>aa</u>-i la\'allahum yata<u>dh</u>arra\'uun\r\r\n');
INSERT INTO quranLatin VALUES (832, '6:43', 6, 43, 'falawl<u>aa</u> i<u>dz</u> j<u>aa</u>-ahum ba/sun<u>aa</u> ta<u>dh</u>arra\'uu wal<u>aa</u>kin qasat quluubuhum wazayyana lahumu sysyay<u>th</u><u>aa</u>nu m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (833, '6:44', 6, 44, 'falamm<u>aa</u> nasuu m<u>aa</u> <u>dz</u>ukkiruu bihi fata<u>h</u>n<u>aa</u> \'alayhim abw<u>aa</u>ba kulli syay-in <u>h</u>att<u>aa</u> i<u>dzaa</u> fari<u>h</u>uu bim<u>aa</u> uutuu akha<u>dz</u>n<u>aa</u>hum baghtatan fa-i<u>dzaa</u> hum mublisuun\r\r\n');
INSERT INTO quranLatin VALUES (834, '6:45', 6, 45, 'faqu<u>th</u>i\'a d<u>aa</u>biru lqawmilla<u>dz</u>iina <u>zh</u>alamuu wal<u>h</u>amdu lill<u>aa</u>hi rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (835, '6:46', 6, 46, 'qul ara-aytum in akha<u>dz</u>all<u>aa</u>hu sam\'akum wa-ab<u>shaa</u>rakum wakhatama \'al<u>aa</u> quluubikum man il<u>aa</u>hun ghayrull<u>aa</u>hi ya/tiikum bihi un<u>zh</u>ur kayfa nu<u>sh</u>arrifu l-<u>aa</u>y<u>aa</u>ti tsumma hum ya<u>sh</u>difuun\r\r\n');
INSERT INTO quranLatin VALUES (836, '6:47', 6, 47, 'qul ara-aytakum in at<u>aa</u>kum \'a<u>dzaa</u>bull<u>aa</u>hi baghtatan aw jahratan hal yuhlaku ill<u>aa</u> lqawmu <u>zhzhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (837, '6:48', 6, 48, 'wam<u>aa</u> nursilu lmursaliina ill<u>aa</u> mubasysyiriina wamun<u>dz</u>iriina faman <u>aa</u>mana wa-a<u>sh</u>la<u>h</u>a fal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (838, '6:49', 6, 49, 'walla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> yamassuhumu l\'a<u>dzaa</u>bu bim<u>aa</u> k<u>aa</u>nuu yafsuquun\r\r\n');
INSERT INTO quranLatin VALUES (839, '6:50', 6, 50, 'qul l<u>aa</u> aquulu lakum \'indii khaz<u>aa</u>-inull<u>aa</u>hi wal<u>aa</u> a\'lamu lghayba wal<u>aa</u> aquulu lakum innii malakun in attabi\'u ill<u>aa</u> m<u>aa</u> yuu<u>haa</u> ilayya qul hal yastawii l-a\'m<u>aa</u> walba<u>sh</u>iiru afal<u>aa</u> tatafakkaruun\r\r\n');
INSERT INTO quranLatin VALUES (840, '6:51', 6, 51, 'wa-an<u>dz</u>ir bihilla<u>dz</u>iina yakh<u>aa</u>fuuna an yu<u>h</u>syaruu il<u>aa</u> rabbihim laysa lahum min duunihi waliyyun wal<u>aa</u> syafii\'un la\'allahum yattaquun\r\r\n');
INSERT INTO quranLatin VALUES (841, '6:52', 6, 52, 'wal<u>aa</u> ta<u>th</u>rudilla<u>dz</u>iina yad\'uuna rabbahum bilghad<u>aa</u>ti wal\'asyiyyi yuriiduuna wajhahu m<u>aa</u> \'alayka min <u>h</u>is<u>aa</u>bihim min syay-in wam<u>aa</u> min <u>h</u>is<u>aa</u>bika \'alayhim min syay-in fata<u>th</u>rudahum fatakuuna mina <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (842, '6:53', 6, 53, 'waka<u>dzaa</u>lika fatann<u>aa</u> ba\'<u>dh</u>ahum biba\'<u>dh</u>in liyaquuluu ah<u>aa</u>ul<u>aa</u>-i mannall<u>aa</u>hu \'alayhim min baynin<u>aa</u> alaysall<u>aa</u>hu bi-a\'lama bisysy<u>aa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (843, '6:54', 6, 54, 'wa-i<u>dzaa</u> j<u>aa</u>-akalla<u>dz</u>iina yu/minuuna bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> faqul sal<u>aa</u>mun \'alaykum kataba rabbukum \'al<u>aa</u> nafsihi rra<u>h</u>mata annahu man \'amila minkum suu-an bijah<u>aa</u>latin tsumma t<u>aa</u>ba min ba\'dihi wa-a<u>sh</u>la<u>h</u>a fa-annahu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (844, '6:55', 6, 55, 'waka<u>dzaa</u>lika nufa<u>shsh</u>ilu l-<u>aa</u>y<u>aa</u>ti walitastabiina sabiilu lmujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (845, '6:56', 6, 56, 'qul innii nuhiitu an a\'budalla<u>dz</u>iina tad\'uuna min duunill<u>aa</u>hi qul l<u>aa</u> attabi\'u ahw<u>aa</u>-akum qad <u>dh</u>alaltu i<u>dz</u>an wam<u>aa</u> an<u>aa</u> mina lmuhtadiin\r\r\n');
INSERT INTO quranLatin VALUES (846, '6:57', 6, 57, 'qul innii \'al<u>aa</u> bayyinatin min rabbii waka<u>dzdz</u>abtum bihi m<u>aa</u> \'indii m<u>aa</u> tasta\'jiluuna bihi ini l<u>h</u>ukmu ill<u>aa</u> lill<u>aa</u>hi yaqu<u>shsh</u>u l<u>h</u>aqqa wahuwa khayru lf<u>aas</u>iliin\r\r\n');
INSERT INTO quranLatin VALUES (847, '6:58', 6, 58, 'qul law anna \'indii m<u>aa</u> tasta\'jiluuna bihi laqu<u>dh</u>iya l-amru baynii wabaynakum wal<u>aa</u>hu a\'lamu bi<u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (848, '6:59', 6, 59, 'wa\'indahu maf<u>aa</u>ti<u>h</u>u lghaybi l<u>aa</u> ya\'lamuh<u>aa</u> ill<u>aa</u> huwa waya\'lamu m<u>aa</u> fii lbarri walba<u>h</u>ri wam<u>aa</u> tasqu<u>th</u>u min waraqatin ill<u>aa</u> ya\'lamuh<u>aa</u> wal<u>aa</u> <u>h</u>abbatin fii <u>zh</u>ulum<u>aa</u>ti l-ar<u>dh</u>i wal<u>aa</u> ra<u>th</u>bin wal<u>aa</u> y<u>aa</u>bisin ill<u>aa</u> fii kit<u>aa</u>bin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (849, '6:60', 6, 60, 'wahuwalla<u>dz</u>ii yatawaff<u>aa</u>kum billayli waya\'lamu m<u>aa</u> jara<u>h</u>tum binnah<u>aa</u>ri tsumma yab\'atsukum fiihi liyuq<u>daa</u> ajalun musamman tsumma ilayhi marji\'ukum tsumma yunabbi-ukum bim<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (850, '6:61', 6, 61, 'wahuwa lq<u>aa</u>hiru fawqa \'ib<u>aa</u>dihi wayursilu \'alaykum <u>h</u>afa<u>zh</u>atan <u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-a a<u>h</u>adakumu lmawtu tawaffat-hu rusulun<u>aa</u> wahum l<u>aa</u> yufarri<u>th</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (851, '6:62', 6, 62, 'tsumma rudduu il<u>aa</u>ll<u>aa</u>hi mawl<u>aa</u>humu l<u>h</u>aqqi l<u>aa</u> lahu l<u>h</u>ukmu wahuwa asra\'u l<u>haa</u>sibiin\r\r\n');
INSERT INTO quranLatin VALUES (852, '6:63', 6, 63, 'qul man yunajjiikum min <u>zh</u>ulum<u>aa</u>ti lbarri walba<u>h</u>ri tad\'uunahu ta<u>dh</u>arru\'an wakhufyatan la-in anj<u>aa</u>n<u>aa</u> min h<u>aadz</u>ihi lanakuunanna mina sysy<u>aa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (853, '6:64', 6, 64, 'qulill<u>aa</u>hu yunajjiikum minh<u>aa</u> wamin kulli karbin tsumma antum tusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (854, '6:65', 6, 65, 'qul huwa lq<u>aa</u>diru \'al<u>aa</u> an yab\'atsa \'alaykum \'a<u>dzaa</u>ban min fawqikum aw min ta<u>h</u>ti arjulikum aw yalbisakum syiya\'an wayu<u>dz</u>iiqa ba\'<u>dh</u>akum ba/sa ba\'<u>dh</u>in un<u>zh</u>ur kayfa nu<u>sh</u>arrifu l-<u>aa</u>y<u>aa</u>ti la\'allahum yafqahuun\r\r\n');
INSERT INTO quranLatin VALUES (855, '6:66', 6, 66, 'waka<u>dzdz</u>aba bihi qawmuka wahuwa l<u>h</u>aqqu qul lastu \'alaykum biwakiil\r\r\n');
INSERT INTO quranLatin VALUES (856, '6:67', 6, 67, 'likulli naba-in mustaqarrun wasawfa ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (857, '6:68', 6, 68, 'wa-i<u>dzaa</u> ra-aytalla<u>dz</u>iina yakhuu<u>dh</u>uuna fii <u>aa</u>y<u>aa</u>tin<u>aa</u> fa-a\'ri<u>dh</u> \'anhum <u>h</u>att<u>aa</u> yakhuu<u>dh</u>uu fii <u>h</u>adiitsin ghayrihi wa-imm<u>aa</u> yunsiyannaka sysyay<u>th</u><u>aa</u>nu fal<u>aa</u> taq\'ud ba\'da <u>dzdz</u>ikr<u>aa</u> ma\'a lqawmi <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (858, '6:69', 6, 69, 'wam<u>aa</u> \'al<u>aa</u>lla<u>dz</u>iina yattaquuna min <u>h</u>is<u>aa</u>bihim min syay-in wal<u>aa</u>kin <u>dz</u>ikr<u>aa</u> la\'allahum yattaquun\r\r\n');
INSERT INTO quranLatin VALUES (859, '6:70', 6, 70, 'wa<u>dz</u>arilla<u>dz</u>iina ittakha<u>dz</u>uu diinahum la\'iban walahwan wagharrat-humu l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> wa<u>dz</u>akkir bihi an tubsala nafsun bim<u>aa</u> kasabat laysa lah<u>aa</u> min duunill<u>aa</u>hi waliyyun wal<u>aa</u> syafii\'un wa-in ta\'dil kulla \'adlin l<u>aa</u> yu/kha<u>dz</u> minh<u>aa</u> ul<u>aa</u>-ikalla<u>dz</u>iina ubsiluu bim<u>aa</u> kasabuu lahum syar<u>aa</u>bun min <u>h</u>amiimin wa\'a<u>dzaa</u>bun liimun bim<u>aa</u> k<u>aa</u>nuu yakfuruun\r\r\n');
INSERT INTO quranLatin VALUES (860, '6:71', 6, 71, 'qul anad\'uu min duunill<u>aa</u>hi m<u>aa</u> l<u>aa</u> yanfa\'un<u>aa</u> wal<u>aa</u> ya<u>dh</u>urrun<u>aa</u> wanuraddu \'al<u>aa</u> a\'q<u>aa</u>bin<u>aa</u> ba\'da i<u>dz</u> had<u>aa</u>n<u>aa</u>ll<u>aa</u>hu kalla<u>dz</u>ii<u> </u>istahwat-hu sysyay<u>aath</u>iinu fii l-ar<u>dh</u>i <u>h</u>ayr<u>aa</u>na lahu a<u>sh</u>-<u>haa</u>bun yad\'uunahu il<u>aa</u> lhud<u>aa</u> i/tin<u>aa</u> qul inna hud<u>aa</u>ll<u>aa</u>hi huwa lhud<u>aa</u> waumirn<u>aa</u> linuslima lirabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (861, '6:72', 6, 72, 'wa-an aqiimuu <u>shsh</u>al<u>aa</u>ta wattaquuhu wahuwalla<u>dz</u>ii ilayhi tu<u>h</u>syaruun\r\r\n');
INSERT INTO quranLatin VALUES (862, '6:73', 6, 73, 'wahuwalla<u>dz</u>ii khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a bil<u>h</u>aqqi wayawma yaquulu kun fayakuunu qawluhu l<u>h</u>aqqu walahu lmulku yawma yunfakhu fii <u>shsh</u>uuri \'<u>aa</u>limu lghaybi wasysyah<u>aa</u>dati wahuwa l<u>h</u>akiimu lkhabiir\r\r\n');
INSERT INTO quranLatin VALUES (863, '6:74', 6, 74, 'wa-i<u>dz</u> q<u>aa</u>la ibr<u>aa</u>hiimu li-abiihi <u>aa</u>zara atattakhi<u>dz</u>u a<u>sh</u>n<u>aa</u>man <u>aa</u>lihatan innii ar<u>aa</u>ka waqawmaka fii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (864, '6:75', 6, 75, 'waka<u>dzaa</u>lika nurii ibr<u>aa</u>hiima malakuuta ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i waliyakuuna mina lmuuqiniin\r\r\n');
INSERT INTO quranLatin VALUES (865, '6:76', 6, 76, 'falamm<u>aa</u> janna \'alayhillaylu ra<u>aa</u> kawkaban q<u>aa</u>la h<u>aadzaa</u> rabbii falamm<u>aa</u> afala q<u>aa</u>la l<u>aa</u> u<u>h</u>ibbu l-<u>aa</u>filiin\r\r\n');
INSERT INTO quranLatin VALUES (866, '6:77', 6, 77, 'falamm<u>aa</u> ra<u>aa</u> lqamara b<u>aa</u>zighan q<u>aa</u>la h<u>aadzaa</u> rabbii falamm<u>aa</u> afala q<u>aa</u>la la-in lam yahdinii rabbii la-akuunanna mina lqawmi <u>dhdhaa</u>lliin\r\r\n');
INSERT INTO quranLatin VALUES (867, '6:78', 6, 78, 'falamm<u>aa</u> ra<u>aa</u> sysyamsa b<u>aa</u>zighatan q<u>aa</u>la h<u>aadzaa</u> rabbii h<u>aadzaa</u> akbaru falamm<u>aa</u> afalat q<u>aa</u>la y<u>aa</u> qawmi innii barii-un mimm<u>aa</u> tusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (868, '6:79', 6, 79, 'innii wajjahtu wajhiya lilla<u>dz</u>ii fa<u>th</u>ara ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a <u>h</u>aniifan wam<u>aa</u> an<u>aa</u> mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (869, '6:80', 6, 80, 'wa<u>haa</u>jjahu qawmuhu q<u>aa</u>la atu<u>haa</u>jjuunnii fiill<u>aa</u>hi waqad had<u>aa</u>ni wal<u>aa</u> akh<u>aa</u>fu m<u>aa</u> tusyrikuuna bihi ill<u>aa</u> an yasy<u>aa</u>-a rabbii syay-an wasi\'a rabbii kulla syay-in \'ilman afal<u>aa</u> tata<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (870, '6:81', 6, 81, 'wakayfa akh<u>aa</u>fu m<u>aa</u> asyraktum wal<u>aa</u> takh<u>aa</u>fuuna annakum asyraktum bil<u>aa</u>hi m<u>aa</u> lam yunazzil bihi \'alaykum sul<u>th</u><u>aa</u>nan fa-ayyu lfariiqayni a<u>h</u>aqqu bil-amni in kuntum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (871, '6:82', 6, 82, '<B>al</b>la<u>dz</u>iina <u>aa</u>manuu walam yalbisuu iim<u>aa</u>nahum bi<u>zh</u>ulmin ul<u>aa</u>-ika lahumu l-amnu wahum muhtaduun\r\r\n');
INSERT INTO quranLatin VALUES (872, '6:83', 6, 83, 'watilka <u>h</u>ujjatun<u>aa</u> <u>aa</u>tayn<u>aa</u>h<u>aa</u> ibr<u>aa</u>hiima \'al<u>aa</u> qawmihi narfa\'u daraj<u>aa</u>tin man nasy<u>aa</u>u inna rabbaka <u>h</u>akiimun \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (873, '6:84', 6, 84, 'wawahabn<u>aa</u> lahu is<u>haa</u>qa waya\'quuba kullan hadayn<u>aa</u> wanuu<u>h</u>an hadayn<u>aa</u> min qablu wamin <u>dz</u>urriyyatihi d<u>aa</u>wuuda wasulaym<u>aa</u>na wa-ayyuuba wayuusufa wamuus<u>aa</u> wah<u>aa</u>ruuna waka<u>dzaa</u>lika najzii lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (874, '6:85', 6, 85, 'wazakariyy<u>aa</u> waya<u>h</u>y<u>aa</u> wa\'iis<u>aa</u> waly<u>aa</u>sa kullun mina <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (875, '6:86', 6, 86, 'wa-ism<u>aa</u>\'iila walyasa\'a wayuunusa waluu<u>th</u>an wakullan fa<u>dhdh</u>aln<u>aa</u> \'al<u>aa</u> l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (876, '6:87', 6, 87, 'wamin <u>aa</u>b<u>aa</u>-ihim wa<u>dz</u>urriyy<u>aa</u>tihim wa-ikhw<u>aa</u>nihim wajtabayn<u>aa</u>hum wahadayn<u>aa</u>hum il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (877, '6:88', 6, 88, '<u>dzaa</u>lika hud<u>aa</u>ll<u>aa</u>hi yahdii bihi man yasy<u>aa</u>u min \'ib<u>aa</u>dihi walaw asyrakuu la<u>h</u>abi<u>th</u>a \'anhum m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (878, '6:89', 6, 89, 'ul<u>aa</u>-ikalla<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu lkit<u>aa</u>ba wal<u>h</u>ukma wannubuwwata fa-in yakfur bih<u>aa</u> h<u>aa</u>ul<u>aa</u>-i faqad wakkaln<u>aa</u> bih<u>aa</u> qawman laysuu bih<u>aa</u> bik<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (879, '6:90', 6, 90, 'ul<u>aa</u>-ikalla<u>dz</u>iina had<u>aa</u>ll<u>aa</u>hu fabihud<u>aa</u>humu iqtadih qul l<u>aa</u> as-alukum \'alayhi ajran in huwa ill<u>aa</u> <u>dz</u>ikr<u>aa</u> lil\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (880, '6:91', 6, 91, 'wam<u>aa</u> qadaruull<u>aa</u>ha <u>h</u>aqqa qadrihi i<u>dz</u> q<u>aa</u>luu m<u>aa</u> anzalall<u>aa</u>hu \'al<u>aa</u> basyarin min syay-in qul man anzala lkit<u>aa</u>balla<u>dz</u>ii j<u>aa</u>-a bihi muus<u>aa</u> nuuran wahudan linn<u>aa</u>si taj\'aluunahu qar<u>aath</u>iisa tubduunah<u>aa</u> watukhfuuna katsiiran wa\'ullimtum m<u>aa</u> lam ta\'lamuu antum wal<u>aa</u> <u>aa</u>b<u>aa</u>ukum qulill<u>aa</u>hu tsumma <u>dz</u>arhum fii khaw<u>dh</u>ihim yal\'abuun\r\r\n');
INSERT INTO quranLatin VALUES (881, '6:92', 6, 92, 'wah<u>aadzaa</u> kit<u>aa</u>bun anzaln<u>aa</u>hu mub<u>aa</u>rakun mu<u>sh</u>addiqulla<u>dz</u>ii bayna yadayhi walitun<u>dz</u>ira umma lqur<u>aa</u> waman <u>h</u>awlah<u>aa</u> walla<u>dz</u>iina yu/minuuna bil-<u>aa</u>khirati yu/minuuna bihi wahum \'al<u>aa</u> <u>sh</u>al<u>aa</u>tihim yu<u>haa</u>fi<u>zh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (882, '6:93', 6, 93, 'waman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>iban aw q<u>aa</u>la uu<u>h</u>iya ilayya walam yuu<u>h</u>a ilayhi syay-un waman q<u>aa</u>la saunzilu mitsla m<u>aa</u> anzalall<u>aa</u>hu walaw tar<u>aa</u> i<u>dz</u>i <u>zhzhaa</u>limuuna fii ghamar<u>aa</u>ti lmawti walmal<u>aa</u>-ikatu b<u>aa</u>si<u>th</u>uu aydiihim akhrijuu anfusakumu lyawma tujzawna \'a<u>dzaa</u>ba lhuuni bim<u>aa</u> kuntum taquuluuna \'al<u>aa</u>ll<u>aa</u>hi ghayra l<u>h</u>aqqi wakuntum \'an <u>aa</u>y<u>aa</u>tihi tastakbiruun\r\r\n');
INSERT INTO quranLatin VALUES (883, '6:94', 6, 94, 'walaqad ji/tumuun<u>aa</u> fur<u>aa</u>d<u>aa</u> kam<u>aa</u> khalaqn<u>aa</u>kum awwala marratin wataraktum m<u>aa</u> khawwaln<u>aa</u>kum war<u>aa</u>-a <u>zh</u>uhuurikum wam<u>aa</u> nar<u>aa</u> ma\'akum syufa\'<u>aa</u>-akumulla<u>dz</u>iina za\'amtum annahum fiikum syurak<u>aa</u>u laqad taqa<u>ththh</u>a\'a baynakum wa<u>dh</u>alla \'ankum m<u>aa</u> kuntum taz\'umuun\r\r\n');
INSERT INTO quranLatin VALUES (884, '6:95', 6, 95, 'innall<u>aa</u>ha f<u>aa</u>liqu l<u>h</u>abbi wannaw<u>aa</u> yukhriju l<u>h</u>ayya mina lmayyiti wamukhriju lmayyiti mina l<u>h</u>ayyi <u>dzaa</u>likumull<u>aa</u>hu fa-ann<u>aa</u> tu/fakuun\r\r\n');
INSERT INTO quranLatin VALUES (885, '6:96', 6, 96, 'f<u>aa</u>liqu l-i<u>sh</u>b<u>aah</u>i waja\'alallayla sakanan wasysyamsa walqamara <u>h</u>usb<u>aa</u>nan <u>dzaa</u>lika taqdiiru l\'aziizi l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (886, '6:97', 6, 97, 'wahuwalla<u>dz</u>ii ja\'ala lakumu nnujuuma litahtaduu bih<u>aa</u> fii <u>zh</u>ulum<u>aa</u>ti lbarri walba<u>h</u>ri qad fa<u>shsh</u>aln<u>aa</u> l-<u>aa</u>y<u>aa</u>ti liqawmin ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (887, '6:98', 6, 98, 'wahuwalla<u>dz</u>ii ansya-akum min nafsin w<u>aah</u>idatin famustaqarrun wamustawda\'un qad fa<u>shsh</u>aln<u>aa</u> l-<u>aa</u>y<u>aa</u>ti liqawmin yafqahuun\r\r\n');
INSERT INTO quranLatin VALUES (888, '6:99', 6, 99, 'wahuwalla<u>dz</u>ii anzala mina ssam<u>aa</u>-i m<u>aa</u>-an fa-akhrajn<u>aa</u> bihi nab<u>aa</u>ta kulli syay-in fa-akhrajn<u>aa</u> minhu kha<u>dh</u>iran nukhriju minhu <u>h</u>abban mutar<u>aa</u>kiban wamina nnakhli min <u>th</u>al\'ih<u>aa</u> qinw<u>aa</u>nun d<u>aa</u>niyatun wajann<u>aa</u>tin min a\'n<u>aa</u>bin wazzaytuuna warrumm<u>aa</u>na musytabihan waghayra mutasy<u>aa</u>bihin un<u>zh</u>uruu il<u>aa</u> tsamarihi i<u>dzaa</u> atsmara wayan\'ihi inna fii <u>dzaa</u>likum la<u>aa</u>y<u>aa</u>tin liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (889, '6:100', 6, 100, 'waja\'aluu lill<u>aa</u>hi syurak<u>aa</u>-a ljinna wakhalaqahum wakharaquu lahu baniina waban<u>aa</u>tin bighayri \'ilmin sub<u>haa</u>nahu wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> ya<u>sh</u>ifuun\r\r\n');
INSERT INTO quranLatin VALUES (890, '6:101', 6, 101, 'badii\'u ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i ann<u>aa</u> yakuunu lahu waladun walam takun lahu <u>shaah</u>ibatun wakhalaqa kulla syay-in wahuwa bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (891, '6:102', 6, 102, '<u>dzaa</u>likumull<u>aa</u>hu rabbukum l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa kh<u>aa</u>liqu kulli syay-in fa\'buduuhu wahuwa \'al<u>aa</u> kulli syay-in wakiil\r\r\n');
INSERT INTO quranLatin VALUES (892, '6:103', 6, 103, 'l<u>aa</u> tudrikuhu l-ab<u>shaa</u>ru wahuwa yudriku l-ab<u>shaa</u>ra wahuwalla<u>th</u>iifu lkhabiir\r\r\n');
INSERT INTO quranLatin VALUES (893, '6:104', 6, 104, 'qad j<u>aa</u>-akum ba<u>shaa</u>-iru min rabbikum faman ab<u>sh</u>ara falinafsihi waman \'amiya fa\'alayh<u>aa</u> wam<u>aa</u> an<u>aa</u> \'alaykum bi<u>h</u>afii<u>zh</u>\r\r\n');
INSERT INTO quranLatin VALUES (894, '6:105', 6, 105, 'waka<u>dzaa</u>lika nu<u>sh</u>arrifu l-<u>aa</u>y<u>aa</u>ti waliyaquuluu darasta walinubayyinahu liqawmin ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (895, '6:106', 6, 106, 'ittabi\'m<u>aa</u> uu<u>h</u>iya ilayka min rabbika l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa wa-a\'ri<u>dh</u> \'ani lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (896, '6:107', 6, 107, 'walaw sy<u>aa</u>-all<u>aa</u>hu m<u>aa</u> asyrakuu wam<u>aa</u> ja\'aln<u>aa</u>ka \'alayhim <u>h</u>afii<u>zh</u>an wam<u>aa</u> anta \'alayhim biwakiil\r\r\n');
INSERT INTO quranLatin VALUES (897, '6:108', 6, 108, 'wal<u>aa</u> tasubbuulla<u>dz</u>iina yad\'uuna min duunill<u>aa</u>hi fayasubbuull<u>aa</u>ha \'adwan bighayri \'ilmin ka<u>dzaa</u>lika zayyann<u>aa</u> likulli ummatin \'amalahum tsumma il<u>aa</u> rabbihim marji\'uhum fayunabbi-uhum bim<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (898, '6:109', 6, 109, 'wa-aqsamuu bil<u>aa</u>hi jahda aym<u>aa</u>nihim la-in j<u>aa</u>-at-hum <u>aa</u>yatun layu/minunna bih<u>aa</u> qul innam<u>aa</u> l-<u>aa</u>y<u>aa</u>tu \'indall<u>aa</u>hi wam<u>aa</u> yusy\'irukum annah<u>aa</u> i<u>dzaa</u> j<u>aa</u>-at l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (899, '6:110', 6, 110, 'wanuqallibu af-idatahum wa-ab<u>shaa</u>rahum kam<u>aa</u> lam yu/minuu bihi awwala marratin wana<u>dz</u>aruhum fii <u>th</u>ughy<u>aa</u>nihim ya\'mahuun\r\r\n');
INSERT INTO quranLatin VALUES (900, '6:111', 6, 111, 'walaw annan<u>aa</u> nazzaln<u>aa</u> ilayhimu lmal<u>aa</u>-ikata wakallamahumu lmawt<u>aa</u> wa<u>h</u>asyarn<u>aa</u> \'alayhim kulla syay-in qubulan m<u>aa</u> k<u>aa</u>nuu liyu/minuu ill<u>aa</u> an yasy<u>aa</u>-all<u>aa</u>hu wal<u>aa</u>kinna aktsarahum yajhaluun\r\r\n');
INSERT INTO quranLatin VALUES (901, '6:112', 6, 112, 'waka<u>dzaa</u>lika ja\'aln<u>aa</u> likulli nabiyyin \'aduwwan syay<u>aath</u>iina l-insi waljinni yuu<u>h</u>ii ba\'<u>dh</u>uhum il<u>aa</u> ba\'<u>dh</u>in zukhrufa lqawli ghuruuran walaw sy<u>aa</u>-a rabbuka m<u>aa</u> fa\'aluuhu fa<u>dz</u>arhum wam<u>aa</u> yaftaruun\r\r\n');
INSERT INTO quranLatin VALUES (902, '6:113', 6, 113, 'walita<u>sh</u>gh<u>aa</u> ilayhi af-idatulla<u>dz</u>iina l<u>aa</u> yu/minuuna bil-<u>aa</u>khirati waliyar<u>dh</u>awhu waliyaqtarifuu m<u>aa</u> hum muqtarifuun\r\r\n');
INSERT INTO quranLatin VALUES (903, '6:114', 6, 114, 'afaghayrall<u>aa</u>hi abtaghii <u>h</u>akaman wahuwalla<u>dz</u>ii anzala ilaykumu lkit<u>aa</u>ba mufa<u>shsh</u>alan walla<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu lkit<u>aa</u>ba ya\'lamuuna annahu munazzalun min rabbika bil<u>h</u>aqqi fal<u>aa</u> takuunanna mina lmumtariin\r\r\n');
INSERT INTO quranLatin VALUES (904, '6:115', 6, 115, 'watammat kalimatu rabbika <u>sh</u>idqan wa\'adlan l<u>aa</u> mubaddila likalim<u>aa</u>tihi wahuwa ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (905, '6:116', 6, 116, 'wa-in tu<u>th</u>i\'aktsara man fii l-ar<u>dh</u>i yu<u>dh</u>illuuka \'an sabiilill<u>aa</u>hi in yattabi\'uuna ill<u>aa</u> <u>zhzh</u>anna wa-in hum ill<u>aa</u> yakhru<u>sh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (906, '6:117', 6, 117, 'inna rabbaka huwa a\'lamu man ya<u>dh</u>illu \'an sabiilihi wahuwa a\'lamu bilmuhtadiin\r\r\n');
INSERT INTO quranLatin VALUES (907, '6:118', 6, 118, 'fakuluu mimm<u>aa</u> <u>dz</u>ukira ismull<u>aa</u>hi \'alayhi in kuntum bi-<u>aa</u>y<u>aa</u>tihi mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (908, '6:119', 6, 119, 'wam<u>aa</u> lakumll<u>aa</u> ta/kuluu mimm<u>aa</u> <u>dz</u>ukira ismull<u>aa</u>hi \'alayhi waqad fa<u>shsh</u>ala lakum m<u>aa</u> <u>h</u>arrama \'alaykum ill<u>aa</u> m<u>aa</u> i<u>dth</u>urirtum ilayhi wa-inna katsiiran layu<u>dh</u>illuuna bi-ahw<u>aa</u>-ihim bighayri \'ilmin inna rabbaka huwa a\'lamu bilmu\'tadiin\r\r\n');
INSERT INTO quranLatin VALUES (909, '6:120', 6, 120, 'wa<u>dz</u>aruu <u>zhaa</u>hira l-itsmi wab<u>aath</u>inahu innalla<u>dz</u>iina yaksibuuna l-itsma sayujzawna bim<u>aa</u> k<u>aa</u>nuu yaqtarifuun\r\r\n');
INSERT INTO quranLatin VALUES (910, '6:121', 6, 121, 'wal<u>aa</u> ta/kuluu mimm<u>aa</u> lam yu<u>dz</u>kari ismull<u>aa</u>hi \'alayhi wa-innahu lafisqun wa-inna sysyay<u>aath</u>iina layuu<u>h</u>uuna il<u>aa</u> awliy<u>aa</u>-ihim liyuj<u>aa</u>diluukum wa-in a<u>th</u>a\'tumuuhum innakum lamusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (911, '6:122', 6, 122, 'awa man k<u>aa</u>na maytan fa-a<u>h</u>yayn<u>aa</u>hu waja\'aln<u>aa</u> lahu nuuran yamsyii bihi fii nn<u>aa</u>si kaman matsaluhu fii <u>zhzh</u>ulum<u>aa</u>ti laysa bikh<u>aa</u>rijin minh<u>aa</u> ka<u>dzaa</u>lika zuyyina lilk<u>aa</u>firiina m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (912, '6:123', 6, 123, 'waka<u>dzaa</u>lika ja\'aln<u>aa</u> fii kulli qaryatin ak<u>aa</u>bira mujrimiih<u>aa</u> liyamkuruu fiih<u>aa</u> wam<u>aa</u> yamkuruuna ill<u>aa</u> bi-anfusihim wam<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (913, '6:124', 6, 124, 'wa-i<u>dzaa</u> j<u>aa</u>-at-hum <u>aa</u>yatun q<u>aa</u>luu lan nu/mina <u>h</u>att<u>aa</u> nu/t<u>aa</u> mitsla m<u>aa</u> uutiya rusulull<u>aa</u>hill<u>aa</u>hu a\'lamu <u>h</u>aytsu yaj\'alu ris<u>aa</u>latahu sayu<u>sh</u>iibulla<u>dz</u>iina ajramuu <u>sh</u>agh<u>aa</u>run \'indall<u>aa</u>hi wa\'a<u>dzaa</u>bun syadiidun bim<u>aa</u> k<u>aa</u>nuu yamkuruun\r\r\n');
INSERT INTO quranLatin VALUES (914, '6:125', 6, 125, 'faman yuridill<u>aa</u>hu an yahdiyahu yasyra<u>h</u> <u>sh</u>adrahu lil-isl<u>aa</u>mi waman yurid an yu<u>dh</u>illahu yaj\'al <u>sh</u>adrahu <u>dh</u>ayyiqan <u>h</u>arajan ka-annam<u>aa</u> ya<u>shsh</u>a\'\'adu fii ssam<u>aa</u>-i ka<u>dzaa</u>lika yaj\'alull<u>aa</u>hu rrijsa \'al<u>aa</u>lla<u>dz</u>iina l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (915, '6:126', 6, 126, 'wah<u>aadzaa</u> <u>sh</u>ir<u>aath</u>u rabbika mustaqiiman qad fa<u>shsh</u>aln<u>aa</u> l-<u>aa</u>y<u>aa</u>ti liqawmin ya<u>dzdz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (916, '6:127', 6, 127, 'lahum d<u>aa</u>ru ssal<u>aa</u>mi \'inda rabbihim wahuwa waliyyuhum bim<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (917, '6:128', 6, 128, 'wayawma ya<u>h</u>syuruhum jamii\'an y<u>aa</u> ma\'syara ljinni qadi istaktsartum mina l-insi waq<u>aa</u>la awliy<u>aa</u>uhum mina l-insi rabban<u>aa</u> istamta\'a ba\'<u>dh</u>un<u>aa</u> biba\'<u>dh</u>in wabalaghn<u>aa</u> ajalan<u>aa</u>lla<u>dz</u>ii ajjalta lan<u>aa</u> q<u>aa</u>la nn<u>aa</u>ru matsw<u>aa</u>kum kh<u>aa</u>lidiina fiih<u>aa</u> ill<u>aa</u> m<u>aa</u> sy<u>aa</u>-all<u>aa</u>hu inna rabbaka <u>h</u>akiimun \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (918, '6:129', 6, 129, 'waka<u>dzaa</u>lika nuwallii ba\'<u>dh</u>a <u>zhzhaa</u>limiina ba\'<u>dh</u>an bim<u>aa</u> k<u>aa</u>nuu yaksibuun\r\r\n');
INSERT INTO quranLatin VALUES (919, '6:130', 6, 130, 'y<u>aa</u> ma\'syara aljinni wal-insi alam ya/tikum rusulun minkum yaqu<u>shsh</u>uuna \'alaykum <u>aa</u>y<u>aa</u>tii wayun<u>dz</u>iruunakum liq<u>aa</u>-a yawmikum h<u>aadzaa</u> q<u>aa</u>luu syahidn<u>aa</u> \'al<u>aa</u> anfusin<u>aa</u> wagharrat-humu al<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> wasyahiduu \'al<u>aa</u> anfusihim annahum k<u>aa</u>nuu k<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (920, '6:131', 6, 131, '<u>dzaa</u>lika an lam yakun rabbuka muhlika lqur<u>aa</u> bi<u>zh</u>ulmin wa-ahluh<u>aa</u> gh<u>aa</u>filuun\r\r\n');
INSERT INTO quranLatin VALUES (921, '6:132', 6, 132, 'walikullin daraj<u>aa</u>tun mimm<u>aa</u> \'amiluu wam<u>aa</u> rabbuka bigh<u>aa</u>filin \'amm<u>aa</u> ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (922, '6:133', 6, 133, 'warabbuka lghaniyyu <u>dz</u>uu rra<u>h</u>mati in yasya/ yu<u>dz</u>hibkum wayastakhlif min ba\'dikum m<u>aa</u> yasy<u>aa</u>u kam<u>aa</u> ansya-akum min <u>dz</u>urriyyati qawmin <u>aa</u>khariin\r\r\n');
INSERT INTO quranLatin VALUES (923, '6:134', 6, 134, 'inna m<u>aa</u> tuu\'aduuna la<u>aa</u>tin wam<u>aa</u> antum bimu\'jiziin\r\r\n');
INSERT INTO quranLatin VALUES (924, '6:135', 6, 135, 'qul y<u>aa</u> qawmi i\'maluu \'al<u>aa</u> mak<u>aa</u>natikum innii \'<u>aa</u>milun fasawfa ta\'lamuuna man takuunu lahu \'<u>aa</u>qibatu dd<u>aa</u>ri innahu l<u>aa</u> yufli<u>h</u>u <u>zhzhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (925, '6:136', 6, 136, 'waja\'aluu lill<u>aa</u>hi mimm<u>aa</u> <u>dz</u>ara-a mina l<u>h</u>artsi wal-an\'<u>aa</u>mi na<u>sh</u>iiban faq<u>aa</u>luu h<u>aadzaa</u> lill<u>aa</u>hi biza\'mihim wah<u>aadzaa</u> lisyurak<u>aa</u>-in<u>aa</u> fam<u>aa</u> k<u>aa</u>na lisyurak<u>aa</u>-ihim fal<u>aa</u> ya<u>sh</u>ilu il<u>aa</u>ll<u>aa</u>hi wam<u>aa</u> k<u>aa</u>na lill<u>aa</u>hi fahuwa ya<u>sh</u>ilu il<u>aa</u> syurak<u>aa</u>-ihim s<u>aa</u>-a m<u>aa</u> ya<u>h</u>kumuun\r\r\n');
INSERT INTO quranLatin VALUES (926, '6:137', 6, 137, 'waka<u>dzaa</u>lika zayyana likatsiirin mina lmusyrikiina qatla awl<u>aa</u>dihim syurak<u>aa</u>uhum liyurduuhum waliyalbisuu \'alayhim diinahum walaw sy<u>aa</u>-all<u>aa</u>hu m<u>aa</u> fa\'aluuhu fa<u>dz</u>arhum wam<u>aa</u> yaftaruun\r\r\n');
INSERT INTO quranLatin VALUES (927, '6:138', 6, 138, 'waq<u>aa</u>luu h<u>aadz</u>ihi an\'<u>aa</u>mun wa<u>h</u>artsun <u>h</u>ijrun l<u>aa</u> ya<u>th</u>\'amuh<u>aa</u> ill<u>aa</u> man nasy<u>aa</u>u biza\'mihim wa-an\'<u>aa</u>mun <u>h</u>urrimat <u>zh</u>uhuuruh<u>aa</u> wa-an\'<u>aa</u>mun l<u>aa</u> ya<u>dz</u>kuruuna ismall<u>aa</u>hi \'alayh<u>aa</u> iftir<u>aa</u>-an \'>\r\n');
INSERT INTO quranLatin VALUES (928, '6:139', 6, 139, 'waq<u>aa</u>luu m<u>aa</u> fii bu<u>th</u>uuni h<u>aadz</u>ihi l-an\'<u>aa</u>mi kh<u>aa</u>li<u>sh</u>atun li<u>dz</u>ukuurin<u>aa</u> wamu<u>h</u>arramun \'al<u>aa</u> azw<u>aa</u>jin<u>aa</u> wa-in yakun maytatan fahum fiihi syurak<u>aa</u>u sayajziihim wa<u>sh</u>fahum innahu <u>h</u>akiimun \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (929, '6:140', 6, 140, 'qad khasiralla<u>dz</u>iina qataluu awl<u>aa</u>dahum safahan bighayri \'ilmin wa<u>h</u>arramuu m<u>aa</u> razaqahumull<u>aa</u>hu iftir<u>aa</u>-an \'al<u>aa</u>ll<u>aa</u>hi qad <u>dh</u>alluu wam<u>aa</u> k<u>aa</u>nuu muhtadiin\r\r\n');
INSERT INTO quranLatin VALUES (930, '6:141', 6, 141, 'wahuwalla<u>dz</u>ii ansya-a jann<u>aa</u>tin ma\'ruusy<u>aa</u>tin waghayra ma\'ruusy<u>aa</u>tin wannakhla wazzar\'a mukhtalifan ukuluhu wazzaytuuna warrumm<u>aa</u>na mutasy<u>aa</u>bihan waghayra mutasy<u>aa</u>bihin kuluu min tsamarihi i<u>dzaa</u> atsmara wa<u>aa</u>tuu <u>h</u>aqqahu yawma <u>h</u>a<u>shaa</u>dihi wal<u>aa</u> tusrifuu innahu l<u>aa</u> yu<u>h</u>ibbu lmusrifiin\r\r\n');
INSERT INTO quranLatin VALUES (931, '6:142', 6, 142, 'wamina l-an\'<u>aa</u>mi <u>h</u>amuulatan wafarsyan kuluu mimm<u>aa</u> razaqakumull<u>aa</u>hu wal<u>aa</u> tattabi\'uu khu<u>th</u>uw<u>aa</u>ti sysyay<u>th</u><u>aa</u>ni innahu lakum \'aduwwun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (932, '6:143', 6, 143, 'tsam<u>aa</u>niyata azw<u>aa</u>jin mina <u>dhdh</u>a/ni itsnayni wamina lma\'zi itsnayni qul <u>aa</u><u>dzdz</u>akarayni <u>h</u>arrama ami luntsayayni amm<u>aa</u> isytamalat \'alayhi ar<u>haa</u>mu luntsayayni nabbi-uunii bi\'ilmin in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (933, '6:144', 6, 144, 'wamina l-ibili itsnayni wamina lbaqari itsnayni qul <u>aa</u><u>dzdz</u>akarayni <u>h</u>arrama ami luntsayayni amm<u>aa</u> isytamalat \'alayhi ar<u>haa</u>mu luntsayayni am kuntum syuhad<u>aa</u>-a i<u>dz</u> wa<u>shshaa</u>kumull<u>aa</u>hu bih<u>aadzaa</u> faman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>iban liyu<u>dh</u>illa nn<u>aa</u>sa bighayri \'ilmin innall<u>aa</u>ha l<u>aa</u> yahdii lqawma <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (934, '6:145', 6, 145, 'qul l<u>aa</u> ajidu fiim<u>aa</u> uu<u>h</u>iya ilayya mu<u>h</u>arraman \'al<u>aa</u> <u>th</u><u>aa</u>\'imin ya<u>th</u>\'amuhu ill<u>aa</u> an yakuuna maytatan aw daman masfuu<u>h</u>an aw la<u>h</u>ma khinziirin fa-innahu rijsun aw fisqan uhilla lighayrill<u>aa</u>hi bihi famani i<u>dth</u>urra ghayra b<u>aa</u>ghin wal<u>aa</u> \'<u>aa</u>din fa-inna rabbaka ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (935, '6:146', 6, 146, 'wa\'al<u>aa</u>lla<u>dz</u>iina h<u>aa</u>duu <u>h</u>arramn<u>aa</u> kulla <u>dz</u>ii <u>zh</u>ufurin wamina lbaqari walghanami <u>h</u>arramn<u>aa</u> \'alayhim syu<u>h</u>uumahum<u>aa</u> ill<u>aa</u> m<u>aa</u> <u>h</u>amalat <u>zh</u>uhuuruhum<u>aa</u> awi l<u>h</u>aw<u>aa</u>y<u>aa</u> aw m<u>aa</u> ikhtala<u>th</u>a bi\'a<u>zh</u>min <u>dzaa</u>lika jazayn<u>aa</u>hum bibaghyihim wa-inn<u>aa</u> la<u>shaa</u>diquun\r\r\n');
INSERT INTO quranLatin VALUES (936, '6:147', 6, 147, 'fa-in ka<u>dzdz</u>abuuka faqul rabbukum <u>dz</u>uu ra<u>h</u>matin w<u>aa</u>si\'atin wal<u>aa</u> yuraddu ba/suhu \'ani lqawmi lmujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (937, '6:148', 6, 148, 'sayaquululla<u>dz</u>iina asyrakuu law sy<u>aa</u>-all<u>aa</u>hu m<u>aa</u> asyrakn<u>aa</u> wal<u>aa</u> <u>aa</u>b<u>aa</u>un<u>aa</u> wal<u>aa</u> <u>h</u>arramn<u>aa</u> min syay-in ka<u>dzaa</u>lika ka<u>dzdz</u>aballa<u>dz</u>iina min qablihim <u>h</u>att<u>aa</u> <u>dzaa</u>quu ba/san<u>aa</u> qul hal \'indakum min \'ilmin fatukhrijuuhu lan<u>aa</u> in tattabi\'uuna ill<u>aa</u> <u>zhzh</u>anna wa-in antum ill<u>aa</u> takhru<u>sh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (938, '6:149', 6, 149, 'qul falill<u>aa</u>hi l<u>h</u>ujjatu lb<u>aa</u>lighatu falaw sy<u>aa</u>-a lahad<u>aa</u>kum ajma\'iin\r\r\n');
INSERT INTO quranLatin VALUES (939, '6:150', 6, 150, 'qul halumma syuhad<u>aa</u>-akumulla<u>dz</u>iina yasyhaduuna annall<u>aa</u>ha <u>h</u>arrama h<u>aadzaa</u> fa-in syahiduu fal<u>aa</u> tasyhad ma\'ahum wal<u>aa</u> tattabi\'ahw<u>aa</u>-alla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> walla<u>dz</u>iina l<u>aa</u> yu/minuuna bil-<u>aa</u>khirati wahum birabbihim ya\'diluun\r\r\n');
INSERT INTO quranLatin VALUES (940, '6:151', 6, 151, 'qul ta\'<u>aa</u>law atlu m<u>aa</u> <u>h</u>arrama rabbukum \'alaykumll<u>aa</u> tusyrikuu bihi syay-an wabilw<u>aa</u>lidayni i<u>h</u>s<u>aa</u>nan wal<u>aa</u> taqtuluu awl<u>aa</u>dakum min iml<u>aa</u>qin na<u>h</u>nu narzuqukum wa-iyy<u>aa</u>hum wal<u>aa</u> taqrabuu lfaw<u>aah</u>isya m<u>aa</u> <u>zh</u>ahara minh<u>aa</u> wam<u>aa</u> ba<u>th</u>ana wal<u>aa</u> taqtuluu nnafsallatii <u>h</u>arramall<u>aa</u>hu ill<u>aa</u> bil<u>h</u>aqqi <u>dzaa</u>likum wa<u>shshaa</u>kum bihi la\'allakum ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (941, '6:152', 6, 152, 'wal<u>aa</u> taqrabuu m<u>aa</u>la lyatiimi ill<u>aa</u> billatii hiya a<u>h</u>sanu <u>h</u>att<u>aa</u> yablugha asyuddahu wa-awfuu lkayla walmiiz<u>aa</u>na bilqis<u>th</u>i l<u>aa</u> nukallifu nafsan ill<u>aa</u> wus\'ah<u>aa</u> wa-i<u>dzaa</u> qultum fa\'diluu walaw k<u>aa</u>na <u>dzaa</u> qurb<u>aa</u> wabi\'ahdill<u>aa</u>hi awfuu <u>dzaa</u>likum wa<u>shshaa</u>kum bihi la\'allakum ta<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (942, '6:153', 6, 153, 'wa-anna h<u>aadzaa</u> <u>sh</u>ir<u>aath</u>ii mustaqiiman fattabi\'uuhu wal<u>aa</u> tattabi\'uu ssubula fatafarraqa bikum \'an sabiilihi <u>dzaa</u>likum wa<u>shshaa</u>kum bihi la\'allakum tattaquun\r\r\n');
INSERT INTO quranLatin VALUES (943, '6:154', 6, 154, 'tsumma <u>aa</u>tayn<u>aa</u> muus<u>aa</u> lkit<u>aa</u>ba tam<u>aa</u>man \'al<u>aa</u>lla<u>dz</u>ii a<u>h</u>sana wataf<u>sh</u>iilan likulli syay-in wahudan wara<u>h</u>matan la\'allahum biliq<u>aa</u>-i rabbihim yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (944, '6:155', 6, 155, 'wah<u>aadzaa</u> kit<u>aa</u>bun anzaln<u>aa</u>hu mub<u>aa</u>rakun fattabi\'uuhu wattaquu la\'allakum tur<u>h</u>amuun\r\r\n');
INSERT INTO quranLatin VALUES (945, '6:156', 6, 156, 'an taquuluu innam<u>aa</u> unzila lkit<u>aa</u>bu \'al<u>aa</u> <u>th</u><u>aa</u>-ifatayni min qablin<u>aa</u> wa-in kunn<u>aa</u> \'an dir<u>aa</u>satihim lagh<u>aa</u>filiin\r\r\n');
INSERT INTO quranLatin VALUES (946, '6:157', 6, 157, 'aw taquuluu law ann<u>aa</u> unzila \'alayn<u>aa</u> lkit<u>aa</u>bu lakunn<u>aa</u> ahd<u>aa</u> minhum faqad j<u>aa</u>-akum bayyinatun min rabbikum wahudan wara<u>h</u>matun faman a<u>zh</u>lamu mimman ka<u>dzdz</u>aba bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi wa<u>sh</u>adafa \'anh<u>aa</u> sanajziilla<u>dz</u>iina ya<u>sh</u>difuuna \'an <u>aa</u>y<u>aa</u>tin<u>aa</u> suu-a l\'a<u>dzaa</u>bi bim<u>aa</u> k<u>aa</u>nuu ya<u>sh</u>difuun\r\r\n');
INSERT INTO quranLatin VALUES (947, '6:158', 6, 158, 'hal yan<u>zh</u>uruuna ill<u>aa</u> an ta/tiyahumu lmal<u>aa</u>-ikatu aw ya/tiya rabbuka aw ya/tiya ba\'<u>dh</u>u <u>aa</u>y<u>aa</u>ti rabbika yawma ya/tii ba\'<u>dh</u>u <u>aa</u>y<u>aa</u>ti rabbika l<u>aa</u> yanfa\'u nafsan iim<u>aa</u>nuh<u>aa</u> lam takun <u>aa</u>manat min qablu aw kasabat fii iim<u>aa</u>nih<u>aa</u> khayran quli inta<u>zh</u>iruu inn<u>aa</u> munta<u>zh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (948, '6:159', 6, 159, 'innalla<u>dz</u>iina farraquu diinahum wak<u>aa</u>nuu syiya\'an lasta minhum fii syay-in innam<u>aa</u> amruhum il<u>aa</u>ll<u>aa</u>hi tsumma yunabbi-uhum bim<u>aa</u> k<u>aa</u>nuu yaf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (949, '6:160', 6, 160, 'man j<u>aa</u>-a bil<u>h</u>asanati falahu \'asyru amts<u>aa</u>lih<u>aa</u> waman j<u>aa</u>-a bissayyi-<u>aa</u>ti fal<u>aa</u> yujz<u>aa</u> ill<u>aa</u> mitslah<u>aa</u> wahum l<u>aa</u> yu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (950, '6:161', 6, 161, 'qul innanii had<u>aa</u>nii rabbii il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiimin diinan qiyaman millata ibr<u>aa</u>hiima <u>h</u>aniifan wam<u>aa</u> k<u>aa</u>na mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (951, '6:162', 6, 162, 'qul inna <u>sh</u>al<u>aa</u>tii wanusukii wama<u>h</u>y<u>aa</u>ya wamam<u>aa</u>tii lill<u>aa</u>hi rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (952, '6:163', 6, 163, 'l<u>aa</u> syariika lahu wabi<u>dzaa</u>lika umirtu wa-an<u>aa</u> awwalu lmuslimiin\r\r\n');
INSERT INTO quranLatin VALUES (953, '6:164', 6, 164, 'qul aghayrall<u>aa</u>hi abghii rabban wahuwa rabbu kulli syay-in wal<u>aa</u> taksibu kullu nafsin ill<u>aa</u> \'alayh<u>aa</u> wal<u>aa</u> taziru w<u>aa</u>ziratun wizra ukhr<u>aa</u> tsumma il<u>aa</u> rabbikum marji\'ukum fayunabbi-ukum bim<u>aa</u> kuntum fiihi takhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (954, '6:165', 6, 165, 'wahuwalla<u>dz</u>ii ja\'alakum khal<u>aa</u>-ifa l-ar<u>dh</u>i warafa\'a ba\'<u>dh</u>akum fawqa ba\'<u>dh</u>in daraj<u>aa</u>tin liyabluwakum fii m<u>aa</u> <u>aa</u>t<u>aa</u>kum inna rabbaka sarii\'u l\'iq<u>aa</u>bi wa-innahu laghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (955, '7:1', 7, 1, 'alif-l<u>aa</u>m-miim-<u>shaa</u>d</u>\r\r\n');
INSERT INTO quranLatin VALUES (956, '7:2', 7, 2, 'kit<u>aa</u>bun unzila ilayka fal<u>aa</u> yakun fii <u>sh</u>adrika <u>h</u>arajun minhu litun<u>dz</u>ira bihi wa<u>dz</u>ikr<u>aa</u> lilmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (957, '7:3', 7, 3, 'ittabi\'uu m<u>aa</u> unzila ilaykum min rabbikum wal<u>aa</u> tattabi\'uu min duunihi awliy<u>aa</u>-a qaliilan m<u>aa</u> ta<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (958, '7:4', 7, 4, 'wakam min qaryatin ahlakn<u>aa</u>h<u>aa</u> faj<u>aa</u>-ah<u>aa</u> ba/sun<u>aa</u> bay<u>aa</u>tan aw hum q<u>aa</u>-iluun\r\r\n');
INSERT INTO quranLatin VALUES (959, '7:5', 7, 5, 'fam<u>aa</u> k<u>aa</u>na da\'w<u>aa</u>hum i<u>dz</u> j<u>aa</u>-ahum ba/sun<u>aa</u> ill<u>aa</u> an q<u>aa</u>luu inn<u>aa</u> kunn<u>aa</u> <u>zhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (960, '7:6', 7, 6, 'falanas-alannalla<u>dz</u>iina ursila ilayhim walanas-alanna lmursaliin\r\r\n');
INSERT INTO quranLatin VALUES (961, '7:7', 7, 7, 'falanaqu<u>shsh</u>anna \'alayhim bi\'ilmin wam<u>aa</u> kunn<u>aa</u> gh<u>aa</u>-ibiin\r\r\n');
INSERT INTO quranLatin VALUES (962, '7:8', 7, 8, 'walwaznu yawma-i<u>dz</u>ini l<u>h</u>aqqu faman tsaqulat maw<u>aa</u>ziinuhu faul<u>aa</u>-ika humu lmufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (963, '7:9', 7, 9, 'waman khaffat maw<u>aa</u>ziinuhu faul<u>aa</u>-ikalla<u>dz</u>iina khasiruu anfusahum bim<u>aa</u> k<u>aa</u>nuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ya<u>zh</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (964, '7:10', 7, 10, 'walaqad makkann<u>aa</u>kum fii l-ar<u>dh</u>i waja\'aln<u>aa</u> lakum fiih<u>aa</u> ma\'<u>aa</u>yisya qaliilan m<u>aa</u> tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (965, '7:11', 7, 11, 'walaqad khalaqn<u>aa</u>kum tsumma <u>sh</u>awwarn<u>aa</u>kum tsumma quln<u>aa</u> lilmal<u>aa</u>-ikati usjuduu li-<u>aa</u>dama fasajaduu ill<u>aa</u> ibliisa lam yakun mina ss<u>aa</u>jidiin\r\r\n');
INSERT INTO quranLatin VALUES (966, '7:12', 7, 12, 'q<u>aa</u>la m<u>aa</u> mana\'akall<u>aa</u> tasjuda i<u>dz</u> amartuka q<u>aa</u>la an<u>aa</u> khayrun minhu khalaqtanii min n<u>aa</u>rin wakhalaqtahu min <u>th</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (967, '7:13', 7, 13, 'q<u>aa</u>la fahbi<u>th</u> minh<u>aa</u> fam<u>aa</u> yakuunu laka an tatakabbara fiih<u>aa</u> fakhruj innaka mina <u>shshaa</u>ghiriin\r\r\n');
INSERT INTO quranLatin VALUES (968, '7:14', 7, 14, 'q<u>aa</u>la an<u>zh</u>irnii il<u>aa</u> yawmi yub\'atsuun\r\r\n');
INSERT INTO quranLatin VALUES (969, '7:15', 7, 15, 'q<u>aa</u>la innaka mina lmun<u>zh</u>ariin\r\r\n');
INSERT INTO quranLatin VALUES (970, '7:16', 7, 16, 'q<u>aa</u>la fabim<u>aa</u> aghwaytanii la-aq\'udanna lahum <u>sh</u>ir<u>aath</u>aka lmustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (971, '7:17', 7, 17, 'tsumma la<u>aa</u>tiyannahum min bayni aydiihim wamin khalfihim wa\'an aym<u>aa</u>nihim wa\'an syam<u>aa</u>-ilihim wal<u>aa</u> tajidu aktsarahum sy<u>aa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (972, '7:18', 7, 18, 'q<u>aa</u>la ukhruj minh<u>aa</u> ma<u>dz</u>uuman mad<u>h</u>uuran laman tabi\'aka minhum la-amla-anna jahannama minkum ajma\'iin\r\r\n');
INSERT INTO quranLatin VALUES (973, '7:19', 7, 19, 'way<u>aa</u> <u>aa</u>damu uskun anta wazawjuka ljannata fakul<u>aa</u> min <u>h</u>aytsu syi/tum<u>aa</u> wal<u>aa</u> taqrab<u>aa</u> h<u>aadz</u>ihi sysyajarata fatakuun<u>aa</u> mina <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (974, '7:20', 7, 20, 'fawaswasa lahum<u>aa</u> sysyay<u>th</u><u>aa</u>nu liyubdiya lahum<u>aa</u> m<u>aa</u> wuuriya \'anhum<u>aa</u> min saw-<u>aa</u>tihim<u>aa</u> waq<u>aa</u>la m<u>aa</u> nah<u>aa</u>kum<u>aa</u> rabbukum<u>aa</u> \'an h<u>aadz</u>ihi sysyajarati ill<u>aa</u> an takuun<u>aa</u> malakayni aw takuun<u>aa</u> mina lkh<u>aa</u>lidiin\r\r\n');
INSERT INTO quranLatin VALUES (975, '7:21', 7, 21, 'waq<u>aa</u>samahum<u>aa</u> innii lakum<u>aa</u> lamina nn<u>aas</u>i<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (976, '7:22', 7, 22, 'fadall<u>aa</u>hum<u>aa</u> bighuruurin falamm<u>aa</u> <u>dzaa</u>q<u>aa</u> sysyajarata badat lahum<u>aa</u> saw-<u>aa</u>tuhum<u>aa</u> wa<u>th</u>afiq<u>aa</u> yakh<u>sh</u>if<u>aa</u>ni \'alayhim<u>aa</u> min waraqi aljannati wan<u>aa</u>d<u>aa</u>hum<u>aa</u> rabbuhum<u>aa</u> alam anhakum<u>aa</u> \'an tilkum<u>aa</u> sysyajarati wa-aqul lakum<u>aa</u> inna sysyay<u>th</u><u>aa</u>na lakum<u>aa</u> \'aduwwun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (977, '7:23', 7, 23, 'q<u>aa</u>l<u>aa</u> rabban<u>aa</u> <u>zh</u>alamn<u>aa</u> anfusan<u>aa</u> wa-in lam taghfir lan<u>aa</u> watar<u>h</u>amn<u>aa </u>lanakuunanna mina lkh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (978, '7:24', 7, 24, 'q<u>aa</u>la ihbi<u>th</u>uu ba\'<u>dh</u>ukum liba\'<u>dh</u>in \'aduwwun walakum fii l-ar<u>dh</u>i mustaqarrun wamat<u>aa</u>\'un il<u>aa</u> <u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (979, '7:25', 7, 25, 'q<u>aa</u>la fiih<u>aa</u> ta<u>h</u>yawna wafiih<u>aa</u> tamuutuuna waminh<u>aa</u> tukhrajuun\r\r\n');
INSERT INTO quranLatin VALUES (980, '7:26', 7, 26, 'y<u>aa</u> banii <u>aa</u>dama qad anzaln<u>aa</u> \'alaykum lib<u>aa</u>san yuw<u>aa</u>rii saw-<u>aa</u>tikum wariisyan walib<u>aa</u>su ttaqw<u>aa</u> <u>dzaa</u>lika khayrun <u>dzaa</u>lika min <u>aa</u>y<u>aa</u>till<u>aa</u>hi la\'allahum ya<u>dzdz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (981, '7:27', 7, 27, 'y<u>aa</u> banii <u>aa</u>dama l<u>aa</u> yaftinannakumu sysyay<u>th</u><u>aa</u>nu kam<u>aa</u> akhraja abawaykum mina ljannati yanzi\'u \'anhum<u>aa</u> lib<u>aa</u>sahum<u>aa</u> liyuriyahum<u>aa</u> saw-<u>aa</u>tihim<u>aa</u> innahu yar<u>aa</u>kum huwa waqabiiluhu min <u>h</u>aytsu l<u>aa</u> tarawnahum inn<u>aa</u> ja\'aln<u>aa</u> sysyay<u>aath</u>iina awliy<u>aa</u>-a lilla<u>dz</u>iina l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (982, '7:28', 7, 28, 'wa-i<u>dzaa</u> fa\'aluu f<u>aah</u>isyatan q<u>aa</u>luu wajadn<u>aa</u> \'alayh<u>aa</u> <u>aa</u>b<u>aa</u>-an<u>aa</u> wal<u>aa</u>hu amaran<u>aa</u> bih<u>aa</u> qul innall<u>aa</u>ha l<u>aa</u> ya/muru bilfa<u>h</u>sy<u>aa</u>-i ataquuluuna \'al<u>aa</u>ll<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (983, '7:29', 7, 29, 'qul amara rabbii bilqis<u>th</u>i wa-aqiimuu wujuuhakum \'inda kulli masjidin wad\'uuhu mukhli<u>sh</u>iina lahu ddiina kam<u>aa</u> bada-akum ta\'uuduun\r\r\n');
INSERT INTO quranLatin VALUES (984, '7:30', 7, 30, 'fariiqan had<u>aa</u> wafariiqan <u>h</u>aqqa \'alayhimu <u>dhdh</u>al<u>aa</u>latu innahumu ittakha<u>dz</u>uu sysyay<u>aath</u>iina awliy<u>aa</u>-a min duunill<u>aa</u>hi waya<u>h</u>sabuuna annahum muhtaduun\r\r\n');
INSERT INTO quranLatin VALUES (985, '7:31', 7, 31, 'y<u>aa</u> banii <u>aa</u>dama khu<u>dz</u>uu ziinatakum \'inda kulli masjidin wakuluu wasyrabuu wal<u>aa</u> tusrifuu innahu l<u>aa</u> yu<u>h</u>ibbu lmusrifiin\r\r\n');
INSERT INTO quranLatin VALUES (986, '7:32', 7, 32, 'qul man <u>h</u>arrama ziinatall<u>aa</u>hillatii akhraja li\'ib<u>aa</u>dihi wa<u>ththh</u>ayyib<u>aa</u>ti mina rrizqi qul hiya lilla<u>dz</u>iina <u>aa</u>manuu fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> kh<u>aa</u>li<u>sh</u>atan yawma lqiy<u>aa</u>mati ka<u>dzaa</u>lika nufa<u>shsh</u>ilu l-<u>aa</u>y<u>aa</u>ti liqawmin ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (987, '7:33', 7, 33, 'qul innam<u>aa</u> <u>h</u>arrama rabbiya lfaw<u>aah</u>isya m<u>aa</u> <u>zh</u>ahara minh<u>aa</u> wam<u>aa</u> ba<u>th</u>ana wal-itsma walbaghya bighayri l<u>h</u>aqqi wa-an tusyrikuu bil<u>aa</u>hi m<u>aa</u> lam yunazzil bihi sul<u>th</u><u>aa</u>nan wa-an taquuluu \'al<u>aa</u>ll<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (988, '7:34', 7, 34, 'walikulli ummatin ajalun fa-i<u>dzaa</u> j<u>aa</u>-a ajaluhum l<u>aa</u> yasta/khiruuna s<u>aa</u>\'atan wal<u>aa</u> yastaqdimuun\r\r\n');
INSERT INTO quranLatin VALUES (989, '7:35', 7, 35, 'y<u>aa</u> banii <u>aa</u>dama imm<u>aa</u> ya/tiyannakum rusulun minkum yaqu<u>shsh</u>uuna \'alaykum <u>aa</u>y<u>aa</u>tii famani ittaq<u>aa</u> wa-a<u>sh</u>la<u>h</u>a fal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (990, '7:36', 7, 36, 'walla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wastakbaruu \'anh<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (991, '7:37', 7, 37, 'faman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>iban aw ka<u>dzdz</u>aba bi-<u>aa</u>y<u>aa</u>tihi ul<u>aa</u>-ika yan<u>aa</u>luhum na<u>sh</u>iibuhum mina lkit<u>aa</u>bi <u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-at-hum rusulun<u>aa</u> yatawaffawnahum q<u>aa</u>luu ayna m<u>aa</u> kuntum tad\'uuna min duunill<u>aa</u>hi q<u>aa</u>luu <u>dh</u>alluu \'ann<u>aa</u> wasyahiduu \'al<u>aa</u> anfusihim annahum k<u>aa</u>nuu k<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (992, '7:38', 7, 38, 'q<u>aa</u>la udkhuluu fii umamin qad khalat min qablikum mina ljinni wal-insi fii nn<u>aa</u>ri kullam<u>aa</u> dakhalat ummatun la\'anat ukhtah<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> idd<u>aa</u>rakuu fiih<u>aa</u> jamii\'an q<u>aa</u>lat ukhr<u>aa</u>hum li-uul<u>aa</u>hum rabban<u>aa</u> h<u>aa</u>ul<u>aa</u>-i a<u>dh</u>alluun<u>aa</u> fa<u>aa</u>tihim \'a<u>dzaa</u>ban <u>dh</u>i\'fan mina nn<u>aa</u>ri q<u>aa</u>la likullin <u>dh</u>i\'fun wal<u>aa</u>kin l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (993, '7:39', 7, 39, 'waq<u>aa</u>lat uul<u>aa</u>hum li-ukhr<u>aa</u>hum fam<u>aa</u> k<u>aa</u>na lakum \'alayn<u>aa</u> min fa<u>dh</u>lin fa<u>dz</u>uuquu l\'a<u>dzaa</u>ba bim<u>aa</u> kuntum taksibuun\r\r\n');
INSERT INTO quranLatin VALUES (994, '7:40', 7, 40, 'innalla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wastakbaruu \'anh<u>aa</u> l<u>aa</u> tufatta<u>h</u>u lahum abw<u>aa</u>bu ssam<u>aa</u>-i wal<u>aa</u> yadkhuluuna ljannata <u>h</u>att<u>aa</u> yalija ljamalu fii sammi lkhiy<u>aath</u>i waka<u>dzaa</u>lika najzii lmujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (995, '7:41', 7, 41, 'lahum min jahannama mih<u>aa</u>dun wamin fawqihim ghaw<u>aa</u>syin waka<u>dzaa</u>lika najzii <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (996, '7:42', 7, 42, 'walla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti l<u>aa</u> nukallifu nafsan ill<u>aa</u> wus\'ah<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu ljannati hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (997, '7:43', 7, 43, 'wanaza\'n<u>aa</u> m<u>aa</u> fii <u>sh</u>uduurihim min ghillin tajrii min ta<u>h</u>tihimu l-anh<u>aa</u>ru waq<u>aa</u>luu l<u>h</u>amdu lill<u>aa</u>hilla<u>dz</u>ii had<u>aa</u>n<u>aa</u> lih<u>aadzaa</u> wam<u>aa</u> kunn<u>aa</u> linahtadiya lawl<u>aa</u> an had<u>aa</u>n<u>aa</u>ll<u>aa</u>hu laqad j<u>aa</u>-at rusulu rabbin<u>aa</u> bil<u>h</u>aqqi wanuuduu an tilkumu ljannatu uuritstumuuh<u>aa</u> bim<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (998, '7:44', 7, 44, 'wan<u>aa</u>d<u>aa</u> a<u>sh</u>-<u>haa</u>bu ljannati a<u>sh</u>-<u>haa</u>ba nn<u>aa</u>ri an qad wajadn<u>aa</u> m<u>aa</u> wa\'adan<u>aa</u> rabbun<u>aa</u> <u>h</u>aqqan fahal wajadtum m<u>aa</u> wa\'ada rabbukum <u>h</u>aqqan q<u>aa</u>luu na\'am fa-a<u>dzdz</u>ana mu-a<u>dzdz</u>inun baynahum an la\'natull<u>aa</u>hi \'al<u>aa</u> <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (999, '7:45', 7, 45, '<B>al</b>la<u>dz</u>iina ya<u>sh</u>udduuna \'an sabiilill<u>aa</u>hi wayabghuunah<u>aa</u> \'iwajan wahum bil-<u>aa</u>khirati k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (1000, '7:46', 7, 46, 'wabaynahum<u>aa</u> <u>h</u>ij<u>aa</u>bun wa\'al<u>aa</u> l-a\'r<u>aa</u>fi rij<u>aa</u>lun ya\'rifuuna kullan bisiim<u>aa</u>hum wan<u>aa</u>daw a<u>sh</u>-<u>haa</u>ba ljannati an sal<u>aa</u>mun \'alaykum lam yadkhuluuh<u>aa</u> wahum ya<u>th</u>ma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1001, '7:47', 7, 47, 'wa-i<u>dzaa</u> <u>sh</u>urifat ab<u>shaa</u>ruhum tilq<u>aa</u>-a a<u>sh</u>-<u>haa</u>bi nn<u>aa</u>ri q<u>aa</u>luu rabban<u>aa</u> l<u>aa</u> taj\'aln<u>aa</u> ma\'a lqawmi <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1002, '7:48', 7, 48, 'wan<u>aa</u>d<u>aa</u> a<u>sh</u>-<u>haa</u>bu l-a\'r<u>aa</u>fi rij<u>aa</u>lan ya\'rifuunahum bisiim<u>aa</u>hum q<u>aa</u>luu m<u>aa</u> aghn<u>aa</u> \'ankum jam\'ukum wam<u>aa</u> kuntum tastakbiruun\r\r\n');
INSERT INTO quranLatin VALUES (1003, '7:49', 7, 49, 'ah<u>aa</u>ul<u>aa</u>-illa<u>dz</u>iina aqsamtum l<u>aa</u> yan<u>aa</u>luhumull<u>aa</u>hu bira<u>h</u>matin udkhuluu ljannata l<u>aa</u> khawfun \'alaykum wal<u>aa</u> antum ta<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (1004, '7:50', 7, 50, 'wan<u>aa</u>d<u>aa</u> a<u>sh</u>-<u>haa</u>bu n<u>aa</u>ri a<u>sh</u>-<u>haa</u>ba ljannati an afii<u>dh</u>uu \'alayn<u>aa</u> mina lm<u>aa</u>-i aw mimm<u>aa</u> razaqakumull<u>aa</u>hu q<u>aa</u>luu innall<u>aa</u>ha <u>h</u>arramahum<u>aa</u> \'al<u>aa</u> lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1005, '7:51', 7, 51, '<B>al</b>la<u>dz</u>iina ittakha<u>dz</u>uu diinahum lahwan wala\'iban wagharrat-humu l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> falyawma nans<u>aa</u>hum kam<u>aa</u> nasuu liq<u>aa</u>-a yawmihim h<u>aadzaa</u> wam<u>aa</u> k<u>aa</u>nuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> yaj<u>h</u>aduun\r\r\n');
INSERT INTO quranLatin VALUES (1006, '7:52', 7, 52, 'walaqad ji/n<u>aa</u>hum bikit<u>aa</u>bin fa<u>shsh</u>aln<u>aa</u>hu \'al<u>aa</u> \'ilmin hudan wara<u>h</u>matan liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1007, '7:53', 7, 53, 'hal yan<u>zh</u>uruuna ill<u>aa</u> ta/wiilahu yawma ya/tii ta/wiiluhu yaquululla<u>dz</u>iina nasuuhu min qablu qad j<u>aa</u>-at rusulu rabbin<u>aa</u> bil<u>h</u>aqqi fahal lan<u>aa</u> min syufa\'<u>aa</u>-a fayasyfa\'uu lan<u>aa</u> aw nuraddu fana\'mala ghayralla<u>dz</u>ii kunn<u>aa</u> na\'malu qad khasiruu anfusahum wa<u>dh</u>alla \'anhum m<u>aa</u> k<u>aa</u>nuu yaftaruun\r\r\n');
INSERT INTO quranLatin VALUES (1008, '7:54', 7, 54, 'inna rabbakumull<u>aa</u>hulla<u>dz</u>ii khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a fii sittati ayy<u>aa</u>min tsumma istaw<u>aa</u> \'al<u>aa</u> l\'arsyi yughsyiillayla nnah<u>aa</u>ra ya<u>th</u>lubuhu <u>h</u>atsiitsan wasysyamsa walqamara wannujuuma musakhkhar<u>aa</u>tin bi-amrihi l<u>aa</u> lahu lkhalqu wal-amru tab<u>aa</u>rakall<u>aa</u>hu rabbu l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (1009, '7:55', 7, 55, 'ud\'uu rabbakum ta<u>dh</u>arru\'an wakhufyatan innahu l<u>aa</u> yu<u>h</u>ibbu lmu\'tadiin\r\r\n');
INSERT INTO quranLatin VALUES (1010, '7:56', 7, 56, 'wal<u>aa</u> tufsiduu fii l-ar<u>dh</u>i ba\'da i<u>sh</u>l<u>aah</u>ih<u>aa</u> wad\'uuhu khawfan wa<u>th</u>ama\'an inna ra<u>h</u>matall<u>aa</u>hi qariibun mina lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (1011, '7:57', 7, 57, 'wahuwalla<u>dz</u>ii yursilu rriy<u>aah</u>a busyran bayna yaday ra<u>h</u>matihi <u>h</u>att<u>aa</u> i<u>dzaa</u> aqallat sa<u>haa</u>ban tsiq<u>aa</u>lan suqn<u>aa</u>hu libaladin mayyitin fa-anzaln<u>aa</u> bihi lm<u>aa</u>-a fa-akhrajn<u>aa</u> bihi min kulli tstsamar<u>aa</u>ti ka<u>dzaa</u>lika nukhriju lmawt<u>aa</u> la\'allakum ta<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1012, '7:58', 7, 58, 'walbaladu <u>ththh</u>ayyibu yakhruju nab<u>aa</u>tuhu bi-i<u>dz</u>ni rabbihi walla<u>dz</u>ii khabutsa l<u>aa</u> yakhruju ill<u>aa</u> nakidan ka<u>dzaa</u>lika nu<u>sh</u>arrifu l-<u>aa</u>y<u>aa</u>ti liqawmin yasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (1013, '7:59', 7, 59, 'laqad arsaln<u>aa</u> nuu<u>h</u>an il<u>aa</u> qawmihi faq<u>aa</u>la y<u>aa</u> qawmi u\'buduull<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu innii akh<u>aa</u>fu \'alaykum \'a<u>dzaa</u>ba yawmin \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1014, '7:60', 7, 60, 'q<u>aa</u>la lmalau min qawmihi inn<u>aa</u> lanar<u>aa</u>ka fii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1015, '7:61', 7, 61, 'q<u>aa</u>la y<u>aa</u> qawmi laysa bii <u>dh</u>al<u>aa</u>latun wal<u>aa</u>kinnii rasuulun min rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (1016, '7:62', 7, 62, 'uballighukum ris<u>aa</u>l<u>aa</u>ti rabbii wa-an<u>sh</u>a<u>h</u>u lakum wa-a\'lamu minall<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1017, '7:63', 7, 63, 'awa \'ajibtum an j<u>aa</u>-akum <u>dz</u>ikrun min rabbikum \'al<u>aa</u> rajulin minkum liyun<u>dz</u>irakum walitattaquu wala\'allakum tur<u>h</u>amuun\r\r\n');
INSERT INTO quranLatin VALUES (1018, '7:64', 7, 64, 'faka<u>dzdz</u>abuuhu fa-anjayn<u>aa</u>hu walla<u>dz</u>iina ma\'ahu fii lfulki wa-aghraqn<u>aa</u>lla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> innahum k<u>aa</u>nuu qawman \'amiin\r\r\n');
INSERT INTO quranLatin VALUES (1019, '7:65', 7, 65, 'wa-il<u>aa</u> \'<u>aa</u>din akh<u>aa</u>hum huudan q<u>aa</u>la y<u>aa</u> qawmi u\'buduull<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu afal<u>aa</u> tattaquun\r\r\n');
INSERT INTO quranLatin VALUES (1020, '7:66', 7, 66, 'q<u>aa</u>la lmalaulla<u>dz</u>iina kafaruu min qawmihi inn<u>aa</u> lanar<u>aa</u>ka fii saf<u>aa</u>hatin wa-inn<u>aa</u> lana<u>zh</u>unnuka mina lk<u>aadz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (1021, '7:67', 7, 67, 'q<u>aa</u>la y<u>aa</u> qawmi laysa bii saf<u>aa</u>hatun wal<u>aa</u>kinnii rasuulun min rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (1022, '7:68', 7, 68, 'uballighukum ris<u>aa</u>l<u>aa</u>ti rabbii wa-an<u>aa</u> lakum n<u>aas</u>i<u>h</u>un amiin\r\r\n');
INSERT INTO quranLatin VALUES (1023, '7:69', 7, 69, 'awa \'ajibtum an j<u>aa</u>-akum <u>dz</u>ikrun min rabbikum \'al<u>aa</u> rajulin minkum liyun<u>dz</u>irakum wa<u>dz</u>kuruu i<u>dz</u> ja\'alakum khulaf<u>aa</u>-a min ba\'di qawmi nuu<u>h</u>in waz<u>aa</u>dakum fii lkhalqi bas<u>th</u>atan fa<u>dz</u>kuruu <u>aa</u>l<u>aa</u>-all<u>aa</u>hi la\'allakum tufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1024, '7:70', 7, 70, 'q<u>aa</u>luu aji/tan<u>aa</u> lina\'budall<u>aa</u>ha wa<u>h</u>dahu wana<u>dz</u>ara m<u>aa</u> k<u>aa</u>na ya\'budu <u>aa</u>b<u>aa</u>un<u>aa</u> fa/tin<u>aa</u> bim<u>aa</u> ta\'idun<u>aa</u> in kunta mina <u>shshaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (1025, '7:71', 7, 71, 'q<u>aa</u>la qad waqa\'a \'alaykum min rabbikum rijsun wagha<u>dh</u>abun atuj<u>aa</u>diluunanii fii asm<u>aa</u>-in sammaytumuuh<u>aa</u> antum wa<u>aa</u>b<u>aa</u>ukum m<u>aa</u> nazzalall<u>aa</u>hu bih<u>aa</u> min sul<u>th</u><u>aa</u>nin fanta<u>zh</u>iruu innii ma\'akum mina lmunta<u>zh</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (1026, '7:72', 7, 72, 'fa-anjayn<u>aa</u>hu walla<u>dz</u>iina ma\'ahu bira<u>h</u>matin minn<u>aa</u> waqa<u>th</u>a\'n<u>aa</u> d<u>aa</u>biralla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wam<u>aa</u> k<u>aa</u>nuu mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1027, '7:73', 7, 73, 'wa-il<u>aa</u> tsamuuda akh<u>aa</u>hum <u>shaa</u>li<u>h</u>an q<u>aa</u>la y<u>aa</u> qawmi u\'buduull<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu qad j<u>aa</u>-atkum bayyinatun min rabbikum h<u>aadz</u>ihi n<u>aa</u>qatull<u>aa</u>hi lakum <u>aa</u>yatan fa<u>dz</u>aruuh<u>aa</u> ta/kul fii ar<u>dh</u>ill<u>aa</u>hi wal<u>aa</u> tamassuuh<u>aa</u> bisuu-in faya/khu<u>dz</u>akum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (1028, '7:74', 7, 74, 'wa<u>dz</u>kuruu i<u>dz</u> ja\'alakum khulaf<u>aa</u>-a min ba\'di \'<u>aa</u>din wabawwa-akum fii l-ar<u>dh</u>i tattakhi<u>dz</u>uuna min suhuulih<u>aa</u> qu<u>sh</u>uuran watan<u>h</u>ituuna ljib<u>aa</u>la buyuutan fa<u>dz</u>kuruu <u>aa</u>l<u>aa</u>-all<u>aa</u>hi wal<u>aa</u> ta\'tsaw fii l-ar<u>dh</u>i mufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (1029, '7:75', 7, 75, 'q<u>aa</u>la lmalaulla<u>dz</u>iina istakbaruu min qawmihi lilla<u>dz</u>iina istu<u>dh</u>\'ifuu liman <u>aa</u>mana minhum ata\'lamuuna anna <u>shaa</u>li<u>h</u>an mursalun min rabbihi q<u>aa</u>luu inn<u>aa</u> bim<u>aa</u> ursila bihi mu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1030, '7:76', 7, 76, 'q<u>aa</u>lalla<u>dz</u>iina istakbaruu inn<u>aa</u> billa<u>dz</u>ii <u>aa</u>mantum bihi k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (1031, '7:77', 7, 77, 'fa\'aqaruu nn<u>aa</u>qata wa\'ataw \'an amri rabbihim waq<u>aa</u>luu y<u>aa</u> <u>shaa</u>li<u>h</u>u i/tin<u>aa</u> bim<u>aa</u> ta\'idun<u>aa</u> in kunta mina lmursaliin\r\r\n');
INSERT INTO quranLatin VALUES (1032, '7:78', 7, 78, 'fa-akha<u>dz</u>at-humu rrajfatu fa-a<u>sh</u>ba<u>h</u>uu fii d<u>aa</u>rihim j<u>aa</u>tsimiin\r\r\n');
INSERT INTO quranLatin VALUES (1033, '7:79', 7, 79, 'fatawall<u>aa</u> \'anhum waq<u>aa</u>la y<u>aa</u> qawmi laqad ablaghtukum ris<u>aa</u>lata rabbii wana<u>sh</u>a<u>h</u>tu lakum wal<u>aa</u>kin l<u>aa</u> tu<u>h</u>ibbuuna nn<u>aas</u>i<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1034, '7:80', 7, 80, 'waluu<u>th</u>an i<u>dz</u> q<u>aa</u>la liqawmihi ata/tuuna lf<u>aah</u>isyata m<u>aa</u> sabaqakum bih<u>aa</u> min a<u>h</u>adin mina l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (1035, '7:81', 7, 81, 'innakum lata/tuuna rrij<u>aa</u>la syahwatan min duuni nnis<u>aa</u>-i bal antum qawmun musrifuun\r\r\n');
INSERT INTO quranLatin VALUES (1036, '7:82', 7, 82, 'wam<u>aa</u> k<u>aa</u>na jaw<u>aa</u>ba qawmihi ill<u>aa</u> an q<u>aa</u>luu akhrijuuhum min qaryatikum innahum un<u>aa</u>sun yata<u>th</u>ahharuun\r\r\n');
INSERT INTO quranLatin VALUES (1037, '7:83', 7, 83, 'fa-anjayn<u>aa</u>hu wa-ahlahu ill<u>aa</u> imra-atahu k<u>aa</u>nat mina lgh<u>aa</u>biriin\r\r\n');
INSERT INTO quranLatin VALUES (1038, '7:84', 7, 84, 'wa-am<u>th</u>arn<u>aa</u> \'alayhim ma<u>th</u>aran fan<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu lmujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (1039, '7:85', 7, 85, 'wa-il<u>aa</u> madyana akh<u>aa</u>hum syu\'ayban q<u>aa</u>la y<u>aa</u> qawmi u\'buduull<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu qad j<u>aa</u>-atkum bayyinatun min rabbikum fa-awfuu lkayla walmiiz<u>aa</u>na wal<u>aa</u> tabkhasuu nn<u>aa</u>sa asyy<u>aa</u>-ahum wal<u>aa</u> tufsiduu fii l-ar<u>dh</u>i ba\'da i<u>sh</u>l<u>aah</u>ih<u>aa</u> <u>dzaa</u>likum khayrun lakum in kuntum mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1040, '7:86', 7, 86, 'wal<u>aa</u> taq\'uduu bikulli <u>sh</u>ir<u>aath</u>in tuu\'iduuna wata<u>sh</u>udduuna \'an sabiilill<u>aa</u>hi man <u>aa</u>mana bihi watabghuunah<u>aa</u> \'iwajan wa<u>dz</u>kuruu i<u>dz</u> kuntum qaliilan fakatstsarakum wan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu lmufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (1041, '7:87', 7, 87, 'wa-in k<u>aa</u>na <u>th</u><u>aa</u>-ifatun minkum <u>aa</u>manuu billa<u>dz</u>ii ursiltu bihi wa<u>th</u><u>aa</u>-ifatun lam yu/minuu fa<u>sh</u>biruu <u>h</u>att<u>aa</u> ya<u>h</u>kumall<u>aa</u>hu baynan<u>aa</u> wahuwa khayru l<u>haa</u>kimiin\r\r\n');
INSERT INTO quranLatin VALUES (1042, '7:88', 7, 88, 'q<u>aa</u>la lmalaulla<u>dz</u>iina istakbaruu min qawmihi lanukhrijannaka y<u>aa</u> syu\'aybu walla<u>dz</u>iina <u>aa</u>manuu ma\'aka min qaryatin<u>aa</u> aw lata\'uudunna fii millatin<u>aa</u> q<u>aa</u>la awa law kunn<u>aa</u> k<u>aa</u>rihiin\r\r\n');
INSERT INTO quranLatin VALUES (1043, '7:89', 7, 89, 'qadi iftarayn<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>iban in \'udn<u>aa</u> fii millatikum ba\'da i<u>dz</u> najj<u>aa</u>n<u>aa</u>ll<u>aa</u>hu minh<u>aa</u> wam<u>aa</u> yakuunu lan<u>aa</u> an na\'uuda fiih<u>aa</u> ill<u>aa</u> an yasy<u>aa</u>-all<u>aa</u>hu rabbun<u>aa</u> wasi\'a rabbun<u>aa</u> kulla syay-in \'ilman \'al<u>aa</u>ll<u>aa</u>hi tawakkaln<u>aa</u> rabban<u>aa</u> ifta<u>h</u> baynan<u>aa</u> wabayna qawmin<u>aa</u> bil<u>h</u>aqqi wa-anta khayru lf<u>aa</u>ti<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1044, '7:90', 7, 90, 'waq<u>aa</u>la lmalaulla<u>dz</u>iina kafaruu min qawmihi la-ini ittaba\'tum syu\'ayban innakum i<u>dz</u>an lakh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (1045, '7:91', 7, 91, 'fa-akha<u>dz</u>at-humu rrajfatu fa-a<u>sh</u>ba<u>h</u>uu fii d<u>aa</u>rihim j<u>aa</u>tsimiin\r\r\n');
INSERT INTO quranLatin VALUES (1046, '7:92', 7, 92, '<B>al</b>la<u>dz</u>iina ka<u>dzdz</u>abuu syu\'ayban ka-an lam yaghnaw fiih<u>aa</u>lla<u>dz</u>iina ka<u>dzdz</u>abuu syu\'ayban k<u>aa</u>nuu humu lkh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (1047, '7:93', 7, 93, 'fatawall<u>aa</u> \'anhum waq<u>aa</u>la y<u>aa</u> qawmi laqad ablaghtukum ris<u>aa</u>l<u>aa</u>ti rabbii wana<u>sh</u>a<u>h</u>tu lakum fakayfa <u>aa</u>s<u>aa</u> \'al<u>aa</u> qawmin k<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1048, '7:94', 7, 94, 'wam<u>aa</u> arsaln<u>aa</u> fii qaryatin min nabiyyin ill<u>aa</u> akha<u>dz</u>n<u>aa</u> ahlah<u>aa</u> bilba/s<u>aa</u>-i wa<u>dhdh</u>arr<u>aa</u>-i la\'allahum ya<u>dhdh</u>arra\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1049, '7:95', 7, 95, 'tsumma baddaln<u>aa</u> mak<u>aa</u>na ssayyi-ati l<u>h</u>asanata <u>h</u>att<u>aa</u> \'afaw waq<u>aa</u>luu qad massa <u>aa</u>b<u>aa</u>-an<u>aa</u> <u>dhdh</u>arr<u>aa</u>u wassarr<u>aa</u>u fa-akha<u>dz</u>n<u>aa</u>hum baghtatan wahum l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (1050, '7:96', 7, 96, 'walaw anna ahla lqur<u>aa</u> <u>aa</u>manuu wattaqaw lafata<u>h</u>n<u>aa</u> \'alayhim barak<u>aa</u>tin mina ssam<u>aa</u>-i wal-ar<u>dh</u>i wal<u>aa</u>kin ka<u>dzdz</u>abuu fa-akha<u>dz</u>n<u>aa</u>hum bim<u>aa</u> k<u>aa</u>nuu yaksibuun\r\r\n');
INSERT INTO quranLatin VALUES (1051, '7:97', 7, 97, 'afa-amina ahlu lqur<u>aa</u> an ya/tiyahum ba/sun<u>aa</u> bay<u>aa</u>tan wahum n<u>aa</u>-imuun\r\r\n');
INSERT INTO quranLatin VALUES (1052, '7:98', 7, 98, 'awa amina ahlu lqur<u>aa</u> an ya/tiyahum ba/sun<u>aa</u> <u>dh</u>u<u>h</u>an wahum yal\'abuun\r\r\n');
INSERT INTO quranLatin VALUES (1053, '7:99', 7, 99, 'afa-aminuu makrall<u>aa</u>hi fal<u>aa</u> ya/manu makrall<u>aa</u>hi ill<u>aa</u> lqawmu lkh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (1054, '7:100', 7, 100, 'awa lam yahdi lilla<u>dz</u>iina yaritsuuna l-ar<u>dh</u>a min ba\'di ahlih<u>aa</u> an law nasy<u>aa</u>u a<u>sh</u>abn<u>aa</u>hum bi<u>dz</u>unuubihim wana<u>th</u>ba\'u \'al<u>aa</u> quluubihim fahum l<u>aa</u> yasma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1055, '7:101', 7, 101, 'tilka lqur<u>aa</u> naqu<u>shsh</u>u \'alayka min anb<u>aa</u>-ih<u>aa</u> walaqad j<u>aa</u>-at-hum rusuluhum bilbayyin<u>aa</u>ti fam<u>aa</u> k<u>aa</u>nuu liyu/minuu bim<u>aa</u> ka<u>dzdz</u>abuu min qablu ka<u>dzaa</u>lika ya<u>th</u>ba\'ull<u>aa</u>hu \'al<u>aa</u> quluubi lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1056, '7:102', 7, 102, 'wam<u>aa</u> wajadn<u>aa</u> li-aktsarihim min \'ahdin wa-in wajadn<u>aa</u> aktsarahum laf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (1057, '7:103', 7, 103, 'tsumma ba\'atsn<u>aa</u> min ba\'dihim muus<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> il<u>aa</u> fir\'awna wamala-ihi fa<u>zh</u>alamuu bih<u>aa</u> fanu<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu lmufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (1058, '7:104', 7, 104, 'waq<u>aa</u>la muus<u>aa</u> y<u>aa</u> fir\'awnu innii rasuulun min rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (1059, '7:105', 7, 105, '<u>h</u>aqiiqun \'al<u>aa</u> an l<u>aa</u> aquula \'al<u>aa</u>ll<u>aa</u>hi ill<u>aa</u> l<u>h</u>aqqa qad ji/tukum bibayyinatin min rabbikum fa-arsil ma\'iya banii isr<u>aa</u>-iil\r\r\n');
INSERT INTO quranLatin VALUES (1060, '7:106', 7, 106, 'q<u>aa</u>la in kunta ji/ta bi-<u>aa</u>yatin fa/ti bih<u>aa</u> in kunta mina <u>shshaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (1061, '7:107', 7, 107, 'fa-alq<u>aa</u> \'a<u>shaa</u>hu fa-i<u>dzaa</u> hiya tsu\'b<u>aa</u>nun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1062, '7:108', 7, 108, 'wanaza\'a yadahu fa-i<u>dzaa</u> hiya bay<u>daa</u>u linn<u>aats</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (1063, '7:109', 7, 109, 'q<u>aa</u>la lmalau min qawmi fir\'awna inna h<u>aadzaa</u> las<u>aah</u>irun \'aliim\r\r\n');
