INSERT INTO quranLatin VALUES (1065, '7:111', 7, 111, 'q<u>aa</u>luu arjih wa-akh<u>aa</u>hu wa-arsil fii lmad<u>aa</u>-ini <u>haa</u>syiriin\r\r\n');
