INSERT INTO quranLatin VALUES (1069, '7:115', 7, 115, 'q<u>aa</u>luu y<u>aa</u> muus<u>aa</u> imm<u>aa</u> an tulqiya wa-imm<u>aa</u> an nakuuna na<u>h</u>nu lmulqiin\r\r\n');
INSERT INTO quranLatin VALUES (1070, '7:116', 7, 116, 'q<u>aa</u>la lquu falamm<u>aa</u> lqaw sa<u>h</u>aruu a\'yuna nn<u>aa</u>si wastarhabuuhum waj<u>aa</u>uu bisi<u>h</u>rin \'a<u>zh</u>iim\r\r\n');
