INSERT INTO quranLatin VALUES (1072, '7:118', 7, 118, 'fawaqa\'a l<u>h</u>aqqu waba<u>th</u>ala m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1073, '7:119', 7, 119, 'faghulibuu hun<u>aa</u>lika wanqalabuu <u>shaa</u>ghiriin\r\r\n');
