INSERT INTO quranLatin VALUES (1075, '7:121', 7, 121, 'q<u>aa</u>luu <u>aa</u>mann<u>aa</u> birabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (1076, '7:122', 7, 122, 'rabbi muus<u>aa</u> wah<u>aa</u>ruun\r\r\n');
INSERT INTO quranLatin VALUES (1077, '7:123', 7, 123, 'q<u>aa</u>la fir\'awnu <u>aa</u>mantum bihi qabla an <u>aadz</u>ana lakum inna h<u>aadzaa</u> lamakrun makartumuuhu fii lmadiinati litukhrijuu minh<u>aa</u> ahlah<u>aa</u> fasawfa ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1078, '7:124', 7, 124, 'lauqa<u>th</u>i\'anna aydiyakum wa-arjulakum min khil<u>aa</u>fin tsumma lau<u>sh</u>allibannakum ajma\'iin\r\r\n');
INSERT INTO quranLatin VALUES (1079, '7:125', 7, 125, 'q<u>aa</u>luu inn<u>aa</u> il<u>aa</u> rabbin<u>aa</u> munqalibuun\r\r\n');
INSERT INTO quranLatin VALUES (1080, '7:126', 7, 126, 'wam<u>aa</u> tanqimu minn<u>aa</u> ill<u>aa</u> an <u>aa</u>mann<u>aa</u> bi-<u>aa</u>y<u>aa</u>ti rabbin<u>aa</u> lamm<u>aa</u> j<u>aa</u>-atn<u>aa</u> rabban<u>aa</u> afrigh \'alayn<u>aa</u> <u>sh</u>abran watawaffan<u>aa</u> muslimiin\r\r\n');
INSERT INTO quranLatin VALUES (1081, '7:127', 7, 127, 'waq<u>aa</u>la lmalau min qawmi fir\'awna ata<u>dz</u>aru muus<u>aa</u> waqawmahu liyufsiduu fii l-ar<u>dh</u>i waya<u>dz</u>araka wa<u>aa</u>lihataka q<u>aa</u>la sanuqattilu abn<u>aa</u>-ahum wanasta<u>h</u>yii nis<u>aa</u>-ahum wa-inn<u>aa</u> fawqahum q<u>aa</u>hiruun\r\r\n');
INSERT INTO quranLatin VALUES (1082, '7:128', 7, 128, 'q<u>aa</u>la muus<u>aa</u> liqawmihi ista\'iinuu bil<u>aa</u>hi wa<u>sh</u>biruu inna l-ar<u>dh</u>a lill<u>aa</u>hi yuuritsuh<u>aa</u> man yasy<u>aa</u>u min \'ib<u>aa</u>dihi wal\'<u>aa</u>qibatu lilmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (1083, '7:129', 7, 129, 'q<u>aa</u>luu uu<u>dz</u>iin<u>aa</u> min qabli an ta/tiyan<u>aa</u> wamin ba\'di m<u>aa</u> ji/tan<u>aa</u> q<u>aa</u>la \'as<u>aa</u> rabbukum an yuhlika \'aduwwakum wayastakhlifakum fii l-ar<u>dh</u>i fayan<u>zh</u>ura kayfa ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1084, '7:130', 7, 130, 'walaqad akha<u>dz</u>n<u>aa</u> <u>aa</u>la fir\'awna bissiniina wanaq<u>sh</u>in mina tstsamar<u>aa</u>ti la\'allahum ya<u>dzdz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1085, '7:131', 7, 131, 'fa-i<u>dzaa</u> j<u>aa</u>-at-humu l<u>h</u>asanatu q<u>aa</u>luu lan<u>aa</u> h<u>aadz</u>ihi wa-in tu<u>sh</u>ibhum sayyi-atun ya<u>ththh</u>ayyaruu bimuus<u>aa</u> waman ma\'ahu l<u>aa</u> innam<u>aa</u> <u>th</u><u>aa</u>-iruhum \'indall<u>aa</u>hi wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1086, '7:132', 7, 132, 'waq<u>aa</u>luu mahm<u>aa</u> ta/tin<u>aa</u> bihi min <u>aa</u>yatin litas<u>h</u>aran<u>aa</u> bih<u>aa</u> fam<u>aa</u> na<u>h</u>nu laka bimu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1087, '7:133', 7, 133, 'fa-arsaln<u>aa</u> \'alayhimu <u>ththh</u>uuf<u>aa</u>na waljar<u>aa</u>da walqummala wa<u>dhdh</u>af<u>aa</u>di\'a waddama <u>aa</u>y<u>aa</u>tin mufa<u>shsh</u>al<u>aa</u>tin fastakbaruu wak<u>aa</u>nuu qawman mujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (1088, '7:134', 7, 134, 'walamm<u>aa</u> waqa\'a \'alayhimu rrijzu q<u>aa</u>luu y<u>aa</u> muus<u>aa</u> ud\'u lan<u>aa</u> rabbaka bim<u>aa</u> \'ahida \'indaka la-in kasyafta \'ann<u>aa</u> rrijza lanu/minanna laka walanursilanna ma\'aka banii isr<u>aa</u>-iil\r\r\n');
INSERT INTO quranLatin VALUES (1089, '7:135', 7, 135, 'falamm<u>aa</u> kasyafn<u>aa</u> \'anhumu rrijza il<u>aa</u> ajalin hum b<u>aa</u>lighuuhu i<u>dzaa</u> hum yankutsuun\r\r\n');
INSERT INTO quranLatin VALUES (1090, '7:136', 7, 136, 'fantaqamn<u>aa</u> minhum fa-aghraqn<u>aa</u>hum fii lyammi bi-annahum ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wak<u>aa</u>nuu \'anh<u>aa</u> gh<u>aa</u>filiin\r\r\n');
INSERT INTO quranLatin VALUES (1091, '7:137', 7, 137, 'wa-awratsn<u>aa</u> lqawmalla<u>dz</u>iina k<u>aa</u>nuu yusta<u>dh</u>\'afuuna masy<u>aa</u>riqa l-ar<u>dh</u>i wamagh<u>aa</u>ribah<u>aa</u>llatii b<u>aa</u>rakn<u>aa</u> fiih<u>aa</u> watammat kalimatu rabbika l<u>h</u>usn<u>aa</u> \'al<u>aa</u> banii isr<u>aa</u>-iila bim<u>aa</u> <u>sh</u>abaruu wadammarn<u>aa</u> m<u>aa</u> k<u>aa</u>na ya<u>sh</u>na\'u fir\'awnu waqawmuhu wam<u>aa</u> k<u>aa</u>nuu ya\'risyuun\r\r\n');
INSERT INTO quranLatin VALUES (1092, '7:138', 7, 138, 'waj<u>aa</u>wazn<u>aa</u> bibanii isr<u>aa</u>-iila lba<u>h</u>ra fa-ataw \'al<u>aa</u> qawmin ya\'kufuuna \'al<u>aa</u> a<u>sh</u>n<u>aa</u>min lahum q<u>aa</u>luu y<u>aa</u> muus<u>aa</u> ij\'al lan<u>aa</u> il<u>aa</u>han kam<u>aa</u> lahum <u>aa</u>lihatun q<u>aa</u>la innakum qawmun tajhaluun\r\r\n');
INSERT INTO quranLatin VALUES (1093, '7:139', 7, 139, 'inna h<u>aa</u>ul<u>aa</u>-i mutabbarun m<u>aa</u> hum fiihi wab<u>aath</u>ilun m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1094, '7:140', 7, 140, 'q<u>aa</u>la aghayrall<u>aa</u>hi abghiikum il<u>aa</u>han wahuwa fa<u>dhdh</u>alakum \'al<u>aa</u> l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (1095, '7:141', 7, 141, 'wa-i<u>dz</u> anjayn<u>aa</u>kum min <u>aa</u>li fir\'awna yasuumuunakum suu-a l\'a<u>dzaa</u>bi yuqattiluuna abn<u>aa</u>-akum wayasta<u>h</u>yuuna nis<u>aa</u>-akum wafii <u>dzaa</u>likum bal<u>aa</u>un min rabbikum \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1096, '7:142', 7, 142, 'waw<u>aa</u>\'adn<u>aa</u> muus<u>aa</u> tsal<u>aa</u>tsiina laylatan wa-atmamn<u>aa</u>h<u>aa</u> bi\'asyrin fatamma miiq<u>aa</u>tu rabbihi arba\'iina laylatan waq<u>aa</u>la muus<u>aa</u> li-akhiihi h<u>aa</u>ruuna ukhlufnii fii qawmii wa-a<u>sh</u>li<u>h</u> wal<u>aa</u> tattabi\'sabiila lmufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (1097, '7:143', 7, 143, 'walamm<u>aa</u> j<u>aa</u>-a muus<u>aa</u> limiiq<u>aa</u>tin<u>aa</u> wakallamahu rabbuhu q<u>aa</u>la rabbi arinii an<u>zh</u>ur ilayka q<u>aa</u>la lan tar<u>aa</u>nii wal<u>aa</u>kini un<u>zh</u>ur il<u>aa</u> ljabali fa-ini istaqarra mak<u>aa</u>nahu fasawfa tar<u>aa</u>nii falamm<u>aa</u> tajall<u>aa</u> rabbuhu liljabali ja\'alahu dakkan wakharra muus<u>aa</u> <u>sh</u>a\'iqan falamm<u>aa</u> af<u>aa</u>qa q<u>aa</u>la sub<u>haa</u>naka tubtu ilayka wa-an<u>aa</u> awwalu lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1098, '7:144', 7, 144, 'q<u>aa</u>la y<u>aa</u> muus<u>aa</u> innii i<u>sth</u>afaytuka \'al<u>aa</u> nn<u>aa</u>si biris<u>aa</u>l<u>aa</u>tii wabikal<u>aa</u>mii fakhu<u>dz</u> m<u>aa</u> <u>aa</u>taytuka wakun mina sysy<u>aa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (1099, '7:145', 7, 145, 'wakatabn<u>aa</u> lahu fii l-alw<u>aah</u>i min kulli syay-in maw\'i<u>zh</u>atan wataf<u>sh</u>iilan likulli syay-in fakhu<u>dz</u>h<u>aa</u> biquwwatin wa/mur qawmaka ya/khu<u>dz</u>uu bi-a<u>h</u>sanih<u>aa</u> sauriikum d<u>aa</u>ra lf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (1100, '7:146', 7, 146, 'sa-a<u>sh</u>rifu \'an <u>aa</u>y<u>aa</u>tiyalla<u>dz</u>iina yatakabbaruuna fii l-ar<u>dh</u>i bighayri l<u>h</u>aqqi wa-in yaraw kulla <u>aa</u>yatin l<u>aa</u> yu/minuu bih<u>aa</u> wa-in yaraw sabiila rrusydi l<u>aa</u> yattakhi<u>dz</u>uuhu sabiilan wa-in yaraw sabiila lghayyi yattakhi<u>dz</u>uuhu sabiilan <u>dzaa</u>lika bi-annahum ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wak<u>aa</u>nuu \'anh<u>aa</u> gh<u>aa</u>filiin\r\r\n');
INSERT INTO quranLatin VALUES (1101, '7:147', 7, 147, 'walla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> waliq<u>aa</u>-i l-<u>aa</u>khirati <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum hal yujzawna ill<u>aa</u> m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1102, '7:148', 7, 148, 'wattakha<u>dz</u>a qawmu muus<u>aa</u> min ba\'dihi min <u>h</u>uliyyihim \'ijlan jasadan lahu khuw<u>aa</u>run alam yaraw annahu l<u>aa</u> yukallimuhum wal<u>aa </u>yahdiihim sabiilan ittakha<u>dz</u>uuhu wak<u>aa</u>nuu <u>zhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1103, '7:149', 7, 149, 'walamm<u>aa</u> suqi<u>th</u>a fii aydiihim wara-aw annahum qad <u>dh</u>alluu q<u>aa</u>luu la-in lam yar<u>h</u>amn<u>aa</u> rabbun<u>aa</u> wayaghfir lan<u>aa</u> lanakuunanna mina lkh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (1104, '7:150', 7, 150, 'walamm<u>aa</u> raja\'a muus<u>aa</u> il<u>aa</u> qawmihi gha<u>dh</u>b<u>aa</u>na asifan q<u>aa</u>la bi/sam<u>aa</u> khalaftumuunii min ba\'dii a\'ajiltum amra rabbikum wa-alq<u>aa </u>al-alw<u>aah</u>a wa-akha<u>dz</u>a bira/si akhiihi yajurruhu ilayhi q<u>aa</u>la ibna umma inna lqawma ista<u>dh</u>\'afuunii wak<u>aa</u>duu yaqtuluunanii fal<u>aa</u> tusymit biya l-a\'d<u>aa</u>-a wal<u>aa</u> taj\'alnii ma\'a lqawmi <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1105, '7:151', 7, 151, 'q<u>aa</u>la rabbi ighfir lii wali-akhii wa-adkhiln<u>aa</u> fii ra<u>h</u>matika wa-anta ar<u>h</u>amu rr<u>aah</u>imiin\r\r\n');
INSERT INTO quranLatin VALUES (1106, '7:152', 7, 152, 'innalla<u>dz</u>iina ittakha<u>dz</u>uu l\'ijla sayan<u>aa</u>luhum gha<u>dh</u>abun min rabbihim wa<u>dz</u>illatun fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> waka<u>dzaa</u>lika najzii lmuftariin\r\r\n');
INSERT INTO quranLatin VALUES (1107, '7:153', 7, 153, 'walla<u>dz</u>iina \'amiluu ssayyi-<u>aa</u>ti tsumma t<u>aa</u>buu min ba\'dih<u>aa</u> wa<u>aa</u>manuu inna rabbaka min ba\'dih<u>aa</u> laghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1108, '7:154', 7, 154, 'walamm<u>aa</u> sakata \'an muus<u>aa</u> lgha<u>dh</u>abu akha<u>dz</u>a l-alw<u>aah</u>a wafii nuskhatih<u>aa</u> hudan wara<u>h</u>matun lilla<u>dz</u>iina hum lirabbihim yarhabuun\r\r\n');
INSERT INTO quranLatin VALUES (1109, '7:155', 7, 155, 'wakht<u>aa</u>ra muus<u>aa</u> qawmahu sab\'iina rajulan limiiq<u>aa</u>tin<u>aa</u> falamm<u>aa</u> akha<u>dz</u>at-humu rrajfatu q<u>aa</u>la rabbi law syi/ta ahlaktahum min qablu wa-iyy<u>aa</u>ya atuhlikun<u>aa</u> bim<u>aa</u> fa\'ala ssufah<u>aa</u>u minn<u>aa</u> in hiya ill<u>aa</u> fitnatuka tu<u>dh</u>illu bih<u>aa</u> man tasy<u>aa</u>u watahdii man tasy<u>aa</u>u anta waliyyun<u>aa</u> faghfir lan<u>aa</u> war<u>h</u>amn<u>aa</u> wa-anta khayru lgh<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1110, '7:156', 7, 156, 'waktub lan<u>aa</u> fii h<u>aadz</u>ihi dduny<u>aa</u> <u>h</u>asanatan wafii l-<u>aa</u>khirati inn<u>aa</u> hudn<u>aa</u> ilayka q<u>aa</u>la \'a<u>dzaa</u>bii u<u>sh</u>iibu bihi man asy<u>aa</u>u wara<u>h</u>matii wasi\'at kulla syay-in fasa-aktubuh<u>aa</u> lilla<u>dz</u>iina yattaquuna wayu/tuuna zzak<u>aa</u>ta walla<u>dz</u>iina hum bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1111, '7:157', 7, 157, '<B>al</b>la<u>dz</u>iina yattabi\'uuna rrasuula nnabiyya l-ummiyyalla<u>dz</u>ii yajiduunahu maktuuban \'indahum fii ttawr<u>aa</u>ti wal-injiili ya/muruhum bilma\'ruufi wayanh<u>aa</u>hum \'ani lmunkari wayu<u>h</u>illu lahumu <u>ththh</u>ayyib<u>aa</u>ti wayu<u>h</u>arrimu \'alayhimu lkhab<u>aa</u>-itsa waya<u>dh</u>a\'u \'anhum i<u>sh</u>rahum wal-aghl<u>aa</u>lallatii k<u>aa</u>nat \'alayhim falla<u>dz</u>iina <u>aa</u>manuu bihi wa\'azzaruuhu wana<u>sh</u>aruuhu wattaba\'uu nnuuralla<u>dz</u>ii unzila ma\'ahu ul<u>aa</u>-ika humu lmufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1112, '7:158', 7, 158, 'qul y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su innii rasuulull<u>aa</u>hi ilaykum jamii\'anlla<u>dz</u>ii lahu mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa yu<u>h</u>yii wayumiitu fa<u>aa</u>minuu bil<u>aa</u>hi warasuulihi nnabiyyi l-ummiyyilla<u>dz</u>ii yu/minu bil<u>aa</u>hi wakalim<u>aa</u>tihi wattabi\'uuhu la\'allakum tahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (1113, '7:159', 7, 159, 'wamin qawmi muus<u>aa</u> ummatun yahduuna bil<u>h</u>aqqi wabihi ya\'diluun\r\r\n');
INSERT INTO quranLatin VALUES (1114, '7:160', 7, 160, 'waqa<u>ththh</u>a\'n<u>aa</u>humu itsnatay \'asyrata asb<u>aath</u>an umaman wa-aw<u>h</u>ayn<u>aa</u> il<u>aa</u> muus<u>aa</u> i<u>dz</u>i istasq<u>aa</u>hu qawmuhu ani i<u>dh</u>rib bi\'a<u>shaa</u>ka l<u>h</u>ajara fanbajasat minhu itsnat<u>aa</u> \'asyrata \'aynan qad \'alima kullu un<u>aa</u>sin masyrabahum wa<u>zh</u>allaln<u>aa</u> \'alayhimu lgham<u>aa</u>ma wa-anzaln<u>aa</u> \'alayhimu lmanna wassalw<u>aa</u> kuluu min <u>th</u>ayyib<u>aa</u>ti m<u>aa</u> razaqn<u>aa</u>kum wam<u>aa</u> <u>zh</u>alamuun<u>aa</u> wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (1115, '7:161', 7, 161, 'wa-i<u>dz</u> qiila lahumu uskunuu h<u>aadz</u>ihi lqaryata wakuluu minh<u>aa</u> <u>h</u>aytsu syi/tum waquuluu <u>h</u>i<u>ththh</u>atun wadkhuluu lb<u>aa</u>ba sujjadan naghfir lakum kha<u>th</u>ii-<u>aa</u>tikum sanaziidu lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (1116, '7:162', 7, 162, 'fabaddalalla<u>dz</u>iina <u>zh</u>alamuu minhum qawlan ghayralla<u>dz</u>ii qiila lahum fa-arsaln<u>aa</u> \'alayhim rijzan mina ssam<u>aa</u>-i bim<u>aa</u> k<u>aa</u>nuu ya<u>zh</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (1117, '7:163', 7, 163, 'was-alhum \'ani lqaryatillatii k<u>aa</u>nat <u>had</u>irata lba<u>h</u>ri i<u>dz</u> ya\'duuna fii ssabti i<u>dz</u> ta/tiihim <u>h</u>iit<u>aa</u>nuhum yawma sabtihim syurra\'an wayawma l<u>aa</u> yasbituuna l<u>aa</u> ta/tiihim ka<u>dzaa</u>lika nabluuhum bim<u>aa</u> k<u>aa</u>nuu yafsuquun\r\r\n');
INSERT INTO quranLatin VALUES (1118, '7:164', 7, 164, 'wa-i<u>dz</u> q<u>aa</u>lat ummatun minhum lima ta\'i<u>zh</u>uuna qawmanll<u>aa</u>hu muhlikuhum aw mu\'a<u>dzdz</u>ibuhum \'a<u>dzaa</u>ban syadiidan q<u>aa</u>luu ma\'<u>dz</u>iratan il<u>aa</u> rabbikum wala\'allahum yattaquun\r\r\n');
INSERT INTO quranLatin VALUES (1119, '7:165', 7, 165, 'falamm<u>aa</u> nasuu m<u>aa</u> <u>dz</u>ukkiruu bihi anjayn<u>aa</u>lla<u>dz</u>iina yanhawna \'ani ssuu-i wa-akha<u>dz</u>n<u>aa</u>lla<u>dz</u>iina <u>zh</u>alamuu bi\'a<u>dzaa</u>bin ba-iisin bim<u>aa</u> k<u>aa</u>nuu yafsuquun\r\r\n');
INSERT INTO quranLatin VALUES (1120, '7:166', 7, 166, 'falamm<u>aa</u> \'ataw \'an m<u>aa</u> nuhuu \'anhu quln<u>aa</u> lahum kuunuu qiradatan kh<u>aa</u>si-iin\r\r\n');
INSERT INTO quranLatin VALUES (1121, '7:167', 7, 167, 'wa-i<u>dz</u> ta-a<u>dzdz</u>ana rabbuka layab\'atsanna \'alayhim il<u>aa</u> yawmi lqiy<u>aa</u>mati man yasuumuhum suu-a l\'a<u>dzaa</u>bi inna rabbaka lasarii\'u l\'iq<u>aa</u>bi wa-innahu laghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1122, '7:168', 7, 168, 'waqa<u>ththh</u>a\'n<u>aa</u>hum fii l-ar<u>dh</u>i umaman minhumu <u>shshaa</u>li<u>h</u>uuna waminhum duuna <u>dzaa</u>lika wabalawn<u>aa</u>hum bil<u>h</u>asan<u>aa</u>ti wassayyi-<u>aa</u>ti la\'allahum yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1123, '7:169', 7, 169, 'fakhalafa min ba\'dihim khalfun waritsuu alkit<u>aa</u>ba ya/khu<u>dz</u>uuna \'ara<u>dh</u>a h<u>aadzaa</u> al-adn<u>aa</u> wayaquuluuna sayughfaru lan<u>aa</u> wa-in ya/tihim \'ara<u>dh</u>un mitsluhu ya/khu<u>dz</u>uuhu alam yu/kha<u>dz</u> \'alayhim miits<u>aa</u>qu alkit<u>aa</u>bi an l<u>aa</u> yaquuluu \'al<u>aa</u>ll<u>aa</u>hi ill<u>aa</u> al<u>h</u>aqqa wadarasuu m<u>aa</u> fiihi wadd<u>aa</u>ru al-<u>aa</u>khiratu khayrun lilla<u>dz</u>iina yattaquuna afal<u>aa</u> ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (1124, '7:170', 7, 170, 'walla<u>dz</u>iina yumassikuuna bilkit<u>aa</u>bi wa-aq<u>aa</u>muu <u>shsh</u>al<u>aa</u>ta inn<u>aa</u> l<u>aa</u> nu<u>dh</u>ii\'u ajra lmu<u>sh</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1125, '7:171', 7, 171, 'wa-i<u>dz</u> nataqn<u>aa</u> ljabala fawqahum ka-annahu <u>zh</u>ullatun wa<u>zh</u>annuu annahu w<u>aa</u>qi\'un bihim khu<u>dz</u>uu m<u>aa</u> <u>aa</u>tayn<u>aa</u>kum biquwwatin wa<u>dz</u>kuruu m<u>aa</u> fiihi la\'allakum tattaquun\r\r\n');
INSERT INTO quranLatin VALUES (1126, '7:172', 7, 172, 'wa-i<u>dz</u> akha<u>dz</u>a rabbuka min banii <u>aa</u>dama min <u>zh</u>uhuurihim <u>dz</u>urriyyatahum wa-asyhadahum \'al<u>aa</u> anfusihim alastu birabbikum q<u>aa</u>luu bal<u>aa</u> syahidn<u>aa</u> an taquuluu yawma alqiy<u>aa</u>mati inn<u>aa</u> kunn<u>aa</u> \'an h<u>aadzaa</u> gh<u>aa</u>filiin\r\r\n');
INSERT INTO quranLatin VALUES (1127, '7:173', 7, 173, 'aw taquuluu innam<u>aa</u> asyraka <u>aa</u>b<u>aa</u>un<u>aa</u> min qablu wakunn<u>aa</u> <u>dz</u>urriyyatan min ba\'dihim afatuhlikun<u>aa</u> bim<u>aa</u> fa\'ala lmub<u>th</u>iluun\r\r\n');
INSERT INTO quranLatin VALUES (1128, '7:174', 7, 174, 'waka<u>dzaa</u>lika nufa<u>shsh</u>ilu l-<u>aa</u>y<u>aa</u>ti wala\'allahum yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1129, '7:175', 7, 175, 'watlu \'alayhim naba-alla<u>dz</u>ii <u>aa</u>tayn<u>aa</u>hu <u>aa</u>y<u>aa</u>tin<u>aa</u> fansalakha minh<u>aa</u> fa-atba\'ahu sysyay<u>th</u><u>aa</u>nu fak<u>aa</u>na mina lgh<u>aa</u>wiin\r\r\n');
INSERT INTO quranLatin VALUES (1130, '7:176', 7, 176, 'walaw syi/n<u>aa</u> larafa\'n<u>aa</u>hu bih<u>aa</u> wal<u>aa</u>kinnahu akhlada il<u>aa</u> l-ar<u>dh</u>i wattaba\'a haw<u>aa</u>hu famatsaluhu kamatsali lkalbi in ta<u>h</u>mil \'alayhi yalhats aw tatruk-hu yalhats <u>dzaa</u>lika matsalu lqawmilla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> faq<u>sh</u>u<u>sh</u>i lqa<u>sh</u>a<u>sh</u>a la\'allahum yatafakkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1131, '7:177', 7, 177, 's<u>aa</u>-a matsalan lqawmulla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wa-anfusahum k<u>aa</u>nuu ya<u>zh</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (1132, '7:178', 7, 178, 'man yahdill<u>aa</u>hu fahuwa lmuhtadii waman yu<u>dh</u>lil faul<u>aa</u>-ika humu lkh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (1133, '7:179', 7, 179, 'walaqad <u>dz</u>ara/n<u>aa</u> lijahannama katsiiran mina ljinni wal-insi lahum quluubun l<u>aa</u> yafqahuuna bih<u>aa</u> walahum a\'yunun l<u>aa</u> yub<u>sh</u>iruuna bih<u>aa</u> walahum <u>aatsaa</u>nun l<u>aa</u> yasma\'uuna bih<u>aa</u> ul<u>aa</u>-ika kal-an\'<u>aa</u>mi bal hum a<u>dh</u>allu ul<u>aa</u>-ika humu lgh<u>aa</u>filuun\r\r\n');
INSERT INTO quranLatin VALUES (1134, '7:180', 7, 180, 'walill<u>aa</u>hi l-asm<u>aa</u>u l<u>h</u>usn<u>aa</u> fad\'uuhu bih<u>aa</u> wa<u>dz</u>aruulla<u>dz</u>iina yul<u>h</u>iduuna fii asm<u>aa</u>-ihi sayujzawna m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1135, '7:181', 7, 181, 'wamimman khalaqn<u>aa</u> ummatun yahduuna bil<u>h</u>aqqi wabihi ya\'diluun\r\r\n');
INSERT INTO quranLatin VALUES (1136, '7:182', 7, 182, 'walla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> sanastadrijuhum min <u>h</u>aytsu l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1137, '7:183', 7, 183, 'waumlii lahum inna kaydii matiin\r\r\n');
INSERT INTO quranLatin VALUES (1138, '7:184', 7, 184, 'awa lam yatafakkaruu m<u>aa</u> bi<u>shaah</u>ibihim min jinnatin in huwa ill<u>aa</u> na<u>dz</u>iirun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1139, '7:185', 7, 185, 'awalam yan<u>zh</u>uruu fii malakuuti ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wam<u>aa</u> khalaqall<u>aa</u>hu min syay-in wa-an \'as<u>aa</u> an yakuuna qadi iqtaraba ajaluhum fabi-ayyi <u>h</u>adiitsin ba\'dahu yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1140, '7:186', 7, 186, 'man yu<u>dh</u>lilill<u>aa</u>hu fal<u>aa</u> h<u>aa</u>diya lahu waya<u>dz</u>aruhum fii <u>th</u>ughy<u>aa</u>nihim ya\'mahuun\r\r\n');
INSERT INTO quranLatin VALUES (1141, '7:187', 7, 187, 'yas-aluunaka \'ani ss<u>aa</u>\'ati ayy<u>aa</u>na murs<u>aa</u>h<u>aa</u> qul innam<u>aa</u> \'ilmuh<u>aa</u> \'inda rabbii l<u>aa</u> yujalliih<u>aa</u> liwaqtih<u>aa</u> ill<u>aa</u> huwa tsaqulat fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i l<u>aa</u> ta/tiikum ill<u>aa</u> baghtatan yas-aluunaka ka-annaka <u>h</u>afiyyun \'anh<u>aa</u> qul innam<u>aa</u> \'ilmuh<u>aa</u> \'indall<u>aa</u>hi wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1142, '7:188', 7, 188, 'qul l<u>aa</u> amliku linafsii naf\'an wal<u>aa</u> <u>dh</u>arran ill<u>aa</u> m<u>aa</u> sy<u>aa</u>-all<u>aa</u>hu walaw kuntu a\'lamu lghayba lastaktsartu mina lkhayri wam<u>aa</u> massaniya ssuu-u in an<u>aa</u> ill<u>aa</u> na<u>dz</u>iirun wabasyiirun liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1143, '7:189', 7, 189, 'huwalla<u>dz</u>ii khalaqakum min nafsin w<u>aah</u>idatin waja\'ala minh<u>aa</u> zawjah<u>aa</u> liyaskuna ilayh<u>aa</u> falamm<u>aa</u> taghasysy<u>aa</u>h<u>aa</u> <u>h</u>amalat <u>h</u>amlan khafiifan famarrat bihi falamm<u>aa</u> atsqalat da\'aw<u>aa</u>ll<u>aa</u>ha rabbahum<u>aa</u> la-in <u>aa</u>taytan<u>aa</u> <u>shaa</u>li<u>h</u>an lanakuunanna mina sysy<u>aa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (1144, '7:190', 7, 190, 'falamm<u>aa</u> <u>aa</u>t<u>aa</u>hum<u>aa</u> <u>shaa</u>li<u>h</u>an ja\'al<u>aa</u> lahu syurak<u>aa</u>-a fiim<u>aa</u> <u>aa</u>t<u>aa</u>hum<u>aa</u> fata\'<u>aa</u>l<u>aa</u>ll<u>aa</u>hu \'amm<u>aa</u> yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (1145, '7:191', 7, 191, 'ayusyrikuuna m<u>aa</u> l<u>aa</u> yakhluqu syay-an wahum yukhlaquun\r\r\n');
INSERT INTO quranLatin VALUES (1146, '7:192', 7, 192, 'wal<u>aa</u> yasta<u>th</u>ii\'uuna lahum na<u>sh</u>ran wal<u>aa</u> anfusahum yan<u>sh</u>uruun\r\r\n');
INSERT INTO quranLatin VALUES (1147, '7:193', 7, 193, 'wa-in tad\'uuhum il<u>aa</u> lhud<u>aa</u> l<u>aa</u> yattabi\'uukum saw<u>aa</u>un \'alaykum ada\'awtumuuhum am antum <u>shaa</u>mituun\r\r\n');
INSERT INTO quranLatin VALUES (1148, '7:194', 7, 194, 'innalla<u>dz</u>iina tad\'uuna min duunill<u>aa</u>hi \'ib<u>aa</u>dun amts<u>aa</u>lukum fad\'uuhum falyastajiibuu lakum in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (1149, '7:195', 7, 195, 'alahum arjulun yamsyuuna bih<u>aa</u> am lahum aydin yab<u>th</u>isyuuna bih<u>aa</u> am lahum a\'yunun yub<u>sh</u>iruuna bih<u>aa</u> am lahum <u>aatsaa</u>nun yasma\'uuna bih<u>aa</u> quli ud\'uu syurak<u>aa</u>-akum tsumma kiiduuni fal<u>aa</u> tun<u>zh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (1150, '7:196', 7, 196, 'inna waliyyiyall<u>aa</u>hulla<u>dz</u>ii nazzala lkit<u>aa</u>ba wahuwa yatawall<u>aa</u> <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1151, '7:197', 7, 197, 'walla<u>dz</u>iina tad\'uuna min duunihi l<u>aa</u> yasta<u>th</u>ii\'uuna na<u>sh</u>rakum wal<u>aa</u> anfusahum yan<u>sh</u>uruun\r\r\n');
INSERT INTO quranLatin VALUES (1152, '7:198', 7, 198, 'wa-in tad\'uuhum il<u>aa</u> lhud<u>aa</u> l<u>aa</u> yasma\'uu watar<u>aa</u>hum yan<u>zh</u>uruuna ilayka wahum l<u>aa</u> yub<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (1153, '7:199', 7, 199, 'khu<u>dz</u>i l\'afwa wa/mur bil\'urfi wa-a\'ri<u>dh</u> \'ani lj<u>aa</u>hiliin\r\r\n');
INSERT INTO quranLatin VALUES (1154, '7:200', 7, 200, 'wa-imm<u>aa</u> yanzaghannaka mina sysyay<u>th</u><u>aa</u>ni nazghun fasta\'i<u>dz</u> bil<u>aa</u>hi innahu samii\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1155, '7:201', 7, 201, 'innalla<u>dz</u>iina ittaqaw i<u>dzaa</u> massahum <u>th</u><u>aa</u>-ifun mina sysyay<u>th</u><u>aa</u>ni ta<u>dz</u>akkaruu fa-i<u>dzaa</u> hum mub<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (1156, '7:202', 7, 202, 'wa-ikhw<u>aa</u>nuhum yamudduunahum fii lghayyi tsumma l<u>aa</u> yuq<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (1157, '7:203', 7, 203, 'wa-i<u>dzaa</u> lam ta/tihim bi-<u>aa</u>yatin q<u>aa</u>luu lawl<u>aa</u> ijtabaytah<u>aa</u> qul innam<u>aa</u> attabi\'u m<u>aa</u> yuu<u>haa</u> ilayya min rabbii h<u>aadzaa</u> ba<u>shaa</u>-iru min rabbikum wahudan wara<u>h</u>matun liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1158, '7:204', 7, 204, 'wa-i<u>dzaa</u> quri-a lqur-<u>aa</u>nu fastami\'uu lahu wa-an<u>sh</u>ituu la\'allakum tur<u>h</u>amuun\r\r\n');
INSERT INTO quranLatin VALUES (1159, '7:205', 7, 205, 'wa<u>dz</u>kur rabbaka fii nafsika ta<u>dh</u>arru\'an wakhiifatan waduuna ljahri mina lqawli bilghuduwwi wal-<u>aasaa</u>li wal<u>aa</u> takun mina lgh<u>aa</u>filiin\r\r\n');
INSERT INTO quranLatin VALUES (1160, '7:206', 7, 206, 'innalla<u>dz</u>iina \'inda rabbika l<u>aa</u> yastakbiruuna \'an \'ib<u>aa</u>datihi wayusabbi<u>h</u>uunahu walahu yasjuduun\r\r\n');
INSERT INTO quranLatin VALUES (1161, '8:1', 8, 1, 'yas-aluunaka \'ani l-anf<u>aa</u>li quli l-anf<u>aa</u>lu lill<u>aa</u>hi warrasuuli fattaquull<u>aa</u>ha wa-a<u>sh</u>li<u>h</u>uu <u>dzaa</u>ta baynikum wa-a<u>th</u>ii\'uull<u>aa</u>ha warasuulahu in kuntum mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1162, '8:2', 8, 2, 'innam<u>aa</u> lmu/minuunalla<u>dz</u>iina i<u>dzaa</u> <u>dz</u>ukirall<u>aa</u>hu wajilat quluubuhum wa-i<u>dzaa</u> tuliyat \'alayhim <u>aa</u>y<u>aa</u>tuhu z<u>aa</u>dat-hum iim<u>aa</u>nan wa\'al<u>aa</u> rabbihim yatawakkaluun\r\r\n');
INSERT INTO quranLatin VALUES (1163, '8:3', 8, 3, '<B>al</b>la<u>dz</u>iina yuqiimuuna <u>shsh</u>al<u>aa</u>ta wamimm<u>aa</u> razaqn<u>aa</u>hum yunfiquun\r\r\n');
INSERT INTO quranLatin VALUES (1164, '8:4', 8, 4, 'ul<u>aa</u>-ika humu lmu/minuuna <u>h</u>aqqan lahum daraj<u>aa</u>tun \'inda rabbihim wamaghfiratun warizqun kariim\r\r\n');
INSERT INTO quranLatin VALUES (1165, '8:5', 8, 5, 'kam<u>aa</u> akhrajaka rabbuka min baytika bil<u>h</u>aqqi wa-inna fariiqan mina lmu/miniina lak<u>aa</u>rihuun\r\r\n');
INSERT INTO quranLatin VALUES (1166, '8:6', 8, 6, 'yuj<u>aa</u>diluunaka fii l<u>h</u>aqqi ba\'da m<u>aa</u> tabayyana ka-annam<u>aa</u> yus<u>aa</u>quuna il<u>aa</u> lmawti wahum yan<u>zh</u>uruun\r\r\n');
INSERT INTO quranLatin VALUES (1167, '8:7', 8, 7, 'wa-i<u>dz</u> ya\'idukumull<u>aa</u>hu i<u>h</u>d<u>aa</u> <u>ththaa</u>-ifatayni annah<u>aa</u> lakum watawadduuna anna ghayra <u>dzaa</u>ti sysyawkati takuunu lakum wayuriidull<u>aa</u>hu an yu<u>h</u>iqqa l<u>h</u>aqqa bikalim<u>aa</u>tihi wayaq<u>th</u>a\'a d<u>aa</u>bira lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1168, '8:8', 8, 8, ' liyu<u>h</u>iqqa l<u>h</u>aqqa wayub<u>th</u>ila lb<u>aath</u>ila walaw kariha lmujrimuun\r\r\n');
INSERT INTO quranLatin VALUES (1169, '8:9', 8, 9, 'i<u>dz</u> tastaghiitsuuna rabbakum fastaj<u>aa</u>ba lakum annii mumiddukum bi-alfin mina lmal<u>aa</u>-ikati murdifiin\r\r\n');
INSERT INTO quranLatin VALUES (1170, '8:10', 8, 10, 'wam<u>aa</u> ja\'alahull<u>aa</u>hu ill<u>aa</u> busyr<u>aa</u> walita<u>th</u>ma-inna bihi quluubukum wam<u>aa</u> nna<u>sh</u>ru ill<u>aa</u> min \'indill<u>aa</u>hi innall<u>aa</u>ha \'aziizun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1171, '8:11', 8, 11, 'i<u>dz</u> yughasysyiikumu nnu\'<u>aa</u>sa amanatan minhu wayunazzilu \'alaykum mina ssam<u>aa</u>-i m<u>aa</u>-an liyu<u>th</u>ahhirakum bihi wayu<u>dz</u>hiba \'ankum rijza sysyay<u>th</u><u>aa</u>ni waliyarbi<u>th</u>a \'al<u>aa</u> quluubikum wayutsabbita bihi l-aqd<u>aa</u>m\r\r\n');
INSERT INTO quranLatin VALUES (1172, '8:12', 8, 12, 'i<u>dz</u> yuu<u>h</u>ii rabbuka il<u>aa</u> lmal<u>aa</u>-ikati annii ma\'akum fatsabbituulla<u>dz</u>iina <u>aa</u>manuu saulqii fii quluubilla<u>dz</u>iina kafaruu rru\'ba fa<u>dh</u>ribuu fawqa l-a\'n<u>aa</u>qi wa<u>dh</u>ribuu minhum kulla ban<u>aa</u>n\r\r\n');
INSERT INTO quranLatin VALUES (1173, '8:13', 8, 13, '<u>dzaa</u>lika bi-annahum sy<u>aa</u>qquull<u>aa</u>ha warasuulahu waman yusy<u>aa</u>qiqill<u>aa</u>ha warasuulahu fa-innall<u>aa</u>ha syadiidu l\'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1174, '8:14', 8, 14, '<u>dzaa</u>likum fa<u>dz</u>uuquuhu wa-anna lilk<u>aa</u>firiina \'a<u>dzaa</u>ba nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1175, '8:15', 8, 15, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> laqiitumulla<u>dz</u>iina kafaruu za<u>h</u>fan fal<u>aa</u> tuwalluuhumu l-adb<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1176, '8:16', 8, 16, 'waman yuwallihim yawma-i<u>dz</u>in duburahu ill<u>aa</u> muta<u>h</u>arrifan liqit<u>aa</u>lin aw muta<u>h</u>ayyizan il<u>aa</u> fi-atin faqad b<u>aa</u>-a bigha<u>dh</u>abin minall<u>aa</u>hi wama/w<u>aa</u>hu jahannamu wabi/sa lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (1177, '8:17', 8, 17, 'falam taqtuluuhum wal<u>aa</u>kinnall<u>aa</u>ha qatalahum wam<u>aa</u> ramayta i<u>dz</u> ramayta wal<u>aa</u>kinnall<u>aa</u>ha ram<u>aa</u> waliyubliya lmu/miniina minhu bal<u>aa</u>-an <u>h</u>asanan innall<u>aa</u>ha samii\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1178, '8:18', 8, 18, '<u>dzaa</u>likum wa-annall<u>aa</u>ha muuhinu kaydi lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1179, '8:19', 8, 19, 'in tastafti<u>h</u>uu faqad j<u>aa</u>-akumu lfat<u>h</u>u wa-in tantahuu fahuwa khayrun lakum wa-in ta\'uuduu na\'ud walan tughniya \'ankum fi-atukum syay-an walaw katsurat wa-annall<u>aa</u>ha ma\'a lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1180, '8:20', 8, 20, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu a<u>th</u>ii\'uull<u>aa</u>ha warasuulahu wal<u>aa</u> tawallaw \'anhu wa-antum tasma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1181, '8:21', 8, 21, 'wal<u>aa</u> takuunuu kalla<u>dz</u>iina q<u>aa</u>luu sami\'n<u>aa</u> wahum l<u>aa</u> yasma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1182, '8:22', 8, 22, 'inna syarra ddaw<u>aa</u>bbi \'indall<u>aa</u>hi <u>shsh</u>ummu lbukmulla<u>dz</u>iina l<u>aa</u> ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (1183, '8:23', 8, 23, 'walaw \'alimall<u>aa</u>hu fiihim khayran la-asma\'ahum walaw asma\'ahum latawallaw wahum mu\'ri<u>dh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1184, '8:24', 8, 24, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu istajiibuu lill<u>aa</u>hi walirrasuuli i<u>dzaa</u> da\'<u>aa</u>kum lim<u>aa</u> yu<u>h</u>yiikum wa\'lamuu annall<u>aa</u>ha ya<u>h</u>uulu bayna lmar-i waqalbihi wa-annahu ilayhi tu<u>h</u>syaruun\r\r\n');
INSERT INTO quranLatin VALUES (1185, '8:25', 8, 25, 'wattaquu fitnatan l<u>aa</u> tu<u>sh</u>iibannalla<u>dz</u>iina <u>zh</u>alamuu minkum kh<u>aass</u>atan wa\'lamuu annall<u>aa</u>ha syadiidu l\'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1186, '8:26', 8, 26, 'wa<u>dz</u>kuruu i<u>dz</u> antum qaliilun musta<u>dh</u>\'afuuna fii l-ar<u>dh</u>i takh<u>aa</u>fuuna an yatakha<u>ththh</u>afakumu nn<u>aa</u>su fa<u>aa</u>w<u>aa</u>kum wa-ayyadakum bina<u>sh</u>rihi warazaqakum mina <u>ththh</u>ayyib<u>aa</u>ti la\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (1187, '8:27', 8, 27, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> takhuunuull<u>aa</u>ha warrasuula watakhuunuu am<u>aa</u>n<u>aa</u>tikum wa-antum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1188, '8:28', 8, 28, 'wa\'lamuu annam<u>aa</u> amw<u>aa</u>lukum wa-awl<u>aa</u>dukum fitnatun wa-annall<u>aa</u>ha \'indahu ajrun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1189, '8:29', 8, 29, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>m<u>aa</u>nuu in tattaquull<u>aa</u>ha yaj\'al lakum furq<u>aa</u>nan wayukaffir \'ankum sayyi-<u>aa</u>tikum wayaghfir lakum wal<u>aa</u>hu <u>dz</u>uu lfa<u>dh</u>li l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1190, '8:30', 8, 30, 'wa-i<u>dz</u> yamkuru bikalla<u>dz</u>iina kafaruu liyutsbituuka aw yaqtuluuka aw yukhrijuuka wayamkuruuna wayamkurull<u>aa</u>hu wal<u>aa</u>hu khayru lm<u>aa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (1191, '8:31', 8, 31, 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tun<u>aa</u> q<u>aa</u>luu qad sami\'n<u>aa</u> law nasy<u>aa</u>u laquln<u>aa</u> mitsla h<u>aadzaa</u> in h<u>aadzaa</u> ill<u>aa</u> as<u>aath</u>iiru l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (1192, '8:32', 8, 32, 'wa-i<u>dz</u> q<u>aa</u>luull<u>aa</u>humma in k<u>aa</u>na h<u>aadzaa</u> huwa l<u>h</u>aqqa min \'indika fa-am<u>th</u>ir \'alayn<u>aa</u> <u>h</u>ij<u>aa</u>ratan mina ssam<u>aa</u>-i awi i/tin<u>aa</u> bi\'a<u>dzaa</u>bin liim\r\r\n');
INSERT INTO quranLatin VALUES (1193, '8:33', 8, 33, 'wam<u>aa</u> k<u>aa</u>nall<u>aa</u>hu liyu\'a<u>dzdz</u>ibahum wa-anta fiihim wam<u>aa</u> k<u>aa</u>nall<u>aa</u>hu mu\'a<u>dzdz</u>ibahum wahum yastaghfiruun\r\r\n');
INSERT INTO quranLatin VALUES (1194, '8:34', 8, 34, 'wam<u>aa</u> lahumll<u>aa</u> yu\'a<u>dzdz</u>ibahumull<u>aa</u>hu wahum ya<u>sh</u>udduuna \'ani lmasjidi l<u>h</u>ar<u>aa</u>mi wam<u>aa</u> k<u>aa</u>nuu awliy<u>aa</u>-ahu in awliy<u>aa</u>uhu ill<u>aa</u> lmuttaquuna wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1195, '8:35', 8, 35, 'wam<u>aa</u> k<u>aa</u>na <u>sh</u>al<u>aa</u>tuhum \'inda lbayti ill<u>aa</u> muk<u>aa</u>-an wata<u>sh</u>diyatan fa<u>dz</u>uuquu l\'a<u>dzaa</u>ba bim<u>aa</u> kuntum takfuruun\r\r\n');
INSERT INTO quranLatin VALUES (1196, '8:36', 8, 36, 'innalla<u>dz</u>iina kafaruu yunfiquuna amw<u>aa</u>lahum liya<u>sh</u>udduu \'an sabiilill<u>aa</u>hi fasayunfiquunah<u>aa</u> tsumma takuunu \'alayhim <u>h</u>asratan tsumma yughlabuuna walla<u>dz</u>iina kafaruu il<u>aa</u> jahannama yu<u>h</u>syaruun\r\r\n');
INSERT INTO quranLatin VALUES (1197, '8:37', 8, 37, 'liyamiizall<u>aa</u>hu lkhabiitsa mina <u>ththh</u>ayyibi wayaj\'ala lkhabiitsa ba\'<u>dh</u>ahu \'al<u>aa</u> ba\'<u>dh</u>in fayarkumahu jamii\'an fayaj\'alahu fii jahannama ul<u>aa</u>-ika humu lkh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (1198, '8:38', 8, 38, 'qul lilla<u>dz</u>iina kafaruu in yantahuu yughfar lahum m<u>aa</u> qad salafa wa-in ya\'uuduu faqad ma<u>dh</u>at sunnatu l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (1199, '8:39', 8, 39, 'waq<u>aa</u>tiluuhum <u>h</u>att<u>aa</u> l<u>aa</u> takuuna fitnatun wayakuuna ddiinu kulluhu lill<u>aa</u>hi fa-ini intahaw fa-innall<u>aa</u>ha bim<u>aa</u> ya\'maluuna ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (1200, '8:40', 8, 40, 'wa-in tawallaw fa\'lamuu annall<u>aa</u>ha mawl<u>aa</u>kum ni\'ma lmawl<u>aa</u> wani\'ma nna<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (1201, '8:41', 8, 41, 'wa\'lamuu annam<u>aa</u> ghanimtum min syay-in fa-anna lill<u>aa</u>hi khumusahu walirrasuuli wali<u>dz</u>ii lqurb<u>aa</u> walyat<u>aa</u>m<u>aa</u> walmas<u>aa</u>kiini wabni ssabiili in kuntum <u>aa</u>mantum bil<u>aa</u>hi wam<u>aa</u> anzaln<u>aa</u> \'al<u>aa</u> \'abdin<u>aa</u> yawma lfurq<u>aa</u>ni yawma iltaq<u>aa</u> ljam\'<u>aa</u>ni wal<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (1202, '8:42', 8, 42, 'i<u>dz</u> antum bil\'udwati dduny<u>aa</u> wahum bil\'udwati lqu<u>sh</u>w<u>aa</u> warrakbu asfala minkum walaw taw<u>aa</u>\'adtum lakhtalaftum fii lmii\'<u>aa</u>di wal<u>aa</u>kin liyaq<u>dh</u>iyall<u>aa</u>hu amran k<u>aa</u>na maf\'uulan liyahlika man halaka \'an bayyinatin waya<u>h</u>y<u>aa</u> man <u>h</u>ayya \'an bayyinatin wa-innall<u>aa</u>ha lasamii\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1203, '8:43', 8, 43, 'i<u>dz</u> yuriikahumull<u>aa</u>hu fii man<u>aa</u>mika qaliilan walaw ar<u>aa</u>kahum katsiiran lafasyiltum walatan<u>aa</u>za\'tum fii l-amri wal<u>aa</u>kinnall<u>aa</u>ha sallama innahu \'aliimun bi<u>dzaa</u>ti <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (1204, '8:44', 8, 44, 'wa-i<u>dz</u> yuriikumuuhum i<u>dz</u>i iltaqaytum fii a\'yunikum qaliilan wayuqallilukum fii a\'yunihim liyaq<u>dh</u>iyall<u>aa</u>hu amran k<u>aa</u>na maf\'uulan wa-il<u>aa</u>ll<u>aa</u>hi turja\'u l-umuur\r\r\n');
INSERT INTO quranLatin VALUES (1205, '8:45', 8, 45, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> laqiitum fi-atan fatsbutuu wa<u>dz</u>kuruull<u>aa</u>ha katsiiran la\'allakum tufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1206, '8:46', 8, 46, 'wa-a<u>th</u>ii\'uull<u>aa</u>ha warasuulahu wal<u>aa</u> tan<u>aa</u>za\'uu fatafsyaluu wata<u>dz</u>haba rii<u>h</u>ukum wa<u>sh</u>biruu innall<u>aa</u>ha ma\'a <u>shshaa</u>biriin\r\r\n');
INSERT INTO quranLatin VALUES (1207, '8:47', 8, 47, 'wal<u>aa</u> takuunuu kalla<u>dz</u>iina kharajuu min diy<u>aa</u>rihim ba<u>th</u>aran wari-<u>aa</u>-a nn<u>aa</u>si waya<u>sh</u>udduuna \'an sabiilill<u>aa</u>hi wal<u>aa</u>hu bim<u>aa</u> ya\'maluuna mu<u>h</u>ii<u>th</u>\r\r\n');
INSERT INTO quranLatin VALUES (1208, '8:48', 8, 48, 'wa-i<u>dz</u> zayyana lahumu sysyay<u>th</u><u>aa</u>nu a\'m<u>aa</u>lahum waq<u>aa</u>la l<u>aa</u> gh<u>aa</u>liba lakumu lyawma mina nn<u>aa</u>si wa-innii j<u>aa</u>run lakum falamm<u>aa</u> tar<u>aa</u>-ati lfi-at<u>aa</u>ni naka<u>sh</u>a \'al<u>aa</u> \'aqibayhi waq<u>aa</u>la innii barii-un minkum innii ar<u>aa</u> m<u>aa</u> l<u>aa</u> tarawna innii akh<u>aa</u>full<u>aa</u>ha wal<u>aa</u>hu syadiidu l\'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1209, '8:49', 8, 49, 'i<u>dz</u> yaquulu lmun<u>aa</u>fiquuna walla<u>dz</u>iina fii quluubihim mara<u>dh</u>un gharra h<u>aa</u>ul<u>aa</u>-i diinuhum waman yatawakkal \'al<u>aa</u>ll<u>aa</u>hi fa-innall<u>aa</u>ha \'aziizun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1210, '8:50', 8, 50, 'walaw tar<u>aa</u> i<u>dz</u> yatawaff<u>aa</u>lla<u>dz</u>iina kafaruu lmal<u>aa</u>-ikatu ya<u>dh</u>ribuuna wujuuhahum wa-adb<u>aa</u>rahum wa<u>dz</u>uuquu \'a<u>dzaa</u>ba l<u>h</u>ariiq\r\r\n');
INSERT INTO quranLatin VALUES (1211, '8:51', 8, 51, '<u>dzaa</u>lika bim<u>aa</u> qaddamat aydiikum wa-annall<u>aa</u>ha laysa bi<u>zh</u>all<u>aa</u>min lil\'abiid\r\r\n');
INSERT INTO quranLatin VALUES (1212, '8:52', 8, 52, 'kada/bi <u>aa</u>li fir\'awna walla<u>dz</u>iina min qablihim kafaruu bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi fa-akha<u>dz</u>ahumull<u>aa</u>hu bi<u>dz</u>unuubihim innall<u>aa</u>ha qawiyyun syadiidu l\'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1213, '8:53', 8, 53, '<u>dzaa</u>lika bi-annall<u>aa</u>ha lam yaku mughayyiran ni\'matan an\'amah<u>aa</u> \'al<u>aa</u> qawmin <u>h</u>att<u>aa</u> yughayyiruu m<u>aa</u> bi-anfusihim wa-annall<u>aa</u>ha samii\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1214, '8:54', 8, 54, 'kada/bi <u>aa</u>li fir\'awna walla<u>dz</u>iina min qablihim ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>ti rabbihim fa-ahlakn<u>aa</u>hum bi<u>dz</u>unuubihim wa-aghraqn<u>aa</u> <u>aa</u>la fir\'awna wakullun k<u>aa</u>nuu <u>zhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1215, '8:55', 8, 55, 'inna syarra ddaw<u>aa</u>bbi \'indall<u>aa</u>hilla<u>dz</u>iina kafaruu fahum l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1216, '8:56', 8, 56, '<B>al</b>la<u>dz</u>iina \'<u>aa</u>hadta minhum tsumma yanqu<u>dh</u>uuna \'ahdahum fii kulli marratin wahum l<u>aa</u> yattaquun\r\r\n');
INSERT INTO quranLatin VALUES (1217, '8:57', 8, 57, 'fa-imm<u>aa</u> tatsqafannahum fii l<u>h</u>arbi fasyarrid bihim man khalfahum la\'allahum ya<u>dzdz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1218, '8:58', 8, 58, 'wa-imm<u>aa</u> takh<u>aa</u>fanna min qawmin khiy<u>aa</u>natan fanbi<u>dz</u> ilayhim \'al<u>aa</u> sawa-in innall<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu lkh<u>aa</u>-iniin\r\r\n');
INSERT INTO quranLatin VALUES (1219, '8:59', 8, 59, 'wal<u>aa</u> ya<u>h</u>sabannalla<u>dz</u>iina kafaruu sabaquu innahum l<u>aa</u> yu\'jizuun\r\r\n');
INSERT INTO quranLatin VALUES (1220, '8:60', 8, 60, 'wa-a\'idduu lahum m<u>aa</u> ista<u>th</u>a\'tum min quwwatin wamin rib<u>aath</u>i lkhayli turhibuuna bihi \'aduwwall<u>aa</u>hi wa\'aduwwakum wa<u>aa</u>khariina min duunihim l<u>aa</u> ta\'lamuunahumull<u>aa</u>hu ya\'lamuhum wam<u>aa</u> tunfiquu min syay-in fii sabiilill<u>aa</u>hi yuwaffa ilaykum wa-antum l<u>aa</u> tu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1221, '8:61', 8, 61, 'wa-in jana<u>h</u>uu lissalmi fajna<u>h</u> lah<u>aa</u> watawakkal \'al<u>aa</u>ll<u>aa</u>hi innahu huwa ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1222, '8:62', 8, 62, 'wa-in yuriiduu an yakhda\'uuka fa-inna <u>h</u>asbakall<u>aa</u>hu huwalla<u>dz</u>ii ayyadaka bina<u>sh</u>rihi wabilmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1223, '8:63', 8, 63, 'wa-allafa bayna quluubihim law anfaqta m<u>aa</u> fii l-ar<u>dh</u>i jamii\'an m<u>aa</u>llafta bayna quluubihim wal<u>aa</u>kinnall<u>aa</u>hallafa baynahum innahu \'aziizun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1224, '8:64', 8, 64, 'y<u>aa</u> ayyuh<u>aa</u> nnabiyyu <u>h</u>asbukall<u>aa</u>hu wamani ittaba\'aka mina lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1225, '8:65', 8, 65, 'y<u>aa</u> ayyuh<u>aa</u> nnabiyyu <u>h</u>arri<u>dh</u>i lmu/miniina \'al<u>aa</u> lqit<u>aa</u>li in yakun minkum \'isyruuna <u>shaa</u>biruuna yaghlibuu mi-atayni wa-in yakun minkum mi-atun yaghlibuu lfan minalla<u>dz</u>iina kafaruu bi-annahum qawmun l<u>aa</u> yafqahuun\r\r\n');
INSERT INTO quranLatin VALUES (1226, '8:66', 8, 66, 'al-<u>aa</u>na khaffafall<u>aa</u>hu \'ankum wa\'alima anna fiikum <u>dh</u>a\'fan fa-in yakun minkum mi-atun <u>shaa</u>biratun yaghlibuu mi-atayni wa-in yakun minkum lfun yaghlibuu lfayni bi-i<u>dz</u>nill<u>aa</u>hi wal<u>aa</u>hu ma\'a <u>shshaa</u>biriin\r\r\n');
INSERT INTO quranLatin VALUES (1227, '8:67', 8, 67, 'm<u>aa</u> k<u>aa</u>na linabiyyin an yakuuna lahu asr<u>aa</u> <u>h</u>att<u>aa</u> yutskhina fii l-ar<u>dh</u>i turiiduuna \'ara<u>dh</u>a dduny<u>aa</u> wal<u>aa</u>hu yuriidu l-<u>aa</u>khirata wal<u>aa</u>hu \'aziizun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1228, '8:68', 8, 68, 'lawl<u>aa</u> kit<u>aa</u>bun minall<u>aa</u>hi sabaqa lamassakum fiim<u>aa</u> akha<u>dz</u>tum \'a<u>dzaa</u>bun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1229, '8:69', 8, 69, 'fakuluu mimm<u>aa</u> ghanimtum <u>h</u>al<u>aa</u>lan <u>th</u>ayyiban wattaquull<u>aa</u>ha innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1230, '8:70', 8, 70, 'y<u>aa</u> ayyuh<u>aa</u> nnabiyyu qul liman fii aydiikum mina l-asr<u>aa</u> in ya\'lamill<u>aa</u>hu fii quluubikum khayran yu/tikum khayran mimm<u>aa</u> ukhi<u>dz</u>a minkum wayaghfir lakum wal<u>aa</u>hu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1231, '8:71', 8, 71, 'wa-in yuriiduu khiy<u>aa</u>nataka faqad kh<u>aa</u>nuull<u>aa</u>ha min qablu fa-amkana minhum wal<u>aa</u>hu \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1232, '8:72', 8, 72, 'innalla<u>dz</u>iina <u>aa</u>manuu wah<u>aa</u>jaruu waj<u>aa</u>haduu bi-amw<u>aa</u>lihim wa-anfusihim fii sabiilill<u>aa</u>hi walla<u>dz</u>iina <u>aa</u>waw wana<u>sh</u>aruu ul<u>aa</u>-ika ba\'<u>dh</u>uhum awliy<u>aa</u>u ba\'<u>dh</u>in walla<u>dz</u>iina <u>aa</u>manuu walam yuh<u>aa</u>jiruu m<u>aa</u> lakum min wal<u>aa</u>yatihim min syay-in <u>h</u>att<u>aa</u> yuh<u>aa</u>jiruu wa-ini istan<u>sh</u>aruukum fii ddiini fa\'alaykumu nna<u>sh</u>ru ill<u>aa</u> \'al<u>aa</u> qawmin baynakum wabaynahum miits<u>aa</u>qun wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (1233, '8:73', 8, 73, 'walla<u>dz</u>iina kafaruu ba\'<u>dh</u>uhum awliy<u>aa</u>u ba\'<u>dh</u>in ill<u>aa</u> taf\'aluuhu takun fitnatun fii l-ar<u>dh</u>i wafas<u>aa</u>dun kabiir\r\r\n');
INSERT INTO quranLatin VALUES (1234, '8:74', 8, 74, 'walla<u>dz</u>iina <u>aa</u>manuu wah<u>aa</u>jaruu waj<u>aa</u>haduu fii sabiilill<u>aa</u>hi walla<u>dz</u>iina <u>aa</u>waw wana<u>sh</u>aruu ul<u>aa</u>-ika humu lmu/minuuna <u>h</u>aqqan lahum maghfiratun warizqun kariim\r\r\n');
INSERT INTO quranLatin VALUES (1235, '8:75', 8, 75, 'walla<u>dz</u>iina <u>aa</u>manuu min ba\'du wah<u>aa</u>jaruu waj<u>aa</u>haduu ma\'akum faul<u>aa</u>-ika minkum wauluu l-ar<u>haa</u>mi ba\'<u>dh</u>uhum awl<u>aa</u> biba\'<u>dh</u>in fii kit<u>aa</u>bill<u>aa</u>hi innall<u>aa</u>ha bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1236, '9:1', 9, 1, 'bar<u>aa</u>-atun minall<u>aa</u>hi warasuulihi il<u>aa</u>lla<u>dz</u>iina \'<u>aa</u>hadtum mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (1237, '9:2', 9, 2, 'fasii<u>h</u>uu fii l-ar<u>dh</u>i arba\'ata asyhurin wa\'lamuu annakum ghayru mu\'jiziill<u>aa</u>hi wa-annall<u>aa</u>ha mukhzii lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1238, '9:3', 9, 3, 'wa-a<u>dzaa</u>nun minall<u>aa</u>hi warasuulihi il<u>aa</u> nn<u>aa</u>si yawma l<u>h</u>ajji l-akbari annall<u>aa</u>ha barii-un mina lmusyrikiina warasuuluhu fa-in tubtum fahuwa khayrun lakum wa-in tawallaytum fa\'lamuu annakum ghayru mu\'jiziill<u>aa</u>hi wabasysyirilla<u>dz</u>iina kafaruu bi\'a<u>dzaa</u>bin liim\r\r\n');
INSERT INTO quranLatin VALUES (1239, '9:4', 9, 4, 'ill<u>aa</u>lla<u>dz</u>iina \'<u>aa</u>hadtum mina lmusyrikiina tsumma lam yanqu<u>sh</u>uukum syay-an walam yu<u>zhaa</u>hiruu \'alaykum a<u>h</u>adan fa-atimmuu ilayhim \'ahdahum il<u>aa</u> muddatihim innall<u>aa</u>ha yu<u>h</u>ibbu lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (1240, '9:5', 9, 5, 'fa-i<u>dzaa</u> insalakha l-asyhuru l<u>h</u>urumu faqtuluu lmusyrikiina <u>h</u>aytsu wajadtumuuhum wakhu<u>dz</u>uuhum wa<u>hs</u>uruuhum waq\'uduu lahum kulla mar<u>sh</u>adin fa-in t<u>aa</u>buu wa-aq<u>aa</u>muu <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tawuu zzak<u>aa</u>ta fakhalluu sabiilahum innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1241, '9:6', 9, 6, 'wa-in a<u>h</u>adun mina lmusyrikiina istaj<u>aa</u>raka fa-ajirhu <u>h</u>att<u>aa</u> yasma\'a kal<u>aa</u>mall<u>aa</u>hi tsumma ablighhu ma/manahu <u>dzaa</u>lika bi-annahum qawmun l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1242, '9:7', 9, 7, 'kayfa yakuunu lilmusyrikiina \'ahdun \'indall<u>aa</u>hi wa\'inda rasuulihi ill<u>aa</u>lla<u>dz</u>iina \'<u>aa</u>hadtum \'inda lmasjidi l<u>h</u>ar<u>aa</u>mi fam<u>aa</u> istaq<u>aa</u>muu lakum fastaqiimuu lahum innall<u>aa</u>ha yu<u>h</u>ibbu lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (1243, '9:8', 9, 8, 'kayfa wa-in ya<u>zh</u>haruu \'alaykum l<u>aa</u> yarqubuu fiikum illan wal<u>aa</u> <u>dz</u>immatan yur<u>dh</u>uunakum bi-afw<u>aa</u>hihim wata/b<u>aa</u> quluubuhum wa-aktsaruhum f<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (1244, '9:9', 9, 9, 'isytaraw bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi tsamanan qaliilan fa<u>sh</u>adduu \'an sabiilihi innahum s<u>aa</u>-a m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1245, '9:10', 9, 10, 'l<u>aa</u> yarqubuuna fii mu/minin illan wal<u>aa</u> <u>dz</u>immatan waul<u>aa</u>-ika humu lmu\'taduun\r\r\n');
INSERT INTO quranLatin VALUES (1246, '9:11', 9, 11, 'fa-in t<u>aa</u>buu wa-aq<u>aa</u>muu <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tawuu zzak<u>aa</u>ta fa-ikhw<u>aa</u>nukum fii ddiini wanufa<u>shsh</u>ilu l-<u>aa</u>y<u>aa</u>ti liqawmin ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1247, '9:12', 9, 12, 'wa-in nakatsuu aym<u>aa</u>nahum min ba\'di \'ahdihim wa<u>th</u>a\'anuu fii diinikum faq<u>aa</u>tiluu a-immata lkufri innahum l<u>aa</u> aym<u>aa</u>na lahum la\'allahum yantahuun\r\r\n');
INSERT INTO quranLatin VALUES (1248, '9:13', 9, 13, 'al<u>aa</u> tuq<u>aa</u>tiluuna qawman nakatsuu aym<u>aa</u>nahum wahammuu bi-ikhr<u>aa</u>ji rrasuuli wahum badauukum awwala marratin atakhsyawnahum fal<u>aa</u>hu a<u>h</u>aqqu an takhsyawhu in kuntum mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1249, '9:14', 9, 14, 'q<u>aa</u>tiluuhum yu\'a<u>dzdz</u>ibhumull<u>aa</u>hu bi-aydiikum wayukhzihim wayan<u>sh</u>urkum \'alayhim wayasyfi <u>sh</u>uduura qawmin mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1250, '9:15', 9, 15, 'wayu<u>dz</u>hib ghay<u>zh</u>a quluubihim wayatuubull<u>aa</u>hu \'al<u>aa</u> man yasy<u>aa</u>u wal<u>aa</u>hu \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1251, '9:16', 9, 16, 'am <u>h</u>asibtum an tutrakuu walamm<u>aa</u> ya\'lamill<u>aa</u>hulla<u>dz</u>iina j<u>aa</u>haduu minkum walam yattakhi<u>dz</u>uu min duunill<u>aa</u>hi wal<u>aa</u> rasuulihi wal<u>aa</u> lmu/miniina waliijatan wal<u>aa</u>hu khabiirun bim<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1252, '9:17', 9, 17, 'm<u>aa</u> k<u>aa</u>na lilmusyrikiina an ya\'muruu mas<u>aa</u>jidall<u>aa</u>hi sy<u>aa</u>hidiina \'al<u>aa</u> anfusihim bilkufri ul<u>aa</u>-ika <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum wafii nn<u>aa</u>ri hum kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (1253, '9:18', 9, 18, 'innam<u>aa</u> ya\'muru mas<u>aa</u>jidall<u>aa</u>hi man <u>aa</u>mana bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri wa-aq<u>aa</u>ma <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>t<u>aa</u> zzak<u>aa</u>ta walam yakhsya ill<u>aa</u>ll<u>aa</u>ha fa\'as<u>aa</u> ul<u>aa</u>-ika an yakuunuu mina lmuhtadiin\r\r\n');
INSERT INTO quranLatin VALUES (1254, '9:19', 9, 19, 'aja\'altum siq<u>aa</u>yata l<u>haa</u>jji wa\'im<u>aa</u>rata lmasjidi l<u>h</u>ar<u>aa</u>mi kaman <u>aa</u>mana bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri waj<u>aa</u>hada fii sabiilill<u>aa</u>hi l<u>aa</u> yastawuuna \'indall<u>aa</u>hi wal<u>aa</u>hu l<u>aa</u> yahdii lqawma <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1255, '9:20', 9, 20, '<B>al</b>la<u>dz</u>iina <u>aa</u>manuu wah<u>aa</u>jaruu waj<u>aa</u>haduu fii sabiilill<u>aa</u>hi bi-amw<u>aa</u>lihim wa-anfusihim a\'<u>zh</u>amu darajatan \'indall<u>aa</u>hi waul<u>aa</u>-ika humu lf<u>aa</u>-izuun\r\r\n');
INSERT INTO quranLatin VALUES (1256, '9:21', 9, 21, 'yubasysyiruhum rabbuhum bira<u>h</u>matin minhu wari<u>dh</u>w<u>aa</u>nin wajann<u>aa</u>tin lahum fiih<u>aa</u> na\'iimun muqiim\r\r\n');
INSERT INTO quranLatin VALUES (1257, '9:22', 9, 22, 'kh<u>aa</u>lidiina fiih<u>aa</u> abadan innall<u>aa</u>ha \'indahu ajrun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1258, '9:23', 9, 23, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattakhi<u>dz</u>uu <u>aa</u>b<u>aa</u>-akum wa-ikhw<u>aa</u>nakum awliy<u>aa</u>-a ini ista<u>h</u>abbuu lkufra \'al<u>aa</u> l-iim<u>aa</u>ni waman yatawallahum minkum faul<u>aa</u>-ika humu <u>zhzhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (1259, '9:24', 9, 24, 'qul in k<u>aa</u>na <u>aa</u>b<u>aa</u>ukum wa-abn<u>aa</u>ukum wa-ikhw<u>aa</u>nukum wa-azw<u>aa</u>jukum wa\'asyiiratukum wa-amw<u>aa</u>lun iqtaraftumuuh<u>aa</u> watij<u>aa</u>ratun takhsyawna kas<u>aa</u>dah<u>aa</u> wamas<u>aa</u>kinu tar<u>dh</u>awnah<u>aa</u> a<u>h</u>abba ilaykum minall<u>aa</u>hi warasuulihi wajih<u>aa</u>din fii sabiilihi fatarabba<u>sh</u>uu <u>h</u>att<u>aa</u> ya/tiyall<u>aa</u>hu bi-amrihi wal<u>aa</u>hu l<u>aa</u> yahdii lqawma lf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (1260, '9:25', 9, 25, 'laqad na<u>sh</u>arakumull<u>aa</u>hu fii maw<u>aath</u>ina katsiiratin wayawma <u>h</u>unaynin i<u>dz</u> a\'jabatkum katsratukum falam tughni \'ankum syay-an wa<u>daa</u>qat \'alaykumu l-ar<u>dh</u>u bim<u>aa</u> ra<u>h</u>ubat tsumma wallaytum mudbiriin\r\r\n');
INSERT INTO quranLatin VALUES (1261, '9:26', 9, 26, 'tsumma anzalall<u>aa</u>hu sakiinatahu \'al<u>aa</u> rasuulihi wa\'al<u>aa</u> lmu/miniina wa-anzala junuudan lam tarawh<u>aa</u> wa\'a<u>dzdz</u>aballa<u>dz</u>iina kafaruu wa<u>dzaa</u>lika jaz<u>aa</u>u lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1262, '9:27', 9, 27, 'tsumma yatuubull<u>aa</u>hu min ba\'di <u>dzaa</u>lika \'al<u>aa</u> man yasy<u>aa</u>u wal<u>aa</u>hu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1263, '9:28', 9, 28, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu innam<u>aa</u> lmusyrikuuna najasun fal<u>aa</u> yaqrabuu lmasjida l<u>h</u>ar<u>aa</u>ma ba\'da \'<u>aa</u>mihim h<u>aadzaa</u> wa-in khiftum \'aylatan fasawfa yughniikumull<u>aa</u>hu min fa<u>dh</u>lihi in sy<u>aa</u>-a innall<u>aa</u>ha \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1264, '9:29', 9, 29, 'q<u>aa</u>tiluulla<u>dz</u>iina l<u>aa</u> yu/minuuna bil<u>aa</u>hi wal<u>aa</u> bilyawmi l-<u>aa</u>khiri wal<u>aa</u> yu<u>h</u>arrimuuna m<u>aa</u> <u>h</u>arramall<u>aa</u>hu warasuuluhu wal<u>aa</u> yadiinuuna diina l<u>h</u>aqqi minalla<u>dz</u>iina uutuu lkit<u>aa</u>ba <u>h</u>att<u>aa</u> yu\'<u>th</u>uu ljizyata \'an yadin wahum <u>shaa</u>ghiruun\r\r\n');
INSERT INTO quranLatin VALUES (1265, '9:30', 9, 30, 'waq<u>aa</u>lati lyahuudu \'uzayrun ibnull<u>aa</u>hi waq<u>aa</u>lati nna<u>shaa</u>r<u>aa</u> lmasii<u>h</u>u ibnull<u>aa</u>hi <u>dzaa</u>lika qawluhum bi-afw<u>aa</u>hihim yu<u>daa</u>hi-uuna qawlalla<u>dz</u>iina kafaruu min qablu q<u>aa</u>talahumull<u>aa</u>hu ann<u>aa</u> yu/fakuun\r\r\n');
INSERT INTO quranLatin VALUES (1266, '9:31', 9, 31, 'ittakha<u>dz</u>uu a<u>h</u>b<u>aa</u>rahum waruhb<u>aa</u>nahum arb<u>aa</u>ban min duunill<u>aa</u>hi walmasii<u>h</u>a ibna maryama wam<u>aa</u> umiruu ill<u>aa</u> liya\'buduu il<u>aa</u>han w<u>aah</u>idan l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa sub<u>haa</u>nahu \'amm<u>aa</u> yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (1267, '9:32', 9, 32, 'yuriiduuna an yu<u>th</u>fi-uu nuurall<u>aa</u>hi bi-afw<u>aa</u>hihim waya/b<u>aa</u>ll<u>aa</u>hu ill<u>aa</u> an yutimma nuurahu walaw kariha lk<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (1268, '9:33', 9, 33, 'huwalla<u>dz</u>ii arsala rasuulahu bilhud<u>aa</u> wadiini l<u>h</u>aqqi liyu<u>zh</u>hirahu \'al<u>aa</u> ddiini kullihi walaw kariha lmusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (1269, '9:34', 9, 34, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu inna katsiiran mina l-a<u>h</u>b<u>aa</u>ri warruhb<u>aa</u>ni laya/kuluuna amw<u>aa</u>la nn<u>aa</u>si bilb<u>aath</u>ili waya<u>sh</u>udduuna \'an sabiilill<u>aa</u>hi walla<u>dz</u>iina yaknizuuna <u>dzdz</u>ahaba walfi<u>dhdh</u>ata wal<u>aa</u> yunfiquunah<u>aa</u> fii sabiilill<u>aa</u>hi fabasysyirhum bi\'a<u>dzaa</u>bin liim\r\r\n');
INSERT INTO quranLatin VALUES (1270, '9:35', 9, 35, 'yawma yu<u>h</u>m<u>aa</u> \'alayh<u>aa</u> fii n<u>aa</u>ri jahannama fatukw<u>aa</u> bih<u>aa</u> jib<u>aa</u>huhum wajunuubuhum wa<u>zh</u>uhuuruhum h<u>aadzaa</u> m<u>aa</u> kanaztum li-anfusikum fa<u>dz</u>uuquu m<u>aa</u> kuntum taknizuun\r\r\n');
INSERT INTO quranLatin VALUES (1271, '9:36', 9, 36, 'inna \'iddata sysyuhuuri \'indall<u>aa</u>hi itsn<u>aa</u> \'asyara syahran fii kit<u>aa</u>bill<u>aa</u>hi yawma khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a minh<u>aa</u> arba\'atun <u>h</u>urumun <u>dzaa</u>lika ddiinu lqayyimu fal<u>aa</u> ta<u>zh</u>limuu fiihinna anfusakum waq<u>aa</u>tiluu lmusyrikiina k<u>aa</u>ffatan kam<u>aa</u> yuq<u>aa</u>tiluunakum k<u>aa</u>ffatan wa\'lamuu annall<u>aa</u>ha ma\'a lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (1272, '9:37', 9, 37, 'innam<u>aa</u> nnasii-u ziy<u>aa</u>datun fii lkufri yu<u>dh</u>allu bihilla<u>dz</u>iina kafaruu yu<u>h</u>illuunahu \'<u>aa</u>man wayu<u>h</u>arrimuunahu \'<u>aa</u>man liyuw<u>aath</u>i-uu \'iddata m<u>aa</u> <u>h</u>arramall<u>aa</u>hu fayu<u>h</u>illuu m<u>aa</u> <u>h</u>arramall<u>aa</u>hu zuyyina lahum suu-u a\'m<u>aa</u>lihim wal<u>aa</u>hu l<u>aa</u> yahdii lqawma lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1273, '9:38', 9, 38, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu m<u>aa</u> lakum i<u>dzaa</u> qiila lakumu infiruu fii sabiilill<u>aa</u>hi itsts<u>aa</u>qaltum il<u>aa</u> l-ar<u>dh</u>i ara<u>dh</u>iitum bil<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> mina l-<u>aa</u>khirati fam<u>aa</u> mat<u>aa</u>\'u l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> fii l-<u>aa</u>khirati ill<u>aa</u> qaliil\r\r\n');
INSERT INTO quranLatin VALUES (1274, '9:39', 9, 39, 'ill<u>aa</u> tanfiruu yu\'a<u>dzdz</u>ibkum \'a<u>dzaa</u>ban liiman wayastabdil qawman ghayrakum wal<u>aa</u> ta<u>dh</u>urruuhu syay-an wal<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (1275, '9:40', 9, 40, 'ill<u>aa</u> tan<u>sh</u>uruuhu faqad na<u>sh</u>arahull<u>aa</u>hu i<u>dz</u> akhrajahulla<u>dz</u>iina kafaruu ts<u>aa</u>niya itsnayni i<u>dz</u> hum<u>aa</u> fii lgh<u>aa</u>ri i<u>dz</u> yaquulu li<u>shaah</u>ibihi l<u>aa</u> ta<u>h</u>zan innall<u>aa</u>ha ma\'an<u>aa</u> fa-anzalall<u>aa</u>hu sakiinatahu \'alayhi wa-ayyadahu bijunuudin lam tarawh<u>aa</u> waja\'ala kalimatalla<u>dz</u>iina kafaruu ssufl<u>aa</u> wakalimatull<u>aa</u>hi hiya l\'uly<u>aa</u> wal<u>aa</u>hu \'aziizun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1276, '9:41', 9, 41, 'infiruu khif<u>aa</u>fan watsiq<u>aa</u>lan waj<u>aa</u>hiduu bi-amw<u>aa</u>likum wa-anfusikum fii sabiilill<u>aa</u>hi <u>dzaa</u>likum khayrun lakum in kuntum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1277, '9:42', 9, 42, 'law k<u>aa</u>na \'ara<u>dh</u>an qariiban wasafaran q<u>aas</u>idan lattaba\'uuka wal<u>aa</u>kin ba\'udat \'alayhimu sysyuqqatu wasaya<u>h</u>lifuuna bil<u>aa</u>hi lawi ista<u>th</u>a\'n<u>aa</u> lakharajn<u>aa</u> ma\'akum yuhlikuuna anfusahum wal<u>aa</u>hu ya\'lamu innahum lak<u>aadz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (1278, '9:43', 9, 43, '\'af<u>aa</u>ll<u>aa</u>hu \'anka lima a<u>dz</u>inta lahum <u>h</u>att<u>aa</u> yatabayyana lakalla<u>dz</u>iina <u>sh</u>adaquu wata\'lama lk<u>aadz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (1279, '9:44', 9, 44, 'l<u>aa</u> yasta/<u>dz</u>inukalla<u>dz</u>iina yu/minuuna bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri an yuj<u>aa</u>hiduu bi-amw<u>aa</u>lihim wa-anfusihim wal<u>aa</u>hu \'aliimun bilmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (1280, '9:45', 9, 45, 'innam<u>aa</u> yasta/<u>dz</u>inukalla<u>dz</u>iina l<u>aa</u> yu/minuuna bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri wart<u>aa</u>bat quluubuhum fahum fii raybihim yataraddaduun\r\r\n');
INSERT INTO quranLatin VALUES (1281, '9:46', 9, 46, 'walaw ar<u>aa</u>duu lkhuruuja la-a\'adduu lahu \'uddatan wal<u>aa</u>kin karihall<u>aa</u>hu inbi\'<u>aa</u>tsahum fatsabba<u>th</u>ahum waqiila uq\'uduu ma\'a lq<u>aa</u>\'idiin\r\r\n');
INSERT INTO quranLatin VALUES (1282, '9:47', 9, 47, 'law kharajuu fiikum m<u>aa</u> z<u>aa</u>duukum ill<u>aa</u> khab<u>aa</u>lan wala-aw<u>dh</u>a\'uu khil<u>aa</u>lakum yabghuunakumu lfitnata wafiikum samm<u>aa</u>\'uuna lahum wal<u>aa</u>hu \'aliimun bi<u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1283, '9:48', 9, 48, 'laqadi ibtaghawuu lfitnata min qablu waqallabuu laka l-umuura <u>h</u>att<u>aa</u> j<u>aa</u>-a l<u>h</u>aqqu wa<u>zh</u>ahara amrull<u>aa</u>hi wahum k<u>aa</u>rihuun\r\r\n');
INSERT INTO quranLatin VALUES (1284, '9:49', 9, 49, 'waminhum man yaquulu i/<u>dz</u>an lii wal<u>aa</u> taftinnii l<u>aa</u> fii lfitnati saqa<u>th</u>uu wa-inna jahannama lamu<u>h</u>ii<u>th</u>atun bilk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1285, '9:50', 9, 50, 'in tu<u>sh</u>ibka <u>h</u>asanatun tasu/hum wa-in tu<u>sh</u>ibka mu<u>sh</u>iibatun yaquuluu qad akha<u>dz</u>n<u>aa</u> amran<u>aa</u> min qablu wayatawallaw wahum fari<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1286, '9:51', 9, 51, 'qul lan yu<u>sh</u>iiban<u>aa</u> ill<u>aa</u> m<u>aa</u> kataball<u>aa</u>hu lan<u>aa</u> huwa mawl<u>aa</u>n<u>aa</u> wa\'al<u>aa</u>ll<u>aa</u>hi falyatawakkali lmu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1287, '9:52', 9, 52, 'qul hal tarabba<u>sh</u>uuna bin<u>aa</u> ill<u>aa</u> i<u>h</u>d<u>aa</u> l<u>h</u>usnayayni wana<u>h</u>nu natarabba<u>sh</u>u bikum an yu<u>sh</u>iibakumull<u>aa</u>hu bi\'a<u>dzaa</u>bin min \'indihi aw bi-aydiin<u>aa</u> fatarabba<u>sh</u>uu inn<u>aa</u> ma\'akum mutarabbi<u>sh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1288, '9:53', 9, 53, 'qul anfiquu <u>th</u>aw\'an aw karhan lan yutaqabbala minkum innakum kuntum qawman f<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (1289, '9:54', 9, 54, 'wam<u>aa</u> mana\'ahum an tuqbala minhum nafaq<u>aa</u>tuhum ill<u>aa</u> annahum kafaruu bil<u>aa</u>hi wabirasuulihi wal<u>aa</u> ya/tuuna <u>shsh</u>al<u>aa</u>ta ill<u>aa</u> wahum kus<u>aa</u>l<u>aa</u> wal<u>aa</u> yunfiquuna ill<u>aa</u> wahum k<u>aa</u>rihuun\r\r\n');
INSERT INTO quranLatin VALUES (1290, '9:55', 9, 55, 'fal<u>aa</u> tu\'jibka amw<u>aa</u>luhum wal<u>aa</u> awl<u>aa</u>duhum innam<u>aa</u> yuriidull<u>aa</u>hu liyu\'a<u>dzdz</u>ibahum bih<u>aa</u> fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> watazhaqa anfusuhum wahum k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (1291, '9:56', 9, 56, 'waya<u>h</u>lifuuna bil<u>aa</u>hi innahum laminkum wam<u>aa</u> hum minkum wal<u>aa</u>kinnahum qawmun yafraquun\r\r\n');
INSERT INTO quranLatin VALUES (1292, '9:57', 9, 57, 'law yajiduuna malja-an aw magh<u>aa</u>r<u>aa</u>tin aw muddakhalan lawallaw ilayhi wahum yajma<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1293, '9:58', 9, 58, 'waminhum man yalmizuka fii <u>shsh</u>adaq<u>aa</u>ti fa-in u\'<u>th</u>uu minh<u>aa</u> ra<u>dh</u>uu wa-in lam yu\'<u>th</u>aw minh<u>aa</u> i<u>dzaa</u> hum yaskha<u>th</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1294, '9:59', 9, 59, 'walaw annahum ra<u>dh</u>uu m<u>aa</u> <u>aa</u>t<u>aa</u>humull<u>aa</u>hu warasuuluhu waq<u>aa</u>luu <u>h</u>asbun<u>aa</u>ll<u>aa</u>hu sayu/tiin<u>aa</u>ll<u>aa</u>hu min fa<u>dh</u>lihi warasuuluhu inn<u>aa</u> il<u>aa</u>ll<u>aa</u>hi r<u>aa</u>ghibuun\r\r\n');
INSERT INTO quranLatin VALUES (1295, '9:60', 9, 60, 'innam<u>aa</u> <u>shsh</u>adaq<u>aa</u>tu lilfuqar<u>aa</u>-i walmas<u>aa</u>kiini wal\'<u>aa</u>miliina \'alayh<u>aa</u> walmu-allafati quluubuhum wafii rriq<u>aa</u>bi walgh<u>aa</u>rimiina wafii sabiilill<u>aa</u>hi wabni ssabiili farii<u>dh</u>atan minall<u>aa</u>hi wal<u>aa</u>hu \'aliimun <u>h</u>akiimun</u>\r\r\n');
INSERT INTO quranLatin VALUES (1296, '9:61', 9, 61, 'waminhumulla<u>dz</u>iina yu/<u>dz</u>uuna nnabiyya wayaquuluuna huwa u<u>dz</u>unun qul u<u>dz</u>unu khayrin lakum yu/minu bil<u>aa</u>hi wayu/minu lilmu/miniina wara<u>h</u>matun lilla<u>dz</u>iina <u>aa</u>manuu minkum walla<u>dz</u>iina yu/<u>dz</u>uuna rasuulall<u>aa</u>hi lahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (1297, '9:62', 9, 62, 'ya<u>h</u>lifuuna bil<u>aa</u>hi lakum liyur<u>dh</u>uukum wal<u>aa</u>hu warasuuluhu a<u>h</u>aqqu an yur<u>dh</u>uuhu in k<u>aa</u>nuu mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1298, '9:63', 9, 63, 'alam ya\'lamuu annahu man yu<u>haa</u>didill<u>aa</u>ha warasuulahu fa-anna lahu n<u>aa</u>ra jahannama kh<u>aa</u>lidan fiih<u>aa</u> <u>dzaa</u>lika lkhizyu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1299, '9:64', 9, 64, 'ya<u>hts</u>aru lmun<u>aa</u>fiquuna an tunazzala \'alayhim suuratun tunabbi-uhum bim<u>aa</u> fii quluubihim quli istahzi-uu innall<u>aa</u>ha mukhrijun m<u>aa</u> ta<u>hts</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (1300, '9:65', 9, 65, 'wala-in sa-altahum layaquulunna innam<u>aa</u> kunn<u>aa</u> nakhuu<u>dh</u>u wanal\'abu qul abil<u>aa</u>hi wa<u>aa</u>y<u>aa</u>tihi warasuulihi kuntum tastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (1301, '9:66', 9, 66, 'l<u>aa</u> ta\'ta<u>dz</u>iruu qad kafartum ba\'da iim<u>aa</u>nikum in na\'fu \'an <u>th</u><u>aa</u>-ifatin minkum nu\'a<u>dzdz</u>ib <u>th</u><u>aa</u>-ifatan bi-annahum k<u>aa</u>nuu mujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (1302, '9:67', 9, 67, 'almun<u>aa</u>fiquuna walmun<u>aa</u>fiq<u>aa</u>tu ba\'<u>dh</u>uhum min ba\'<u>dh</u>in ya/muruuna bilmunkari wayanhawna \'ani lma\'ruufi wayaqbi<u>dh</u>uuna aydiyahum nasuull<u>aa</u>ha fanasiyahum inna lmun<u>aa</u>fiqiina humu lf<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (1303, '9:68', 9, 68, 'wa\'adall<u>aa</u>hu lmun<u>aa</u>fiqiina walmun<u>aa</u>fiq<u>aa</u>ti walkuff<u>aa</u>ra n<u>aa</u>ra jahannama kh<u>aa</u>lidiina fiih<u>aa</u> hiya <u>h</u>asbuhum wala\'anahumull<u>aa</u>hu walahum \'a<u>dzaa</u>bun muqiim\r\r\n');
INSERT INTO quranLatin VALUES (1304, '9:69', 9, 69, 'kalla<u>dz</u>iina min qablikum k<u>aa</u>nuu asyadda minkum quwwatan wa-aktsara amw<u>aa</u>lan wa-awl<u>aa</u>dan fastamta\'uu bikhal<u>aa</u>qihim fastamta\'tum bikhal<u>aa</u>qikum kam<u>aa</u> istamta\'alla<u>dz</u>iina min qablikum bikhal<u>aa</u>qihim wakhu<u>dh</u>tum kalla<u>dz</u>ii kh<u>aad</u>uu ul<u>aa</u>-ika <u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum fii dduny<u>aa</u> wal-<u>aa</u>khirati waul<u>aa</u>-ika humu lkh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (1305, '9:70', 9, 70, 'alam ya/tihim nabaulla<u>dz</u>iina min qablihim qawmi nuu<u>h</u>in wa\'<u>aa</u>din watsamuuda waqawmi ibr<u>aa</u>hiima wa-a<u>sh</u>-<u>haa</u>bi madyana walmu/tafik<u>aa</u>ti atat-hum rusuluhum bilbayyin<u>aa</u>ti fam<u>aa</u> k<u>aa</u>nall<u>aa</u>hu liya<u>zh</u>limahum wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (1306, '9:71', 9, 71, 'walmu/minuuna walmu/min<u>aa</u>tu ba\'<u>dh</u>uhum awliy<u>aa</u>u ba\'<u>dh</u>in ya/muruuna bilma\'ruufi wayanhawna \'ani lmunkari wayuqiimuuna <u>shsh</u>al<u>aa</u>ta wayu/tuuna zzak<u>aa</u>ta wayu<u>th</u>ii\'uunall<u>aa</u>ha warasuulahu ul<u>aa</u>-ika sayar<u>h</u>amuhumull<u>aa</u>hu innall<u>aa</u>ha \'aziizun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1307, '9:72', 9, 72, 'wa\'adall<u>aa</u>hu lmu/miniina walmu/min<u>aa</u>ti jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wamas<u>aa</u>kina <u>th</u>ayyibatan fii jann<u>aa</u>ti \'adnin wari<u>dh</u>w<u>aa</u>nun minall<u>aa</u>hi akbaru <u>dzaa</u>lika huwa lfawzu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1308, '9:73', 9, 73, 'y<u>aa</u> ayyuh<u>aa</u> nnabiyyu j<u>aa</u>hidi lkuff<u>aa</u>ra walmun<u>aa</u>fiqiina waghlu<u>zh</u> \'alayhim wama/w<u>aa</u>hum jahannamu wabi/sa lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (1309, '9:74', 9, 74, 'ya<u>h</u>lifuuna bil<u>aa</u>hi m<u>aa</u> q<u>aa</u>luu walaqad q<u>aa</u>luu kalimata lkufri wakafaruu ba\'da isl<u>aa</u>mihim wahammuu bim<u>aa</u> lam yan<u>aa</u>luu wam<u>aa</u> naqamuu ill<u>aa</u> an aghn<u>aa</u>humull<u>aa</u>hu warasuuluhu min fa<u>dh</u>lihi fa-in yatuubuu yaku khayran lahum wa-in yatawallaw yu\'a<u>dzdz</u>ibhumull<u>aa</u>hu \'a<u>dzaa</u>ban liiman fii dduny<u>aa</u> wal-<u>aa</u>khirati wam<u>aa</u> lahum fii l-ar<u>dh</u>i min waliyyin wal<u>aa</u> na<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (1310, '9:75', 9, 75, 'waminhum man \'<u>aa</u>hadall<u>aa</u>ha la-in <u>aa</u>t<u>aa</u>n<u>aa</u> min fa<u>dh</u>lihi lana<u>shsh</u>addaqanna walanakuunanna mina <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1311, '9:76', 9, 76, 'falamm<u>aa</u> <u>aa</u>t<u>aa</u>hum min fa<u>dh</u>lihi bakhiluu bihi watawallaw wahum mu\'ri<u>dh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1312, '9:77', 9, 77, 'fa-a\'qabahum nif<u>aa</u>qan fii quluubihim il<u>aa</u> yawmi yalqawnahu bim<u>aa</u> akhlafuull<u>aa</u>ha m<u>aa</u> wa\'aduuhu wabim<u>aa</u> k<u>aa</u>nuu yak<u>dz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (1313, '9:78', 9, 78, 'alam ya\'lamuu annall<u>aa</u>ha ya\'lamu sirrahum wanajw<u>aa</u>hum wa-annall<u>aa</u>ha \'all<u>aa</u>mu lghuyuub\r\r\n');
INSERT INTO quranLatin VALUES (1314, '9:79', 9, 79, '<B>al</b>la<u>dz</u>iina yalmizuuna lmu<u>ththh</u>awwi\'iina mina lmu/miniina fii <u>shsh</u>adaq<u>aa</u>ti walla<u>dz</u>iina l<u>aa</u> yajiduuna ill<u>aa</u> juhdahum fayaskharuuna minhum sakhirall<u>aa</u>hu minhum walahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (1315, '9:80', 9, 80, 'istaghfir lahum aw l<u>aa</u> tastaghfir lahum in tastaghfir lahum sab\'iina marratan falan yaghfirall<u>aa</u>hu lahum <u>dzaa</u>lika bi-annahum kafaruu bil<u>aa</u>hi warasuulihi wal<u>aa</u>hu l<u>aa</u> yahdii lqawma lf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (1316, '9:81', 9, 81, 'fari<u>h</u>a lmukhallafuuna bimaq\'adihim khil<u>aa</u>fa rasuulill<u>aa</u>hi wakarihuu an yuj<u>aa</u>hiduu bi-amw<u>aa</u>lihim wa-anfusihim fii sabiilill<u>aa</u>hi waq<u>aa</u>luu l<u>aa</u> tanfiruu fii l<u>h</u>arri qul n<u>aa</u>ru jahannama asyaddu <u>h</u>arran law k<u>aa</u>nuu yafqahuun\r\r\n');
INSERT INTO quranLatin VALUES (1317, '9:82', 9, 82, 'falya<u>dh</u>akuu qaliilan walyabkuu katsiiran jaz<u>aa</u>-an bim<u>aa</u> k<u>aa</u>nuu yaksibuun\r\r\n');
INSERT INTO quranLatin VALUES (1318, '9:83', 9, 83, 'fa-in raja\'akall<u>aa</u>hu il<u>aa</u> <u>th</u><u>aa</u>-ifatin minhum fasta/<u>dz</u>anuuka lilkhuruuji faqul lan takhrujuu ma\'iya abadan walan tuq<u>aa</u>tiluu ma\'iya \'aduwwan innakum ra<u>dh</u>iitum bilqu\'uudi awwala marratin faq\'uduu ma\'a lkh<u>aa</u>lifiin\r\r\n');
INSERT INTO quranLatin VALUES (1319, '9:84', 9, 84, 'wal<u>aa</u> tu<u>sh</u>alli \'al<u>aa</u> a<u>h</u>adin minhum m<u>aa</u>ta abadan wal<u>aa</u> taqum \'al<u>aa</u> qabrihi innahum kafaruu bil<u>aa</u>hi warasuulihi wam<u>aa</u>tuu wahum f<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (1320, '9:85', 9, 85, 'wal<u>aa</u> tu\'jibka amw<u>aa</u>luhum wa-awl<u>aa</u>duhum innam<u>aa</u> yuriidull<u>aa</u>hu an yu\'a<u>dzdz</u>ibahum bih<u>aa</u> fii dduny<u>aa</u> watazhaqa anfusuhum wahum k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (1321, '9:86', 9, 86, 'wa-i<u>dzaa</u> unzilat suuratun an <u>aa</u>minuu bil<u>aa</u>hi waj<u>aa</u>hiduu ma\'a rasuulihi ista/<u>dz</u>anaka uluu <u>ththh</u>awli minhum waq<u>aa</u>luu <u>dz</u>arn<u>aa</u> nakun ma\'a lq<u>aa</u>\'idiin\r\r\n');
INSERT INTO quranLatin VALUES (1322, '9:87', 9, 87, 'ra<u>dh</u>uu bi-an yakuunuu ma\'a lkhaw<u>aa</u>lifi wa<u>th</u>ubi\'a \'al<u>aa</u> quluubihim fahum l<u>aa</u> yafqahuun\r\r\n');
INSERT INTO quranLatin VALUES (1323, '9:88', 9, 88, 'l<u>aa</u>kini rrasuulu walla<u>dz</u>iina <u>aa</u>manuu ma\'ahu j<u>aa</u>haduu bi-amw<u>aa</u>lihim wa-anfusihim waul<u>aa</u>-ika lahumu lkhayr<u>aa</u>tu waul<u>aa</u>-ika humu lmufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1324, '9:89', 9, 89, 'a\'addall<u>aa</u>hu lahum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> <u>dzaa</u>lika lfawzu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1325, '9:90', 9, 90, 'waj<u>aa</u>-a lmu\'a<u>dzdz</u>iruuna mina l-a\'r<u>aa</u>bi liyu/<u>dz</u>ana lahum waqa\'adalla<u>dz</u>iina ka<u>dz</u>abuull<u>aa</u>ha warasuulahu sayu<u>sh</u>iibulla<u>dz</u>iina kafaruu minhum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (1326, '9:91', 9, 91, 'laysa \'al<u>aa</u> <u>dhdh</u>u\'af<u>aa</u>-i wal<u>aa</u> \'al<u>aa</u> lmar<u>daa</u> wal<u>aa</u> \'al<u>aa</u>lla<u>dz</u>iina l<u>aa</u> yajiduuna m<u>aa</u> yunfiquuna <u>h</u>arajun i<u>dzaa</u> na<u>sh</u>a<u>h</u>uu lill<u>aa</u>hi warasuulihi m<u>aa</u> \'al<u>aa</u> lmu<u>h</u>siniina min sabiilin wal<u>aa</u>hu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1327, '9:92', 9, 92, 'wal<u>aa</u> \'al<u>aa</u>lla<u>dz</u>iina i<u>dzaa</u> m<u>aa</u> atawka lita<u>h</u>milahum qulta l<u>aa</u> ajidu m<u>aa</u> a<u>h</u>milukum \'alayhi tawallaw wa-a\'yunuhum tafii<u>dh</u>u mina ddam\'i <u>h</u>azananll<u>aa</u> yajiduu m<u>aa</u> yunfiquun\r\r\n');
INSERT INTO quranLatin VALUES (1328, '9:93', 9, 93, 'innam<u>aa</u> ssabiilu \'al<u>aa</u>lla<u>dz</u>iina yasta/<u>dz</u>inuunaka wahum aghniy<u>aa</u>u ra<u>dh</u>uu bi-an yakuunuu ma\'a lkhaw<u>aa</u>lifi wa<u>th</u>aba\'all<u>aa</u>hu \'al<u>aa</u> quluubihim fahum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1329, '9:94', 9, 94, 'ya\'ta<u>dz</u>iruuna ilaykum i<u>dzaa</u> raja\'tum ilayhim qul l<u>aa</u> ta\'ta<u>dz</u>iruu lan nu/mina lakum qad nabba-an<u>aa</u>ll<u>aa</u>hu min akhb<u>aa</u>rikum wasayar<u>aa</u>ll<u>aa</u>hu \'amalakum warasuuluhu tsumma turadduuna il<u>aa</u> \'<u>aa</u>limi lghaybi wasysyah<u>aa</u>dati fayunabbi-ukum bim<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1330, '9:95', 9, 95, 'saya<u>h</u>lifuuna bil<u>aa</u>hi lakum i<u>dzaa</u> inqalabtum ilayhim litu\'ri<u>dh</u>uu \'anhum fa-a\'ri<u>dh</u>uu \'anhum innahum rijsun wama/w<u>aa</u>hum jahannamu jaz<u>aa</u>-an bim<u>aa</u> k<u>aa</u>nuu yaksibuun\r\r\n');
INSERT INTO quranLatin VALUES (1331, '9:96', 9, 96, 'ya<u>h</u>lifuuna lakum litar<u>dh</u>aw \'anhum fa-in tar<u>dh</u>aw \'anhum fa-innall<u>aa</u>ha l<u>aa</u> yar<u>daa</u> \'ani lqawmi lf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (1332, '9:97', 9, 97, 'al-a\'r<u>aa</u>bu asyaddu kufran wanif<u>aa</u>qan wa-ajdarull<u>aa</u> ya\'lamuu <u>h</u>uduuda m<u>aa</u> anzalall<u>aa</u>hu \'al<u>aa</u> rasuulihi wal<u>aa</u>hu \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1333, '9:98', 9, 98, 'wamina l-a\'r<u>aa</u>bi man yattakhi<u>dz</u>u m<u>aa</u> yunfiqu maghraman wayatarabba<u>sh</u>u bikumu ddaw<u>aa</u>-ira \'alayhim d<u>aa</u>-iratu ssaw-i wal<u>aa</u>hu samii\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1334, '9:99', 9, 99, 'wamina l-a\'r<u>aa</u>bi man yu/minu bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri wayattakhi<u>dz</u>u m<u>aa</u> yunfiqu qurub<u>aa</u>tin \'indall<u>aa</u>hi wa<u>sh</u>alaw<u>aa</u>ti rrasuuli l<u>aa</u> innah<u>aa</u> qurbatun lahum sayudkhiluhumull<u>aa</u>hu fii ra<u>h</u>matihi innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1335, '9:100', 9, 100, 'wass<u>aa</u>biquuna l-awwaluuna mina lmuh<u>aa</u>jiriina wal-an<u>shaa</u>ri walla<u>dz</u>iina ittaba\'uuhum bi-i<u>h</u>s<u>aa</u>nin ra<u>dh</u>iyall<u>aa</u>hu \'anhum wara<u>dh</u>uu \'anhu wa-a\'adda lahum jann<u>aa</u>tin tajrii ta<u>h</u>tah<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> abadan <u>dzaa</u>lika lfawzu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1336, '9:101', 9, 101, 'wamimman <u>h</u>awlakum mina l-a\'r<u>aa</u>bi mun<u>aa</u>fiquuna wamin ahli lmadiinati maraduu \'al<u>aa</u> nnif<u>aa</u>qi l<u>aa</u> ta\'lamuhum na<u>h</u>nu na\'lamuhum sanu\'a<u>dzdz</u>ibuhum marratayni tsumma yuradduuna il<u>aa</u> \'a<u>dzaa</u>bin \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1337, '9:102', 9, 102, 'wa<u>aa</u>kharuuna i\'tarafuu bi<u>dz</u>unuubihim khala<u>th</u>uu \'amalan <u>shaa</u>li<u>h</u>an wa<u>aa</u>khara sayyi-an \'as<u>aa</u>ll<u>aa</u>hu an yatuuba \'alayhim innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1338, '9:103', 9, 103, 'khu<u>dz</u> min amw<u>aa</u>lihim <u>sh</u>adaqatan tu<u>th</u>ahhiruhum watuzakkiihim bih<u>aa</u> wa<u>sh</u>alli \'alayhim inna <u>sh</u>al<u>aa</u>taka sakanun lahum wal<u>aa</u>hu samii\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1339, '9:104', 9, 104, 'alam ya\'lamuu annall<u>aa</u>ha huwa yaqbalu ttawbata \'an \'ib<u>aa</u>dihi waya/khu<u>dz</u>u <u>shsh</u>adaq<u>aa</u>ti wa-annall<u>aa</u>ha huwa ttaww<u>aa</u>bu rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1340, '9:105', 9, 105, 'waquli i\'maluu fasayar<u>aa</u>ll<u>aa</u>hu \'amalakum warasuuluhu walmu/minuuna wasaturadduuna il<u>aa</u> \'<u>aa</u>limi lghaybi wasysyah<u>aa</u>dati fayunabbi-ukum bim<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1341, '9:106', 9, 106, 'wa<u>aa</u>kharuuna murjawna li-amrill<u>aa</u>hi imm<u>aa</u> yu\'a<u>dzdz</u>ibuhum wa-imm<u>aa</u> yatuubu \'alayhim wal<u>aa</u>hu \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1342, '9:107', 9, 107, 'walla<u>dz</u>iina ittakha<u>dz</u>uu masjidan <u>dh</u>ir<u>aa</u>ran wakufran watafriiqan bayna lmu/miniina wa-ir<u>shaa</u>dan liman <u>haa</u>raball<u>aa</u>ha warasuulahu min qablu walaya<u>h</u>lifunna in aradn<u>aa</u> ill<u>aa</u> l<u>h</u>usn<u>aa</u> wal<u>aa</u>hu yasyhadu innahum lak<u>aadz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (1343, '9:108', 9, 108, 'l<u>aa</u> taqum fiihi abadan lamasjidun ussisa \'al<u>aa</u> ttaqw<u>aa</u> min awwali yawmin a<u>h</u>aqqu an taquuma fiihi fiihi rij<u>aa</u>lun yu<u>h</u>ibbuuna an yata<u>th</u>ahharuu wal<u>aa</u>hu yu<u>h</u>ibbu lmu<u>ththh</u>ahhiriin\r\r\n');
INSERT INTO quranLatin VALUES (1344, '9:109', 9, 109, 'afaman assasa buny<u>aa</u>nahu \'al<u>aa</u> taqw<u>aa</u> minall<u>aa</u>hi wari<u>dh</u>w<u>aa</u>nin khayrun am man assasa buny<u>aa</u>nahu \'al<u>aa</u> syaf<u>aa</u> jurufin h<u>aa</u>rin fanh<u>aa</u>ra bihi fii n<u>aa</u>ri jahannama wal<u>aa</u>hu l<u>aa</u> yahdii lqawma <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1345, '9:110', 9, 110, 'l<u>aa</u> yaz<u>aa</u>lu buny<u>aa</u>nuhumulla<u>dz</u>ii banaw riibatan fii quluubihim ill<u>aa</u> an taqa<u>ththh</u>a\'a quluubuhum wal<u>aa</u>hu \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1346, '9:111', 9, 111, 'innall<u>aa</u>ha isytar<u>aa</u> mina lmu/miniina anfusahum wa-amw<u>aa</u>lahum bi-anna lahumu ljannata yuq<u>aa</u>tiluuna fii sabiilill<u>aa</u>hi fayaqtuluuna wayuqtaluuna wa\'dan \'alayhi <u>h</u>aqqan fii ttawr<u>aa</u>ti wal-injiili walqur-<u>aa</u>ni waman awf<u>aa</u> bi\'ahdihi minall<u>aa</u>hi fastabsyiruu bibay\'ikumulla<u>dz</u>ii b<u>aa</u>ya\'tum bihi wa<u>dzaa</u>lika huwa lfawzu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1347, '9:112', 9, 112, 'att<u>aa</u>-ibuuna l\'<u>aa</u>biduuna l<u>haa</u>miduuna ss<u>aa</u>-i<u>h</u>uuna rr<u>aa</u>ki\'uuna ss<u>aa</u>jiduuna l-<u>aa</u>miruuna bilma\'ruufi wann<u>aa</u>huuna \'ani lmunkari wal<u>haa</u>fi<u>zh</u>uuna li<u>h</u>uduudill<u>aa</u>hi wabasysyiri lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1348, '9:113', 9, 113, 'm<u>aa</u> k<u>aa</u>na linnabiyyi walla<u>dz</u>iina <u>aa</u>manuu an yastaghfiruu lilmusyrikiina walaw k<u>aa</u>nuu ulii qurb<u>aa</u> min ba\'di m<u>aa</u> tabayyana lahum annahum a<u>sh</u>-<u>haa</u>bu lja<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1349, '9:114', 9, 114, 'wam<u>aa</u> k<u>aa</u>na istighf<u>aa</u>ru ibr<u>aa</u>hiima li-abiihi ill<u>aa</u> \'an maw\'idatin wa\'adah<u>aa</u> iyy<u>aa</u>hu falamm<u>aa</u> tabayyana lahu annahu \'aduwwun lill<u>aa</u>hi tabarra-a minhu inna ibr<u>aa</u>hiima la-aww<u>aa</u>hun <u>h</u>aliim\r\r\n');
INSERT INTO quranLatin VALUES (1350, '9:115', 9, 115, 'wam<u>aa</u> k<u>aa</u>nall<u>aa</u>hu liyu<u>dh</u>illa qawman ba\'da i<u>dz</u> had<u>aa</u>hum <u>h</u>att<u>aa</u> yubayyina lahum m<u>aa</u> yattaquuna innall<u>aa</u>ha bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1351, '9:116', 9, 116, 'innall<u>aa</u>ha lahu mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i yu<u>h</u>yii wayumiitu wam<u>aa</u> lakum min duunill<u>aa</u>hi min waliyyin wal<u>aa</u> na<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (1352, '9:117', 9, 117, 'laqad t<u>aa</u>ball<u>aa</u>hu \'al<u>aa</u> nnabiyyi walmuh<u>aa</u>jiriina wal-an<u>shaa</u>rilla<u>dz</u>iina ittaba\'uuhu fii s<u>aa</u>\'ati l\'usrati min ba\'di m<u>aa</u> k<u>aa</u>da yaziighu quluubu fariiqin minhum tsumma t<u>aa</u>ba \'alayhim innahu bihim rauufun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1353, '9:118', 9, 118, 'wa\'al<u>aa</u> tstsal<u>aa</u>tsatilla<u>dz</u>iina khullifuu <u>h</u>att<u>aa</u> i<u>dzaa</u> <u>daa</u>qat \'alayhimu l-ar<u>dh</u>u bim<u>aa</u> ra<u>h</u>ubat wa<u>daa</u>qat \'alayhim anfusuhum wa<u>zh</u>annuu an l<u>aa</u> malja-a minall<u>aa</u>hi ill<u>aa</u> ilayhi tsumma t<u>aa</u>ba \'alayhim liyatuubuu innall<u>aa</u>ha huwa ttaww<u>aa</u>bu rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1354, '9:119', 9, 119, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu ittaquull<u>aa</u>ha wakuunuu ma\'a <u>shshaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (1355, '9:120', 9, 120, 'm<u>aa</u> k<u>aa</u>na li-ahli lmadiinati waman <u>h</u>awlahum mina l-a\'r<u>aa</u>bi an yatakhallafuu \'an rasuulill<u>aa</u>hi wal<u>aa</u> yarghabuu bi-anfusihim \'an nafsihi <u>dzaa</u>lika bi-annahum l<u>aa</u> yu<u>sh</u>iibuhum <u>zh</u>amaun wal<u>aa</u> na<u>sh</u>abun wal<u>aa</u> makhma<u>sh</u>atun fii sabiilill<u>aa</u>hi wal<u>aa</u> ya<u>th</u>auuna maw<u>th</u>i-an yaghii<u>zh</u>u lkuff<u>aa</u>ra wal<u>aa</u> yan<u>aa</u>luuna min \'aduwwin naylan ill<u>aa</u> kutiba lahum bihi \'amalun <u>shaa</u>li<u>h</u>un innall<u>aa</u>ha l<u>aa</u> yu<u>dh</u>ii\'u ajra lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (1356, '9:121', 9, 121, 'wal<u>aa</u> yunfiquuna nafaqatan <u>sh</u>aghiiratan wal<u>aa</u> kabiiratan wal<u>aa</u> yaq<u>th</u>a\'uuna w<u>aa</u>diyan ill<u>aa</u> kutiba lahum liyajziyahumull<u>aa</u>hu a<u>h</u>sana m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1357, '9:122', 9, 122, 'wam<u>aa</u> k<u>aa</u>na lmu/minuuna liyanfiruu k<u>aa</u>ffatan falawl<u>aa</u> nafara min kulli firqatin minhum <u>th</u><u>aa</u>-ifatun liyatafaqqahuu fii ddiini waliyun<u>dz</u>iruu qawmahum i<u>dzaa</u> raja\'uu ilayhim la\'allahum ya<u>hts</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (1358, '9:123', 9, 123, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu q<u>aa</u>tiluulla<u>dz</u>iina yaluunakum mina lkuff<u>aa</u>ri walyajiduu fiikum ghil<u>zh</u>atan wa\'lamuu annall<u>aa</u>ha ma\'a lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (1359, '9:124', 9, 124, 'wa-i<u>dzaa</u> m<u>aa</u> unzilat suuratun faminhum man yaquulu ayyukum z<u>aa</u>dat-hu h<u>aadz</u>ihi iim<u>aa</u>nan fa-amm<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu faz<u>aa</u>dat-hum iim<u>aa</u>nan wahum yastabsyiruun\r\r\n');
INSERT INTO quranLatin VALUES (1360, '9:125', 9, 125, 'wa-amm<u>aa</u>lla<u>dz</u>iina fii quluubihim mara<u>dh</u>un faz<u>aa</u>dat-hum rijsan il<u>aa</u> rijsihim wam<u>aa</u>tuu wahum k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (1361, '9:126', 9, 126, 'awa l<u>aa</u> yarawna annahum yuftanuuna fii kulli \'<u>aa</u>min marratan aw marratayni tsumma l<u>aa</u> yatuubuuna wal<u>aa</u> hum ya<u>dzdz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1362, '9:127', 9, 127, 'wa-i<u>dzaa</u> m<u>aa</u> unzilat suuratun na<u>zh</u>ara ba\'<u>dh</u>uhum il<u>aa</u> ba\'<u>dh</u>in hal yar<u>aa</u>kum min a<u>h</u>adin tsumma in<u>sh</u>arafuu <u>sh</u>arafall<u>aa</u>hu quluubahum bi-annahum qawmun l<u>aa</u> yafqahuun\r\r\n');
INSERT INTO quranLatin VALUES (1363, '9:128', 9, 128, 'laqad j<u>aa</u>-akum rasuulun min anfusikum \'aziizun \'alayhi m<u>aa </u>\'anittum <u>h</u>arii<u>sh</u>un \'alaykum bilmu/miniina rauufun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1364, '9:129', 9, 129, 'fa-in tawallaw faqul <u>h</u>asbiyall<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa \'alayhi tawakkaltu wahuwa rabbu l\'arsyi l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1365, '10:1', 10, 1, 'alif-l<u>aa</u>m-r<u>aa</u> tilka <u>aa</u>y<u>aa</u>tu lkit<u>aa</u>bi l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1366, '10:2', 10, 2, 'ak<u>aa</u>na linn<u>aa</u>si \'ajaban an aw<u>h</u>ayn<u>aa</u> il<u>aa</u> rajulin minhum an an<u>dz</u>iri nn<u>aa</u>sa wabasysyirilla<u>dz</u>iina <u>aa</u>manuu anna lahum qadama <u>sh</u>idqin \'inda rabbihim q<u>aa</u>la lk<u>aa</u>firuuna inna h<u>aadzaa</u> las<u>aah</u>irun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1367, '10:3', 10, 3, 'inna rabbakumull<u>aa</u>hulla<u>dz</u>ii khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a fii sittati ayy<u>aa</u>min tsumma istaw<u>aa</u> \'al<u>aa</u> l\'arsyi yudabbiru l-amra m<u>aa</u> min syafii\'in ill<u>aa</u> min ba\'di i<u>dz</u>nihi <u>dzaa</u>likumull<u>aa</u>hu rabbukum fa\'buduuhu afal<u>aa</u> ta<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1368, '10:4', 10, 4, 'ilayhi marji\'ukum jamii\'an wa\'dall<u>aa</u>hi <u>h</u>aqqan innahu yabdau lkhalqa tsumma yu\'iiduhu liyajziyalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti bilqis<u>th</u>i walla<u>dz</u>iina kafaruu lahum syar<u>aa</u>bun min <u>h</u>amiimin wa\'a<u>dzaa</u>bun liimun bim<u>aa</u> k<u>aa</u>nuu yakfuruun\r\r\n');
INSERT INTO quranLatin VALUES (1369, '10:5', 10, 5, 'huwalla<u>dz</u>ii ja\'ala sysyamsa <u>dh</u>iy<u>aa</u>-an walqamara nuuran waqaddarahu man<u>aa</u>zila lita\'lamuu \'adada ssiniina wal<u>h</u>is<u>aa</u>ba m<u>aa</u> khalaqall<u>aa</u>hu <u>dzaa</u>lika ill<u>aa</u> bil<u>h</u>aqqi yufa<u>shsh</u>ilu l-<u>aa</u>y<u>aa</u>ti liqawmin ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1370, '10:6', 10, 6, 'inna fii ikhtil<u>aa</u>fillayli wannah<u>aa</u>ri wam<u>aa</u> khalaqall<u>aa</u>hu fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i la<u>aa</u>y<u>aa</u>tin liqawmin yattaquun\r\r\n');
INSERT INTO quranLatin VALUES (1371, '10:7', 10, 7, 'innalla<u>dz</u>iina l<u>aa</u> yarjuuna liq<u>aa</u>-an<u>aa</u> wara<u>dh</u>uu bil<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wa<u>th</u>ma-annuu bih<u>aa</u> walla<u>dz</u>iina hum \'an <u>aa</u>y<u>aa</u>tin<u>aa</u> gh<u>aa</u>filuun\r\r\n');
INSERT INTO quranLatin VALUES (1372, '10:8', 10, 8, 'ul<u>aa</u>-ika ma/w<u>aa</u>humu nn<u>aa</u>ru bim<u>aa</u> k<u>aa</u>nuu yaksibuun\r\r\n');
INSERT INTO quranLatin VALUES (1373, '10:9', 10, 9, 'innalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti yahdiihim rabbuhum bi-iim<u>aa</u>nihim tajrii min ta<u>h</u>tihimu l-anh<u>aa</u>ru fii jann<u>aa</u>ti nna\'iim\r\r\n');
INSERT INTO quranLatin VALUES (1374, '10:10', 10, 10, 'da\'w<u>aa</u>hum fiih<u>aa</u> sub<u>haa</u>nakall<u>aa</u>humma wata<u>h</u>iyyatuhum fiih<u>aa</u> sal<u>aa</u>mun wa<u>aa</u>khiru da\'w<u>aa</u>hum ani l<u>h</u>amdu lill<u>aa</u>hi rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (1375, '10:11', 10, 11, 'walaw yu\'ajjilull<u>aa</u>hu linn<u>aa</u>si sysyarra isti\'j<u>aa</u>lahum bilkhayri laqu<u>dh</u>iya ilayhim ajaluhum fana<u>dz</u>arulla<u>dz</u>iina l<u>aa</u> yarjuuna liq<u>aa</u>-an<u>aa</u> fii <u>th</u>ughy<u>aa</u>nihim ya\'mahuun\r\r\n');
INSERT INTO quranLatin VALUES (1376, '10:12', 10, 12, 'wa-i<u>dzaa</u> massa l-ins<u>aa</u>na <u>dhdh</u>urru da\'<u>aa</u>n<u>aa</u> lijanbihi aw q<u>aa</u>\'idan aw q<u>aa</u>-iman falamm<u>aa</u> kasyafn<u>aa</u> \'anhu <u>dh</u>urrahu marra ka-an lam yad\'un<u>aa</u> il<u>aa</u> <u>dh</u>urrin massahu ka<u>dzaa</u>lika zuyyina lilmusrifiina m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1377, '10:13', 10, 13, 'walaqad ahlakn<u>aa</u> lquruuna min qablikum lamm<u>aa</u> <u>zh</u>alamuu waj<u>aa</u>-at-hum rusuluhum bilbayyin<u>aa</u>ti wam<u>aa</u> k<u>aa</u>nuu liyu/minuu ka<u>dzaa</u>lika najzii lqawma lmujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (1378, '10:14', 10, 14, 'tsumma ja\'aln<u>aa</u>kum khal<u>aa</u>-ifa fii l-ar<u>dh</u>i min ba\'dihim linan<u>zh</u>ura kayfa ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1379, '10:15', 10, 15, 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tun<u>aa</u> bayyin<u>aa</u>tin q<u>aa</u>lalla<u>dz</u>iina l<u>aa</u> yarjuuna liq<u>aa</u>-an<u>aa</u> i/ti biqur-<u>aa</u>nin ghayri h<u>aadzaa</u> aw baddilhu qul m<u>aa</u> yakuunu lii an ubaddilahu min tilq<u>aa</u>-i nafsii in attabi\'u ill<u>aa</u> m<u>aa</u> yuu<u>haa</u> ilayya innii akh<u>aa</u>fu in \'a<u>sh</u>aytu rabbii \'a<u>dzaa</u>ba yawmin \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1380, '10:16', 10, 16, 'qul law sy<u>aa</u>-all<u>aa</u>hu m<u>aa</u> talawtuhu \'alaykum wal<u>aa</u> adr<u>aa</u>kum bihi faqad labitstu fiikum \'umuran min qablihi afal<u>aa</u> ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (1381, '10:17', 10, 17, 'faman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>iban aw ka<u>dzdz</u>aba bi-<u>aa</u>y<u>aa</u>tihi innahu l<u>aa</u> yufli<u>h</u>u lmujrimuun\r\r\n');
INSERT INTO quranLatin VALUES (1382, '10:18', 10, 18, 'waya\'buduuna min duunill<u>aa</u>hi m<u>aa</u> l<u>aa</u> ya<u>dh</u>urruhum wal<u>aa</u> yanfa\'uhum wayaquuluuna h<u>aa</u>ul<u>aa</u>-i syufa\'<u>aa</u>un<u>aa</u> \'indall<u>aa</u>hi qul atunabbi-uunall<u>aa</u>ha bim<u>aa</u> l<u>aa</u> ya\'lamu fii ssam<u>aa</u>w<u>aa</u>ti wal<u>aa</u> fii l-ar<u>dh</u>i sub<u>haa</u>nahu wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (1383, '10:19', 10, 19, 'wam<u>aa</u> k<u>aa</u>na nn<u>aa</u>su ill<u>aa</u> ummatan w<u>aah</u>idatan fakhtalafuu walawl<u>aa</u> kalimatun sabaqat min rabbika laqu<u>dh</u>iya baynahum fiim<u>aa</u> fiihi yakhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (1384, '10:20', 10, 20, 'wayaquuluuna lawl<u>aa</u> unzila \'alayhi <u>aa</u>yatun min rabbihi faqul innam<u>aa</u> lghaybu lill<u>aa</u>hi fanta<u>zh</u>iruu innii ma\'akum mina lmunta<u>zh</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (1385, '10:21', 10, 21, 'wa-i<u>dzaa</u> a<u>dz</u>aqn<u>aa</u> nn<u>aa</u>sa ra<u>h</u>matan min ba\'di <u>dh</u>arr<u>aa</u>-a massat-hum i<u>dzaa</u> lahum makrun fii <u>aa</u>y<u>aa</u>tin<u>aa</u> qulill<u>aa</u>hu asra\'u makran inna rusulan<u>aa</u> yaktubuuna m<u>aa</u> tamkuruun\r\r\n');
INSERT INTO quranLatin VALUES (1386, '10:22', 10, 22, 'huwalla<u>dz</u>ii yusayyirukum fii lbarri walba<u>h</u>ri <u>h</u>att<u>aa</u> i<u>dzaa</u> kuntum fii lfulki wajarayna bihim birii<u>h</u>in <u>th</u>ayyibatin wafari<u>h</u>uu bih<u>aa</u> j<u>aa</u>-at-h<u>aa</u> rii<u>h</u>un \'<u>aas</u>ifun waj<u>aa</u>-ahumu lmawju min kulli mak<u>aa</u>nin wa<u>zh</u>annuu annahum u<u>h</u>ii<u>th</u>a bihim da\'awuull<u>aa</u>ha mukhli<u>sh</u>iina lahu ddiina la-in anjaytan<u>aa</u> min h<u>aadz</u>ihi lanakuunanna mina sysy<u>aa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (1387, '10:23', 10, 23, 'falamm<u>aa</u> anj<u>aa</u>hum i<u>dzaa</u> hum yabghuuna fii l-ar<u>dh</u>i bighayri l<u>h</u>aqqi y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su innam<u>aa</u> baghyukum \'al<u>aa</u> anfusikum mat<u>aa</u>\'a l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> tsumma ilayn<u>aa</u> marji\'ukum fanunabbi-ukum bim<u>aa</u> kuntum ta\'maluun<b>a </b>\r\r\n');
INSERT INTO quranLatin VALUES (1388, '10:24', 10, 24, 'innam<u>aa</u> matsalu l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> kam<u>aa</u>-in anzaln<u>aa</u>hu mina ssam<u>aa</u>-i fakhtala<u>th</u>a bihi nab<u>aa</u>tu l-ar<u>dh</u>i mimm<u>aa</u> ya/kulu nn<u>aa</u>su wal-an\'<u>aa</u>mu <u>h</u>att<u>aa</u> i<u>dzaa</u> akha<u>dz</u>ati l-ar<u>dh</u>u zukhrufah<u>aa</u> wazzayyanat wa<u>zh</u>anna ahluh<u>aa</u> annahum q<u>aa</u>diruuna \'alayh<u>aa</u> at<u>aa</u>h<u>aa</u> amrun<u>aa</u> laylan aw nah<u>aa</u>ran faja\'aln<u>aa</u>h<u>aa</u> <u>h</u>a<u>sh</u>iidan ka-an lam taghna bil-amsi ka<u>dzaa</u>lika nufa<u>shsh</u>ilu l-<u>aa</u>y<u>aa</u>ti liqawmin yatafakkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1389, '10:25', 10, 25, 'wal<u>aa</u>hu yad\'uu il<u>aa</u> d<u>aa</u>ri ssal<u>aa</u>mi wayahdii man yasy<u>aa</u>u il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (1390, '10:26', 10, 26, 'lilla<u>dz</u>iina a<u>h</u>sanuu l<u>h</u>usn<u>aa</u> waziy<u>aa</u>datun wal<u>aa</u> yarhaqu wujuuhahum qatarun wal<u>aa</u> <u>dz</u>illatun ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu ljannati hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (1391, '10:27', 10, 27, 'walla<u>dz</u>iina kasabuu ssayyi-<u>aa</u>ti jaz<u>aa</u>u sayyi-atin bimitslih<u>aa</u> watarhaquhum <u>dz</u>illatun m<u>aa</u> lahum minall<u>aa</u>hi min \'<u>aas</u>imin ka-annam<u>aa</u> ughsyiyat wujuuhuhum qi<u>th</u>a\'an minallayli mu<u>zh</u>liman ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (1392, '10:28', 10, 28, 'wayawma na<u>h</u>syuruhum jamii\'an tsumma naquulu lilla<u>dz</u>iina asyrakuu mak<u>aa</u>nakum antum wasyurak<u>aa</u>ukum fazayyaln<u>aa</u> baynahum waq<u>aa</u>la syurak<u>aa</u>uhum m<u>aa</u> kuntum iyy<u>aa</u>n<u>aa</u> ta\'buduun\r\r\n');
INSERT INTO quranLatin VALUES (1393, '10:29', 10, 29, 'fakaf<u>aa</u> bil<u>aa</u>hi syahiidan baynan<u>aa</u> wabaynakum in kunn<u>aa</u> \'an \'ib<u>aa</u>datikum lagh<u>aa</u>filiin\r\r\n');
INSERT INTO quranLatin VALUES (1394, '10:30', 10, 30, 'hun<u>aa</u>lika tabluu kullu nafsin m<u>aa</u> aslafat warudduu il<u>aa</u>ll<u>aa</u>hi mawl<u>aa</u>humu l<u>h</u>aqqi wa<u>dh</u>alla \'anhum m<u>aa</u> k<u>aa</u>nuu yaftaruun\r\r\n');
INSERT INTO quranLatin VALUES (1395, '10:31', 10, 31, 'qul man yarzuqukum mina ssam<u>aa</u>-i wal-ar<u>dh</u>i amman yamliku ssam\'a wal-ab<u>shaa</u>ra waman yukhriju l<u>h</u>ayya mina lmayyiti wayukhriju lmayyita mina l<u>h</u>ayyi waman yudabbiru l-amra fasayaquuluunall<u>aa</u>hu faqul afal<u>aa</u> tattaquun\r\r\n');
INSERT INTO quranLatin VALUES (1396, '10:32', 10, 32, 'fa<u>dzaa</u>likumull<u>aa</u>hu rabbukumu l<u>h</u>aqqu fam<u>aatsaa</u> ba\'da l<u>h</u>aqqi ill<u>aa</u> <u>dhdh</u>al<u>aa</u>lu fa-ann<u>aa</u> tu<u>sh</u>rafuun\r\r\n');
INSERT INTO quranLatin VALUES (1397, '10:33', 10, 33, 'ka<u>dzaa</u>lika <u>h</u>aqqat kalimatu rabbika \'al<u>aa</u>lla<u>dz</u>iina fasaquu annahum l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1398, '10:34', 10, 34, 'qul hal min syurak<u>aa</u>-ikum man yabdau lkhalqa tsumma yu\'iiduhu qulill<u>aa</u>hu yabdau lkhalqa tsumma yu\'iiduhu fa-ann<u>aa</u> tu/fakuun\r\r\n');
INSERT INTO quranLatin VALUES (1399, '10:35', 10, 35, 'qul hal min syurak<u>aa</u>-ikum man yahdii il<u>aa</u> l<u>h</u>aqqi qulill<u>aa</u>hu yahdii lil<u>h</u>aqqi afaman yahdii il<u>aa</u> l<u>h</u>aqqi a<u>h</u>aqqu an yuttaba\'a amman l<u>aa</u> yahiddii ill<u>aa</u> an yuhd<u>aa</u> fam<u>aa</u> lakum kayfa ta<u>h</u>kumuun\r\r\n');
INSERT INTO quranLatin VALUES (1400, '10:36', 10, 36, 'wam<u>aa</u> yattabi\'u aktsaruhum ill<u>aa</u> <u>zh</u>annan inna <u>zhzh</u>anna l<u>aa</u> yughnii mina l<u>h</u>aqqi syay-an innall<u>aa</u>ha \'aliimun bim<u>aa</u> yaf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (1401, '10:37', 10, 37, 'wam<u>aa</u> k<u>aa</u>na h<u>aadzaa</u> lqur-<u>aa</u>nu an yuftar<u>aa</u> min duunill<u>aa</u>hi wal<u>aa</u>kin ta<u>sh</u>diiqalla<u>dz</u>ii bayna yadayhi wataf<u>sh</u>iila lkit<u>aa</u>bi l<u>aa</u> rayba fiihi min rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (1402, '10:38', 10, 38, 'am yaquuluuna iftar<u>aa</u>hu qul fa/tuu bisuuratin mitslihi wad\'uu mani ista<u>th</u>a\'tum min duunill<u>aa</u>hi in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (1403, '10:39', 10, 39, 'bal ka<u>dzdz</u>abuu bim<u>aa</u> lam yu<u>h</u>ii<u>th</u>uu bi\'ilmihi walamm<u>aa</u> ya/tihim ta/wiiluhu ka<u>dzaa</u>lika ka<u>dzdz</u>aballa<u>dz</u>iina min qablihim fan<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1404, '10:40', 10, 40, 'waminhum man yu/minu bihi waminhum man l<u>aa</u> yu/minu bihi warabbuka a\'lamu bilmufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (1405, '10:41', 10, 41, 'wa-in ka<u>dzdz</u>abuuka faqul lii \'amalii walakum \'amalukum antum barii-uuna mimm<u>aa</u> a\'malu wa-an<u>aa</u> barii-un mimm<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1406, '10:42', 10, 42, 'waminhum man yastami\'uuna ilayka afa-anta tusmi\'u <u>shsh</u>umma walaw k<u>aa</u>nuu l<u>aa</u> ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (1407, '10:43', 10, 43, 'waminhum man yan<u>zh</u>uru ilayka afa-anta tahdii l\'umya walaw k<u>aa</u>nuu l<u>aa</u> yub<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (1408, '10:44', 10, 44, 'innall<u>aa</u>ha l<u>aa</u> ya<u>zh</u>limu nn<u>aa</u>sa syay-an wal<u>aa</u>kinna nn<u>aa</u>sa anfusahum ya<u>zh</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (1409, '10:45', 10, 45, 'wayawma ya<u>h</u>syuruhum ka-an lam yalbatsuu ill<u>aa</u> s<u>aa</u>\'atan mina nnah<u>aa</u>ri yata\'<u>aa</u>rafuuna baynahum qad khasiralla<u>dz</u>iina ka<u>dzdz</u>abuu biliq<u>aa</u>-ill<u>aa</u>hi wam<u>aa</u> k<u>aa</u>nuu muhtadiin\r\r\n');
INSERT INTO quranLatin VALUES (1410, '10:46', 10, 46, 'wa-imm<u>aa</u> nuriyannaka ba\'<u>dh</u>alla<u>dz</u>ii na\'iduhum aw natawaffayannaka fa-ilayn<u>aa</u> marji\'uhum tsummall<u>aa</u>hu syahiidun \'al<u>aa</u> m<u>aa</u> yaf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (1411, '10:47', 10, 47, 'walikulli ummatin rasuulun fa-i<u>dzaa</u> j<u>aa</u>-a rasuuluhum qu<u>dh</u>iya baynahum bilqis<u>th</u>i wahum l<u>aa</u> yu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1412, '10:48', 10, 48, 'wayaquuluuna mat<u>aa</u> h<u>aadzaa</u> lwa\'du in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (1413, '10:49', 10, 49, 'qul l<u>aa</u> amliku linafsii <u>dh</u>arran wal<u>aa</u> naf\'an ill<u>aa</u> m<u>aa</u> sy<u>aa</u>-all<u>aa</u>hu likulli ummatin ajalun i<u>dzaa</u> j<u>aa</u>-a ajaluhum fal<u>aa</u> yasta/khiruuna s<u>aa</u>\'atan wal<u>aa</u> yastaqdimuun\r\r\n');
INSERT INTO quranLatin VALUES (1414, '10:50', 10, 50, 'qul ara-aytum in at<u>aa</u>kum \'a<u>dzaa</u>buhu bay<u>aa</u>tan aw nah<u>aa</u>ran m<u>aatsaa</u> yasta\'jilu minhu lmujrimuun\r\r\n');
INSERT INTO quranLatin VALUES (1415, '10:51', 10, 51, 'atsumma i<u>dzaa</u> m<u>aa</u> waqa\'a <u>aa</u>mantum bihi <u>aa</u>l-<u>aa</u>na waqad kuntum bihi tasta\'jiluun\r\r\n');
INSERT INTO quranLatin VALUES (1416, '10:52', 10, 52, 'tsumma qiila lilla<u>dz</u>iina <u>zh</u>alamuu <u>dz</u>uuquu \'a<u>dzaa</u>ba lkhuldi hal tujzawna ill<u>aa</u> bim<u>aa</u> kuntum taksibuun\r\r\n');
INSERT INTO quranLatin VALUES (1417, '10:53', 10, 53, 'wayastanbi-uunaka a<u>h</u>aqqun huwa qul ii warabbii innahu la<u>h</u>aqqun wam<u>aa</u> antum bimu\'jiziin\r\r\n');
INSERT INTO quranLatin VALUES (1418, '10:54', 10, 54, 'walaw anna likulli nafsin <u>zh</u>alamat m<u>aa</u> fii l-ar<u>dh</u>i laftadat bihi wa-asarruu nnad<u>aa</u>mata lamm<u>aa</u> ra-awuu l\'a<u>dzaa</u>ba waqu<u>dh</u>iya baynahum bilqis<u>th</u>i wahum l<u>aa</u> yu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1419, '10:55', 10, 55, 'al<u>aa</u> inna lill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i l<u>aa</u> inna wa\'dall<u>aa</u>hi <u>h</u>aqqun wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1420, '10:56', 10, 56, 'huwa yu<u>h</u>yii wayumiitu wa-ilayhi turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1421, '10:57', 10, 57, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su qad j<u>aa</u>-atkum maw\'i<u>zh</u>atun min rabbikum wasyif<u>aa</u>un lim<u>aa</u> fii <u>shsh</u>uduuri wahudan wara<u>h</u>matun lilmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1422, '10:58', 10, 58, 'qul bifa<u>dh</u>lill<u>aa</u>hi wabira<u>h</u>matihi fabi<u>dzaa</u>lika falyafra<u>h</u>uu huwa khayrun mimm<u>aa</u> yajma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1423, '10:59', 10, 59, 'qul ara-aytum m<u>aa</u> anzalall<u>aa</u>hu lakum min rizqin faja\'altum minhu <u>h</u>ar<u>aa</u>man wa<u>h</u>al<u>aa</u>lan qul <u>aa</u>ll<u>aa</u>hu a<u>dz</u>ina lakum am \'al<u>aa</u>ll<u>aa</u>hi taftaruun\r\r\n');
INSERT INTO quranLatin VALUES (1424, '10:60', 10, 60, 'wam<u>aa</u> <u>zh</u>annulla<u>dz</u>iina yaftaruuna \'al<u>aa</u>ll<u>aa</u>hi lka<u>dz</u>iba yawma lqiy<u>aa</u>mati innall<u>aa</u>ha la<u>dz</u>uu fa<u>dh</u>lin \'al<u>aa</u> nn<u>aa</u>si wal<u>aa</u>kinna aktsarahum l<u>aa</u> yasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (1425, '10:61', 10, 61, 'wam<u>aa</u> takuunu fii sya/nin wam<u>aa</u> tatluu minhu min qur-<u>aa</u>nin wal<u>aa</u> ta\'maluuna min \'amalin ill<u>aa</u> kunn<u>aa</u> \'alaykum syuhuudan i<u>dz</u> tufii<u>dh</u>uuna fiihi wam<u>aa</u> ya\'zubu \'an rabbika min mitsq<u>aa</u>li <u>dz</u>arratin fii l-ar<u>dh</u>i wal<u>aa</u> fii ssam<u>aa</u>-i wal<u>aa</u> a<u>sh</u>ghara min <u>dzaa</u>lika wal<u>aa</u> akbara ill<u>aa</u> fii kit<u>aa</u>bin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1426, '10:62', 10, 62, 'al<u>aa</u> inna awliy<u>aa</u>-all<u>aa</u>hi l<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (1427, '10:63', 10, 63, '<B>al</b>la<u>dz</u>iina <u>aa</u>manuu wak<u>aa</u>nuu yattaquun<b>a </b>\r\r\n');
INSERT INTO quranLatin VALUES (1428, '10:64', 10, 64, 'lahumu lbusyr<u>aa</u> fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wafii l-<u>aa</u>khirati l<u>aa</u> tabdiila likalim<u>aa</u>till<u>aa</u>hi <u>dzaa</u>lika huwa lfawzu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1429, '10:65', 10, 65, 'wal<u>aa</u> ya<u>h</u>zunka qawluhum inna l\'izzata lill<u>aa</u>hi jamii\'an huwa ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1430, '10:66', 10, 66, 'al<u>aa</u> inna lill<u>aa</u>hi man fii ssam<u>aa</u>w<u>aa</u>ti waman fii l-ar<u>dh</u>i wam<u>aa</u> yattabi\'ulla<u>dz</u>iina yad\'uuna min duunill<u>aa</u>hi syurak<u>aa</u>-a in yattabi\'uuna ill<u>aa</u> <u>zhzh</u>anna wa-in hum ill<u>aa</u> yakhru<u>sh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1431, '10:67', 10, 67, 'huwalla<u>dz</u>ii ja\'ala lakumullayla litaskunuu fiihi wannah<u>aa</u>ra mub<u>sh</u>iran inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yasma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1432, '10:68', 10, 68, 'q<u>aa</u>luu ittakha<u>dz</u>all<u>aa</u>hu waladan sub<u>haa</u>nahu huwa lghaniyyu lahu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i in \'indakum min sul<u>th</u><u>aa</u>nin bih<u>aadzaa</u> ataquuluuna \'al<u>aa</u>ll<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1433, '10:69', 10, 69, 'qul innalla<u>dz</u>iina yaftaruuna \'al<u>aa</u>ll<u>aa</u>hi lka<u>dz</u>iba l<u>aa</u> yufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1434, '10:70', 10, 70, 'mat<u>aa</u>\'un fii dduny<u>aa</u> tsumma ilayn<u>aa</u> marji\'uhum tsumma nu<u>dz</u>iiquhumu l\'a<u>dzaa</u>ba sysyadiida bim<u>aa</u> k<u>aa</u>nuu yakfuruun\r\r\n');
INSERT INTO quranLatin VALUES (1435, '10:71', 10, 71, 'watlu \'alayhim naba-a nuu<u>h</u>in i<u>dz</u> q<u>aa</u>la liqawmihi y<u>aa</u> qawmi in k<u>aa</u>na kabura \'alaykum maq<u>aa</u>mii wata<u>dz</u>kiirii bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi fa\'al<u>aa</u>ll<u>aa</u>hi tawakkaltu fa-ajmi\'uu amrakum wasyurak<u>aa</u>-akum tsumma l<u>aa</u> yakun amrukum \'alaykum ghummatan tsumma iq<u>dh</u>uu ilayya wal<u>aa</u> tun<u>zh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (1436, '10:72', 10, 72, 'fa-in tawallaytum fam<u>aa</u> sa-altukum min ajrin in ajriya ill<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi waumirtu an akuuna mina lmuslimiin\r\r\n');
INSERT INTO quranLatin VALUES (1437, '10:73', 10, 73, 'faka<u>dzdz</u>abuuhu fanajjayn<u>aa</u>hu waman ma\'ahu fii lfulki waja\'aln<u>aa</u>hum khal<u>aa</u>-ifa wa-aghraqn<u>aa</u>lla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fan<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu lmun<u>dz</u>ariin\r\r\n');
INSERT INTO quranLatin VALUES (1438, '10:74', 10, 74, 'tsumma ba\'atsn<u>aa</u> min ba\'dihi rusulan il<u>aa</u> qawmihim faj<u>aa</u>uuhum bilbayyin<u>aa</u>ti fam<u>aa</u> k<u>aa</u>nuu liyu/minuu bim<u>aa</u> ka<u>dzdz</u>abuu bihi min qablu ka<u>dzaa</u>lika na<u>th</u>ba\'u \'al<u>aa</u> quluubi lmu\'tadiin\r\r\n');
INSERT INTO quranLatin VALUES (1439, '10:75', 10, 75, 'tsumma ba\'atsn<u>aa</u> min ba\'dihim muus<u>aa</u> wah<u>aa</u>ruuna il<u>aa</u> fir\'awna wamala-ihi bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fastakbaruu wak<u>aa</u>nuu qawman mujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (1440, '10:76', 10, 76, 'falamm<u>aa</u> j<u>aa</u>-ahumu l<u>h</u>aqqu min \'indin<u>aa</u> q<u>aa</u>luu inna h<u>aadzaa</u> lasi<u>h</u>run mubiin<b>un </b>\r\r\n');
INSERT INTO quranLatin VALUES (1441, '10:77', 10, 77, 'q<u>aa</u>la muus<u>aa</u> ataquuluuna lil<u>h</u>aqqi lamm<u>aa</u> j<u>aa</u>-akum asi<u>h</u>run h<u>aadzaa</u> wal<u>aa</u> yufli<u>h</u>u ss<u>aah</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (1442, '10:78', 10, 78, 'q<u>aa</u>luu aji/tan<u>aa</u> litalfitan<u>aa</u> \'amm<u>aa</u> wajadn<u>aa</u> \'alayhi <u>aa</u>b<u>aa</u>-an<u>aa</u> watakuuna lakum<u>aa</u> lkibriy<u>aa</u>u fii l-ar<u>dh</u>i wam<u>aa</u> na<u>h</u>nu lakum<u>aa</u> bimu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1443, '10:79', 10, 79, 'waq<u>aa</u>la fir\'awnu i/tuunii bikulli s<u>aah</u>irin \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1444, '10:80', 10, 80, 'falamm<u>aa</u> j<u>aa</u>-a ssa<u>h</u>aratu q<u>aa</u>la lahum muus<u>aa</u> lquu m<u>aa</u> antum mulquun<b>a </b>\r\r\n');
INSERT INTO quranLatin VALUES (1445, '10:81', 10, 81, 'falamm<u>aa</u> lqaw q<u>aa</u>la muus<u>aa</u> m<u>aa</u> ji/tum bihi ssi<u>h</u>ru innall<u>aa</u>ha sayub<u>th</u>iluhu innall<u>aa</u>ha l<u>aa</u> yu<u>sh</u>li<u>h</u>u \'amala lmufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (1446, '10:82', 10, 82, 'wayu<u>h</u>iqqull<u>aa</u>hu l<u>h</u>aqqa bikalim<u>aa</u>tihi walaw kariha lmujrimuun\r\r\n');
INSERT INTO quranLatin VALUES (1447, '10:83', 10, 83, 'fam<u>aa</u> <u>aa</u>mana limuus<u>aa</u> ill<u>aa</u> <u>dz</u>urriyyatun min qawmihi \'al<u>aa</u> khawfin min fir\'awna wamala-ihim an yaftinahum wa-inna fir\'awna la\'<u>aa</u>lin fii l-ar<u>dh</u>i wa-innahu lamina lmusrifiin\r\r\n');
INSERT INTO quranLatin VALUES (1448, '10:84', 10, 84, 'waq<u>aa</u>la muus<u>aa</u> y<u>aa</u> qawmi in kuntum <u>aa</u>mantum bil<u>aa</u>hi fa\'alayhi tawakkaluu in kuntum muslimiin\r\r\n');
INSERT INTO quranLatin VALUES (1449, '10:85', 10, 85, 'faq<u>aa</u>luu \'al<u>aa</u>ll<u>aa</u>hi tawakkaln<u>aa</u> rabban<u>aa</u> l<u>aa</u> taj\'aln<u>aa</u> fitnatan lilqawmi <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1450, '10:86', 10, 86, 'wanajjin<u>aa</u> bira<u>h</u>matika mina lqawmi lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1451, '10:87', 10, 87, 'wa-aw<u>h</u>ayn<u>aa</u> il<u>aa</u> muus<u>aa</u> wa-akhiihi an tabawwa<u>aa</u> liqawmikum<u>aa</u> bimi<u>sh</u>ra buyuutan waj\'aluu buyuutakum qiblatan wa-aqiimuu <u>shsh</u>al<u>aa</u>ta wabasysyiri lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1452, '10:88', 10, 88, 'waq<u>aa</u>la muus<u>aa</u> rabban<u>aa</u> innaka <u>aa</u>tayta fir\'awna wamala-ahu ziinatan wa-amw<u>aa</u>lan fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> rabban<u>aa</u> liyu<u>dh</u>illuu \'an sabiilika rabban<u>aa</u> i<u>th</u>mis \'al<u>aa</u> amw<u>aa</u>lihim wasydud \'al<u>aa</u> quluubihim fal<u>aa</u> yu/minuu <u>h</u>att<u>aa</u> yarawuu l\'a<u>dzaa</u>ba l-aliim\r\r\n');
INSERT INTO quranLatin VALUES (1453, '10:89', 10, 89, 'q<u>aa</u>la qad ujiibat da\'watukum<u>aa</u> fastaqiim<u>aa</u> wal<u>aa</u> tattabi\'<u>aa</u>nni sabiilalla<u>dz</u>iina l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1454, '10:90', 10, 90, 'waj<u>aa</u>wazn<u>aa</u> bibanii isr<u>aa</u>-iila lba<u>h</u>ra fa-atba\'ahum fir\'awnu wajunuuduhu baghyan wa\'adwan <u>h</u>att<u>aa</u> i<u>dzaa</u> adrakahu lgharaqu q<u>aa</u>la <u>aa</u>mantu annahu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u>lla<u>dz</u>ii <u>aa</u>manat bihi banuu isr<u>aa</u>-iila wa-an<u>aa</u> mina lmuslimiin\r\r\n');
INSERT INTO quranLatin VALUES (1455, '10:91', 10, 91, '<u>aa</u>l-<u>aa</u>na waqad \'a<u>sh</u>ayta qablu wakunta mina lmufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (1456, '10:92', 10, 92, 'falyawma nunajjiika bibadanika litakuuna liman khalfaka <u>aa</u>yatan wa-inna katsiiran mina nn<u>aa</u>si \'an <u>aa</u>y<u>aa</u>tin<u>aa</u> lagh<u>aa</u>filuun\r\r\n');
INSERT INTO quranLatin VALUES (1457, '10:93', 10, 93, 'walaqad bawwa/n<u>aa</u> banii isr<u>aa</u>-iila mubawwa-a <u>sh</u>idqin warazaqn<u>aa</u>hum mina <u>ththh</u>ayyib<u>aa</u>ti fam<u>aa</u> ikhtalafuu <u>h</u>att<u>aa</u> j<u>aa</u>-ahumu l\'ilmu inna rabbaka yaq<u>dh</u>ii baynahum yawma lqiy<u>aa</u>mati fiim<u>aa</u> k<u>aa</u>nuu fiihi yakhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (1458, '10:94', 10, 94, 'fa-in kunta fii syakkin mimm<u>aa</u> anzalnn<u>aa</u> ilayka fas-alilla<u>dz</u>iina yaqrauuna lkit<u>aa</u>ba min qablika laqad j<u>aa</u>-aka l<u>h</u>aqqu min rabbika fal<u>aa</u> takuunanna mina lmumtariin\r\r\n');
INSERT INTO quranLatin VALUES (1459, '10:95', 10, 95, 'wal<u>aa</u> takuunanna minalla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi fatakuuna mina lkh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (1460, '10:96', 10, 96, 'innalla<u>dz</u>iina <u>h</u>aqqat \'alayhim kalimatu rabbika l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1461, '10:97', 10, 97, 'walaw j<u>aa</u>-at-hum kullu <u>aa</u>yatin <u>h</u>att<u>aa</u> yarawuu l\'a<u>dzaa</u>ba l-aliim\r\r\n');
INSERT INTO quranLatin VALUES (1462, '10:98', 10, 98, 'falawl<u>aa</u> k<u>aa</u>nat qaryatun <u>aa</u>manat fanafa\'ah<u>aa</u> iim<u>aa</u>nuh<u>aa</u> ill<u>aa</u> qawma yuunusa lamm<u>aa</u> <u>aa</u>manuu kasyafn<u>aa</u> \'anhum \'a<u>dzaa</u>ba lkhizyi fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wamatta\'n<u>aa</u>hum il<u>aa</u> <u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1463, '10:99', 10, 99, 'walaw sy<u>aa</u>-a rabbuka la<u>aa</u>mana man fii l-ar<u>dh</u>i kulluhum jamii\'an afa-anta tukrihu nn<u>aa</u>sa <u>h</u>att<u>aa</u> yakuunuu mu/miniin<b>a </b>\r\r\n');
INSERT INTO quranLatin VALUES (1464, '10:100', 10, 100, 'wam<u>aa</u> k<u>aa</u>na linafsin an tu/mina ill<u>aa</u> bi-i<u>dz</u>nill<u>aa</u>hi wayaj\'alu rrijsa \'al<u>aa</u>lla<u>dz</u>iina l<u>aa</u> ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (1465, '10:101', 10, 101, 'quli un<u>zh</u>uruu m<u>aatsaa</u> fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wam<u>aa</u> tughnii l-<u>aa</u>y<u>aa</u>tu wannu<u>dz</u>uru \'an qawmin l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1466, '10:102', 10, 102, 'fahal yanta<u>zh</u>iruuna ill<u>aa</u> mitsla ayy<u>aa</u>milla<u>dz</u>iina khalaw min qablihim qul fanta<u>zh</u>iruu innii ma\'akum mina lmunta<u>zh</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (1467, '10:103', 10, 103, 'tsumma nunajjii rusulan<u>aa</u> walla<u>dz</u>iina <u>aa</u>manuu ka<u>dzaa</u>lika <u>h</u>aqqan \'alayn<u>aa</u> nunjii lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1468, '10:104', 10, 104, 'qul y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su in kuntum fii syakkin min diinii fal<u>aa</u> a\'budulla<u>dz</u>iina ta\'buduuna min duunill<u>aa</u>hi wal<u>aa</u>kin a\'budull<u>aa</u>halla<u>dz</u>ii yatawaff<u>aa</u>kum waumirtu an akuuna mina lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1469, '10:105', 10, 105, 'wa-an aqim wajhaka liddiini <u>h</u>aniifan wal<u>aa</u> takuunanna mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (1470, '10:106', 10, 106, 'wal<u>aa</u> tad\'u min duunill<u>aa</u>hi m<u>aa</u> l<u>aa</u> yanfa\'uka wal<u>aa</u> ya<u>dh</u>urruka fa-in fa\'alta fa-innaka i<u>dz</u>an mina <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1471, '10:107', 10, 107, 'wa-in yamsaskall<u>aa</u>hu bi<u>dh</u>urrin fal<u>aa</u> k<u>aa</u>syifa lahu ill<u>aa</u> huwa wa-in yuridka bikhayrin fal<u>aa</u> r<u>aa</u>dda lifa<u>dh</u>lihi yu<u>sh</u>iibu bihi man yasy<u>aa</u>u min \'ib<u>aa</u>dihi wahuwa lghafuuru rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1472, '10:108', 10, 108, 'qul y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su qad j<u>aa</u>-akumu l<u>h</u>aqqu min rabbikum famani ihtad<u>aa</u> fa-innam<u>aa</u> yahtadii linafsihi waman <u>dh</u>alla fa-innam<u>aa</u> ya<u>dh</u>illu \'alayh<u>aa</u> wam<u>aa</u> an<u>aa</u> \'alaykum biwakiil\r\r\n');
INSERT INTO quranLatin VALUES (1473, '10:109', 10, 109, 'wattabi\'m<u>aa</u> yuu<u>haa</u> ilayka wa<u>sh</u>bir <u>h</u>att<u>aa</u> ya<u>h</u>kumall<u>aa</u>hu wahuwa khayru l<u>haa</u>kimiin\r\r\n');
INSERT INTO quranLatin VALUES (1474, '11:1', 11, 1, 'alif-l<u>aa</u>m-r<u>aa</u> kit<u>aa</u>bun u<u>h</u>kimat <u>aa</u>y<u>aa</u>tuhu tsumma fu<u>shsh</u>ilat min ladun <u>h</u>akiimin khabiir\r\r\n');
INSERT INTO quranLatin VALUES (1475, '11:2', 11, 2, '<B>al</b>l<u>aa</u> ta\'buduu ill<u>aa</u>ll<u>aa</u>ha innanii lakum minhu na<u>dz</u>iirun wabasyiir\r\r\n');
INSERT INTO quranLatin VALUES (1476, '11:3', 11, 3, 'wa-ani istaghfiruu rabbakum tsumma tuubuu ilayhi yumatti\'kum mat<u>aa</u>\'an <u>h</u>asanan il<u>aa</u> ajalin musamman wayu/ti kulla <u>dz</u>ii fa<u>dh</u>lin fa<u>dh</u>lahu wa-in tawallaw fa-inii akh<u>aa</u>fu \'alaykum \'a<u>dzaa</u>ba yawmin kabiir\r\r\n');
INSERT INTO quranLatin VALUES (1477, '11:4', 11, 4, 'il<u>aa</u>ll<u>aa</u>hi marji\'ukum wahuwa \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (1478, '11:5', 11, 5, 'al<u>aa</u> innahum yatsnuuna <u>sh</u>uduurahum liyastakhfuu minhu l<u>aa</u> <u>h</u>iina yastaghsyuuna tsiy<u>aa</u>bahum ya\'lamu m<u>aa</u> yusirruuna wam<u>aa</u> yu\'linuuna innahu \'aliimun bi<u>dzaa</u>ti <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (1479, '11:6', 11, 6, 'wam<u>aa</u> min d<u>aa</u>bbatin fii l-ar<u>dh</u>i ill<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi rizquh<u>aa</u> waya\'lamu mustaqarrah<u>aa</u> wamustawda\'ah<u>aa</u> kullun fii kit<u>aa</u>bin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1480, '11:7', 11, 7, 'wahuwalla<u>dz</u>ii khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a fii sittati ayy<u>aa</u>min wak<u>aa</u>na \'arsyuhu \'al<u>aa</u> lm<u>aa</u>-i liyabluwakum ayyukum a<u>h</u>sanu \'amalan wala-in qulta innakum mab\'uutsuuna min ba\'di lmawti layaquulannalla<u>dz</u>iina kafaruu in h<u>aadzaa</u> ill<u>aa</u> si<u>h</u>run mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1481, '11:8', 11, 8, 'wala-in akhkharn<u>aa</u> \'anhumu l\'a<u>dzaa</u>ba il<u>aa</u> ummatin ma\'duudatin layaquulunna m<u>aa</u> ya<u>h</u>bisuhu l<u>aa</u> yawma ya/tiihim laysa ma<u>sh</u>ruufan \'anhum wa<u>haa</u>qa bihim m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (1482, '11:9', 11, 9, 'wala-in a<u>dz</u>aqn<u>aa</u> l-ins<u>aa</u>na minn<u>aa</u> ra<u>h</u>matan tsumma naza\'n<u>aa</u>h<u>aa</u> minhu innahu layauusun kafuur\r\r\n');
INSERT INTO quranLatin VALUES (1483, '11:10', 11, 10, 'wala-in a<u>dz</u>aqn<u>aa</u>hu na\'m<u>aa</u>-a ba\'da <u>dh</u>arr<u>aa</u>-a massat-hu layaquulanna <u>dz</u>ahaba ssayyi-<u>aa</u>tu \'annii innahu lafari<u>h</u>un fakhuur\r\r\n');
INSERT INTO quranLatin VALUES (1484, '11:11', 11, 11, 'ill<u>aa</u>lla<u>dz</u>iina <u>sh</u>abaruu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti ul<u>aa</u>-ika lahum maghfiratun wa-ajrun kabiir\r\r\n');
INSERT INTO quranLatin VALUES (1485, '11:12', 11, 12, 'fala\'allaka t<u>aa</u>rikun ba\'<u>dh</u>a m<u>aa</u> yuu<u>haa</u> ilayka wa<u>daa</u>-iqun bihi <u>sh</u>adruka an yaquuluu lawl<u>aa</u> unzila \'alayhi kanzun aw j<u>aa</u>-a ma\'ahu malakun innam<u>aa</u> anta na<u>dz</u>iirun wal<u>aa</u>hu \'al<u>aa</u> kulli syay-in wakiil\r\r\n');
INSERT INTO quranLatin VALUES (1486, '11:13', 11, 13, 'am yaquuluuna iftar<u>aa</u>hu qul fa/tuu bi\'asyri suwarin mitslihi muftaray<u>aa</u>tin wad\'uu mani ista<u>th</u>a\'tum min duunill<u>aa</u>hi in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (1487, '11:14', 11, 14, 'fa-illam yastajiibuu lakum fa\'lamuu annam<u>aa</u> unzila bi\'ilmill<u>aa</u>hi wa-an l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa fahal antum muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (1488, '11:15', 11, 15, 'man k<u>aa</u>na yuriidu l<u>h</u>ay<u>aa</u>ta dduny<u>aa</u> waziinatah<u>aa</u> nuwaffi ilayhim a\'m<u>aa</u>lahum fiih<u>aa</u> wahum fiih<u>aa</u> l<u>aa</u> yubkhasuun\r\r\n');
INSERT INTO quranLatin VALUES (1489, '11:16', 11, 16, 'ul<u>aa</u>-ikalla<u>dz</u>iina laysa lahum fii l-<u>aa</u>khirati ill<u>aa</u> nn<u>aa</u>ru wa<u>h</u>abi<u>th</u>a m<u>aa</u> <u>sh</u>ana\'uu fiih<u>aa</u> wab<u>aath</u>ilun m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1490, '11:17', 11, 17, 'afaman k<u>aa</u>na \'al<u>aa</u> bayyinatin min rabbihi wayatluuhu sy<u>aa</u>hidun minhu wamin qablihi kit<u>aa</u>bu muus<u>aa</u> im<u>aa</u>man wara<u>h</u>matan ul<u>aa</u>-ika yu/minuuna bihi waman yakfur bihi mina l-a<u>h</u>z<u>aa</u>bi fann<u>aa</u>ru maw\'iduhu fal<u>aa</u> taku fii miryatin minhu innahu l<u>h</u>aqqu min rabbika wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1491, '11:18', 11, 18, 'waman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>iban ul<u>aa</u>-ika yu\'ra<u>dh</u>uuna \'al<u>aa</u> rabbihim wayaquulu l-asyh<u>aa</u>du h<u>aa</u>ul<u>aa</u>-illa<u>dz</u>iina ka<u>dz</u>abuu \'al<u>aa</u> rabbihim l<u>aa</u> la\'natull<u>aa</u>hi \'al<u>aa</u> <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1492, '11:19', 11, 19, '<B>al</b>la<u>dz</u>iina ya<u>sh</u>udduuna \'an sabiilill<u>aa</u>hi wayabghuunah<u>aa</u> \'iwajan wahum bil-<u>aa</u>khirati hum k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (1493, '11:20', 11, 20, 'ul<u>aa</u>-ika lam yakuunuu mu\'jiziina fii l-ar<u>dh</u>i wam<u>aa</u> k<u>aa</u>na lahum min duunill<u>aa</u>hi min awliy<u>aa</u>-a yu<u>daa</u>\'afu lahumu l\'a<u>dzaa</u>bu m<u>aa</u> k<u>aa</u>nuu yasta<u>th</u>ii\'uuna ssam\'a wam<u>aa</u> k<u>aa</u>nuu yub<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (1494, '11:21', 11, 21, 'ul<u>aa</u>-ikalla<u>dz</u>iina khasiruu anfusahum wa<u>dh</u>alla \'anhum m<u>aa</u> k<u>aa</u>nuu yaftaruun\r\r\n');
INSERT INTO quranLatin VALUES (1495, '11:22', 11, 22, 'l<u>aa</u> jarama annahum fii l-<u>aa</u>khirati humu l-akhsaruun\r\r\n');
INSERT INTO quranLatin VALUES (1496, '11:23', 11, 23, 'innalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shaa</u>li<u>haa</u>ti wa-akhbatuu il<u>aa</u> rabbihim ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu ljannati hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (1497, '11:24', 11, 24, 'matsalu lfariiqayni kal<b>-</b>a\'m<u>aa</u> wal-a<u>sh</u>ammi walba<u>sh</u>iiri wassamii\'i hal yastawiy<u>aa</u>ni matsalan afal<u>aa</u> ta<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1498, '11:25', 11, 25, 'walaqad arsaln<u>aa</u> nuu<u>h</u>an il<u>aa</u> qawmihi innii lakum na<u>dz</u>iirun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1499, '11:26', 11, 26, 'an l<u>aa</u> ta\'buduu ill<u>aa</u>ll<u>aa</u>ha innii akh<u>aa</u>fu \'alaykum \'a<u>dzaa</u>ba yawmin liim\r\r\n');
INSERT INTO quranLatin VALUES (1500, '11:27', 11, 27, 'faq<u>aa</u>la lmalaulla<u>dz</u>iina kafaruu min qawmihi m<u>aa</u> nar<u>aa</u>ka ill<u>aa</u> basyaran mitslan<u>aa</u> wam<u>aa</u> nar<u>aa</u>ka ittaba\'aka ill<u>aa</u>lla<u>dz</u>iina hum ar<u>aadz</u>ilun<u>aa</u> b<u>aa</u>diya rra/yi wam<u>aa</u> nar<u>aa</u> lakum \'alayn<u>aa</u> min fa<u>dh</u>lin bal na<u>zh</u>unnukum k<u>aadz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (1501, '11:28', 11, 28, 'q<u>aa</u>la y<u>aa</u> qawmi ara-aytum in kuntu \'al<u>aa</u> bayyinatin min rabbii wa<u>aa</u>t<u>aa</u>nii ra<u>h</u>matan min \'indihi fa\'ummiyat \'alaykum anulzimukumuuh<u>aa</u> wa-antum lah<u>aa</u> k<u>aa</u>rihuun\r\r\n');
INSERT INTO quranLatin VALUES (1502, '11:29', 11, 29, 'way<u>aa</u> qawmi l<u>aa</u> as-alukum \'alayhi m<u>aa</u>lan in ajriya ill<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi wam<u>aa</u> an<u>aa</u> bi<u>th</u><u>aa</u>ridilla<u>dz</u>iina <u>aa</u>manuu innahum mul<u>aa</u>quu rabbihim wal<u>aa</u>kinnii ar<u>aa</u>kum qawman tajhaluun\r\r\n');
INSERT INTO quranLatin VALUES (1503, '11:30', 11, 30, 'way<u>aa</u> qawmi man yan<u>sh</u>urunii minall<u>aa</u>hi in <u>th</u>aradtuhum afal<u>aa</u> ta<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1504, '11:31', 11, 31, 'wal<u>aa</u> aquulu lakum \'indii khaz<u>aa</u>-inull<u>aa</u>hi wal<u>aa</u> a\'lamu lghayba wal<u>aa</u> aquulu innii malakun wal<u>aa</u> aquulu lilla<u>dz</u>iina tazdarii a\'yunukum lan yu/tiyahumull<u>aa</u>hu khayranll<u>aa</u>hu a\'lamu bim<u>aa</u> fii anfusihim innii i<u>dz</u>an lamina <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1505, '11:32', 11, 32, 'q<u>aa</u>luu y<u>aa</u> nuu<u>h</u>u qad j<u>aa</u>daltan<u>aa</u> fa-aktsarta jid<u>aa</u>lan<u>aa</u> fa/tin<u>aa</u> bim<u>aa</u> ta\'idun<u>aa</u> in kunta mina <u>shshaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (1506, '11:33', 11, 33, 'q<u>aa</u>la innam<u>aa</u> ya/tiikum bihill<u>aa</u>hu in sy<u>aa</u>-a wam<u>aa</u> antum bimu\'jiziin\r\r\n');
INSERT INTO quranLatin VALUES (1507, '11:34', 11, 34, 'wal<u>aa</u> yanfa\'ukum nu<u>sy</u>ii in aradtu an an<u>sh</u>a<u>h</u>a lakum in k<u>aa</u>nall<u>aa</u>hu yuriidu an yughwiyakum huwa rabbukum wa-ilayhi turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1508, '11:35', 11, 35, 'am yaquuluuna iftar<u>aa</u>hu qul ini iftaraytuhu fa\'alayya ijr<u>aa</u>mii wa-an<u>aa</u> barii-un mimm<u>aa</u> tujrimuun\r\r\n');
INSERT INTO quranLatin VALUES (1509, '11:36', 11, 36, 'wauu<u>h</u>iya il<u>aa</u> nuu<u>h</u>in annahu lan yu/mina min qawmika ill<u>aa</u> man qad <u>aa</u>mana fal<u>aa</u> tabta-is bim<u>aa</u> k<u>aa</u>nuu yaf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (1510, '11:37', 11, 37, 'wa<u>sh</u>na\'i lfulka bi-a\'yunin<u>aa</u> wawa<u>h</u>yin<u>aa</u> wal<u>aa</u> tukh<u>aath</u>ibnii fiilla<u>dz</u>iina <u>zh</u>alamuu innahum mughraquun\r\r\n');
INSERT INTO quranLatin VALUES (1511, '11:38', 11, 38, 'waya<u>sh</u>na\'u lfulka wakullam<u>aa</u> marra \'alayhi malaun min qawmihi sakhiruu minhu q<u>aa</u>la in taskharuu minn<u>aa</u> fa-inn<u>aa</u> naskharu minkum kam<u>aa</u> taskharuun\r\r\n');
INSERT INTO quranLatin VALUES (1512, '11:39', 11, 39, 'fasawfa ta\'lamuuna man ya/tiihi \'a<u>dzaa</u>bun yukhziihi waya<u>h</u>illu \'alayhi \'a<u>dzaa</u>bun muqiim\r\r\n');
INSERT INTO quranLatin VALUES (1513, '11:40', 11, 40, '<u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-a amrun<u>aa</u> waf<u>aa</u>ra ttannuuru quln<u>aa</u> i<u>h</u>mil fiih<u>aa</u> min kullin zawjayni itsnayni wa-ahlaka ill<u>aa</u> man sabaqa \'alayhi lqawlu waman <u>aa</u>mana wam<u>aa</u> <u>aa</u>mana ma\'ahu ill<u>aa</u> qaliil\r\r\n');
INSERT INTO quranLatin VALUES (1514, '11:41', 11, 41, 'waq<u>aa</u>la irkabuu fiih<u>aa</u> bismill<u>aa</u>hi majr<u>aa</u>h<u>aa</u> wamurs<u>aa</u>h<u>aa</u> inna rabbii laghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1515, '11:42', 11, 42, 'wahiya tajrii bihim fii mawjin kaljib<u>aa</u>li wan<u>aa</u>d<u>aa</u> nuu<u>h</u>unu ibnahu wak<u>aa</u>na fii ma\'zilin y<u>aa</u> bunayya irkab ma\'an<u>aa</u> wal<u>aa</u> takun ma\'a lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1516, '11:43', 11, 43, 'q<u>aa</u>la sa<u>aa</u>wii il<u>aa</u> jabalin ya\'<u>sh</u>imunii mina lm<u>aa</u>-i q<u>aa</u>la l<u>aa</u> \'<u>aas</u>ima lyawma min amrill<u>aa</u>hi ill<u>aa</u> man ra<u>h</u>ima wa<u>haa</u>la baynahum<u>aa</u> lmawju fak<u>aa</u>na mina lmughraqiin\r\r\n');
INSERT INTO quranLatin VALUES (1517, '11:44', 11, 44, 'waqiila y<u>aa</u> ar<u>dh</u>u ibla\'ii m<u>aa</u>-aki way<u>aa</u> sam<u>aa</u>u aqli\'ii waghii<u>dh</u>a lm<u>aa</u>u waqu<u>dh</u>iya l-amru wastawat \'al<u>aa</u> ljuudiyyi waqiila bu\'dan lilqawmi <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1518, '11:45', 11, 45, 'wan<u>aa</u>d<u>aa</u> nuu<u>h</u>un rabbahu faq<u>aa</u>la rabbi inna ibnii min ahlii wa-inna wa\'daka l<u>h</u>aqqu wa-anta a<u>h</u>kamu l<u>haa</u>kimiin\r\r\n');
INSERT INTO quranLatin VALUES (1519, '11:46', 11, 46, 'q<u>aa</u>la y<u>aa</u> nuu<u>h</u>u innahu laysa min ahlika innahu \'amalun ghayru <u>shaa</u>li<u>h</u>in fal<u>aa</u> tas-alni m<u>aa</u> laysa laka bihi \'ilmun innii a\'i<u>zh</u>uka an takuuna mina lj<u>aa</u>hiliin\r\r\n');
INSERT INTO quranLatin VALUES (1520, '11:47', 11, 47, 'q<u>aa</u>la rabbi innii a\'uu<u>dz</u>u bika an as-alaka m<u>aa</u> laysa lii bihi \'ilmun wa-ill<u>aa</u> taghfir lii watar<u>h</u>amnii akun mina lkh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (1521, '11:48', 11, 48, 'qiila y<u>aa</u> nuu<u>h</u>u ihbi<u>th</u> bisal<u>aa</u>min minn<u>aa</u> wabarak<u>aa</u>tin \'alayka wa\'al<u>aa</u> umamin mimman ma\'aka waumamun sanumatti\'uhum tsumma yamassuhum minn<u>aa</u> \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (1522, '11:49', 11, 49, 'tilka min anb<u>aa</u>-i lghaybi nuu<u>h</u>iih<u>aa</u> ilayka m<u>aa</u> kunta ta\'lamuh<u>aa</u> anta wal<u>aa</u> qawmuka min qabli h<u>aadzaa</u> fa<u>sh</u>bir inna l\'<u>aa</u>qibata lilmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (1523, '11:50', 11, 50, 'wa-il<u>aa</u> \'<u>aa</u>din akh<u>aa</u>hum huudan q<u>aa</u>la y<u>aa</u> qawmi u\'buduull<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu in antum ill<u>aa</u> muftaruun\r\r\n');
INSERT INTO quranLatin VALUES (1524, '11:51', 11, 51, 'y<u>aa</u> qawmi l<u>aa</u> as-alukum \'alayhi ajran in ajriya ill<u>aa</u> \'al<u>aa</u>lla<u>dz</u>ii fa<u>th</u>aranii afal<u>aa</u> ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (1525, '11:52', 11, 52, 'way<u>aa</u> qawmi istaghfiruu rabbakum tsumma tuubuu ilayhi yursili ssam<u>aa</u>-a \'alaykum midr<u>aa</u>ran wayazidkum quwwatan il<u>aa</u> quwwatikum wal<u>aa</u> tatawallaw mujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (1526, '11:53', 11, 53, 'q<u>aa</u>luu y<u>aa</u> huudu m<u>aa</u> ji/tan<u>aa</u> bibayyinatin wam<u>aa</u> na<u>h</u>nu bit<u>aa</u>rikii <u>aa</u>lihatin<u>aa</u> \'an qawlika wam<u>aa</u> na<u>h</u>nu laka bimu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1527, '11:54', 11, 54, 'in naquulu ill<u>aa</u> i\'tar<u>aa</u>ka ba\'<u>dh</u>u <u>aa</u>lihatin<u>aa</u> bisuu-in q<u>aa</u>la innii usyhidull<u>aa</u>ha wasyhaduu annii barii-un mimm<u>aa</u> tusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (1528, '11:55', 11, 55, 'min duunihi fakiiduunii jamii\'an tsumma l<u>aa</u> tun<u>zh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (1529, '11:56', 11, 56, 'innii tawakkaltu \'al<u>aa</u>ll<u>aa</u>hi rabbii warabbikum m<u>aa</u> min d<u>aa</u>bbatin ill<u>aa</u> huwa <u>aa</u>khi<u>dz</u>un bin<u>aas</u>iyatih<u>aa</u> inna rabbii \'al<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (1530, '11:57', 11, 57, 'fa-in tawallaw faqad ablaghtukum m<u>aa</u> ursiltu bihi ilaykum wayastakhlifu rabbii qawman ghayrakum wal<u>aa</u> ta<u>dh</u>urruunahu syay-an inna rabbii \'al<u>aa</u> kulli syay-in <u>h</u>afii<u>zh</u>\r\r\n');
INSERT INTO quranLatin VALUES (1531, '11:58', 11, 58, 'walamm<u>aa</u> j<u>aa</u>-a amrun<u>aa</u> najjayn<u>aa</u> huudan walla<u>dz</u>iina <u>aa</u>manuu ma\'ahu bira<u>h</u>matin minn<u>aa</u> wanajjayn<u>aa</u>hum min \'a<u>dzaa</u>bin ghalii<u>zh</u>\r\r\n');
INSERT INTO quranLatin VALUES (1532, '11:59', 11, 59, 'watilka \'<u>aa</u>dun ja<u>h</u>aduu bi-<u>aa</u>y<u>aa</u>ti rabbihim wa\'a<u>sh</u>aw rusulahu wattaba\'uu amra kulli jabb<u>aa</u>rin \'aniid\r\r\n');
INSERT INTO quranLatin VALUES (1533, '11:60', 11, 60, 'wautbi\'uu fii h<u>aadz</u>ihi dduny<u>aa</u> la\'natan wayawma lqiy<u>aa</u>mati l<u>aa</u> inna \'<u>aa</u>dan kafaruu rabbahum l<u>aa</u> bu\'dan li\'<u>aa</u>din qawmi huud\r\r\n');
INSERT INTO quranLatin VALUES (1534, '11:61', 11, 61, 'wa-il<u>aa</u> tsamuuda akh<u>aa</u>hum <u>shaa</u>li<u>h</u>an q<u>aa</u>la y<u>aa</u> qawmi u\'buduull<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu huwa ansya-akum mina l-ar<u>dh</u>i wasta\'marakum fiih<u>aa</u> fastaghfiruuhu tsumma tuubuu ilayhi inna rabbii qariibun mujiib\r\r\n');
INSERT INTO quranLatin VALUES (1535, '11:62', 11, 62, 'q<u>aa</u>luu y<u>aa</u> <u>shaa</u>li<u>h</u>u qad kunta fiin<u>aa</u> marjuwwan qabla h<u>aadzaa</u> atanh<u>aa</u>n<u>aa</u> an na\'buda m<u>aa</u> ya\'budu <u>aa</u>b<u>aa</u>un<u>aa</u> wa-innan<u>aa</u> lafii syakkin mimm<u>aa</u> tad\'uun<u>aa</u> ilayhi muriib\r\r\n');
INSERT INTO quranLatin VALUES (1536, '11:63', 11, 63, 'q<u>aa</u>la y<u>aa</u> qawmi ara-aytum in kuntu \'al<u>aa</u> bayyinatin min rabbii wa<u>aa</u>t<u>aa</u>nii minhu ra<u>h</u>matan faman yan<u>sh</u>urunii minall<u>aa</u>hi in \'a<u>sh</u>aytuhu fam<u>aa</u> taziiduunanii ghayra takhsiir\r\r\n');
INSERT INTO quranLatin VALUES (1537, '11:64', 11, 64, 'way<u>aa</u> qawmi h<u>aadz</u>ihi n<u>aa</u>qatull<u>aa</u>hi lakum <u>aa</u>yatan fa<u>dz</u>aruuh<u>aa</u> ta/kul fii ar<u>dh</u>ill<u>aa</u>hi wal<u>aa</u> tamassuuh<u>aa</u> bisuu-in faya/khu<u>dz</u>akum \'a<u>dzaa</u>bun qariib\r\r\n');
INSERT INTO quranLatin VALUES (1538, '11:65', 11, 65, 'fa\'aqaruuh<u>aa</u> faq<u>aa</u>la tamatta\'uu fii d<u>aa</u>rikum tsal<u>aa</u>tsata ayy<u>aa</u>min <u>dzaa</u>lika wa\'dun ghayru mak<u>dz</u>uub\r\r\n');
INSERT INTO quranLatin VALUES (1539, '11:66', 11, 66, 'falamm<u>aa</u> j<u>aa</u>-a amrun<u>aa</u> najjayn<u>aa</u> <u>shaa</u>li<u>h</u>an walla<u>dz</u>iina <u>aa</u>manuu ma\'ahu bira<u>h</u>matin minn<u>aa</u> wamin khizyi yawmi-i<u>dz</u>in inna rabbaka huwa lqawiyyu l\'aziiz\r\r\n');
INSERT INTO quranLatin VALUES (1540, '11:67', 11, 67, 'wa-akha<u>dz</u>alla<u>dz</u>iina <u>zh</u>alamuu <u>shsh</u>ay<u>h</u>atu fa-a<u>sh</u>ba<u>h</u>uu fii diy<u>aa</u>rihim j<u>aa</u>tsimiin\r\r\n');
INSERT INTO quranLatin VALUES (1541, '11:68', 11, 68, 'ka-an lam yaghnaw fiih<u>aa</u> l<u>aa</u> inna tsamuuda kafaruu rabbahum l<u>aa</u> bu\'dan litsamuud\r\r\n');
INSERT INTO quranLatin VALUES (1542, '11:69', 11, 69, 'walaqad j<u>aa</u>-at rusulun<u>aa</u> ibr<u>aa</u>hiima bilbusyr<u>aa</u> q<u>aa</u>luu sal<u>aa</u>man q<u>aa</u>la sal<u>aa</u>mun fam<u>aa</u> labitsa an j<u>aa</u>-a bi\'ijlin <u>h</u>anii<u>dz</u>\r\r\n');
INSERT INTO quranLatin VALUES (1543, '11:70', 11, 70, 'falamm<u>aa</u> ra<u>aa</u> aydiyahum l<u>aa</u> ta<u>sh</u>ilu ilayhi nakirahum wa-awjasa minhum khiifatan q<u>aa</u>luu l<u>aa</u> takhaf inn<u>aa</u> ursiln<u>aa</u> il<u>aa</u> qawmi luu<u>th</u>\r\r\n');
INSERT INTO quranLatin VALUES (1544, '11:71', 11, 71, 'wamra-atuhu q<u>aa</u>-imatun fa<u>dh</u>a<u>h</u>ikat fabasysyarn<u>aa</u>h<u>aa</u> bi-is<u>haa</u>qa wamin war<u>aa</u>-i is<u>haa</u>qa ya\'quub\r\r\n');
INSERT INTO quranLatin VALUES (1545, '11:72', 11, 72, 'q<u>aa</u>lat y<u>aa</u> waylat<u>aa</u> a-alidu wa-an<u>aa</u> \'ajuuzun wah<u>aadzaa</u> ba\'lii syaykhan inna h<u>aadzaa</u> lasyay-un \'ajiib\r\r\n');
INSERT INTO quranLatin VALUES (1546, '11:73', 11, 73, 'q<u>aa</u>luu ata\'jabiina min amrill<u>aa</u>hi ra<u>h</u>matull<u>aa</u>hi wabarak<u>aa</u>tuhu \'alaykum ahla lbayti innahu <u>h</u>amiidun majiid\r\r\n');
INSERT INTO quranLatin VALUES (1547, '11:74', 11, 74, 'falamm<u>aa</u> <u>dz</u>ahaba \'an ibr<u>aa</u>hiima rraw\'u waj<u>aa</u>-at-hu lbusyr<u>aa </u>yuj<u>aa</u>dilun<u>aa</u> fii qawmi luu<u>th</u>\r\r\n');
INSERT INTO quranLatin VALUES (1548, '11:75', 11, 75, 'inna ibr<u>aa</u>hiima la<u>h</u>aliimun aww<u>aa</u>hun muniib\r\r\n');
INSERT INTO quranLatin VALUES (1549, '11:76', 11, 76, 'y<u>aa</u> ibr<u>aa</u>hiimu a\'ri<u>dh</u> \'an h<u>aadzaa</u> innahu qad j<u>aa</u>-a amru rabbika wa-innahum <u>aa</u>tiihim \'a<u>dzaa</u>bun ghayru marduud\r\r\n');
INSERT INTO quranLatin VALUES (1550, '11:77', 11, 77, 'walamm<u>aa</u> j<u>aa</u>-at rusulun<u>aa</u> luu<u>th</u>an sii-a bihim wa<u>daa</u>qa bihim <u>dz</u>ar\'an waq<u>aa</u>la h<u>aadzaa</u> yawmun \'a<u>sh</u>iib\r\r\n');
INSERT INTO quranLatin VALUES (1551, '11:78', 11, 78, 'waj<u>aa</u>-ahu qawmuhu yuhra\'uuna ilayhi wamin qablu k<u>aa</u>nuu ya\'maluuna ssayyi-<u>aa</u>ti q<u>aa</u>la y<u>aa</u> qawmi h<u>aa</u>ul<u>aa</u>-i ban<u>aa</u>tii hunna a<u>th</u>haru lakum fattaquull<u>aa</u>ha wal<u>aa</u> tukhzuuni fii <u>dh</u>ayfii alaysa minkum rajulun rasyiid\r\r\n');
INSERT INTO quranLatin VALUES (1552, '11:79', 11, 79, 'q<u>aa</u>luu laqad \'alimta m<u>aa</u> lan<u>aa</u> fii ban<u>aa</u>tika min <u>h</u>aqqin wa-innaka lata\'lamu m<u>aa</u> nuriid\r\r\n');
INSERT INTO quranLatin VALUES (1553, '11:80', 11, 80, 'q<u>aa</u>la law anna lii bikum quwwatan aw <u>aa</u>wii il<u>aa</u> ruknin syadiid\r\r\n');
INSERT INTO quranLatin VALUES (1554, '11:81', 11, 81, 'q<u>aa</u>luu y<u>aa</u> luu<u>th</u>u inn<u>aa</u> rusulu rabbika lan ya<u>sh</u>iluu ilayka fa-asri bi-ahlika biqi<u>th</u>\'in minallayli wal<u>aa</u> yaltafit minkum a<u>h</u>adun ill<u>aa</u> imra-ataka innahu mu<u>sh</u>iibuh<u>aa</u> m<u>aa</u> a<u>shaa</u>bahum inna maw\'idahumu <u>shsh</u>ub<u>h</u>u alaysa <u>shsh</u>ub<u>h</u>u biqariib\r\r\n');
INSERT INTO quranLatin VALUES (1555, '11:82', 11, 82, 'falamm<u>aa</u> j<u>aa</u>-a amrun<u>aa</u> ja\'aln<u>aa</u> \'<u>aa</u>liyah<u>aa</u> s<u>aa</u>filah<u>aa</u> wa-am<u>th</u>arn<u>aa</u> \'alayh<u>aa</u> <u>h</u>ij<u>aa</u>ratan min sijjiilin man<u>dh</u>uud\r\r\n');
INSERT INTO quranLatin VALUES (1556, '11:83', 11, 83, 'musawwamatan \'inda rabbika wam<u>aa</u> hiya mina <u>zhzhaa</u>limiina biba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (1557, '11:84', 11, 84, 'wa-il<u>aa</u> madyana akh<u>aa</u>hum syu\'ayban q<u>aa</u>la y<u>aa</u> qawmi u\'buduull<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu wal<u>aa</u> tanqu<u>sh</u>uu lmiky<u>aa</u>la walmiiz<u>aa</u>na innii ar<u>aa</u>kum bikhayrin wa-innii akh<u>aa</u>fu \'alaykum \'a<u>dzaa</u>ba yawmin mu<u>h</u>ii<u>th</u>\r\r\n');
INSERT INTO quranLatin VALUES (1558, '11:85', 11, 85, 'way<u>aa</u> qawmi awfuu lmiky<u>aa</u>la walmiiz<u>aa</u>na bilqis<u>th</u>i wal<u>aa</u> tabkhasuu nn<u>aa</u>sa asyy<u>aa</u>-ahum wal<u>aa</u> ta\'tsaw fii l-ar<u>dh</u>i mufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (1559, '11:86', 11, 86, 'baqiyyatull<u>aa</u>hi khayrun lakum in kuntum mu/miniina wam<u>aa</u> an<u>aa</u> \'alaykum bi<u>h</u>afii<u>zh</u>\r\r\n');
INSERT INTO quranLatin VALUES (1560, '11:87', 11, 87, 'q<u>aa</u>luu y<u>aa</u> syu\'aybu a<u>sh</u>al<u>aa</u>tuka ta/muruka an natruka m<u>aa</u> ya\'budu <u>aa</u>b<u>aa</u>un<u>aa</u> aw an naf\'ala fii amw<u>aa</u>lin<u>aa</u> m<u>aa</u> nasy<u>aa</u>u innaka la-anta l<u>h</u>aliimu rrasyiid\r\r\n');
INSERT INTO quranLatin VALUES (1561, '11:88', 11, 88, 'q<u>aa</u>la y<u>aa</u> qawmi ara-aytum in kuntu \'al<u>aa</u> bayyinatin min rabbii warazaqanii minhu rizqan <u>h</u>asanan wam<u>aa</u> uriidu an ukh<u>aa</u>lifakum il<u>aa</u> m<u>aa</u> anh<u>aa</u>kum \'anhu in uriidu ill<u>aa</u> l-i<u>sh</u>l<u>aah</u>a m<u>aa</u> ista<u>th</u>a\'tu wam<u>aa</u> tawfiiqii ill<u>aa</u> bil<u>aa</u>hi \'alayhi tawakkaltu wa-ilayhi uniib\r\r\n');
INSERT INTO quranLatin VALUES (1562, '11:89', 11, 89, 'way<u>aa</u> qawmi l<u>aa</u> yajrimannakum syiq<u>aa</u>qii an yu<u>sh</u>iibakum mitslu m<u>aa</u> a<u>shaa</u>ba qawma nuu<u>h</u>in aw qawma huudin aw qawma <u>shaa</u>li<u>h</u>in wam<u>aa</u> qawmu luu<u>th</u>in minkum biba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (1563, '11:90', 11, 90, 'wastaghfiruu rabbakum tsumma tuubuu ilayhi inna rabbii ra<u>h</u>iimun waduud\r\r\n');
INSERT INTO quranLatin VALUES (1564, '11:91', 11, 91, 'q<u>aa</u>luu y<u>aa</u> syu\'aybu m<u>aa</u> nafqahu katsiiran mimm<u>aa</u> taquulu wa-inn<u>aa</u> lanar<u>aa</u>ka fiin<u>aa</u> <u>dh</u>a\'iifan walawl<u>aa</u> rah<u>th</u>uka larajamn<u>aa</u>ka wam<u>aa</u> anta \'alayn<u>aa</u> bi\'aziiz\r\r\n');
INSERT INTO quranLatin VALUES (1565, '11:92', 11, 92, 'q<u>aa</u>la y<u>aa</u> qawmi arah<u>th</u>ii a\'azzu \'alaykum minall<u>aa</u>hi wattakha<u>dz</u>tumuuhu war<u>aa</u>-akum <u>zh</u>ihriyyan inna rabbii bim<u>aa</u> ta\'maluuna mu<u>h</u>ii<u>th</u>\r\r\n');
INSERT INTO quranLatin VALUES (1566, '11:93', 11, 93, 'way<u>aa</u> qawmi i\'maluu \'al<u>aa</u> mak<u>aa</u>natikum innii \'<u>aa</u>milun sawfa ta\'lamuuna man ya/tiihi \'a<u>dzaa</u>bun yukhziihi waman huwa k<u>aadz</u>ibun wartaqibuu inne ma\'akum raqiib\r\r\n');
INSERT INTO quranLatin VALUES (1567, '11:94', 11, 94, 'walamm<u>aa</u> j<u>aa</u>-a amrun<u>aa</u> najjayn<u>aa</u> syu\'ayban walla<u>dz</u>iina <u>aa</u>manuu ma\'ahu bira<u>h</u>matin minn<u>aa</u> wa-akha<u>dz</u>atilla<u>dz</u>iina <u>zh</u>alamuu <u>shsh</u>ay<u>h</u>atu fa-a<u>sh</u>ba<u>h</u>uu fii diy<u>aa</u>rihim j<u>aa</u>tsimiin\r\r\n');
INSERT INTO quranLatin VALUES (1568, '11:95', 11, 95, 'ka-an lam yaghnaw fiih<u>aa</u> l<u>aa</u> bu\'dan limadyana kam<u>aa</u> ba\'idat tsamuud\r\r\n');
INSERT INTO quranLatin VALUES (1569, '11:96', 11, 96, 'walaqad arsaln<u>aa</u> muus<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wasul<u>th</u><u>aa</u>nin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1570, '11:97', 11, 97, 'il<u>aa</u> fir\'awna wamala-ihi fattaba\'uu amra fir\'awna wam<u>aa</u> amru fir\'awna birasyiid\r\r\n');
INSERT INTO quranLatin VALUES (1571, '11:98', 11, 98, 'yaqdumu qawmahu yawma lqiy<u>aa</u>mati fa-awradahumu nn<u>aa</u>ra wabi/sa lwirdu lmawruud\r\r\n');
INSERT INTO quranLatin VALUES (1572, '11:99', 11, 99, 'wautbi\'uu fii h<u>aadz</u>ihi la\'natan wayawma lqiy<u>aa</u>mati bi/sa rrifdu lmarfuud\r\r\n');
INSERT INTO quranLatin VALUES (1573, '11:100', 11, 100, '<u>dzaa</u>lika min anb<u>aa</u>-i lqur<u>aa</u> naqu<u>shsh</u>uhu \'alayka minh<u>aa</u> q<u>aa</u>-imun wa<u>h</u>a<u>sh</u>iid\r\r\n');
INSERT INTO quranLatin VALUES (1574, '11:101', 11, 101, 'wam<u>aa</u> <u>zh</u>alamn<u>aa</u>hum wal<u>aa</u>kin <u>zh</u>alamuu anfusahum fam<u>aa</u> aghnat \'anhum <u>aa</u>lihatuhumullatii yad\'uuna min duunill<u>aa</u>hi min syay-in lamm<u>aa</u> j<u>aa</u>-a amru rabbika wam<u>aa</u> z<u>aa</u>duuhum ghayra tatbiib\r\r\n');
INSERT INTO quranLatin VALUES (1575, '11:102', 11, 102, 'waka<u>dzaa</u>lika akh<u>dz</u>u rabbika i<u>dzaa</u> akha<u>dz</u>a lqur<u>aa</u> wahiya <u>zhaa</u>limatun inna akh<u>dz</u>ahu liimun syadiid\r\r\n');
INSERT INTO quranLatin VALUES (1576, '11:103', 11, 103, 'inna fii <u>dzaa</u>lika la<u>aa</u>yatan liman kh<u>aa</u>fa \'a<u>dzaa</u>ba l-<u>aa</u>khirati <u>dzaa</u>lika yawmun majmuu\'un lahu nn<u>aa</u>su wa<u>dzaa</u>lika yawmun masyhuudun</u>\r\r\n');
INSERT INTO quranLatin VALUES (1577, '11:104', 11, 104, 'wam<u>aa</u> nu-akhkhiruhu ill<u>aa</u> li-ajalin ma\'duud\r\r\n');
INSERT INTO quranLatin VALUES (1578, '11:105', 11, 105, 'yawma ya/ti l<u>aa</u> takallamu nafsun ill<u>aa</u> bi-i<u>dz</u>nihi faminhum syaqiyyun wasa\'iid\r\r\n');
INSERT INTO quranLatin VALUES (1579, '11:106', 11, 106, 'fa-amm<u>aa</u>lla<u>dz</u>iina syaquu fafii nn<u>aa</u>ri lahum fiih<u>aa</u> zafiirun wasyahiiq\r\r\n');
INSERT INTO quranLatin VALUES (1580, '11:107', 11, 107, 'kh<u>aa</u>lidiina fiih<u>aa</u> m<u>aa</u> d<u>aa</u>mati ssam<u>aa</u>w<u>aa</u>tu wal-ar<u>dh</u>u ill<u>aa</u> m<u>aa</u> sy<u>aa</u>-a rabbuka inna rabbaka fa\'\'<u>aa</u>lun lim<u>aa</u> yuriid\r\r\n');
INSERT INTO quranLatin VALUES (1581, '11:108', 11, 108, 'wa-amm<u>aa</u>lla<u>dz</u>iina su\'iduu fafii ljannati kh<u>aa</u>lidiina fiih<u>aa</u> m<u>aa</u> d<u>aa</u>mati ssam<u>aa</u>w<u>aa</u>tu wal-ar<u>dh</u>u ill<u>aa</u> m<u>aa</u> sy<u>aa</u>-a rabbuka \'a<u>th</u><u>aa</u>-an ghayra maj<u>dz</u>uu<u>dz</u>\r\r\n');
INSERT INTO quranLatin VALUES (1582, '11:109', 11, 109, 'fal<u>aa</u> taku fii miryatin mimm<u>aa</u> ya\'budu h<u>aa</u>ul<u>aa</u>-i m<u>aa</u> ya\'buduuna ill<u>aa</u> kam<u>aa</u> ya\'budu <u>aa</u>b<u>aa</u>uhum min qablu wa-inn<u>aa</u> lamuwaffuuhum na<u>sh</u>iibahum ghayra manquu<u>sh</u>\r\r\n');
INSERT INTO quranLatin VALUES (1583, '11:110', 11, 110, 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> lkit<u>aa</u>ba fakhtulifa fiihi walawl<u>aa</u> kalimatun sabaqat min rabbika laqu<u>dh</u>iya baynahum wa-innahum lafii syakkin minhu muriib\r\r\n');
INSERT INTO quranLatin VALUES (1584, '11:111', 11, 111, 'wa-inna kullan lamm<u>aa</u> layuwaffiyannahum rabbuka a\'m<u>aa</u>lahum innahu bim<u>aa</u> ya\'maluuna khabiir\r\r\n');
INSERT INTO quranLatin VALUES (1585, '11:112', 11, 112, 'fastaqim kam<u>aa</u> umirta waman t<u>aa</u>ba ma\'aka wal<u>aa</u> ta<u>th</u>ghaw innahu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (1586, '11:113', 11, 113, 'wal<u>aa</u> tarkanuu il<u>aa</u>lla<u>dz</u>iina <u>zh</u>alamuu fatamassakumu nn<u>aa</u>ru wam<u>aa</u> lakum min duunill<u>aa</u>hi min awliy<u>aa</u>-a tsumma l<u>aa</u> tun<u>sh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (1587, '11:114', 11, 114, 'wa-aqimi <u>shsh</u>al<u>aa</u>ta <u>th</u>arafayi nnah<u>aa</u>ri wazulafan minallayli inna l<u>h</u>asan<u>aa</u>ti yu<u>dz</u>hibna ssayyi-<u>aa</u>ti <u>dzaa</u>lika <u>dz</u>ikr<u>aa</u> li<u>dzdzaa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (1588, '11:115', 11, 115, 'wa<u>sh</u>bir fa-innall<u>aa</u>ha l<u>aa</u> yu<u>dh</u>ii\'u ajra lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (1589, '11:116', 11, 116, 'falawl<u>aa</u> k<u>aa</u>na mina lquruuni min qablikum uluu baqiyyatin yanhawna \'ani lfas<u>aa</u>di fii l-ar<u>dh</u>i ill<u>aa</u> qaliilan mimman anjayn<u>aa</u> minhum wattaba\'alla<u>dz</u>iina <u>zh</u>alamuu m<u>aa</u> utrifuu fiihi wak<u>aa</u>nuu mujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (1590, '11:117', 11, 117, 'wam<u>aa</u> k<u>aa</u>na rabbuka liyuhlika lqur<u>aa</u> bi<u>zh</u>ulmin wa-ahluh<u>aa</u> mu<u>sh</u>li<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1591, '11:118', 11, 118, 'walaw sy<u>aa</u>-a rabbuka laja\'ala nn<u>aa</u>sa ummatan w<u>aah</u>idatan wal<u>aa</u> yaz<u>aa</u>luuna mukhtalifiin\r\r\n');
INSERT INTO quranLatin VALUES (1592, '11:119', 11, 119, 'ill<u>aa</u> man ra<u>h</u>ima rabbuka wali<u>dzaa</u>lika khalaqahum watammat kalimatu rabbika la-amla-anna jahannama mina ljinnati wann<u>aa</u>si ajma\'iin\r\r\n');
INSERT INTO quranLatin VALUES (1593, '11:120', 11, 120, 'wakullan naqu<u>shsh</u>u \'alayka min anb<u>aa</u>-i rrusuli m<u>aa</u> nutsabbitu bihi fu-<u>aa</u>daka waj<u>aa</u>-aka fii h<u>aadz</u>ihi l<u>h</u>aqqu wamaw\'i<u>zh</u>atun wa<u>dz</u>ikr<u>aa</u> lilmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1594, '11:121', 11, 121, 'waqul lilla<u>dz</u>iina l<u>aa</u> yu/minuuna i\'maluu \'al<u>aa</u> mak<u>aa</u>natikum inn<u>aa</u> \'<u>aa</u>miluun\r\r\n');
INSERT INTO quranLatin VALUES (1595, '11:122', 11, 122, 'wanta<u>zh</u>iruu inn<u>aa</u> munta<u>zh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (1596, '11:123', 11, 123, 'walill<u>aa</u>hi ghaybu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wa-ilayhi yurja\'u l-amru kulluhu fa\'budhu watawakkal \'alayhi wam<u>aa</u> rabbuka bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1597, '12:1', 12, 1, 'alif-l<u>aa</u>m-r<u>aa</u> tilka <u>aa</u>y<u>aa</u>tu lkit<u>aa</u>bi lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (1598, '12:2', 12, 2, 'inn<u>aa</u> anzaln<u>aa</u>hu qur-<u>aa</u>nan \'arabiyyan la\'allakum ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (1599, '12:3', 12, 3, 'na<u>h</u>nu naqu<u>shsh</u>u \'alayka a<u>h</u>sana lqa<u>sh</u>a<u>sh</u>i bim<u>aa</u> aw<u>h</u>ayn<u>aa</u> ilayka h<u>aadzaa</u> lqur-<u>aa</u>na wa-in kunta min qablihi lamina lgh<u>aa</u>filiin\r\r\n');
INSERT INTO quranLatin VALUES (1600, '12:4', 12, 4, 'i<u>dz</u> q<u>aa</u>la yuusufu li-abiihi y<u>aa</u> abati innii ra-aytu a<u>h</u>ada \'asyara kawkaban wasysyamsa walqamara ra-aytuhum lii s<u>aa</u>jidiin\r\r\n');
INSERT INTO quranLatin VALUES (1601, '12:5', 12, 5, 'q<u>aa</u>la y<u>aa</u> bunayya l<u>aa</u> taq<u>sh</u>u<u>sh</u> ru/y<u>aa</u>ka \'al<u>aa</u> ikhwatika fayakiiduu laka kaydan inna sysyay<u>th</u><u>aa</u>na lil-ins<u>aa</u>ni \'aduwwun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1602, '12:6', 12, 6, 'waka<u>dzaa</u>lika yajtabiika rabbuka wayu\'allimuka min ta/wiili l-a<u>haa</u>diitsi wayutimmu ni\'matahu \'alayka wa\'al<u>aa</u> <u>aa</u>li ya\'quuba kam<u>aa</u> atammah<u>aa</u> \'al<u>aa</u> abawayka min qablu ibr<u>aa</u>hiima wa-is<u>haa</u>qa inna rabbaka \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1603, '12:7', 12, 7, 'laqad k<u>aa</u>na fii yuusufa wa-ikhwatihi <u>aa</u>y<u>aa</u>tun liss<u>aa</u>-iliin\r\r\n');
INSERT INTO quranLatin VALUES (1604, '12:8', 12, 8, 'i<u>dz</u> q<u>aa</u>luu layuusufu wa-akhuuhu a<u>h</u>abbu il<u>aa</u> abiin<u>aa</u> minn<u>aa</u> wana<u>h</u>nu \'u<u>sh</u>batun inna ab<u>aa</u>n<u>aa</u> lafii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1605, '12:9', 12, 9, 'uqtuluu yuusufa awi i<u>th</u>ra<u>h</u>uuhu ar<u>dh</u>an yakhlu lakum wajhu abiikum watakuunuu min ba\'dihi qawman <u>shaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1606, '12:10', 12, 10, 'q<u>aa</u>la q<u>aa</u>-ilun minhum l<u>aa</u> taqtuluu yuusufa wa-alquuhu fii ghay<u>aa</u>bati ljubbi yaltaqi<u>th</u>hu ba\'<u>dh</u>u ssayy<u>aa</u>rati in kuntum f<u>aa</u>\'iliin\r\r\n');
INSERT INTO quranLatin VALUES (1607, '12:11', 12, 11, 'q<u>aa</u>luu y<u>aa</u> ab<u>aa</u>n<u>aa</u> m<u>aa</u> laka l<u>aa</u> ta/mann<u>aa</u> \'al<u>aa</u> yuusufa wa-inn<u>aa</u> lahu lan<u>aas</u>i<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1608, '12:12', 12, 12, 'arsilhu ma\'an<u>aa</u> ghadan yarta\'wayal\'ab wa-inn<u>aa</u> lahu la<u>haa</u>fi<u>zh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1609, '12:13', 12, 13, 'q<u>aa</u>la innii laya<u>h</u>zununii an tsa<u>dz</u>habuu bihi wa-akh<u>aa</u>fu an ya/kulahu <u>dzdz</u>i/bu wa-antum \'anhu gh<u>aa</u>filuun\r\r\n');
INSERT INTO quranLatin VALUES (1610, '12:14', 12, 14, 'q<u>aa</u>luu la-in akalahu <u>dzdz</u>i/bu wana<u>h</u>nu \'u<u>sh</u>batun inn<u>aa</u> i<u>dz</u>an lakh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (1611, '12:15', 12, 15, 'falamm<u>aa</u> <u>dz</u>ahabuu bihi wa-ajma\'uu an yaj\'aluuhu fii ghay<u>aa</u>bati ljubbi wa-aw<u>h</u>ayn<u>aa</u> ilayhi latunabi-annahum bi-amrihim h<u>aadzaa</u> wahum l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (1612, '12:16', 12, 16, 'waj<u>aa</u>uu ab<u>aa</u>hum \'isy<u>aa</u>-an yabkuun\r\r\n');
INSERT INTO quranLatin VALUES (1613, '12:17', 12, 17, 'q<u>aa</u>luu y<u>aa</u> ab<u>aa</u>n<u>aa</u> inn<u>aa</u> <u>dz</u>ahabn<u>aa</u> nastabiqu watarakn<u>aa</u> yuusufa \'inda mat<u>aa</u>\'in<u>aa</u> fa-akalahu <u>dzdz</u>i/bu wam<u>aa</u> anta bimu/minin lan<u>aa</u> walaw kunn<u>aa</u> <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (1614, '12:18', 12, 18, 'waj<u>aa</u>uu \'al<u>aa</u> qamii<u>sh</u>ihi bidamin ka<u>dz</u>ibin q<u>aa</u>la bal sawwalat lakum anfusukum amran fa<u>sh</u>abrun jamiilun wal<u>aa</u>hu lmusta\'<u>aa</u>nu \'al<u>aa</u> m<u>aa</u> ta<u>sh</u>ifuun\r\r\n');
INSERT INTO quranLatin VALUES (1615, '12:19', 12, 19, 'waj<u>aa</u>-at sayy<u>aa</u>ratun fa-arsaluu w<u>aa</u>ridahum fa-adl<u>aa</u> dalwahu q<u>aa</u>la y<u>aa</u> busyr<u>aa</u> h<u>aadzaa</u> ghul<u>aa</u>mun wa-asarruuhu bi<u>daa</u>\'atan wal<u>aa</u>hu \'aliimun bim<u>aa</u> ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1616, '12:20', 12, 20, 'wasyarawhu bitsamanin bakhsin dar<u>aa</u>hima ma\'duudatin wak<u>aa</u>nuu fiihi mina zz<u>aa</u>hidiin\r\r\n');
INSERT INTO quranLatin VALUES (1617, '12:21', 12, 21, 'waq<u>aa</u>lalla<u>dz</u>ii isytar<u>aa</u>hu min mi<u>sh</u>ra limra-atihi akrimii matsw<u>aa</u>hu \'as<u>aa</u> an yanfa\'an<u>aa</u> aw nattakhi<u>dz</u>ahu waladan waka<u>dzaa</u>lika makkann<u>aa</u> liyuusufa fii l-ar<u>dh</u>i walinu\'allimahu min ta/wiili l-a<u>haa</u>diitsi wal<u>aa</u>hu gh<u>aa</u>libun \'al<u>aa</u> amrihi wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1618, '12:22', 12, 22, 'walamm<u>aa</u> balagha asyuddahu <u>aa</u>tayn<u>aa</u>hu <u>h</u>ukman wa\'ilman waka<u>dzaa</u>lika najzii lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (1619, '12:23', 12, 23, 'war<u>aa</u>wadat-hullatii huwa fii baytih<u>aa</u> \'an nafsihi waghallaqati l-abw<u>aa</u>ba waq<u>aa</u>lat hayta laka q<u>aa</u>la ma\'<u>aadz</u>all<u>aa</u>hi innahu rabbii a<u>h</u>sana matsw<u>aa</u>ya innahu l<u>aa</u> yufli<u>h</u>u <u>zhzhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (1620, '12:24', 12, 24, 'walaqad hammat bihi wahamma bih<u>aa</u> lawl<u>aa</u> an ra<u>aa</u> burh<u>aa</u>na rabbihi ka<u>dzaa</u>lika lina<u>sh</u>rifa \'anhu ssuu-a walfa<u>h</u>sy<u>aa</u>-a innahu min \'ib<u>aa</u>din<u>aa</u> lmukhla<u>sh</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1621, '12:25', 12, 25, 'wastabaq<u>aa</u> lb<u>aa</u>ba waqaddat qamii<u>sh</u>ahu min duburin wa-alfay<u>aa</u> sayyidah<u>aa</u> lad<u>aa</u> lb<u>aa</u>bi q<u>aa</u>lat m<u>aa</u> jaz<u>aa</u>u man ar<u>aa</u>da bi-ahlika suu-an ill<u>aa</u> an yusjana aw \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (1622, '12:26', 12, 26, 'q<u>aa</u>la hiya r<u>aa</u>wadatnii \'an nafsii wasyahida sy<u>aa</u>hidun min ahlih<u>aa</u> in k<u>aa</u>na qamii<u>sh</u>uhu qudda min qubulin fa<u>sh</u>adaqat wahuwa mina lk<u>aadz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (1623, '12:27', 12, 27, 'wa-in k<u>aa</u>na qamii<u>sh</u>uhu qudda min duburin faka<u>dz</u>abat wahuwa mina <u>shshaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (1624, '12:28', 12, 28, 'falamm<u>aa</u> ra<u>aa</u> qamii<u>sh</u>ahu qudda min duburin q<u>aa</u>la innahu min kaydikunna inna kaydakunna \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1625, '12:29', 12, 29, 'yuusufu a\'ri<u>dh</u> \'an h<u>aadzaa</u> wastaghfirii li<u>dz</u>anbiki innaki kunti mina lkh<u>aath</u>i-iin\r\r\n');
INSERT INTO quranLatin VALUES (1626, '12:30', 12, 30, 'waq<u>aa</u>la niswatun fii lmadiinati imra-atu l\'aziizi tur<u>aa</u>widu fat<u>aa</u>h<u>aa</u> \'an nafsihi qad syaghafah<u>aa</u> <u>h</u>ubban inn<u>aa</u> lanar<u>aa</u>h<u>aa</u> fii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1627, '12:31', 12, 31, 'falamm<u>aa</u> sami\'at bimakrihinna arsalat ilayhinna wa-a\'tadat lahunna muttaka-an wa<u>aa</u>tat kulla w<u>aah</u>idatin minhunna sikkiinan waq<u>aa</u>lati ukhruj \'alayhinna falamm<u>aa</u> ra-aynahu akbarnahu waqa<u>ththh</u>a\'na aydiyahunna waqulna <u>haa</u>sya lill<u>aa</u>hi m<u>aa</u> h<u>aadzaa</u> basyaran in h<u>aadzaa</u> ill<u>aa</u> malakun kariim\r\r\n');
INSERT INTO quranLatin VALUES (1628, '12:32', 12, 32, 'q<u>aa</u>lat fa<u>dzaa</u>likunnalla<u>dz</u>ii lumtunnanii fiihi walaqad r<u>aa</u>wadtuhu \'an nafsihi fast\'<u>sh</u>ama wala-in lam yaf\'al m<u>aa</u> <u>aa</u>muruhu layusjananna walayakuunan mina <u>shshaa</u>ghiriin\r\r\n');
INSERT INTO quranLatin VALUES (1629, '12:33', 12, 33, 'q<u>aa</u>la rabbi ssijnu a<u>h</u>abbu ilayya mimm<u>aa</u> yad\'uunanii ilayhi wa-ill<u>aa</u> ta<u>sh</u>rif \'annii kaydahunna a<u>sh</u>bu ilayhinna wa-akun mina lj<u>aa</u>hiliin\r\r\n');
INSERT INTO quranLatin VALUES (1630, '12:34', 12, 34, 'fastaj<u>aa</u>ba lahu rabbuhu fa<u>sh</u>arafa \'anhu kaydahunna innahu huwa ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1631, '12:35', 12, 35, 'tsumma bad<u>aa</u> lahum min ba\'di m<u>aa</u> ra-awuu l-<u>aa</u>y<u>aa</u>ti layasjununnahu <u>h</u>att<u>aa</u> <u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1632, '12:36', 12, 36, 'wadakhala ma\'ahu ssijna fatay<u>aa</u>ni q<u>aa</u>la a<u>h</u>aduhum<u>aa</u> innii ar<u>aa</u>nii a\'<u>sh</u>iru khamran waq<u>aa</u>la l-<u>aa</u>kharu innii ar<u>aa</u>nii a<u>h</u>milu fawqa ra/sii khubzan ta/kulu <u>ththh</u>ayru minhu nabbi/n<u>aa</u> bita/wiilihi inn<u>aa</u> nar<u>aa</u>ka mina lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (1633, '12:37', 12, 37, 'q<u>aa</u>la l<u>aa</u> ya/tiikum<u>aa</u> <u>th</u>a\'<u>aa</u>mun turzaq<u>aa</u>nihi ill<u>aa</u> nabba/tukum<u>aa</u> bita/wiilihi qabla an ya/tiyakum<u>aa</u> <u>dzaa</u>likum<u>aa</u> mimm<u>aa</u> \'allamanii rabbii innii taraktu millata qawmin l<u>aa</u> yu/minuuna bil<u>aa</u>hi wahum bil-<u>aa</u>khirati hum k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (1634, '12:38', 12, 38, 'wattaba\'tu millata <u>aa</u>b<u>aa</u>-ii ibr<u>aa</u>hiima wa-is<u>haa</u>qa waya\'quuba m<u>aa</u> k<u>aa</u>na lan<u>aa</u> an nusyrika bil<u>aa</u>hi min syay-in <u>dzaa</u>lika min fa<u>dh</u>lill<u>aa</u>hi \'alayn<u>aa</u> wa\'al<u>aa</u> nn<u>aa</u>si wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> yasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (1635, '12:39', 12, 39, 'y<u>aa</u> <u>shaah</u>ibayi ssijni a-arb<u>aa</u>bun mutafarriquuna khayrun amill<u>aa</u>hu lw<u>aah</u>idu lqahh<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1636, '12:40', 12, 40, 'm<u>aa</u> ta\'buduuna min duunihi ill<u>aa</u> asm<u>aa</u>-an sammaytumuuh<u>aa</u> antum wa<u>aa</u>b<u>aa</u>ukum m<u>aa</u> anzalall<u>aa</u>hu bih<u>aa</u> min sul<u>th</u><u>aa</u>nin ini l<u>h</u>ukmu ill<u>aa</u> lill<u>aa</u>hi amarall<u>aa</u> ta\'buduu ill<u>aa</u> iyy<u>aa</u>hu <u>dzaa</u>lika ddiinu lqayyimu wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1637, '12:41', 12, 41, 'y<u>aa</u> <u>shaah</u>ibayi ssijni amm<u>aa</u> a<u>h</u>adukum<u>aa</u> fayasqii rabbahu khamran wa-amm<u>aa</u> l-<u>aa</u>kharu fayu<u>sh</u>labu fata/kulu <u>ththh</u>ayru min ra/sihi qu<u>dh</u>iya l-amrulla<u>dz</u>ii fiihi tastaftiy<u>aa</u>n\r\r\n');
INSERT INTO quranLatin VALUES (1638, '12:42', 12, 42, 'waq<u>aa</u>la lilla<u>dz</u>ii <u>zh</u>anna annahu n<u>aa</u>jin minhum<u>aa</u> u<u>dz</u>kurnii \'inda rabbika fa-ans<u>aa</u>hu sysyay<u>th</u><u>aa</u>nu <u>dz</u>ikra rabbihi falabitsa fii ssijni bi<u>dh</u>\'a siniin\r\r\n');
INSERT INTO quranLatin VALUES (1639, '12:43', 12, 43, 'waq<u>aa</u>la lmaliku innii ar<u>aa</u> sab\'a baqar<u>aa</u>tin sim<u>aa</u>nin ya/kuluhunna sab\'un \'ij<u>aa</u>fun wasab\'a sunbul<u>aa</u>tin khu<u>dh</u>rin waukhara y<u>aa</u>bis<u>aa</u>tin y<u>aa</u> ayyuh<u>aa</u> lmalau aftuunii fii ru/y<u>aa</u>ya in kuntum lirru/y<u>aa</u> ta\'buruun\r\r\n');
INSERT INTO quranLatin VALUES (1640, '12:44', 12, 44, 'q<u>aa</u>luu a<u>dh</u>gh<u>aa</u>tsu a<u>h</u>l<u>aa</u>min wam<u>aa</u> na<u>h</u>nu bita/wiili l-a<u>h</u>l<u>aa</u>mi bi\'<u>aa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1641, '12:45', 12, 45, 'waq<u>aa</u>lalla<u>dz</u>ii naj<u>aa</u> minhum<u>aa</u> waddakara ba\'da ummatin an<u>aa</u> unabbi-ukum bita/wiilihi fa-arsiluun\r\r\n');
INSERT INTO quranLatin VALUES (1642, '12:46', 12, 46, 'yuusufu ayyuh<u>aa</u> <u>shsh</u>iddiiqu aftin<u>aa</u> fii sab\'i baqar<u>aa</u>tin sim<u>aa</u>nin ya/kuluhunna sab\'un \'ij<u>aa</u>fun wasab\'i sunbul<u>aa</u>tin khu<u>dh</u>rin waukhara y<u>aa</u>bis<u>aa</u>tin la\'allii arji\'u il<u>aa</u> nn<u>aa</u>si la\'allahum ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1643, '12:47', 12, 47, 'q<u>aa</u>la tazra\'uuna sab\'a siniina da-aban fam<u>aa</u> <u>h</u>a<u>sh</u>adtum fa<u>dz</u>aruuhu fii sunbulihi ill<u>aa</u> qaliilan mimm<u>aa</u> ta/kuluun\r\r\n');
INSERT INTO quranLatin VALUES (1644, '12:48', 12, 48, 'tsumma ya/tii min ba\'di <u>dzaa</u>lika sab\'un syid<u>aa</u>dun ya/kulna m<u>aa</u> qaddamtum lahunna ill<u>aa</u> qaliilan mimm<u>aa</u> tu<u>hs</u>inuun\r\r\n');
INSERT INTO quranLatin VALUES (1645, '12:49', 12, 49, 'tsumma ya/tii min ba\'di <u>dzaa</u>lika \'<u>aa</u>mun fiihi yugh<u>aa</u>tsu nn<u>aa</u>su wafiihi ya\'<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (1646, '12:50', 12, 50, 'waq<u>aa</u>la lmaliku i/tuunii bihi falamm<u>aa</u> j<u>aa</u>-ahu rrasuulu q<u>aa</u>la irji\'il<u>aa</u> rabbika fas-alhu m<u>aa</u> b<u>aa</u>lu nniswatill<u>aa</u>tii qa<u>ththh</u>a\'na aydiyahunna inna rabbii bikaydihinna \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1647, '12:51', 12, 51, 'q<u>aa</u>la m<u>aa</u> kha<u>th</u>bukunna i<u>dz</u> r<u>aa</u>wadtunna yuusufa \'an nafsihi qulna <u>haa</u>sya lill<u>aa</u>hi m<u>aa</u> \'alimn<u>aa</u> \'alayhi min suu-in q<u>aa</u>lati imra-atu l\'aziizi l-<u>aa</u>na <u>h</u>a<u>sy</u>a<u>sh</u>a l<u>h</u>aqqu an<u>aa</u> r<u>aa</u>wadtuhu \'an nafsihi wa-innahu lamina <u>shshaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (1648, '12:52', 12, 52, '<u>dzaa</u>lika liya\'lama annii lam akhunhu bilghaybi wa-annall<u>aa</u>ha l<u>aa</u> yahdii kayda lkh<u>aa</u>-iniin\r\r\n');
INSERT INTO quranLatin VALUES (1649, '12:53', 12, 53, 'wam<u>aa</u> ubarri-u nafsii inna nnafsa la-amm<u>aa</u>ratun bissuu-i ill<u>aa</u> m<u>aa</u> ra<u>h</u>ima rabbii inna rabbii ghafuurun ra<u>h</u>iim<b>un </b>\r\r\n');
INSERT INTO quranLatin VALUES (1650, '12:54', 12, 54, 'waq<u>aa</u>la lmaliku i/tuunii bihi astakhli<u>sh</u>hu linafsii falamm<u>aa</u> kallamahu q<u>aa</u>la innaka lyawma ladayn<u>aa</u> makiinun amiin\r\r\n');
INSERT INTO quranLatin VALUES (1651, '12:55', 12, 55, 'q<u>aa</u>la ij\'alnii \'al<u>aa</u> khaz<u>aa</u>-ini l-ar<u>dh</u>i innii <u>h</u>afii<u>zh</u>un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1652, '12:56', 12, 56, 'waka<u>dzaa</u>lika makann<u>aa</u> liyuusufa fii l-ar<u>dh</u>i yatabawwau minh<u>aa</u> <u>h</u>aytsu yasy<u>aa</u>u nu<u>sh</u>iibu bira<u>h</u>matin<u>aa</u> man nasy<u>aa</u>u wal<u>aa</u> nu<u>dh</u>ii\'u ajra lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (1653, '12:57', 12, 57, 'wala-ajru l-<u>aa</u>khirati khayrun lilla<u>dz</u>iina <u>aa</u>manuu wak<u>aa</u>nuu yattaquun\r\r\n');
INSERT INTO quranLatin VALUES (1654, '12:58', 12, 58, 'waj<u>aa</u>-a ikhwatu yuusufa fadakhaluu \'alayhi fa\'arafahum wahum lahu munkiruun\r\r\n');
INSERT INTO quranLatin VALUES (1655, '12:59', 12, 59, 'walamm<u>aa</u> jahhazahum bijah<u>aa</u>zihim q<u>aa</u>la i/tuunii bi-akhin lakum min abiikum l<u>aa</u> tarawna annii uufii lkayla wa-an<u>aa</u> khayru lmunziliin\r\r\n');
INSERT INTO quranLatin VALUES (1656, '12:60', 12, 60, 'fa-in lam ta/tuunii bihi fal<u>aa</u> kayla lakum \'indii wal<u>aa</u> taqrabuun\r\r\n');
INSERT INTO quranLatin VALUES (1657, '12:61', 12, 61, 'q<u>aa</u>luu sanur<u>aa</u>widu \'anhu ab<u>aa</u>hu wa-inn<u>aa</u> laf<u>aa</u>\'iluun\r\r\n');
INSERT INTO quranLatin VALUES (1658, '12:62', 12, 62, 'waq<u>aa</u>la lifity<u>aa</u>nihi ij\'aluu bi<u>daa</u>\'atahum fii ri<u>haa</u>lihim la\'allahum ya\'rifuunah<u>aa</u> i<u>dzaa</u> inqalabuu il<u>aa</u> ahlihim la\'allahum yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1659, '12:63', 12, 63, 'falamm<u>aa</u> raja\'uu il<u>aa</u> abiihim q<u>aa</u>luu y<u>aa</u> ab<u>aa</u>n<u>aa</u> muni\'a minn<u>aa</u> lkaylu fa-arsil ma\'an<u>aa</u> akh<u>aa</u>n<u>aa</u> naktal wa-inn<u>aa</u> lahu la<u>haa</u>fi<u>zh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1660, '12:64', 12, 64, 'q<u>aa</u>la hal <u>aa</u>manukum \'alayhi ill<u>aa</u> kam<u>aa</u> amintukum \'al<u>aa</u> akhiihi min qablu fal<u>aa</u>hu khayrun <u>haa</u>fi<u>zh</u>an wahuwa ar<u>h</u>amu rr<u>aah</u>imiin\r\r\n');
INSERT INTO quranLatin VALUES (1661, '12:65', 12, 65, 'walamm<u>aa</u> fata<u>h</u>uu mat<u>aa</u>\'ahum wajaduu bi<u>daa</u>\'atahum ruddat ilayhim q<u>aa</u>luu y<u>aa</u> ab<u>aa</u>n<u>aa</u> m<u>aa</u> nabghii h<u>aadz</u>ihi bi<u>daa</u>\'atun<u>aa</u> ruddat ilayn<u>aa</u> wanamiiru ahlan<u>aa</u> wana<u>h</u>fa<u>zh</u>u akh<u>aa</u>n<u>aa</u> wanazd<u>aa</u>du kayla ba\'iirin <u>dzaa</u>lika kaylun yasiir\r\r\n');
INSERT INTO quranLatin VALUES (1662, '12:66', 12, 66, 'q<u>aa</u>la lan ursilahu ma\'akum <u>h</u>att<u>aa</u> tu/tuuni mawtsiqan minall<u>aa</u>hi lata/tunnanii bihi ill<u>aa</u> an yu<u>hath</u>a bikum falamm<u>aa</u> <u>aa</u>tawhu mawtsiqahum q<u>aa</u>lall<u>aa</u>hu \'al<u>aa</u> m<u>aa</u> naquulu wakiil\r\r\n');
INSERT INTO quranLatin VALUES (1663, '12:67', 12, 67, 'waq<u>aa</u>la y<u>aa</u> baniyya l<u>aa</u> tadkhuluu min b<u>aa</u>bin w<u>aah</u>idin wadkhuluu min abw<u>aa</u>bin mutafarriqatin wam<u>aa</u> ughnii \'ankum minall<u>aa</u>hi min syay-in ini l<u>h</u>ukmu ill<u>aa</u> lill<u>aa</u>hi \'alayhi tawakkaltu wa\'alayhi falyatawakkali lmutawakkiluun\r\r\n');
INSERT INTO quranLatin VALUES (1664, '12:68', 12, 68, 'walamm<u>aa</u> dakhaluu min <u>h</u>aytsu amarahum abuuhum m<u>aa</u> k<u>aa</u>na yughnii \'anhum minall<u>aa</u>hi min syay-in ill<u>aa</u> <u>haa</u>jatan fii nafsi ya\'quuba qa<u>daa</u>h<u>aa</u> wa-innahu la<u>dz</u>uu \'ilmin lim<u>aa</u> \'allamn<u>aa</u>hu wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1665, '12:69', 12, 69, 'walamm<u>aa</u> dakhaluu \'al<u>aa</u> yuusufa <u>aa</u>w<u>aa</u> ilayhi akh<u>aa</u>hu q<u>aa</u>la innii an<u>aa</u> akhuuka fal<u>aa</u> tabta-is bim<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1666, '12:70', 12, 70, 'falamm<u>aa</u> jahhazahum bijah<u>aa</u>zihim ja\'ala ssiq<u>aa</u>yata fii ra<u>h</u>li akhiihi tsumma a<u>dzdz</u>ana mu-a<u>dzdz</u>inun ayyatuh<u>aa</u> l\'iiru innakum las<u>aa</u>riquun\r\r\n');
INSERT INTO quranLatin VALUES (1667, '12:71', 12, 71, 'q<u>aa</u>luu wa-aqbaluu \'alayhim m<u>aatsaa</u> tafqiduun\r\r\n');
INSERT INTO quranLatin VALUES (1668, '12:72', 12, 72, 'q<u>aa</u>luu nafqidu <u>sh</u>uw<u>aa</u>\'a lmaliki waliman j<u>aa</u>-a bihi <u>h</u>imlu ba\'iirin wa-an<u>aa</u> bihi za\'iim\r\r\n');
INSERT INTO quranLatin VALUES (1669, '12:73', 12, 73, 'q<u>aa</u>luu tal<u>aa</u>hi laqad \'alimtum m<u>aa</u> ji/n<u>aa</u> linufsida fii l-ar<u>dh</u>i wam<u>aa</u> kunn<u>aa</u> s<u>aa</u>riqiin\r\r\n');
INSERT INTO quranLatin VALUES (1670, '12:74', 12, 74, 'q<u>aa</u>luu fam<u>aa</u> jaz<u>aa</u>uhu in kuntum k<u>aadz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (1671, '12:75', 12, 75, 'q<u>aa</u>luu jaz<u>aa</u>uhu man wujida fii ra<u>h</u>lihi fahuwa jaz<u>aa</u>uhu ka<u>dzaa</u>lika najzii <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1672, '12:76', 12, 76, 'fabada-a bi-aw\'iyatihim qabla wi\'<u>aa</u>-i akhiihi tsumma istakhrajah<u>aa</u> min wi\'<u>aa</u>-i akhiihi ka<u>dzaa</u>lika kidn<u>aa</u> liyuusufa m<u>aa</u> k<u>aa</u>na liya/khu<u>dz</u>a akh<u>aa</u>hu fii diini lmaliki ill<u>aa</u> an yasy<u>aa</u>-all<u>aa</u>hu narfa\'u daraj<u>aa</u>tin man nasy<u>aa</u>u wafawqa kulli <u>dz</u>ii \'ilmin \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (1673, '12:77', 12, 77, 'q<u>aa</u>luu in yasriq faqad saraqa akhun lahu min qablu fa-asarrah<u>aa</u> yuusufu fii nafsihi walam yubdih<u>aa</u> lahum q<u>aa</u>la antum syarrun mak<u>aa</u>nan wal<u>aa</u>hu a\'lamu bim<u>aa</u> ta<u>sh</u>ifuun\r\r\n');
INSERT INTO quranLatin VALUES (1674, '12:78', 12, 78, 'q<u>aa</u>luu y<u>aa</u> ayyuh<u>aa</u> l\'aziizu inna lahu aban syaykhan kabiiran fakhu<u>dz</u> a<u>h</u>adan<u>aa</u> mak<u>aa</u>nahu inn<u>aa</u> nar<u>aa</u>ka mina lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (1675, '12:79', 12, 79, 'q<u>aa</u>la ma\'<u>aadz</u>all<u>aa</u>hi an na/khu<u>dz</u>a ill<u>aa</u> man wajadn<u>aa</u> mat<u>aa</u>\'an<u>aa</u> \'indahu inn<u>aa</u> i<u>dz</u>an la<u>zhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (1676, '12:80', 12, 80, 'falamm<u>aa</u> istay-asuu minhu khala<u>sh</u>uu najiyyan q<u>aa</u>la kabiiruhum alam ta\'lamuu anna ab<u>aa</u>kum qad akha<u>dz</u>a \'alaykum mawtsiqan minall<u>aa</u>hi wamin qablu m<u>aa</u> farra<u>th</u>tum fii yuusufa falan abra<u>h</u>a al-ar<u>dh</u>a <u>h</u>att<u>aa</u> ya/<u>dz</u>ana lii abii aw ya<u>h</u>kumall<u>aa</u>hu lii wahuwa khayru al<u>haa</u>kimiin\r\r\n');
INSERT INTO quranLatin VALUES (1677, '12:81', 12, 81, 'irji\'uu il<u>aa</u> abiikum faquuluu y<u>aa</u> ab<u>aa</u>n<u>aa</u> inna ibnaka saraqa wam<u>aa</u> syahidn<u>aa</u> ill<u>aa</u> bim<u>aa</u> \'alimn<u>aa</u> wam<u>aa</u> kunn<u>aa</u> lilghaybi <u>haa</u>fi<u>zh</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1678, '12:82', 12, 82, 'was-ali lqaryatallatii kunn<u>aa</u> fiih<u>aa</u> wal\'iirallatii aqbaln<u>aa</u> fiih<u>aa</u> wa-inn<u>aa</u> la<u>shaa</u>diquun\r\r\n');
INSERT INTO quranLatin VALUES (1679, '12:83', 12, 83, 'q<u>aa</u>la bal sawwalat lakum anfusukum amran fa<u>sh</u>abrun jamiilun \'as<u>aa</u>ll<u>aa</u>hu an ya/tiyanii bihim jamii\'an innahu huwa l\'aliimu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1680, '12:84', 12, 84, 'watawall<u>aa</u> \'anhum waq<u>aa</u>la y<u>aa</u> asaf<u>aa</u> \'al<u>aa</u> yuusufa wabya<u>dhdh</u>at \'ayn<u>aa</u>hu mina l<u>h</u>uzni fahuwa ka<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1681, '12:85', 12, 85, 'q<u>aa</u>luu tal<u>aa</u>hi taftau ta<u>dz</u>kuru yuusufa <u>h</u>att<u>aa</u> takuuna <u>h</u>ara<u>dh</u>an aw takuuna mina lh<u>aa</u>likiin\r\r\n');
INSERT INTO quranLatin VALUES (1682, '12:86', 12, 86, 'q<u>aa</u>la innam<u>aa</u> asykuu batstsii wa<u>h</u>uznii il<u>aa</u>ll<u>aa</u>hi wa-a\'lamu minall<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1683, '12:87', 12, 87, 'y<u>aa</u> baniyya i<u>dz</u>habuu fata<u>h</u>assasuu min yuusufa wa-akhiihi wal<u>aa</u> tay-asuu min raw<u>h</u>ill<u>aa</u>hi innahu l<u>aa</u> yay-asu min raw<u>h</u>ill<u>aa</u>hi ill<u>aa</u> lqawmu lk<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (1684, '12:88', 12, 88, 'falamm<u>aa</u> dakhaluu \'alayhi q<u>aa</u>luu y<u>aa</u> ayyuh<u>aa</u> l\'aziizu massan<u>aa</u> wa-ahlan<u>aa</u> <u>dhdh</u>urru waji/n<u>aa</u> bibi<u>daa</u>\'atin muzj<u>aa</u>tin fa-awfi lan<u>aa</u> lkayla wata<u>sh</u>addaq \'alayn<u>aa</u> innall<u>aa</u>ha yajzii lmuta<u>sh</u>addiqiin\r\r\n');
INSERT INTO quranLatin VALUES (1685, '12:89', 12, 89, 'q<u>aa</u>la hal \'alimtum m<u>aa</u> fa\'altum biyuusufa wa-akhiihi i<u>dz</u> antum j<u>aa</u>hiluun\r\r\n');
INSERT INTO quranLatin VALUES (1686, '12:90', 12, 90, 'q<u>aa</u>luu a-innaka la-anta yuusufa q<u>aa</u>la an<u>aa</u> yuusufu wah<u>aadzaa</u> akhii qad mannall<u>aa</u>hu \'alayn<u>aa</u> innahu man yattaqi waya<u>sh</u>bir fa-innall<u>aa</u>ha l<u>aa</u> yu<u>dh</u>ii\'u ajra lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (1687, '12:91', 12, 91, 'q<u>aa</u>luu tal<u>aa</u>hi laqad <u>aa</u>tsarakall<u>aa</u>hu \'alayn<u>aa</u> wa-in kunn<u>aa</u> lakh<u>aath</u>i-iin\r\r\n');
INSERT INTO quranLatin VALUES (1688, '12:92', 12, 92, 'q<u>aa</u>la l<u>aa</u> tatsriiba \'alaykumu lyawma yaghfirull<u>aa</u>hu lakum wahuwa ar<u>h</u>amu rr<u>aah</u>imiin\r\r\n');
INSERT INTO quranLatin VALUES (1689, '12:93', 12, 93, 'i<u>dz</u>habuu biqamii<u>sh</u>ii h<u>aadzaa</u> fa-alquuhu \'al<u>aa</u> wajhi abii ya/ti ba<u>sh</u>iiran wa/tuunii bi-ahlikum ajma\'iin\r\r\n');
INSERT INTO quranLatin VALUES (1690, '12:94', 12, 94, 'walamm<u>aa</u> fa<u>sh</u>alati l\'iiru q<u>aa</u>la abuuhum innii la-ajidu rii<u>h</u>a yuusufa lawl<u>aa</u> an tufanniduun\r\r\n');
INSERT INTO quranLatin VALUES (1691, '12:95', 12, 95, 'q<u>aa</u>luu tal<u>aa</u>hi innaka lafii <u>dh</u>al<u>aa</u>lika lqadiim\r\r\n');
INSERT INTO quranLatin VALUES (1692, '12:96', 12, 96, 'falamm<u>aa</u> an j<u>aa</u>-a albasyiiru alq<u>aa</u>hu \'al<u>aa</u> wajhihi fartadda ba<u>sh</u>iiran q<u>aa</u>la alam aqul lakum innii a\'lamu minall<u>aa</u>hi m<u>aa</u> l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1693, '12:97', 12, 97, 'q<u>aa</u>luu y<u>aa</u> ab<u>aa</u>n<u>aa</u> istaghfir lan<u>aa</u> <u>dz</u>unuuban<u>aa</u> inn<u>aa</u> kunn<u>aa</u> kh<u>aath</u>i-iin\r\r\n');
INSERT INTO quranLatin VALUES (1694, '12:98', 12, 98, 'q<u>aa</u>la sawfa astaghfiru lakum rabbii innahu huwa lghafuuru rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1695, '12:99', 12, 99, 'falamm<u>aa</u> dakhaluu \'al<u>aa</u> yuusufa <u>aa</u>w<u>aa</u> ilayhi abawayhi waq<u>aa</u>la udkhuluu mi<u>sh</u>ra in sy<u>aa</u>-all<u>aa</u>hu <u>aa</u>miniin\r\r\n');
INSERT INTO quranLatin VALUES (1696, '12:100', 12, 100, 'warafa\'a abawayhi \'al<u>aa</u> l\'arsyi wakharruu lahu sujjadan waq<u>aa</u>la y<u>aa</u> abati h<u>aadzaa</u> ta/wiilu ru/y<u>aa</u>ya min qablu qad ja\'alah<u>aa</u> rabbii <u>h</u>aqqan waqad a<u>h</u>sana bii i<u>dz</u> akhrajanii mina ssijni waj<u>aa</u>-a bikum mina lbadwi min ba\'di an nazagha sysyay<u>th</u><u>aa</u>nu baynii wabayna ikhwatii inna rabbii la<u>th</u>iifun lim<u>aa</u> yasy<u>aa</u>u innahu huwa l\'aliimu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1697, '12:101', 12, 101, 'rabbi qad <u>aa</u>taytanii mina lmulki wa\'allamtanii min ta/wiili l-a<u>haa</u>diitsi f<u>aath</u>ira ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i anta waliyyii fii dduny<u>aa</u> wal-<u>aa</u>khirati tawaffanii musliman wa-al<u>h</u>iqnii bi<u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1698, '12:102', 12, 102, '<u>dzaa</u>lika min anb<u>aa</u>-i lghaybi nuu<u>h</u>iihi ilayka wam<u>aa</u> kunta ladayhim i<u>dz</u> ajma\'uu amrahum wahum yamkuruun\r\r\n');
INSERT INTO quranLatin VALUES (1699, '12:103', 12, 103, 'wam<u>aa</u> aktsaru nn<u>aa</u>si walaw <u>h</u>ara<u>sh</u>ta bimu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (1700, '12:104', 12, 104, 'wam<u>aa</u> tas-aluhum \'alayhi min ajrin in huwa ill<u>aa</u> <u>dz</u>ikrun lil\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (1701, '12:105', 12, 105, 'waka-ayyin min <u>aa</u>yatin fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i yamurruuna \'alayh<u>aa</u> wahum \'anh<u>aa</u> mu\'ri<u>dh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1702, '12:106', 12, 106, 'wam<u>aa</u> yu/minu aktsaruhum bil<u>aa</u>hi ill<u>aa</u> wahum musyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (1703, '12:107', 12, 107, 'afa-aminuu an ta/tiyahum gh<u>aa</u>syiyatun min \'a<u>dzaa</u>bill<u>aa</u>hi aw ta/tiyahumu ss<u>aa</u>\'atu baghtatan wahum l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (1704, '12:108', 12, 108, 'qul h<u>aadz</u>ihi sabiilii ad\'uu il<u>aa</u>ll<u>aa</u>hi \'al<u>aa</u> ba<u>sh</u>iiratin an<u>aa</u> wamani ittaba\'anii wasub<u>haa</u>nall<u>aa</u>hi wam<u>aa</u> an<u>aa</u> mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (1705, '12:109', 12, 109, 'wam<u>aa</u> arsaln<u>aa</u> min qablika ill<u>aa</u> rij<u>aa</u>lan nuu<u>h</u>ii ilayhim min ahli lqur<u>aa</u> afalam yasiiruu fii l-ar<u>dh</u>i fayan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatulla<u>dz</u>iina min qablihim walad<u>aa</u>ru l-<u>aa</u>khirati khayrun lilla<u>dz</u>iina ittaqaw afal<u>aa</u> ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (1706, '12:110', 12, 110, '<u>h</u>att<u>aa</u> i<u>dzaa</u> istay-asa rrusulu wa<u>zh</u>annuu annahum qad ku<u>dz</u>ibuu j<u>aa</u>-ahum na<u>sh</u>run<u>aa</u> fanujjiya man nasy<u>aa</u>u wal<u>aa</u> yuraddu ba/sun<u>aa</u> \'ani lqawmi lmujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (1707, '12:111', 12, 111, 'laqad k<u>aa</u>na fii qa<u>sh</u>a<u>sh</u>ihim \'ibratun li-ulii l-alb<u>aa</u>bi m<u>aa</u> k<u>aa</u>na <u>h</u>adiitsan yuftar<u>aa</u> wal<u>aa</u>kin ta<u>sh</u>diiqalla<u>dz</u>ii bayna yadayhi wataf<u>sh</u>iila kulli syay-in wahudan wara<u>h</u>matan liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1708, '13:1', 13, 1, 'alif-l<u>aa</u>m-miim-r<u>aa</u> tilka <u>aa</u>y<u>aa</u>tu lkit<u>aa</u>bi walla<u>dz</u>ii unzila ilayka min rabbika l<u>h</u>aqqu wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1709, '13:2', 13, 2, '<B>al</b>l<u>aa</u>hulla<u>dz</u>ii rafa\'a ssam<u>aa</u>w<u>aa</u>ti bighayri \'amadin tarawnah<u>aa</u> tsumma istaw<u>aa</u> \'al<u>aa</u> l\'arsyi wasakhkhara sysyamsa walqamara kullun yajrii li-ajalin musamman yudabbiru l-amra yufa<u>shsh</u>ilu l-<u>aa</u>y<u>aa</u>ti la\'allakum biliq<u>aa</u>-i rabbikum tuuqinuun\r\r\n');
INSERT INTO quranLatin VALUES (1710, '13:3', 13, 3, 'wahuwalla<u>dz</u>ii madda l-ar<u>dh</u>a waja\'ala fiih<u>aa</u> raw<u>aa</u>siya wa-anh<u>aa</u>ran wamin kulli tstsamar<u>aa</u>ti ja\'ala fiih<u>aa</u> zawjayni itsnayni yughsyiillayla nnah<u>aa</u>ra inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yatafakkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1711, '13:4', 13, 4, 'wafii l-ar<u>dh</u>i qi<u>th</u>a\'un mutaj<u>aa</u>wir<u>aa</u>tun wajann<u>aa</u>tun min a\'n<u>aa</u>bin wazar\'un wanakhiilun <u>sh</u>inw<u>aa</u>nun waghayru <u>sh</u>inw<u>aa</u>nin yusq<u>aa</u> bim<u>aa</u>-in w<u>aah</u>idin wanufa<u>dhdh</u>ilu ba\'<u>dh</u>ah<u>aa</u> \'al<u>aa</u> ba\'<u>dh</u>in fii lukuli inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (1712, '13:5', 13, 5, 'wa-in ta\'jab fa\'ajabun qawluhum a-i<u>dzaa</u> kunn<u>aa</u> tur<u>aa</u>ban a-inn<u>aa</u> lafii khalqin jadiidin ul<u>aa</u>-ikalla<u>dz</u>iina kafaruu birabbihim waul<u>aa</u>-ika l-aghl<u>aa</u>lu fii a\'n<u>aa</u>qihim waul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (1713, '13:6', 13, 6, 'wayasta\'jiluunaka bissayyi-ati qabla l<u>h</u>asanati waqad khalat min qablihimu lmatsul<u>aa</u>tu wa-inna rabbaka la<u>dz</u>uu maghfiratin linn<u>aa</u>si \'al<u>aa</u> <u>zh</u>ulmihim wa-inna rabbaka lasyadiidu l\'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1714, '13:7', 13, 7, 'wayaquululla<u>dz</u>iina kafaruu lawl<u>aa</u> unzila \'alayhi <u>aa</u>yatun min rabbihi innam<u>aa</u> anta mun<u>dz</u>irun walikulli qawmin h<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (1715, '13:8', 13, 8, '<B>al</b>l<u>aa</u>hu ya\'lamu m<u>aa</u> ta<u>h</u>milu kullu unts<u>aa</u> wam<u>aa</u> taghii<u>dh</u>u l-ar<u>haa</u>mu wam<u>aa</u> tazd<u>aa</u>du wakullu syay-in \'indahu bimiqd<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1716, '13:9', 13, 9, ' \'<u>aa</u>limu lghaybi wasysyah<u>aa</u>dati lkabiiru lmuta\'<u>aa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (1717, '13:10', 13, 10, 'saw<u>aa</u>un minkum man asarra lqawla waman jahara bihi waman huwa mustakhfin billayli was<u>aa</u>ribun binnah<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1718, '13:11', 13, 11, 'lahu mu\'aqqib<u>aa</u>tun min bayni yadayhi wamin khalfihi ya<u>h</u>fa<u>zh</u>uunahu min amrill<u>aa</u>hi innall<u>aa</u>ha l<u>aa</u> yughayyiru m<u>aa</u> biqawmin <u>h</u>att<u>aa</u> yughayyiruu m<u>aa</u> bi-anfusihim wa-i<u>dzaa</u> ar<u>aa</u>dall<u>aa</u>hu biqawmin suu-an fal<u>aa</u> maradda lahu wam<u>aa</u> lahum min duunihi min w<u>aa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (1719, '13:12', 13, 12, 'huwalla<u>dz</u>ii yuriikumu lbarqa khawfan wa<u>th</u>ama\'an wayunsyi-u ssa<u>haa</u>ba tstsiq<u>aa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (1720, '13:13', 13, 13, 'wayusabbi<u>h</u>u rra\'du bi<u>h</u>amdihi walmal<u>aa</u>-ikatu min khiifatihi wayursilu <u>shsh</u>aw<u>aa</u>\'iqa fayu<u>sh</u>iibu bih<u>aa</u> man yasy<u>aa</u>u wahum yuj<u>aa</u>diluuna fiill<u>aa</u>hi wahuwa syadiidu lmi<u>haa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (1721, '13:14', 13, 14, 'lahu da\'watu l<u>h</u>aqqi walla<u>dz</u>iina yad\'uuna min duunihi l<u>aa</u> yastajiibuuna lahum bisyay-in ill<u>aa</u> kab<u>aa</u>si<u>th</u>i kaffayhi il<u>aa</u> lm<u>aa</u>-i liyablugha f<u>aa</u>hu wam<u>aa</u> huwa bib<u>aa</u>lighihi wam<u>aa</u> du\'<u>aa</u>u lk<u>aa</u>firiina ill<u>aa</u> fii <u>dh</u>al<u>aa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (1722, '13:15', 13, 15, 'walill<u>aa</u>hi yasjudu man fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i <u>th</u>aw\'an wakarhan wa<u>zh</u>il<u>aa</u>luhum bilghuduwwi wal-<u>aasaa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (1723, '13:16', 13, 16, 'qul man rabbu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i qulill<u>aa</u>hu qul afattakha<u>dz</u>tum min duunihi awliy<u>aa</u>-a l<u>aa</u> yamlikuuna li-anfusihim naf\'an wal<u>aa</u> <u>dh</u>arran qul hal yastawii l-a\'m<u>aa</u> walba<u>sh</u>iiru am hal tastawii <u>zhzh</u>ulum<u>aa</u>tu wannuuru am ja\'aluu lill<u>aa</u>hi syurak<u>aa</u>-a khalaquu kakhalqihi fatasy<u>aa</u>baha lkhalqu \'alayhim qulill<u>aa</u>hu kh<u>aa</u>liqu kulli syay-in wahuwa lw<u>aah</u>idu lqahh<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1724, '13:17', 13, 17, 'anzala mina ssam<u>aa</u>-i m<u>aa</u>-an fas<u>aa</u>lat awdiyatun biqadarih<u>aa</u> fa<u>h</u>tamala ssaylu zabadan r<u>aa</u>biyan wamimm<u>aa</u> yuuqiduuna \'alayhi fii nn<u>aa</u>ri ibtigh<u>aa</u>-a <u>h</u>ilyatin aw mat<u>aa</u>\'in zabadun mitsluhu ka<u>dzaa</u>lika ya<u>dh</u>ribull<u>aa</u>hu l<u>h</u>aqqa walb<u>aath</u>ila fa-amm<u>aa</u> zzabadu faya<u>dz</u>habu juf<u>aa</u>-an wa-amm<u>aa</u> m<u>aa</u> yanfa\'u nn<u>aa</u>sa fayamkutsu fii l-ar<u>dh</u>i ka<u>dzaa</u>lika ya<u>dh</u>ribull<u>aa</u>hu l-amts<u>aa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (1725, '13:18', 13, 18, 'lilla<u>dz</u>iina istaj<u>aa</u>buu lirabbihimu l<u>h</u>usn<u>aa</u> walla<u>dz</u>iina lam yastajiibuu lahu law anna lahum m<u>aa</u> fii l-ar<u>dh</u>i jamii\'an wamitslahu ma\'ahu laftadaw bihi ul<u>aa</u>-ika lahum suu-u l<u>h</u>is<u>aa</u>bi wama/w<u>aa</u>hum jahannamu wabi/sa lmih<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (1726, '13:19', 13, 19, 'afaman ya\'lamu annam<u>aa</u> unzila ilayka min rabbika l<u>h</u>aqqu kaman huwa a\'m<u>aa</u> innam<u>aa</u> yata<u>dz</u>akkaru uluu l-alb<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1727, '13:20', 13, 20, '<B>al</b>la<u>dz</u>iina yuufuuna bi\'ahdill<u>aa</u>hi wal<u>aa</u> yanqu<u>dh</u>uuna lmiits<u>aa</u>q\r\r\n');
INSERT INTO quranLatin VALUES (1728, '13:21', 13, 21, 'walla<u>dz</u>iina ya<u>sh</u>iluuna m<u>aa</u> amarall<u>aa</u>hu bihi an yuu<u>sh</u>ala wayakhsyawna rabbahum wayakh<u>aa</u>fuuna suu-a l<u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1729, '13:22', 13, 22, 'walla<u>dz</u>iina <u>sh</u>abaruu ibtigh<u>aa</u>-a wajhi rabbihim wa-aq<u>aa</u>muu <u>shsh</u>al<u>aa</u>ta wa-anfaquu mimm<u>aa</u> razaqn<u>aa</u>hum sirran wa\'al<u>aa</u>niyatan wayadrauuna bil<u>h</u>asanati ssayyi-ata ul<u>aa</u>-ika lahum \'uqb<u>aa</u> dd<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1730, '13:23', 13, 23, 'jann<u>aa</u>tu \'adnin yadkhuluunah<u>aa</u> waman <u>sh</u>ala<u>h</u>a min <u>aa</u>b<u>aa</u>-ihim wa-azw<u>aa</u>jihim wa<u>dz</u>urriyy<u>aa</u>tihim walmal<u>aa</u>-ikatu yadkhuluuna \'alayhim min kulli b<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1731, '13:24', 13, 24, 'sal<u>aa</u>mun \'alaykum bim<u>aa</u> <u>sh</u>abartum fani\'ma \'uqb<u>aa</u> dd<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1732, '13:25', 13, 25, 'walla<u>dz</u>iina yanqu<u>dh</u>uuna \'ahdall<u>aa</u>hi min ba\'di miits<u>aa</u>qihi wayaq<u>th</u>a\'uuna m<u>aa</u> amarall<u>aa</u>hu bihi an yuu<u>sh</u>ala wayufsiduuna fii l-ar<u>dh</u>i ul<u>aa</u>-ika lahumulla\'natu walahum suu-u dd<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1733, '13:26', 13, 26, '<B>al</b>l<u>aa</u>hu yabsu<u>th</u>u rrizqa liman yasy<u>aa</u>u wayaqdiru wafari<u>h</u>uu bil<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wam<u>aa</u> l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> fii l-<u>aa</u>khirati ill<u>aa</u> mat<u>aa</u>\'<B>un</b>\r\r\n');
INSERT INTO quranLatin VALUES (1734, '13:27', 13, 27, 'wayaquululla<u>dz</u>iina kafaruu lawl<u>aa</u> unzila \'alayhi <u>aa</u>yatun min rabbihi qul innall<u>aa</u>ha yu<u>dh</u>illu man yasy<u>aa</u>u wayahdii ilayhi man an<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1735, '13:28', 13, 28, '<B>al</b>la<u>dz</u>iina <u>aa</u>manuu wata<u>th</u>ma-innu quluubuhum bi<u>dz</u>ikrill<u>aa</u>hi l<u>aa</u> bi<u>dz</u>ikrill<u>aa</u>hi ta<u>th</u>ma-innu lquluub\r\r\n');
