INSERT INTO quranLatin VALUES (1737, '13:30', 13, 30, 'ka<u>dzaa</u>lika arsaln<u>aa</u>ka fii ummatin qad khalat min qablih<u>aa</u> umamun litatluwa \'alayhimulla<u>dz</u>ii aw<u>h</u>ayn<u>aa</u> ilayka wahum yakfuruuna birra<u>h</u>m<u>aa</u>ni qul huwa rabbii l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa \'alayhi tawakkaltu wa-ilayhi mat<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1738, '13:31', 13, 31, 'walaw anna qur-<u>aa</u>nan suyyirat bihi ljib<u>aa</u>lu aw qu<u>ththh</u>i\'at bihi l-ar<u>dh</u>u aw kullima bihi lmawt<u>aa</u> bal lill<u>aa</u>hi l-amru jamii\'an afalam yay-asilla<u>dz</u>iina <u>aa</u>manuu an law yasy<u>aa</u>ull<u>aa</u>hu lahad<u>aa</u> nn<u>aa</u>sa jamii\'an wal<u>aa</u> yaz<u>aa</u>lulla<u>dz</u>iina kafaruu tu<u>sh</u>iibuhum bim<u>aa</u> <u>sh</u>ana\'uu q<u>aa</u>ri\'atun aw ta<u>h</u>ullu qariiban min d<u>aa</u>rihim <u>h</u>att<u>aa</u> ya/tiya wa\'dull<u>aa</u>hi innall<u>aa</u>ha l<u>aa</u> yukhlifu lmii\'<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (1739, '13:32', 13, 32, 'walaqadi istuhzi-a birusulin min qablika fa-amlaytu lilla<u>dz</u>iina kafaruu tsumma akha<u>dz</u>tuhum fakayfa k<u>aa</u>na \'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1740, '13:33', 13, 33, 'afaman huwa q<u>aa</u>-imun \'al<u>aa</u> kulli nafsin bim<u>aa</u> kasabat waja\'aluu lill<u>aa</u>hi syurak<u>aa</u>-a qul sammuuhum am tunabbi-uunahu bim<u>aa</u> l<u>aa</u> ya\'lamu fii l-ar<u>dh</u>i am bi<u>zhaa</u>hirin mina lqawli bal zuyyina lilla<u>dz</u>iina kafaruu makruhum wa<u>sh</u>udduu \'ani ssabiili waman yu<u>dh</u>lilill<u>aa</u>hu fam<u>aa</u> lahu min h<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (1741, '13:34', 13, 34, 'lahum \'a<u>dzaa</u>bun fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wala\'a<u>dzaa</u>bu l-<u>aa</u>khirati asyaqqu wam<u>aa</u> lahum minall<u>aa</u>hi min w<u>aa</u>q\r\r\n');
