INSERT INTO quranLatin VALUES (1743, '13:36', 13, 36, 'walla<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu lkit<u>aa</u>ba yafra<u>h</u>uuna bim<u>aa</u> unzila ilayka wamina l-a<u>h</u>z<u>aa</u>bi man yunkiru ba\'<u>dh</u>ahu qul innam<u>aa</u> umirtu an a\'budall<u>aa</u>ha wal<u>aa</u> usyrika bihi ilayhi ad\'uu wa-ilayhi ma<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1744, '13:37', 13, 37, 'waka<u>dzaa</u>lika anzaln<u>aa</u>hu <u>h</u>ukman \'arabiyyan wala-ini ittaba\'ta ahw<u>aa</u>-ahum ba\'da m<u>aa</u> j<u>aa</u>-aka mina l\'ilmi m<u>aa</u> laka minall<u>aa</u>hi min waliyyin wal<u>aa</u> w<u>aa</u>q\r\r\n');
INSERT INTO quranLatin VALUES (1745, '13:38', 13, 38, 'walaqad arsaln<u>aa</u> rusulan min qablika waja\'aln<u>aa</u> lahum azw<u>aa</u>jan wa<u>dz</u>urriyyatan wam<u>aa</u> k<u>aa</u>na lirasuulin an ya/tiya bi-<u>aa</u>yatin ill<u>aa</u> bi-i<u>dz</u>nill<u>aa</u>hi likulli ajalin kit<u>aa</u>b\r\r\n');
