INSERT INTO quranLatin VALUES (1747, '13:40', 13, 40, 'wa-in m<u>aa</u> nuriyannaka ba\'<u>dh</u>alla<u>dz</u>ii na\'iduhum aw natawaffayannaka fa-innam<u>aa</u> \'alayka lbal<u>aa</u>ghu wa\'alayn<u>aa</u> l<u>h</u>is<u>aa</u>b\r\r\n');
