INSERT INTO quranLatin VALUES (1749, '13:42', 13, 42, 'waqad makaralla<u>dz</u>iina min qablihim falill<u>aa</u>hi lmakru jamii\'an ya\'lamu m<u>aa</u> taksibu kullu nafsin wasaya\'lamu lkuff<u>aa</u>ru liman \'uqb<u>aa</u> dd<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1750, '13:43', 13, 43, 'wayaquululla<u>dz</u>iina kafaruu lasta mursalan qul kaf<u>aa</u> bil<u>aa</u>hi syahiidan baynii wabaynakum waman \'indahu \'ilmu lkit<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1751, '14:1', 14, 1, 'alif-l<u>aa</u>m-r<u>aa</u> kit<u>aa</u>bun anzaln<u>aa</u>hu ilayka litukhrija nn<u>aa</u>sa mina <u>zhzh</u>ulum<u>aa</u>ti il<u>aa</u> nnuuri bi-i<u>dz</u>ni rabbihim il<u>aa</u> <u>sh</u>ir<u>aath</u>i l\'aziizi l<u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (1752, '14:2', 14, 2, '<B>al</b>l<u>aa</u>hilla<u>dz</u>ii lahu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wawaylun lilk<u>aa</u>firiina min \'a<u>dzaa</u>bin syadiid\r\r\n');
INSERT INTO quranLatin VALUES (1753, '14:3', 14, 3, '<B>al</b>la<u>dz</u>iina yasta<u>h</u>ibbuuna l<u>h</u>ay<u>aa</u>ta dduny<u>aa</u> \'al<u>aa</u> l-<u>aa</u>khirati waya<u>sh</u>udduuna \'an sabiilill<u>aa</u>hi wayabghuunah<u>aa</u> \'iwajan ul<u>aa</u>-ika fii <u>dh</u>al<u>aa</u>lin ba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (1754, '14:4', 14, 4, 'wam<u>aa</u> arsaln<u>aa</u> min rasuulin ill<u>aa</u> bilis<u>aa</u>ni qawmihi liyubayyina lahum fayu<u>dh</u>illull<u>aa</u>hu man yasy<u>aa</u>u wayahdii man yasy<u>aa</u>u wahuwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1755, '14:5', 14, 5, 'walaqad arsaln<u>aa</u> muus<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> an akhrij qawmaka mina <u>zhzh</u>ulum<u>aa</u>ti il<u>aa</u> nnuuri wa<u>dz</u>akkirhum bi-ayy<u>aa</u>mill<u>aa</u>hi inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin likulli <u>sh</u>abb<u>aa</u>rin syakuur\r\r\n');
INSERT INTO quranLatin VALUES (1756, '14:6', 14, 6, 'wa-i<u>dz</u> q<u>aa</u>la muus<u>aa</u> liqawmihi u<u>dz</u>kuruu ni\'matall<u>aa</u>hi \'alaykum i<u>dz</u> anj<u>aa</u>kum min <u>aa</u>li fir\'awna yasuumuunakum suu-a l\'a<u>dzaa</u>bi wayu<u>dz</u>abbi<u>h</u>uuna abn<u>aa</u>-akum wayasta<u>h</u>yuuna nis<u>aa</u>-akum wafii <u>dzaa</u>likum bal<u>aa</u>un min rabbikum \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1757, '14:7', 14, 7, 'wa-i<u>dz</u> ta-a<u>dzdz</u>ana rabbukum la-in syakartum la-aziidannakum wala-in kafartum inna \'a<u>dzaa</u>bii lasyadiid\r\r\n');
INSERT INTO quranLatin VALUES (1758, '14:8', 14, 8, 'waq<u>aa</u>la muus<u>aa</u> in takfuruu antum waman fii l-ar<u>dh</u>i jamii\'an fa-innall<u>aa</u>ha laghaniyyun <u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (1759, '14:9', 14, 9, 'alam ya/tikum nabaulla<u>dz</u>iina min qablikum qawmi nuu<u>h</u>in wa\'<u>aa</u>din watsamuuda walla<u>dz</u>iina min ba\'dihim l<u>aa</u> ya\'lamuhum ill<u>aa</u>ll<u>aa</u>hu j<u>aa</u>-at-hum rusuluhum bilbayyin<u>aa</u>ti faradduu aydiyahum fii afw<u>aa</u>hihim waq<u>aa</u>luu inn<u>aa</u> kafarn<u>aa</u> bim<u>aa</u> ursiltum bihi wa-inn<u>aa</u> lafii syakkin mimm<u>aa</u> tad\'uunan<u>aa</u> ilayhi muriib\r\r\n');
INSERT INTO quranLatin VALUES (1760, '14:10', 14, 10, 'q<u>aa</u>lat rusuluhum afiill<u>aa</u>hi syakkun f<u>aath</u>iri ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i yad\'uukum liyaghfira lakum min <u>dz</u>unuubikum wayu-akhkhirakum il<u>aa</u> ajalin musamman q<u>aa</u>luu in antum ill<u>aa</u> basyarun mitslun<u>aa</u> turiiduuna an ta<u>sh</u>udduun<u>aa</u> \'amm<u>aa</u> k<u>aa</u>na ya\'budu <u>aa</u>b<u>aa</u>un<u>aa</u> fa/tuun<u>aa</u> bisul<u>th</u><u>aa</u>nin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1761, '14:11', 14, 11, 'q<u>aa</u>lat lahum rusuluhum in na<u>h</u>nu ill<u>aa</u> basyarun mitslukum wal<u>aa</u>kinnall<u>aa</u>ha yamunnu \'al<u>aa</u> man yasy<u>aa</u>u min \'ib<u>aa</u>dihi wam<u>aa</u> k<u>aa</u>na lan<u>aa</u> an na/tiyakum bisul<u>th</u><u>aa</u>nin ill<u>aa</u> bi-i<u>dz</u>nill<u>aa</u>hi wa\'al<u>aa</u>ll<u>aa</u>hi falyatawakkali lmu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1762, '14:12', 14, 12, 'wam<u>aa</u> lan<u>aa</u>ll<u>aa</u> natawakkala \'al<u>aa</u>ll<u>aa</u>hi waqad had<u>aa</u>n<u>aa</u> subulan<u>aa</u> walana<u>sh</u>biranna \'al<u>aa</u> m<u>aa</u> <u>aadz</u>aytumuun<u>aa</u> wa\'al<u>aa</u>ll<u>aa</u>hi falyatawakkali lmutawakkiluun\r\r\n');
INSERT INTO quranLatin VALUES (1763, '14:13', 14, 13, 'waq<u>aa</u>lalla<u>dz</u>iina kafaruu lirusulihim lanukhrijannakum min ar<u>dh</u>in<u>aa</u> aw lata\'uudunna fii millatin<u>aa</u> fa-aw<u>haa</u> ilayhim rabbuhum lanuhlikanna <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (1764, '14:14', 14, 14, 'walanuskinannakumu l-ar<u>dh</u>a min ba\'dihim <u>dzaa</u>lika liman kh<u>aa</u>fa maq<u>aa</u>mii wakh<u>aa</u>fa wa\'iid\r\r\n');
INSERT INTO quranLatin VALUES (1765, '14:15', 14, 15, 'wastafta<u>h</u>uu wakh<u>aa</u>ba kullu jabb<u>aa</u>rin \'aniid\r\r\n');
INSERT INTO quranLatin VALUES (1766, '14:16', 14, 16, 'min war<u>aa</u>-ihi jahannamu wayusq<u>aa</u> min m<u>aa</u>-in <u>sh</u>adiid\r\r\n');
INSERT INTO quranLatin VALUES (1767, '14:17', 14, 17, 'yatajarra\'uhu wal<u>aa</u> yak<u>aa</u>du yusiighuhu waya/tiihi lmawtu min kulli mak<u>aa</u>nin wam<u>aa</u> huwa bimayyitin wamin war<u>aa</u>-ihi \'a<u>dzaa</u>bun ghalii<u>zh</u>\r\r\n');
INSERT INTO quranLatin VALUES (1768, '14:18', 14, 18, 'matsalulla<u>dz</u>iina kafaruu birabbihim a\'m<u>aa</u>luhum karam<u>aa</u>din isytaddat bihi rrii<u>h</u>u fii yawmin \'<u>aas</u>ifin l<u>aa</u> yaqdiruuna mimm<u>aa</u> kasabuu \'al<u>aa</u> syay-in <u>dzaa</u>lika huwa <u>dhdh</u>al<u>aa</u>lu lba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (1769, '14:19', 14, 19, 'alam tara annall<u>aa</u>ha khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a bil<u>h</u>aqqi in yasya/ yu<u>dz</u>hibkum waya/ti bikhalqin jadiid<b>in </b>\r\r\n');
INSERT INTO quranLatin VALUES (1770, '14:20', 14, 20, 'wam<u>aa</u> <u>dzaa</u>lika \'al<u>aa</u>ll<u>aa</u>hi bi\'aziiz\r\r\n');
INSERT INTO quranLatin VALUES (1771, '14:21', 14, 21, 'wabarazuu lill<u>aa</u>hi jamii\'an faq<u>aa</u>la <u>dhdh</u>u\'af<u>aa</u>u lilla<u>dz</u>iina istakbaruu inn<u>aa</u> kunn<u>aa</u> lakum taba\'an fahal antum mughnuuna \'ann<u>aa</u> min \'a<u>dzaa</u>bill<u>aa</u>hi min syay-in q<u>aa</u>luu law had<u>aa</u>n<u>aa</u>ll<u>aa</u>hu lahadayn<u>aa</u>kum saw<u>aa</u>un \'alayn<u>aa</u> ajazi\'n<u>aa</u> am <u>sh</u>abarn<u>aa</u> m<u>aa</u> lan<u>aa</u> min ma<u>h</u>ii<u>sh</u>\r\r\n');
INSERT INTO quranLatin VALUES (1772, '14:22', 14, 22, 'waq<u>aa</u>la sysyay<u>th</u><u>aa</u>nu lamm<u>aa</u> qu<u>dh</u>iya l-amru innall<u>aa</u>ha wa\'adakum wa\'da l<u>h</u>aqqi wawa\'adtukum fa-akhlaftukum wam<u>aa</u> k<u>aa</u>na liya \'alaykum min sul<u>th</u><u>aa</u>nin ill<u>aa</u> an da\'awtukum fastajabtum lii fal<u>aa</u> taluumuunii waluumuu anfusakum m<u>aa</u> an<u>aa</u> bimu<u>sh</u>rikhikum wam<u>aa</u> antum bimu<u>sh</u>rikhiyya innii kafartu bim<u>aa</u> asyraktumuuni min qablu inna <u>zhzhaa</u>limiina lahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (1773, '14:23', 14, 23, 'waudkhilalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> bi-i<u>dz</u>ni rabbihim ta<u>h</u>iyyatuhum fiih<u>aa</u> sal<u>aa</u>m\r\r\n');
INSERT INTO quranLatin VALUES (1774, '14:24', 14, 24, 'alam tara kayfa <u>dh</u>araball<u>aa</u>hu matsalan kalimatan <u>th</u>ayyibatan kasyajaratin <u>th</u>ayyibatin a<u>sh</u>luh<u>aa</u> ts<u>aa</u>bitun wafar\'uh<u>aa</u> fii ssam<u>aa</u>/-\r\r\n');
INSERT INTO quranLatin VALUES (1775, '14:25', 14, 25, 'tu/tii ukulah<u>aa</u> kulla <u>h</u>iinin bi-i<u>dz</u>ni rabbih<u>aa</u> waya<u>dh</u>ribull<u>aa</u>hu l-amts<u>aa</u>la linn<u>aa</u>si la\'allahum yata<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1776, '14:26', 14, 26, 'wamatsalu kalimatin khabiitsatin kasyajaratin khabiitsatin ijtutstsat min fawqi l-ar<u>dh</u>i m<u>aa</u> lah<u>aa</u> min qar<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1777, '14:27', 14, 27, 'yutsabbitull<u>aa</u>hulla<u>dz</u>iina <u>aa</u>manuu bilqawli tsts<u>aa</u>biti fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wafii l-<u>aa</u>khirati wayu<u>dh</u>illull<u>aa</u>hu <u>zhzhaa</u>limiina wayaf\'alull<u>aa</u>hu m<u>aa</u> yasy<u>aa</u>/\r\r\n');
INSERT INTO quranLatin VALUES (1778, '14:28', 14, 28, 'alam tara il<u>aa</u>lla<u>dz</u>iina baddaluu ni\'matall<u>aa</u>hi kufran wa-a<u>h</u>alluu qawmahum d<u>aa</u>ra lbaw<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1779, '14:29', 14, 29, 'jahannama ya<u>sh</u>lawnah<u>aa</u> wabi/sa lqar<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1780, '14:30', 14, 30, 'waja\'aluu lill<u>aa</u>hi and<u>aa</u>dan liyu<u>dh</u>illuu \'an sabiilihi qul tamatta\'uu fa-inna ma<u>sh</u>iirakum il<u>aa</u> nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1781, '14:31', 14, 31, 'qul li\'ib<u>aa</u>diyalla<u>dz</u>iina <u>aa</u>manuu yuqiimuu <u>shsh</u>al<u>aa</u>ta wayunfiquu mimm<u>aa</u> razaqn<u>aa</u>hum sirran wa\'al<u>aa</u>niyatan min qabli an ya/tiya yawmun l<u>aa</u> bay\'un fiihi wal<u>aa</u> khil<u>aa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (1782, '14:32', 14, 32, '<B>al</b>l<u>aa</u>hulla<u>dz</u>ii khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a wa-anzala mina ssam<u>aa</u>-i m<u>aa</u>-an fa-akhraja bihi mina tstsamar<u>aa</u>ti rizqan lakum wasakhkhara lakumu lfulka litajriya fii lba<u>h</u>ri bi-amrihi wasakhkhara lakumu l-anh<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1783, '14:33', 14, 33, 'wasakhkhara lakumu sysyamsa walqamara d<u>aa</u>-ibayni wasakhkhara lakumullayla wannah<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1784, '14:34', 14, 34, 'wa<u>aa</u>t<u>aa</u>kum min kulli m<u>aa</u> sa-altumuuhu wa-in ta\'udduu ni\'matall<u>aa</u>hi l<u>aa</u> tu<u>hs</u>uuh<u>aa</u> inna l-ins<u>aa</u>na la<u>zh</u>aluumun kaff<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1785, '14:35', 14, 35, 'wa-i<u>dz</u> q<u>aa</u>la ibr<u>aa</u>hiimu rabbi ij\'al h<u>aadzaa</u> lbalada <u>aa</u>minan wajnubnii wabaniyya an na\'buda l-a<u>sh</u>n<u>aa</u>m\r\r\n');
INSERT INTO quranLatin VALUES (1786, '14:36', 14, 36, 'rabbi innahunna a<u>dh</u>lalna katsiiran mina nn<u>aa</u>si faman tabi\'anii fa-innahu minnii waman \'a<u>shaa</u>nii fa-innaka ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1787, '14:37', 14, 37, 'rabban<u>aa</u> innii askantu min <u>dz</u>urriyyatii biw<u>aa</u>din ghayri <u>dz</u>ii zar\'in \'inda baytika lmu<u>h</u>arrami rabban<u>aa</u> liyuqiimuu <u>shsh</u>al<u>aa</u>ta faj\'al af-idatan mina nn<u>aa</u>si tahwii ilayhim warzuqhum mina tstsamar<u>aa</u>ti la\'allahum yasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (1788, '14:38', 14, 38, 'rabban<u>aa</u> innaka ta\'lamu m<u>aa</u> nukhfii wam<u>aa</u> nu\'linu wam<u>aa</u> yakhf<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi min syay-in fii l-ar<u>dh</u>i wal<u>aa</u> fii ssam<u>aa</u>/-\r\r\n');
INSERT INTO quranLatin VALUES (1789, '14:39', 14, 39, 'al<u>h</u>amdu lill<u>aa</u>hilla<u>dz</u>ii wahaba lii \'al<u>aa</u> lkibari ism<u>aa</u>\'iila wa-is<u>haa</u>qa inna rabbii lasamii\'u ddu\'<u>aa</u>/-\r\r\n');
INSERT INTO quranLatin VALUES (1790, '14:40', 14, 40, 'rabbi ij\'alnii muqiima <u>shsh</u>al<u>aa</u>ti wamin <u>dz</u>urriyyatii rabban<u>aa</u> wtaqabbal du\'<u>aa</u>/-\r\r\n');
INSERT INTO quranLatin VALUES (1791, '14:41', 14, 41, 'rabban<u>aa</u> ighfir lii waliw<u>aa</u>lidayya walilmu/miniina yawma yaquumu l<u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1792, '14:42', 14, 42, 'wal<u>aa</u> ta<u>h</u>sabannall<u>aa</u>ha gh<u>aa</u>filan \'amm<u>aa</u> ya\'malu <u>zhzhaa</u>limuuna innam<u>aa</u> yu-akhkhiruhum liyawmin tasykha<u>sh</u>u fiihi l-ab<u>shaa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1793, '14:43', 14, 43, 'muh<u>th</u>i\'iina muqni\'ii ruuusihim l<u>aa</u> yartaddu ilayhim <u>th</u>arfuhum wa-af-idatuhum haw<u>aa</u>/\r\r\n');
INSERT INTO quranLatin VALUES (1794, '14:44', 14, 44, 'wa-an<u>dz</u>iri nn<u>aa</u>sa yawma ya/tiihimu l\'a<u>dzaa</u>bu fayaquululla<u>dz</u>iina <u>zh</u>alamuu rabban<u>aa</u> akhkhirn<u>aa</u> il<u>aa</u> ajalin qariibin nujib da\'wataka wanattabi\'i rrusula awa lam takuunuu aqsamtum min qablu m<u>aa</u> lakum min zaw<u>aa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (1795, '14:45', 14, 45, 'wasakantum fii mas<u>aa</u>kinilla<u>dz</u>iina <u>zh</u>alamuu anfusahum watabayyana lakum kayfa fa\'aln<u>aa</u> bihim wa<u>dh</u>arabn<u>aa</u> lakumu l-amts<u>aa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (1796, '14:46', 14, 46, 'waqad makaruu makrahum wa\'indall<u>aa</u>hi makruhum wa-in k<u>aa</u>na makruhum litazuula minhu ljib<u>aa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (1797, '14:47', 14, 47, 'fal<u>aa</u> ta<u>h</u>sabannall<u>aa</u>ha mukhlifa wa\'dihi rusulahu innall<u>aa</u>ha \'aziizun <u>dz</u>uu intiq<u>aa</u>m\r\r\n');
INSERT INTO quranLatin VALUES (1798, '14:48', 14, 48, 'yawma tubaddalu l-ar<u>dh</u>u ghayra l-ar<u>dh</u>i wassam<u>aa</u>w<u>aa</u>tu wabarazuu lill<u>aa</u>hi lw<u>aah</u>idi lqahh<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1799, '14:49', 14, 49, 'watar<u>aa</u> lmujrimiina yawma-i<u>dz</u>in muqarraniina fii l-a<u>sh</u>f<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (1800, '14:50', 14, 50, 'sar<u>aa</u>biiluhum min qa<u>th</u>r<u>aa</u>nin wataghsy<u>aa</u> wujuuhahumu nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (1801, '14:51', 14, 51, 'liyajziyall<u>aa</u>hu kulla nafsin m<u>aa</u> kasabat innall<u>aa</u>ha sarii\'u l<u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (1802, '14:52', 14, 52, 'h<u>aadzaa</u> bal<u>aa</u>ghun linn<u>aa</u>si waliyun<u>dz</u>aruu bihi waliya\'lamuu annam<u>aa</u> huwa il<u>aa</u>hun w<u>aah</u>idun waliya<u>dzdz</u>akkara uluu l-alb<u>aa</u>b<dir>\r\r\n');
INSERT INTO quranLatin VALUES (1803, '15:1', 15, 1, 'alif-l<u>aa</u>m-r<u>aa</u> tilka <u>aa</u>y<u>aa</u>tu lkit<u>aa</u>bi waqur-<u>aa</u>nin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (1804, '15:2', 15, 2, 'rubam<u>aa</u> yawaddulla<u>dz</u>iina kafaruu law k<u>aa</u>nuu muslimiin\r\r\n');
INSERT INTO quranLatin VALUES (1805, '15:3', 15, 3, '<u>dz</u>arhum ya/kuluu wayatamatta\'uu wayulhihimu l-amalu fasawfa ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1806, '15:4', 15, 4, 'wam<u>aa</u> ahlakn<u>aa</u> min qaryatin ill<u>aa</u> walah<u>aa</u> kit<u>aa</u>bun ma\'luum\r\r\n');
INSERT INTO quranLatin VALUES (1807, '15:5', 15, 5, 'm<u>aa</u> tasbiqu min ummatin ajalah<u>aa</u> wam<u>aa</u> yasta/khiruun\r\r\n');
INSERT INTO quranLatin VALUES (1808, '15:6', 15, 6, 'waq<u>aa</u>luu y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>ii nuzzila \'alayhi <u>dzdz</u>ikru innaka lamajnuun\r\r\n');
