INSERT INTO quranLatin VALUES (1911, '16:10', 16, 10, 'huwalla<u>dz</u>ii anzala mina ssam<u>aa</u>-i m<u>aa</u>-an lakum minhu syar<u>aa</u>bun waminhu syajarun fiihi tusiimuun\r\r\n');
INSERT INTO quranLatin VALUES (1912, '16:11', 16, 11, 'yunbitu lakum bihi zzar\'a wazzaytuuna wannakhiila wal-a\'n<u>aa</u>ba wamin kulli tstsamar<u>aa</u>ti inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin yatafakkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1913, '16:12', 16, 12, 'wasakhkhara lakumullayla wannah<u>aa</u>ra wasysyamsa walqamara wannujuumu musakhkhar<u>aa</u>tun bi-amrihi inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (1914, '16:13', 16, 13, 'wam<u>aa</u> <u>dz</u>ara-a lakum fii l-ar<u>dh</u>i mukhtalifan lw<u>aa</u>nuhu inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin ya<u>dzdz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1915, '16:14', 16, 14, 'wahuwalla<u>dz</u>ii sakhkhara lba<u>h</u>ra lita/kuluu minhu la<u>h</u>man <u>th</u>ariyyan watastakhrijuu minhu <u>h</u>ilyatan talbasuunah<u>aa</u> watar<u>aa</u> lfulka maw<u>aa</u>khira fiihi walitabtaghuu min fa<u>dh</u>lihi wala\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (1916, '16:15', 16, 15, 'wa-alq<u>aa</u> fii l-ar<u>dh</u>i raw<u>aa</u>siya an tamiida bikum wa-anh<u>aa</u>ran wasubulan la\'allakum tahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (1917, '16:16', 16, 16, 'wa\'al<u>aa</u>m<u>aa</u>tin wabinnajmi hum yahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (1918, '16:17', 16, 17, 'afaman yakhluqu kaman l<u>aa</u> yakhluqu afal<u>aa</u> ta<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1919, '16:18', 16, 18, 'wa-in ta\'udduu ni\'matall<u>aa</u>hi l<u>aa</u> tu<u>hs</u>uuh<u>aa</u> innall<u>aa</u>ha laghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1920, '16:19', 16, 19, 'wal<u>aa</u>hu ya\'lamu m<u>aa</u> tusirruuna wam<u>aa</u> tu\'linuun\r\r\n');
INSERT INTO quranLatin VALUES (1921, '16:20', 16, 20, 'walla<u>dz</u>iina yad\'uuna min duunill<u>aa</u>hi l<u>aa</u> yakhluquuna syay-an wahum yukhlaquun\r\r\n');
INSERT INTO quranLatin VALUES (1922, '16:21', 16, 21, 'amw<u>aa</u>tun ghayru a<u>h</u>y<u>aa</u>-in wam<u>aa</u> yasy\'uruuna ayy<u>aa</u>na yub\'atsuun\r\r\n');
INSERT INTO quranLatin VALUES (1923, '16:22', 16, 22, 'il<u>aa</u>hukum il<u>aa</u>hun w<u>aah</u>idun falla<u>dz</u>iina l<u>aa</u> yu/minuuna bil-<u>aa</u>khirati quluubuhum munkiratun wahum mustakbiruun\r\r\n');
INSERT INTO quranLatin VALUES (1924, '16:23', 16, 23, 'l<u>aa</u> jarama annall<u>aa</u>ha ya\'lamu m<u>aa</u> yusirruuna wam<u>aa</u> yu\'linuuna innahu l<u>aa</u> yu<u>h</u>ibbu lmustakbiriin\r\r\n');
INSERT INTO quranLatin VALUES (1925, '16:24', 16, 24, 'wa-i<u>dzaa</u> qiila lahum m<u>aatsaa</u> anzala rabbukum q<u>aa</u>luu as<u>aath</u>iiru l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (1926, '16:25', 16, 25, 'liya<u>h</u>miluu awz<u>aa</u>rahum k<u>aa</u>milatan yawma lqiy<u>aa</u>mati wamin awz<u>aa</u>rilla<u>dz</u>iina yu<u>dh</u>illuunahum bighayri \'ilmin l<u>aa</u> s<u>aa</u>-a m<u>aa</u> yaziruun\r\r\n');
INSERT INTO quranLatin VALUES (1927, '16:26', 16, 26, 'qad makaralla<u>dz</u>iina min qablihim fa-at<u>aa</u>ll<u>aa</u>hu buny<u>aa</u>nahum mina lqaw<u>aa</u>\'idi fakharra \'alayhimu ssaqfu min fawqihim wa-at<u>aa</u>humu l\'a<u>dzaa</u>bu min <u>h</u>aytsu l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (1928, '16:27', 16, 27, 'tsumma yawma lqiy<u>aa</u>mati yukhziihim wayaquulu ayna syurak<u>aa</u>-iyalla<u>dz</u>iina kuntum tusy<u>aa</u>qquuna fiihim q<u>aa</u>lalla<u>dz</u>iina uutuu l\'ilma inna lkhizya lyawma wassuu-a \'al<u>aa</u> lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (1929, '16:28', 16, 28, '<B>al</b>la<u>dz</u>iina tatawaff<u>aa</u>humu lmal<u>aa</u>-ikatu <u>zhaa</u>limii anfusihim fa-alqawuu ssalama m<u>aa</u> kunn<u>aa</u> na\'malu min suu-in bal<u>aa</u> innall<u>aa</u>ha \'aliimun bim<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1930, '16:29', 16, 29, 'fadkhuluu abw<u>aa</u>ba jahannama kh<u>aa</u>lidiina fiih<u>aa</u> falabi/sa matsw<u>aa</u> lmutakabbiriin\r\r\n');
INSERT INTO quranLatin VALUES (1931, '16:30', 16, 30, 'waqiila lilla<u>dz</u>iina ittaqaw m<u>aatsaa</u> anzala rabbukum q<u>aa</u>luu khayran lilla<u>dz</u>iina a<u>h</u>sanuu fii h<u>aadz</u>ihi dduny<u>aa</u> <u>h</u>asanatun walad<u>aa</u>ru l-<u>aa</u>khirati khayrun walani\'ma d<u>aa</u>ru lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (1932, '16:31', 16, 31, 'jann<u>aa</u>tu \'adnin yadkhuluunah<u>aa</u> tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru lahum fiih<u>aa</u> m<u>aa</u> yasy<u>aa</u>uuna ka<u>dzaa</u>lika yajziill<u>aa</u>hu lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (1933, '16:32', 16, 32, '<B>al</b>la<u>dz</u>iina tatawaff<u>aa</u>humu lmal<u>aa</u>-ikatu <u>th</u>ayyibiina yaquuluuna sal<u>aa</u>mun \'alaykumu udkhuluu ljannata bim<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1934, '16:33', 16, 33, 'hal yan<u>zh</u>uruuna ill<u>aa</u> an ta/tiyahumu lmal<u>aa</u>-ikatu aw ya/tiya amru rabbika ka<u>dzaa</u>lika fa\'alalla<u>dz</u>iina min qablihim wam<u>aa</u> <u>zh</u>alamahumull<u>aa</u>hu wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (1935, '16:34', 16, 34, 'fa-a<u>shaa</u>bahum sayyi-<u>aa</u>tu m<u>aa</u> \'amiluu wa<u>haa</u>qa bihim m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (1936, '16:35', 16, 35, 'waq<u>aa</u>lalla<u>dz</u>iina asyrakuu law sy<u>aa</u>-all<u>aa</u>hu m<u>aa</u> \'abadn<u>aa</u> min duunihi min syay-in na<u>h</u>nu wal<u>aa</u> <u>aa</u>b<u>aa</u>un<u>aa</u> wal<u>aa</u> <u>h</u>arramn<u>aa</u> min duunihi min syay-in ka<u>dzaa</u>lika fa\'alalla<u>dz</u>iina min qablihim fahal \'al<u>aa</u> rrusuli ill<u>aa</u> lbal<u>aa</u>ghu lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (1937, '16:36', 16, 36, 'walaqad ba\'atsn<u>aa</u> fii kulli ummatin rasuulan ani u\'buduull<u>aa</u>ha wajtanibuu <u>ththaa</u>ghuuta faminhum man had<u>aa</u>ll<u>aa</u>hu waminhum man <u>h</u>aqqat \'alayhi <u>dhdh</u>al<u>aa</u>latu fasiiruu fii l-ar<u>dh</u>i fan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu lmuka<u>dzdz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (1938, '16:37', 16, 37, 'in ta<u>h</u>ri<u>sh</u> \'al<u>aa</u> hud<u>aa</u>hum fa-innall<u>aa</u>ha l<u>aa</u> yahdii man yu<u>dh</u>illu wam<u>aa</u> lahum min n<u>aas</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (1939, '16:38', 16, 38, 'wa-aqsamuu bil<u>aa</u>hi jahda aym<u>aa</u>nihim l<u>aa</u> yab\'atsull<u>aa</u>hu man yamuutu bal<u>aa</u> wa\'dan \'alayhi <u>h</u>aqqan wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1940, '16:39', 16, 39, 'liyubayyina lahumulla<u>dz</u>ii yakhtalifuuna fiihi waliya\'lamalla<u>dz</u>iina kafaruu annahum k<u>aa</u>nuu k<u>aadz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (1941, '16:40', 16, 40, 'innam<u>aa</u> qawlun<u>aa</u> lisyay-in i<u>dzaa</u> aradn<u>aa</u>hu an naquula lahu kun fayakuun\r\r\n');
INSERT INTO quranLatin VALUES (1942, '16:41', 16, 41, 'walla<u>dz</u>iina h<u>aa</u>jaruu fiill<u>aa</u>hi min ba\'di m<u>aa</u> <u>zh</u>ulimuu lanubawwi-annahum fii dduny<u>aa</u> <u>h</u>asanatan wala-ajru l-<u>aa</u>khirati akbaru law k<u>aa</u>nuu ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1943, '16:42', 16, 42, '<B>al</b>la<u>dz</u>iina <u>sh</u>abaruu wa\'al<u>aa</u> rabbihim yatawakkaluun\r\r\n');
INSERT INTO quranLatin VALUES (1944, '16:43', 16, 43, 'wam<u>aa</u> arsaln<u>aa</u> min qablika ill<u>aa</u> rij<u>aa</u>lan nuu<u>h</u>ii ilayhim fas-aluu ahla <u>dzdz</u>ikri in kuntum l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1945, '16:44', 16, 44, 'bilbayyin<u>aa</u>ti wazzuburi wa-anzaln<u>aa</u> ilayka <u>dzdz</u>ikra litubayyina linn<u>aa</u>si m<u>aa</u> nuzzila ilayhim wala\'allahum yatafakkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1946, '16:45', 16, 45, 'afa-aminalla<u>dz</u>iina makaruu ssayyi-<u>aa</u>ti an yakhsifall<u>aa</u>hu bihimu l-ar<u>dh</u>a aw ya/tiyahumu l\'a<u>dzaa</u>bu min <u>h</u>aytsu l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (1947, '16:46', 16, 46, 'aw ya/khu<u>dz</u>ahum fii taqallubihim fam<u>aa</u> hum bimu\'jiziin<b>a </b>\r\r\n');
INSERT INTO quranLatin VALUES (1948, '16:47', 16, 47, 'aw ya/khu<u>dz</u>ahum \'al<u>aa</u> takhawwufin fa-inna rabbakum larauufun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1949, '16:48', 16, 48, 'awa lam yaraw il<u>aa</u> m<u>aa</u> khalaqall<u>aa</u>hu min syay-in yatafayyau <u>zh</u>il<u>aa</u>luhu \'ani lyamiini wasysyam<u>aa</u>-ili sujjadan lill<u>aa</u>hi wahum d<u>aa</u>khiruun\r\r\n');
INSERT INTO quranLatin VALUES (1950, '16:49', 16, 49, 'walill<u>aa</u>hi yasjudu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i min d<u>aa</u>bbatin walmal<u>aa</u>-ikatu wahum l<u>aa</u> yastakbiruun\r\r\n');
INSERT INTO quranLatin VALUES (1951, '16:50', 16, 50, 'yakh<u>aa</u>fuuna rabbahum min fawqihim wayaf\'aluuna m<u>aa</u> yu/maruun\r\r\n');
INSERT INTO quranLatin VALUES (1952, '16:51', 16, 51, 'waq<u>aa</u>lall<u>aa</u>hu l<u>aa</u> tattakhi<u>dz</u>uu il<u>aa</u>hayni itsnayni innam<u>aa</u> huwa il<u>aa</u>hun w<u>aah</u>idun fa-iyy<u>aa</u>ya farhabuun\r\r\n');
INSERT INTO quranLatin VALUES (1953, '16:52', 16, 52, 'walahu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i walahu ddiinu w<u>aas</u>iban afaghayrall<u>aa</u>hi tattaquun\r\r\n');
INSERT INTO quranLatin VALUES (1954, '16:53', 16, 53, 'wam<u>aa</u> bikum min ni\'matin faminall<u>aa</u>hi tsumma i<u>dzaa</u> massakumu <u>dhdh</u>urru fa-ilayhi taj-aruun\r\r\n');
INSERT INTO quranLatin VALUES (1955, '16:54', 16, 54, 'tsumma i<u>dzaa</u> kasyafa <u>dhdh</u>urra \'ankum i<u>dzaa</u> fariiqun minkum birabbihim yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (1956, '16:55', 16, 55, 'liyakfuruu bim<u>aa</u> <u>aa</u>tayn<u>aa</u>hum fatamatta\'uu fasawfa ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1957, '16:56', 16, 56, 'wayaj\'aluuna lim<u>aa</u> l<u>aa</u> ya\'lamuuna na<u>sh</u>iiban mimm<u>aa</u> razaqn<u>aa</u>hum tal<u>aa</u>hi latus-alunna \'amm<u>aa</u> kuntum taftaruun\r\r\n');
INSERT INTO quranLatin VALUES (1958, '16:57', 16, 57, 'wayaj\'aluuna lill<u>aa</u>hi lban<u>aa</u>ti sub<u>haa</u>nahu walahum m<u>aa</u> yasytahuun\r\r\n');
INSERT INTO quranLatin VALUES (1959, '16:58', 16, 58, 'wa-i<u>dzaa</u> busysyira a<u>h</u>aduhum bilunts<u>aa</u> <u>zh</u>alla wajhuhu muswaddan wahuwa ka<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1960, '16:59', 16, 59, 'yataw<u>aa</u>r<u>aa</u> mina lqawmi min suu-i m<u>aa</u> busysyira bihi ayumsikuhu \'al<u>aa</u> huunin am yadussuhu fii ttur<u>aa</u>bi l<u>aa</u> s<u>aa</u>-a m<u>aa</u> ya<u>h</u>kumuun\r\r\n');
INSERT INTO quranLatin VALUES (1961, '16:60', 16, 60, 'lilla<u>dz</u>iina l<u>aa</u> yu/minuuna bil-<u>aa</u>khirati matsalu ssaw-i walill<u>aa</u>hi lmatsalu l-a\'l<u>aa</u> wahuwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (1962, '16:61', 16, 61, 'walaw yu-<u>aa</u>khi<u>dz</u>ull<u>aa</u>hu nn<u>aa</u>sa bi<u>zh</u>ulmihim m<u>aa</u> taraka \'alayh<u>aa</u> min d<u>aa</u>bbatin wal<u>aa</u>kin yu-akhkhiruhum il<u>aa</u> ajalin musamman fa-i<u>dzaa</u> j<u>aa</u>-a ajaluhum l<u>aa</u> yasta/khiruuna s<u>aa</u>\'atan wal<u>aa</u> yastaqdimuun\r\r\n');
INSERT INTO quranLatin VALUES (1963, '16:62', 16, 62, 'wayaj\'aluuna lill<u>aa</u>hi m<u>aa</u> yakrahuuna wata<u>sh</u>ifu lsinatuhumu lka<u>dz</u>iba anna lahumu l<u>h</u>usn<u>aa</u> l<u>aa</u> jarama anna lahumu nn<u>aa</u>ra wa-annahum mufra<u>th</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (1964, '16:63', 16, 63, 'tal<u>aa</u>hi laqad arsaln<u>aa</u> il<u>aa</u> umamin min qablika fazayyana lahumu sysyay<u>th</u><u>aa</u>nu a\'m<u>aa</u>lahum fahuwa waliyyuhumu lyawma walahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (1965, '16:64', 16, 64, 'wam<u>aa</u> anzaln<u>aa</u> \'alayka lkit<u>aa</u>ba ill<u>aa</u> litubayyina lahumulla<u>dz</u>ii ikhtalafuu fiihi wahudan wara<u>h</u>matan liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1966, '16:65', 16, 65, 'wal<u>aa</u>hu anzala mina ssam<u>aa</u>-i m<u>aa</u>-an fa-a<u>h</u>y<u>aa</u> bihi l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin yasma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1967, '16:66', 16, 66, 'wa-inna lakum fii l-an\'<u>aa</u>mi la\'ibratan nusqiikum mimm<u>aa</u> fii bu<u>th</u>uunihi min bayni fartsin wadamin labanan kh<u>aa</u>li<u>sh</u>an s<u>aa</u>-ighan lisysy<u>aa</u>ribiin\r\r\n');
INSERT INTO quranLatin VALUES (1968, '16:67', 16, 67, 'wamin tsamar<u>aa</u>ti nnakhiili wal-a\'n<u>aa</u>bi tattakhi<u>dz</u>uuna minhu sakaran warizqan <u>h</u>asanan inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (1969, '16:68', 16, 68, 'wa-aw<u>haa</u> rabbuka il<u>aa</u> nna<u>h</u>li ani ittakhi<u>dz</u>ii mina ljib<u>aa</u>li buyuutan wamina sysyajari wamimm<u>aa</u> ya\'risyuun\r\r\n');
INSERT INTO quranLatin VALUES (1970, '16:69', 16, 69, 'tsumma kulii min kulli tstsamar<u>aa</u>ti faslukii subula rabbiki <u>dz</u>ululan yakhruju min bu<u>th</u>uunih<u>aa</u> syar<u>aa</u>bun mukhtalifun lw<u>aa</u>nuhu fiihi syif<u>aa</u>un linn<u>aa</u>si inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin yatafakkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1971, '16:70', 16, 70, 'wal<u>aa</u>hu khalaqakum tsumma yatawaff<u>aa</u>kum waminkum man yuraddu il<u>aa</u> ar<u>dz</u>ali l\'umuri likay l<u>aa</u> ya\'lama ba\'da \'ilmin syay-an innall<u>aa</u>ha \'aliimun qadiir\r\r\n');
INSERT INTO quranLatin VALUES (1972, '16:71', 16, 71, 'wal<u>aa</u>hu fa<u>dhdh</u>ala ba\'<u>dh</u>akum \'al<u>aa</u> ba\'<u>dh</u>in fii rrizqi fam<u>aa</u>lla<u>dz</u>iina fu<u>dhdh</u>iluu bir<u>aa</u>ddii rizqihim \'al<u>aa</u> m<u>aa</u> malakat aym<u>aa</u>nuhum fahum fiihi saw<u>aa</u>un afabini\'matill<u>aa</u>hi yaj<u>h</u>aduun\r\r\n');
INSERT INTO quranLatin VALUES (1973, '16:72', 16, 72, 'wal<u>aa</u>hu ja\'ala lakum min anfusikum azw<u>aa</u>jan waja\'ala lakum min azw<u>aa</u>jikum baniina wa<u>h</u>afadatan warazaqakum mina <u>ththh</u>ayyib<u>aa</u>ti afabilb<u>aath</u>ili yu/minuuna wabini\'matill<u>aa</u>hi hum yakfuruun\r\r\n');
INSERT INTO quranLatin VALUES (1974, '16:73', 16, 73, 'waya\'buduuna min duunill<u>aa</u>hi m<u>aa</u> l<u>aa</u> yamliku lahum rizqan mina ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i syay-an wal<u>aa</u> yasta<u>th</u>ii\'uun\r\r\n');
INSERT INTO quranLatin VALUES (1975, '16:74', 16, 74, 'fal<u>aa</u> ta<u>dh</u>ribuu lill<u>aa</u>hi l-amts<u>aa</u>la innall<u>aa</u>ha ya\'lamu wa-antum l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1976, '16:75', 16, 75, '<u>dh</u>araball<u>aa</u>hu matsalan \'abdan mamluukan l<u>aa</u> yaqdiru \'al<u>aa</u> syay-in waman razaqn<u>aa</u>hu minn<u>aa</u> rizqan <u>h</u>asanan fahuwa yunfiqu minhu sirran wajahran hal yastawuuna l<u>h</u>amdu lill<u>aa</u>hi bal aktsaruhum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1977, '16:76', 16, 76, 'wa<u>dh</u>araball<u>aa</u>hu matsalan rajulayni a<u>h</u>aduhum<u>aa</u> abkamu l<u>aa</u> yaqdiru \'al<u>aa</u> syay-in wahuwa kallun \'al<u>aa</u> mawl<u>aa</u>hu aynam<u>aa</u> yuwajjihhu l<u>aa</u> ya/ti bikhayrin hal yastawii huwa waman ya/muru bil\'adli wahuwa \'al<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (1978, '16:77', 16, 77, 'walill<u>aa</u>hi ghaybu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wam<u>aa</u> amru ss<u>aa</u>\'ati ill<u>aa</u> kalam<u>h</u>i lba<u>sh</u>ari aw huwa aqrabu innall<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (1979, '16:78', 16, 78, 'wal<u>aa</u>hu akhrajakum min bu<u>th</u>uuni ummah<u>aa</u>tikum l<u>aa</u> ta\'lamuuna syay-an waja\'ala lakumu ssam\'a wal-ab<u>shaa</u>ra wal-af-idata la\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (1980, '16:79', 16, 79, 'alam yaraw il<u>aa</u> <u>ththh</u>ayri musakhkhar<u>aa</u>tin fii jawwi ssam<u>aa</u>-i m<u>aa</u> yumsikuhunna ill<u>aa</u>ll<u>aa</u>hu inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (1981, '16:80', 16, 80, 'wal<u>aa</u>hu ja\'ala lakum min buyuutikum sakanan waja\'ala lakum min juluudi l-an\'<u>aa</u>mi buyuutan tastakhiffuunah<u>aa</u> yawma <u>zh</u>a\'nikum wayawma iq<u>aa</u>matikum wamin a<u>sh</u>w<u>aa</u>fih<u>aa</u> wa-awb<u>aa</u>rih<u>aa</u> wa-asy\'<u>aa</u>rih<u>aa</u> ats<u>aa</u>tsan wamat<u>aa</u>\'an il<u>aa</u> <u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (1982, '16:81', 16, 81, 'wal<u>aa</u>hu ja\'ala lakum mimm<u>aa</u> khalaqa <u>zh</u>il<u>aa</u>lan waja\'ala lakum mina ljib<u>aa</u>li akn<u>aa</u>nan waja\'ala lakum sar<u>aa</u>biila taqiikumu l<u>h</u>arra wasar<u>aa</u>biila taqiikum ba/sakum ka<u>dzaa</u>lika yutimmu ni\'matahu \'alaykum la\'allakum tuslimuun\r\r\n');
INSERT INTO quranLatin VALUES (1983, '16:82', 16, 82, 'fa-in tawallaw fa-innam<u>aa</u> \'alayka lbal<u>aa</u>ghu lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (1984, '16:83', 16, 83, 'ya\'rifuuna ni\'matall<u>aa</u>hi tsumma yunkiruunah<u>aa</u> wa-aktsaruhumu lk<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (1985, '16:84', 16, 84, 'wayawma nab\'atsu min kulli ummatin syahiidan tsumma l<u>aa</u> yu/<u>dz</u>anu lilla<u>dz</u>iina kafaruu wal<u>aa</u> hum yusta\'tabuun\r\r\n');
INSERT INTO quranLatin VALUES (1986, '16:85', 16, 85, 'wa-i<u>dzaa</u> ra<u>aa</u>lla<u>dz</u>iina <u>zh</u>alamuu l\'a<u>dzaa</u>ba fal<u>aa</u> yukhaffafu \'anhum wal<u>aa</u> hum yun<u>zh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (1987, '16:86', 16, 86, 'wa-i<u>dzaa</u> ra<u>aa</u>lla<u>dz</u>iina asyrakuu syurak<u>aa</u>-ahum q<u>aa</u>luu rabban<u>aa</u> h<u>aa</u>ul<u>aa</u>-i syurak<u>aa</u>un<u>aa</u>lla<u>dz</u>iina kunn<u>aa</u> nad\'uu min duunika fa-alqaw ilayhimu lqawla innakum lak<u>aadz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (1988, '16:87', 16, 87, 'wa-alqaw il<u>aa</u>ll<u>aa</u>hi yawma-i<u>dz</u>in ssalama wa<u>dh</u>alla \'anhum m<u>aa</u> k<u>aa</u>nuu yaftaruun\r\r\n');
INSERT INTO quranLatin VALUES (1989, '16:88', 16, 88, '<B>al</b>la<u>dz</u>iina kafaruu wa<u>sh</u>adduu \'an sabiilill<u>aa</u>hi zidn<u>aa</u>hum \'a<u>dzaa</u>ban fawqa l\'a<u>dzaa</u>bi bim<u>aa</u> k<u>aa</u>nuu yufsiduun\r\r\n');
INSERT INTO quranLatin VALUES (1990, '16:89', 16, 89, 'wayawma nab\'atsu fii kulli ummatin syahiidan \'alayhim min anfusihim waji/n<u>aa</u> bika syahiidan \'al<u>aa</u> h<u>aa</u>ul<u>aa</u>-i wanazzaln<u>aa</u> \'alayka lkit<u>aa</u>ba tiby<u>aa</u>nan likulli syay-in wahudan wara<u>h</u>matan wabusyr<u>aa</u> lilmuslimiin\r\r\n');
INSERT INTO quranLatin VALUES (1991, '16:90', 16, 90, 'innall<u>aa</u>ha ya/muru bil\'adli wal-i<u>h</u>s<u>aa</u>ni wa-iit<u>aa</u>-i <u>dz</u>ii lqurb<u>aa</u> wayanh<u>aa</u> \'ani lfa<u>h</u>sy<u>aa</u>-i walmunkari walbaghyi ya\'i<u>zh</u>ukum la\'allakum ta<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (1992, '16:91', 16, 91, 'wa-awfuu bi\'ahdill<u>aa</u>hi i<u>dzaa</u> \'<u>aa</u>hadtum wal<u>aa</u> tanqu<u>dh</u>uu l-aym<u>aa</u>na ba\'da tawkiidih<u>aa</u> waqad ja\'altumull<u>aa</u>ha \'alaykum kafiilan innall<u>aa</u>ha ya\'lamu m<u>aa</u> taf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (1993, '16:92', 16, 92, 'wal<u>aa</u> takuunuu kallatii naqa<u>dh</u>at ghazlah<u>aa</u> min ba\'di quwwatin ank<u>aa</u>tsan tattakhi<u>dz</u>uuna aym<u>aa</u>nakum dakhalan baynakum an takuuna ummatun hiya arb<u>aa</u> min ummatin innam<u>aa</u> yabluukumull<u>aa</u>hu bihi walayubayyinanna lakum yawma lqiy<u>aa</u>mati m<u>aa</u> kuntum fiihi takhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (1994, '16:93', 16, 93, 'walaw sy<u>aa</u>-all<u>aa</u>hu laja\'alakum ummatan w<u>aah</u>idatan wal<u>aa</u>kin yu<u>dh</u>illu man yasy<u>aa</u>u wayahdii man yasy<u>aa</u>u walatus-alunna \'amm<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1995, '16:94', 16, 94, 'wal<u>aa</u> tattakhi<u>dz</u>uu aym<u>aa</u>nakum dakhalan baynakum fatazilla qadamun ba\'da tsubuutih<u>aa</u> wata<u>dz</u>uuquu ssuu-a bim<u>aa</u> <u>sh</u>adadtum \'an sabiilill<u>aa</u>hi walakum \'a<u>dzaa</u>bun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (1996, '16:95', 16, 95, 'wal<u>aa</u> tasytaruu bi\'ahdill<u>aa</u>hi tsamanan qaliilan innam<u>aa</u> \'indall<u>aa</u>hi huwa khayrun lakum in kuntum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (1997, '16:96', 16, 96, 'm<u>aa</u> \'indakum yanfadu wam<u>aa</u> \'indall<u>aa</u>hi b<u>aa</u>qin walanajziyannalla<u>dz</u>iina <u>sh</u>abaruu ajrahum bi-a<u>h</u>sani m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1998, '16:97', 16, 97, 'man \'amila <u>shaa</u>li<u>h</u>an min <u>dz</u>akarin aw unts<u>aa</u> wahuwa mu/minun falanu<u>h</u>yiyannahu <u>h</u>ay<u>aa</u>tan <u>th</u>ayyibatan walanajziyannahum ajrahum bi-a<u>h</u>sani m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (1999, '16:98', 16, 98, 'fa-i<u>dzaa</u> qara/ta lqur-<u>aa</u>na fasta\'i<u>dz</u> bil<u>aa</u>hi mina sysyay<u>th</u><u>aa</u>ni rrajiim\r\r\n');
INSERT INTO quranLatin VALUES (2000, '16:99', 16, 99, 'innahu laysa lahu sul<u>th</u><u>aa</u>nun \'al<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu wa\'al<u>aa</u> rabbihim yatawakkaluun\r\r\n');
INSERT INTO quranLatin VALUES (2001, '16:100', 16, 100, 'innam<u>aa</u> sul<u>th</u><u>aa</u>nuhu \'al<u>aa</u>lla<u>dz</u>iina yatawallawnahu walla<u>dz</u>iina hum bihi musyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (2002, '16:101', 16, 101, 'wa-i<u>dzaa</u> baddaln<u>aa</u> <u>aa</u>yatan mak<u>aa</u>na <u>aa</u>yatin wal<u>aa</u>hu a\'lamu bim<u>aa</u> yunazzilu q<u>aa</u>luu innam<u>aa</u> anta muftarin bal aktsaruhum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (2003, '16:102', 16, 102, 'qul nazzalahu ruu<u>h</u>u lqudusi min rabbika bil<u>h</u>aqqi liyutsabbitalla<u>dz</u>iina <u>aa</u>manuu wahudan wabusyr<u>aa</u> lilmuslimiin\r\r\n');
INSERT INTO quranLatin VALUES (2004, '16:103', 16, 103, 'walaqad na\'lamu annahum yaquuluuna innam<u>aa</u> yu\'allimuhu basyarun lis<u>aa</u>nulla<u>dz</u>ii yul<u>h</u>iduuna ilayhi a\'jamiyyun wah<u>aadzaa</u> lis<u>aa</u>nun \'arabiyyun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (2005, '16:104', 16, 104, 'innalla<u>dz</u>iina l<u>aa</u> yu/minuuna bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi l<u>aa</u> yahdiihimull<u>aa</u>hu walahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (2006, '16:105', 16, 105, 'innam<u>aa</u> yaftarii lka<u>dz</u>iballa<u>dz</u>iina l<u>aa</u> yu/minuuna bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi waul<u>aa</u>-ika humu lk<u>aadz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (2007, '16:106', 16, 106, 'man kafara bil<u>aa</u>hi min ba\'di iim<u>aa</u>nihi ill<u>aa</u> man ukriha waqalbuhu mu<u>th</u>ma-innun bil-iim<u>aa</u>ni wal<u>aa</u>kin man syara<u>h</u>a bilkufri <u>sh</u>adran fa\'alayhim gha<u>dh</u>abun minall<u>aa</u>hi walahum \'a<u>dzaa</u>bun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (2008, '16:107', 16, 107, '<u>dzaa</u>lika bi-annahumu ista<u>h</u>abbuu l<u>h</u>ay<u>aa</u>ta dduny<u>aa</u> \'al<u>aa</u> l-<u>aa</u>khirati wa-annall<u>aa</u>ha l<u>aa</u> yahdii lqawma lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (2009, '16:108', 16, 108, 'ul<u>aa</u>-ikalla<u>dz</u>iina <u>th</u>aba\'all<u>aa</u>hu \'al<u>aa</u> quluubihim wasam\'ihim wa-ab<u>shaa</u>rihim waul<u>aa</u>-ika humu lgh<u>aa</u>filuun\r\r\n');
INSERT INTO quranLatin VALUES (2010, '16:109', 16, 109, 'l<u>aa</u> jarama annahum fii l-<u>aa</u>khirati humu lkh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (2011, '16:110', 16, 110, 'tsumma inna rabbaka lilla<u>dz</u>iina h<u>aa</u>jaruu min ba\'di m<u>aa</u> futinuu tsumma j<u>aa</u>haduu wa<u>sh</u>abaruu inna rabbaka min ba\'dih<u>aa</u> laghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (2012, '16:111', 16, 111, 'yawma ta/tii kullu nafsin tuj<u>aa</u>dilu \'an nafsih<u>aa</u> watuwaff<u>aa</u> kullu nafsin m<u>aa</u> \'amilat wahum l<u>aa</u> yu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (2013, '16:112', 16, 112, 'wa<u>dh</u>araball<u>aa</u>hu matsalan qaryatan k<u>aa</u>nat <u>aa</u>minatan mu<u>th</u>ma-innatan ya/tiih<u>aa</u> rizquh<u>aa</u> raghadan min kulli mak<u>aa</u>nin fakafarat bi-an\'umill<u>aa</u>hi fa-a<u>dzaa</u>qah<u>aa</u>ll<u>aa</u>hu lib<u>aa</u>sa ljuu\'i walkhawfi bim<u>aa</u> k<u>aa</u>nuu ya<u>sh</u>na\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2014, '16:113', 16, 113, 'walaqad j<u>aa</u>-ahum rasuulun minhum faka<u>dzdz</u>abuuhu fa-akha<u>dz</u>ahumu l\'a<u>dzaa</u>bu wahum <u>zhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (2015, '16:114', 16, 114, 'fakuluu mimm<u>aa</u> razaqakumull<u>aa</u>hu <u>h</u>al<u>aa</u>lan <u>th</u>ayyiban wasykuruu ni\'matall<u>aa</u>hi in kuntum iyy<u>aa</u>hu ta\'buduun\r\r\n');
INSERT INTO quranLatin VALUES (2016, '16:115', 16, 115, 'innam<u>aa</u> <u>h</u>arrama \'alaykumu lmaytata waddama wala<u>h</u>ma lkhinziiri wam<u>aa</u> uhilla lighayrill<u>aa</u>hi bihi famani i<u>dth</u>urra ghayra b<u>aa</u>ghin wal<u>aa</u> \'<u>aa</u>din fa-innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (2017, '16:116', 16, 116, 'wal<u>aa</u> taquuluu lim<u>aa</u> ta<u>sh</u>ifu lsinatukumu lka<u>dz</u>iba h<u>aadzaa</u> <u>h</u>al<u>aa</u>lun wah<u>aadzaa</u> <u>h</u>ar<u>aa</u>mun litaftaruu \'al<u>aa</u>ll<u>aa</u>hi lka<u>dz</u>iba innalla<u>dz</u>iina yaftaruuna \'al<u>aa</u>ll<u>aa</u>hi lka<u>dz</u>iba l<u>aa</u> yufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2018, '16:117', 16, 117, 'mat<u>aa</u>\'un qaliilun walahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (2019, '16:118', 16, 118, 'wa\'al<u>aa</u>lla<u>dz</u>iina h<u>aa</u>duu <u>h</u>arramn<u>aa</u> m<u>aa</u> qa<u>sh</u>a<u>sh</u>n<u>aa</u> \'alayka min qablu wam<u>aa</u> <u>zh</u>alamn<u>aa</u>hum wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (2020, '16:119', 16, 119, 'tsumma inna rabbaka lilla<u>dz</u>iina \'amiluu ssuu-a bijah<u>aa</u>latin tsumma t<u>aa</u>buu min ba\'di <u>dzaa</u>lika wa-a<u>sh</u>la<u>h</u>uu inna rabbaka min ba\'dih<u>aa</u> laghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (2021, '16:120', 16, 120, 'inna ibr<u>aa</u>hiima k<u>aa</u>na ummatan q<u>aa</u>nitan lill<u>aa</u>hi <u>h</u>aniifan walam yaku mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (2022, '16:121', 16, 121, 'sy<u>aa</u>kiran li-an\'umihi ijtab<u>aa</u>hu wahad<u>aa</u>hu il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (2023, '16:122', 16, 122, 'wa<u>aa</u>tayn<u>aa</u>hu fii dduny<u>aa</u> <u>h</u>asanatan wa-innahu fii l-<u>aa</u>khirati lamina <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (2024, '16:123', 16, 123, 'tsumma aw<u>h</u>ayn<u>aa</u> ilayka ani ittabi\'millata ibr<u>aa</u>hiima <u>h</u>aniifan wam<u>aa</u> k<u>aa</u>na mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (2025, '16:124', 16, 124, 'innam<u>aa</u> ju\'ila ssabtu \'al<u>aa</u>lla<u>dz</u>iina ikhtalafuu fiihi wa-inna rabbaka laya<u>h</u>kumu baynahum yawma lqiy<u>aa</u>mati fiim<u>aa</u> k<u>aa</u>nuu fiihi yakhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (2026, '16:125', 16, 125, 'ud\'u il<u>aa</u> sabiili rabbika bil<u>h</u>ikmati walmaw\'i<u>zh</u>ati l<u>h</u>asanati waj<u>aa</u>dilhum billatii hiya a<u>h</u>sanu inna rabbaka huwa a\'lamu biman <u>dh</u>alla \'an sabiilihi wahuwa a\'lamu bilmuhtadiin\r\r\n');
INSERT INTO quranLatin VALUES (2027, '16:126', 16, 126, 'wa-in \'<u>aa</u>qabtum fa\'<u>aa</u>qibuu bimitsli m<u>aa</u> \'uuqibtum bihi wala-in <u>sh</u>abartum lahuwa khayrun li<u>shshaa</u>biriin\r\r\n');
INSERT INTO quranLatin VALUES (2028, '16:127', 16, 127, 'wa<u>sh</u>bir wam<u>aa</u> <u>sh</u>abruka ill<u>aa</u> bil<u>aa</u>hi wal<u>aa</u> ta<u>h</u>zan \'alayhim wal<u>aa</u> taku fii <u>dh</u>ayqin mimm<u>aa</u> yamkuruun\r\r\n');
INSERT INTO quranLatin VALUES (2029, '16:128', 16, 128, 'innall<u>aa</u>ha ma\'alla<u>dz</u>iina ittaqaw walla<u>dz</u>iina hum mu<u>h</u>sinuun\r\r\n');
INSERT INTO quranLatin VALUES (2030, '17:1', 17, 1, 'sub<u>haa</u>nalla<u>dz</u>ii asr<u>aa</u> bi\'abdihi laylan mina lmasjidi l<u>h</u>ar<u>aa</u>mi il<u>aa</u> lmasjidi l-aq<u>shaa</u>lla<u>dz</u>ii b<u>aa</u>rakn<u>aa</u> <u>h</u>awlahu linuriyahu min <u>aa</u>y<u>aa</u>tin<u>aa</u> innahu huwa ssamii\'u lba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (2031, '17:2', 17, 2, 'wa<u>aa</u>tayn<u>aa</u> muus<u>aa</u> lkit<u>aa</u>ba waja\'aln<u>aa</u>hu hudan libanii isr<u>aa</u>-iilall<u>aa</u> tattakhi<u>dz</u>uu min duunii wakiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2032, '17:3', 17, 3, '<u>dz</u>urriyyata man <u>h</u>amaln<u>aa</u> ma\'a nuu<u>h</u>in innahu k<u>aa</u>na \'abdan syakuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2033, '17:4', 17, 4, 'waqa<u>dh</u>ayn<u>aa</u> il<u>aa</u> banii isr<u>aa</u>-iila fii lkit<u>aa</u>bi latufsidunna fii l-ar<u>dh</u>i marratayni walata\'lunna \'uluwwan kabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2034, '17:5', 17, 5, 'fa-i<u>dzaa</u> j<u>aa</u>-a wa\'du uul<u>aa</u>hum<u>aa</u> ba\'atsn<u>aa</u> \'alaykum \'ib<u>aa</u>dan lan<u>aa</u> ulii ba/sin syadiidin faj<u>aa</u>suu khil<u>aa</u>la ddiy<u>aa</u>ri wak<u>aa</u>na wa\'dan maf\'uul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2035, '17:6', 17, 6, 'tsumma radadn<u>aa</u> lakumu lkarrata \'alayhim wa-amdadn<u>aa</u>kum bi-amw<u>aa</u>lin wabaniina waja\'aln<u>aa</u>kum aktsara nafiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2036, '17:7', 17, 7, 'in a<u>h</u>santum a<u>h</u>santum li-anfusikum wa-in asa/tum falah<u>aa</u> fa-i<u>dzaa</u> j<u>aa</u>-a wa\'du l-<u>aa</u>khirati liyasuu-uu wujuuhakum waliyadkhuluu lmasjida kam<u>aa</u> dakhaluuhu awwala marratin waliyutabbiruu m<u>aa</u> \'alaw tatbiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2037, '17:8', 17, 8, '\'as<u>aa</u> rabbukum an yar<u>h</u>amakum wa-in \'udtum \'udn<u>aa</u> waja\'aln<u>aa</u> jahannama lilk<u>aa</u>firiina <u>h</u>a<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2038, '17:9', 17, 9, 'inna h<u>aadzaa</u> lqur-<u>aa</u>na yahdii lillatii hiya aqwamu wayubasysyiru lmu/miniinalla<u>dz</u>iina ya\'maluuna <u>shshaa</u>li<u>haa</u>ti anna lahum ajran kabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2039, '17:10', 17, 10, 'wa-annalla<u>dz</u>iina l<u>aa</u> yu/minuuna bil-<u>aa</u>khirati a\'tadn<u>aa</u> lahum \'a<u>dzaa</u>ban liim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2040, '17:11', 17, 11, 'wayad\'u l-ins<u>aa</u>nu bisysyarri du\'<u>aa</u>-ahu bilkhayri wak<u>aa</u>na l-ins<u>aa</u>nu \'ajuul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2041, '17:12', 17, 12, 'waja\'aln<u>aa</u>llayla wannah<u>aa</u>ra <u>aa</u>yatayni fama<u>h</u>awn<u>aa</u> <u>aa</u>yatallayli waja\'aln<u>aa</u> <u>aa</u>yata nnah<u>aa</u>ri mub<u>sh</u>iratan litabtaghuu fa<u>dh</u>lan min rabbikum walita\'lamuu \'adada ssiniina wal<u>h</u>is<u>aa</u>ba wakulla syay-in fa<u>shsh</u>aln<u>aa</u>hu taf<u>sh</u>iil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2042, '17:13', 17, 13, 'wakulla ins<u>aa</u>nin lzamn<u>aa</u>hu <u>th</u><u>aa</u>-irahu fii \'unuqihi wanukhriju lahu yawma lqiy<u>aa</u>mati kit<u>aa</u>ban yalq<u>aa</u>hu mansyuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2043, '17:14', 17, 14, 'iqra/ kit<u>aa</u>baka kaf<u>aa</u> binafsika lyawma \'alayka <u>h</u>asiib<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2044, '17:15', 17, 15, 'mani ihtad<u>aa</u> fa-innam<u>aa</u> yahtadii linafsihi waman <u>dh</u>alla fa-innam<u>aa</u> ya<u>dh</u>illu \'alayh<u>aa</u> wal<u>aa</u> taziru w<u>aa</u>ziratun wizra ukhr<u>aa</u> wam<u>aa</u> kunn<u>aa</u> mu\'a<u>dzdz</u>ibiina <u>h</u>att<u>aa</u> nab\'atsa rasuul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2045, '17:16', 17, 16, 'wa-i<u>dzaa</u> aradn<u>aa</u> an nuhlika qaryatan amarn<u>aa</u> mutrafiih<u>aa</u> fafasaquu fiih<u>aa</u> fa<u>h</u>aqqa \'alayh<u>aa</u> lqawlu fadammarn<u>aa</u>h<u>aa</u> tadmiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2046, '17:17', 17, 17, 'wakam ahlakn<u>aa</u> mina lquruuni min ba\'di nuu<u>h</u>in wakaf<u>aa</u> birabbika bi<u>dz</u>unuubi \'ib<u>aa</u>dihi khabiiran ba<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2047, '17:18', 17, 18, 'man k<u>aa</u>na yuriidu l\'<u>aa</u>jilata \'ajjaln<u>aa</u> lahu fiih<u>aa</u> m<u>aa</u> nasy<u>aa</u>u liman nuriidu tsumma ja\'aln<u>aa</u> lahu jahannama ya<u>sh</u>l<u>aa</u>h<u>aa</u> ma<u>dz</u>muuman mad<u>h</u>uur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2048, '17:19', 17, 19, 'waman ar<u>aa</u>da l-<u>aa</u>khirata wasa\'<u>aa</u> lah<u>aa</u> sa\'yah<u>aa</u> wahuwa mu/minun faul<u>aa</u>-ika k<u>aa</u>na sa\'yuhum masykuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2049, '17:20', 17, 20, 'kullan numiddu h<u>aa</u>ul<u>aa</u>-i wah<u>aa</u>ul<u>aa</u>-i min \'a<u>th</u><u>aa</u>-i rabbika wam<u>aa</u> k<u>aa</u>na \'a<u>th</u><u>aa</u>u rabbika ma<u>hts</u>uur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2050, '17:21', 17, 21, 'un<u>zh</u>ur kayfa fa<u>dhdh</u>aln<u>aa</u> ba\'<u>dh</u>ahum \'al<u>aa</u> ba\'<u>dh</u>in walal-<u>aa</u>khiratu akbaru daraj<u>aa</u>tin wa-akbaru taf<u>dh</u>iil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2051, '17:22', 17, 22, 'l<u>aa</u> taj\'al ma\'all<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara fataq\'uda ma<u>dz</u>muuman makh<u>dz</u>uul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2052, '17:23', 17, 23, 'waqa<u>daa</u> rabbukall<u>aa</u> ta\'buduu ill<u>aa</u> iyy<u>aa</u>hu wabilw<u>aa</u>lidayni i<u>h</u>s<u>aa</u>nan imm<u>aa</u> yablughanna \'indaka lkibara a<u>h</u>aduhum<u>aa</u> aw kil<u>aa</u>hum<u>aa</u> fal<u>aa</u> taqul lahum<u>aa</u> uffin wal<u>aa</u> tanharhum<u>aa</u> waqul lahum<u>aa</u> qawlan kariim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2053, '17:24', 17, 24, 'wakhfi<u>dh</u> lahum<u>aa</u> jan<u>aah</u>a <u>dzdz</u>ulli mina rra<u>h</u>mati waqul rabbi ir<u>h</u>amhum<u>aa</u> kam<u>aa</u> rabbay<u>aa</u>nii <u>sh</u>aghiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2054, '17:25', 17, 25, 'rabbukum a\'lamu bim<u>aa</u> fii nufuusikum in takuunuu <u>shaa</u>li<u>h</u>iina fa-innahu k<u>aa</u>na lil-aww<u>aa</u>biina ghafuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2055, '17:26', 17, 26, 'wa<u>aa</u>ti <u>dzaa</u> lqurb<u>aa</u> <u>h</u>aqqahu walmiskiina wabna ssabiili wal<u>aa</u> tuba<u>dzdz</u>ir tab<u>dz</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2056, '17:27', 17, 27, 'inna lmuba<u>dzdz</u>iriina k<u>aa</u>nuu ikhw<u>aa</u>na sysyay<u>aath</u>iini wak<u>aa</u>na sysyay<u>th</u><u>aa</u>nu lirabbihi kafuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2057, '17:28', 17, 28, 'wa-imm<u>aa</u> tu\'ri<u>dh</u>anna \'anhumu ibtigh<u>aa</u>-a ra<u>h</u>matin min rabbika tarjuuh<u>aa</u> faqul lahum qawlan maysuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2058, '17:29', 17, 29, 'wal<u>aa</u> taj\'al yadaka maghluulatan il<u>aa</u> \'unuqika wal<u>aa</u> tabsu<u>th</u>h<u>aa</u> kulla lbas<u>th</u>i fataq\'uda maluuman ma<u>h</u>suur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2059, '17:30', 17, 30, 'inna rabbaka yabsu<u>th</u>u rrizqa liman yasy<u>aa</u>u wayaqdiru innahu k<u>aa</u>na bi\'ib<u>aa</u>dihi khabiiran ba<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2060, '17:31', 17, 31, 'wal<u>aa</u> taqtuluu awl<u>aa</u>dakum khasyyata iml<u>aa</u>qin na<u>h</u>nu narzuquhum wa-iyy<u>aa</u>kum inna qatlahum k<u>aa</u>na khi<u>th</u>-an kabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2061, '17:32', 17, 32, 'wal<u>aa</u> taqrabuu zzin<u>aa</u> innahu k<u>aa</u>na f<u>aah</u>isyatan was<u>aa</u>-a sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2062, '17:33', 17, 33, 'wal<u>aa</u> taqtuluu nnafsallatii <u>h</u>arramall<u>aa</u>hu ill<u>aa</u> bil<u>h</u>aqqi waman qutila ma<u>zh</u>luuman faqad ja\'aln<u>aa</u> liwaliyyihi sul<u>th</u><u>aa</u>nan fal<u>aa</u> yusrif fii lqatli innahu k<u>aa</u>na man<u>sh</u>uur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2063, '17:34', 17, 34, 'wal<u>aa</u> taqrabuu m<u>aa</u>la lyatiimi ill<u>aa</u> billatii hiya a<u>h</u>sanu <u>h</u>att<u>aa</u> yablugha asyuddahu wa-awfuu bil\'ahdi inna l\'ahda k<u>aa</u>na mas-uul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2064, '17:35', 17, 35, 'wa-awfuu lkayla i<u>dzaa</u> kiltum wazinuu bilqis<u>th</u><u>aa</u>si lmustaqiimi <u>dzaa</u>lika khayrun wa-a<u>h</u>sanu ta/wiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2065, '17:36', 17, 36, 'wal<u>aa</u> taqfu m<u>aa</u> laysa laka bihi \'ilmun inna ssam\'a walba<u>sh</u>ara walfu-<u>aa</u>da kullu ul<u>aa</u>-ika k<u>aa</u>na \'anhu mas-uul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2066, '17:37', 17, 37, 'wal<u>aa</u> tamsyi fii l-ar<u>dh</u>i mara<u>h</u>an innaka lan takhriqa l-ar<u>dh</u>a walan tablugha ljib<u>aa</u>la <u>th</u>uul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2067, '17:38', 17, 38, 'kullu <u>dzaa</u>lika k<u>aa</u>na sayyi-uhu \'inda rabbika makruuh<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2068, '17:39', 17, 39, '<u>dzaa</u>lika mimm<u>aa</u> aw<u>haa</u> ilayka rabbuka mina l<u>h</u>ikmati wal<u>aa</u> taj\'al ma\'all<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara fatulq<u>aa</u> fii jahannama maluuman mad<u>h</u>uur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2069, '17:40', 17, 40, 'afa-a<u>sh</u>f<u>aa</u>kum rabbukum bilbaniina wattakha<u>dz</u>a mina lmal<u>aa</u>-ikati in<u>aa</u>tsan innakum lataquuluuna qawlan \'a<u>zh</u>iim<u>aa</u><b>n </b>\r\r\n');
INSERT INTO quranLatin VALUES (2070, '17:41', 17, 41, 'walaqad <u>sh</u>arrafn<u>aa</u> fii h<u>aadzaa</u> lqur-<u>aa</u>ni liya<u>dzdz</u>akkaruu wam<u>aa</u> yaziiduhum ill<u>aa</u> nufuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2071, '17:42', 17, 42, 'qul law k<u>aa</u>na ma\'ahu <u>aa</u>lihatun kam<u>aa</u> yaquuluuna i<u>dz</u>an labtaghaw il<u>aa</u> <u>dz</u>ii l\'arsyi sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2072, '17:43', 17, 43, 'sub<u>haa</u>nahu wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yaquuluuna \'uluwwan kabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2073, '17:44', 17, 44, 'tusabbi<u>h</u>u lahu ssam<u>aa</u>w<u>aa</u>tu ssab\'u wal-ar<u>dh</u>u waman fiihinna wa-in min syay-in ill<u>aa</u> yusabbi<u>h</u>u bi<u>h</u>amdihi wal<u>aa</u>kin l<u>aa</u> tafqahuuna tasbii<u>h</u>ahum innahu k<u>aa</u>na <u>h</u>aliiman ghafuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2074, '17:45', 17, 45, 'wa-i<u>dzaa</u> qara/ta lqur-<u>aa</u>na ja\'aln<u>aa</u> baynaka wabaynalla<u>dz</u>iina l<u>aa</u> yu/minuuna bil-<u>aa</u>khirati <u>h</u>ij<u>aa</u>ban mastuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2075, '17:46', 17, 46, 'waja\'aln<u>aa</u> \'al<u>aa</u> quluubihim akinnatan an yafqahuuhu wafii <u>aatsaa</u>nihim waqran wa-i<u>dzaa</u> <u>dz</u>akarta rabbaka fii lqur-<u>aa</u>ni wa<u>h</u>dahu wallaw \'al<u>aa</u> adb<u>aa</u>rihim nufuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2076, '17:47', 17, 47, 'na<u>h</u>nu a\'lamu bim<u>aa</u> yastami\'uuna bihi i<u>dz</u> yastami\'uuna ilayka wa-i<u>dz</u> hum najw<u>aa</u> i<u>dz</u> yaquulu <u>zhzhaa</u>limuuna in tattabi\'uuna ill<u>aa</u> rajulan mas<u>h</u>uur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2077, '17:48', 17, 48, 'un<u>zh</u>ur kayfa <u>dh</u>arabuu laka l-amts<u>aa</u>la fa<u>dh</u>alluu fal<u>aa</u> yasta<u>th</u>ii\'uuna sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2078, '17:49', 17, 49, 'waq<u>aa</u>luu a-i<u>dzaa</u> kunn<u>aa</u> \'i<u>zhaa</u>man waruf<u>aa</u>tan a-inn<u>aa</u> lamab\'uutsuuna khalqan jadiid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2079, '17:50', 17, 50, 'qul kuunuu <u>h</u>ij<u>aa</u>ratan aw <u>h</u>adiid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2080, '17:51', 17, 51, 'aw khalqan mimm<u>aa</u> yakburu fii <u>sh</u>uduurikum fasayaquuluuna man yu\'iidun<u>aa</u> qulilla<u>dz</u>ii fa<u>th</u>arakum awwala marratin fasayunghi<u>dh</u>uuna ilayka ruuusahum wayaquuluuna mat<u>aa</u> huwa qul \'as<u>aa</u> an yakuuna qariib<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2081, '17:52', 17, 52, 'yawma yad\'uukum fatastajiibuuna bi<u>h</u>amdihi wata<u>zh</u>unnuuna in labitstum ill<u>aa</u> qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2082, '17:53', 17, 53, 'waqul li\'ib<u>aa</u>dii yaquuluullatii hiya a<u>h</u>sanu inna sysyay<u>th</u><u>aa</u>na yanzaghu baynahum inna sysyay<u>th</u><u>aa</u>na k<u>aa</u>na lil-ins<u>aa</u>ni \'aduwwan mubiin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2083, '17:54', 17, 54, 'rabbukum a\'lamu bikum in yasya/ yar<u>h</u>amkum aw in yasya/ yu\'a<u>dzdz</u>ibkum wam<u>aa</u> arsaln<u>aa</u>ka \'alayhim wakiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2084, '17:55', 17, 55, 'warabbuka a\'lamu biman fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i walaqad fa<u>dhdh</u>aln<u>aa</u> ba\'<u>dh</u>a nnabiyyiina \'al<u>aa</u> ba\'<u>dh</u>in wa<u>aa</u>tayn<u>aa</u> d<u>aa</u>wuuda zabuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2085, '17:56', 17, 56, 'quli ud\'ulla<u>dz</u>iina za\'amtum min duunihi fal<u>aa</u> yamlikuuna kasyfa <u>dhdh</u>urri \'ankum wal<u>aa</u> ta<u>h</u>wiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2086, '17:57', 17, 57, 'ul<u>aa</u>-ikalla<u>dz</u>iina yad\'uuna yabtaghuuna il<u>aa</u> rabbihimu lwasiilata ayyuhum aqrabu wayarjuuna ra<u>h</u>matahu wayakh<u>aa</u>fuuna \'a<u>dzaa</u>bahu inna \'a<u>dzaa</u>ba rabbika k<u>aa</u>na ma<u>hts</u>uur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2087, '17:58', 17, 58, 'wa-in min qaryatin ill<u>aa</u> na<u>h</u>nu muhlikuuh<u>aa</u> qabla yawmi lqiy<u>aa</u>mati aw mu\'a<u>dzdz</u>ibuuh<u>aa</u> \'a<u>dzaa</u>ban syadiidan k<u>aa</u>na <u>dzaa</u>lika fii lkit<u>aa</u>bi mas<u>th</u>uur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2088, '17:59', 17, 59, 'wam<u>aa</u> mana\'an<u>aa</u> an nursila bil-<u>aa</u>y<u>aa</u>ti ill<u>aa</u> an ka<u>dzdz</u>aba bih<u>aa</u> l-awwaluuna wa<u>aa</u>tayn<u>aa</u> tsamuuda nn<u>aa</u>qata mub<u>sh</u>iratan fa<u>zh</u>alamuu bih<u>aa</u> wam<u>aa</u> nursilu bil-<u>aa</u>y<u>aa</u>ti ill<u>aa</u> takhwiif<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2089, '17:60', 17, 60, 'wa-i<u>dz</u> quln<u>aa</u> laka inna rabbaka a<u>hath</u>a binn<u>aa</u>si wam<u>aa</u> ja\'aln<u>aa</u> rru/y<u>aa</u>llatii arayn<u>aa</u>ka ill<u>aa</u> fitnatan linn<u>aa</u>si wasysyajarata lmal\'uunata fii lqur-<u>aa</u>ni wanukhawwifuhum fam<u>aa</u> yaziiduhum ill<u>aa</u> <u>th</u>ughy<u>aa</u>nan kabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2090, '17:61', 17, 61, 'wa-i<u>dz</u> quln<u>aa</u> lilmal<u>aa</u>-ikati usjuduu li-<u>aa</u>dama fasajaduu ill<u>aa</u> ibliisa q<u>aa</u>la a-asjudu liman khalaqta <u>th</u>iin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2091, '17:62', 17, 62, 'q<u>aa</u>la ara-aytaka h<u>aadzaa</u>lla<u>dz</u>ii karramta \'alayya la-in akhkhartani il<u>aa</u> yawmi lqiy<u>aa</u>mati la-a<u>h</u>tanikanna <u>dz</u>urriyyatahu ill<u>aa</u> qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2092, '17:63', 17, 63, 'q<u>aa</u>la i<u>dz</u>hab faman tabi\'aka minhum fa-inna jahannama jaz<u>aa</u>ukum jaz<u>aa</u>-an mawfuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2093, '17:64', 17, 64, 'wastafziz mani ista<u>th</u>a\'ta minhum bi<u>sh</u>awtika wa-ajlib \'alayhim bikhaylika warajlika wasy<u>aa</u>rik-hum fii l-amw<u>aa</u>li wal-awl<u>aa</u>di wa\'idhum wam<u>aa</u> ya\'iduhumu sysyay<u>th</u><u>aa</u>nu ill<u>aa</u> ghuruur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2094, '17:65', 17, 65, 'inna \'ib<u>aa</u>dii laysa laka \'alayhim sul<u>th</u><u>aa</u>nun wakaf<u>aa</u> birabbika wakiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2095, '17:66', 17, 66, 'rabbukumulla<u>dz</u>ii yuzjii lakumu lfulka fii lba<u>h</u>ri litabtaghuu min fa<u>dh</u>lihi innahu k<u>aa</u>na bikum ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2096, '17:67', 17, 67, 'wa-i<u>dzaa</u> massakumu <u>dhdh</u>urru fii lba<u>h</u>ri <u>dh</u>alla man tad\'uuna ill<u>aa</u> iyy<u>aa</u>hu falamm<u>aa</u> najj<u>aa</u>kum il<u>aa</u> lbarri a\'ra<u>dh</u>tum wak<u>aa</u>na l-ins<u>aa</u>nu kafuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2097, '17:68', 17, 68, 'afa-amintum an yakhsifa bikum j<u>aa</u>niba lbarri aw yursila \'alaykum <u>has</u>iban tsumma l<u>aa</u> tajiduu lakum wakiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2098, '17:69', 17, 69, 'am amintum an yu\'iidakum fiihi t<u>aa</u>ratan ukhr<u>aa</u> fayursila \'alaykum q<u>aas</u>ifan mina rrii<u>h</u>i fayughriqakum bim<u>aa</u> kafartum tsumma l<u>aa</u> tajiduu lakum \'alayn<u>aa</u> bihi tabii\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2099, '17:70', 17, 70, 'walaqad karramn<u>aa</u> banii <u>aa</u>dama wa<u>h</u>amaln<u>aa</u>hum fii lbarri walba<u>h</u>ri warazaqn<u>aa</u>hum mina <u>ththh</u>ayyib<u>aa</u>ti wafa<u>dhdh</u>aln<u>aa</u>hum \'al<u>aa</u> katsiirin mimman khalaqn<u>aa</u> taf<u>dh</u>iil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2100, '17:71', 17, 71, 'yawma nad\'uu kulla un<u>aa</u>sin bi-im<u>aa</u>mihim faman uutiya kit<u>aa</u>bahu biyamiinihi faul<u>aa</u>-ika yaqrauuna kit<u>aa</u>bahum wal<u>aa</u> yu<u>zh</u>lamuuna fatiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2101, '17:72', 17, 72, 'waman k<u>aa</u>na fii h<u>aadz</u>ihi a\'m<u>aa</u> fahuwa fii l-<u>aa</u>khirati a\'m<u>aa</u> wa-a<u>dh</u>allu sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2102, '17:73', 17, 73, 'wa-in k<u>aa</u>duu layaftinuunaka \'anilla<u>dz</u>ii aw<u>h</u>ayn<u>aa</u> ilayka litaftariya \'alayn<u>aa</u> ghayrahu wa-i<u>dz</u>an lattakha<u>dz</u>uuka khaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2103, '17:74', 17, 74, 'walawl<u>aa</u> an tsabbatn<u>aa</u>ka laqad kidta tarkanu ilayhim syay-an qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2104, '17:75', 17, 75, 'i<u>dz</u>an la-a<u>dz</u>aqn<u>aa</u>ka <u>dh</u>i\'fa l<u>h</u>ay<u>aa</u>ti wa<u>dh</u>i\'fa lmam<u>aa</u>ti tsumma l<u>aa</u> tajidu laka \'alayn<u>aa</u> na<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2105, '17:76', 17, 76, 'wa-in k<u>aa</u>duu layastafizzuunaka mina l-ar<u>dh</u>i liyukhrijuuka minh<u>aa</u> wa-i<u>dz</u>an l<u>aa</u> yalbatsuuna khil<u>aa</u>faka ill<u>aa</u> qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2106, '17:77', 17, 77, 'sunnata man qad arsaln<u>aa</u> qablaka min rusulin<u>aa</u> wal<u>aa</u> tajidu lisunnatin<u>aa</u> ta<u>h</u>wiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2107, '17:78', 17, 78, 'aqimi <u>shsh</u>al<u>aa</u>ta liduluuki sysyamsi il<u>aa</u> ghasaqillayli waqur-<u>aa</u>na lfajri inna qur-<u>aa</u>na lfajri k<u>aa</u>na masyhuud<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2108, '17:79', 17, 79, 'waminallayli fatahajjad bihi n<u>aa</u>filatan laka \'as<u>aa</u> an yab\'atsaka rabbuka maq<u>aa</u>man ma<u>h</u>muud<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2109, '17:80', 17, 80, 'waqul rabbi adkhilnii mudkhala <u>sh</u>idqin wa-akhrijnii mukhraja <u>sh</u>idqin waj\'al lii min ladunka sul<u>th</u><u>aa</u>nan na<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2110, '17:81', 17, 81, 'waqul j<u>aa</u>-a l<u>h</u>aqqu wazahaqa lb<u>aath</u>ilu inna lb<u>aath</u>ila k<u>aa</u>na zahuuq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2111, '17:82', 17, 82, 'wanunazzilu mina lqur-<u>aa</u>ni m<u>aa</u> huwa syif<u>aa</u>un wara<u>h</u>matun lilmu/miniina wal<u>aa</u> yaziidu <u>zhzhaa</u>limiina ill<u>aa</u> khas<u>aa</u>r<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2112, '17:83', 17, 83, 'wa-i<u>dzaa</u> an\'amn<u>aa</u> \'al<u>aa</u> l-ins<u>aa</u>ni a\'ra<u>dh</u>a wana<u>aa</u> bij<u>aa</u>nibihi wa-i<u>dzaa</u> massahu sysyarru k<u>aa</u>na yauus<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2113, '17:84', 17, 84, 'qul kullun ya\'malu \'al<u>aa</u> sy<u>aa</u>kilatihi farabbukum a\'lamu biman huwa ahd<u>aa</u> sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2114, '17:85', 17, 85, 'wayas-aluunaka \'ani rruu<u>h</u>i quli rruu<u>h</u>u min amri rabbii wam<u>aa</u> uutiitum mina l\'ilmi ill<u>aa</u> qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2115, '17:86', 17, 86, 'wala-in syi/n<u>aa</u> lana<u>dz</u>habanna billa<u>dz</u>ii aw<u>h</u>ayn<u>aa</u> ilayka tsumma l<u>aa</u> tajidu laka bihi \'alayn<u>aa</u> wakiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2116, '17:87', 17, 87, 'ill<u>aa</u> ra<u>h</u>matan min rabbika inna fa<u>dh</u>lahu k<u>aa</u>na \'alayka kabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2117, '17:88', 17, 88, 'qul la-ini ijtama\'ati l-insu waljinnu \'al<u>aa</u> an ya/tuu bimitsli h<u>aadzaa</u> lqur-<u>aa</u>ni l<u>aa</u> ya/tuuna bimitslihi walaw k<u>aa</u>na ba\'<u>dh</u>uhum liba\'<u>dh</u>in <u>zh</u>ahiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2118, '17:89', 17, 89, 'walaqad <u>sh</u>arrafn<u>aa</u> linn<u>aa</u>si fii h<u>aadzaa</u> lqur-<u>aa</u>ni min kulli matsalin fa-ab<u>aa</u> aktsaru nn<u>aa</u>si ill<u>aa</u> kufuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2119, '17:90', 17, 90, 'waq<u>aa</u>luu lan nu/mina laka <u>h</u>att<u>aa</u> tafjura lan<u>aa</u> mina l-ar<u>dh</u>i yanbuu\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2120, '17:91', 17, 91, 'aw takuuna laka jannatun min nakhiilin wa\'inabin fatufajjira l-anh<u>aa</u>ra khil<u>aa</u>lah<u>aa</u> tafjiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2121, '17:92', 17, 92, 'aw tusqi<u>th</u>a ssam<u>aa</u>-a kam<u>aa</u> za\'amta \'alayn<u>aa</u> kisafan aw ta/tiya bil<u>aa</u>hi walmal<u>aa</u>-ikati qabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2122, '17:93', 17, 93, 'aw yakuuna laka baytun min zukhrufin aw tarq<u>aa</u> fii ssam<u>aa</u>-i walan nu/mina liruqiyyika <u>h</u>att<u>aa</u> tunazzila \'alayn<u>aa</u> kit<u>aa</u>ban naqrauhu qul sub<u>haa</u>na rabbii hal kuntu ill<u>aa</u> basyaran rasuul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2123, '17:94', 17, 94, 'wam<u>aa</u> mana\'a nn<u>aa</u>sa an yu/minuu i<u>dz</u> j<u>aa</u>-ahumu lhud<u>aa</u> ill<u>aa</u> an q<u>aa</u>luu aba\'atsall<u>aa</u>hu basyaran rasuul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2124, '17:95', 17, 95, 'qul law k<u>aa</u>na fii l-ar<u>dh</u>i mal<u>aa</u>-ikatun yamsyuuna mu<u>th</u>ma-inniina lanazzaln<u>aa</u> \'alayhim mina ssam<u>aa</u>-i malakan rasuul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2125, '17:96', 17, 96, 'qul kaf<u>aa</u> bil<u>aa</u>hi syahiidan baynii wabaynakum innahu k<u>aa</u>na bi\'ib<u>aa</u>dihi khabiiran ba<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2126, '17:97', 17, 97, 'waman yahdill<u>aa</u>hu fahuwa lmuhtadi waman yu<u>dh</u>lil falan tajida lahum awliy<u>aa</u>-a min duunihi wana<u>h</u>syuruhum yawma lqiy<u>aa</u>mati \'al<u>aa</u> wujuuhihim \'umyan wabukman wa<u>sh</u>umman ma/w<u>aa</u>hum jahannamu kullam<u>aa</u> khabat zidn<u>aa</u>hum sa\'iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2127, '17:98', 17, 98, '<u>dzaa</u>lika jaz<u>aa</u>uhum bi-annahum kafaruu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> waq<u>aa</u>luu a-i<u>dzaa</u> kunn<u>aa</u> \'i<u>zhaa</u>man waruf<u>aa</u>tan a-inn<u>aa</u> lamab\'uutsuuna khalqan jadiid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2128, '17:99', 17, 99, 'awa lam yaraw annall<u>aa</u>halla<u>dz</u>ii khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a q<u>aa</u>dirun \'al<u>aa</u> an yakhluqa mitslahum waja\'ala lahum ajalan l<u>aa</u> rayba fiihi fa-ab<u>aa</u> <u>zhzhaa</u>limuuna ill<u>aa</u> kufuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2129, '17:100', 17, 100, 'qul law antum tamlikuuna khaz<u>aa</u>-ina ra<u>h</u>mati rabbii i<u>dz</u>an la-amsaktum khasyyata l-inf<u>aa</u>qi wak<u>aa</u>na l-ins<u>aa</u>nu qatuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2130, '17:101', 17, 101, 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> tis\'a <u>aa</u>y<u>aa</u>tin bayyin<u>aa</u>tin fas-al banii isr<u>aa</u>-iila i<u>dz</u> j<u>aa</u>-ahum faq<u>aa</u>la lahu fir\'awnu innii la-a<u>zh</u>unnuka y<u>aa</u> muus<u>aa</u> mas<u>h</u>uur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2131, '17:102', 17, 102, 'q<u>aa</u>la laqad \'alimta m<u>aa</u> anzala h<u>aa</u>ul<u>aa</u>-i ill<u>aa</u> rabbu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i ba<u>shaa</u>-ira wa-innii la-a<u>zh</u>unnuka y<u>aa</u> fir\'awnu matsbuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2132, '17:103', 17, 103, 'fa-ar<u>aa</u>da an yastafizzahum mina l-ar<u>dh</u>i fa-aghraqn<u>aa</u>hu waman ma\'ahu jamii\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2133, '17:104', 17, 104, 'waquln<u>aa</u> min ba\'dihi libanii isr<u>aa</u>-iila uskunuu l-ar<u>dh</u>a fa-i<u>dzaa</u> j<u>aa</u>-a wa\'du l-<u>aa</u>khirati ji/n<u>aa</u> bikum lafiif<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2134, '17:105', 17, 105, 'wabil<u>h</u>aqqi anzaln<u>aa</u>hu wabil<u>h</u>aqqi nazala wam<u>aa</u> arsaln<u>aa</u>ka ill<u>aa</u> mubasysyiran wana<u>dz</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2135, '17:106', 17, 106, 'waqur-<u>aa</u>nan faraqn<u>aa</u>hu litaqra-ahu \'al<u>aa</u> nn<u>aa</u>si \'al<u>aa</u> muktsin wanazzaln<u>aa</u>hu tanziil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2136, '17:107', 17, 107, 'qul <u>aa</u>minuu bihi aw l<u>aa</u> tu/minuu innalla<u>dz</u>iina uutuu l\'ilma min qablihi i<u>dzaa</u> yutl<u>aa</u> \'alayhim yakhirruuna lil-a<u>dz</u>q<u>aa</u>ni sujjad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2137, '17:108', 17, 108, 'wayaquuluuna sub<u>haa</u>na rabbin<u>aa</u> in k<u>aa</u>na wa\'du rabbin<u>aa</u> lamaf\'uul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2138, '17:109', 17, 109, 'wayakhirruuna lil-a<u>dz</u>q<u>aa</u>ni yabkuuna wayaziiduhum khusyuu\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2139, '17:110', 17, 110, 'quli ud\'uull<u>aa</u>ha awi ud\'uu rra<u>h</u>m<u>aa</u>na ayyan m<u>aa</u> tad\'uu falahu l-asm<u>aa</u>u l<u>h</u>usn<u>aa</u> wal<u>aa</u> tajhar bi<u>sh</u>al<u>aa</u>tika wal<u>aa</u> tukh<u>aa</u>fit bih<u>aa</u> wabtaghi bayna <u>dzaa</u>lika sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2140, '17:111', 17, 111, 'waquli l<u>h</u>amdu lill<u>aa</u>hilla<u>dz</u>ii lam yattakhi<u>dz</u> waladan walam yakun lahu syariikun fii lmulki walam yakun lahu waliyyun mina <u>dzdz</u>ulli wakabbirhu takbiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2141, '18:1', 18, 1, 'al<u>h</u>amdu lill<u>aa</u>hilla<u>dz</u>ii anzala \'al<u>aa</u> \'abdihi lkit<u>aa</u>ba walam yaj\'al lahu \'iwaj<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2142, '18:2', 18, 2, 'qayyiman liyun<u>dz</u>ira ba/san syadiidan min ladunhu wayubasysyira lmu/miniinalla<u>dz</u>iina ya\'maluuna <u>shshaa</u>li<u>haa</u>ti anna lahum ajran <u>h</u>asan<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2143, '18:3', 18, 3, ' m<u>aa</u>kitsiina fiihi abad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2144, '18:4', 18, 4, ' wayun<u>dz</u>iralla<u>dz</u>iina q<u>aa</u>luu ittakha<u>dz</u>all<u>aa</u>hu walad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2145, '18:5', 18, 5, 'm<u>aa</u> lahum bihi min \'ilmin wal<u>aa</u> li-<u>aa</u>b<u>aa</u>-ihim kaburat kalimatan takhruju min afw<u>aa</u>hihim in yaquuluuna ill<u>aa</u> ka<u>dz</u>ib<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2146, '18:6', 18, 6, 'fala\'allaka b<u>aa</u>khi\'un nafsaka \'al<u>aa</u> <u>aa</u>ts<u>aa</u>rihim in lam yu/minuu bih<u>aadzaa</u> l<u>h</u>adiitsi asaf<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2147, '18:7', 18, 7, 'inn<u>aa</u> ja\'aln<u>aa</u> m<u>aa</u> \'al<u>aa</u> l-ar<u>dh</u>i ziinatan lah<u>aa</u> linabluwahum ayyuhum a<u>h</u>sanu \'amal<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2148, '18:8', 18, 8, 'wa-inn<u>aa</u> laj<u>aa</u>\'iluuna m<u>aa</u> \'alayh<u>aa</u> <u>sh</u>a\'iidan juruz<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2149, '18:9', 18, 9, 'am <u>h</u>asibta anna a<u>sh</u>-<u>haa</u>ba lkahfi warraqiimi k<u>aa</u>nuu min <u>aa</u>y<u>aa</u>tin<u>aa</u> \'ajab<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2150, '18:10', 18, 10, 'i<u>dz</u> aw<u>aa</u> lfityatu il<u>aa</u> lkahfi faq<u>aa</u>luu rabban<u>aa</u> <u>aa</u>tin<u>aa</u> min ladunka ra<u>h</u>matan wahayyi/ lan<u>aa</u> min amrin<u>aa</u> rasyad<u>aa</u><u> </u>\r\r\n');
INSERT INTO quranLatin VALUES (2151, '18:11', 18, 11, 'fa<u>dh</u>arabn<u>aa</u> \'al<u>aa</u> <u>aatsaa</u>nihim fii lkahfi siniina \'adad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2152, '18:12', 18, 12, 'tsumma ba\'atsn<u>aa</u>hum lina\'lama ayyu l<u>h</u>izbayni a<u>hsaa</u> lim<u>aa</u> labitsuu amad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2153, '18:13', 18, 13, 'na<u>h</u>nu naqu<u>shsh</u>u \'alayka naba-ahum bil<u>h</u>aqqi innahum fityatun <u>aa</u>manuu birabbihim wazidn<u>aa</u>hum hud<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2154, '18:14', 18, 14, 'waraba<u>th</u>n<u>aa</u> \'al<u>aa</u> quluubihim i<u>dz</u> q<u>aa</u>muu faq<u>aa</u>luu rabbun<u>aa</u> rabbu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i lan nad\'uwa min duunihi il<u>aa</u>han laqad quln<u>aa</u> i<u>dz</u>an sya<u>th</u>a<u>th</u><u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2155, '18:15', 18, 15, 'h<u>aa</u>ul<u>aa</u>-i qawmun<u>aa</u> ittakha<u>dz</u>uu min duunihi <u>aa</u>lihatan lawl<u>aa</u> ya/tuuna \'alayhim bisul<u>th</u><u>aa</u>nin bayyinin faman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>ib<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2156, '18:16', 18, 16, 'wa-i<u>dz</u>i i\'tazaltumuuhum wam<u>aa</u> ya\'buduuna ill<u>aa</u>ll<u>aa</u>ha fa/wuu il<u>aa</u> lkahfi yansyur lakum rabbukum min ra<u>h</u>matihi wayuhayyi/ lakum min amrikum mirfaq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2157, '18:17', 18, 17, 'watar<u>aa</u> sysyamsa i<u>dzaa</u> <u>th</u>ala\'at taz<u>aa</u>waru \'an kahfihim <u>dzaa</u>ta lyamiini wa-i<u>dzaa</u> gharabat taqri<u>dh</u>uhum <u>dzaa</u>ta sysyim<u>aa</u>li wahum fii fajwatin minhu <u>dzaa</u>lika min <u>aa</u>y<u>aa</u>till<u>aa</u>hi man yahdill<u>aa</u>hu fahuwa lmuhtadi waman yu<u>dh</u>lil falan tajida lahu waliyyan mursyid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2158, '18:18', 18, 18, 'wata<u>h</u>sabuhum ayq<u>aats</u>an wahum ruquudun wanuqallibuhum <u>dzaa</u>ta lyamiini wa<u>dzaa</u>ta sysyim<u>aa</u>li wakalbuhum b<u>aa</u>si<u>th</u>un <u>dz</u>ir<u>aa</u>\'ayhi bilwa<u>sh</u>iidi lawi i<u>ththh</u>ala\'ta \'alayhim lawallayta minhum fir<u>aa</u>ran walamuli/ta minhum ru\'b<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2159, '18:19', 18, 19, 'waka<u>dzaa</u>lika ba\'atsn<u>aa</u>hum liyatas<u>aa</u>-aluu baynahum q<u>aa</u>la q<u>aa</u>-ilun minhum kam labitstum q<u>aa</u>luu labitsn<u>aa</u> yawman aw ba\'<u>dh</u>a yawmin q<u>aa</u>luu rabbukum a\'lamu bim<u>aa</u> labitstum fab\'atsuu a<u>h</u>adakum biwariqikum h<u>aadz</u>ihi il<u>aa</u> lmadiinati falyan<u>zh</u>ur ayyuh<u>aa</u> azk<u>aa</u> <u>th</u>a\'<u>aa</u>man falya/tikum birizqin minhu walyatala<u>ththh</u>af wal<u>aa</u> yusy\'iranna bikum a<u>h</u>ad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2160, '18:20', 18, 20, 'innahum in ya<u>zh</u>haruu \'alaykum yarjumuukum aw yu\'iiduukum fii millatihim walan tufli<u>h</u>uu i<u>dz</u>an abad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2161, '18:21', 18, 21, 'waka<u>dzaa</u>lika a\'tsarn<u>aa</u> \'alayhim liya\'lamuu anna wa\'dall<u>aa</u>hi <u>h</u>aqqun wa-anna s<u>aa</u>\'ata l<u>aa</u> rayba fiih<u>aa</u> i<u>dz</u> yatan<u>aa</u>za\'uuna baynahum amrahum faq<u>aa</u>luu ibnuu \'alayhim buny<u>aa</u>nan rabbuhum a\'lamu bihim q<u>aa</u>lalla<u>dz</u>iina ghalabuu \'al<u>aa</u> amrihim lanattakhi<u>dz</u>anna \'alayhim masjid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2162, '18:22', 18, 22, 'sayaquuluuna tsal<u>aa</u>tsatun r<u>aa</u>bi\'uhum kalbuhum wayaquuluuna khamsatun s<u>aa</u>disuhum kalbuhum rajman bilghaybi wayaquuluuna sab\'atun wats<u>aa</u>minuhum kalbuhum qul rabbii a\'lamu bi\'iddatihim m<u>aa</u> ya\'lamuhum ill<u>aa</u> qaliilun fal<u>aa</u> tum<u>aa</u>ri fiihim ill<u>aa</u> mir<u>aa</u>-an <u>zhaa</u>hiran wal<u>aa</u> tastafti fiihim minhum a<u>h</u>ad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2163, '18:23', 18, 23, 'wal<u>aa</u> taquulanna lisyay-in innii f<u>aa</u>\'ilun <u>dzaa</u>lika ghad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2164, '18:24', 18, 24, 'ill<u>aa</u> an yasy<u>aa</u>-all<u>aa</u>hu wa<u>dz</u>kur rabbaka i<u>dzaa</u> nasiita waqul \'as<u>aa</u> an yahdiyani rabbii li-aqraba min h<u>aadzaa</u> rasyad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2165, '18:25', 18, 25, 'walabitsuu fii kahfihim tsal<u>aa</u>tsa mi-atin siniina wazd<u>aa</u>duu tis\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2166, '18:26', 18, 26, 'qulill<u>aa</u>hu a\'lamu bim<u>aa</u> labitsuu lahu ghaybu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i ab<u>sh</u>ir bihi wa-asmi\'m<u>aa</u> lahum min duunihi min waliyyin wal<u>aa</u> yusyriku fii <u>h</u>ukmihi a<u>h</u>ad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2167, '18:27', 18, 27, 'watlu m<u>aa</u> uu<u>h</u>iya ilayka min kit<u>aa</u>bi rabbika l<u>aa</u> mubaddila likalim<u>aa</u>tihi walan tajida min duunihi multa<u>h</u>ad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2168, '18:28', 18, 28, 'wa<u>sh</u>bir nafsaka ma\'alla<u>dz</u>iina yad\'uuna rabbahum bilghad<u>aa</u>ti wal\'asyiyyi yuriiduuna wajhahu wal<u>aa</u> ta\'du \'ayn<u>aa</u>ka \'anhum turiidu ziinata l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wal<u>aa</u> tu<u>th</u>i\'man aghfaln<u>aa</u> qalbahu \'an <u>dz</u>ikrin<u>aa</u> wattaba\'a haw<u>aa</u>hu wak<u>aa</u>na amruhu furu<u>th</u><u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2169, '18:29', 18, 29, 'waquli l<u>h</u>aqqu min rabbikum faman sy<u>aa</u>-a falyu/min waman sy<u>aa</u>-a falyakfur inn<u>aa</u> a\'tadn<u>aa</u> li<u>zhzhaa</u>limiina n<u>aa</u>ran a<u>hath</u>a bihim sur<u>aa</u>diquh<u>aa</u> wa-in yastaghiitsuu yugh<u>aa</u>tsuu bim<u>aa</u>-in kalmuhli yasywii lwujuuha bi/sa sysyar<u>aa</u>bu was<u>aa</u>-at murtafaq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2170, '18:30', 18, 30, 'innalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti inn<u>aa</u> l<u>aa</u> nu<u>dh</u>ii\'u ajra man a<u>h</u>sana \'amal<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2171, '18:31', 18, 31, 'ul<u>aa</u>-ika lahum jann<u>aa</u>tu \'adnin tajrii min ta<u>h</u>tihimu l-anh<u>aa</u>ru yu<u>h</u>allawna fiih<u>aa</u> min as<u>aa</u>wira min <u>dz</u>ahabin waylbasuuna tsiy<u>aa</u>ban khu<u>dh</u>ran min sundusin wa-istabraqin muttaki-iina fiih<u>aa</u> \'al<u>aa</u> l-ar<u>aa</u>-iki ni\'ma tstsaw<u>aa</u>bu wa<u>h</u>asunat murtafaq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2172, '18:32', 18, 32, 'wa<u>dh</u>rib lahum matsalan rajulayni ja\'aln<u>aa</u> li-a<u>h</u>adihim<u>aa</u> jannatayni min a\'n<u>aa</u>bin wa<u>h</u>afafn<u>aa</u>hum<u>aa</u> binakhlin waja\'aln<u>aa</u> baynahum<u>aa</u> zar\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2173, '18:33', 18, 33, 'kilt<u>aa</u> ljannatayni <u>aa</u>tat ukulah<u>aa</u> walam ta<u>zh</u>lim minhu syay-an wafajjarn<u>aa</u> khil<u>aa</u>lahum<u>aa</u> nahar<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2174, '18:34', 18, 34, 'wak<u>aa</u>na lahu tsamarun faq<u>aa</u>la li<u>shaah</u>ibihi wahuwa yu<u>haa</u>wiruhu an<u>aa</u> aktsaru minka m<u>aa</u>lan wa-a\'azzu nafar<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2175, '18:35', 18, 35, 'wadakhala jannatahu wahuwa <u>zhaa</u>limun linafsihi q<u>aa</u>la m<u>aa</u> a<u>zh</u>unnu an tabiida h<u>aadz</u>ihi abad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2176, '18:36', 18, 36, 'wam<u>aa</u> a<u>zh</u>unnu ss<u>aa</u>\'ata q<u>aa</u>-imatan wala-in rudidtu il<u>aa</u> rabbii la-ajidanna khayran minh<u>aa</u> munqalab<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2177, '18:37', 18, 37, 'q<u>aa</u>la lahu <u>shaah</u>ibuhu wahuwa yu<u>haa</u>wiruhu akafarta billa<u>dz</u>ii khalaqaka min tur<u>aa</u>bin tsumma min nu<u>th</u>fatin tsumma saww<u>aa</u>ka rajul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2178, '18:38', 18, 38, 'l<u>aa</u>kinna huwall<u>aa</u>hu rabbii wal<u>aa</u> usyriku birabbii a<u>h</u>ad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2179, '18:39', 18, 39, 'walawl<u>aa</u> i<u>dz</u> dakhalta jannataka qulta m<u>aa</u> sy<u>aa</u>-all<u>aa</u>hu l<u>aa</u> quwwata ill<u>aa</u> bil<u>aa</u>hi in tarani an<u>aa</u> aqalla minka m<u>aa</u>lan wawalad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2180, '18:40', 18, 40, 'fa\'as<u>aa</u> rabbii an yu/tiyani khayran min jannatika wayursila \'alayh<u>aa</u> <u>h</u>usb<u>aa</u>nan mina ssam<u>aa</u>-i fatu<u>sh</u>bi<u>h</u>a <u>sh</u>a\'iidan zalaq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2181, '18:41', 18, 41, 'aw yu<u>sh</u>bi<u>h</u>a m<u>aa</u>uh<u>aa</u> ghawran falan tasta<u>th</u>ii\'a lahu <u>th</u>alab<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2182, '18:42', 18, 42, 'wau<u>h</u>ii<u>th</u>a bitsamarihi fa-a<u>sh</u>ba<u>h</u>a yuqallibu kaffayhi \'al<u>aa</u> m<u>aa</u> anfaqa fiih<u>aa</u> wahiya kh<u>aa</u>wiyatun \'al<u>aa</u> \'uruusyih<u>aa</u> wayaquulu y<u>aa</u> laytanii lam usyrik birabbii a<u>h</u>ad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2183, '18:43', 18, 43, 'walam takun lahu fi-atun yan<u>sh</u>uruunahu min duunill<u>aa</u>hi wam<u>aa</u> k<u>aa</u>na munta<u>sh</u>ir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2184, '18:44', 18, 44, 'hun<u>aa</u>lika lwal<u>aa</u>yatu lill<u>aa</u>hi l<u>h</u>aqqi huwa khayrun tsaw<u>aa</u>ban wakhayrun \'uqb<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2185, '18:45', 18, 45, 'wa<u>dh</u>rib lahum matsala l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> kam<u>aa</u>-in anzaln<u>aa</u>hu mina ssam<u>aa</u>-i fakhtala<u>th</u>a bihi nab<u>aa</u>tu l-ar<u>dh</u>i fa-a<u>sh</u>ba<u>h</u>a hasyiiman ta<u>dz</u>ruuhu rriy<u>aah</u>u wak<u>aa</u>nall<u>aa</u>hu \'al<u>aa</u> kulli syay-in muqtadir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2186, '18:46', 18, 46, 'alm<u>aa</u>lu walbanuuna ziinatu l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> walb<u>aa</u>qiy<u>aa</u>tu <u>shshaa</u>li<u>haa</u>tu khayrun \'inda rabbika tsaw<u>aa</u>ban wakhayrun amal<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2187, '18:47', 18, 47, 'wayawma nusayyiru ljib<u>aa</u>la watar<u>aa</u> l-ar<u>dh</u>a b<u>aa</u>rizatan wa<u>h</u>asyarn<u>aa</u>hum falam nugh<u>aa</u>dir minhum a<u>h</u>ad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2188, '18:48', 18, 48, 'wa\'uri<u>dh</u>uu \'al<u>aa</u> rabbika <u>sh</u>affan laqad ji/tumuun<u>aa</u> kam<u>aa</u> khalaqn<u>aa</u>kum awwala marratin bal za\'amtumllan naj\'ala lakum maw\'id<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2189, '18:49', 18, 49, 'wawu<u>dh</u>i\'a lkit<u>aa</u>bu fatar<u>aa</u> lmujrimiina musyfiqiina mimm<u>aa</u> fiihi wayaquuluuna y<u>aa</u> waylatan<u>aa</u> m<u>aa</u> lih<u>aadzaa</u> lkit<u>aa</u>bi l<u>aa</u> yugh<u>aa</u>diru <u>sh</u>aghiiratan wal<u>aa</u> kabiiratan ill<u>aa</u> a<u>hsaa</u>h<u>aa</u> wawajaduu m<u>aa</u> \'amiluu <u>had</u>iran wal<u>aa</u> ya<u>zh</u>limu rabbuka a<u>h</u>ad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2190, '18:50', 18, 50, 'wa-i<u>dz</u> quln<u>aa</u> lilmal<u>aa</u>-ikati usjuduu li-<u>aa</u>dama fasajaduu ill<u>aa</u> ibliisa k<u>aa</u>na mina ljinni fafasaqa \'an amri rabbihi afatattakhi<u>dz</u>uunahu wa<u>dz</u>urriyyatahu awliy<u>aa</u>-a min duunii wahum lakum \'aduwwun bi/sa li<u>zhzhaa</u>limiina badal<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2191, '18:51', 18, 51, 'm<u>aa</u> asyhadtuhum khalqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wal<u>aa</u> khalqa anfusihim wam<u>aa</u> kuntu muttakhi<u>dz</u>a lmu<u>dh</u>illiina \'a<u>dh</u>ud<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2192, '18:52', 18, 52, 'wayawma yaquulu n<u>aa</u>duu syurak<u>aa</u>-iyalla<u>dz</u>iina za\'amtum fada\'awhum falam yastajiibuu lahum waja\'aln<u>aa</u> baynahum mawbiq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2193, '18:53', 18, 53, 'wara<u>aa</u> lmujrimuuna nn<u>aa</u>ra fa<u>zh</u>annuu annahum muw<u>aa</u>qi\'uuh<u>aa</u> walam yajiduu \'anh<u>aa</u> ma<u>sh</u>rif<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2194, '18:54', 18, 54, 'walaqad <u>sh</u>arrafn<u>aa</u> fii h<u>aadzaa</u> lqur-<u>aa</u>ni linn<u>aa</u>si min kulli matsalin wak<u>aa</u>na l-ins<u>aa</u>nu aktsara syay-in jadal<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2195, '18:55', 18, 55, 'wam<u>aa</u> mana\'a nn<u>aa</u>sa an yu/minuu i<u>dz</u> j<u>aa</u>-ahumu lhud<u>aa</u> wayastaghfiruu rabbahum ill<u>aa</u> an ta/tiyahum sunnatu l-awwaliina aw ya/tiyahumu l\'a<u>dzaa</u>bu qubul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2196, '18:56', 18, 56, 'wam<u>aa</u> nursilu lmursaliina ill<u>aa</u> mubasysyiriina wamun<u>dz</u>iriina wayuj<u>aa</u>dilulla<u>dz</u>iina kafaruu bilb<u>aath</u>ili liyud<u>h</u>i<u>dh</u>uu bihi l<u>h</u>aqqa wattakha<u>dz</u>uu <u>aa</u>y<u>aa</u>tii wam<u>aa</u> un<u>dz</u>iruu huzuw<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2197, '18:57', 18, 57, 'waman a<u>zh</u>lamu mimman <u>dz</u>ukkira bi-<u>aa</u>y<u>aa</u>ti rabbihi fa-a\'ra<u>dh</u>a \'anh<u>aa</u> wanasiya m<u>aa</u> qaddamat yad<u>aa</u>hu inn<u>aa</u> ja\'aln<u>aa</u> \'al<u>aa</u> quluubihim akinnatan an yafqahuuhu wafii <u>aatsaa</u>nihim waqran wa-in tad\'uhum il<u>aa</u> lhud<u>aa</u> falan yahtaduu i<u>dz</u>an abad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2198, '18:58', 18, 58, 'warabbuka lghafuuru <u>dz</u>uu rra<u>h</u>mati law yu-<u>aa</u>khi<u>dz</u>uhum bim<u>aa</u> kasabuu la\'ajjala lahumu l\'a<u>dzaa</u>ba bal lahum maw\'idun lan yajiduu min duunihi maw-il<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2199, '18:59', 18, 59, 'watilka lqur<u>aa</u> ahlakn<u>aa</u>hum lamm<u>aa</u> <u>zh</u>alamuu waja\'aln<u>aa</u> limahlikihim maw\'id<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2200, '18:60', 18, 60, 'wa-i<u>dz</u> q<u>aa</u>la muus<u>aa</u> lifat<u>aa</u>hu l<u>aa</u> abra<u>h</u>u <u>h</u>att<u>aa</u> ablugha majma\'a lba<u>h</u>rayni aw am<u>dh</u>iya <u>h</u>uqub<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2201, '18:61', 18, 61, 'falamm<u>aa</u> balagh<u>aa</u> majma\'a baynihim<u>aa</u> nasiy<u>aa</u> <u>h</u>uutahum<u>aa</u> fattakha<u>dz</u>a sabiilahu fii lba<u>h</u>ri sarab<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2202, '18:62', 18, 62, 'falamm<u>aa</u> j<u>aa</u>waz<u>aa</u> q<u>aa</u>la lifat<u>aa</u>hu <u>aa</u>tin<u>aa</u> ghad<u>aa</u>-an<u>aa</u> laqad laqiin<u>aa</u> min safarin<u>aa</u> h<u>aadzaa</u> na<u>sh</u>ab<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2203, '18:63', 18, 63, 'q<u>aa</u>la ara-ayta i<u>dz</u> awayn<u>aa</u> il<u>aa</u> <u>shsh</u>akhrati fa-innii nasiitu l<u>h</u>uuta wam<u>aa</u> ans<u>aa</u>niihu ill<u>aa</u> sysyay<u>th</u><u>aa</u>nu an a<u>dz</u>kurahu wattakha<u>dz</u>a sabiilahu fii lba<u>h</u>ri \'ajab<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2204, '18:64', 18, 64, 'q<u>aa</u>la <u>dzaa</u>lika m<u>aa</u> kunn<u>aa</u> nabghi fartadd<u>aa</u> \'al<u>aa</u> <u>aa</u>ts<u>aa</u>rihim<u>aa</u> qa<u>sh</u>a<u>shaa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2205, '18:65', 18, 65, 'fawajad<u>aa</u> \'abdan min \'ib<u>aa</u>din<u>aa</u> <u>aa</u>tayn<u>aa</u>hu ra<u>h</u>matan min \'indin<u>aa</u> wa\'allamn<u>aa</u>hu min ladunn<u>aa</u> \'ilm<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2206, '18:66', 18, 66, 'q<u>aa</u>la lahu muus<u>aa</u> hal attabi\'uka \'al<u>aa</u> an tu\'allimani mimm<u>aa</u> \'ullimta rusyd<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2207, '18:67', 18, 67, 'q<u>aa</u>la innaka lan tasta<u>th</u>ii\'a ma\'iya <u>sh</u>abr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2208, '18:68', 18, 68, 'wakayfa ta<u>sh</u>biru \'al<u>aa</u> m<u>aa</u> lam tu<u>h</u>i<u>th</u> bihi khubr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2209, '18:69', 18, 69, 'q<u>aa</u>la satajidunii in sy<u>aa</u>-all<u>aa</u>hu <u>shaa</u>biran wal<u>aa</u> a\'<u>sh</u>ii laka amr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2210, '18:70', 18, 70, 'q<u>aa</u>la fa-ini ittaba\'tanii fal<u>aa</u> tas-alnii \'an syay-in <u>h</u>att<u>aa</u> u<u>h</u>ditsa laka minhu <u>dz</u>ikr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2211, '18:71', 18, 71, 'fan<u>th</u>alaq<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> rakib<u>aa</u> fii ssafiinati kharaqah<u>aa</u> q<u>aa</u>la akharaqtah<u>aa</u> litughriqa ahlah<u>aa</u> laqad ji/ta syay-an imr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2212, '18:72', 18, 72, 'q<u>aa</u>la alam aqul innaka lan tasta<u>th</u>ii\'a ma\'iya <u>sh</u>abr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2213, '18:73', 18, 73, 'q<u>aa</u>la l<u>aa</u> tu-<u>aa</u>khi<u>dz</u>nii bim<u>aa</u> nasiitu wal<u>aa</u> turhiqnii min amrii \'usr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2214, '18:74', 18, 74, 'fan<u>th</u>alaq<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> laqiy<u>aa</u> ghul<u>aa</u>man faqatalahu q<u>aa</u>la aqatalta nafsan zakiyyatan bighayri nafsin laqad ji/ta syay-an nukr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2215, '18:75', 18, 75, 'q<u>aa</u>la alam aqul laka innaka lan tasta<u>th</u>ii\'a ma\'iya <u>sh</u>abr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2216, '18:76', 18, 76, 'q<u>aa</u>la in sa-altuka \'an syay-in ba\'dah<u>aa</u> fal<u>aa</u> tu<u>shaah</u>ibnii qad balaghta min ladunnii \'u<u>dz</u>r<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2217, '18:77', 18, 77, 'fan<u>th</u>alaq<u>aa</u> <u>h</u>att<u>aa</u> i<u>dzaa</u> atay<u>aa</u> ahla qaryatin ista<u>th</u>\'am<u>aa</u> ahlah<u>aa</u> fa-abaw an yu<u>dh</u>ayyifuuhum<u>aa</u> fawajad<u>aa</u> fiih<u>aa</u> jid<u>aa</u>ran yuriidu an yanqa<u>dhdh</u>a fa-aq<u>aa</u>mahu q<u>aa</u>la law syi/ta lattakha<u>dz</u>ta \'alayhi ajr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2218, '18:78', 18, 78, 'q<u>aa</u>la h<u>aadzaa</u> fir<u>aa</u>qu baynii wabaynika sa-unabbi-uka bita/wiili m<u>aa</u> lam tasta<u>th</u>i\'\'alayhi <u>sh</u>abr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2219, '18:79', 18, 79, 'amm<u>aa</u> ssafiinatu fak<u>aa</u>nat limas<u>aa</u>kiina ya\'maluuna fii lba<u>h</u>ri fa-aradtu an a\'iibah<u>aa</u> wak<u>aa</u>na war<u>aa</u>-ahum malikun ya/khu<u>dz</u>u kulla safiinatin gha<u>sh</u>b<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2220, '18:80', 18, 80, 'wa-amm<u>aa</u> lghul<u>aa</u>mu fak<u>aa</u>na abaw<u>aa</u>hu mu/minayni fakhasyiin<u>aa</u> an yurhiqahum<u>aa</u> <u>th</u>ughy<u>aa</u>nan wakufr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2221, '18:81', 18, 81, 'fa-aradn<u>aa</u> an yubdilahum<u>aa</u> rabbuhum<u>aa</u> khayran minhu zak<u>aa</u>tan wa-aqraba ru<u>h</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2222, '18:82', 18, 82, 'wa-amm<u>aa</u> ljid<u>aa</u>ru fak<u>aa</u>na lighul<u>aa</u>mayni yatiimayni fii lmadiinati wak<u>aa</u>na ta<u>h</u>tahu kanzun lahum<u>aa</u> wak<u>aa</u>na abuuhum<u>aa</u> <u>shaa</u>li<u>h</u>an fa-ar<u>aa</u>da rabbuka an yablugh<u>aa</u> asyuddahum<u>aa</u> wayastakhrij<u>aa</u> kanzahum<u>aa</u> ra<u>h</u>matan min rabbika wam<u>aa</u> fa\'altuhu \'an amrii <u>dzaa</u>lika ta/wiilu m<u>aa</u> lam tas<u>th</u>i\'\'alayhi <u>sh</u>abr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2223, '18:83', 18, 83, 'wayas-aluunaka \'an <u>dz</u>ii lqarnayni qul sa-atluu \'alaykum minhu <u>dz</u>ikr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2224, '18:84', 18, 84, 'inn<u>aa</u> makkann<u>aa</u> lahu fii l-ar<u>dh</u>i wa<u>aa</u>tayn<u>aa</u>hu min kulli syay-in sabab<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2225, '18:85', 18, 85, 'fa-atba\'a sabab<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2226, '18:86', 18, 86, '<u>h</u>att<u>aa</u> i<u>dzaa</u> balagha maghriba sysyamsi wajadah<u>aa</u> taghrubu fii \'aynin <u>h</u>ami-atin wawajada \'indah<u>aa</u> qawman quln<u>aa</u> y<u>aa</u> <u>dzaa</u> lqarnayni imm<u>aa</u> an tu\'a<u>dzdz</u>iba wa-imm<u>aa</u> an tattakhi<u>dz</u>a fiihim <u>h</u>usn<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2227, '18:87', 18, 87, 'q<u>aa</u>la amm<u>aa</u> man <u>zh</u>alama fasawfa nu\'a<u>dzdz</u>ibuhu tsumma yuraddu il<u>aa</u> rabbihi fayu\'a<u>dzdz</u>ibuhu \'a<u>dzaa</u>ban nukr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2228, '18:88', 18, 88, 'wa-amm<u>aa</u> man <u>aa</u>mana wa\'amila <u>shaa</u>li<u>h</u>an falahu jaz<u>aa</u>-an l<u>h</u>usn<u>aa</u> wasanaquulu lahu min amrin<u>aa</u> yusr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2229, '18:89', 18, 89, 'tsumma atba\'a sabab<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2230, '18:90', 18, 90, '<u>h</u>att<u>aa</u> i<u>dzaa</u> balagha ma<u>th</u>li\'a sysyamsi wajadah<u>aa</u> ta<u>th</u>lu\'u \'al<u>aa</u> qawmin lam naj\'al lahum min duunih<u>aa</u> sitr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2231, '18:91', 18, 91, 'ka<u>dzaa</u>lika waqad a<u>h</u>a<u>th</u>n<u>aa</u> bim<u>aa</u> ladayhi khubr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2232, '18:92', 18, 92, 'tsumma atba\'a sabab<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2233, '18:93', 18, 93, '<u>h</u>att<u>aa</u> i<u>dzaa</u> balagha bayna ssaddayni wajada min duunihim<u>aa</u> qawman l<u>aa</u> yak<u>aa</u>duuna yafqahuuna qawl<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2234, '18:94', 18, 94, 'q<u>aa</u>luu y<u>aa</u> <u>dzaa</u> lqarnayni inna ya/juuja wama/juuja mufsiduuna fii l-ar<u>dh</u>i fahal naj\'alu laka kharjan \'al<u>aa</u> an taj\'ala baynan<u>aa</u> wabaynahum sadd<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2235, '18:95', 18, 95, 'q<u>aa</u>la m<u>aa</u> makkannii fiihi rabbii khayrun fa-a\'iinuunii biquwwatin aj\'al baynakum wabaynahum radm<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2236, '18:96', 18, 96, '<u>aa</u>tuunii zubara l<u>h</u>adiidi <u>h</u>att<u>aa</u> i<u>dzaa</u> s<u>aa</u>w<u>aa</u> bayna <u>sh</u>adafayni q<u>aa</u>la unfukhuu <u>h</u>att<u>aa</u> i<u>dzaa</u> ja\'alahu n<u>aa</u>ran q<u>aa</u>la <u>aa</u>tuunii ufrigh \'alayhi qi<u>th</u>r<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2237, '18:97', 18, 97, 'fam<u>aa</u> is<u>th</u><u>aa</u>\'uu an ya<u>zh</u>haruuhu wam<u>aa</u> ista<u>th</u><u>aa</u>\'uu lahu naqb<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2238, '18:98', 18, 98, 'q<u>aa</u>la h<u>aadzaa</u> ra<u>h</u>matun min rabbii fa-i<u>dzaa</u> j<u>aa</u>-a wa\'du rabbii ja\'alahu dakk<u>aa</u>-a wak<u>aa</u>na wa\'du rabbii <u>h</u>aqq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2239, '18:99', 18, 99, 'watarakn<u>aa</u> ba\'<u>dh</u>ahum yawma-i<u>dz</u>in yamuuju fii ba\'<u>dh</u>in wanufikha fii <u>shsh</u>uuri fajama\'n<u>aa</u>hum jam\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2240, '18:100', 18, 100, 'wa\'ara<u>dh</u>n<u>aa</u> jahannama yawma-i<u>dz</u>in lilk<u>aa</u>firiina \'ar<u>daa</u><b>n </b>\r\r\n');
INSERT INTO quranLatin VALUES (2241, '18:101', 18, 101, '<B>al</b>la<u>dz</u>iina k<u>aa</u>nat a\'yunuhum fii ghi<u>th</u><u>aa</u>-in \'an <u>dz</u>ikrii wak<u>aa</u>nuu l<u>aa</u> yasta<u>th</u>ii\'uuna sam\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2242, '18:102', 18, 102, 'afa<u>h</u>asiballa<u>dz</u>iina kafaruu an yattakhi<u>dz</u>uu \'ib<u>aa</u>dii min duunii awliy<u>aa</u>-a inn<u>aa</u> a\'tadn<u>aa</u> jahannama lilk<u>aa</u>firiina nuzul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2243, '18:103', 18, 103, 'qul hal nunabbi-ukum bil-akhsariina a\'m<u>aa</u>l<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2244, '18:104', 18, 104, '<B>al</b>la<u>dz</u>iina <u>dh</u>alla sa\'yuhum fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wahum ya<u>h</u>sabuuna annahum yu<u>h</u>sinuuna <u>sh</u>un\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2245, '18:105', 18, 105, 'ul<u>aa</u>-ikalla<u>dz</u>iina kafaruu bi-<u>aa</u>y<u>aa</u>ti rabbihim waliq<u>aa</u>-ihi fa<u>h</u>abi<u>th</u>at a\'m<u>aa</u>luhum fal<u>aa</u> nuqiimu lahum yawma lqiy<u>aa</u>mati wazn<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2246, '18:106', 18, 106, '<u>dzaa</u>lika jaz<u>aa</u>uhum jahannamu bim<u>aa</u> kafaruu wattakha<u>dz</u>uu <u>aa</u>y<u>aa</u>tii warusulii huzuw<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2247, '18:107', 18, 107, 'innalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti k<u>aa</u>nat lahum jann<u>aa</u>tu lfirdawsi nuzul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2248, '18:108', 18, 108, 'kh<u>aa</u>lidiina fiih<u>aa</u> l<u>aa</u> yabghuuna \'anh<u>aa</u> <u>h</u>iwal<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2249, '18:109', 18, 109, 'qul law k<u>aa</u>na lba<u>h</u>ru mid<u>aa</u>dan likalim<u>aa</u>ti rabbii lanafida lba<u>h</u>ru qabla an tanfada kalim<u>aa</u>tu rabbii walaw ji/n<u>aa</u> bimitslihi madad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2250, '18:110', 18, 110, 'qul innam<u>aa</u> an<u>aa</u> basyarun mitslukum yuu<u>haa</u> ilayya annam<u>aa</u> il<u>aa</u>hukum il<u>aa</u>hun w<u>aah</u>idun faman k<u>aa</u>na yarjuu liq<u>aa</u>-a rabbihi falya\'mal \'amalan <u>shaa</u>li<u>h</u>an wal<u>aa</u> yusyrik bi\'ib<u>aa</u>dati rabbihi a<u>h</u>ad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2251, '19:1', 19, 1, 'k<u>aa</u>f-h<u>aa</u>-y<u>aa</u>-\'ayn-<u>shaa</u>d</u>\r\r\n');
INSERT INTO quranLatin VALUES (2252, '19:2', 19, 2, '<u>dz</u>ikru ra<u>h</u>mati rabbika \'abdahu zakariyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2253, '19:3', 19, 3, 'i<u>dz</u> n<u>aa</u>d<u>aa</u> rabbahu nid<u>aa</u>-an khafiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2254, '19:4', 19, 4, 'q<u>aa</u>la rabbi innii wahana l\'a<u>zh</u>mu minnii wasyta\'ala rra/su syayban walam akun bidu\'<u>aa</u>-ika rabbi syaqiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2255, '19:5', 19, 5, 'wa-innii khiftu lmaw<u>aa</u>liya min war<u>aa</u>-ii wak<u>aa</u>nati imra-atii \'<u>aa</u>qiran fahab lii min ladunka waliyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2256, '19:6', 19, 6, 'yaritsunii wayaritsu min <u>aa</u>li ya\'quuba waj\'alhu rabbi ra<u>dh</u>iyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2257, '19:7', 19, 7, 'y<u>aa</u> zakariyy<u>aa</u> inn<u>aa</u> nubasysyiruka bighul<u>aa</u>min ismuhu ya<u>h</u>y<u>aa</u> lam naj\'al lahu min qablu samiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2258, '19:8', 19, 8, 'q<u>aa</u>la rabbi ann<u>aa</u> yakuunu lii ghul<u>aa</u>mun wak<u>aa</u>nati imra-atii \'<u>aa</u>qiran waqad balaghtu mina lkibari \'itiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2259, '19:9', 19, 9, 'q<u>aa</u>la ka<u>dzaa</u>lika q<u>aa</u>la rabbuka huwa \'alayya hayyinun waqad khalaqtuka min qablu walam taku syay-<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2260, '19:10', 19, 10, 'q<u>aa</u>la rabbi ij\'al lii <u>aa</u>yatan q<u>aa</u>la <u>aa</u>yatukall<u>aa</u> tukallima nn<u>aa</u>sa tsal<u>aa</u>tsa lay<u>aa</u>lin sawiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2261, '19:11', 19, 11, 'fakharaja \'al<u>aa</u> qawmihi mina lmi<u>h</u>r<u>aa</u>bi fa-aw<u>haa</u> ilayhim an sabbi<u>h</u>uu bukratan wa\'asyiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2262, '19:12', 19, 12, 'y<u>aa</u> ya<u>h</u>y<u>aa</u> khu<u>dz</u>i lkit<u>aa</u>ba biquwwatin wa<u>aa</u>tayn<u>aa</u>hu l<u>h</u>ukma <u>sh</u>abiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2263, '19:13', 19, 13, 'wa<u>h</u>an<u>aa</u>nan min ladunn<u>aa</u> wazak<u>aa</u>tan wak<u>aa</u>na taqiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2264, '19:14', 19, 14, 'wabarran biw<u>aa</u>lidayhi walam yakun jabb<u>aa</u>ran \'a<u>sh</u>iyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2265, '19:15', 19, 15, 'wasal<u>aa</u>mun \'alayhi yawma wulida wayawma yamuutu wayawma yub\'atsu <u>h</u>ayy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2266, '19:16', 19, 16, 'wa<u>dz</u>kur fii lkit<u>aa</u>bi maryama i<u>dz</u>i intaba<u>dz</u>at min ahlih<u>aa</u> mak<u>aa</u>nan syarqiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2267, '19:17', 19, 17, 'fattakha<u>dz</u>at min duunihim <u>h</u>ij<u>aa</u>ban fa-arsaln<u>aa</u> ilayh<u>aa</u> ruu<u>h</u>an<u>aa</u> fatamatstsala lah<u>aa</u> basyaran sawiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2268, '19:18', 19, 18, 'q<u>aa</u>lat innii a\'uu<u>dz</u>u birra<u>h</u>m<u>aa</u>ni minka in kunta taqiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2269, '19:19', 19, 19, 'q<u>aa</u>la innam<u>aa</u> an<u>aa</u> rasuulu rabbiki li-ahaba laki ghul<u>aa</u>man zakiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2270, '19:20', 19, 20, 'q<u>aa</u>lat ann<u>aa</u> yakuunu lii ghul<u>aa</u>mun walam yamsasnii basyarun walam aku baghiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2271, '19:21', 19, 21, 'q<u>aa</u>la ka<u>dzaa</u>liki q<u>aa</u>la rabbuki huwa \'alayya hayyinun walinaj\'alahu <u>aa</u>yatan linn<u>aa</u>si wara<u>h</u>matan minn<u>aa</u> wak<u>aa</u>na amran maq<u>dh</u>iyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2272, '19:22', 19, 22, 'fa<u>h</u>amalat-hu fantaba<u>dz</u>at bihi mak<u>aa</u>nan qa<u>sh</u>iyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2273, '19:23', 19, 23, 'fa-aj<u>aa</u>-ah<u>aa</u> lmakh<u>aad</u>u il<u>aa</u> ji<u>dz</u>\'i nnakhlati q<u>aa</u>lat y<u>aa</u> laytanii mittu qabla h<u>aadzaa</u> wakuntu nasyan mansiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2274, '19:24', 19, 24, 'fan<u>aa</u>d<u>aa</u>h<u>aa</u> min ta<u>h</u>tih<u>aa</u>ll<u>aa</u> ta<u>h</u>zanii qad ja\'ala rabbuki ta<u>h</u>taki sariyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2275, '19:25', 19, 25, 'wahuzzii ilayki biji<u>dz</u>\'i nnakhlati tus<u>aa</u>qi<u>th</u> \'alayki ru<u>th</u>aban janiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2276, '19:26', 19, 26, 'fakulii wasyrabii waqarrii \'aynan fa-imm<u>aa</u> tarayinna mina lbasyari a<u>h</u>adan faquulii innii na<u>dz</u>artu lirra<u>h</u>m<u>aa</u>ni <u>sh</u>awman falan ukallima lyawma insiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2277, '19:27', 19, 27, 'fa-atat bihi qawmah<u>aa</u> ta<u>h</u>miluhu q<u>aa</u>luu y<u>aa</u> maryamu laqad ji/ti syay-an fariyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2278, '19:28', 19, 28, 'y<u>aa</u> ukhta h<u>aa</u>ruuna m<u>aa</u> k<u>aa</u>na abuuki imra-a saw-in wam<u>aa</u> k<u>aa</u>nat ummuki baghiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2279, '19:29', 19, 29, 'fa-asy<u>aa</u>rat ilayhi q<u>aa</u>luu kayfa nukallimu man k<u>aa</u>na fii lmahdi <u>sh</u>abiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2280, '19:30', 19, 30, 'q<u>aa</u>la innii \'abdull<u>aa</u>hi <u>aa</u>t<u>aa</u>niya lkit<u>aa</u>ba waja\'alanii nabiyy<u>aa</u>\r\r\n');
