INSERT INTO quranLatin VALUES (2283, '19:33', 19, 33, 'wassal<u>aa</u>mu \'alayya yawma wulidtu wayawma amuutu wayawma ub\'atsu <u>h</u>ayy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2284, '19:34', 19, 34, '<u>dzaa</u>lika \'iis<u>aa</u> ibnu maryama qawla l<u>h</u>aqqilla<u>dz</u>ii fiihi yamtaruun\r\r\n');
INSERT INTO quranLatin VALUES (2285, '19:35', 19, 35, 'm<u>aa</u> k<u>aa</u>na lill<u>aa</u>hi an yattakhi<u>dz</u>a min waladin sub<u>haa</u>nahu i<u>dzaa</u> qa<u>daa</u> amran fa-innam<u>aa</u> yaquulu lahu kun fayakuun\r\r\n');
