INSERT INTO quranLatin VALUES (2288, '19:38', 19, 38, 'asmi\'bihim wa-ab<u>sh</u>ir yawma ya/tuunan<u>aa</u> l<u>aa</u>kini <u>zhzhaa</u>limuuna lyawma fii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (2289, '19:39', 19, 39, 'wa-an<u>dz</u>irhum yawma l<u>h</u>asrati i<u>dz</u> qu<u>dh</u>iya l-amru wahum fii ghaflatin wahum l<u>aa</u> yu/minuun\r\r\n');
