INSERT INTO quranLatin VALUES (2291, '19:41', 19, 41, 'wa<u>dz</u>kur fii lkit<u>aa</u>bi ibr<u>aa</u>hiima innahu k<u>aa</u>na <u>sh</u>iddiiqan nabiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2292, '19:42', 19, 42, 'i<u>dz</u> q<u>aa</u>la li-abiihi y<u>aa</u> abati lima ta\'budu m<u>aa</u> l<u>aa</u> yasma\'u wal<u>aa</u> yub<u>sh</u>iru wal<u>aa</u> yughnii \'anka syay-<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2293, '19:43', 19, 43, 'y<u>aa</u> abati innii qad j<u>aa</u>-anii mina l\'ilmi m<u>aa</u> lam ya/tika fattabi\'nii ahdika <u>sh</u>ir<u>aath</u>an sawiyy<u>aa</u>\r\r\n');
