INSERT INTO quranLatin VALUES (2295, '19:45', 19, 45, 'y<u>aa</u> abati innii akh<u>aa</u>fu an yamassaka \'a<u>dzaa</u>bun mina rra<u>h</u>m<u>aa</u>ni fatakuuna lisysyya<u>th</u><u>aa</u>ni waliyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2296, '19:46', 19, 46, 'q<u>aa</u>la ar<u>aa</u>ghibun anta \'an <u>aa</u>lihatii y<u>aa</u> ibr<u>aa</u>hiimu la-in lam tantahi la-arjumannaka wahjurnii maliyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2297, '19:47', 19, 47, 'q<u>aa</u>la sal<u>aa</u>mun \'alayka sa-astaghfiru laka rabbii innahu k<u>aa</u>na bii <u>h</u>afiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2298, '19:48', 19, 48, 'wa-a\'tazilukum wam<u>aa</u> tad\'uuna min duunill<u>aa</u>hi wa-ad\'uu rabbii \'as<u>aa</u>ll<u>aa</u> akuuna bidu\'<u>aa</u>-i rabbii syaqiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2299, '19:49', 19, 49, 'falamm<u>aa</u> i\'tazalahum wam<u>aa</u> ya\'buduuna min duunill<u>aa</u>hi wahabn<u>aa</u> lahu is<u>haa</u>qa waya\'quuba wakullan ja\'aln<u>aa</u> nabiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2300, '19:50', 19, 50, 'wawahabn<u>aa</u> lahum min ra<u>h</u>matin<u>aa</u> waja\'aln<u>aa</u> lahum lis<u>aa</u>na <u>sh</u>idqin \'aliyy<u>aa</u>\r\r\n');
