INSERT INTO quranLatin VALUES (2308, '19:58', 19, 58, 'ul<u>aa</u>-ikalla<u>dz</u>iina an\'amall<u>aa</u>hu \'alayhim mina nnabiyyiina min <u>dz</u>urriyyati <u>aa</u>dama wamimman <u>h</u>amaln<u>aa</u> ma\'a nuu<u>h</u>in wamin <u>dz</u>urriyyati ibr<u>aa</u>hiima wa-isr<u>aa</u>-iila wamimman hadayn<u>aa</u> wajtabayn<u>aa</u> i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tu rra<u>h</u>m<u>aa</u>ni kharruu sujjadan wabukiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2309, '19:59', 19, 59, 'fakhalafa min ba\'dihim khalfun a<u>daa</u>\'uu <u>shsh</u>al<u>aa</u>ta wattaba\'uu sysyahaw<u>aa</u>ti fasawfa yalqawna ghayy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2310, '19:60', 19, 60, 'ill<u>aa</u> man t<u>aa</u>ba wa<u>aa</u>mana wa\'amila <u>shaa</u>li<u>h</u>an faul<u>aa</u>-ika yadkhuluuna ljannata wal<u>aa</u> yu<u>zh</u>lamuuna syay-<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2311, '19:61', 19, 61, 'jann<u>aa</u>ti \'adninllatii wa\'ada rra<u>h</u>m<u>aa</u>nu \'ib<u>aa</u>dahu bighaybi innahu k<u>aa</u>na wa\'duhu ma/tiyy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2312, '19:62', 19, 62, 'l<u>aa</u> yasma\'uuna fiih<u>aa</u> laghwan ill<u>aa</u> sal<u>aa</u>man walahum rizquhum fiih<u>aa</u> bukratan wa\'asyiyy<u>aa</u>\r\r\n');
