INSERT INTO quranLatin VALUES (2325, '19:75', 19, 75, 'qul man k<u>aa</u>na fii <u>dhdh</u>al<u>aa</u>lati falyamdud lahu rra<u>h</u>m<u>aa</u>nu maddan <u>h</u>att<u>aa</u> i<u>dzaa</u> ra-aw m<u>aa</u> yuu\'aduuna imm<u>aa</u> l\'a<u>dzaa</u>ba wa-imm<u>aa</u> ss<u>aa</u>\'ata fasaya\'lamuuna man huwa syarrun mak<u>aa</u>nan wa-a<u>dh</u>\'afu jund<u>aa</u>\r\r\n');
