INSERT INTO quranLatin VALUES (2401, '20:53', 20, 53, '<B>al</b>la<u>dz</u>ii ja\'ala lakumu l-ar<u>dh</u>a mahdan wasalaka lakum fiih<u>aa</u> subulan wa-anzala mina ssam<u>aa</u>-i m<u>aa</u>-an fa-akhrajn<u>aa</u> bihi azw<u>aa</u>jan min nab<u>aa</u>tin syatt<u>aa</u>\r\r\n');
