INSERT INTO quranLatin VALUES (2417, '20:69', 20, 69, 'wa-alqi m<u>aa</u> fii yamiinika talqaf m<u>aa</u> <u>sh</u>ana\'uu innam<u>aa</u> <u>sh</u>ana\'uu kaydu s<u>aah</u>irin wal<u>aa</u> yufli<u>h</u>u ss<u>aah</u>iru <u>h</u>aytsu at<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2418, '20:70', 20, 70, 'faulqiya ssa<u>h</u>aratu sujjadan q<u>aa</u>luu <u>aa</u>mann<u>aa</u> birabbi h<u>aa</u>ruuna wamuus<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2419, '20:71', 20, 71, 'q<u>aa</u>la <u>aa</u>mantum lahu qabla an <u>aadz</u>ana lakum innahu lakabiirukumulla<u>dz</u>ii \'allamakumu ssi<u>h</u>ra falauqa<u>ththh</u>i\'anna aydiyakum wa-arjulakum min khil<u>aa</u>fin walau<u>sh</u>allibannakum fii ju<u>dz</u>uu\'i nnakhli walata\'lamunna ayyun<u>aa</u> asyaddu \'a<u>dzaa</u>ban wa-abq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2420, '20:72', 20, 72, 'q<u>aa</u>luu lan nu/tsiraka \'al<u>aa</u> m<u>aa</u> j<u>aa</u>-an<u>aa</u> mina lbayyin<u>aa</u>ti walla<u>dz</u>ii fa<u>th</u>aran<u>aa</u> faq<u>dh</u>i m<u>aa</u> anta q<u>aad</u>in innam<u>aa</u> taq<u>dh</u>ii h<u>aadz</u>ihi l<u>h</u>ay<u>aa</u>ta dduny<u>aa</u> </u>\r\r\n');
INSERT INTO quranLatin VALUES (2421, '20:73', 20, 73, 'inn<u>aa</u> <u>aa</u>mann<u>aa</u> birabbin<u>aa</u> liyaghfira lan<u>aa</u> kha<u>th</u><u>aa</u>y<u>aa</u>n<u>aa</u> wam<u>aa</u> akrahtan<u>aa</u> \'alayhi mina ssi<u>h</u>ri wal<u>aa</u>hu khayrun wa-abq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2422, '20:74', 20, 74, 'innahu man ya/ti rabbahu mujriman fa-inna lahu jahannama l<u>aa</u> yamuutu fiih<u>aa</u> wal<u>aa</u> ya<u>h</u>y<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2423, '20:75', 20, 75, 'waman ya/tihi mu/minan qad \'amila <u>shshaa</u>li<u>haa</u>ti faul<u>aa</u>-ika lahumu ddaraj<u>aa</u>tu l\'ul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2424, '20:76', 20, 76, 'jann<u>aa</u>tu \'<u>aa</u>dnin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wa<u>dzaa</u>lika jaz<u>aa</u>u man tazakk<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2425, '20:77', 20, 77, 'walaqad aw<u>h</u>ayn<u>aa</u> il<u>aa</u> muus<u>aa</u> an asri bi\'ib<u>aa</u>dii fa<u>dh</u>rib lahum <u>th</u>ariiqan fii lba<u>h</u>ri yabasan l<u>aa</u> takh<u>aa</u>fu darakan wal<u>aa</u> takhsy<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2426, '20:78', 20, 78, 'fa-atba\'ahum fir\'awnu bijunuudihi faghasyiyahum mina lyammi m<u>aa</u> ghasyiyahum</u>\r\r\n');
