INSERT INTO quranLatin VALUES (2428, '20:80', 20, 80, 'y<u>aa</u> banii isr<u>aa</u>-iila qad anjayn<u>aa</u>kum min \'aduwwikum waw<u>aa</u>\'adn<u>aa</u>kum j<u>aa</u>niba <u>ththh</u>uuri l-aymana wanazzaln<u>aa</u> \'alaykumu lmanna wassalw<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2429, '20:81', 20, 81, 'kuluu min <u>th</u>ayyib<u>aa</u>ti m<u>aa</u> razaqn<u>aa</u>kum wal<u>aa</u> ta<u>th</u>ghaw fiihi faya<u>h</u>illa \'alaykum gha<u>dh</u>abii waman ya<u>h</u>lil \'alayhi gha<u>dh</u>abii faqad haw<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2430, '20:82', 20, 82, 'wa-innii laghaff<u>aa</u>run liman t<u>aa</u>ba wa<u>aa</u>mana wa\'amila <u>shaa</u>li<u>h</u>an tsumma ihtad<u>aa</u>\r\r\n');
