INSERT INTO quranLatin VALUES (2432, '20:84', 20, 84, 'q<u>aa</u>la hum ul<u>aa</u>-i \'al<u>aa</u> atsarii wa\'ajiltu ilayka rabbi litar<u>daa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2433, '20:85', 20, 85, 'q<u>aa</u>la fa-inn<u>aa</u> qad fatann<u>aa</u> qawmaka min ba\'dika wa-a<u>dh</u>allahumu ss<u>aa</u>miriy\r\r\n');
INSERT INTO quranLatin VALUES (2434, '20:86', 20, 86, 'faraja\'a muus<u>aa</u> il<u>aa</u> qawmihi gha<u>dh</u>b<u>aa</u>na asifan q<u>aa</u>la y<u>aa</u> qawmi alam ya\'idkum rabbukum wa\'dan <u>h</u>asanan afa<u>th</u><u>aa</u>la \'alaykumu al\'ahdu am aradtum an ya<u>h</u>illa \'alaykum gha<u>dh</u>abun min rabbikum fa-akhlaftum maw\'idii</u>\r\r\n');
INSERT INTO quranLatin VALUES (2435, '20:87', 20, 87, 'q<u>aa</u>luu m<u>aa</u> akhlafn<u>aa</u> maw\'idaka bimalkin<u>aa</u> wal<u>aa</u>kinn<u>aa</u> <u>h</u>ummiln<u>aa</u> awz<u>aa</u>ran min ziinati lqawmi faqa<u>dz</u>afn<u>aa</u>h<u>aa</u> faka<u>dzaa</u>lika lq<u>aa</u> ss<u>aa</u>miriy\r\r\n');
INSERT INTO quranLatin VALUES (2436, '20:88', 20, 88, 'fa-akhraja lahum \'ijlan jasadan lahu khuw<u>aa</u>run faq<u>aa</u>luu h<u>aadzaa</u> il<u>aa</u>hukum wa-il<u>aa</u>hu muus<u>aa</u> fanasiy\r\r\n');
INSERT INTO quranLatin VALUES (2437, '20:89', 20, 89, 'afal<u>aa</u> yarawnall<u>aa</u> yarji\'u ilayhim qawlan wal<u>aa</u> yamliku lahum <u>dh</u>arran wal<u>aa</u> naf\'<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2438, '20:90', 20, 90, 'walaqad q<u>aa</u>la lahum h<u>aa</u>ruunu min qablu y<u>aa</u> qawmi innam<u>aa</u> futintum bihi wa-inna rabbakumu rra<u>h</u>m<u>aa</u>nu fattabi\'uunii wa-a<u>th</u>ii\'uu amrii</u>\r\r\n');
INSERT INTO quranLatin VALUES (2439, '20:91', 20, 91, 'q<u>aa</u>luu lan nabra<u>h</u>a \'alayhi \'<u>aa</u>kifiina <u>h</u>att<u>aa</u> yarji\'a ilayn<u>aa</u> muus<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2440, '20:92', 20, 92, 'q<u>aa</u>la y<u>aa</u> h<u>aa</u>ruunu m<u>aa</u> mana\'aka i<u>dz</u> ra-aytahum <u>dh</u>alluu </u>\r\r\n');
INSERT INTO quranLatin VALUES (2441, '20:93', 20, 93, '<B>al</b>l<u>aa</u> tattabi\'ani afa\'a<u>sh</u>ayta amrii</u>\r\r\n');
INSERT INTO quranLatin VALUES (2442, '20:94', 20, 94, 'q<u>aa</u>la yabnaumma l<u>aa</u> ta/khu<u>dz</u> bili<u>h</u>yatii wal<u>aa</u> bira/sii innii khasyiitu an taquula farraqta bayna banii isr<u>aa</u>-iila walam tarqub qawlii</u>\r\r\n');
INSERT INTO quranLatin VALUES (2443, '20:95', 20, 95, 'q<u>aa</u>la fam<u>aa</u> kha<u>th</u>buka y<u>aa</u> s<u>aa</u>miriy\r\r\n');
INSERT INTO quranLatin VALUES (2444, '20:96', 20, 96, 'q<u>aa</u>la ba<u>sh</u>urtu bim<u>aa</u> lam yab<u>sh</u>uruu bihi faqaba<u>dh</u>tu qab<u>dh</u>atan min atsari rrasuuli fanaba<u>dz</u>tuh<u>aa</u> waka<u>dzaa</u>lika sawwalat lii nafsii</u>\r\r\n');
INSERT INTO quranLatin VALUES (2445, '20:97', 20, 97, 'q<u>aa</u>la fa<u>dz</u>hab fa-inna laka fii l<u>h</u>ay<u>aa</u>ti an taquula l<u>aa</u> mis<u>aa</u>sa wa-inna laka maw\'idan lan tukhlafahu wan<u>zh</u>ur il<u>aa</u> il<u>aa</u>hikalla<u>dz</u>ii <u>zh</u>alta \'alayhi \'<u>aa</u>kifan lanu<u>h</u>arriqannahu tsumma lanansifannahu fii lyammi nasf<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2446, '20:98', 20, 98, 'innam<u>aa</u> il<u>aa</u>hukumull<u>aa</u>hulla<u>dz</u>ii l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa wasi\'a kulla syay-in \'ilm<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2447, '20:99', 20, 99, 'ka<u>dzaa</u>lika naqu<u>shsh</u>u \'alayka min anb<u>aa</u>-i m<u>aa</u> qad sabaqa waqad <u>aa</u>tayn<u>aa</u>ka min ladunn<u>aa</u> tsikra\r\r\n');
INSERT INTO quranLatin VALUES (2448, '20:100', 20, 100, 'man a\'ra<u>dh</u>a \'anhu fa-innahu ya<u>h</u>milu yawma lqiy<u>aa</u>mati wizr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2449, '20:101', 20, 101, 'kh<u>aa</u>lidiina fiihi was<u>aa</u>-a lahum yawma lqiy<u>aa</u>mati <u>h</u>iml<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2450, '20:102', 20, 102, 'yawma yunfakhu fii <u>shsh</u>uuri wana<u>h</u>syuru lmujrimiina yawma-i<u>dz</u>in zurq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2451, '20:103', 20, 103, 'yatakh<u>aa</u>fatuuna baynahum in labitstum ill<u>aa</u> \'asyr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2452, '20:104', 20, 104, 'na<u>h</u>nu a\'lamu bim<u>aa</u> yaquuluuna i<u>dz</u> yaquulu amtsaluhum <u>th</u>ariiqatan in labitstum ill<u>aa</u> yawm<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2453, '20:105', 20, 105, 'wayas-aluunaka \'ani ljib<u>aa</u>li faqul yansifuh<u>aa</u> rabbii nasf<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2454, '20:106', 20, 106, 'faya<u>dz</u>aruh<u>aa</u> q<u>aa</u>\'an <u>sh</u>af<u>sh</u>af<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2455, '20:107', 20, 107, 'l<u>aa</u> tar<u>aa</u> fiih<u>aa</u> \'iwajan wal<u>aa</u> amt<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2456, '20:108', 20, 108, 'yawma-i<u>dz</u>in yattabi\'uuna dd<u>aa</u>\'iya l<u>aa</u> \'iwaja lahu wakhasya\'ati l-a<u>sh</u>w<u>aa</u>tu lirra<u>h</u>m<u>aa</u>ni fal<u>aa</u> tasma\'u ill<u>aa</u> hams<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2457, '20:109', 20, 109, 'yawma-i<u>dz</u>in l<u>aa</u> tanfa\'u sysyaf<u>aa</u>\'atu ill<u>aa</u> man a<u>dz</u>ina lahu rra<u>h</u>m<u>aa</u>nu wara<u>dh</u>iya lahu qawl<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2458, '20:110', 20, 110, 'ya\'lamu m<u>aa</u> bayna aydiihim wam<u>aa</u> khalfahum wal<u>aa</u> yu<u>h</u>ii<u>th</u>uuna bihi \'ilm<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2459, '20:111', 20, 111, 'wa\'anati lwujuuhu lil<u>h</u>ayyi lqayyuumi waqad kh<u>aa</u>ba man <u>h</u>amala <u>zh</u>ulm<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2460, '20:112', 20, 112, 'waman ya\'mal mina <u>shshaa</u>li<u>haa</u>ti wahuwa mu/minun fal<u>aa</u> yakh<u>aa</u>fu <u>zh</u>ulman wal<u>aa</u> ha<u>dh</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2461, '20:113', 20, 113, 'waka<u>dzaa</u>lika anzaln<u>aa</u>hu qur-<u>aa</u>nan \'arabiyyan wa<u>sh</u>arrafn<u>aa</u> fiihi mina lwa\'iidi la\'allahum yattaquuna aw yu<u>h</u>ditsu lahum <u>dz</u>ikr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2462, '20:114', 20, 114, 'fata\'<u>aa</u>l<u>aa</u>ll<u>aa</u>hu lmaliku l<u>h</u>aqqu wal<u>aa</u> ta\'jal bilqur-<u>aa</u>ni min qabli an yuq<u>daa</u> ilayka wa<u>h</u>yuhu waqul rabbi zidnii \'ilm<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2463, '20:115', 20, 115, 'walaqad \'ahidn<u>aa</u> il<u>aa</u> <u>aa</u>dama min qablu fanasiya walam najid lahu \'azm<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2464, '20:116', 20, 116, 'wa-i<u>dz</u> quln<u>aa</u> lilmal<u>aa</u>-ikati usjuduu li-<u>aa</u>dama fasajaduu ill<u>aa</u> ibliisa ab<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2465, '20:117', 20, 117, 'faquln<u>aa</u> y<u>aa</u> <u>aa</u>damu inna h<u>aadzaa</u> \'aduwwun laka walizawjika fal<u>aa</u> yukhrijannakum<u>aa</u> mina ljannati fatasyq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2466, '20:118', 20, 118, 'inna lakall<u>aa</u> tajuu\'a fiih<u>aa</u> wal<u>aa</u> ta\'r<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2467, '20:119', 20, 119, 'wa-annaka l<u>aa</u> ta<u>zh</u>mau fiih<u>aa</u> wal<u>aa</u> ta<u>dhaa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2468, '20:120', 20, 120, 'fawaswasa ilayhi sysyay<u>th</u><u>aa</u>nu q<u>aa</u>la y<u>aa</u> <u>aa</u>damu hal adulluka \'al<u>aa</u> syajarati lkhuldi wamulkin l<u>aa</u> yabl<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2469, '20:121', 20, 121, 'fa-akal<u>aa</u> minh<u>aa</u> fabadat lahum<u>aa</u> saw-<u>aa</u>tuhum<u>aa</u> wa<u>th</u>afiq<u>aa</u> yakh<u>sh</u>if<u>aa</u>ni \'alayhim<u>aa</u> min waraqi ljannati wa\'a<u>shaa</u> <u>aa</u>damu rabbahu faghaw<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2470, '20:122', 20, 122, 'tsumma ijtab<u>aa</u>hu rabbuhu fat<u>aa</u>ba \'alayhi wahad<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2471, '20:123', 20, 123, 'q<u>aa</u>la ihbi<u>th</u><u>aa</u> minh<u>aa</u> jamii\'an ba\'<u>dh</u>ukum liba\'<u>dh</u>in \'aduwwun fa-imm<u>aa</u> ya/tiyannakum minnii hudan famani ittaba\'a hud<u>aa</u>ya fal<u>aa</u> ya<u>dh</u>illu wal<u>aa</u> yasyq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2472, '20:124', 20, 124, 'waman a\'ra<u>dh</u>a \'an <u>dz</u>ikrii fa-inna lahu ma\'iisyatan <u>dh</u>ank<u>aa</u> wana<u>h</u>syuruhu yawma lqiy<u>aa</u>mati a\'m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2473, '20:125', 20, 125, 'q<u>aa</u>la rabbi lima <u>h</u>asyartanii a\'m<u>aa</u> waqad kuntu ba<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2474, '20:126', 20, 126, 'q<u>aa</u>la ka<u>dzaa</u>lika atatka <u>aa</u>y<u>aa</u>tun<u>aa</u> fanasiitah<u>aa</u> waka<u>dzaa</u>lika lyawma tuns<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2475, '20:127', 20, 127, 'waka<u>dzaa</u>lika najzii man asrafa walam yu/min bi-<u>aa</u>y<u>aa</u>ti rabbihi wala\'a<u>dzaa</u>bu l-<u>aa</u>khirati asyaddu wa-abq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2476, '20:128', 20, 128, 'afalam yahdi lahum kam ahlakn<u>aa</u> qablahum mina lquruuni yamsyuuna fii mas<u>aa</u>kinihim inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin li-ulii nnuh<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2477, '20:129', 20, 129, 'walawl<u>aa</u> kalimatun sabaqat min rabbika lak<u>aa</u>na liz<u>aa</u>man wa-ajalun musamm<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2478, '20:130', 20, 130, 'fa<u>sh</u>bir \'al<u>aa</u> m<u>aa</u> yaquuluuna wasabbi<u>h</u> bi<u>h</u>amdi rabbika qabla <u>th</u>uluu\'i sysyamsi waqabla ghuruubih<u>aa</u> wamin <u>aa</u>n<u>aa</u>-illayli fasabbi<u>h</u> wa-a<u>th</u>r<u>aa</u>fa nnah<u>aa</u>ri la\'allaka tar<u>daa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2479, '20:131', 20, 131, 'wal<u>aa</u> tamuddanna \'aynayka il<u>aa</u> m<u>aa</u> matta\'n<u>aa</u> bihi azw<u>aa</u>jan minhum zahrata l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> linaftinahum fiihi warizqu rabbika khayrun wa-abq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2480, '20:132', 20, 132, 'wa/mur ahlaka bi<u>shsh</u>al<u>aa</u>ti wa<u>sth</u>abir \'alayh<u>aa</u> l<u>aa</u> nas-aluka rizqan na<u>h</u>nu narzuquka wal\'<u>aa</u>qibatu littaqw<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2481, '20:133', 20, 133, 'waq<u>aa</u>luu lawl<u>aa</u> ya/tiin<u>aa</u> bi-<u>aa</u>yatin min rabbihi awa lam ta/tihim bayyinatu m<u>aa</u> fii <u>shsh</u>u<u>h</u>ufi l-uul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2482, '20:134', 20, 134, 'walaw ann<u>aa</u> ahlakn<u>aa</u>hum bi\'a<u>dzaa</u>bin min qablihi laq<u>aa</u>luu rabban<u>aa</u> lawl<u>aa</u> arsalta ilayn<u>aa</u> rasuulan fanattabi\'a <u>aa</u>y<u>aa</u>tika min qabli an na<u>dz</u>illa wanakhz<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2483, '20:135', 20, 135, 'qul kullun mutarabbi<u>sh</u>un fatarabba<u>sh</u>uu fasata\'lamuuna man a<u>sh</u>-<u>haa</u>bu <u>shsh</u>ir<u>aath</u>i ssawiyyi wamani ihtad<u>aa</u></u>\r\r\n');
INSERT INTO quranLatin VALUES (2484, '21:1', 21, 1, 'iqtaraba linn<u>aa</u>si <u>h</u>is<u>aa</u>buhum wahum fii ghaflatin mu\'ri<u>dh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2485, '21:2', 21, 2, 'm<u>aa</u> ya/tiihim min <u>dz</u>ikrin min rabbihim mu<u>h</u>datsin ill<u>aa</u> istama\'uuhu wahum yal\'abuun\r\r\n');
INSERT INTO quranLatin VALUES (2486, '21:3', 21, 3, 'l<u>aa</u>hiyatan quluubuhum wa-asarruu nnajw<u>aa</u>lla<u>dz</u>iina <u>zh</u>alamuu hal h<u>aadzaa</u> ill<u>aa</u> basyarun mitslukum afata/tuuna ssi<u>h</u>ra wa-antum tub<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (2487, '21:4', 21, 4, 'q<u>aa</u>la rabbii ya\'lamu lqawla fii ssam<u>aa</u>-i wal-ar<u>dh</u>i wahuwa ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (2488, '21:5', 21, 5, 'bal q<u>aa</u>luu a<u>dh</u>gh<u>aa</u>tsu a<u>h</u>l<u>aa</u>min bali iftar<u>aa</u>hu bal huwa sy<u>aa</u>\'irun falya/tin<u>aa</u> bi-<u>aa</u>yatin kam<u>aa</u> ursila l-awwaluun\r\r\n');
INSERT INTO quranLatin VALUES (2489, '21:6', 21, 6, 'm<u>aa</u> <u>aa</u>manat qablahum min qaryatin ahlakn<u>aa</u>h<u>aa</u> afahum yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (2490, '21:7', 21, 7, 'wam<u>aa</u> arsaln<u>aa</u> qablaka ill<u>aa</u> rij<u>aa</u>lan nuu<u>h</u>ii ilayhim fas-aluu ahla <u>dzdz</u>ikri in kuntum l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (2491, '21:8', 21, 8, 'wam<u>aa</u> ja\'aln<u>aa</u>hum jasadan l<u>aa</u> ya/kuluuna <u>ththh</u>a\'<u>aa</u>ma wam<u>aa</u> k<u>aa</u>nuu kh<u>aa</u>lidiin\r\r\n');
INSERT INTO quranLatin VALUES (2492, '21:9', 21, 9, 'tsumma <u>sh</u>adaqn<u>aa</u>humu lwa\'da fa-anjayn<u>aa</u>hum waman nasy<u>aa</u>u wa-ahlakn<u>aa</u> lmusrifiin\r\r\n');
INSERT INTO quranLatin VALUES (2493, '21:10', 21, 10, 'laqad anzaln<u>aa</u> ilaykum kit<u>aa</u>ban fiihi <u>dz</u>ikrukum afal<u>aa</u> ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (2494, '21:11', 21, 11, 'wakam qa<u>sh</u>amn<u>aa</u> min qaryatin k<u>aa</u>nat <u>zhaa</u>limatan wa-ansya/n<u>aa</u> ba\'dah<u>aa</u> qawman <u>aa</u>khariin\r\r\n');
INSERT INTO quranLatin VALUES (2495, '21:12', 21, 12, 'falamm<u>aa</u> a<u>h</u>assuu ba/san<u>aa</u> i<u>dzaa</u> hum minh<u>aa</u> yarku<u>dh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2496, '21:13', 21, 13, 'l<u>aa</u> tarku<u>dh</u>uu warji\'uu il<u>aa</u> m<u>aa</u> utriftum fiihi wamas<u>aa</u>kinikum la\'allakum tus-aluun\r\r\n');
INSERT INTO quranLatin VALUES (2497, '21:14', 21, 14, 'q<u>aa</u>luu y<u>aa</u> waylan<u>aa</u> inn<u>aa</u> kunn<u>aa</u> <u>zhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2498, '21:15', 21, 15, 'fam<u>aa</u> z<u>aa</u>lat tilka da\'w<u>aa</u>hum <u>h</u>att<u>aa</u> ja\'aln<u>aa</u>hum <u>h</u>a<u>sh</u>iidan kh<u>aa</u>midiin\r\r\n');
INSERT INTO quranLatin VALUES (2499, '21:16', 21, 16, 'wam<u>aa</u> khalaqn<u>aa</u> ssam<u>aa</u>-a wal-ar<u>dh</u>a wam<u>aa</u> baynahum<u>aa</u> l<u>aa</u>\'ibiin\r\r\n');
INSERT INTO quranLatin VALUES (2500, '21:17', 21, 17, 'law aradn<u>aa</u> an nattakhi<u>dz</u>a lahwan lattakha<u>dz</u>n<u>aa</u>hu min ladunn<u>aa</u> in kunn<u>aa</u> f<u>aa</u>\'iliin\r\r\n');
INSERT INTO quranLatin VALUES (2501, '21:18', 21, 18, 'bal naq<u>dz</u>ifu bil<u>h</u>aqqi \'al<u>aa</u> lb<u>aath</u>ili fayadmaghuhu fa-i<u>dzaa</u> huwa z<u>aa</u>hiqun walakumu lwaylu mimm<u>aa</u> ta<u>sh</u>ifuun\r\r\n');
INSERT INTO quranLatin VALUES (2502, '21:19', 21, 19, 'walahu man fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i waman \'indahu l<u>aa</u> yastakbiruuna \'an \'ib<u>aa</u>datihi wal<u>aa</u> yasta<u>h</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (2503, '21:20', 21, 20, 'yusabbi<u>h</u>uunallayla wannah<u>aa</u>ra l<u>aa</u> yafturuun\r\r\n');
INSERT INTO quranLatin VALUES (2504, '21:21', 21, 21, 'ami ittakha<u>dz</u>uu <u>aa</u>lihatan mina l-ar<u>dh</u>i hum yunsyiruun\r\r\n');
INSERT INTO quranLatin VALUES (2505, '21:22', 21, 22, 'law k<u>aa</u>na fiihim<u>aa</u> <u>aa</u>lihatun ill<u>aa</u>ll<u>aa</u>hu lafasadat<u>aa</u> fasub<u>haa</u>nall<u>aa</u>hi rabbi l\'arsyi \'amm<u>aa</u> ya<u>sh</u>ifuun\r\r\n');
INSERT INTO quranLatin VALUES (2506, '21:23', 21, 23, 'l<u>aa</u> yus-alu \'amm<u>aa</u> yaf\'alu wahum yus-aluun\r\r\n');
INSERT INTO quranLatin VALUES (2507, '21:24', 21, 24, 'ami ittakha<u>dz</u>uu min duunihi <u>aa</u>lihatan qul h<u>aa</u>tuu burh<u>aa</u>nakum h<u>aadzaa</u> <u>dz</u>ikru man ma\'iya wa<u>dz</u>ikru man qablii bal aktsaruhum l<u>aa</u> ya\'lamuuna l<u>h</u>aqqa fahum mu\'ri<u>dh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2508, '21:25', 21, 25, 'wam<u>aa</u> arsaln<u>aa</u> min qablika min rasuulin ill<u>aa</u> nuu<u>h</u>ii ilayhi annahu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> an<u>aa</u> fa\'buduun\r\r\n');
INSERT INTO quranLatin VALUES (2509, '21:26', 21, 26, 'waq<u>aa</u>luu ittakha<u>dz</u>a rra<u>h</u>m<u>aa</u>nu waladan sub<u>haa</u>nahu bal \'ib<u>aa</u>dun mukramuun\r\r\n');
INSERT INTO quranLatin VALUES (2510, '21:27', 21, 27, 'l<u>aa</u> yasbiquunahu bilqawli wahum bi-amrihi ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (2511, '21:28', 21, 28, 'ya\'lamu m<u>aa</u> bayna aydiihim wam<u>aa</u> khalfahum wal<u>aa</u> yasyfa\'uuna ill<u>aa</u> limani irta<u>daa</u> wahum min khasyyatihi musyfiquun\r\r\n');
INSERT INTO quranLatin VALUES (2512, '21:29', 21, 29, 'waman yaqul minhum innii il<u>aa</u>hun min duunihi fa<u>dzaa</u>lika najziihi jahannama ka<u>dzaa</u>lika najzii <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2513, '21:30', 21, 30, 'awa lam yar<u>aa</u>lla<u>dz</u>iina kafaruu anna ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a k<u>aa</u>nat<u>aa</u> ratqan fafataqn<u>aa</u>hum<u>aa</u> waja\'aln<u>aa</u> mina lm<u>aa</u>-i kulla syay-in <u>h</u>ayyin afal<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (2514, '21:31', 21, 31, 'waja\'aln<u>aa</u> fii l-ar<u>dh</u>i raw<u>aa</u>siya an tamiida bihim waja\'aln<u>aa</u> fiih<u>aa </u>fij<u>aa</u>jan subulan la\'allahum yahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (2515, '21:32', 21, 32, 'waja\'aln<u>aa</u> ssam<u>aa</u>-a saqfan ma<u>h</u>fuu<u>zh</u>an wahum \'an <u>aa</u>y<u>aa</u>tih<u>aa</u> mu\'ri<u>dh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2516, '21:33', 21, 33, 'wahuwalla<u>dz</u>ii khalaqallayla wannah<u>aa</u>ra wasysyamsa walqamara kullun fii falakin yasba<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2517, '21:34', 21, 34, 'wam<u>aa</u> ja\'aln<u>aa</u> libasyarin min qablika lkhulda afa-in mitta fahumu lkh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (2518, '21:35', 21, 35, 'kullu nafsin <u>dzaa</u>-iqatu lmawti wanabluukum bisysyarri walkhayri fitnatan wa-ilayn<u>aa</u> turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2519, '21:36', 21, 36, 'wa-i<u>dzaa</u> ra<u>aa</u>kalla<u>dz</u>iina kafaruu in yattakhi<u>dz</u>uunaka ill<u>aa</u> huzuwan ah<u>aadzaa</u>lla<u>dz</u>ii ya<u>dz</u>kuru <u>aa</u>lihatakum wahum bi<u>dz</u>ikri rra<u>h</u>m<u>aa</u>ni hum k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (2520, '21:37', 21, 37, 'khuliqa l-ins<u>aa</u>nu min \'ajalin sauriikum <u>aa</u>y<u>aa</u>tii fal<u>aa</u> tasta\'jiluun\r\r\n');
INSERT INTO quranLatin VALUES (2521, '21:38', 21, 38, 'wayaquuluuna mat<u>aa</u> h<u>aadzaa</u> lwa\'du in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (2522, '21:39', 21, 39, 'law ya\'lamulla<u>dz</u>iina kafaruu <u>h</u>iina l<u>aa</u> yakuffuuna \'an wujuuhihimu nn<u>aa</u>ra wal<u>aa</u> \'an <u>zh</u>uhuurihim wal<u>aa</u> hum yun<u>sh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (2523, '21:40', 21, 40, 'bal ta/tiihim baghtatan fatabhatuhum fal<u>aa</u> yasta<u>th</u>ii\'uuna raddah<u>aa</u> wal<u>aa</u> hum yun<u>zh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (2524, '21:41', 21, 41, 'walaqadi istuhzi-a birusulin min qablika fa<u>haa</u>qa billa<u>dz</u>iina sakhiruu minhum m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (2525, '21:42', 21, 42, 'qul man yaklaukum billayli wannah<u>aa</u>ri mina rra<u>h</u>m<u>aa</u>ni bal hum \'an <u>dz</u>ikri rabbihim mu\'ri<u>dh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2526, '21:43', 21, 43, 'am lahum <u>aa</u>lihatun tamna\'uhum min duunin<u>aa</u> l<u>aa</u> yasta<u>th</u>ii\'uuna na<u>sh</u>ra anfusihim wal<u>aa</u> hum minn<u>aa</u> yu<u>sy</u>abuun\r\r\n');
INSERT INTO quranLatin VALUES (2527, '21:44', 21, 44, 'bal matta\'n<u>aa</u> h<u>aa</u>ul<u>aa</u>-i wa<u>aa</u>b<u>aa</u>-ahum <u>h</u>att<u>aa</u> <u>th</u><u>aa</u>la \'alayhimu l\'umuru afal<u>aa</u> yarawna ann<u>aa</u> na/tii l-ar<u>dh</u>a nanqu<u>sh</u>uh<u>aa</u> min a<u>th</u>r<u>aa</u>fih<u>aa</u> afahumu lgh<u>aa</u>libuun\r\r\n');
INSERT INTO quranLatin VALUES (2528, '21:45', 21, 45, 'qul innam<u>aa</u> un<u>dz</u>irukum bilwa<u>h</u>yi wal<u>aa</u> yasma\'u <u>shsh</u>ummu ddu\'<u>aa</u>-a i<u>dzaa</u> m<u>aa</u> yun<u>dz</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (2529, '21:46', 21, 46, 'wala-in massat-hum naf<u>h</u>atun min \'a<u>dzaa</u>bi rabbika layaquulunna y<u>aa </u>waylan<u>aa</u> inn<u>aa</u> kunn<u>aa</u> <u>zhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2530, '21:47', 21, 47, 'wana<u>dh</u>a\'u lmaw<u>aa</u>ziina lqis<u>th</u>a liyawmi lqiy<u>aa</u>mati fal<u>aa</u> tu<u>zh</u>lamu nafsun syay-an wa-in k<u>aa</u>na mitsq<u>aa</u>la <u>h</u>abbatin min khardalin atayn<u>aa</u> bih<u>aa</u> wakaf<u>aa</u> bin<u>aa</u> <u>haa</u>sibiin\r\r\n');
INSERT INTO quranLatin VALUES (2531, '21:48', 21, 48, 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> wah<u>aa</u>ruuna lfurq<u>aa</u>na wa<u>dh</u>iy<u>aa</u>-an wa<u>dz</u>ikran lilmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (2532, '21:49', 21, 49, '<B>al</b>la<u>dz</u>iina yakhsyawna rabbahum bilghaybi wahum mina ss<u>aa</u>\'ati musyfiquun\r\r\n');
INSERT INTO quranLatin VALUES (2533, '21:50', 21, 50, 'wah<u>aadzaa</u> <u>dz</u>ikrun mub<u>aa</u>rakun anzaln<u>aa</u>hu afa-antum lahu munkiruun\r\r\n');
INSERT INTO quranLatin VALUES (2534, '21:51', 21, 51, 'walaqad <u>aa</u>tayn<u>aa</u> ibr<u>aa</u>hiima rusydahu min qablu wakunn<u>aa</u> bihi \'<u>aa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2535, '21:52', 21, 52, 'i<u>dz</u> q<u>aa</u>la li-abiihi waqawmihi m<u>aa</u> h<u>aadz</u>ihi ttam<u>aa</u>tsiilullatii antum lah<u>aa</u> \'<u>aa</u>kifuun\r\r\n');
INSERT INTO quranLatin VALUES (2536, '21:53', 21, 53, 'q<u>aa</u>luu wajadn<u>aa</u> <u>aa</u>b<u>aa</u>-an<u>aa</u> lah<u>aa</u> \'<u>aa</u>bidiin\r\r\n');
INSERT INTO quranLatin VALUES (2537, '21:54', 21, 54, 'q<u>aa</u>la laqad kuntum antum wa<u>aa</u>b<u>aa</u>ukum fii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (2538, '21:55', 21, 55, 'q<u>aa</u>luu aji/tan<u>aa</u> bil<u>h</u>aqqi am anta minall<u>aa</u>\'ibiin\r\r\n');
INSERT INTO quranLatin VALUES (2539, '21:56', 21, 56, 'q<u>aa</u>la bal rabbukum rabbu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>illa<u>dz</u>ii fa<u>th</u>arahunna wa-an<u>aa</u> \'al<u>aa</u> <u>dzaa</u>likum mina sysy<u>aa</u>hidiin\r\r\n');
INSERT INTO quranLatin VALUES (2540, '21:57', 21, 57, 'watal<u>aa</u>hi la-akiidanna a<u>sh</u>n<u>aa</u>makum ba\'da an tuwalluu mudbiriin\r\r\n');
INSERT INTO quranLatin VALUES (2541, '21:58', 21, 58, 'faja\'alahum ju<u>dzats</u>an ill<u>aa</u> kabiiran lahum la\'allahum ilayhi yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2542, '21:59', 21, 59, 'q<u>aa</u>luu man fa\'ala h<u>aadzaa</u> bi-<u>aa</u>lihatin<u>aa</u> innahu lamina <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2543, '21:60', 21, 60, 'q<u>aa</u>luu sami\'n<u>aa</u> fatan ya<u>dz</u>kuruhum yuq<u>aa</u>lu lahu ibr<u>aa</u>hiim\r\r\n');
INSERT INTO quranLatin VALUES (2544, '21:61', 21, 61, 'q<u>aa</u>luu fa/tuu bihi \'al<u>aa</u> a\'yuni nn<u>aa</u>si la\'allahum yasyhaduun\r\r\n');
INSERT INTO quranLatin VALUES (2545, '21:62', 21, 62, 'q<u>aa</u>luu a-anta fa\'alta h<u>aadzaa</u> bi-<u>aa</u>lihatin<u>aa</u> y<u>aa</u> ibr<u>aa</u>hiim\r\r\n');
INSERT INTO quranLatin VALUES (2546, '21:63', 21, 63, 'q<u>aa</u>la bal fa\'alahu kabiiruhum h<u>aadzaa</u> fas-aluuhum in k<u>aa</u>nuu yan<u>th</u>iquun\r\r\n');
INSERT INTO quranLatin VALUES (2547, '21:64', 21, 64, 'faraja\'uu il<u>aa</u> anfusihim faq<u>aa</u>luu innakum antumu <u>zhzhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (2548, '21:65', 21, 65, 'tsumma nukisuu \'al<u>aa</u> ruuusihim laqad \'alimta m<u>aa</u> h<u>aa</u>ul<u>aa</u>-i yan<u>th</u>iquun\r\r\n');
INSERT INTO quranLatin VALUES (2549, '21:66', 21, 66, 'q<u>aa</u>la afata\'buduuna min duunill<u>aa</u>hi m<u>aa</u> l<u>aa</u> yanfa\'ukum syay-an wal<u>aa</u> ya<u>dh</u>urrukum</u>\r\r\n');
INSERT INTO quranLatin VALUES (2550, '21:67', 21, 67, 'uffin lakum walim<u>aa</u> ta\'buduuna min duunill<u>aa</u>hi afal<u>aa</u> ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (2551, '21:68', 21, 68, 'q<u>aa</u>luu <u>h</u>arriquuhu wan<u>sh</u>uruu <u>aa</u>lihatakum in kuntum f<u>aa</u>\'iliin\r\r\n');
INSERT INTO quranLatin VALUES (2552, '21:69', 21, 69, 'quln<u>aa</u> y<u>aa</u> n<u>aa</u>ru kuunii bardan wasal<u>aa</u>man \'al<u>aa</u> ibr<u>aa</u>hiim\r\r\n');
INSERT INTO quranLatin VALUES (2553, '21:70', 21, 70, 'wa-ar<u>aa</u>duu bihi kaydan faja\'aln<u>aa</u>humu l-akhsariin\r\r\n');
INSERT INTO quranLatin VALUES (2554, '21:71', 21, 71, 'wanajjayn<u>aa</u>hu waluu<u>th</u>an il<u>aa</u> l-ar<u>dh</u>illatii b<u>aa</u>rakn<u>aa</u> fiih<u>aa</u> lil\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (2555, '21:72', 21, 72, 'wawahabn<u>aa</u> lahu is<u>haa</u>qa waya\'quuba n<u>aa</u>filatan wakullan ja\'aln<u>aa</u> <u>shaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (2556, '21:73', 21, 73, 'waja\'aln<u>aa</u>hum a-immatan yahduuna bi-amrin<u>aa</u> wa-aw<u>h</u>ayn<u>aa</u> ilayhim fi\'la lkhayr<u>aa</u>ti wa-iq<u>aa</u>ma <u>shsh</u>al<u>aa</u>ti wa-iit<u>aa</u>-a zzak<u>aa</u>ti wak<u>aa</u>nuu lan<u>aa</u> \'<u>aa</u>bidiin\r\r\n');
INSERT INTO quranLatin VALUES (2557, '21:74', 21, 74, 'waluu<u>th</u>an <u>aa</u>tayn<u>aa</u>hu <u>h</u>ukman wa\'ilman wanajjayn<u>aa</u>hu mina lqaryatillatii k<u>aa</u>nat ta\'malu lkhab<u>aa</u>-itsa innahum k<u>aa</u>nuu qawma saw-in f<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (2558, '21:75', 21, 75, 'wa-adkhaln<u>aa</u>hu fii ra<u>h</u>matin<u>aa</u> innahu mina <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (2559, '21:76', 21, 76, 'wanuu<u>h</u>an i<u>dz</u> n<u>aa</u>d<u>aa</u> min qablu fastajabn<u>aa</u> lahu fanajjayn<u>aa</u>hu wa-ahlahu mina lkarbi l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (2560, '21:77', 21, 77, 'wana<u>sh</u>arn<u>aa</u>hu mina lqawmilla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> innahum k<u>aa</u>nuu qawma saw-in fa-aghraqn<u>aa</u>hum ajma\'iin\r\r\n');
INSERT INTO quranLatin VALUES (2561, '21:78', 21, 78, 'wad<u>aa</u>wuuda wasulaym<u>aa</u>na i<u>dz</u> ya<u>h</u>kum<u>aa</u>ni fii l<u>h</u>artsi i<u>dz</u> nafasyat fiihi ghanamu lqawmi wakunn<u>aa</u> li<u>h</u>ukmihim sy<u>aa</u>hidiin\r\r\n');
INSERT INTO quranLatin VALUES (2562, '21:79', 21, 79, 'fafahhamn<u>aa</u>h<u>aa</u> sulaym<u>aa</u>na wakullan <u>aa</u>tayn<u>aa</u> <u>h</u>ukman wa\'ilman wasakhkharn<u>aa</u> ma\'a d<u>aa</u>wuuda ljib<u>aa</u>la yusabbi<u>h</u>na wa<u>ththh</u>ayra wakunn<u>aa</u> f<u>aa</u>\'iliin\r\r\n');
INSERT INTO quranLatin VALUES (2563, '21:80', 21, 80, 'wa\'allamn<u>aa</u>hu <u>sh</u>an\'ata labuusin lakum litu<u>hs</u>inakum min ba/sikum fahal antum sy<u>aa</u>kiruun\r\r\n');
INSERT INTO quranLatin VALUES (2564, '21:81', 21, 81, 'walisulaym<u>aa</u>na rrii<u>h</u>a \'<u>aas</u>ifatan tajrii bi-amrihi il<u>aa</u> l-ar<u>dh</u>illatii b<u>aa</u>rakn<u>aa</u> fiih<u>aa</u> wakunn<u>aa</u> bikulli syay-in \'<u>aa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2565, '21:82', 21, 82, 'wamina sysyay<u>aath</u>iini man yaghuu<u>sh</u>uuna lahu waya\'maluuna \'amalan duuna <u>dzaa</u>lika wakunn<u>aa</u> lahum <u>haa</u>fi<u>zh</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (2566, '21:83', 21, 83, 'wa-ayyuuba i<u>dz</u> n<u>aa</u>d<u>aa</u> rabbahu annii massaniya <u>dhdh</u>urru wa-anta ar<u>h</u>amu rr<u>aah</u>imiin\r\r\n');
INSERT INTO quranLatin VALUES (2567, '21:84', 21, 84, 'fastajabn<u>aa</u> lahu fakasyafn<u>aa</u> m<u>aa</u> bihi min <u>dh</u>urrin wa<u>aa</u>tayn<u>aa</u>hu ahlahu wamitslahum ma\'ahum ra<u>h</u>matan min \'indin<u>aa</u> wa<u>dz</u>ikr<u>aa</u> lil\'<u>aa</u>bidiin\r\r\n');
INSERT INTO quranLatin VALUES (2568, '21:85', 21, 85, 'wa-ism<u>aa</u>\'iila wa-idriisa wa<u>dzaa</u> lkifli kullun mina <u>shshaa</u>biriin\r\r\n');
INSERT INTO quranLatin VALUES (2569, '21:86', 21, 86, 'wa-adkhaln<u>aa</u>hum fii ra<u>h</u>matin<u>aa</u> innahum mina <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (2570, '21:87', 21, 87, 'wa<u>dzaa</u> nnuuni i<u>dz</u> <u>dz</u>ahaba mugh<u>aad</u>iban fa<u>zh</u>anna an lan naqdira \'alayhi fan<u>aa</u>d<u>aa</u> fii <u>zhzh</u>ulum<u>aa</u>ti an l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> anta sub<u>haa</u>naka innii kuntu mina <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2571, '21:88', 21, 88, 'fastajabn<u>aa</u> lahu wanajjayn<u>aa</u>hu mina lghammi waka<u>dzaa</u>lika nunjii lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (2572, '21:89', 21, 89, 'wazakariyy<u>aa</u> i<u>dz</u> n<u>aa</u>d<u>aa</u> rabbahu rabbi l<u>aa</u> ta<u>dz</u>arnii fardan wa-anta khayru lw<u>aa</u>ritsiin\r\r\n');
INSERT INTO quranLatin VALUES (2573, '21:90', 21, 90, 'fastajabn<u>aa</u> lahu wawahabn<u>aa</u> lahu ya<u>h</u>y<u>aa</u> wa-a<u>sh</u>la<u>h</u>n<u>aa</u> lahu zawjahu innahum k<u>aa</u>nuu yus<u>aa</u>ri\'uuna fii lkhayr<u>aa</u>ti wayad\'uunan<u>aa</u> raghaban warahaban wak<u>aa</u>nuu lan<u>aa</u> kh<u>aa</u>syi\'iin\r\r\n');
INSERT INTO quranLatin VALUES (2574, '21:91', 21, 91, 'wallatii a<u>hs</u>anat farjah<u>aa</u> fanafakhn<u>aa</u> fiih<u>aa</u> min ruu<u>h</u>in<u>aa</u> waja\'aln<u>aa</u>h<u>aa</u> wabnah<u>aa</u> <u>aa</u>yatan lil\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (2575, '21:92', 21, 92, 'inna h<u>aadz</u>ihi ummatukum ummatan w<u>aah</u>idatan wa-an<u>aa</u> rabbukum fau\'buduun\r\r\n');
INSERT INTO quranLatin VALUES (2576, '21:93', 21, 93, 'wataqa<u>ththh</u>a\'uu amrahum baynahum kullun ilayn<u>aa</u> r<u>aa</u>ji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2577, '21:94', 21, 94, 'faman ya\'mal mina <u>shshaa</u>li<u>haa</u>ti wahuwa mu/minun fal<u>aa</u> kufr<u>aa</u>na lisa\'yihi wa-inn<u>aa</u> lahu k<u>aa</u>tibuun\r\r\n');
INSERT INTO quranLatin VALUES (2578, '21:95', 21, 95, 'wa<u>h</u>ar<u>aa</u>mun \'al<u>aa</u> qaryatin ahlakn<u>aa</u>h<u>aa</u> annahum l<u>aa</u> yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2579, '21:96', 21, 96, '<u>h</u>att<u>aa</u> i<u>dzaa</u> futi<u>h</u>at ya/juuju wama/juuju wahum min kulli <u>h</u>adabin yansiluun\r\r\n');
INSERT INTO quranLatin VALUES (2580, '21:97', 21, 97, 'waqtaraba lwa\'du l<u>h</u>aqqu fa-i<u>dzaa</u> hiya sy<u>aa</u>khi<u>sh</u>atun ab<u>shaa</u>rulla<u>dz</u>iina kafaruu y<u>aa</u> waylan<u>aa</u> qad kunn<u>aa</u> fii ghaflatin min h<u>aadzaa</u> bal kunn<u>aa</u> <u>zhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2581, '21:98', 21, 98, 'innakum wam<u>aa</u> ta\'buduuna min duunill<u>aa</u>hi <u>h</u>a<u>sh</u>abu jahannama antum lah<u>aa</u> w<u>aa</u>riduun\r\r\n');
INSERT INTO quranLatin VALUES (2582, '21:99', 21, 99, 'law k<u>aa</u>na h<u>aa</u>ul<u>aa</u>-i <u>aa</u>lihatan m<u>aa</u> waraduuh<u>aa</u> wakullun fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (2583, '21:100', 21, 100, 'lahum fiih<u>aa</u> zafiirun wahum fiih<u>aa</u> l<u>aa</u> yasma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2584, '21:101', 21, 101, 'innalla<u>dz</u>iina sabaqat lahum minn<u>aa</u> l<u>h</u>usn<u>aa</u> ul<u>aa</u>-ika \'anh<u>aa </u>mub\'aduun\r\r\n');
INSERT INTO quranLatin VALUES (2585, '21:102', 21, 102, 'l<u>aa</u> yasma\'uuna <u>h</u>asiisah<u>aa</u> wahum fii m<u>aa</u> isytahat anfusuhum kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (2586, '21:103', 21, 103, 'l<u>aa</u> ya<u>h</u>zunuhumu lfaza\'u l-akbaru watatalaqq<u>aa</u>humu lmal<u>aa</u>-ikatu h<u>aadzaa</u> yawmukumulla<u>dz</u>ii kuntum tuu\'aduun\r\r\n');
