INSERT INTO quranLatin VALUES (2589, '21:106', 21, 106, 'inna fii h<u>aadzaa</u> labal<u>aa</u>ghan liqawmin \'<u>aa</u>bidiin\r\r\n');
INSERT INTO quranLatin VALUES (2590, '21:107', 21, 107, 'wam<u>aa</u> arsaln<u>aa</u>ka ill<u>aa</u> ra<u>h</u>matan lil\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (2591, '21:108', 21, 108, 'qul innam<u>aa</u> yuu<u>haa</u> ilayya annam<u>aa</u> il<u>aa</u>hukum il<u>aa</u>hun w<u>aah</u>idun fahal antum muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (2592, '21:109', 21, 109, 'fa-in tawallaw faqul <u>aadz</u>antukum \'al<u>aa</u> saw<u>aa</u>-in wa-in adrii aqariibun am ba\'iidun m<u>aa</u> tuu\'aduun\r\r\n');
INSERT INTO quranLatin VALUES (2593, '21:110', 21, 110, 'innahu ya\'lamu ljahra mina lqawli waya\'lamu m<u>aa</u> taktumuun\r\r\n');
INSERT INTO quranLatin VALUES (2594, '21:111', 21, 111, 'wa-in adrii la\'allahu fitnatun lakum wamat<u>aa</u>\'un il<u>aa</u> <u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (2595, '21:112', 21, 112, 'q<u>aa</u>la rabbi u<u>h</u>kum bil<u>h</u>aqqi warabbun<u>aa</u> rra<u>h</u>m<u>aa</u>nu lmusta\'<u>aa</u>nu \'al<u>aa</u> m<u>aa</u> ta<u>sh</u>ifuun\r\r\n');
INSERT INTO quranLatin VALUES (2596, '22:1', 22, 1, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su ittaquu rabbakum inna zalzalata ss<u>aa</u>\'ati syay-un \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (2597, '22:2', 22, 2, 'yawma tarawnah<u>aa</u> ta<u>dz</u>halu kullu mur<u>dh</u>i\'atin \'amm<u>aa</u> ar<u>dh</u>a\'at wata<u>dh</u>a\'u kullu <u>dzaa</u>ti <u>h</u>amlin <u>h</u>amlah<u>aa</u> watar<u>aa</u> nn<u>aa</u>sa suk<u>aa</u>r<u>aa</u> wam<u>aa</u> hum bisuk<u>aa</u>r<u>aa</u> wal<u>aa</u>kinna \'a<u>dzaa</u>ball<u>aa</u>hi syadiid\r\r\n');
INSERT INTO quranLatin VALUES (2598, '22:3', 22, 3, 'wamina nn<u>aa</u>si man yuj<u>aa</u>dilu fiill<u>aa</u>hi bighayri \'ilmin wayattabi\'u kulla syay<u>th</u><u>aa</u>nin mariid\r\r\n');
INSERT INTO quranLatin VALUES (2599, '22:4', 22, 4, 'kutiba \'alayhi annahu man tawall<u>aa</u>hu fa-annahu yu<u>dh</u>illuhu wayahdiihi il<u>aa</u> \'a<u>dzaa</u>bi ssa\'iir\r\r\n');
INSERT INTO quranLatin VALUES (2600, '22:5', 22, 5, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su in kuntum fii raybin mina lba\'tsi fa-inn<u>aa</u> khalaqn<u>aa</u>kum min tur<u>aa</u>bin tsumma min nu<u>th</u>fatin tsumma min \'alaqatin tsumma min mu<u>dh</u>ghatin mukhallaqatin waghayri mukhallaqatin linubayyina lakum wanuqirru fii l-ar<u>haa</u>mi m<u>aa</u> nasy<u>aa</u>u il<u>aa</u> ajalin musamman tsumma nukhrijukum <u>th</u>iflan tsumma litablughuu asyuddakum waminkum man yutawaff<u>aa</u> waminkum man yuraddu il<u>aa</u> ar<u>dz</u>ali l\'umuri likayl<u>aa</u> ya\'lama min ba\'di \'ilmin syay-an watar<u>aa</u> l-ar<u>dh</u>a h<u>aa</u>midatan fa-i<u>dzaa</u> anzaln<u>aa</u> \'alayh<u>aa</u> lm<u>aa</u>-a ihtazzat warabat wa-anbatat min kulli zawjin bahiij\r\r\n');
INSERT INTO quranLatin VALUES (2601, '22:6', 22, 6, '<u>dzaa</u>lika bi-annall<u>aa</u>ha huwa l<u>h</u>aqqu wa-annahu yu<u>h</u>yii lmawt<u>aa</u> wa-annahu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (2602, '22:7', 22, 7, 'wa-anna ss<u>aa</u>\'ata <u>aa</u>tiyatun l<u>aa</u> rayba fiih<u>aa</u> wa-annall<u>aa</u>ha yab\'atsu man fii lqubuur\r\r\n');
INSERT INTO quranLatin VALUES (2603, '22:8', 22, 8, 'wamina nn<u>aa</u>si man yuj<u>aa</u>dilu fiill<u>aa</u>hi bighayri \'ilmin wal<u>aa</u> hudan wal<u>aa</u> kit<u>aa</u>bin muniir\r\r\n');
INSERT INTO quranLatin VALUES (2604, '22:9', 22, 9, 'ts<u>aa</u>niya \'i<u>th</u>fihi liyu<u>dh</u>illa \'an sabiilill<u>aa</u>hi lahu fii dduny<u>aa</u> khizyun wanu<u>dz</u>iiquhu yawma lqiy<u>aa</u>mati \'a<u>dzaa</u>ba l<u>h</u>ariiq\r\r\n');
INSERT INTO quranLatin VALUES (2605, '22:10', 22, 10, '<u>dzaa</u>lika bim<u>aa</u> qaddamat yad<u>aa</u>ka wa-annall<u>aa</u>ha laysa bi<u>zh</u>all<u>aa</u>min lil\'abiid\r\r\n');
INSERT INTO quranLatin VALUES (2606, '22:11', 22, 11, 'wamina nn<u>aa</u>si man ya\'budull<u>aa</u>ha \'al<u>aa</u> <u>h</u>arfin fa-in a<u>shaa</u>bahu khayrun i<u>th</u>ma-anna bihi wa-in a<u>shaa</u>bat-hu fitnatun inqalaba \'al<u>aa</u> wajhihi khasira dduny<u>aa</u> wal-<u>aa</u>khirata <u>dzaa</u>lika huwa lkhusr<u>aa</u>nu lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (2607, '22:12', 22, 12, 'yad\'uu min duunill<u>aa</u>hi m<u>aa</u> l<u>aa</u> ya<u>dh</u>urruhu wam<u>aa</u> l<u>aa</u> yanfa\'uhu <u>dzaa</u>lika huwa <u>dhdh</u>al<u>aa</u>lu lba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (2608, '22:13', 22, 13, 'yad\'uu laman <u>dh</u>arruhu aqrabu min naf\'ihi labi/sa lmawl<u>aa</u> walabi/sa l\'asyiir\r\r\n');
INSERT INTO quranLatin VALUES (2609, '22:14', 22, 14, 'innall<u>aa</u>ha yudkhilulla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru innall<u>aa</u>ha yaf\'alu m<u>aa</u> yuriid\r\r\n');
INSERT INTO quranLatin VALUES (2610, '22:15', 22, 15, 'man k<u>aa</u>na ya<u>zh</u>unnu an lan yan<u>sh</u>urahull<u>aa</u>hu fii dduny<u>aa</u> wal-<u>aa</u>khirati falyamdud bisababin il<u>aa</u> ssam<u>aa</u>-i tsumma liyaq<u>th</u>a\'falyan<u>zh</u>ur hal yu<u>dz</u>hibanna kayduhu m<u>aa</u> yaghii<u>zh</u>\r\r\n');
INSERT INTO quranLatin VALUES (2611, '22:16', 22, 16, 'waka<u>dzaa</u>lika anzaln<u>aa</u>hu <u>aa</u>y<u>aa</u>tin bayyin<u>aa</u>tin wa-annall<u>aa</u>ha yahdii man yuriid\r\r\n');
INSERT INTO quranLatin VALUES (2612, '22:17', 22, 17, 'innalla<u>dz</u>iina <u>aa</u>manuu walla<u>dz</u>iina h<u>aa</u>duu wa<u>shshaa</u>bi-iina wanna<u>shaa</u>r<u>aa</u> walmajuusa walla<u>dz</u>iina asyrakuu innall<u>aa</u>ha yaf<u>sh</u>ilu baynahum yawma lqiy<u>aa</u>mati innall<u>aa</u>ha \'al<u>aa</u> kulli syay-in syahiid\r\r\n');
INSERT INTO quranLatin VALUES (2613, '22:18', 22, 18, 'alam tara annall<u>aa</u>ha yasjudu lahu man fii ssam<u>aa</u>w<u>aa</u>ti waman fii l-ar<u>dh</u>i wasysyamsu walqamaru wannujuumu waljib<u>aa</u>lu wasysyajaru waddaw<u>aa</u>bbu wakatsiirun mina nn<u>aa</u>si wakatsiirun <u>h</u>aqqa \'alayhi l\'a<u>dzaa</u>bu waman yuhinill<u>aa</u>hu fam<u>aa</u> lahu min mukrimin innall<u>aa</u>ha yaf\'alu m<u>aa</u> yasy<u>aa</u>/\r\r\n');
INSERT INTO quranLatin VALUES (2614, '22:19', 22, 19, 'h<u>aadzaa</u>ni kha<u>sh</u>m<u>aa</u>ni ikhta<u>sh</u>amuu fii rabbihim falla<u>dz</u>iina kafaruu qu<u>ththh</u>i\'at lahum tsiy<u>aa</u>bun min n<u>aa</u>rin yu<u>sh</u>abbu min fawqi ruuusihimu l<u>h</u>amiim\r\r\n');
INSERT INTO quranLatin VALUES (2615, '22:20', 22, 20, 'yu<u>sh</u>haru bihi m<u>aa</u> fii bu<u>th</u>uunihim waljuluud\r\r\n');
INSERT INTO quranLatin VALUES (2616, '22:21', 22, 21, 'walahum maq<u>aa</u>mi\'u min <u>h</u>adiid\r\r\n');
INSERT INTO quranLatin VALUES (2617, '22:22', 22, 22, 'kullam<u>aa</u> ar<u>aa</u>duu an yakhrujuu minh<u>aa</u> min ghammin u\'iiduu fiih<u>aa</u> wa<u>dz</u>uuquu \'a<u>dzaa</u>ba l<u>h</u>ariiq\r\r\n');
INSERT INTO quranLatin VALUES (2618, '22:23', 22, 23, 'innall<u>aa</u>ha yudkhilulla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru yu<u>h</u>allawna fiih<u>aa</u> min as<u>aa</u>wira min <u>dz</u>ahabin walu/lu-an walib<u>aa</u>suhum fiih<u>aa</u> <u>h</u>ariir\r\r\n');
INSERT INTO quranLatin VALUES (2619, '22:24', 22, 24, 'wahuduu il<u>aa</u> <u>ththh</u>ayyibi mina lqawli wahuduu il<u>aa</u> <u>sh</u>ir<u>aath</u>i l<u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (2620, '22:25', 22, 25, 'innalla<u>dz</u>iina kafaruu waya<u>sh</u>udduuna \'an sabiilill<u>aa</u>hi walmasjidi l<u>h</u>ar<u>aa</u>milla<u>dz</u>ii ja\'aln<u>aa</u>hu linn<u>aa</u>si saw<u>aa</u>-an l\'<u>aa</u>kifu fiihi walb<u>aa</u>di waman yurid fiihi bi-il<u>haa</u>din bi<u>zh</u>ulmin nu<u>dz</u>iqhu min \'a<u>dzaa</u>bin liim\r\r\n');
INSERT INTO quranLatin VALUES (2621, '22:26', 22, 26, 'wa-i<u>dz</u> bawwa/n<u>aa</u> li-ibr<u>aa</u>hiima mak<u>aa</u>na lbayti an l<u>aa</u> tusyrik bii syay-an wa<u>th</u>ahhir baytiya li<u>ththaa</u>-ifiina walq<u>aa</u>-imiina warrukka\'i ssujuud\r\r\n');
INSERT INTO quranLatin VALUES (2622, '22:27', 22, 27, 'wa-a<u>dzdz</u>in fii nn<u>aa</u>si bil<u>h</u>ajji ya/tuuka rij<u>aa</u>lan wa\'al<u>aa</u> kulli <u>daa</u>mirin ya/tiina min kulli fajjin \'amiiq\r\r\n');
INSERT INTO quranLatin VALUES (2623, '22:28', 22, 28, 'liyasyhaduu man<u>aa</u>fi\'a lahum waya<u>dz</u>kuruu ismall<u>aa</u>hi fii ayy<u>aa</u>min ma\'luum<u>aa</u>tin \'al<u>aa</u> m<u>aa</u> razaqahum min bahiimati l-an\'<u>aa</u>mi fakuluu minh<u>aa</u> wa-a<u>th</u>\'imuu lb<u>aa</u>-isa lfaqiir\r\r\n');
INSERT INTO quranLatin VALUES (2624, '22:29', 22, 29, 'tsumma lyaq<u>dh</u>uu tafatsahum walyuufuu nu<u>dz</u>uurahum walya<u>ththh</u>awwafuu bilbayti l\'atiiq\r\r\n');
INSERT INTO quranLatin VALUES (2625, '22:30', 22, 30, '<u>dzaa</u>lika waman yu\'a<u>zhzh</u>im <u>h</u>urum<u>aa</u>till<u>aa</u>hi fahuwa khayrun lahu \'inda rabbihi wau<u>h</u>illat lakumu l-an\'<u>aa</u>mu ill<u>aa</u> m<u>aa</u> yutl<u>aa</u> \'alaykum fajtanibuu rrijsa mina l-awts<u>aa</u>ni wajtanibuu qawla zzuur\r\r\n');
INSERT INTO quranLatin VALUES (2626, '22:31', 22, 31, '<u>h</u>unaf<u>aa</u>-a lill<u>aa</u>hi ghayra musyrikiina bihi waman yusyrik bil<u>aa</u>hi faka-annam<u>aa</u> kharra mina ssam<u>aa</u>-i fatakh<u>th</u>afuhu <u>ththh</u>ayru aw tahwii bihi rrii<u>h</u>u fii mak<u>aa</u>nin sa<u>h</u>iiq\r\r\n');
INSERT INTO quranLatin VALUES (2627, '22:32', 22, 32, '<u>dzaa</u>lika waman yu\'a<u>zhzh</u>im sya\'<u>aa</u>-irall<u>aa</u>hi fa-innah<u>aa</u> min taqw<u>aa</u> lquluub\r\r\n');
INSERT INTO quranLatin VALUES (2628, '22:33', 22, 33, 'lakum fiih<u>aa</u> man<u>aa</u>fi\'u il<u>aa</u> ajalin musamman tsumma ma<u>h</u>illuh<u>aa</u> il<u>aa</u> lbayti l\'atiiq\r\r\n');
INSERT INTO quranLatin VALUES (2629, '22:34', 22, 34, 'walikulli ummatin ja\'aln<u>aa</u> mansakan liya<u>dz</u>kuruu ismall<u>aa</u>hi \'al<u>aa</u> m<u>aa</u> razaqahum min bahiimati l-an\'<u>aa</u>mi fa-il<u>aa</u>hukum il<u>aa</u>hun w<u>aah</u>idun falahu aslimuu wabasysyiri lmukhbitiin\r\r\n');
INSERT INTO quranLatin VALUES (2630, '22:35', 22, 35, '<B>al</b>la<u>dz</u>iina i<u>dzaa</u> <u>dz</u>ukirall<u>aa</u>hu wajilat quluubuhum wa<u>shshaa</u>biriina \'al<u>aa</u> m<u>aa</u> a<u>shaa</u>bahum walmuqiimii <u>shsh</u>al<u>aa</u>ti wamimm<u>aa</u> razaqn<u>aa</u>hum yunfiquun\r\r\n');
INSERT INTO quranLatin VALUES (2631, '22:36', 22, 36, 'walbudna ja\'aln<u>aa</u>h<u>aa</u> lakum min sya\'<u>aa</u>-irill<u>aa</u>hi lakum fiih<u>aa</u> khayrun fa<u>dz</u>kuruu ismall<u>aa</u>hi \'alayh<u>aa</u> <u>sh</u>aw<u>aa</u>ffa fa-i<u>dzaa</u> wajabat junuubuh<u>aa</u> fakuluu minh<u>aa</u> wa-a<u>th</u>\'imuu lq<u>aa</u>ni\'a walmu\'tarra ka<u>dzaa</u>lika sakhkharn<u>aa</u>h<u>aa</u> lakum la\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (2632, '22:37', 22, 37, 'lan yan<u>aa</u>lall<u>aa</u>ha lu<u>h</u>uumuh<u>aa</u> wal<u>aa</u> dim<u>aa</u>uh<u>aa</u> wal<u>aa</u>kin yan<u>aa</u>luhu ttaqw<u>aa</u> minkum ka<u>dzaa</u>lika sakhkharah<u>aa</u> lakum litukabbiruull<u>aa</u>ha \'al<u>aa</u> m<u>aa</u> had<u>aa</u>kum wabasysyiri lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (2633, '22:38', 22, 38, 'innall<u>aa</u>ha yud<u>aa</u>fi\'u \'anilla<u>dz</u>iina <u>aa</u>manuu innall<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu kulla khaww<u>aa</u>nin kafuur\r\r\n');
INSERT INTO quranLatin VALUES (2634, '22:39', 22, 39, 'u<u>dz</u>ina lilla<u>dz</u>iina yuq<u>aa</u>taluuna bi-annahum <u>zh</u>ulimuu wa-innall<u>aa</u>ha \'al<u>aa</u> na<u>sh</u>rihim laqadiir\r\r\n');
INSERT INTO quranLatin VALUES (2635, '22:40', 22, 40, '<B>al</b>la<u>dz</u>iina ukhrijuu min diy<u>aa</u>rihim bighayri <u>h</u>aqqin ill<u>aa</u> an yaquuluu rabbun<u>aa</u>ll<u>aa</u>hu walawl<u>aa</u> daf\'ull<u>aa</u>hi nn<u>aa</u>sa ba\'<u>dh</u>ahum biba\'<u>dh</u>in lahuddimat <u>sh</u>aw<u>aa</u>mi\'u wabiya\'un wa<u>sh</u>alaw<u>aa</u>tun wamas<u>aa</u>jidu yu<u>dz</u>karu fiih<u>aa</u> ismull<u>aa</u>hi katsiiran walayan<u>sh</u>urannall<u>aa</u>hu man yan<u>sh</u>uruhu innall<u>aa</u>ha laqawiyyun \'aziiz\r\r\n');
INSERT INTO quranLatin VALUES (2636, '22:41', 22, 41, '<B>al</b>la<u>dz</u>iina in makkann<u>aa</u>hum fii l-ar<u>dh</u>i aq<u>aa</u>muu <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tawuu zzak<u>aa</u>ta wa-amaruu bilma\'ruufi wanahaw \'ani lmunkari walill<u>aa</u>hi \'<u>aa</u>qibatu l-umuur\r\r\n');
INSERT INTO quranLatin VALUES (2637, '22:42', 22, 42, 'wa-in yuka<u>dzdz</u>ibuuka faqad ka<u>dzdz</u>abat qablahum qawmu nuu<u>h</u>in wa\'<u>aa</u>dun watsamuud\r\r\n');
INSERT INTO quranLatin VALUES (2638, '22:43', 22, 43, 'waqawmu ibr<u>aa</u>hiima waqawmu luu<u>th</u>\r\r\n');
INSERT INTO quranLatin VALUES (2639, '22:44', 22, 44, 'wa-a<u>sh</u>-<u>haa</u>bu madyana waku<u>dzdz</u>iba muus<u>aa</u> fa-amlaytu lilk<u>aa</u>firiina tsumma akha<u>dz</u>tuhum fakayfa k<u>aa</u>na nakiir\r\r\n');
INSERT INTO quranLatin VALUES (2640, '22:45', 22, 45, 'faka-ayyin min qaryatin ahlakn<u>aa</u>h<u>aa</u> wahiya <u>zhaa</u>limatun fahiya kh<u>aa</u>wiyatun \'al<u>aa</u> \'uruusyih<u>aa</u> wabi/rin mu\'a<u>ththh</u>alatin waqa<u>sh</u>rin masyiid\r\r\n');
INSERT INTO quranLatin VALUES (2641, '22:46', 22, 46, 'afalam yasiiruu fii l-ar<u>dh</u>i fatakuuna lahum quluubun ya\'qiluuna bih<u>aa</u> aw <u>aatsaa</u>nun yasma\'uuna bih<u>aa</u> fa-innah<u>aa</u> l<u>aa</u> ta\'m<u>aa</u> l-ab<u>shaa</u>ru wal<u>aa</u>kin ta\'m<u>aa</u> lquluubullatii fii <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (2642, '22:47', 22, 47, 'wayasta\'jiluunaka bil\'a<u>dzaa</u>bi walan yukhlifall<u>aa</u>hu wa\'dahu wa-inna yawman \'inda rabbika ka-alfi sanatin mimm<u>aa</u> ta\'udduun\r\r\n');
INSERT INTO quranLatin VALUES (2643, '22:48', 22, 48, 'waka-ayyin min qaryatin amlaytu lah<u>aa</u> wahiya <u>zhaa</u>limatun tsumma akha<u>dz</u>tuh<u>aa</u> wa-ilayya lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (2644, '22:49', 22, 49, 'qul y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su innam<u>aa</u> an<u>aa</u> lakum na<u>dz</u>iirun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (2645, '22:50', 22, 50, 'falla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti lahum maghfiratun warizqun kariim\r\r\n');
INSERT INTO quranLatin VALUES (2646, '22:51', 22, 51, 'walla<u>dz</u>iina sa\'aw fii <u>aa</u>y<u>aa</u>tin<u>aa</u> mu\'<u>aa</u>jiziina ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu lja<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (2647, '22:52', 22, 52, 'wam<u>aa</u> arsaln<u>aa</u> min qablika min rasuulin wal<u>aa</u> nabiyyin ill<u>aa</u> i<u>dzaa</u> tamann<u>aa</u> lq<u>aa</u> sysyay<u>th</u><u>aa</u>nu fii umniyyatihi fayansakhull<u>aa</u>hu m<u>aa</u> yulqii sysyay<u>th</u><u>aa</u>nu tsumma yu<u>h</u>kimull<u>aa</u>hu <u>aa</u>y<u>aa</u>tihi wal<u>aa</u>hu \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (2648, '22:53', 22, 53, 'liyaj\'ala m<u>aa</u> yulqii sysyay<u>th</u><u>aa</u>nu fitnatan lilla<u>dz</u>iina fii quluubihim mara<u>dh</u>un walq<u>aa</u>siyati quluubuhum wa-inna <u>zhzhaa</u>limiina lafii syiq<u>aa</u>qin ba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (2649, '22:54', 22, 54, 'waliya\'lamalla<u>dz</u>iina uutuu l\'ilma annahu l<u>h</u>aqqu min rabbika fayu/minuu bihi fatukhbita lahu quluubuhum wa-innall<u>aa</u>ha lah<u>aa</u>dilla<u>dz</u>iina <u>aa</u>manuu il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (2650, '22:55', 22, 55, 'wal<u>aa</u> yaz<u>aa</u>lulla<u>dz</u>iina kafaruu fii miryatin minhu <u>h</u>att<u>aa</u> ta/tiyahumu ss<u>aa</u>\'atu baghtatan aw ya/tiyahum \'a<u>dzaa</u>bu yawmin \'aqiim\r\r\n');
INSERT INTO quranLatin VALUES (2651, '22:56', 22, 56, 'almulku yawma-i<u>dz</u>in lill<u>aa</u>hi ya<u>h</u>kumu baynahum falla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti fii jann<u>aa</u>ti nna\'iim\r\r\n');
INSERT INTO quranLatin VALUES (2652, '22:57', 22, 57, 'walla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> faul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun muhiin\r\r\n');
INSERT INTO quranLatin VALUES (2653, '22:58', 22, 58, 'walla<u>dz</u>iina h<u>aa</u>jaruu fii sabiilill<u>aa</u>hi tsumma qutiluu aw m<u>aa</u>tuu layarzuqannahumull<u>aa</u>hu rizqan <u>h</u>asanan wa-innall<u>aa</u>ha lahuwa khayru rr<u>aa</u>ziqiin\r\r\n');
INSERT INTO quranLatin VALUES (2654, '22:59', 22, 59, 'layudkhilannahum mudkhalan yar<u>dh</u>awnahu wa-innall<u>aa</u>ha la\'aliimun <u>h</u>aliim\r\r\n');
INSERT INTO quranLatin VALUES (2655, '22:60', 22, 60, '<u>dzaa</u>lika waman \'<u>aa</u>qaba bimitsli m<u>aa</u> \'uuqiba bihi tsumma bughiya \'alayhi layan<u>sh</u>urannahull<u>aa</u>hu innall<u>aa</u>ha la\'afuwwun ghafuur\r\r\n');
INSERT INTO quranLatin VALUES (2656, '22:61', 22, 61, '<u>dzaa</u>lika bi-annall<u>aa</u>ha yuulijullayla fii nnah<u>aa</u>ri wayuuliju nnah<u>aa</u>ra fiillayli wa-annall<u>aa</u>ha samii\'un ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (2657, '22:62', 22, 62, '<u>dzaa</u>lika bi-annall<u>aa</u>ha huwa l<u>h</u>aqqu wa-anna m<u>aa</u> yad\'uuna min duunihi huwa lb<u>aath</u>ilu wa-annall<u>aa</u>ha huwa l\'aliyyu lkabiir\r\r\n');
INSERT INTO quranLatin VALUES (2658, '22:63', 22, 63, 'alam tara annall<u>aa</u>ha anzala mina ssam<u>aa</u>-i m<u>aa</u>-an fatu<u>sh</u>bi<u>h</u>u l-ar<u>dh</u>u mukh<u>dh</u>arratan innall<u>aa</u>ha la<u>th</u>iifun khabiir\r\r\n');
INSERT INTO quranLatin VALUES (2659, '22:64', 22, 64, 'lahu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wa-innall<u>aa</u>ha lahuwa lghaniyyu l<u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (2660, '22:65', 22, 65, 'alam tara annall<u>aa</u>ha sakhkhara lakum m<u>aa</u> fii l-ar<u>dh</u>i walfulka tajrii fii lba<u>h</u>ri bi-amrihi wayumsiku ssam<u>aa</u>-a an taqa\'a \'al<u>aa</u> l-ar<u>dh</u>i ill<u>aa</u> bi-i<u>dz</u>nihi innall<u>aa</u>ha binn<u>aa</u>si larauufun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (2661, '22:66', 22, 66, 'wahuwalla<u>dz</u>ii a<u>h</u>y<u>aa</u>kum tsumma yumiitukum tsumma yu<u>h</u>yiikum inna l-ins<u>aa</u>na lakafuur\r\r\n');
INSERT INTO quranLatin VALUES (2662, '22:67', 22, 67, 'likulli ummatin ja\'aln<u>aa</u> mansakan hum n<u>aa</u>sikuuhu fal<u>aa</u> yun<u>aa</u>zi\'unnaka fii l-amri wad\'u il<u>aa</u> rabbika innaka la\'al<u>aa</u> hudan mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (2663, '22:68', 22, 68, 'wa-in j<u>aa</u>daluuka faqulill<u>aa</u>hu a\'lamu bim<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (2664, '22:69', 22, 69, '<B>al</b>l<u>aa</u>hu ya<u>h</u>kumu baynakum yawma lqiy<u>aa</u>mati fiim<u>aa</u> kuntum fiihi takhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (2665, '22:70', 22, 70, 'alam ta\'lam annall<u>aa</u>ha ya\'lamu m<u>aa</u> fii ssam<u>aa</u>-i wal-ar<u>dh</u>i inna <u>dzaa</u>lika fii kit<u>aa</u>bin inna <u>dzaa</u>lika \'al<u>aa</u>ll<u>aa</u>hi yasiir\r\r\n');
INSERT INTO quranLatin VALUES (2666, '22:71', 22, 71, 'waya\'buduuna min duunill<u>aa</u>hi m<u>aa</u> lam yunazzil bihi sul<u>th</u><u>aa</u>nan wam<u>aa</u> laysa lahum bihi \'ilmun wam<u>aa</u> li<u>zhzhaa</u>limiina min na<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (2667, '22:72', 22, 72, 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tun<u>aa</u> bayyin<u>aa</u>tin ta\'rifu fii wujuuhilla<u>dz</u>iina kafaruu lmunkara yak<u>aa</u>duuna yas<u>th</u>uuna billa<u>dz</u>iina yatluuna \'alayhim <u>aa</u>y<u>aa</u>tin<u>aa</u> qul afa-unabbi-ukum bisyarrin min <u>dzaa</u>likum nn<u>aa</u>ru wa\'adah<u>aa</u>ll<u>aa</u>hulla<u>dz</u>iina kafaruu wabi/sa lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (2668, '22:73', 22, 73, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su <u>dh</u>uriba matsalun fastami\'uu lahu innalla<u>dz</u>iina tad\'uuna min duunill<u>aa</u>hi lan yakhluquu <u>dz</u>ub<u>aa</u>ban walawi ijtama\'uu lahu wa-in yaslubuhumu <u>dzdz</u>ub<u>aa</u>bu syay-an l<u>aa</u> yastanqi<u>dz</u>uuhu minhu <u>dh</u>a\'ufa <u>ththaa</u>libu walma<u>th</u>luub\r\r\n');
INSERT INTO quranLatin VALUES (2669, '22:74', 22, 74, 'm<u>aa</u> qadaruull<u>aa</u>ha <u>h</u>aqqa qadrihi innall<u>aa</u>ha laqawiyyun \'aziiz\r\r\n');
INSERT INTO quranLatin VALUES (2670, '22:75', 22, 75, '<B>al</b>l<u>aa</u>hu ya<u>sth</u>afii mina lmal<u>aa</u>-ikati rusulan wamina nn<u>aa</u>si innall<u>aa</u>ha samii\'un ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (2671, '22:76', 22, 76, 'ya\'lamu m<u>aa</u> bayna aydiihim wam<u>aa</u> khalfahum wa-il<u>aa</u>ll<u>aa</u>hi turja\'u l-umuur\r\r\n');
INSERT INTO quranLatin VALUES (2672, '22:77', 22, 77, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu irka\'uu wasjuduu wa\'buduu rabbakum waf\'aluu lkhayra la\'allakum tufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2673, '22:78', 22, 78, 'waj<u>aa</u>hiduu fiill<u>aa</u>hi <u>h</u>aqqa jih<u>aa</u>dihi huwa ijtab<u>aa</u>kum wam<u>aa</u> ja\'ala \'alaykum fii ddiini min <u>h</u>arajin millata abiikum ibr<u>aa</u>hiima huwa samm<u>aa</u>kumu lmuslimiina min qablu wafii h<u>aadzaa</u> liyakuuna rrasuulu syahiidan \'alaykum watakuunuu syuhad<u>aa</u>-a \'al<u>aa</u> nn<u>aa</u>si fa-aqiimuu <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tuu zzak<u>aa</u>ta wa\'ta<u>sh</u>imuu bil<u>aa</u>hi huwa mawl<u>aa</u>kum fani\'ma lmawl<u>aa</u> wani\'ma nna<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (2674, '23:1', 23, 1, 'qad afla<u>h</u>a lmu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (2675, '23:2', 23, 2, '<B>al</b>la<u>dz</u>iina hum fii <u>sh</u>al<u>aa</u>tihim kh<u>aa</u>syi\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2676, '23:3', 23, 3, 'walla<u>dz</u>iina hum \'anillaghwi mu\'ri<u>dh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2677, '23:4', 23, 4, 'walla<u>dz</u>iina hum lizzak<u>aa</u>ti f<u>aa</u>\'iluun\r\r\n');
INSERT INTO quranLatin VALUES (2678, '23:5', 23, 5, 'walla<u>dz</u>iina hum lifuruujihim <u>haa</u>fi<u>zh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2679, '23:6', 23, 6, 'ill<u>aa</u> \'al<u>aa</u> azw<u>aa</u>jihim aw m<u>aa</u> malakat aym<u>aa</u>nuhum fa-innahum ghayru maluumiin\r\r\n');
INSERT INTO quranLatin VALUES (2680, '23:7', 23, 7, 'famani ibtagh<u>aa</u> war<u>aa</u>-a <u>dzaa</u>lika faul<u>aa</u>-ika humu l\'<u>aa</u>duun\r\r\n');
INSERT INTO quranLatin VALUES (2681, '23:8', 23, 8, 'walla<u>dz</u>iina hum li-am<u>aa</u>n<u>aa</u>tihim wa\'ahdihim r<u>aa</u>\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2682, '23:9', 23, 9, 'walla<u>dz</u>iina hum \'al<u>aa</u> <u>sh</u>alaw<u>aa</u>tihim yu<u>haa</u>fi<u>zh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2683, '23:10', 23, 10, 'ul<u>aa</u>-ika humu lw<u>aa</u>ritsuun\r\r\n');
INSERT INTO quranLatin VALUES (2684, '23:11', 23, 11, '<B>al</b>la<u>dz</u>iina yaritsuuna lfirdawsa hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (2685, '23:12', 23, 12, 'walaqad khalaqn<u>aa</u> l-ins<u>aa</u>na min sul<u>aa</u>latin min <u>th</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (2686, '23:13', 23, 13, 'tsumma ja\'aln<u>aa</u>hu nu<u>th</u>fatan fii qar<u>aa</u>rin makiin\r\r\n');
INSERT INTO quranLatin VALUES (2687, '23:14', 23, 14, 'tsumma khalaqn<u>aa</u> nnu<u>th</u>fata \'alaqatan fakhalaqn<u>aa</u> l\'alaqata mu<u>dh</u>ghatan fakhalaqn<u>aa</u> lmu<u>dh</u>ghata \'i<u>zhaa</u>man fakasawn<u>aa</u> l\'i<u>zhaa</u>ma la<u>h</u>man tsumma ansya/n<u>aa</u>hu khalqan <u>aa</u>khara fatab<u>aa</u>rakall<u>aa</u>hu a<u>h</u>sanu lkh<u>aa</u>liqiin\r\r\n');
INSERT INTO quranLatin VALUES (2688, '23:15', 23, 15, 'tsumma innakum ba\'da <u>dzaa</u>lika lamayyituun\r\r\n');
INSERT INTO quranLatin VALUES (2689, '23:16', 23, 16, 'tsumma innakum yawma lqiy<u>aa</u>mati tub\'atsuun\r\r\n');
INSERT INTO quranLatin VALUES (2690, '23:17', 23, 17, 'walaqad khalaqn<u>aa</u> fawqakum sab\'a <u>th</u>ar<u>aa</u>-iqa wam<u>aa</u> kunn<u>aa</u> \'ani lkhalqi gh<u>aa</u>filiin\r\r\n');
INSERT INTO quranLatin VALUES (2691, '23:18', 23, 18, 'wa-anzaln<u>aa</u> mina ssam<u>aa</u>-i m<u>aa</u>-an biqadarin fa-askann<u>aa</u>hu fii l-ar<u>dh</u>i wa-inn<u>aa</u> \'al<u>aa</u> <u>dz</u>ah<u>aa</u>bin bihi laq<u>aa</u>diruun\r\r\n');
INSERT INTO quranLatin VALUES (2692, '23:19', 23, 19, 'fa-ansya/n<u>aa</u> lakum bihi jann<u>aa</u>tin min nakhiilin wa-a\'n<u>aa</u>bin lakum fiih<u>aa</u> faw<u>aa</u>kihu katsiiratun waminh<u>aa</u> ta/kuluun\r\r\n');
INSERT INTO quranLatin VALUES (2693, '23:20', 23, 20, 'wasyajaratan takhruju min <u>th</u>uuri sayn<u>aa</u>-a tanbutu bidduhni wa<u>sh</u>ibghin lil<u>aa</u>kiliin\r\r\n');
INSERT INTO quranLatin VALUES (2694, '23:21', 23, 21, 'wa-inna lakum fii l-an\'<u>aa</u>mi la\'ibratan nusqiikum mimm<u>aa</u> fii bu<u>th</u>uunih<u>aa</u> walakum fiih<u>aa</u> man<u>aa</u>fi\'u katsiiratun waminh<u>aa</u> ta/kuluun\r\r\n');
INSERT INTO quranLatin VALUES (2695, '23:22', 23, 22, 'wa\'alayh<u>aa</u> wa\'al<u>aa</u> lfulki tu<u>h</u>maluun\r\r\n');
INSERT INTO quranLatin VALUES (2696, '23:23', 23, 23, 'walaqad arsaln<u>aa</u> nuu<u>h</u>an il<u>aa</u> qawmihi faq<u>aa</u>la y<u>aa</u> qawmi u\'buduull<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu afal<u>aa</u> tattaquun\r\r\n');
INSERT INTO quranLatin VALUES (2697, '23:24', 23, 24, 'faq<u>aa</u>la lmalaulla<u>dz</u>iina kafaruu min qawmihi m<u>aa</u> h<u>aadzaa</u> ill<u>aa</u> basyarun mitslukum yuriidu an yatafa<u>dhdh</u>ala \'alaykum walaw sy<u>aa</u>-all<u>aa</u>hu la-anzala mal<u>aa</u>-ikatan m<u>aa</u> sami\'n<u>aa</u> bih<u>aadzaa</u> fii <u>aa</u>b<u>aa</u>-in<u>aa</u> l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (2698, '23:25', 23, 25, 'in huwa ill<u>aa</u> rajulun bihi jinnatun fatarabba<u>sh</u>uu bihi <u>h</u>att<u>aa</u> <u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (2699, '23:26', 23, 26, 'q<u>aa</u>la rabbi un<u>sh</u>urnii bim<u>aa</u> ka<u>dzdz</u>abuun\r\r\n');
INSERT INTO quranLatin VALUES (2700, '23:27', 23, 27, 'fa-aw<u>h</u>ayn<u>aa</u> ilayhi ani i<u>sh</u>na\'i lfulka bi-a\'yunin<u>aa</u> wawa<u>h</u>yin<u>aa</u> fa-i<u>dzaa</u> j<u>aa</u>-a amrun<u>aa</u> waf<u>aa</u>ra ttannuuru fasluk fiih<u>aa</u> min kullin zawjayni itsnayni wa-ahlaka ill<u>aa</u> man sabaqa \'alayhi lqawlu minhum wal<u>aa</u> tukh<u>aath</u>ibnii fiilla<u>dz</u>iina <u>zh</u>alamuu innahum mughraquun\r\r\n');
INSERT INTO quranLatin VALUES (2701, '23:28', 23, 28, 'fa-i<u>dzaa</u> istawayta anta waman ma\'aka \'al<u>aa</u> lfulki faquli l<u>h</u>amdu lill<u>aa</u>hilla<u>dz</u>ii najj<u>aa</u>n<u>aa</u> mina lqawmi <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2702, '23:29', 23, 29, 'waqul rabbi anzilnii munzalan mub<u>aa</u>rakan wa-anta khayru lmunziliin\r\r\n');
INSERT INTO quranLatin VALUES (2703, '23:30', 23, 30, 'inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin wa-in kunn<u>aa</u> lamubtaliin\r\r\n');
INSERT INTO quranLatin VALUES (2704, '23:31', 23, 31, 'tsumma ansya/n<u>aa</u> min ba\'dihim qarnan <u>aa</u>khariin\r\r\n');
INSERT INTO quranLatin VALUES (2705, '23:32', 23, 32, 'fa-arsaln<u>aa</u> fiihim rasuulan minhum ani u\'buduull<u>aa</u>ha m<u>aa</u> lakum min il<u>aa</u>hin ghayruhu afal<u>aa</u> tattaquun\r\r\n');
INSERT INTO quranLatin VALUES (2706, '23:33', 23, 33, 'waq<u>aa</u>la lmalau min qawmihilla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu biliq<u>aa</u>-i l-<u>aa</u>khirati wa-atrafn<u>aa</u>hum fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> m<u>aa</u> h<u>aadzaa</u> ill<u>aa</u> basyarun mitslukum ya/kulu mimm<u>aa</u> ta/kuluuna minhu wayasyrabu mimm<u>aa</u> tasyrabuun\r\r\n');
INSERT INTO quranLatin VALUES (2707, '23:34', 23, 34, 'wala-in a<u>th</u>a\'tum basyaran mitslakum innakum i<u>dz</u>an lakh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (2708, '23:35', 23, 35, 'aya\'idukum annakum i<u>dzaa</u> mittum wakuntum tur<u>aa</u>ban wa\'i<u>zhaa</u>man annakum mukhrajuun\r\r\n');
INSERT INTO quranLatin VALUES (2709, '23:36', 23, 36, 'hayh<u>aa</u>ta hayh<u>aa</u>ta lim<u>aa</u> tuu\'aduun\r\r\n');
INSERT INTO quranLatin VALUES (2710, '23:37', 23, 37, 'in hiya ill<u>aa</u> <u>h</u>ay<u>aa</u>tun<u>aa</u> dduny<u>aa</u> namuutu wana<u>h</u>y<u>aa</u> wam<u>aa</u> na<u>h</u>nu bimab\'uutsiin\r\r\n');
INSERT INTO quranLatin VALUES (2711, '23:38', 23, 38, 'in huwa ill<u>aa</u> rajulun iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>iban wam<u>aa</u> na<u>h</u>nu lahu bimu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (2712, '23:39', 23, 39, 'q<u>aa</u>la rabbi un<u>sh</u>urnii bim<u>aa</u> ka<u>dzdz</u>abuun\r\r\n');
INSERT INTO quranLatin VALUES (2713, '23:40', 23, 40, 'q<u>aa</u>la \'amm<u>aa</u> qaliilin layu<u>sh</u>bi<u>h</u>unna n<u>aa</u>dimiin\r\r\n');
INSERT INTO quranLatin VALUES (2714, '23:41', 23, 41, 'fa-akha<u>dz</u>at-humu <u>shsh</u>ay<u>h</u>atu bil<u>h</u>aqqi faja\'aln<u>aa</u>hum ghuts<u>aa</u>-an fabu\'dan lilqawmi <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2715, '23:42', 23, 42, 'tsumma ansya/n<u>aa</u> min ba\'dihim quruunan <u>aa</u>khariin\r\r\n');
INSERT INTO quranLatin VALUES (2716, '23:43', 23, 43, 'm<u>aa</u> tasbiqu min ummatin ajalah<u>aa</u> wam<u>aa</u> yasta/khiruun\r\r\n');
INSERT INTO quranLatin VALUES (2717, '23:44', 23, 44, 'tsumma arsaln<u>aa</u> rusulan<u>aa</u> tatr<u>aa</u> kulla m<u>aa</u> j<u>aa</u>-a ummatan rasuuluh<u>aa</u> ka<u>dzdz</u>abuuhu fa-atba\'n<u>aa</u> ba\'<u>dh</u>ahum ba\'<u>dh</u>an waja\'aln<u>aa</u>hum a<u>haa</u>diitsa fabu\'dan liqawmin l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (2718, '23:45', 23, 45, 'tsumma arsaln<u>aa</u> muus<u>aa</u> wa-akh<u>aa</u>hu h<u>aa</u>ruuna bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wasul<u>th</u><u>aa</u>nin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (2719, '23:46', 23, 46, 'il<u>aa</u> fir\'awna wamala-ihi fastakbaruu wak<u>aa</u>nuu qawman \'<u>aa</u>liin\r\r\n');
INSERT INTO quranLatin VALUES (2720, '23:47', 23, 47, 'faq<u>aa</u>luu anu/minu libasyarayni mitslin<u>aa</u> waqawmuhum<u>aa</u> lan<u>aa</u> \'<u>aa</u>biduun\r\r\n');
INSERT INTO quranLatin VALUES (2721, '23:48', 23, 48, 'faka<u>dzdz</u>abuuhum<u>aa</u> fak<u>aa</u>nuu mina lmuhlakiin\r\r\n');
INSERT INTO quranLatin VALUES (2722, '23:49', 23, 49, 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> lkit<u>aa</u>ba la\'allahum yahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (2723, '23:50', 23, 50, 'waja\'aln<u>aa</u> ibna maryama waummahu <u>aa</u>yatan wa<u>aa</u>wayn<u>aa</u>hum<u>aa</u> il<u>aa</u> rabwatin <u>dzaa</u>ti qar<u>aa</u>rin wama\'iin\r\r\n');
INSERT INTO quranLatin VALUES (2724, '23:51', 23, 51, 'y<u>aa</u> ayyuh<u>aa</u> rrusulu kuluu mina <u>ththh</u>ayyib<u>aa</u>ti wa\'maluu <u>shaa</u>li<u>h</u>an innii bim<u>aa</u> ta\'maluuna \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (2725, '23:52', 23, 52, 'wa-inna h<u>aadz</u>ihi ummatukum ummatan w<u>aah</u>idatan wa-an<u>aa</u> rabbukum fattaquun\r\r\n');
INSERT INTO quranLatin VALUES (2726, '23:53', 23, 53, 'fataqa<u>ththh</u>a\'uu amrahum baynahum zuburan kullu <u>h</u>izbin bim<u>aa</u> ladayhim fari<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2727, '23:54', 23, 54, 'fa<u>dz</u>arhum fii ghamratihim <u>h</u>att<u>aa</u> <u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (2728, '23:55', 23, 55, 'aya<u>h</u>sabuuna annam<u>aa</u> numidduhum bihi min m<u>aa</u>lin wabaniin\r\r\n');
INSERT INTO quranLatin VALUES (2729, '23:56', 23, 56, 'nus<u>aa</u>ri\'u lahum fii lkhayr<u>aa</u>ti bal l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (2730, '23:57', 23, 57, 'innalla<u>dz</u>iina hum min khasyyati rabbihim musyfiquun\r\r\n');
INSERT INTO quranLatin VALUES (2731, '23:58', 23, 58, 'walla<u>dz</u>iina hum bi-<u>aa</u>y<u>aa</u>ti rabbihim yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (2732, '23:59', 23, 59, 'walla<u>dz</u>iina hum birabbihim l<u>aa</u> yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (2733, '23:60', 23, 60, 'walla<u>dz</u>iina yu/tuuna m<u>aa</u> <u>aa</u>taw waquluubuhum wajilatun annahum il<u>aa</u> rabbihim r<u>aa</u>ji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2734, '23:61', 23, 61, 'ul<u>aa</u>-ika yus<u>aa</u>ri\'uuna fii lkhayr<u>aa</u>ti wahum lah<u>aa</u> s<u>aa</u>biquun\r\r\n');
INSERT INTO quranLatin VALUES (2735, '23:62', 23, 62, 'wal<u>aa</u> nukallifu nafsan ill<u>aa</u> wus\'ah<u>aa</u> waladayn<u>aa</u> kit<u>aa</u>bun yan<u>th</u>iqu bil<u>h</u>aqqi wahum l<u>aa</u> yu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (2736, '23:63', 23, 63, 'bal quluubuhum fii ghamratin min h<u>aadzaa</u> walahum a\'m<u>aa</u>lun min duuni <u>dzaa</u>lika hum lah<u>aa</u> \'<u>aa</u>miluun\r\r\n');
INSERT INTO quranLatin VALUES (2737, '23:64', 23, 64, '<u>h</u>att<u>aa</u> i<u>dzaa</u> akha<u>dz</u>n<u>aa</u> mutrafiihim bil\'a<u>dzaa</u>bi i<u>dzaa</u> hum yaj-aruun\r\r\n');
INSERT INTO quranLatin VALUES (2738, '23:65', 23, 65, 'l<u>aa</u> taj-aruu lyawma innakum minn<u>aa</u> l<u>aa</u> tun<u>sh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (2739, '23:66', 23, 66, 'qad k<u>aa</u>nat <u>aa</u>y<u>aa</u>tii tutl<u>aa</u> \'alaykum fakuntum \'al<u>aa</u> a\'q<u>aa</u>bikum tanki<u>sh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2740, '23:67', 23, 67, 'mustakbiriina bihi s<u>aa</u>miran tahjuruun\r\r\n');
INSERT INTO quranLatin VALUES (2741, '23:68', 23, 68, 'afalam yaddabbaruu lqawla am j<u>aa</u>-ahum m<u>aa</u> lam ya/ti <u>aa</u>b<u>aa</u>-ahumu l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (2742, '23:69', 23, 69, 'am lam ya\'rifuu rasuulahum fahum lahu munkiruun\r\r\n');
INSERT INTO quranLatin VALUES (2743, '23:70', 23, 70, 'am yaquuluuna bihi jinnatun bal j<u>aa</u>-ahum bil<u>h</u>aqqi wa-aktsaruhum lil<u>h</u>aqqi k<u>aa</u>rihuun\r\r\n');
INSERT INTO quranLatin VALUES (2744, '23:71', 23, 71, 'walawi ittaba\'a l<u>h</u>aqqu ahw<u>aa</u>-ahum lafasadati ssam<u>aa</u>w<u>aa</u>tu wal-ar<u>dh</u>u waman fiihinna bal atayn<u>aa</u>hum bi<u>dz</u>ikrihim fahum \'an <u>dz</u>ikrihim mu\'ri<u>dh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2745, '23:72', 23, 72, 'am tas-aluhum kharjan fakhar<u>aa</u>ju rabbika khayrun wahuwa khayru rr<u>aa</u>ziqiin\r\r\n');
INSERT INTO quranLatin VALUES (2746, '23:73', 23, 73, 'wa-innaka latad\'uuhum il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (2747, '23:74', 23, 74, 'wa-innalla<u>dz</u>iina l<u>aa</u> yu/minuuna bil-<u>aa</u>khirati \'ani <u>shsh</u>ir<u>aath</u>i lan<u>aa</u>kibuun\r\r\n');
INSERT INTO quranLatin VALUES (2748, '23:75', 23, 75, 'walaw ra<u>h</u>imn<u>aa</u>hum wakasyafn<u>aa</u> m<u>aa</u> bihim min <u>dh</u>urrin lalajjuu fii <u>th</u>ughy<u>aa</u>nihim ya\'mahuun\r\r\n');
INSERT INTO quranLatin VALUES (2749, '23:76', 23, 76, 'walaqad akha<u>dz</u>n<u>aa</u>hum bil\'a<u>dzaa</u>bi fam<u>aa</u> istak<u>aa</u>nuu lirabbihim wam<u>aa</u> yata<u>dh</u>arra\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2750, '23:77', 23, 77, '<u>h</u>att<u>aa</u> i<u>dzaa</u> fata<u>h</u>n<u>aa</u> \'alayhim b<u>aa</u>ban <u>dzaa</u> \'a<u>dzaa</u>bin syadiidin i<u>dzaa</u> hum fiihi mublisuun\r\r\n');
INSERT INTO quranLatin VALUES (2751, '23:78', 23, 78, 'wahuwalla<u>dz</u>ii ansya-a lakumu ssam\'a wal-ab<u>shaa</u>ra wal-af-idata qaliilan m<u>aa</u> tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (2752, '23:79', 23, 79, 'wahuwalla<u>dz</u>ii <u>dz</u>ara-akum fii l-ar<u>dh</u>i wa-ilayhi tu<u>h</u>syaruun\r\r\n');
INSERT INTO quranLatin VALUES (2753, '23:80', 23, 80, 'wahuwalla<u>dz</u>ii yu<u>h</u>yii wayumiitu walahu ikhtil<u>aa</u>fullayli wannah<u>aa</u>ri afal<u>aa</u> ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (2754, '23:81', 23, 81, 'bal q<u>aa</u>luu mitsla m<u>aa</u> q<u>aa</u>la l-awwaluun\r\r\n');
INSERT INTO quranLatin VALUES (2755, '23:82', 23, 82, 'q<u>aa</u>luu a-i<u>dzaa</u> mitn<u>aa</u> wakunn<u>aa</u> tur<u>aa</u>ban wa\'i<u>zhaa</u>man a-inn<u>aa</u> lamab\'uutsuun\r\r\n');
INSERT INTO quranLatin VALUES (2756, '23:83', 23, 83, 'laqad wu\'idn<u>aa</u> na<u>h</u>nu wa<u>aa</u>b<u>aa</u>un<u>aa</u> h<u>aadzaa</u> min qablu in h<u>aadzaa</u> ill<u>aa</u> as<u>aath</u>iiru l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (2757, '23:84', 23, 84, 'qul limani l-ar<u>dh</u>u waman fiih<u>aa</u> in kuntum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (2758, '23:85', 23, 85, 'sayaquuluuna lill<u>aa</u>hi qul afal<u>aa</u> ta<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (2759, '23:86', 23, 86, 'qul man rabbu ssam<u>aa</u>w<u>aa</u>ti ssab\'i warabbu l\'arsyi l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (2760, '23:87', 23, 87, 'sayaquuluuna lill<u>aa</u>hi qul afal<u>aa</u> tattaquun\r\r\n');
INSERT INTO quranLatin VALUES (2761, '23:88', 23, 88, 'qul man biyadihi malakuutu kulli syay-in wahuwa yujiiru wal<u>aa</u> yuj<u>aa</u>ru \'alayhi in kuntum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (2762, '23:89', 23, 89, 'sayaquuluuna lill<u>aa</u>hi qul fa-ann<u>aa</u> tus<u>h</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (2763, '23:90', 23, 90, 'bal atayn<u>aa</u>hum bil<u>h</u>aqqi wa-innahum lak<u>aadz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (2764, '23:91', 23, 91, 'm<u>aa</u> ittakha<u>dz</u>all<u>aa</u>hu min waladin wam<u>aa</u> k<u>aa</u>na ma\'ahu min il<u>aa</u>hin i<u>dz</u>an la<u>dz</u>ahaba kullu il<u>aa</u>hin bim<u>aa</u> khalaqa wala\'al<u>aa</u> ba\'<u>dh</u>uhum \'al<u>aa</u> ba\'<u>dh</u>in sub<u>haa</u>nall<u>aa</u>hi \'amm<u>aa</u> ya<u>sh</u>ifuun\r\r\n');
INSERT INTO quranLatin VALUES (2765, '23:92', 23, 92, '\'<u>aa</u>limi lghaybi wasysyah<u>aa</u>dati fata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (2766, '23:93', 23, 93, 'qul rabbi imm<u>aa</u> turiyannii m<u>aa</u> yuu\'aduun\r\r\n');
INSERT INTO quranLatin VALUES (2767, '23:94', 23, 94, 'rabbi fal<u>aa</u> taj\'alnii fii lqawmi <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2768, '23:95', 23, 95, 'wa-inn<u>aa</u> \'al<u>aa</u> an nuriyaka m<u>aa</u> na\'iduhum laq<u>aa</u>diruun\r\r\n');
INSERT INTO quranLatin VALUES (2769, '23:96', 23, 96, 'idfa\'billatii hiya a<u>h</u>sanu ssayyi-ata na<u>h</u>nu a\'lamu bim<u>aa</u> ya<u>sh</u>ifuun\r\r\n');
INSERT INTO quranLatin VALUES (2770, '23:97', 23, 97, 'waqul rabbi a\'uu<u>dz</u>u bika min hamaz<u>aa</u>ti sysyay<u>aath</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (2771, '23:98', 23, 98, 'wa-a\'uu<u>dz</u>u bika rabbi an ya<u>hd</u>uruun\r\r\n');
INSERT INTO quranLatin VALUES (2772, '23:99', 23, 99, '<u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-a a<u>h</u>adahumu lmawtu q<u>aa</u>la rabbi irji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2773, '23:100', 23, 100, 'la\'allii a\'malu <u>shaa</u>li<u>h</u>an fiim<u>aa</u> taraktu kall<u>aa</u> innah<u>aa</u> kalimatun huwa q<u>aa</u>-iluh<u>aa</u> wamin war<u>aa</u>-ihim barzakhun il<u>aa</u> yawmi yub\'atsuun\r\r\n');
INSERT INTO quranLatin VALUES (2774, '23:101', 23, 101, 'fa-i<u>dzaa</u> nufikha fii <u>shsh</u>uuri fal<u>aa</u> ans<u>aa</u>ba baynahum yawma-i<u>dz</u>in wal<u>aa</u> yatas<u>aa</u>-aluun<b>a </b>\r\r\n');
INSERT INTO quranLatin VALUES (2775, '23:102', 23, 102, 'faman tsaqulat maw<u>aa</u>ziinuhu faul<u>aa</u>-ika humu lmufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2776, '23:103', 23, 103, 'waman khaffat maw<u>aa</u>ziinuhu faul<u>aa</u>-ikalla<u>dz</u>iina khasiruu anfusahum fii jahannama kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (2777, '23:104', 23, 104, 'talfa<u>h</u>u wujuuhahumu nn<u>aa</u>ru wahum fiih<u>aa</u> k<u>aa</u>li<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (2778, '23:105', 23, 105, 'alam takun <u>aa</u>y<u>aa</u>tii tutl<u>aa</u> \'alaykum fakuntum bih<u>aa</u> tuka<u>dzdz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (2779, '23:106', 23, 106, 'q<u>aa</u>luu rabban<u>aa</u> ghalabat \'alayn<u>aa</u> syiqwatun<u>aa</u> wakunn<u>aa</u> qawman <u>daa</u>lliin\r\r\n');
INSERT INTO quranLatin VALUES (2780, '23:107', 23, 107, 'rabban<u>aa</u> akhrijn<u>aa</u> minh<u>aa</u> fa-in \'udn<u>aa</u> fa-inn<u>aa</u> <u>zhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (2781, '23:108', 23, 108, 'q<u>aa</u>la ikhsauu fiih<u>aa</u> wal<u>aa</u> tukallimuun\r\r\n');
INSERT INTO quranLatin VALUES (2782, '23:109', 23, 109, 'innahu k<u>aa</u>na fariiqun min \'ib<u>aa</u>dii yaquuluuna rabban<u>aa</u> <u>aa</u>mann<u>aa</u> faghfir lan<u>aa</u> war<u>h</u>amn<u>aa</u> wa-anta khayru rr<u>aah</u>imiin\r\r\n');
INSERT INTO quranLatin VALUES (2783, '23:110', 23, 110, 'fattakha<u>dz</u>tumuuhum sikhriyyan <u>h</u>att<u>aa</u> ansawkum <u>dz</u>ikrii wakuntum minhum ta<u>dh</u>akuun\r\r\n');
INSERT INTO quranLatin VALUES (2784, '23:111', 23, 111, 'innii jazaytuhumu lyawma bim<u>aa</u> <u>sh</u>abaruu annahum humu lf<u>aa</u>-izuun\r\r\n');
INSERT INTO quranLatin VALUES (2785, '23:112', 23, 112, 'q<u>aa</u>la kam labitstum fii l-ar<u>dh</u>i \'adada siniin\r\r\n');
INSERT INTO quranLatin VALUES (2786, '23:113', 23, 113, 'q<u>aa</u>luu labitsn<u>aa</u> yawman aw ba\'<u>dh</u>a yawmin fas-ali l\'<u>aa</u>ddiin\r\r\n');
INSERT INTO quranLatin VALUES (2787, '23:114', 23, 114, 'q<u>aa</u>la in labitstum ill<u>aa</u> qaliilan law annakum kuntum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (2788, '23:115', 23, 115, 'afa<u>h</u>asibtum annam<u>aa</u> khalaqn<u>aa</u>kum \'abatsan wa-annakum ilayn<u>aa</u> l<u>aa</u> turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2789, '23:116', 23, 116, 'fata\'<u>aa</u>l<u>aa</u>ll<u>aa</u>hu lmaliku l<u>h</u>aqqu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa rabbu l\'arsyi lkariim\r\r\n');
INSERT INTO quranLatin VALUES (2790, '23:117', 23, 117, 'waman yad\'u ma\'all<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara l<u>aa</u> burh<u>aa</u>na lahu bihi fa-innam<u>aa</u> <u>h</u>is<u>aa</u>buhu \'inda rabbihi innahu l<u>aa</u> yufli<u>h</u>u lk<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (2791, '23:118', 23, 118, 'waqul rabbi ighfir war<u>h</u>am wa-anta khayru rr<u>aah</u>imiin\r\r\n');
INSERT INTO quranLatin VALUES (2792, '24:1', 24, 1, 'suuratun anzaln<u>aa</u>h<u>aa</u> wafara<u>dh</u>n<u>aa</u>h<u>aa</u> wa-anzaln<u>aa</u> fiih<u>aa</u> <u>aa</u>y<u>aa</u>tin bayyin<u>aa</u>tin la\'allakum ta<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (2793, '24:2', 24, 2, 'azz<u>aa</u>niyatu wazz<u>aa</u>nii fajliduu kulla w<u>aah</u>idin minhum<u>aa</u> mi-ata jaldatin wal<u>aa</u> ta/khu<u>dz</u>kum bihim<u>aa</u> ra/fatun fii diinill<u>aa</u>hi in kuntum tu/minuuna bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri walyasyhad \'a<u>dzaa</u>bahum<u>aa</u> <u>th</u><u>aa</u>-ifatun mina lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (2794, '24:3', 24, 3, 'azz<u>aa</u>nii l<u>aa</u> yanki<u>h</u>u ill<u>aa</u> z<u>aa</u>niyatan aw musyrikatan wazz<u>aa</u>niyatu l<u>aa</u> yanki<u>h</u>uh<u>aa</u> ill<u>aa</u> z<u>aa</u>nin aw musyrikun wa<u>h</u>urrima <u>dzaa</u>lika \'al<u>aa</u> lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (2795, '24:4', 24, 4, 'walla<u>dz</u>iina yarmuuna lmu<u>hs</u>an<u>aa</u>ti tsumma lam ya/tuu bi-arba\'ati syuhad<u>aa</u>-a fajliduuhum tsam<u>aa</u>niina jaldatan wal<u>aa</u> taqbaluu lahum syah<u>aa</u>datan abadan waul<u>aa</u>-ika humu lf<u>aa</u>siquun\r\r\n');
