INSERT INTO quranLatin VALUES (2844, '24:53', 24, 53, 'wa-aqsamuu bil<u>aa</u>hi jahda aym<u>aa</u>nihim la-in amartahum layakhrujunna qul l<u>aa</u> tuqsimuu <u>th</u><u>aa</u>\'atun ma\'ruufatun innall<u>aa</u>ha khabiirun bim<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (2845, '24:54', 24, 54, 'qul a<u>th</u>ii\'uull<u>aa</u>ha wa-a<u>th</u>ii\'uu rrasuula fa-in tawallaw fa-innam<u>aa</u> \'alayhi m<u>aa</u> <u>h</u>ummila wa\'alaykum m<u>aa</u> <u>h</u>ummiltum wa-in tu<u>th</u>ii\'uuhu tahtaduu wam<u>aa</u> \'al<u>aa</u> rrasuuli ill<u>aa</u> lbal<u>aa</u>ghu lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (2846, '24:55', 24, 55, 'wa\'adall<u>aa</u>hulla<u>dz</u>iina <u>aa</u>manuu minkum wa\'amiluu <u>shshaa</u>li<u>haa</u>ti layastakhlifannahum fii l-ar<u>dh</u>i kam<u>aa</u> istakhlafalla<u>dz</u>iina min qablihim walayumakkinanna lahum diinahumulla<u>dz</u>ii irta<u>daa</u> lahum walayubaddilannahum min ba\'di khawfihim amnan ya\'buduunanii l<u>aa</u> yusyrikuuna bii syay-an waman kafara ba\'da <u>dzaa</u>lika faul<u>aa</u>-ika humu lf<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (2847, '24:56', 24, 56, 'wa-aqiimuu <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tuu zzak<u>aa</u>ta wa-a<u>th</u>ii\'uu rrasuula la\'allakum tur<u>h</u>amuun\r\r\n');
INSERT INTO quranLatin VALUES (2848, '24:57', 24, 57, 'l<u>aa</u> ta<u>h</u>sabannalla<u>dz</u>iina kafaruu mu\'jiziina fii l-ar<u>dh</u>i wama/w<u>aa</u>humu nn<u>aa</u>ru walabi/sa lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (2849, '24:58', 24, 58, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu liyasta/<u>dz</u>inkumulla<u>dz</u>iina malakat aym<u>aa</u>nukum walla<u>dz</u>iina lam yablughuu l<u>h</u>uluma minkum tsal<u>aa</u>tsa marr<u>aa</u>tin min qabli <u>sh</u>al<u>aa</u>ti lfajri wa<u>h</u>iina ta<u>dh</u>a\'uuna tsiy<u>aa</u>bakum mina <u>zhzh</u>ahiirati wamin ba\'di <u>sh</u>al<u>aa</u>ti l\'isy<u>aa</u>-i tsal<u>aa</u>tsu \'awr<u>aa</u>tin lakum laysa \'alaykum wal<u>aa</u> \'alayhim jun<u>aah</u>un ba\'dahunna <u>th</u>aww<u>aa</u>fuuna \'alaykum ba\'<u>dh</u>ukum \'al<u>aa</u> ba\'<u>dh</u>in ka<u>dzaa</u>lika yubayyinull<u>aa</u>hu lakumu l-<u>aa</u>y<u>aa</u>ti wal<u>aa</u>hu \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (2850, '24:59', 24, 59, 'wa-i<u>dzaa</u> balagha l-a<u>th</u>f<u>aa</u>lu minkumu l<u>h</u>uluma falyasta/<u>dz</u>inuu kam<u>aa</u> ista/<u>dz</u>analla<u>dz</u>iina min qablihim ka<u>dzaa</u>lika yubayyinull<u>aa</u>hu lakum <u>aa</u>y<u>aa</u>tihi wal<u>aa</u>hu \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (2851, '24:60', 24, 60, 'walqaw<u>aa</u>\'idu mina nnis<u>aa</u>-ill<u>aa</u>tii l<u>aa</u> yarjuuna nik<u>aah</u>an falaysa \'alayhinna jun<u>aah</u>un an ya<u>dh</u>a\'na tsiy<u>aa</u>bahunna ghayra mutabarrij<u>aa</u>tin biziinatin wa-an yasta\'fifna khayrun lahunna wal<u>aa</u>hu samii\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (2852, '24:61', 24, 61, 'laysa \'al<u>aa</u> l-a\'m<u>aa</u> <u>h</u>arajun wal<u>aa</u> \'al<u>aa</u> l-a\'raji <u>h</u>arajun wal<u>aa</u> \'al<u>aa</u> lmarii<u>dh</u>i <u>h</u>arajun wal<u>aa</u> \'al<u>aa</u> anfusikum an ta/kuluu min buyuutikum aw buyuuti <u>aa</u>b<u>aa</u>-ikum aw buyuuti ummah<u>aa</u>tikum aw buyuuti ikhw<u>aa</u>nikum aw buyuuti akhaw<u>aa</u>tikum aw buyuuti a\'m<u>aa</u>mikum aw buyuuti \'amm<u>aa</u>tikum aw buyuuti akhw<u>aa</u>likum aw buyuuti kh<u>aa</u>l<u>aa</u>tikum aw m<u>aa</u> malaktum maf<u>aa</u>ti<u>h</u>ahu aw <u>sh</u>adiiqikum laysa \'alaykum jun<u>aah</u>un an ta/kuluu jamii\'an aw asyt<u>aa</u>tan fa-i<u>dzaa</u> dakhaltum buyuutan fasallimuu \'al<u>aa</u> anfusikum ta<u>h</u>iyyatan min \'indill<u>aa</u>hi mub<u>aa</u>rakatan <u>th</u>ayyibatan ka<u>dzaa</u>lika yubayyinull<u>aa</u>hu lakumu l-<u>aa</u>y<u>aa</u>ti la\'allakum ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (2853, '24:62', 24, 62, 'innam<u>aa</u> lmu/minuunalla<u>dz</u>iina <u>aa</u>manuu bil<u>aa</u>hi warasuulihi wa-i<u>dzaa</u> k<u>aa</u>nuu ma\'ahu \'al<u>aa</u> amrin j<u>aa</u>mi\'in lam ya<u>dz</u>habuu <u>h</u>att<u>aa</u> yasta/<u>dz</u>inuuhu innalla<u>dz</u>iina yasta/<u>dz</u>inuunaka ul<u>aa</u>-ikalla<u>dz</u>iina yu/minuuna bil<u>aa</u>hi warasuulihi fa-i<u>dzaa</u> ista/<u>dz</u>anuuka liba\'<u>dh</u>i sya/nihim fa/<u>dz</u>an liman syi/ta minhum wastaghfir lahumull<u>aa</u>ha innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (2854, '24:63', 24, 63, 'l<u>aa</u> taj\'aluu du\'<u>aa</u>-a rrasuuli baynakum kadu\'<u>aa</u>-i ba\'<u>dh</u>ikum ba\'<u>dh</u>an qad ya\'lamull<u>aa</u>hulla<u>dz</u>iina yatasallaluuna minkum liw<u>aadz</u>an falya<u>hts</u>arilla<u>dz</u>iina yukh<u>aa</u>lifuuna \'an amrihi an tu<u>sh</u>iibahum fitnatun aw yu<u>sh</u>iibahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (2855, '24:64', 24, 64, 'al<u>aa</u> inna lill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i qad ya\'lamu m<u>aa</u> antum \'alayhi wayawma yurja\'uuna ilayhi fayunabbi-uhum bim<u>aa</u> \'amiluu wal<u>aa</u>hu bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (2856, '25:1', 25, 1, 'tab<u>aa</u>rakalla<u>dz</u>ii nazzala lfurq<u>aa</u>na \'al<u>aa</u> \'abdihi liyakuuna lil\'<u>aa</u>lamiina na<u>dz</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2857, '25:2', 25, 2, '<B>al</b>la<u>dz</u>ii lahu mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i walam yattakhi<u>dz</u> waladan walam yakun lahu syariikun fii lmulki wakhalaqa kulla syay-in faqaddarahu taqdiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2858, '25:3', 25, 3, 'wattakha<u>dz</u>uu min duunihi <u>aa</u>lihatan l<u>aa</u> yakhluquuna syay-an wahum yukhlaquuna wal<u>aa</u> yamlikuuna li-anfusihim <u>dh</u>arran wal<u>aa</u> naf\'an wal<u>aa</u> yamlikuuna mawtan wal<u>aa</u> <u>h</u>ay<u>aa</u>tan wal<u>aa</u> nusyuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2859, '25:4', 25, 4, 'waq<u>aa</u>lalla<u>dz</u>iina kafaruu in h<u>aadzaa</u> ill<u>aa</u> ifkun iftar<u>aa</u>hu wa-a\'<u>aa</u>nahu \'alayhi qawmun <u>aa</u>kharuuna faqad j<u>aa</u>uu <u>zh</u>ulman wazuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2860, '25:5', 25, 5, 'waq<u>aa</u>luu as<u>aath</u>iiru l-awwaliina iktatabah<u>aa</u> fahiya tuml<u>aa</u> \'alayhi bukratan wa-a<u>sh</u>iil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2861, '25:6', 25, 6, 'qul anzalahulla<u>dz</u>ii ya\'lamu ssirra fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i innahu k<u>aa</u>na ghafuuran ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2862, '25:7', 25, 7, 'waq<u>aa</u>luu m<u>aa</u>li h<u>aadzaa</u> rrasuuli ya/kulu <u>ththh</u>a\'<u>aa</u>ma wayamsyii fii l-asw<u>aa</u>qi lawl<u>aa</u> unzila ilayhi malakun fayakuuna ma\'ahu na<u>dz</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2863, '25:8', 25, 8, 'aw yulq<u>aa</u> ilayhi kanzun aw takuunu lahu jannatun ya/kulu minh<u>aa</u> waq<u>aa</u>la <u>zhzhaa</u>limuuna in tattabi\'uuna ill<u>aa</u> rajulan mas<u>h</u>uur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2864, '25:9', 25, 9, 'un<u>zh</u>ur kayfa <u>dh</u>arabuu laka l-amts<u>aa</u>la fa<u>dh</u>alluu fal<u>aa</u> yasta<u>th</u>ii\'uuna sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2865, '25:10', 25, 10, 'tab<u>aa</u>rakalla<u>dz</u>ii in sy<u>aa</u>-a ja\'ala laka khayran min <u>dzaa</u>lika jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru wayaj\'al laka qu<u>sh</u>uur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2866, '25:11', 25, 11, 'bal ka<u>dzdz</u>abuu biss<u>aa</u>\'ati wa-a\'tadn<u>aa</u> liman ka<u>dzdz</u>aba biss<u>aa</u>\'ati sa\'iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2867, '25:12', 25, 12, 'i<u>dzaa</u> ra-at-hum min mak<u>aa</u>nin ba\'iidin sami\'uu lah<u>aa</u> taghayyu<u>zh</u>an wazafiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2868, '25:13', 25, 13, 'wa-i<u>dzaa</u> ulquu minh<u>aa</u> mak<u>aa</u>nan <u>dh</u>ayyiqan muqarraniina da\'aw hun<u>aa</u>lika tsubuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2869, '25:14', 25, 14, 'l<u>aa</u> tad\'uu lyawma tsubuuran w<u>aah</u>idan wad\'uu tsubuuran katsiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2870, '25:15', 25, 15, 'qul a<u>dzaa</u>lika khayrun am jannatu lkhuldillatii wu\'ida lmuttaquuna k<u>aa</u>nat lahum jaz<u>aa</u>-an wama<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2871, '25:16', 25, 16, 'lahum fiih<u>aa</u> m<u>aa</u> yasy<u>aa</u>uuna kh<u>aa</u>lidiina k<u>aa</u>na \'al<u>aa</u> rabbika wa\'dan mas-uul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2872, '25:17', 25, 17, 'wayawma ya<u>h</u>syuruhum wam<u>aa</u> ya\'buduuna min duunill<u>aa</u>hi fayaquulu a-antum a<u>dh</u>laltum \'ib<u>aa</u>dii h<u>aa</u>ul<u>aa</u>-i am hum <u>dh</u>alluu ssabiil\r\r\n');
INSERT INTO quranLatin VALUES (2873, '25:18', 25, 18, 'q<u>aa</u>luu sub<u>haa</u>naka m<u>aa</u> k<u>aa</u>na yanbaghii lan<u>aa</u> an nattakhi<u>dz</u>a min duunika min awliy<u>aa</u>-a wal<u>aa</u>kin matta\'tahum wa<u>aa</u>b<u>aa</u>-ahum <u>h</u>att<u>aa</u> nasuu <u>dzdz</u>ikra wak<u>aa</u>nuu qawman buur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2874, '25:19', 25, 19, 'faqad ka<u>dzdz</u>abuukum bim<u>aa</u> taquuluuna fam<u>aa</u> tasta<u>th</u>ii\'uuna <u>sh</u>arfan wal<u>aa</u> na<u>sh</u>ran waman ya<u>zh</u>lim minkum nu<u>dz</u>iqhu \'a<u>dzaa</u>ban kabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2875, '25:20', 25, 20, 'wam<u>aa</u> arsaln<u>aa</u> qablaka mina lmursaliina ill<u>aa</u> innahum laya/kuluuna <u>ththh</u>a\'<u>aa</u>ma wayamsyuuna fii l-asw<u>aa</u>qi waja\'aln<u>aa</u> ba\'<u>dh</u>akum liba\'<u>dh</u>in fitnatan ata<u>sh</u>biruuna wak<u>aa</u>na rabbuka ba<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2876, '25:21', 25, 21, 'waq<u>aa</u>lalla<u>dz</u>iina l<u>aa</u> yarjuuna liq<u>aa</u>-an<u>aa</u> lawl<u>aa</u> unzila \'alayn<u>aa</u> lmal<u>aa</u>-ikatu aw nar<u>aa</u> rabban<u>aa</u> laqadi istakbaruu fii anfusihim wa\'ataw \'utuwwan kabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2877, '25:22', 25, 22, 'yawma yarawna lmal<u>aa</u>-ikata l<u>aa</u> busyr<u>aa</u> yawma-i<u>dz</u>in lilmujrimiina wayaquuluuna <u>h</u>ijran ma<u>h</u>juur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2878, '25:23', 25, 23, 'waqadimn<u>aa</u> il<u>aa</u> m<u>aa</u> \'amiluu min \'amalin faja\'aln<u>aa</u>hu hab<u>aa</u>-an mantsuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2879, '25:24', 25, 24, 'a<u>sh</u>-<u>haa</u>bu ljannati yawma-i<u>dz</u>in khayrun mustaqarran wa-a<u>h</u>sanu maqiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2880, '25:25', 25, 25, 'wayawma tasyaqqaqu ssam<u>aa</u>u bilgham<u>aa</u>mi wanuzzila lmal<u>aa</u>-ikatu tanziil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2881, '25:26', 25, 26, 'almulku yawma-i<u>dz</u>in l<u>h</u>aqqu lirra<u>h</u>m<u>aa</u>ni wak<u>aa</u>na yawman \'al<u>aa</u> lk<u>aa</u>firiina \'asiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2882, '25:27', 25, 27, 'wayawma ya\'a<u>dhdh</u>u <u>zhzhaa</u>limu \'al<u>aa</u> yadayhi yaquulu y<u>aa</u> laytanii ittakha<u>dz</u>tu ma\'a rrasuuli sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2883, '25:28', 25, 28, 'y<u>aa</u> waylat<u>aa</u> laytanii lam attakhi<u>dz</u> ful<u>aa</u>nan khaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2884, '25:29', 25, 29, 'laqad a<u>dh</u>allanii \'ani <u>dzdz</u>ikri ba\'da i<u>dz</u> j<u>aa</u>-anii wak<u>aa</u>na sysyay<u>th</u><u>aa</u>nu lil-ins<u>aa</u>ni kha<u>dz</u>uul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2885, '25:30', 25, 30, 'waq<u>aa</u>la rrasuulu y<u>aa</u> rabbi inna qawmii ittakha<u>dz</u>uu h<u>aadzaa</u> lqur-<u>aa</u>na mahjuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2886, '25:31', 25, 31, 'waka<u>dzaa</u>lika ja\'aln<u>aa</u> likulli nabiyyin \'aduwwan mina lmujrimiina wakaf<u>aa</u> birabbika h<u>aa</u>diyan wana<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2887, '25:32', 25, 32, 'waq<u>aa</u>lalla<u>dz</u>iina kafaruu lawl<u>aa</u> nuzzila \'alayhi lqur-<u>aa</u>nu jumlatan w<u>aah</u>idatan ka<u>dzaa</u>lika linutsabbita bihi fu-<u>aa</u>daka warattaln<u>aa</u>hu tartiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2888, '25:33', 25, 33, 'wal<u>aa</u> ya/tuunaka bimatsalin ill<u>aa</u> ji/n<u>aa</u>ka bil<u>h</u>aqqi wa-a<u>h</u>sana tafsiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2889, '25:34', 25, 34, '<B>al</b>la<u>dz</u>iina yu<u>h</u>syaruuna \'al<u>aa</u> wujuuhihim il<u>aa</u> jahannama ul<u>aa</u>-ika syarrun mak<u>aa</u>nan wa-a<u>dh</u>allu sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2890, '25:35', 25, 35, 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> lkit<u>aa</u>ba waja\'aln<u>aa</u> ma\'ahu akh<u>aa</u>hu h<u>aa</u>ruuna waziir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2891, '25:36', 25, 36, 'faquln<u>aa</u> i<u>dz</u>hab<u>aa</u> il<u>aa</u> lqawmilla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fadammarn<u>aa</u>hum tadmiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2892, '25:37', 25, 37, 'waqawma nuu<u>h</u>in lamm<u>aa</u> ka<u>dzdz</u>abuu rrusula aghraqn<u>aa</u>hum waja\'aln<u>aa</u>hum linn<u>aa</u>si <u>aa</u>yatan wa-a\'tadn<u>aa</u> li<u>zhzhaa</u>limiina \'a<u>dzaa</u>ban liim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2893, '25:38', 25, 38, 'wa\'<u>aa</u>dan watsamuuda wa-a<u>sh</u>-<u>haa</u>ba rrassi waquruunan bayna <u>dzaa</u>lika katsiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2894, '25:39', 25, 39, 'wakullan <u>dh</u>arabn<u>aa</u> lahu l-amts<u>aa</u>la wakullan tabbarn<u>aa</u> tatbiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2895, '25:40', 25, 40, 'walaqad ataw \'al<u>aa</u> lqaryatillatii um<u>th</u>irat ma<u>th</u>ara ssaw-i afalam yakuunuu yarawnah<u>aa</u> bal k<u>aa</u>nuu l<u>aa</u> yarjuuna nusyuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2896, '25:41', 25, 41, 'wa-i<u>dzaa</u> ra-awka in yattakhi<u>dz</u>uunaka ill<u>aa</u> huzuwan ah<u>aadzaa</u>lla<u>dz</u>ii ba\'atsall<u>aa</u>hu rasuul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2897, '25:42', 25, 42, 'in k<u>aa</u>da layu<u>dh</u>illun<u>aa</u> \'an <u>aa</u>lihatin<u>aa</u> lawl<u>aa</u> an <u>sh</u>abarn<u>aa</u> \'alayh<u>aa </u>wasawfa ya\'lamuuna <u>h</u>iina yarawna l\'a<u>dzaa</u>ba man a<u>dh</u>allu sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2898, '25:43', 25, 43, 'ara-ayta mani ittakha<u>dz</u>a il<u>aa</u>hahu haw<u>aa</u>hu afa-anta takuunu \'alayhi wakiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2899, '25:44', 25, 44, 'am ta<u>h</u>sabu anna aktsarahum yasma\'uuna aw ya\'qiluuna in hum ill<u>aa</u> kal-an\'<u>aa</u>mi bal hum a<u>dh</u>allu sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2900, '25:45', 25, 45, 'alam tara il<u>aa</u> rabbika kayfa madda <u>zhzh</u>illa walaw sy<u>aa</u>-a laja\'alahu s<u>aa</u>kinan tsumma ja\'aln<u>aa</u> sysyamsa \'alayhi daliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2901, '25:46', 25, 46, 'tsumma qaba<u>dh</u>n<u>aa</u>hu ilayn<u>aa</u> qab<u>dh</u>an yasiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2902, '25:47', 25, 47, 'wahuwalla<u>dz</u>ii ja\'ala lakumullayla lib<u>aa</u>san wannawma sub<u>aa</u>tan waja\'ala nnah<u>aa</u>ra nusyuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2903, '25:48', 25, 48, 'wahuwalla<u>dz</u>ii arsala rriy<u>aah</u>a busyran bayna yaday ra<u>h</u>matihi wa-anzaln<u>aa</u> mina ssam<u>aa</u>-i m<u>aa</u>-an <u>th</u>ahuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2904, '25:49', 25, 49, 'linu<u>h</u>yiya bihi baldatan maytan wanusqiyahu mimm<u>aa</u> khalaqn<u>aa</u> an\'<u>aa</u>man wa-an<u>aa</u>siyya katsiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2905, '25:50', 25, 50, 'walaqad <u>sh</u>arrafn<u>aa</u>hu baynahum liya<u>dzdz</u>akkaruu fa-ab<u>aa</u> aktsaru nn<u>aa</u>si ill<u>aa</u> kufuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2906, '25:51', 25, 51, 'walaw syi/n<u>aa</u> laba\'atsn<u>aa</u> fii kulli qaryatin na<u>dz</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2907, '25:52', 25, 52, 'fal<u>aa</u> tu<u>th</u>i\'i lk<u>aa</u>firiina waj<u>aa</u>hidhum bihi jih<u>aa</u>dan kabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2908, '25:53', 25, 53, 'wahuwalla<u>dz</u>ii maraja lba<u>h</u>rayni h<u>aadzaa</u> \'a<u>dz</u>bun fur<u>aa</u>tun wah<u>aadzaa</u> mil<u>h</u>un uj<u>aa</u>jun waja\'ala baynahum<u>aa</u> barzakhan wa<u>h</u>ijran ma<u>h</u>juur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2909, '25:54', 25, 54, 'wahuwalla<u>dz</u>ii khalaqa mina lm<u>aa</u>-i basyaran faja\'alahu nasaban wa<u>sh</u>ihran wak<u>aa</u>na rabbuka qadiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2910, '25:55', 25, 55, 'waya\'buduuna min duunill<u>aa</u>hi m<u>aa</u> l<u>aa</u> yanfa\'uhum wal<u>aa</u> ya<u>dh</u>urruhum wak<u>aa</u>na lk<u>aa</u>firu \'al<u>aa</u> rabbihi <u>zh</u>ahiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2911, '25:56', 25, 56, 'wam<u>aa</u> arsaln<u>aa</u>ka ill<u>aa</u> mubasysyiran wana<u>dz</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2912, '25:57', 25, 57, 'qul m<u>aa</u> as-alukum \'alayhi min ajrin ill<u>aa</u> man sy<u>aa</u>-a an yattakhi<u>dz</u>a il<u>aa</u> rabbihi sabiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2913, '25:58', 25, 58, 'watawakkal \'al<u>aa</u> l<u>h</u>ayyilla<u>dz</u>ii l<u>aa</u> yamuutu wasabbi<u>h</u> bi<u>h</u>amdihi wakaf<u>aa</u> bihi bi<u>dz</u>unuubi \'ib<u>aa</u>dihi khabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2914, '25:59', 25, 59, '<B>al</b>la<u>dz</u>ii khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a wam<u>aa</u> baynahum<u>aa</u> fii sittati ayy<u>aa</u>min tsumma istaw<u>aa</u> \'al<u>aa</u> l\'arsyi rra<u>h</u>m<u>aa</u>nu fas-al bihi khabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2915, '25:60', 25, 60, 'wa-i<u>dzaa</u> qiila lahumu usjuduu lirra<u>h</u>m<u>aa</u>ni q<u>aa</u>luu wam<u>aa</u> rra<u>h</u>m<u>aa</u>nu anasjudu lim<u>aa</u> ta/murun<u>aa</u> waz<u>aa</u>dahum nufuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2916, '25:61', 25, 61, 'tab<u>aa</u>rakalla<u>dz</u>ii ja\'ala fii ssam<u>aa</u>-i buruujan waja\'ala fiih<u>aa</u> sir<u>aa</u>jan waqamaran muniir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2917, '25:62', 25, 62, 'wahuwalla<u>dz</u>ii ja\'alallayla wannah<u>aa</u>ra khilfatan liman ar<u>aa</u>da an ya<u>dzdz</u>akkara aw ar<u>aa</u>da syukuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2918, '25:63', 25, 63, 'wa\'ib<u>aa</u>du rra<u>h</u>m<u>aa</u>nilla<u>dz</u>iina yamsyuuna \'al<u>aa</u> l-ar<u>dh</u>i hawnan wa-i<u>dzaa</u> kh<u>aath</u>abahumu lj<u>aa</u>hiluuna q<u>aa</u>luu sal<u>aa</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2919, '25:64', 25, 64, 'walla<u>dz</u>iina yabiituuna lirabbihim sujjadan waqiy<u>aa</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2920, '25:65', 25, 65, 'walla<u>dz</u>iina yaquuluuna rabban<u>aa</u> i<u>sh</u>rif \'ann<u>aa</u> \'a<u>dzaa</u>ba jahannama inna \'a<u>dzaa</u>bah<u>aa</u> k<u>aa</u>na ghar<u>aa</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2921, '25:66', 25, 66, 'innah<u>aa</u> s<u>aa</u>-at mustaqarran wamuq<u>aa</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2922, '25:67', 25, 67, 'walla<u>dz</u>iina i<u>dzaa</u> anfaquu lam yusrifuu walam yaqturuu wak<u>aa</u>na bayna <u>dzaa</u>lika qaw<u>aa</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2923, '25:68', 25, 68, 'walla<u>dz</u>iina l<u>aa</u> yad\'uuna ma\'<u>aa</u>ll<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara wal<u>aa</u> yaqtuluuna nnafsallatii <u>h</u>arramall<u>aa</u>hu ill<u>aa</u> bil<u>h</u>aqqi wal<u>aa</u> yaznuuna waman yaf\'al <u>dzaa</u>lika yalqa ats<u>aa</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2924, '25:69', 25, 69, 'yu<u>daa</u>\'af lahu l\'a<u>dzaa</u>bu yawma lqiy<u>aa</u>mati wayakhlud fiihi muh<u>aa</u>n<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2925, '25:70', 25, 70, 'ill<u>aa</u> man t<u>aa</u>ba wa<u>aa</u>mana wa\'amila \'amalan <u>shaa</u>li<u>h</u>an faul<u>aa</u>-ika yubaddilull<u>aa</u>hu sayyi-<u>aa</u>tihim <u>h</u>asan<u>aa</u>tin wak<u>aa</u>nall<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2926, '25:71', 25, 71, 'waman t<u>aa</u>ba wa\'amila <u>shaa</u>li<u>h</u>an fa-innahu yatuubu il<u>aa</u>ll<u>aa</u>hi mat<u>aa</u>b<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2927, '25:72', 25, 72, 'walla<u>dz</u>iina l<u>aa</u> yasyhaduuna zzuura wa-i<u>dzaa</u> marruu billaghwi marruu kir<u>aa</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2928, '25:73', 25, 73, 'walla<u>dz</u>iina i<u>dzaa</u> <u>dz</u>ukkiruu bi-<u>aa</u>y<u>aa</u>ti rabbihim lam yakhirruu \'alayh<u>aa</u> <u>sh</u>umman wa\'umy<u>aa</u>n<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2929, '25:74', 25, 74, 'walla<u>dz</u>iina yaquuluuna rabban<u>aa</u> hab lan<u>aa</u> min azw<u>aa</u>jin<u>aa</u> wa<u>dz</u>urriyy<u>aa</u>tin<u>aa</u> qurrata a\'yunin waj\'aln<u>aa</u> lilmuttaqiina im<u>aa</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2930, '25:75', 25, 75, 'ul<u>aa</u>-ika yujzawna lghurfata bim<u>aa</u> <u>sh</u>abaruu wayulaqqawna fiih<u>aa</u> ta<u>h</u>iyyatan wasal<u>aa</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2931, '25:76', 25, 76, 'kh<u>aa</u>lidiina fiih<u>aa</u> <u>h</u>asunat mustaqarran wamuq<u>aa</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2932, '25:77', 25, 77, 'qul m<u>aa</u> ya\'bau bikum rabbii lawl<u>aa</u> du\'<u>aa</u>ukum faqad ka<u>dzdz</u>abtum fasawfa yakuunu liz<u>aa</u>m<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (2933, '26:1', 26, 1, '<u>th</u><u>aa</u>-siin-miim</u>\r\r\n');
INSERT INTO quranLatin VALUES (2934, '26:2', 26, 2, 'tilka <u>aa</u>y<u>aa</u>tu lkit<u>aa</u>bi lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (2935, '26:3', 26, 3, 'la\'allaka b<u>aa</u>khi\'un nafsakall<u>aa</u> yakuunuu mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (2936, '26:4', 26, 4, 'in nasya/ nunazzil \'alayhim mina ssam<u>aa</u>-i <u>aa</u>yatan fa<u>zh</u>allat a\'n<u>aa</u>quhum lah<u>aa</u> kh<u>aad</u>i\'iin\r\r\n');
INSERT INTO quranLatin VALUES (2937, '26:5', 26, 5, 'wam<u>aa</u> ya/tiihim min <u>dz</u>ikrin mina rra<u>h</u>m<u>aa</u>ni mu<u>h</u>datsin ill<u>aa</u> k<u>aa</u>nuu \'anhu mu\'ri<u>dh</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (2938, '26:6', 26, 6, 'faqad ka<u>dzdz</u>abuu fasaya/tiihim anb<u>aa</u>u m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (2939, '26:7', 26, 7, 'awa lam yaraw il<u>aa</u> l-ar<u>dh</u>i kam anbatn<u>aa</u> fiih<u>aa</u> min kulli zawjin kariim\r\r\n');
INSERT INTO quranLatin VALUES (2940, '26:8', 26, 8, 'inna fii <u>dzaa</u>lika la<u>aa</u>yatan wam<u>aa</u> k<u>aa</u>na aktsaruhum mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (2941, '26:9', 26, 9, 'wa-inna rabbaka lahuwa l\'aziizu rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (2942, '26:10', 26, 10, 'wa-i<u>dz</u> n<u>aa</u>d<u>aa</u> rabbuka muus<u>aa</u> ani i/ti lqawma <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (2943, '26:11', 26, 11, 'qawma fir\'awna l<u>aa</u> yattaquun\r\r\n');
INSERT INTO quranLatin VALUES (2944, '26:12', 26, 12, 'q<u>aa</u>la rabbi innii akh<u>aa</u>fu an yuka<u>dzdz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (2945, '26:13', 26, 13, 'waya<u>dh</u>iiqu <u>sh</u>adrii wal<u>aa</u> yan<u>th</u>aliqu lis<u>aa</u>nii fa-arsil il<u>aa</u> h<u>aa</u>ruun\r\r\n');
INSERT INTO quranLatin VALUES (2946, '26:14', 26, 14, 'walahum \'alayya <u>dz</u>anbun fa-akh<u>aa</u>fu an yaqtuluun\r\r\n');
INSERT INTO quranLatin VALUES (2947, '26:15', 26, 15, 'q<u>aa</u>la kall<u>aa</u> fa<u>dz</u>hab<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> inn<u>aa</u> ma\'akum mustami\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2948, '26:16', 26, 16, 'fa/tiy<u>aa</u> fir\'awna faquul<u>aa</u> inn<u>aa</u> rasuulu rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (2949, '26:17', 26, 17, 'an arsil ma\'an<u>aa</u> banii isr<u>aa</u>-iil\r\r\n');
INSERT INTO quranLatin VALUES (2950, '26:18', 26, 18, 'q<u>aa</u>la alam nurabbika fiin<u>aa</u> waliidan walabitsta fiin<u>aa</u> min \'umurika siniin\r\r\n');
INSERT INTO quranLatin VALUES (2951, '26:19', 26, 19, 'wafa\'alta fa\'latakallatii fa\'alta wa-anta mina lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (2952, '26:20', 26, 20, 'q<u>aa</u>la fa\'altuh<u>aa</u> i<u>dz</u>an wa-an<u>aa</u> mina <u>dhdhaa</u>lliin\r\r\n');
INSERT INTO quranLatin VALUES (2953, '26:21', 26, 21, 'fafarartu minkum lamm<u>aa</u> khiftukum fawahaba lii rabbii <u>h</u>ukman waja\'alanii mina lmursaliin\r\r\n');
INSERT INTO quranLatin VALUES (2954, '26:22', 26, 22, 'watilka ni\'matun tamunnuh<u>aa</u> \'alayya an \'abbadta banii isr<u>aa</u>-iil\r\r\n');
INSERT INTO quranLatin VALUES (2955, '26:23', 26, 23, 'q<u>aa</u>la fir\'awnu wam<u>aa</u> rabbu l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (2956, '26:24', 26, 24, 'q<u>aa</u>la rabbu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wam<u>aa</u> baynahum<u>aa</u> in kuntum muuqiniin\r\r\n');
INSERT INTO quranLatin VALUES (2957, '26:25', 26, 25, 'q<u>aa</u>la liman <u>h</u>awlahu l<u>aa</u> tastami\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2958, '26:26', 26, 26, 'q<u>aa</u>la rabbukum warabbu <u>aa</u>b<u>aa</u>-ikumu l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (2959, '26:27', 26, 27, 'q<u>aa</u>la inna rasuulakumulla<u>dz</u>ii ursila ilaykum lamajnuun\r\r\n');
INSERT INTO quranLatin VALUES (2960, '26:28', 26, 28, 'q<u>aa</u>la rabbu lmasyriqi walmaghribi wam<u>aa</u> baynahum<u>aa</u> in kuntum ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (2961, '26:29', 26, 29, 'q<u>aa</u>la la-ini ittakha<u>dz</u>ta il<u>aa</u>han ghayrii la-aj\'alannaka mina lmasjuuniin\r\r\n');
INSERT INTO quranLatin VALUES (2962, '26:30', 26, 30, 'q<u>aa</u>la awa law ji/tuka bisyay-in mubiin\r\r\n');
INSERT INTO quranLatin VALUES (2963, '26:31', 26, 31, 'q<u>aa</u>la fa/ti bihi in kunta mina <u>shshaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (2964, '26:32', 26, 32, 'fa-alq<u>aa</u> \'a<u>shaa</u>hu fa-i<u>dzaa</u> hiya tsu\'b<u>aa</u>nun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (2965, '26:33', 26, 33, 'wanaza\'a yadahu fa-i<u>dzaa</u> hiya bay<u>daa</u>u linn<u>aats</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (2966, '26:34', 26, 34, 'q<u>aa</u>la lilmala-i <u>h</u>awlahu inna h<u>aadzaa</u> las<u>aah</u>irun \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (2967, '26:35', 26, 35, 'yuriidu an yukhrijakum min ar<u>dh</u>ikum bisi<u>h</u>rihi fam<u>aatsaa</u> ta/muruun\r\r\n');
INSERT INTO quranLatin VALUES (2968, '26:36', 26, 36, 'q<u>aa</u>luu arjih wa-akh<u>aa</u>hu wab\'ats fii lmad<u>aa</u>-ini <u>haa</u>syiriin\r\r\n');
INSERT INTO quranLatin VALUES (2969, '26:37', 26, 37, 'ya/tuuka bikulli sa<u>hhaa</u>rin \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (2970, '26:38', 26, 38, 'fajumi\'a ssa<u>h</u>aratu limiiq<u>aa</u>ti yawmin ma\'luum\r\r\n');
INSERT INTO quranLatin VALUES (2971, '26:39', 26, 39, 'waqiila linn<u>aa</u>si hal antum mujtami\'uun\r\r\n');
INSERT INTO quranLatin VALUES (2972, '26:40', 26, 40, 'la\'allan<u>aa</u> nattabi\'u ssa<u>h</u>arata in k<u>aa</u>nuu humu lgh<u>aa</u>libiin\r\r\n');
INSERT INTO quranLatin VALUES (2973, '26:41', 26, 41, 'falamm<u>aa</u> j<u>aa</u>-a ssa<u>h</u>aratu q<u>aa</u>luu lifir\'awna a-inna lan<u>aa</u> la-ajran in kunn<u>aa</u> na<u>h</u>nu lgh<u>aa</u>libiin\r\r\n');
INSERT INTO quranLatin VALUES (2974, '26:42', 26, 42, 'q<u>aa</u>la na\'am wa-innakum i<u>dz</u>an lamina lmuqarrabiin\r\r\n');
INSERT INTO quranLatin VALUES (2975, '26:43', 26, 43, 'q<u>aa</u>la lahum muus<u>aa</u> lquu m<u>aa</u> antum mulquun\r\r\n');
INSERT INTO quranLatin VALUES (2976, '26:44', 26, 44, 'fa-alqaw <u>h</u>ib<u>aa</u>lahum wa\'i<u>sh</u>iyyahum waq<u>aa</u>luu bi\'izzati fir\'awna inn<u>aa</u> lana<u>h</u>nu lgh<u>aa</u>libuun\r\r\n');
INSERT INTO quranLatin VALUES (2977, '26:45', 26, 45, 'fa-alq<u>aa</u> muus<u>aa</u> \'a<u>shaa</u>hu fa-i<u>dzaa</u> hiya talqafu m<u>aa</u> ya/fikuun\r\r\n');
INSERT INTO quranLatin VALUES (2978, '26:46', 26, 46, 'faulqiya ssa<u>h</u>aratu s<u>aa</u>jidiin\r\r\n');
INSERT INTO quranLatin VALUES (2979, '26:47', 26, 47, 'q<u>aa</u>luu <u>aa</u>mann<u>aa</u> birabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (2980, '26:48', 26, 48, 'rabbi muus<u>aa</u> wah<u>aa</u>ruun\r\r\n');
INSERT INTO quranLatin VALUES (2981, '26:49', 26, 49, 'q<u>aa</u>la <u>aa</u>mantum lahu qabla an <u>aadz</u>ana lakum innahu lakabiirukumulla<u>dz</u>ii \'allamakumu ssi<u>h</u>ra falasawfa ta\'lamuuna lauqa<u>ththh</u>i\'anna aydiyakum wa-arjulakum min khil<u>aa</u>fin walau<u>sh</u>allibannakum ajma\'iin\r\r\n');
INSERT INTO quranLatin VALUES (2982, '26:50', 26, 50, 'q<u>aa</u>luu l<u>aa</u> <u>dh</u>ayra inn<u>aa</u> il<u>aa</u> rabbin<u>aa</u> munqalibuun\r\r\n');
INSERT INTO quranLatin VALUES (2983, '26:51', 26, 51, 'inn<u>aa</u> na<u>th</u>ma\'u an yaghfira lan<u>aa</u> rabbun<u>aa</u> kha<u>th</u><u>aa</u>y<u>aa</u>n<u>aa</u> an kunn<u>aa</u> awwala lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (2984, '26:52', 26, 52, 'wa-aw<u>h</u>ayn<u>aa</u> il<u>aa</u> muus<u>aa</u> an asri bi\'ib<u>aa</u>dii innakum muttaba\'uun\r\r\n');
