INSERT INTO quranLatin VALUES (3175, '27:16', 27, 16, 'wawaritsa sulaym<u>aa</u>nu d<u>aa</u>wuuda waq<u>aa</u>la y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su \'ullimn<u>aa</u> man<u>th</u>iqa <u>ththh</u>ayri wauutiin<u>aa</u> min kulli syay-in inna h<u>aadzaa</u> lahuwa lfa<u>dh</u>lu lmubiin\r\r\n');
