INSERT INTO quranLatin VALUES (3177, '27:18', 27, 18, '<u>h</u>att<u>aa</u> i<u>dzaa</u> ataw \'al<u>aa</u> w<u>aa</u>di nnamli q<u>aa</u>lat namlatun y<u>aa</u> ayyuh<u>aa</u> nnamlu udkhuluu mas<u>aa</u>kinakum l<u>aa</u> ya<u>hth</u>imannakum sulaym<u>aa</u>nu wajunuuduhu wahum l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (3178, '27:19', 27, 19, 'fatabassama <u>dah</u>ikan min qawlih<u>aa</u> waq<u>aa</u>la rabbi awzi\'nii an asykura ni\'matakallatii an\'amta \'alayya wa\'al<u>aa</u> w<u>aa</u>lidayya wa-an a\'mala <u>shaa</u>li<u>h</u>an tar<u>daa</u>hu wa-adkhilnii bira<u>h</u>matika fii \'ib<u>aa</u>dika <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (3179, '27:20', 27, 20, 'watafaqqada <u>ththh</u>ayra faq<u>aa</u>la m<u>aa</u> liya l<u>aa</u> ar<u>aa</u> lhudhuda am k<u>aa</u>na mina lgh<u>aa</u>-ibiin\r\r\n');
INSERT INTO quranLatin VALUES (3180, '27:21', 27, 21, 'lau\'a<u>dzdz</u>ibannahu \'a<u>dzaa</u>ban syadiidan aw la-a<u>dz</u>ba<u>h</u>annahu aw laya/tiyannii bisul<u>th</u><u>aa</u>nin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (3181, '27:22', 27, 22, 'famakatsa ghayra ba\'iidin faq<u>aa</u>la a<u>hath</u>tu bim<u>aa</u> lam tu<u>h</u>i<u>th</u> bihi waji/tuka min saba-in binaba-in yaqiin\r\r\n');
INSERT INTO quranLatin VALUES (3182, '27:23', 27, 23, 'innii wajadtu imra-atan tamlikuhum wauutiyat min kulli syay-in walah<u>aa</u> \'arsyun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (3183, '27:24', 27, 24, 'wajadtuh<u>aa</u> waqawmah<u>aa</u> yasjuduuna lisysyamsi min duunill<u>aa</u>hi wazayyana lahumu sysyay<u>th</u><u>aa</u>nu a\'m<u>aa</u>lahum fa<u>sh</u>addahum \'ani ssabiili fahum l<u>aa</u> yahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (3184, '27:25', 27, 25, '<B>al</b>l<u>aa</u> yasjuduu lill<u>aa</u>hilla<u>dz</u>ii yukhriju lkhaba-a fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i waya\'lamu m<u>aa</u> tukhfuuna wam<u>aa</u> tu\'linuun\r\r\n');
INSERT INTO quranLatin VALUES (3185, '27:26', 27, 26, '<B>al</b>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa rabbu l\'arsyi l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (3186, '27:27', 27, 27, 'q<u>aa</u>la sanan<u>zh</u>uru a<u>sh</u>adaqta am kunta mina lk<u>aadz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (3187, '27:28', 27, 28, 'i<u>dz</u>hab bikit<u>aa</u>bii h<u>aadzaa</u> fa-alqih ilayhim tsumma tawalla \'anhum fan<u>zh</u>ur m<u>aatsaa</u> yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3188, '27:29', 27, 29, 'q<u>aa</u>lat y<u>aa</u> ayyuh<u>aa</u> lmalau innii ulqiya ilayya kit<u>aa</u>bun kariim\r\r\n');
INSERT INTO quranLatin VALUES (3189, '27:30', 27, 30, 'innahu min sulaym<u>aa</u>na wa-innahu bismill<u>aa</u>hi rra<u>h</u>m<u>aa</u>ni rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (3190, '27:31', 27, 31, '<B>al</b>l<u>aa</u> ta\'luu \'alayya wa/tuunii muslimiin\r\r\n');
INSERT INTO quranLatin VALUES (3191, '27:32', 27, 32, 'q<u>aa</u>lat y<u>aa</u> ayyuh<u>aa</u> lmalau aftuunii fii amrii m<u>aa</u> kuntu q<u>aath</u>i\'atan amran <u>h</u>att<u>aa</u> tasyhaduun\r\r\n');
INSERT INTO quranLatin VALUES (3192, '27:33', 27, 33, 'q<u>aa</u>luu na<u>h</u>nu uluu quwwatin wauluu ba/sin syadiidin wal-amru ilayki fan<u>zh</u>urii m<u>aatsaa</u> ta/muriin\r\r\n');
INSERT INTO quranLatin VALUES (3193, '27:34', 27, 34, 'q<u>aa</u>lat inna lmuluuka i<u>dzaa</u> dakhaluu qaryatan afsaduuh<u>aa</u> waja\'aluu a\'izzata ahlih<u>aa</u> a<u>dz</u>illatan waka<u>dzaa</u>lika yaf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (3194, '27:35', 27, 35, 'wa-innii mursilatun ilayhim bihadiyyatin fan<u>aats</u>iratun bima yarji\'u lmursaluun\r\r\n');
INSERT INTO quranLatin VALUES (3195, '27:36', 27, 36, 'falamm<u>aa</u> j<u>aa</u>-a sulaym<u>aa</u>na q<u>aa</u>la atumidduunani bim<u>aa</u>lin fam<u>aa</u> <u>aa</u>t<u>aa</u>niyall<u>aa</u>hu khayrun mimm<u>aa</u> <u>aa</u>t<u>aa</u>kum bal antum bihadiyyatikum tafra<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (3196, '27:37', 27, 37, 'irji\'ilayhim falana/tiyannahum bijunuudin l<u>aa</u> qibala lahum bih<u>aa</u> walanukhrijannahum minh<u>aa</u> a<u>dz</u>illatan wahum <u>shaa</u>ghiruun\r\r\n');
INSERT INTO quranLatin VALUES (3197, '27:38', 27, 38, 'q<u>aa</u>la y<u>aa</u> ayyuh<u>aa</u> lmalau ayyukum ya/tiinii bi\'arsyih<u>aa</u> qabla an ya/tuunii muslimiin\r\r\n');
INSERT INTO quranLatin VALUES (3198, '27:39', 27, 39, 'q<u>aa</u>la \'ifriitun mina ljinni an<u>aa</u> <u>aa</u>tiika bihi qabla an taquuma min maq<u>aa</u>mika wa-inne \'alayhi laqawiyyun amiin\r\r\n');
INSERT INTO quranLatin VALUES (3199, '27:40', 27, 40, 'q<u>aa</u>lalla<u>dz</u>ii \'indahu \'ilmun mina lkit<u>aa</u>bi an<u>aa</u> <u>aa</u>tiika bihi qabla an yartadda ilayka <u>th</u>arfuka falamm<u>aa</u> ra<u>aa</u>hu mustaqirran \'indahu q<u>aa</u>la h<u>aadzaa</u> min fa<u>dh</u>li rabbii liyabluwanii a-asykuru am akfuru waman syakara fa-innam<u>aa</u> yasykuru linafsihi waman kafara fa-inna rabbii ghaniyyun kariim\r\r\n');
INSERT INTO quranLatin VALUES (3200, '27:41', 27, 41, 'q<u>aa</u>la nakkiruu lah<u>aa</u> \'arsyah<u>aa</u> nan<u>zh</u>ur atahtadii am takuunu minalla<u>dz</u>iina l<u>aa</u> yahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (3201, '27:42', 27, 42, 'falamm<u>aa</u> j<u>aa</u>-at qiila ah<u>aa</u>ka<u>dzaa</u> \'arsyuki q<u>aa</u>lat ka-annahu huwa wauutiin<u>aa</u> l\'ilma min qablih<u>aa</u> wakunn<u>aa</u> muslimiin\r\r\n');
INSERT INTO quranLatin VALUES (3202, '27:43', 27, 43, 'wa<u>sh</u>addah<u>aa</u> m<u>aa</u> k<u>aa</u>nat ta\'budu min duunill<u>aa</u>hi innah<u>aa</u> k<u>aa</u>nat min qawmin k<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (3203, '27:44', 27, 44, 'qiila lah<u>aa</u> udkhulii <u>shsh</u>ar<u>h</u>a falamm<u>aa</u> ra-at-hu <u>h</u>asibat-hu lujjatan wakasyafat \'an s<u>aa</u>qayh<u>aa</u> q<u>aa</u>la innahu <u>sh</u>ar<u>h</u>un mumarradun min qaw<u>aa</u>riira q<u>aa</u>lat rabbi innii <u>zh</u>alamtu nafsii wa-aslamtu ma\'a sulaym<u>aa</u>na lill<u>aa</u>hi rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (3204, '27:45', 27, 45, 'walaqad arsaln<u>aa</u> il<u>aa</u> tsamuuda akh<u>aa</u>hum <u>shaa</u>li<u>h</u>an ani u\'buduull<u>aa</u>ha fa-i<u>dzaa</u> hum fariiq<u>aa</u>ni yakhta<u>sh</u>imuun\r\r\n');
INSERT INTO quranLatin VALUES (3205, '27:46', 27, 46, 'q<u>aa</u>la y<u>aa</u> qawmi lima tasta\'jiluuna bissayyi-ati qabla l<u>h</u>asanati lawl<u>aa</u> tastaghfiruunall<u>aa</u>ha la\'allakum tur<u>h</u>amuun\r\r\n');
INSERT INTO quranLatin VALUES (3206, '27:47', 27, 47, 'q<u>aa</u>luu i<u>ththh</u>ayyarn<u>aa</u> bika wabiman ma\'aka q<u>aa</u>la <u>th</u><u>aa</u>-irukum \'indall<u>aa</u>hi bal antum qawmun tuftanuun\r\r\n');
INSERT INTO quranLatin VALUES (3207, '27:48', 27, 48, 'wak<u>aa</u>na fii lmadiinati tis\'atu rah<u>th</u>in yufsiduuna fii l-ar<u>dh</u>i wal<u>aa</u> yu<u>sh</u>li<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (3208, '27:49', 27, 49, 'q<u>aa</u>luu taq<u>aa</u>samuu bil<u>aa</u>hi lanubayyitannahu wa-ahlahu tsumma lanaquulanna liwaliyyihi m<u>aa</u> syahidn<u>aa</u> mahlika ahlihi wa-inn<u>aa</u> la<u>shaa</u>diquun\r\r\n');
INSERT INTO quranLatin VALUES (3209, '27:50', 27, 50, 'wamakaruu makran wamakarn<u>aa</u> makran wahum l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (3210, '27:51', 27, 51, 'fan<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu makrihim ann<u>aa</u> dammarn<u>aa</u>hum waqawmahum ajma\'iin\r\r\n');
INSERT INTO quranLatin VALUES (3211, '27:52', 27, 52, 'fatilka buyuutuhum kh<u>aa</u>wiyatan bim<u>aa</u> <u>zh</u>alamuu inna fii <u>dzaa</u>lika la<u>aa</u>yatan liqawmin ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3212, '27:53', 27, 53, 'wa-anjayn<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu wak<u>aa</u>nuu yattaquun\r\r\n');
INSERT INTO quranLatin VALUES (3213, '27:54', 27, 54, 'waluu<u>th</u>an i<u>dz</u> q<u>aa</u>la liqawmihi ata/tuuna lf<u>aah</u>isyata wa-antum tub<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (3214, '27:55', 27, 55, 'a-innakum lata/tuuna rrij<u>aa</u>la syahwatan min duuni nnis<u>aa</u>-i bal antum qawmun tajhaluun\r\r\n');
INSERT INTO quranLatin VALUES (3215, '27:56', 27, 56, 'fam<u>aa</u> k<u>aa</u>na jaw<u>aa</u>ba qawmihi ill<u>aa</u> an q<u>aa</u>luu akhrijuu <u>aa</u>la luu<u>th</u>in min qaryatikum innahum un<u>aa</u>sun yata<u>th</u>ahharuun\r\r\n');
INSERT INTO quranLatin VALUES (3216, '27:57', 27, 57, 'fa-anjayn<u>aa</u>hu wa-ahlahu ill<u>aa</u> imra-atahu qaddarn<u>aa</u>h<u>aa</u> mina lgh<u>aa</u>biriin\r\r\n');
INSERT INTO quranLatin VALUES (3217, '27:58', 27, 58, 'wa-am<u>th</u>arn<u>aa</u> \'alayhim ma<u>th</u>aran fas<u>aa</u>-a ma<u>th</u>aru lmun<u>dz</u>ariin\r\r\n');
INSERT INTO quranLatin VALUES (3218, '27:59', 27, 59, 'quli l<u>h</u>amdu lill<u>aa</u>hi wasal<u>aa</u>mun \'al<u>aa</u> \'ib<u>aa</u>dihilla<u>dz</u>iina i<u>sth</u>af<u>aa</u> <u>aa</u>ll<u>aa</u>hu khayrun amm<u>aa</u> yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (3219, '27:60', 27, 60, 'amman khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a wa-anzala lakum mina ssam<u>aa</u>-i m<u>aa</u>-an fa-anbatn<u>aa</u> bihi <u>h</u>ad<u>aa</u>-iqa <u>dzaa</u>ta bahjatin m<u>aa</u> k<u>aa</u>na lakum an tunbituu syajarah<u>aa</u> a-il<u>aa</u>hun ma\'all<u>aa</u>hi bal hum qawmun ya\'diluun\r\r\n');
INSERT INTO quranLatin VALUES (3220, '27:61', 27, 61, 'amman ja\'ala l-ar<u>dh</u>a qar<u>aa</u>ran waja\'ala khil<u>aa</u>lah<u>aa</u> anh<u>aa</u>ran waja\'ala lah<u>aa</u> raw<u>aa</u>siya waja\'ala bayna lba<u>h</u>rayni <u>haa</u>jizan a-il<u>aa</u>hun ma\'all<u>aa</u>hi bal aktsaruhum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3221, '27:62', 27, 62, 'amman yujiibu lmu<u>dth</u>arra i<u>dzaa</u> da\'<u>aa</u>hu wayaksyifu ssuu-a wayaj\'alukum khulaf<u>aa</u>-a l-ar<u>dh</u>i a-il<u>aa</u>hun ma\'all<u>aa</u>hi qaliilan m<u>aa</u> ta<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (3222, '27:63', 27, 63, 'amman yahdiikum fii <u>zh</u>ulum<u>aa</u>ti lbarri walba<u>h</u>ri waman yursilu rriy<u>aah</u>a busyran bayna yaday ra<u>h</u>matihi a-il<u>aa</u>hun ma\'all<u>aa</u>hi ta\'<u>aa</u>l<u>aa</u>ll<u>aa</u>hu \'amm<u>aa</u> yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (3223, '27:64', 27, 64, 'amman yabdau lkhalqa tsumma yu\'iiduhu waman yarzuqukum mina ssam<u>aa</u>-i wal-ar<u>dh</u>i a-il<u>aa</u>hun ma\'all<u>aa</u>hi qul h<u>aa</u>tuu burh<u>aa</u>nakum in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (3224, '27:65', 27, 65, 'qul l<u>aa</u> ya\'lamu man fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i lghayba ill<u>aa</u>ll<u>aa</u>hu wam<u>aa</u> yasy\'uruuna ayy<u>aa</u>na yub\'atsuun\r\r\n');
INSERT INTO quranLatin VALUES (3225, '27:66', 27, 66, 'bali idd<u>aa</u>raka \'ilmuhum fii l-<u>aa</u>khirati bal hum fii syakkin minh<u>aa</u> bal hum minh<u>aa</u> \'amuun\r\r\n');
INSERT INTO quranLatin VALUES (3226, '27:67', 27, 67, 'waq<u>aa</u>lalla<u>dz</u>iina kafaruu a-i<u>dzaa</u> kunn<u>aa</u> tur<u>aa</u>ban wa<u>aa</u>b<u>aa</u>un<u>aa</u> a-inn<u>aa</u> lamukhrajuun\r\r\n');
INSERT INTO quranLatin VALUES (3227, '27:68', 27, 68, 'laqad wu\'idn<u>aa</u> h<u>aadzaa</u> na<u>h</u>nu wa<u>aa</u>b<u>aa</u>un<u>aa</u> min qablu in h<u>aadzaa</u> ill<u>aa</u> as<u>aath</u>iiru l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (3228, '27:69', 27, 69, 'qul siiruu fii l-ar<u>dh</u>i fan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatu lmujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (3229, '27:70', 27, 70, 'wal<u>aa</u> ta<u>h</u>zan \'alayhim wal<u>aa</u> takun fii <u>dh</u>ayqin mimm<u>aa</u> yamkuruun\r\r\n');
INSERT INTO quranLatin VALUES (3230, '27:71', 27, 71, 'wayaquuluuna mat<u>aa</u> h<u>aadzaa</u> lwa\'du in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (3231, '27:72', 27, 72, 'qul \'as<u>aa</u> an yakuuna radifa lakum ba\'<u>dh</u>ulla<u>dz</u>ii tasta\'jiluun\r\r\n');
INSERT INTO quranLatin VALUES (3232, '27:73', 27, 73, 'wa-inna rabbaka la<u>dz</u>uu fa<u>dh</u>lin \'al<u>aa</u> nn<u>aa</u>si wal<u>aa</u>kinna aktsarahum l<u>aa</u> yasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (3233, '27:74', 27, 74, 'wa-inna rabbaka laya\'lamu m<u>aa</u> tukinnu <u>sh</u>uduuruhum wam<u>aa</u> yu\'linuun\r\r\n');
INSERT INTO quranLatin VALUES (3234, '27:75', 27, 75, 'wam<u>aa</u> min gh<u>aa</u>-ibatin fii ssam<u>aa</u>-i wal-ar<u>dh</u>i ill<u>aa</u> fii kit<u>aa</u>bin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (3235, '27:76', 27, 76, 'inna h<u>aadzaa</u> lqur-<u>aa</u>na yaqu<u>shsh</u>u \'al<u>aa</u> banii isr<u>aa</u>-iila aktsaralla<u>dz</u>ii hum fiihi yakhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (3236, '27:77', 27, 77, 'wa-innahu lahudan wara<u>h</u>matun lilmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (3237, '27:78', 27, 78, 'inna rabbaka yaq<u>dh</u>ii baynahum bi<u>h</u>ukmihi wahuwa l\'aziizu l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (3238, '27:79', 27, 79, 'fatawakkal \'al<u>aa</u>ll<u>aa</u>hi innaka \'al<u>aa</u> l<u>h</u>aqqi lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (3239, '27:80', 27, 80, 'innaka l<u>aa</u> tusmi\'u lmawt<u>aa</u> wal<u>aa</u> tusmi\'u <u>shsh</u>umma ddu\'<u>aa</u>-a i<u>dzaa</u> wallaw mudbiriin\r\r\n');
INSERT INTO quranLatin VALUES (3240, '27:81', 27, 81, 'wam<u>aa</u> anta bih<u>aa</u>dii l\'umyi \'an <u>dh</u>al<u>aa</u>latihim in tusmi\'u ill<u>aa</u> man yu/minu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fahum muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (3241, '27:82', 27, 82, 'wa-i<u>dzaa</u> waqa\'a lqawlu \'alayhim akhrajn<u>aa</u> lahum d<u>aa</u>bbatan mina l-ar<u>dh</u>i tukallimuhum anna nn<u>aa</u>sa k<u>aa</u>nuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> l<u>aa</u> yuuqinuun\r\r\n');
INSERT INTO quranLatin VALUES (3242, '27:83', 27, 83, 'wayawma na<u>h</u>syuru min kulli ummatin fawjan mimman yuka<u>dzdz</u>ibu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fahum yuuza\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3243, '27:84', 27, 84, '<u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>uu q<u>aa</u>la aka<u>dzdz</u>abtum bi-<u>aa</u>y<u>aa</u>tii walam tu<u>h</u>ii<u>th</u>uu bih<u>aa</u> \'ilman amm<u>aatsaa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (3244, '27:85', 27, 85, 'wawaqa\'a lqawlu \'alayhim bim<u>aa</u> <u>zh</u>alamuu fahum l<u>aa</u> yan<u>th</u>iquun\r\r\n');
INSERT INTO quranLatin VALUES (3245, '27:86', 27, 86, 'alam yaraw ann<u>aa</u> ja\'aln<u>aa</u>llayla liyaskunuu fiihi wannah<u>aa</u>ra mub<u>sh</u>iran inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (3246, '27:87', 27, 87, 'wayawma yunfakhu fii <u>shsh</u>uuri fafazi\'a man fii ssam<u>aa</u>w<u>aa</u>ti waman fii l-ar<u>dh</u>i ill<u>aa</u> man sy<u>aa</u>-all<u>aa</u>hu wakullun atawhu d<u>aa</u>khiriin\r\r\n');
INSERT INTO quranLatin VALUES (3247, '27:88', 27, 88, 'watar<u>aa</u> ljib<u>aa</u>la ta<u>h</u>sabuh<u>aa</u> j<u>aa</u>midatan wahiya tamurru marra ssa<u>haa</u>bi <u>sh</u>un\'all<u>aa</u>hilla<u>dz</u>ii atqana kulla syay-in innahu khabiirun bim<u>aa</u> taf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (3248, '27:89', 27, 89, 'man j<u>aa</u>-a bil<u>h</u>asanati falahu khayrun minh<u>aa</u> wahum min faza\'in yawma-i<u>dz</u>in <u>aa</u>minuun\r\r\n');
INSERT INTO quranLatin VALUES (3249, '27:90', 27, 90, 'waman j<u>aa</u>-a bissayyi-ati fakubbat wujuuhuhum fii nn<u>aa</u>ri hal tujzawna ill<u>aa</u> m<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (3250, '27:91', 27, 91, 'innam<u>aa</u> umirtu an a\'buda rabba h<u>aadz</u>ihi lbaldatilla<u>dz</u>ii <u>h</u>arramah<u>aa</u> walahu kullu syay-in waumirtu an akuuna mina lmuslimiin\r\r\n');
INSERT INTO quranLatin VALUES (3251, '27:92', 27, 92, 'wa-an atluwa lqur-<u>aa</u>na famani ihtad<u>aa</u> fa-innam<u>aa</u> yahtadii linafsihi waman <u>dh</u>alla faqul innam<u>aa</u> an<u>aa</u> mina lmun<u>dz</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (3252, '27:93', 27, 93, 'waquli l<u>h</u>amdu lill<u>aa</u>hi sayuriikum <u>aa</u>y<u>aa</u>tihi fata\'rifuunah<u>aa</u> wam<u>aa</u> rabbuka bigh<u>aa</u>filin \'amm<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (3253, '28:1', 28, 1, '<u>th</u><u>aa</u>-siin-miim</u>\r\r\n');
INSERT INTO quranLatin VALUES (3254, '28:2', 28, 2, 'tilka <u>aa</u>y<u>aa</u>tu lkit<u>aa</u>bi lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (3255, '28:3', 28, 3, 'natluu \'alayka min naba-i muus<u>aa</u> wafir\'awna bil<u>h</u>aqqi liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (3256, '28:4', 28, 4, 'inna fir\'awna \'al<u>aa</u> fii l-ar<u>dh</u>i waja\'ala ahlah<u>aa</u> syiya\'an yasta<u>dh</u>\'ifu <u>th</u><u>aa</u>-ifatan minhum yu<u>dz</u>abbi<u>h</u>u abn<u>aa</u>-ahum wayasta<u>h</u>yii nis<u>aa</u>-ahum innahu k<u>aa</u>na mina lmufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (3257, '28:5', 28, 5, 'wanuriidu an namunna \'al<u>aa</u>lla<u>dz</u>iina istu<u>dh</u>\'ifuu fii l-ar<u>dh</u>i wanaj\'alahum a-immatan wanaj\'alahumu lw<u>aa</u>ritsiin\r\r\n');
INSERT INTO quranLatin VALUES (3258, '28:6', 28, 6, 'wanumakkina lahum fii l-ar<u>dh</u>i wanuriya fir\'awna wah<u>aa</u>m<u>aa</u>na wajunuudahum<u>aa</u> minhum m<u>aa</u> k<u>aa</u>nuu ya<u>hts</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (3259, '28:7', 28, 7, 'wa-aw<u>h</u>ayn<u>aa</u> il<u>aa</u> ummi muus<u>aa</u> an ar<u>dh</u>i\'iihi fa-i<u>dzaa</u> khifti \'alayhi fa-alqiihi fii lyammi wal<u>aa</u> takh<u>aa</u>fii wal<u>aa</u> ta<u>h</u>zanii inn<u>aa</u> r<u>aa</u>dduuhu ilayki waj<u>aa</u>\'iluuhu mina lmursaliin\r\r\n');
INSERT INTO quranLatin VALUES (3260, '28:8', 28, 8, 'faltaqa<u>th</u>ahu <u>aa</u>lu fir\'awna liyakuuna lahum \'aduwwan wa<u>h</u>azanan inna fir\'awna wah<u>aa</u>m<u>aa</u>na wajunuudahum<u>aa</u> k<u>aa</u>nuu kh<u>aath</u>i-iin\r\r\n');
INSERT INTO quranLatin VALUES (3261, '28:9', 28, 9, 'waq<u>aa</u>lati imra-atu fir\'awna qurratu \'aynin lii walaka l<u>aa</u> taqtuluuhu \'as<u>aa</u> an yanfa\'an<u>aa</u> aw nattakhi<u>dz</u>ahu waladan wahum l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (3262, '28:10', 28, 10, 'wa-a<u>sh</u>ba<u>h</u>a fu-<u>aa</u>du ummi muus<u>aa</u> f<u>aa</u>righan in k<u>aa</u>dat latubdii bihi lawl<u>aa</u> an raba<u>th</u>n<u>aa</u> \'al<u>aa</u> qalbih<u>aa</u> litakuuna mina lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (3263, '28:11', 28, 11, 'waq<u>aa</u>lat li-ukhtihi qu<u>shsh</u>iihi faba<u>sh</u>urat bihi \'an junubin wahum l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (3264, '28:12', 28, 12, 'wa<u>h</u>arramn<u>aa</u> \'alayhi lmar<u>aad</u>i\'a min qablu faq<u>aa</u>lat hal adullukum \'al<u>aa</u> ahli baytin yakfuluunahu lakum wahum lahu n<u>aas</u>i<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (3265, '28:13', 28, 13, 'faradadn<u>aa</u>hu il<u>aa</u> ummihi kay taqarra \'aynuh<u>aa</u> wal<u>aa</u> ta<u>h</u>zana walita\'lama anna wa\'dall<u>aa</u>hi <u>h</u>aqqun wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3266, '28:14', 28, 14, 'walamm<u>aa</u> balagha asyuddahu wastaw<u>aa</u> <u>aa</u>tayn<u>aa</u>hu <u>h</u>ukman wa\'ilman waka<u>dzaa</u>lika najzii lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (3267, '28:15', 28, 15, 'wadakhala lmadiinata \'al<u>aa</u> <u>h</u>iini ghaflatin min ahlih<u>aa</u> fawajada fiih<u>aa</u> rajulayni yaqtatil<u>aa</u>ni h<u>aadzaa</u> min syii\'atihi wah<u>aadzaa</u> min \'aduwwihi fastagh<u>aa</u>tsahulla<u>dz</u>ii min syii\'atihi \'al<u>aa</u>lla<u>dz</u>ii min \'aduwwihi fawakazahu muus<u>aa</u> faqa<u>daa</u> \'alayhi q<u>aa</u>la h<u>aadzaa</u> min \'amali sysyay<u>th</u><u>aa</u>ni innahu \'aduwwun mu<u>dh</u>illun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (3268, '28:16', 28, 16, 'q<u>aa</u>la rabbi innii <u>zh</u>alamtu nafsii faghfir lii faghafara lahu innahu huwa lghafuuru rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (3269, '28:17', 28, 17, 'q<u>aa</u>la rabbi bim<u>aa</u> an\'amta \'alayya falan akuuna <u>zh</u>ahiiran lilmujrimiin\r\r\n');
