INSERT INTO quranLatin VALUES (3329, '28:77', 28, 77, 'wabtaghi fiim<u>aa</u> <u>aa</u>t<u>aa</u>kall<u>aa</u>hu dd<u>aa</u>ra l-<u>aa</u>khirata wal<u>aa</u> tansa na<u>sh</u>iibaka mina dduny<u>aa</u> wa-a<u>h</u>sin kam<u>aa</u> a<u>h</u>sanall<u>aa</u>hu ilayka wal<u>aa</u> tabghi lfas<u>aa</u>da fii l-ar<u>dh</u>i innall<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu lmufsidiin\r\r\n');
INSERT INTO quranLatin VALUES (3330, '28:78', 28, 78, 'q<u>aa</u>la innam<u>aa</u> uutiituhu \'al<u>aa</u> \'ilmin \'indii awa lam ya\'lam annall<u>aa</u>ha qad ahlaka min qablihi mina lquruuni man huwa asyaddu minhu quwwatan wa-aktsaru jam\'an wal<u>aa</u> yus-alu \'an <u>dz</u>unuubihimu lmujrimuun\r\r\n');
