INSERT INTO quranLatin VALUES (3334, '28:82', 28, 82, 'wa-a<u>sh</u>ba<u>h</u>alla<u>dz</u>iina tamannaw mak<u>aa</u>nahu bil-amsi yaquuluuna wayka-annall<u>aa</u>ha yabsu<u>th</u>u rrizqa liman yasy<u>aa</u>u min \'ib<u>aa</u>dihi wayaqdiru lawl<u>aa</u> an mannall<u>aa</u>hu \'alayn<u>aa</u> lakhasafa bin<u>aa</u> wayka-annahu l<u>aa</u> yufli<u>h</u>u lk<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (3335, '28:83', 28, 83, 'tilka dd<u>aa</u>ru l-<u>aa</u>khiratu naj\'aluh<u>aa</u> lilla<u>dz</u>iina l<u>aa</u> yuriiduuna \'uluwwan fii l-ar<u>dh</u>i wal<u>aa</u> fas<u>aa</u>dan wal\'<u>aa</u>qibatu lilmuttaqiin\r\r\n');
