INSERT INTO quranLatin VALUES (3367, '29:27', 29, 27, 'wawahabn<u>aa</u> lahu is<u>haa</u>qa waya\'quuba waja\'aln<u>aa</u> fii <u>dz</u>urriyyatihi nnubuwwata walkit<u>aa</u>ba wa<u>aa</u>tayn<u>aa</u>hu ajrahu fii dduny<u>aa</u> wa-innahu fii l-<u>aa</u>khirati lamina <u>shshaa</u>li<u>h</u>iin\r\r\n');
