INSERT INTO quranLatin VALUES (3382, '29:42', 29, 42, 'innall<u>aa</u>ha ya\'lamu m<u>aa</u> yad\'uuna min duunihi min syay-in wahuwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (3383, '29:43', 29, 43, 'watilka l-amts<u>aa</u>lu na<u>dh</u>ribuh<u>aa</u> linn<u>aa</u>si wam<u>aa</u> ya\'qiluh<u>aa</u> ill<u>aa</u> l\'<u>aa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (3384, '29:44', 29, 44, 'khalaqall<u>aa</u>hu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a bil<u>h</u>aqqi inna fii <u>dzaa</u>lika la<u>aa</u>yatan lilmu-miniin\r\r\n');
INSERT INTO quranLatin VALUES (3385, '29:45', 29, 45, 'utlu m<u>aa</u> uu<u>h</u>iya ilayka mina lkit<u>aa</u>bi wa-aqimi <u>shsh</u>al<u>aa</u>ta inna <u>shsh</u>al<u>aa</u>ta tanh<u>aa</u> \'ani lfa<u>h</u>sy<u>aa</u>-i walmunkari wala<u>dz</u>ikrull<u>aa</u>hi akbaru wal<u>aa</u>hu ya\'lamu m<u>aa</u> ta<u>sh</u>na\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3386, '29:46', 29, 46, 'wal<u>aa</u> tuj<u>aa</u>diluu ahla lkit<u>aa</u>bi ill<u>aa</u> billatii hiya a<u>h</u>sanu ill<u>aa</u>lla<u>dz</u>iina <u>zh</u>alamuu minhum waquuluu <u>aa</u>mann<u>aa</u> billa<u>dz</u>ii unzila ilayn<u>aa</u> waunzila ilaykum wa-il<u>aa</u>hun<u>aa</u> wa-il<u>aa</u>hukum w<u>aah</u>idun wana<u>h</u>nu lahu muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (3387, '29:47', 29, 47, 'waka<u>dzaa</u>lika anzaln<u>aa</u> ilayka lkit<u>aa</u>ba falla<u>dz</u>iina <u>aa</u>tayn<u>aa</u>humu lkit<u>aa</u>ba yu/minuuna bihi wamin h<u>aa</u>ul<u>aa</u>-i man yu/minu bihi wam<u>aa</u> yaj<u>h</u>adu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ill<u>aa</u> lk<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (3388, '29:48', 29, 48, 'wam<u>aa</u> kunta tatluu min qablihi min kit<u>aa</u>bin wal<u>aa</u> takhu<u>ththh</u>uhu biyamiinika i<u>dz</u>an lart<u>aa</u>ba lmub<u>th</u>iluun\r\r\n');
INSERT INTO quranLatin VALUES (3389, '29:49', 29, 49, 'bal huwa <u>aa</u>y<u>aa</u>tun bayyin<u>aa</u>tun fii <u>sh</u>uduurilla<u>dz</u>iina uutuu l\'ilma wam<u>aa</u> yaj<u>h</u>adu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ill<u>aa</u> <u>zhzhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (3390, '29:50', 29, 50, 'waq<u>aa</u>luu lawl<u>aa</u> unzila \'alayhi <u>aa</u>y<u>aa</u>tun min rabbihi qul innam<u>aa</u> l-<u>aa</u>y<u>aa</u>tu \'indall<u>aa</u>hi wa-innam<u>aa</u> an<u>aa</u> na<u>dz</u>iirun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (3391, '29:51', 29, 51, 'awa lam yakfihim ann<u>aa</u> anzaln<u>aa</u> \'alayka lkit<u>aa</u>ba yutl<u>aa</u> \'alayhim inna fii <u>dzaa</u>lika lara<u>h</u>matan wa<u>dz</u>ikr<u>aa</u> liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (3392, '29:52', 29, 52, 'qul kaf<u>aa</u> bil<u>aa</u>hi baynii wabaynakum syahiidan ya\'lamu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i walla<u>dz</u>iina <u>aa</u>manuu bilb<u>aath</u>ili wakafaruu bil<u>aa</u>hi ul<u>aa</u>-ika humu lkh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (3393, '29:53', 29, 53, 'wayasta\'jiluunaka bil\'a<u>dzaa</u>bi walawl<u>aa</u> ajalun musamman laj<u>aa</u>-ahumu l\'a<u>dzaa</u>bu walaya/tiyannahum baghtatan wahum l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (3394, '29:54', 29, 54, 'yasta\'jiluunaka bil\'a<u>dzaa</u>bi wa-inna jahannama lamu<u>h</u>ii<u>th</u>atun bilk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (3395, '29:55', 29, 55, 'yawma yaghsy<u>aa</u>humu l\'a<u>dzaa</u>bu min fawqihim wamin ta<u>h</u>ti arjulihim wayaquulu <u>dz</u>uuquu m<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (3396, '29:56', 29, 56, 'y<u>aa</u> \'ib<u>aa</u>diyalla<u>dz</u>iina <u>aa</u>manuu inna ar<u>dh</u>ii w<u>aa</u>si\'atun fa-iyy<u>aa</u>ya fa\'buduun\r\r\n');
INSERT INTO quranLatin VALUES (3397, '29:57', 29, 57, 'kullu nafsin <u>dzaa</u>-iqatu lmawti tsumma ilayn<u>aa</u> turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3398, '29:58', 29, 58, 'walla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti lanubawwi-annahum mina ljannati ghurafan tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> ni\'ma ajru l\'<u>aa</u>miliin\r\r\n');
INSERT INTO quranLatin VALUES (3399, '29:59', 29, 59, '<B>al</b>la<u>dz</u>iina <u>sh</u>abaruu wa\'al<u>aa</u> rabbihim yatawakkaluun\r\r\n');
INSERT INTO quranLatin VALUES (3400, '29:60', 29, 60, 'waka-ayyin min d<u>aa</u>bbatin l<u>aa</u> ta<u>h</u>milu rizqah<u>aa</u>ll<u>aa</u>hu yarzuquh<u>aa</u> wa-iyy<u>aa</u>kum wahuwa ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (3401, '29:61', 29, 61, 'wala-in sa-altahum man khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a wasakhkhara sysyamsa walqamara layaquulunnall<u>aa</u>hu fa-ann<u>aa</u> yu/fakuun\r\r\n');
INSERT INTO quranLatin VALUES (3402, '29:62', 29, 62, '<B>al</b>l<u>aa</u>hu yabsu<u>th</u>u rrizqa liman yasy<u>aa</u>u min \'ib<u>aa</u>dihi wayaqdiru lahu innall<u>aa</u>ha bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (3403, '29:63', 29, 63, 'wala-in sa-altahum man nazzala mina ssam<u>aa</u>-i m<u>aa</u>-an fa-a<u>h</u>y<u>aa</u> bihi l-ar<u>dh</u>a min ba\'di mawtih<u>aa</u> layaquulunnall<u>aa</u>hu quli l<u>h</u>amdu lill<u>aa</u>hi bal aktsaruhum l<u>aa</u> ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (3404, '29:64', 29, 64, 'wam<u>aa</u> h<u>aadz</u>ihi l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> ill<u>aa</u> lahwun wala\'ibun wa-inna dd<u>aa</u>ra l-<u>aa</u>khirata lahiya l<u>h</u>ayaw<u>aa</u>nu law k<u>aa</u>nuu ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3405, '29:65', 29, 65, 'fa-i<u>dzaa</u> rakibuu fii lfulki da\'awuull<u>aa</u>ha mukhli<u>sh</u>iina lahu ddiina falamm<u>aa</u> najj<u>aa</u>hum il<u>aa</u> lbarri i<u>dzaa</u> hum yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (3406, '29:66', 29, 66, 'liyakfuruu bim<u>aa</u> <u>aa</u>tayn<u>aa</u>hum waliyatamatta\'uu fasawfa ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3407, '29:67', 29, 67, 'awa lam yaraw ann<u>aa</u> ja\'aln<u>aa</u> <u>h</u>araman <u>aa</u>minan wayutakha<u>ththh</u>afu nn<u>aa</u>su min <u>h</u>awlihim afabilb<u>aath</u>ili yu/minuuna wabini\'matill<u>aa</u>hi yakfuruun\r\r\n');
INSERT INTO quranLatin VALUES (3408, '29:68', 29, 68, 'waman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>iban aw ka<u>dzdz</u>aba bil<u>h</u>aqqi lamm<u>aa</u> j<u>aa</u>-ahu alaysa fii jahannama matswan lilk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (3409, '29:69', 29, 69, 'walla<u>dz</u>iina j<u>aa</u>haduu fiin<u>aa</u> lanahdiyannahum subulan<u>aa</u> wa-innall<u>aa</u>ha lama\'a lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (3410, '30:1', 30, 1, 'alif-l<u>aa</u>m-miim</u>\r\r\n');
INSERT INTO quranLatin VALUES (3411, '30:2', 30, 2, 'ghulibati rruum\r\r\n');
INSERT INTO quranLatin VALUES (3412, '30:3', 30, 3, 'fii adn<u>aa</u> l-ar<u>dh</u>i wahum min ba\'di ghalabihim sayaghlibuun\r\r\n');
INSERT INTO quranLatin VALUES (3413, '30:4', 30, 4, 'fii bi<u>dh</u>\'i siniina lill<u>aa</u>hi l-amru min qablu wamin ba\'du wayawma-i<u>dz</u>in yafra<u>h</u>u lmu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (3414, '30:5', 30, 5, 'bina<u>sh</u>rill<u>aa</u>hi yan<u>sh</u>uru man yasy<u>aa</u>u wahuwa l\'aziizu rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (3415, '30:6', 30, 6, 'wa\'dall<u>aa</u>hi l<u>aa</u> yukhlifull<u>aa</u>hu wa\'dahu wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3416, '30:7', 30, 7, 'ya\'lamuuna <u>zhaa</u>hiran mina l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wahum \'ani l-<u>aa</u>khirati hum gh<u>aa</u>filuun\r\r\n');
INSERT INTO quranLatin VALUES (3417, '30:8', 30, 8, 'awa lam yatafakkaruu fii anfusihim m<u>aa</u> khalaqall<u>aa</u>hu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a wam<u>aa</u> baynahum<u>aa</u> ill<u>aa</u> bil<u>h</u>aqqi wa-ajalin musamman wa-inna katsiiran mina nn<u>aa</u>si biliq<u>aa</u>-i rabbihim lak<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (3418, '30:9', 30, 9, 'awa lam yasiiruu fii l-ar<u>dh</u>i fayan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatulla<u>dz</u>iina min qablihim k<u>aa</u>nuu asyadda minhum quwwatan wa-ats<u>aa</u>ruu l-ar<u>dh</u>a wa\'amaruuh<u>aa</u> aktsara mimm<u>aa</u> \'amaruuh<u>aa</u> waj<u>aa</u>-at-hum rusuluhum bilbayyin<u>aa</u>ti fam<u>aa</u> k<u>aa</u>nall<u>aa</u>hu liya<u>zh</u>limahum wal<u>aa</u>kin k<u>aa</u>nuu anfusahum ya<u>zh</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (3419, '30:10', 30, 10, 'tsumma k<u>aa</u>na \'<u>aa</u>qibatalla<u>dz</u>iina as<u>aa</u>uu ssuu-<u>aa</u> an ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi wak<u>aa</u>nuu bih<u>aa</u> yastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (3420, '30:11', 30, 11, '<B>al</b>l<u>aa</u>hu yabdau lkhalqa tsumma yu\'iiduhu tsumma ilayhi turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3421, '30:12', 30, 12, 'wayawma taquumu ss<u>aa</u>\'atu yublisu lmujrimuun\r\r\n');
INSERT INTO quranLatin VALUES (3422, '30:13', 30, 13, 'walam yakun lahum min syurak<u>aa</u>-ihim syufa\'<u>aa</u>u wak<u>aa</u>nuu bisyurak<u>aa</u>-ihim k<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (3423, '30:14', 30, 14, 'wayawma taquumu ss<u>aa</u>\'atu yawma-i<u>dz</u>in yatafarraquun\r\r\n');
INSERT INTO quranLatin VALUES (3424, '30:15', 30, 15, 'fa-amm<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti fahum fii raw<u>dh</u>atin yu<u>h</u>baruun\r\r\n');
INSERT INTO quranLatin VALUES (3425, '30:16', 30, 16, 'wa-amm<u>aa</u>lla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> waliq<u>aa</u>-i l-<u>aa</u>khirati faul<u>aa</u>-ika fii l\'a<u>dzaa</u>bi mu<u>hd</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (3426, '30:17', 30, 17, 'fasub<u>haa</u>nall<u>aa</u>hi <u>h</u>iina tumsuuna wa<u>h</u>iina tu<u>sh</u>bi<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (3427, '30:18', 30, 18, 'walahu l<u>h</u>amdu fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wa\'asyiyyan wa<u>h</u>iina tu<u>zh</u>hiruun\r\r\n');
INSERT INTO quranLatin VALUES (3428, '30:19', 30, 19, 'yukhriju l<u>h</u>ayya mina lmayyiti wayukhriju lmayyita mina l<u>h</u>ayyi wayu<u>h</u>yii l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> waka<u>dzaa</u>lika tukhrajuun\r\r\n');
INSERT INTO quranLatin VALUES (3429, '30:20', 30, 20, 'wamin <u>aa</u>y<u>aa</u>tihi an khalaqakum min tur<u>aa</u>bin tsumma i<u>dzaa</u> antum basyarun tantasyiruun\r\r\n');
INSERT INTO quranLatin VALUES (3430, '30:21', 30, 21, 'wamin <u>aa</u>y<u>aa</u>tihi an khalaqa lakum min anfusikum azw<u>aa</u>jan litaskunuu ilayh<u>aa</u> waja\'ala baynakum mawaddatan wara<u>h</u>matan inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yatafakkaruun\r\r\n');
INSERT INTO quranLatin VALUES (3431, '30:22', 30, 22, 'wamin <u>aa</u>y<u>aa</u>tihi khalqu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wakhtil<u>aa</u>fu lsinatikum wa-alw<u>aa</u>nikum inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin lil\'<u>aa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (3432, '30:23', 30, 23, 'wamin <u>aa</u>y<u>aa</u>tihi man<u>aa</u>mukum billayli wannah<u>aa</u>ri wabtigh<u>aa</u>ukum min fa<u>dh</u>lihi inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yasma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3433, '30:24', 30, 24, 'wamin <u>aa</u>y<u>aa</u>tihi yuriikumu lbarqa khawfan wa<u>th</u>ama\'an wayunazzilu mina ssam<u>aa</u>-i m<u>aa</u>-an fayu<u>h</u>yii bihi l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (3434, '30:25', 30, 25, 'wamin <u>aa</u>y<u>aa</u>tihi an taquuma ssam<u>aa</u>u wal-ar<u>dh</u>u bi-amrihi tsumma i<u>dzaa</u> da\'<u>aa</u>kum da\'watan mina l-ar<u>dh</u>i i<u>dzaa</u> antum takhrujuun\r\r\n');
INSERT INTO quranLatin VALUES (3435, '30:26', 30, 26, 'walahu man fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i kullun lahu q<u>aa</u>nituun\r\r\n');
INSERT INTO quranLatin VALUES (3436, '30:27', 30, 27, 'wahuwalla<u>dz</u>ii yabdau lkhalqa tsumma yu\'iiduhu wahuwa ahwanu \'alayhi walahu lmatsalu l-a\'l<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wahuwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (3437, '30:28', 30, 28, '<u>dh</u>araba lakum matsalan min anfusikum hal lakum mimm<u>aa</u> malakat aym<u>aa</u>nukum min syurak<u>aa</u>-a fii m<u>aa</u> razaqn<u>aa</u>kum fa-antum fiihi saw<u>aa</u>un takh<u>aa</u>fuunahum kakhiifatikum anfusakum ka<u>dzaa</u>lika nufa<u>shsh</u>ilu l-<u>aa</u>y<u>aa</u>ti liqawmin ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (3438, '30:29', 30, 29, 'bali ittaba\'alla<u>dz</u>iina <u>zh</u>alamuu ahw<u>aa</u>-ahum bighayri \'ilmin faman yahdii man a<u>dh</u>allall<u>aa</u>hu wam<u>aa</u> lahum min n<u>aas</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (3439, '30:30', 30, 30, 'fa-aqim wajhaka liddiini <u>h</u>aniifan fi<u>th</u>ratall<u>aa</u>hillatii fa<u>th</u>ara nn<u>aa</u>sa \'alayh<u>aa</u> l<u>aa</u> tabdiila likhalqill<u>aa</u>hi <u>dzaa</u>lika ddiinu lqayyimu wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3440, '30:31', 30, 31, 'muniibiina ilayhi wattaquuhu wa-aqiimuu <u>shsh</u>al<u>aa</u>ta wal<u>aa</u> takuunuu mina lmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (3441, '30:32', 30, 32, 'minalla<u>dz</u>iina farraquu diinahum wak<u>aa</u>nuu syiya\'an kullu <u>h</u>izbin bim<u>aa</u> ladayhim fari<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (3442, '30:33', 30, 33, 'wa-i<u>dzaa</u> massa nn<u>aa</u>sa <u>dh</u>urrun da\'aw rabbahum muniibiina ilayhi tsumma i<u>dzaa</u> a<u>dzaa</u>qahum minhu ra<u>h</u>matan i<u>dzaa</u> fariiqun minhum birabbihim yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (3443, '30:34', 30, 34, 'liyakfuruu bim<u>aa</u> <u>aa</u>tayn<u>aa</u>hum fatamatta\'uu fasawfa ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3444, '30:35', 30, 35, 'am anzaln<u>aa</u> \'alayhim sul<u>th</u><u>aa</u>nan fahuwa yatakallamu bim<u>aa</u> k<u>aa</u>nuu bihi yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (3445, '30:36', 30, 36, 'wa<u>dzaa</u> a<u>dz</u>aqn<u>aa</u> nn<u>aa</u>sa ra<u>h</u>matan fari<u>h</u>uu bih<u>aa</u> wa-in tu<u>sh</u>ibhum sayyi-atun bim<u>aa</u> qaddamat aydiihim i<u>dzaa</u> hum yaqna<u>th</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (3446, '30:37', 30, 37, 'awa lam yaraw annall<u>aa</u>ha yabsu<u>th</u>u rrizqa liman yasy<u>aa</u>u wayaqdiru inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (3447, '30:38', 30, 38, 'fa<u>aa</u>ti <u>dzaa</u> lqurb<u>aa</u> <u>h</u>aqqahu walmiskiina wabna ssabiili <u>dzaa</u>lika khayrun lilla<u>dz</u>iina yuriiduuna wajhall<u>aa</u>hi waul<u>aa</u>-ika humu lmufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (3448, '30:39', 30, 39, 'wam<u>aa</u> <u>aa</u>taytum min riban liyarbuwa fii amw<u>aa</u>li nn<u>aa</u>si fal<u>aa</u> yarbuu \'indall<u>aa</u>hi wam<u>aa</u> <u>aa</u>taytum min zak<u>aa</u>tin turiiduuna wajhall<u>aa</u>hi faul<u>aa</u>-ika humu lmu<u>dh</u>\'ifuun\r\r\n');
INSERT INTO quranLatin VALUES (3449, '30:40', 30, 40, '<B>al</b>l<u>aa</u>hulla<u>dz</u>ii khalaqakum tsumma razaqakum tsumma yumiitukum tsumma yu<u>h</u>yiikum hal min syurak<u>aa</u>-ikum man yaf\'alu min <u>dzaa</u>likum min syay-in sub<u>haa</u>nahu wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (3450, '30:41', 30, 41, '<u>zh</u>ahara lfas<u>aa</u>du fii lbarri walba<u>h</u>ri bim<u>aa</u> kasabat aydii nn<u>aa</u>si liyu<u>dz</u>iiqahum ba\'<u>dh</u>alla<u>dz</u>ii \'amiluu la\'allahum yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3451, '30:42', 30, 42, 'qul siiruu fii l-ar<u>dh</u>i fan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatulla<u>dz</u>iina min qablu k<u>aa</u>na aktsaruhum musyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (3452, '30:43', 30, 43, 'fa-aqim wajhaka liddiini lqayyimi min qabli an ya/tiya yawmun l<u>aa</u> maradda lahu minall<u>aa</u>hi yawma-i<u>dz</u>in ya<u>shsh</u>adda\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3453, '30:44', 30, 44, 'man kafara fa\'alayhi kufruhu waman \'amila <u>shaa</u>li<u>h</u>an fali-anfusihim yamhaduun\r\r\n');
INSERT INTO quranLatin VALUES (3454, '30:45', 30, 45, 'liyajziyalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti min fa<u>dh</u>lihi innahu l<u>aa</u> yu<u>h</u>ibbu lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (3455, '30:46', 30, 46, 'wamin <u>aa</u>y<u>aa</u>tihi an yursila rriy<u>aah</u>a mubasysyir<u>aa</u>tin waliyu<u>dz</u>iiqakum min ra<u>h</u>matihi walitajriya lfulku bi-amrihi walitabtaghuu min fa<u>dh</u>lihi wala\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (3456, '30:47', 30, 47, 'walaqad arsaln<u>aa</u> min qablika rusulan il<u>aa</u> qawmihim faj<u>aa</u>uuhum bilbayyin<u>aa</u>ti fantaqamn<u>aa</u> minalla<u>dz</u>iina ajramuu wak<u>aa</u>na <u>h</u>aqqan \'alayn<u>aa</u> na<u>sh</u>ru lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (3457, '30:48', 30, 48, '<B>al</b>l<u>aa</u>hulla<u>dz</u>ii yursilu rriy<u>aah</u>a fatutsiiru sa<u>haa</u>ban fayabsu<u>th</u>uhu fii ssam<u>aa</u>-i kayfa yasy<u>aa</u>u wayaj\'aluhu kisafan fatar<u>aa</u> lwadqa yakhruju min khil<u>aa</u>lihi fa-i<u>dzaa</u> a<u>shaa</u>ba bihi man yasy<u>aa</u>u min \'ib<u>aa</u>dihi i<u>dzaa</u> hum yastabsyiruun\r\r\n');
INSERT INTO quranLatin VALUES (3458, '30:49', 30, 49, 'wa-in k<u>aa</u>nuu min qabli an yunazzala \'alayhim min qablihi lamublisiin\r\r\n');
INSERT INTO quranLatin VALUES (3459, '30:50', 30, 50, 'fan<u>zh</u>ur il<u>aa</u> <u>aa</u>ts<u>aa</u>ri ra<u>h</u>matill<u>aa</u>hi kayfa yu<u>h</u>yii l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> inna <u>dzaa</u>lika lamu<u>h</u>yii lmawt<u>aa</u> wahuwa \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (3460, '30:51', 30, 51, 'wala-in arsaln<u>aa</u> rii<u>h</u>an fara-awhu mu<u>sh</u>farran la<u>zh</u>alluu min ba\'dihi yakfuruun\r\r\n');
INSERT INTO quranLatin VALUES (3461, '30:52', 30, 52, 'fa-innaka l<u>aa</u> tusmi\'u lmawt<u>aa</u> wal<u>aa</u> tusmi\'u <u>shsh</u>umma ddu\'<u>aa</u>-a i<u>dzaa</u> wallaw mudbiriin\r\r\n');
INSERT INTO quranLatin VALUES (3462, '30:53', 30, 53, 'wam<u>aa</u> anta bih<u>aa</u>di l\'umyi \'an <u>dh</u>al<u>aa</u>latihim in tusmi\'u ill<u>aa</u> man yu/minu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> fahum muslimuun\r\r\n');
INSERT INTO quranLatin VALUES (3463, '30:54', 30, 54, '<B>al</b>l<u>aa</u>hulla<u>dz</u>ii khalaqakum min <u>dh</u>a\'fin tsumma ja\'ala min ba\'di <u>dh</u>a\'fin quwwatan tsumma ja\'ala min ba\'di quwwatin <u>dh</u>a\'fan wasyaybatan yakhluqu m<u>aa</u> yasy<u>aa</u>u wahuwa l\'aliimu lqadiir\r\r\n');
INSERT INTO quranLatin VALUES (3464, '30:55', 30, 55, 'wayawma taquumu ss<u>aa</u>\'atu yuqsimu lmujrimuuna m<u>aa</u> labitsuu ghayra s<u>aa</u>\'atin ka<u>dzaa</u>lika k<u>aa</u>nuu yu/fakuun\r\r\n');
INSERT INTO quranLatin VALUES (3465, '30:56', 30, 56, 'waq<u>aa</u>lalla<u>dz</u>iina uutuu l\'ilma wal-iim<u>aa</u>na laqad labitstum fii kit<u>aa</u>bill<u>aa</u>hi il<u>aa</u> yawmi lba\'tsi fah<u>aadzaa</u> yawmu lba\'tsi wal<u>aa</u>kinnakum kuntum l<u>aa</u> ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3466, '30:57', 30, 57, 'fayawma-i<u>dz</u>in l<u>aa</u> yanfa\'ulla<u>dz</u>iina <u>zh</u>alamuu ma\'<u>dz</u>iratuhum wal<u>aa</u> hum yusta\'tabuun\r\r\n');
INSERT INTO quranLatin VALUES (3467, '30:58', 30, 58, 'walaqad <u>dh</u>arabn<u>aa</u> linn<u>aa</u>si fii h<u>aadzaa</u> lqur-<u>aa</u>ni min kulli matsalin wala-in ji/tahum bi-<u>aa</u>yatin layaquulannalla<u>dz</u>iina kafaruu in antum ill<u>aa</u> mub<u>th</u>iluun\r\r\n');
INSERT INTO quranLatin VALUES (3468, '30:59', 30, 59, 'ka<u>dzaa</u>lika ya<u>th</u>ba\'ull<u>aa</u>hu \'al<u>aa</u> quluubilla<u>dz</u>iina l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3469, '30:60', 30, 60, 'fa<u>sh</u>bir inna wa\'dall<u>aa</u>hi <u>h</u>aqqun wal<u>aa</u> yastakhiffannakalla<u>dz</u>iina l<u>aa</u> yuuqinuun\r\r\n');
INSERT INTO quranLatin VALUES (3470, '31:1', 31, 1, 'alif-l<u>aa</u>m-miim</u>\r\r\n');
INSERT INTO quranLatin VALUES (3471, '31:2', 31, 2, 'tilka <u>aa</u>y<u>aa</u>tu lkit<u>aa</u>bi l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (3472, '31:3', 31, 3, 'hudan wara<u>h</u>matan lilmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (3473, '31:4', 31, 4, '<B>al</b>la<u>dz</u>iina yuqiimuuna <u>shsh</u>al<u>aa</u>ta wayu/tuuna zzak<u>aa</u>ta wahum bil-<u>aa</u>khirati hum yuuqinuun\r\r\n');
INSERT INTO quranLatin VALUES (3474, '31:5', 31, 5, 'ul<u>aa</u>-ika \'al<u>aa</u> hudan min rabbihim waul<u>aa</u>-ika humu lmufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (3475, '31:6', 31, 6, 'wamina nn<u>aa</u>si man yasytarii lahwa l<u>h</u>adiitsi liyu<u>dh</u>illa \'an sabiilill<u>aa</u>hi bighayri \'ilmin wayattakhi<u>dz</u>ah<u>aa</u> huzuwan ul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun muhiin\r\r\n');
INSERT INTO quranLatin VALUES (3476, '31:7', 31, 7, 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhi <u>aa</u>y<u>aa</u>tun<u>aa</u> wall<u>aa</u> mustakbiran ka-an lam yasma\'h<u>aa</u> ka-anna fii u<u>dz</u>unayhi waqran fabasysyirhu bi\'a<u>dzaa</u>bin liim\r\r\n');
INSERT INTO quranLatin VALUES (3477, '31:8', 31, 8, 'innalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti lahum jann<u>aa</u>tu nna\'iim\r\r\n');
INSERT INTO quranLatin VALUES (3478, '31:9', 31, 9, 'kh<u>aa</u>lidiina fiih<u>aa</u> wa\'dall<u>aa</u>hi <u>h</u>aqqan wahuwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (3479, '31:10', 31, 10, 'khalaqa ssam<u>aa</u>w<u>aa</u>ti bighayri \'amadin tarawnah<u>aa</u> wa-alq<u>aa</u> fii l-ar<u>dh</u>i raw<u>aa</u>siya an tamiida bikum wabatstsa fiih<u>aa</u> min kulli d<u>aa</u>bbatin wa-anzaln<u>aa</u> mina ssam<u>aa</u>-i m<u>aa</u>-an fa-anbatn<u>aa</u> fiih<u>aa</u> min kulli zawjin kariim\r\r\n');
INSERT INTO quranLatin VALUES (3480, '31:11', 31, 11, 'h<u>aadzaa</u> khalqull<u>aa</u>hi fa-aruunii m<u>aatsaa</u> khalaqalla<u>dz</u>iina min duunihi bali <u>zhzhaa</u>limuuna fii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (3481, '31:12', 31, 12, 'walaqad <u>aa</u>tayn<u>aa</u> luqm<u>aa</u>na l<u>h</u>ikmata ani usykur lill<u>aa</u>hi waman yasykur fa-innam<u>aa</u> yasykuru linafsihi waman kafara fa-innall<u>aa</u>ha ghaniyyun <u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (3482, '31:13', 31, 13, 'wa-i<u>dz</u> q<u>aa</u>la luqm<u>aa</u>nu libnihi wahuwa ya\'i<u>zh</u>uhu y<u>aa</u> bunayya l<u>aa</u> tusyrik bil<u>aa</u>hi inna sysyirka la<u>zh</u>ulmun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (3483, '31:14', 31, 14, 'wawa<u>shsh</u>ayn<u>aa</u> l-ins<u>aa</u>na biw<u>aa</u>lidayhi <u>h</u>amalat-hu ummuhu wahnan \'al<u>aa</u> wahnin wafi<u>shaa</u>luhu fii \'<u>aa</u>mayni ani usykur lii waliw<u>aa</u>lidayka ilayya lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (3484, '31:15', 31, 15, 'wa-in j<u>aa</u>had<u>aa</u>ka \'al<u>aa</u> an tusyrika bii m<u>aa</u> laysa laka bihi \'ilmun fal<u>aa</u> tu<u>th</u>i\'hum<u>aa</u> wa<u>shaah</u>ibhum<u>aa</u> fii dduny<u>aa</u> ma\'ruufan wattabi\'sabiila man an<u>aa</u>ba ilayya tsumma ilayya marji\'ukum fa-unabbi-ukum bim<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (3485, '31:16', 31, 16, 'y<u>aa</u> bunayya innah<u>aa</u> in taku mitsq<u>aa</u>la <u>h</u>abbatin min khardalin fatakun fii <u>sh</u>akhratin aw fii ssam<u>aa</u>w<u>aa</u>ti aw fii l-ar<u>dh</u>i ya/ti bih<u>aa</u>ll<u>aa</u>hu innall<u>aa</u>ha la<u>th</u>iifun khabiir\r\r\n');
INSERT INTO quranLatin VALUES (3486, '31:17', 31, 17, 'y<u>aa</u> bunayya aqimi <u>shsh</u>al<u>aa</u>ta wa/mur bilma\'ruufi wanha \'ani lmunkari wa<u>sh</u>bir \'al<u>aa</u> m<u>aa</u> a<u>shaa</u>baka inna <u>dzaa</u>lika min \'azmi l-umuur\r\r\n');
INSERT INTO quranLatin VALUES (3487, '31:18', 31, 18, 'wal<u>aa</u> tu<u>sh</u>a\'\'ir khaddaka linn<u>aa</u>si wal<u>aa</u> tamsyi fii l-ar<u>dh</u>i mara<u>h</u>an innall<u>aa</u>ha l<u>aa</u> yu<u>h</u>ibbu kulla mukht<u>aa</u>lin fakhuur\r\r\n');
INSERT INTO quranLatin VALUES (3488, '31:19', 31, 19, 'waq<u>sh</u>id fii masyyika wagh<u>dh</u>u<u>dh</u> min <u>sh</u>awtika inna ankara l-a<u>sh</u>w<u>aa</u>ti la<u>sh</u>awtu l<u>h</u>amiir\r\r\n');
INSERT INTO quranLatin VALUES (3489, '31:20', 31, 20, 'alam taraw annall<u>aa</u>ha sakhkhara lakum m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wa-asbagha \'alaykum ni\'amahu <u>zhaa</u>hiratan wab<u>aath</u>inatan wamina nn<u>aa</u>si man yuj<u>aa</u>dilu fiill<u>aa</u>hi bighayri \'ilmin wal<u>aa</u> hudan wal<u>aa</u> kit<u>aa</u>bin muniir\r\r\n');
INSERT INTO quranLatin VALUES (3490, '31:21', 31, 21, 'wa-i<u>dzaa</u> qiila lahumu ittabi\'uu m<u>aa</u> anzalall<u>aa</u>hu q<u>aa</u>luu bal nattabi\'u m<u>aa</u> wajadn<u>aa</u> \'alayhi <u>aa</u>b<u>aa</u>-an<u>aa</u> awa law k<u>aa</u>na sysyay<u>th</u><u>aa</u>nu yad\'uuhum il<u>aa</u> \'a<u>dzaa</u>bi ssa\'iir\r\r\n');
INSERT INTO quranLatin VALUES (3491, '31:22', 31, 22, 'waman yuslim wajhahu il<u>aa</u>ll<u>aa</u>hi wahuwa mu<u>h</u>sinun faqadi istamsaka bil\'urwati lwutsq<u>aa</u> wa-il<u>aa</u>ll<u>aa</u>hi \'<u>aa</u>qibatu l-umuur\r\r\n');
INSERT INTO quranLatin VALUES (3492, '31:23', 31, 23, 'waman kafara fal<u>aa</u> ya<u>h</u>zunka kufruhu ilayn<u>aa</u> marji\'uhum fanunabbi-uhum bim<u>aa</u> \'amiluu innall<u>aa</u>ha \'aliimun bi<u>dzaa</u>ti <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (3493, '31:24', 31, 24, 'numatti\'uhum qaliilan tsumma na<u>dth</u>arruhum il<u>aa</u> \'a<u>dzaa</u>bin ghalii<u>zh</u>\r\r\n');
INSERT INTO quranLatin VALUES (3494, '31:25', 31, 25, 'wala-in sa-altahum man khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a layaquulunnall<u>aa</u>hu quli l<u>h</u>amdu lill<u>aa</u>hi bal aktsaruhum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3495, '31:26', 31, 26, 'lill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i innall<u>aa</u>ha huwa lghaniyyu l<u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (3496, '31:27', 31, 27, 'walaw annam<u>aa</u> fii l-ar<u>dh</u>i min syajaratin aql<u>aa</u>mun walba<u>h</u>ru yamudduhu min ba\'dihi sab\'atu ab<u>h</u>urin m<u>aa</u> nafidat kalim<u>aa</u>tull<u>aa</u>hi innall<u>aa</u>ha \'aziizun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (3497, '31:28', 31, 28, 'm<u>aa</u> khalqukum wal<u>aa</u> ba\'tsukum ill<u>aa</u> kanafsin w<u>aah</u>idatin innall<u>aa</u>ha samii\'un ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (3498, '31:29', 31, 29, 'alam tara annall<u>aa</u>ha yuulijullayla fii nnah<u>aa</u>ri wayuuliju nnah<u>aa</u>ra fiillayli wasakhkhara sysyamsa walqamara kullun yajrii il<u>aa</u> ajalin musamman wa-annall<u>aa</u>ha bim<u>aa</u> ta\'maluuna khabiir\r\r\n');
INSERT INTO quranLatin VALUES (3499, '31:30', 31, 30, '<u>dzaa</u>lika bi-annall<u>aa</u>ha huwa l<u>h</u>aqqu wa-anna m<u>aa</u> yad\'uuna min duunihi lb<u>aath</u>ilu wa-annall<u>aa</u>ha huwa l\'aliyyu lkabiir\r\r\n');
INSERT INTO quranLatin VALUES (3500, '31:31', 31, 31, 'alam tara anna lfulka tajrii fii lba<u>h</u>ri bini\'matill<u>aa</u>hi liyuriyakum min <u>aa</u>y<u>aa</u>tihi inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin likulli <u>sh</u>abb<u>aa</u>rin syakuur\r\r\n');
INSERT INTO quranLatin VALUES (3501, '31:32', 31, 32, 'wa-i<u>dzaa</u> ghasyiyahum mawjun ka<u>zhzh</u>ulali da\'awuull<u>aa</u>ha mukhli<u>sh</u>iina lahu ddiina falamm<u>aa</u> najj<u>aa</u>hum il<u>aa</u> lbarri faminhum muqta<u>sh</u>idun wam<u>aa</u> yaj<u>h</u>adu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ill<u>aa</u> kullu khatt<u>aa</u>rin kafuur\r\r\n');
INSERT INTO quranLatin VALUES (3502, '31:33', 31, 33, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su ittaquu rabbakum wakhsyaw yawman l<u>aa</u> yajzii w<u>aa</u>lidun \'an waladihi wal<u>aa</u> mawluudun huwa j<u>aa</u>zin \'an w<u>aa</u>lidihi syay-an inna wa\'dall<u>aa</u>hi <u>h</u>aqqun fal<u>aa</u> taghurrannakumu l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> wal<u>aa</u> yaghurrannakum bil<u>aa</u>hi lgharuur\r\r\n');
INSERT INTO quranLatin VALUES (3503, '31:34', 31, 34, 'innall<u>aa</u>ha \'indahu \'ilmu ss<u>aa</u>\'ati wayunazzilu lghaytsa waya\'lamu m<u>aa</u> fii l-ar<u>haa</u>mi wam<u>aa</u> tadrii nafsun m<u>aatsaa</u> taksibu ghadan wam<u>aa</u> tadrii nafsun bi-ayyi ar<u>dh</u>in tamuutu innall<u>aa</u>ha \'aliimun khabiir<dir>\r\r\n');
INSERT INTO quranLatin VALUES (3504, '32:1', 32, 1, 'alif-l<u>aa</u>m-miim</u>\r\r\n');
INSERT INTO quranLatin VALUES (3505, '32:2', 32, 2, 'tanziilu lkit<u>aa</u>bi l<u>aa</u> rayba fiihi min rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (3506, '32:3', 32, 3, 'am yaquuluuna iftar<u>aa</u>hu bal huwa l<u>h</u>aqqu min rabbika litun<u>dz</u>ira qawman m<u>aa</u> at<u>aa</u>hum min na<u>dz</u>iirin min qablika la\'allahum yahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (3507, '32:4', 32, 4, '<B>al</b>l<u>aa</u>hulla<u>dz</u>ii khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a wam<u>aa</u> baynahum<u>aa</u> fii sittati ayy<u>aa</u>min tsumma istaw<u>aa</u> \'al<u>aa</u> l\'arsyi m<u>aa</u> lakum min duunihi min waliyyin wal<u>aa</u> syafii\'in afal<u>aa</u> tata<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (3508, '32:5', 32, 5, 'yudabbiru l-amra mina ssam<u>aa</u>-i il<u>aa</u> l-ar<u>dh</u>i tsumma ya\'ruju ilayhi fii yawmin k<u>aa</u>na miqd<u>aa</u>ruhu lfa sanatin mimm<u>aa</u> ta\'udduun\r\r\n');
INSERT INTO quranLatin VALUES (3509, '32:6', 32, 6, '<u>dzaa</u>lika \'<u>aa</u>limu lghaybi wasysyah<u>aa</u>dati l\'aziizu rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (3510, '32:7', 32, 7, '<B>al</b>la<u>dz</u>ii a<u>h</u>sana kulla syay-in khalaqahu wabada-a khalqa l-ins<u>aa</u>ni min <u>th</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (3511, '32:8', 32, 8, 'tsumma ja\'ala naslahu min sul<u>aa</u>latin min m<u>aa</u>-in mahiin\r\r\n');
INSERT INTO quranLatin VALUES (3512, '32:9', 32, 9, 'tsumma saww<u>aa</u>hu wanafakha fiihi min ruu<u>h</u>ihi waja\'ala lakumu ssam\'a wal-ab<u>shaa</u>ra wal-af-idata qaliilan m<u>aa</u> tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (3513, '32:10', 32, 10, 'waq<u>aa</u>luu a-i<u>dzaa</u> <u>dh</u>al<u>aa</u>ln<u>aa</u> fii l-ar<u>dh</u>i a-inn<u>aa</u> lafii khalqin jadiidin bal hum biliq<u>aa</u>-i rabbihim k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (3514, '32:11', 32, 11, 'qul yatawaff<u>aa</u>kum malaku lmawtilla<u>dz</u>ii wukkila bikum tsumma il<u>aa</u> rabbikum turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3515, '32:12', 32, 12, 'walaw tar<u>aa</u> i<u>dz</u>i lmujrimuuna n<u>aa</u>kisuu ruuusihim \'inda rabbihim rabban<u>aa</u> ab<u>sh</u>arn<u>aa</u> wasami\'n<u>aa</u> fa-arji\'n<u>aa</u> na\'mal <u>shaa</u>li<u>h</u>an inn<u>aa</u> muuqinuun\r\r\n');
INSERT INTO quranLatin VALUES (3516, '32:13', 32, 13, 'walaw syi-n<u>aa</u> la<u>aa</u>tayn<u>aa</u> kulla nafsin hud<u>aa</u>h<u>aa</u> wal<u>aa</u>kin <u>h</u>aqqa lqawlu minnii la-amla-anna jahannama mina ljinnati wann<u>aa</u>si ajma\'iin\r\r\n');
INSERT INTO quranLatin VALUES (3517, '32:14', 32, 14, 'fa<u>dz</u>uuquu bim<u>aa</u> nasiitum liq<u>aa</u>-a yawmikum h<u>aadzaa</u> inn<u>aa</u> nasiin<u>aa</u>kum wa<u>dz</u>uuquu \'a<u>dzaa</u>ba lkhuldi bim<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (3518, '32:15', 32, 15, 'innam<u>aa</u> yu/minu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u>lla<u>dz</u>iina i<u>dzaa</u> <u>dz</u>ukkiruu bih<u>aa</u> kharruu sujjadan wasabba<u>h</u>uu bi<u>h</u>amdi rabbihim wahum l<u>aa</u> yastakbiruun\r\r\n');
INSERT INTO quranLatin VALUES (3519, '32:16', 32, 16, 'tataj<u>aa</u>f<u>aa</u> junuubuhum \'ani lma<u>daa</u>ji\'i yad\'uuna rabbahum khawfan wa<u>th</u>ama\'an wamimm<u>aa</u> razaqn<u>aa</u>hum yunfiquun\r\r\n');
INSERT INTO quranLatin VALUES (3520, '32:17', 32, 17, 'fal<u>aa</u> ta\'lamu nafsun m<u>aa</u> ukhfiya lahum min qurrati a\'yunin jaz<u>aa</u>-an bim<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (3521, '32:18', 32, 18, 'afaman k<u>aa</u>na mu/minan kaman k<u>aa</u>na f<u>aa</u>siqan l<u>aa</u> yastawuun\r\r\n');
INSERT INTO quranLatin VALUES (3522, '32:19', 32, 19, 'amm<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti falahum jann<u>aa</u>tu lma/w<u>aa</u> nuzulan bim<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (3523, '32:20', 32, 20, 'wa-amm<u>aa</u>lla<u>dz</u>iina fasaquu fama/w<u>aa</u>humu nn<u>aa</u>ru kullam<u>aa</u> ar<u>aa</u>duu an yakhrujuu minh<u>aa</u> u\'iiduu fiih<u>aa</u> waqiila lahum <u>dz</u>uuquu \'a<u>dzaa</u>ba nn<u>aa</u>rilla<u>dz</u>ii kuntum bihi tuka<u>dzdz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (3524, '32:21', 32, 21, 'walanu<u>dz</u>iiqannahum mina l\'a<u>dzaa</u>bi l-adn<u>aa</u> duuna l\'a<u>dzaa</u>bi l-akbari la\'allahum yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3525, '32:22', 32, 22, 'waman a<u>zh</u>lamu mimman <u>dz</u>ukkira bi-<u>aa</u>y<u>aa</u>ti rabbihi tsumma a\'ra<u>dh</u>a \'anh<u>aa</u> inn<u>aa</u> mina lmujrimiina muntaqimuun\r\r\n');
INSERT INTO quranLatin VALUES (3526, '32:23', 32, 23, 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> lkit<u>aa</u>ba fal<u>aa</u> takun fii miryatin min liq<u>aa</u>-ihi waja\'aln<u>aa</u>hu hudan libanii isr<u>aa</u>-iil\r\r\n');
INSERT INTO quranLatin VALUES (3527, '32:24', 32, 24, 'waja\'aln<u>aa</u> minhum a-immatan yahduuna bi-amrin<u>aa</u> lamm<u>aa</u> <u>sh</u>abaruu wak<u>aa</u>nuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> yuuqinuun\r\r\n');
INSERT INTO quranLatin VALUES (3528, '32:25', 32, 25, 'inna rabbaka huwa yaf<u>sh</u>ilu baynahum yawma lqiy<u>aa</u>mati fiim<u>aa</u> k<u>aa</u>nuu fiihi yakhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (3529, '32:26', 32, 26, 'awa lam yahdi lahum kam ahlakn<u>aa</u> min qablihim mina lquruuni yamsyuuna fii mas<u>aa</u>kinihim inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin afal<u>aa</u> yasma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3530, '32:27', 32, 27, 'awa lam yaraw ann<u>aa</u> nasuuqu lm<u>aa</u>-a il<u>aa</u> l-ar<u>dh</u>i ljuruzi fanukhriju bihi zar\'an ta-kulu minhu an\'<u>aa</u>muhum wa-anfusuhum afal<u>aa</u> yub<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (3531, '32:28', 32, 28, 'wayaquuluuna mat<u>aa</u> h<u>aadzaa</u> lfat<u>h</u>u in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (3532, '32:29', 32, 29, 'qul yawma lfat<u>h</u>i l<u>aa</u> yanfa\'ulla<u>dz</u>iina kafaruu iim<u>aa</u>nuhum wal<u>aa</u> hum yun<u>zh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (3533, '32:30', 32, 30, 'fa-a\'ri<u>dh</u> \'anhum wanta<u>zh</u>ir innahum munta<u>zh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (3534, '33:1', 33, 1, 'y<u>aa</u> ayyuh<u>aa</u> nnabiyyu ittaqill<u>aa</u>ha wal<u>aa</u> tu<u>th</u>i\'i lk<u>aa</u>firiina walmun<u>aa</u>fiqiina innall<u>aa</u>ha k<u>aa</u>na \'aliiman <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3535, '33:2', 33, 2, 'wattabi\'m<u>aa</u> yuu<u>haa</u> ilayka min rabbika innall<u>aa</u>ha k<u>aa</u>na bim<u>aa</u> ta\'maluuna khabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3536, '33:3', 33, 3, 'watawakkal \'al<u>aa</u>ll<u>aa</u>hi wakaf<u>aa</u> bil<u>aa</u>hi wakiil<u>aa</u><b>n </b>\r\r\n');
INSERT INTO quranLatin VALUES (3537, '33:4', 33, 4, 'm<u>aa</u> ja\'alall<u>aa</u>hu lirajulin min qalbayni fii jawfihi wam<u>aa</u> ja\'ala azw<u>aa</u>jakumull<u>aa</u>-ii tu<u>zhaa</u>hiruuna minhunna ummah<u>aa</u>tikum wam<u>aa</u> ja\'ala ad\'iy<u>aa</u>-akum abn<u>aa</u>-akum <u>dzaa</u>likum qawlukum bi-afw<u>aa</u>hikum wal<u>aa</u>hu yaquulu l<u>h</u>aqqa wahuwa yahdii ssabiil\r\r\n');
INSERT INTO quranLatin VALUES (3538, '33:5', 33, 5, 'ud\'uuhum li-<u>aa</u>b<u>aa</u>-ihim huwa aqsa<u>th</u>u \'indall<u>aa</u>hi fa-in lam ta\'lamuu <u>aa</u>b<u>aa</u>-ahum fa-ikhw<u>aa</u>nukum fii ddiini wamaw<u>aa</u>liikum walaysa \'alaykum jun<u>aah</u>un fiim<u>aa</u> akh<u>th</u>a/tum bihi wal<u>aa</u>kin m<u>aa</u> ta\'ammadat quluubukum wak<u>aa</u>nall<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3539, '33:6', 33, 6, 'annabiyyu awl<u>aa</u> bilmu/miniina min anfusihim wa-azw<u>aa</u>juhu ummah<u>aa</u>tuhum wauluu l-ar<u>haa</u>mi ba\'<u>dh</u>uhum awl<u>aa</u> biba\'<u>dh</u>in fii kit<u>aa</u>bill<u>aa</u>hi mina lmu/miniina walmuh<u>aa</u>jiriina ill<u>aa</u> an taf\'aluu il<u>aa</u> awliy<u>aa</u>-ikum ma\'ruufan k<u>aa</u>na <u>dzaa</u>lika fii lkit<u>aa</u>bi mas<u>th</u>uur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3540, '33:7', 33, 7, 'wa-i<u>dz</u> akha<u>dz</u>n<u>aa</u> mina nnabiyyiina miits<u>aa</u>qahum waminka wamin nuu<u>h</u>in wa-ibr<u>aa</u>hiima wamuus<u>aa</u> wa\'iis<u>aa</u> ibni maryama wa-akha<u>dz</u>n<u>aa</u> minhum miits<u>aa</u>qan ghalii<u>zhaa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3541, '33:8', 33, 8, 'liyas-ala <u>shshaa</u>diqiina \'an <u>sh</u>idqihim wa-a\'adda lilk<u>aa</u>firiina \'a<u>dzaa</u>ban liim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3542, '33:9', 33, 9, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu u<u>dz</u>kuruu ni\'matall<u>aa</u>hi \'alaykum i<u>dz</u> j<u>aa</u>-atkum junuudun fa-arsaln<u>aa</u> \'alayhim rii<u>h</u>an wajunuudan lam tarawh<u>aa</u> wak<u>aa</u>nall<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3543, '33:10', 33, 10, 'i<u>dz</u> j<u>aa</u>uukum min fawqikum wamin asfala minkum wa-i<u>dz</u> z<u>aa</u>ghati l-ab<u>shaa</u>ru wabalaghati lquluubu l<u>h</u>an<u>aa</u>jira wata<u>zh</u>unnuuna bil<u>aa</u>hi <u>zhzh</u>unuun<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3544, '33:11', 33, 11, 'hun<u>aa</u>lika ibtuliya lmu-minuuna wazulziluu zilz<u>aa</u>lan syadiid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3545, '33:12', 33, 12, 'wa-i<u>dz</u> yaquulu lmun<u>aa</u>fiquuna walla<u>dz</u>iina fii quluubihim mara<u>dh</u>un m<u>aa</u> wa\'adan<u>aa</u>ll<u>aa</u>hu warasuuluhu ill<u>aa</u> ghuruur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3546, '33:13', 33, 13, 'wa-i<u>dz</u> q<u>aa</u>lat <u>th</u><u>aa</u>-ifatun minhum y<u>aa</u> ahla yatsriba l<u>aa</u> muq<u>aa</u>ma lakum farji\'uu wayasta/<u>dz</u>inu fariiqun minhumu nnabiyya yaquuluuna inna buyuutan<u>aa</u> \'awratun wam<u>aa</u> hiya bi\'awratin in yuriiduuna ill<u>aa</u> fir<u>aa</u>r<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3547, '33:14', 33, 14, 'walaw dukhilat \'alayhim min aq<u>th</u><u>aa</u>rih<u>aa</u> tsumma su-iluu lfitnata la<u>aa</u>tawh<u>aa</u> wam<u>aa</u> talabbatsuu bih<u>aa</u> ill<u>aa</u> yasiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3548, '33:15', 33, 15, 'walaqad k<u>aa</u>nuu \'<u>aa</u>haduull<u>aa</u>ha min qablu l<u>aa</u> yuwalluuna l-adb<u>aa</u>ra wak<u>aa</u>na \'ahdull<u>aa</u>hi mas-uul<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3549, '33:16', 33, 16, 'qul lan yanfa\'akumu lfir<u>aa</u>ru in farartum mina lmawti awi lqatli wa-i<u>dz</u>an l<u>aa</u> tumatta\'uuna ill<u>aa</u> qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3550, '33:17', 33, 17, 'qul man <u>dzaa</u>lla<u>dz</u>ii ya\'<u>sh</u>imukum minall<u>aa</u>hi in ar<u>aa</u>da bikum suu-an aw ar<u>aa</u>da bikum ra<u>h</u>matan wal<u>aa</u> yajiduuna lahum min duunill<u>aa</u>hi waliyyan wal<u>aa</u> na<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3551, '33:18', 33, 18, 'qad ya\'lamull<u>aa</u>hu lmu\'awwiqiina minkum walq<u>aa</u>-iliina li-ikhw<u>aa</u>nihim halumma ilayn<u>aa</u> wal<u>aa</u> ya/tuuna lba/sa ill<u>aa</u> qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3552, '33:19', 33, 19, 'asyi<u>hh</u>atan \'alaykum fa-i<u>dzaa</u> j<u>aa</u>-a lkhawfu ra-aytahum yan<u>zh</u>uruuna ilayka taduuru a\'yunuhum kalla<u>dz</u>ii yughsy<u>aa</u> \'alayhi mina lmawti fa-i<u>dzaa</u> <u>dz</u>ahaba lkhawfu salaquukum bi-alsinatin <u>h</u>id<u>aa</u>din asyi<u>hh</u>atan \'al<u>aa</u> lkhayri ul<u>aa</u>-ika lam yu/minuu fa-a<u>h</u>ba<u>th</u>all<u>aa</u>hu a\'m<u>aa</u>lahum wak<u>aa</u>na <u>dzaa</u>lika \'al<u>aa</u>ll<u>aa</u>hi yasiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3553, '33:20', 33, 20, 'ya<u>h</u>sabuuna l-a<u>h</u>z<u>aa</u>ba lam ya<u>dz</u>habuu wa-in ya/ti l-a<u>h</u>z<u>aa</u>bu yawadduu law annahum b<u>aa</u>duuna fii l-a\'r<u>aa</u>bi yas-aluuna \'an anb<u>aa</u>-ikum walaw k<u>aa</u>nuu fiikum m<u>aa</u> q<u>aa</u>taluu ill<u>aa</u> qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3554, '33:21', 33, 21, 'laqad k<u>aa</u>na lakum fii rasuulill<u>aa</u>hi uswatun <u>h</u>asanatun liman k<u>aa</u>na yarjuull<u>aa</u>ha walyawma l-<u>aa</u>khira wa<u>dz</u>akarall<u>aa</u>ha katsiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3555, '33:22', 33, 22, 'walamm<u>aa</u> ra<u>aa</u> lmu/minuuna l-a<u>h</u>z<u>aa</u>ba q<u>aa</u>luu h<u>aadzaa</u> m<u>aa</u> wa\'adan<u>aa</u>ll<u>aa</u>hu warasuuluhu wa<u>sh</u>adaqall<u>aa</u>hu warasuuluhu wam<u>aa</u> z<u>aa</u>dahum ill<u>aa</u> iim<u>aa</u>nan watasliim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3556, '33:23', 33, 23, 'mina lmu/miniina rij<u>aa</u>lun <u>sh</u>adaquu m<u>aa</u> \'<u>aa</u>haduull<u>aa</u>ha \'alayhi faminhum man qa<u>daa</u> na<u>h</u>bahu waminhum man yanta<u>zh</u>iru wam<u>aa</u> baddaluu tabdiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3557, '33:24', 33, 24, 'liyajziyall<u>aa</u>hu <u>shshaa</u>diqiina bi<u>sh</u>idqihim wayu\'a<u>dzdz</u>iba lmun<u>aa</u>fiqiina in sy<u>aa</u>-a aw yatuuba \'alayhim innall<u>aa</u>ha k<u>aa</u>na ghafuuran ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3558, '33:25', 33, 25, 'waraddall<u>aa</u>hulla<u>dz</u>iina kafaruu bighay<u>zh</u>ihim lam yan<u>aa</u>luu khayran wakaf<u>aa</u>ll<u>aa</u>hu lmu/miniina lqit<u>aa</u>la wak<u>aa</u>nall<u>aa</u>hu qawiyyan \'aziiz<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3559, '33:26', 33, 26, 'wa-anzalalla<u>dz</u>iina <u>zhaa</u>haruuhum min ahli lkit<u>aa</u>bi min <u>sh</u>ay<u>aas</u>iihim waqa<u>dz</u>afa fii quluubihimu rru\'ba fariiqan taqtuluuna wata/siruuna fariiq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3560, '33:27', 33, 27, 'wa-awratsakum ar<u>dh</u>ahum wadiy<u>aa</u>rahum wa-amw<u>aa</u>lahum wa-ar<u>dh</u>an lam ta<u>th</u>auuh<u>aa</u> wak<u>aa</u>nall<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3561, '33:28', 33, 28, 'y<u>aa</u> ayyuh<u>aa</u> nnabiyyu qul li-azw<u>aa</u>jika in kuntunna turidna l<u>h</u>ay<u>aa</u>ta dduny<u>aa</u> waziinatah<u>aa</u> fata\'<u>aa</u>layna umatti\'kunna wausarri<u>h</u>kunna sar<u>aah</u>an jamiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3562, '33:29', 33, 29, 'wa-in kuntunna turidnall<u>aa</u>ha warasuulahu wadd<u>aa</u>ra l-<u>aa</u>khirata fa-innall<u>aa</u>ha a\'adda lilmu<u>h</u>sin<u>aa</u>ti minkunna ajran \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3563, '33:30', 33, 30, 'y<u>aa</u> nis<u>aa</u>-a nnabiyyi man ya/ti minkunna bif<u>aah</u>isyatin mubayyinatin yu<u>daa</u>\'af lah<u>aa</u> l\'a<u>dzaa</u>bu <u>dh</u>i\'fayni wak<u>aa</u>na <u>dzaa</u>lika \'al<u>aa</u>ll<u>aa</u>hi yasiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3564, '33:31', 33, 31, 'waman yaqnut minkunna lill<u>aa</u>hi warasuulihi wata\'mal <u>shaa</u>li<u>h</u>an nu/tih<u>aa</u> ajrah<u>aa</u> marratayni wa-a\'tadn<u>aa</u> lah<u>aa</u> rizqan kariim<u>aa</u>\r\r\n');
