INSERT INTO quranLatin VALUES (3609, '34:3', 34, 3, 'waq<u>aa</u>lalla<u>dz</u>iina kafaruu l<u>aa</u> ta/tiin<u>aa</u> ss<u>aa</u>\'atu qul bal<u>aa</u> warabbii lata/tiyannakum \'<u>aa</u>limi lghaybi l<u>aa</u> ya\'zubu \'anhu mitsq<u>aa</u>lu <u>dz</u>arratin fii ssam<u>aa</u>w<u>aa</u>ti wal<u>aa</u> fii l-ar<u>dh</u>i wal<u>aa</u> a<u>sh</u>gharu min <u>dzaa</u>lika wal<u>aa</u> akbaru ill<u>aa</u> fii kit<u>aa</u>bin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (3610, '34:4', 34, 4, 'liyajziyalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti ul<u>aa</u>-ika lahum maghfiratun warizqun kariim\r\r\n');
INSERT INTO quranLatin VALUES (3611, '34:5', 34, 5, 'walla<u>dz</u>iina sa\'aw fii <u>aa</u>y<u>aa</u>tin<u>aa</u> mu\'<u>aa</u>jiziina ul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun min rijzin liim\r\r\n');
INSERT INTO quranLatin VALUES (3612, '34:6', 34, 6, 'wayar<u>aa</u>lla<u>dz</u>iina uutuu l\'ilmalla<u>dz</u>ii unzila ilayka min rabbika huwa l<u>h</u>aqqa wayahdii il<u>aa</u> <u>sh</u>ir<u>aath</u>i l\'aziizi l<u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (3613, '34:7', 34, 7, 'waq<u>aa</u>lalla<u>dz</u>iina kafaruu hal nadullukum \'al<u>aa</u> rajulin yunabbi-ukum i<u>dzaa</u> muzziqtum kulla mumazzaqin innakum lafii khalqin jadiid\r\r\n');
INSERT INTO quranLatin VALUES (3614, '34:8', 34, 8, 'aftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>iban am bihi jinnatun balilla<u>dz</u>iina l<u>aa</u> yu/minuuna bil-<u>aa</u>khirati fii l\'a<u>dzaa</u>bi wa<u>dhdh</u>al<u>aa</u>li lba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (3615, '34:9', 34, 9, 'afalam yaraw il<u>aa</u> m<u>aa</u> bayna aydiihim wam<u>aa</u> khalfahum mina ssam<u>aa</u>-i wal-ar<u>dh</u>i in nasya/ nakhsif bihimu l-ar<u>dh</u>a aw nusqi<u>th</u> \'alayhim kisafan mina ssam<u>aa</u>-i inna fii <u>dzaa</u>lika la<u>aa</u>yatan likulli \'abdin muniib\r\r\n');
INSERT INTO quranLatin VALUES (3616, '34:10', 34, 10, 'walaqad <u>aa</u>tayn<u>aa</u> d<u>aa</u>wuuda minn<u>aa</u> fa<u>dh</u>lan y<u>aa</u> jib<u>aa</u>lu awwibii ma\'ahu wa<u>ththh</u>ayra wa-alann<u>aa</u> lahu l<u>h</u>adiid\r\r\n');
INSERT INTO quranLatin VALUES (3617, '34:11', 34, 11, 'ani i\'mal s<u>aa</u>bigh<u>aa</u>tin waqaddir fii ssardi wa\'maluu <u>shaa</u>li<u>h</u>an innii bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (3618, '34:12', 34, 12, 'walisulaym<u>aa</u>na rrii<u>h</u>a ghuduwwuh<u>aa</u> syahrun waraw<u>aah</u>uh<u>aa</u> syahrun wa-asaln<u>aa</u> lahu \'ayna lqi<u>th</u>ri wamina ljinni man ya\'malu bayna yadayhi bi-i<u>dz</u>ni rabbihi waman yazigh minhum \'an amrin<u>aa</u> nu<u>dz</u>iqhu min \'a<u>dzaa</u>bi ssa\'iir\r\r\n');
INSERT INTO quranLatin VALUES (3619, '34:13', 34, 13, 'ya\'maluuna lahu m<u>aa</u> yasy<u>aa</u>u min ma<u>haa</u>riiba watam<u>aa</u>tsiila wajif<u>aa</u>nin kaljaw<u>aa</u>bi waquduurin r<u>aa</u>siy<u>aa</u>tin i\'maluu <u>aa</u>la d<u>aa</u>wuuda syukran waqaliilun min \'ib<u>aa</u>diya sysyakuur\r\r\n');
INSERT INTO quranLatin VALUES (3620, '34:14', 34, 14, 'falamm<u>aa</u> qa<u>dh</u>ayn<u>aa</u> \'alayhi lmawta m<u>aa</u> dallahum \'al<u>aa</u> mawtihi ill<u>aa</u> d<u>aa</u>bbatu l-ar<u>dh</u>i ta/kulu minsa-atahu falamm<u>aa</u> kharra tabayyanati ljinnu an law k<u>aa</u>nuu ya\'lamuuna lghayba m<u>aa</u> labitsuu fii l\'a<u>dzaa</u>bi lmuhiin\r\r\n');
INSERT INTO quranLatin VALUES (3621, '34:15', 34, 15, 'laqad k<u>aa</u>na lisaba-in fii maskanihim <u>aa</u>yatun jannat<u>aa</u>ni \'an yamiinin wasyim<u>aa</u>lin kuluu min rizqi rabbikum wasykuruu lahu baldatun <u>th</u>ayyibatun warabbun ghafuur\r\r\n');
INSERT INTO quranLatin VALUES (3622, '34:16', 34, 16, 'fa-a\'ra<u>dh</u>uu fa-arsaln<u>aa</u> \'alayhim sayla l\'arimi wabaddaln<u>aa</u>hum bijannatayhim jannatayni <u>dz</u>aw<u>aa</u>tay ukulin kham<u>th</u>in wa-atslin wasyay-in min sidrin qaliil\r\r\n');
INSERT INTO quranLatin VALUES (3623, '34:17', 34, 17, '<u>dzaa</u>lika jazayn<u>aa</u>hum bim<u>aa</u> kafaruu wahal nuj<u>aa</u>zii ill<u>aa</u> lkafuur\r\r\n');
INSERT INTO quranLatin VALUES (3624, '34:18', 34, 18, 'waja\'aln<u>aa</u> baynahum wabayna lqur<u>aa</u>llatii b<u>aa</u>rakn<u>aa</u> fiih<u>aa</u> quran <u>zhaa</u>hiratan waqaddarn<u>aa</u> fiih<u>aa</u> ssayra siiruu fiih<u>aa</u> lay<u>aa</u>liya wa-ayy<u>aa</u>man <u>aa</u>miniin\r\r\n');
INSERT INTO quranLatin VALUES (3625, '34:19', 34, 19, 'faq<u>aa</u>luu rabban<u>aa</u> b<u>aa</u>\'id bayna asf<u>aa</u>rin<u>aa</u> wa<u>zh</u>alamuu anfusahum faja\'aln<u>aa</u>hum a<u>haa</u>diitsa wamazzaqn<u>aa</u>hum kulla mumazzaqin inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin likulli <u>sh</u>abb<u>aa</u>rin syakuur\r\r\n');
INSERT INTO quranLatin VALUES (3626, '34:20', 34, 20, 'walaqad <u>sh</u>addaqa \'alayhim ibliisu <u>zh</u>annahu fattaba\'uuhu ill<u>aa</u> fariiqan mina lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (3627, '34:21', 34, 21, 'wam<u>aa</u> k<u>aa</u>na lahu \'alayhim min sul<u>th</u><u>aa</u>nin ill<u>aa</u> lina\'lama man yu/minu bil-<u>aa</u>khirati mimman huwa minh<u>aa</u> fii syakkin warabbuka \'al<u>aa</u> kulli syay-in <u>h</u>afii<u>zh</u>\r\r\n');
INSERT INTO quranLatin VALUES (3628, '34:22', 34, 22, 'quli ud\'uulla<u>dz</u>iina za\'amtum min duunill<u>aa</u>hi l<u>aa</u> yamlikuuna mitsq<u>aa</u>la <u>dz</u>arratin fii ssam<u>aa</u>w<u>aa</u>ti wal<u>aa</u> fii l-ar<u>dh</u>i wam<u>aa</u> lahum fiihim<u>aa</u> min syirkin wam<u>aa</u> lahu minhum min <u>zh</u>ahiir\r\r\n');
INSERT INTO quranLatin VALUES (3629, '34:23', 34, 23, 'wal<u>aa</u> tanfa\'u sysyaf<u>aa</u>\'atu \'indahu ill<u>aa</u> liman a<u>dz</u>ina lahu <u>h</u>att<u>aa</u> i<u>dzaa</u> fuzzi\'a \'an quluubihim q<u>aa</u>luu m<u>aatsaa</u> q<u>aa</u>la rabbukum q<u>aa</u>luu l<u>h</u>aqqa wahuwa l\'aliyyu lkabiir\r\r\n');
INSERT INTO quranLatin VALUES (3630, '34:24', 34, 24, 'qul man yarzuqukum mina ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i qulill<u>aa</u>hu wa-inn<u>aa</u> aw iyy<u>aa</u>kum la\'al<u>aa</u> hudan aw fii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (3631, '34:25', 34, 25, 'qul l<u>aa</u> tus-aluuna \'amm<u>aa</u> ajramn<u>aa</u> wal<u>aa</u> nus-alu \'amm<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (3632, '34:26', 34, 26, 'qul yajma\'u baynan<u>aa</u> rabbun<u>aa</u> tsumma yafta<u>h</u>u baynan<u>aa</u> bil<u>h</u>aqqi wahuwa lfatt<u>aah</u>u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (3633, '34:27', 34, 27, 'qul aruuniyalla<u>dz</u>iina l<u>h</u>aqtum bihi syurak<u>aa</u>-a kall<u>aa</u> bal huwall<u>aa</u>hu l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (3634, '34:28', 34, 28, 'wam<u>aa</u> arsaln<u>aa</u>ka ill<u>aa</u> k<u>aa</u>ffatan linn<u>aa</u>si basyiiran wana<u>dz</u>iiran wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3635, '34:29', 34, 29, 'wayaquuluuna mat<u>aa</u> h<u>aadzaa</u> lwa\'du in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (3636, '34:30', 34, 30, 'qul lakum mii\'<u>aa</u>du yawmin l<u>aa</u> tasta/khiruuna \'anhu s<u>aa</u>\'atan wal<u>aa</u> tastaqdimuun\r\r\n');
INSERT INTO quranLatin VALUES (3637, '34:31', 34, 31, 'waq<u>aa</u>lalla<u>dz</u>iina kafaruu lan nu/mina bih<u>aadzaa</u> lqur-<u>aa</u>ni wal<u>aa</u> billa<u>dz</u>ii bayna yadayhi walaw tar<u>aa</u> i<u>dz</u>i <u>zhzhaa</u>limuuna mawquufuuna \'inda rabbihim yarji\'u ba\'<u>dh</u>uhum il<u>aa</u> ba\'<u>dh</u>in lqawla yaquululla<u>dz</u>iina istu<u>dh</u>\'ifuu lilla<u>dz</u>iina istakbaruu lawl<u>aa</u> antum lakunn<u>aa</u> mu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (3638, '34:32', 34, 32, 'q<u>aa</u>lalla<u>dz</u>iina istakbaruu lilla<u>dz</u>iina istu<u>dh</u>\'ifuu ana<u>h</u>nu <u>sh</u>adadn<u>aa</u>kum \'ani lhud<u>aa</u> ba\'da i<u>dz</u> j<u>aa</u>-akum bal kuntum mujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (3639, '34:33', 34, 33, 'waq<u>aa</u>lalla<u>dz</u>iina istu<u>dh</u>\'ifuu lilla<u>dz</u>iina istakbaruu bal makrullayli wannah<u>aa</u>ri i<u>dz</u> ta/muruunan<u>aa</u> an nakfura bil<u>aa</u>hi wanaj\'ala lahu and<u>aa</u>dan wa-asarruu nnad<u>aa</u>mata lamm<u>aa</u> ra-awuu l\'a<u>dzaa</u>ba waja\'aln<u>aa</u> l-aghl<u>aa</u>la fii a\'n<u>aa</u>qilla<u>dz</u>iina kafaruu hal yujzawna ill<u>aa</u> m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (3640, '34:34', 34, 34, 'wam<u>aa</u> arsaln<u>aa</u> fii qaryatin min na<u>dz</u>iirin ill<u>aa</u> q<u>aa</u>la mutrafuuh<u>aa</u> inn<u>aa</u> bim<u>aa</u> ursiltum bihi k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (3641, '34:35', 34, 35, 'waq<u>aa</u>luu na<u>h</u>nu aktsaru amw<u>aa</u>lan wa-awl<u>aa</u>dan wam<u>aa</u> na<u>h</u>nu bimu\'a<u>dzdz</u>abiin\r\r\n');
INSERT INTO quranLatin VALUES (3642, '34:36', 34, 36, 'qul inna rabbii yabsu<u>th</u>u rrizqa liman yasy<u>aa</u>u wayaqdiru wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (3643, '34:37', 34, 37, 'wam<u>aa</u> amw<u>aa</u>lukum wal<u>aa</u> awl<u>aa</u>dukum billatii tuqarribukum \'indan<u>aa</u> zulf<u>aa</u> ill<u>aa</u> man <u>aa</u>mana wa\'amila <u>shaa</u>li<u>h</u>an faul<u>aa</u>-ika lahum jaz<u>aa</u>u <u>dhdh</u>i\'fi bim<u>aa</u> \'amiluu wahum fii lghuruf<u>aa</u>ti <u>aa</u>minuun\r\r\n');
INSERT INTO quranLatin VALUES (3644, '34:38', 34, 38, 'walla<u>dz</u>iina yas\'awna fii <u>aa</u>y<u>aa</u>tin<u>aa</u> mu\'<u>aa</u>jiziina ul<u>aa</u>-ika fii l\'a<u>dzaa</u>bi mu<u>hd</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (3645, '34:39', 34, 39, 'qul inna rabbii yabsu<u>th</u>u rrizqa liman yasy<u>aa</u>u min \'ib<u>aa</u>dihi wayaqdiru lahu wam<u>aa</u> anfaqtum min syay-in fahuwa yukhlifuhu wahuwa khayru rr<u>aa</u>ziqiin\r\r\n');
INSERT INTO quranLatin VALUES (3646, '34:40', 34, 40, 'wayawma ya<u>h</u>syuruhum jamii\'an tsumma yaquulu lilmal<u>aa</u>-ikati ah<u>aa</u>ul<u>aa</u>-i iyy<u>aa</u>kum k<u>aa</u>nuu ya\'buduun\r\r\n');
INSERT INTO quranLatin VALUES (3647, '34:41', 34, 41, 'q<u>aa</u>luu sub<u>haa</u>naka anta waliyyun<u>aa</u> min duunihim bal k<u>aa</u>nuu ya\'buduuna ljinna aktsaruhum bihim mu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (3648, '34:42', 34, 42, 'falyawma l<u>aa</u> yamliku ba\'<u>dh</u>ukum liba\'<u>dh</u>in naf\'an wal<u>aa</u> <u>dh</u>arran wanaquulu lilla<u>dz</u>iina <u>zh</u>alamuu <u>dz</u>uuquu \'a<u>dzaa</u>ba nn<u>aa</u>rillatii kuntum bih<u>aa</u> tuka<u>dzdz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (3649, '34:43', 34, 43, 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tun<u>aa</u> bayyin<u>aa</u>tin q<u>aa</u>luu m<u>aa</u> h<u>aadzaa</u> ill<u>aa</u> rajulun yuriidu an ya<u>sh</u>uddakum \'amm<u>aa</u> k<u>aa</u>na ya\'budu <u>aa</u>b<u>aa</u>ukum waq<u>aa</u>luu m<u>aa</u> h<u>aadzaa</u> ill<u>aa</u> ifkun muftaran waq<u>aa</u>lalla<u>dz</u>iina kafaruu lil<u>h</u>aqqi lamm<u>aa</u> j<u>aa</u>-ahum in h<u>aadzaa</u> ill<u>aa</u> si<u>h</u>run mubiin\r\r\n');
INSERT INTO quranLatin VALUES (3650, '34:44', 34, 44, 'wam<u>aa</u> <u>aa</u>tayn<u>aa</u>hum min kutubin yadrusuunah<u>aa</u> wam<u>aa</u> arsaln<u>aa</u> ilayhim qablaka min na<u>dz</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (3651, '34:45', 34, 45, 'waka<u>dzdz</u>aballa<u>dz</u>iina min qablihim wam<u>aa</u> balaghuu mi\'sy<u>aa</u>ra m<u>aa</u> <u>aa</u>tayn<u>aa</u>hum faka<u>dzdz</u>abuu rusulii fakayfa k<u>aa</u>na nakiir\r\r\n');
INSERT INTO quranLatin VALUES (3652, '34:46', 34, 46, 'qul innam<u>aa</u> a\'i<u>zh</u>ukum biw<u>aah</u>idatin an taquumuu lill<u>aa</u>hi matsn<u>aa</u> wafur<u>aa</u>d<u>aa</u> tsumma tatafakkaruu m<u>aa</u> bi<u>shaah</u>ibikum min jinnatin in huwa ill<u>aa</u> na<u>dz</u>iirun lakum bayna yaday \'a<u>dzaa</u>bin syadiid\r\r\n');
INSERT INTO quranLatin VALUES (3653, '34:47', 34, 47, 'qul m<u>aa</u> sa-altukum min ajrin fahuwa lakum in ajriya ill<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi wahuwa \'al<u>aa</u> kulli syay-in syahiid\r\r\n');
INSERT INTO quranLatin VALUES (3654, '34:48', 34, 48, 'qul inna rabbii yaq<u>dz</u>ifu bil<u>h</u>aqqi \'all<u>aa</u>mu lghuyuub\r\r\n');
INSERT INTO quranLatin VALUES (3655, '34:49', 34, 49, 'qul j<u>aa</u>-a l<u>h</u>aqqu wam<u>aa</u> yubdi-u lb<u>aath</u>ilu wam<u>aa</u> yu\'iid\r\r\n');
INSERT INTO quranLatin VALUES (3656, '34:50', 34, 50, 'qul in <u>dh</u>alaltu fa-innam<u>aa</u> a<u>dh</u>illu \'al<u>aa</u> nafsii wa-ini ihtadaytu fabim<u>aa</u> yuu<u>h</u>ii ilayya rabbii innahu samii\'un qariib\r\r\n');
INSERT INTO quranLatin VALUES (3657, '34:51', 34, 51, 'walaw tar<u>aa</u> i<u>dz</u> fazi\'uu fal<u>aa</u> fawta waukhi<u>dz</u>uu min mak<u>aa</u>nin qariib\r\r\n');
INSERT INTO quranLatin VALUES (3658, '34:52', 34, 52, 'waq<u>aa</u>luu <u>aa</u>mann<u>aa</u> bihi wa-ann<u>aa</u> lahumu ttan<u>aa</u>wusyu min mak<u>aa</u>nin ba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (3659, '34:53', 34, 53, 'waqad kafaruu bihi min qablu wayaq<u>dz</u>ifuuna bilghaybi min mak<u>aa</u>nin ba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (3660, '34:54', 34, 54, 'wa<u>h</u>iila baynahum wabayna m<u>aa</u> yasytahuuna kam<u>aa</u> fu\'ila bi-asyy<u>aa</u>\'ihim min qablu innahum k<u>aa</u>nuu fii syakkin muriib\r\r\n');
INSERT INTO quranLatin VALUES (3661, '35:1', 35, 1, 'al<u>h</u>amdu lill<u>aa</u>hi f<u>aath</u>iri ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i j<u>aa</u>\'ili lmal<u>aa</u>-ikati rusulan ulii ajni<u>h</u>atin matsn<u>aa</u> watsul<u>aa</u>tsa warub<u>aa</u>\'a yaziidu fii lkhalqi m<u>aa</u> yasy<u>aa</u>u innall<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (3662, '35:2', 35, 2, 'm<u>aa</u> yafta<u>h</u>ill<u>aa</u>hu linn<u>aa</u>si min ra<u>h</u>matin fal<u>aa</u> mumsika lah<u>aa</u> wam<u>aa</u> yumsik fal<u>aa</u> mursila lahu min ba\'dihi wahuwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (3663, '35:3', 35, 3, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su u<u>dz</u>kuruu ni\'matall<u>aa</u>hi \'alaykum hal min kh<u>aa</u>liqin ghayrull<u>aa</u>hi yarzuqukum mina ssam<u>aa</u>-i wal-ar<u>dh</u>i l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa fa-ann<u>aa</u> tu/fakuun\r\r\n');
INSERT INTO quranLatin VALUES (3664, '35:4', 35, 4, 'wa-in yuka<u>dzdz</u>ibuuka faqad kut<u>hts</u>ibat rusulun min qablika wa-il<u>aa</u>ll<u>aa</u>hi turja\'u l-umuur\r\r\n');
INSERT INTO quranLatin VALUES (3665, '35:5', 35, 5, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su inna wa\'dall<u>aa</u>hi <u>h</u>aqqun fal<u>aa</u> taghurrannakumu l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> wal<u>aa</u> yaghurrannakum bil<u>aa</u>hi lgharuur\r\r\n');
INSERT INTO quranLatin VALUES (3666, '35:6', 35, 6, 'inna sysyay<u>th</u><u>aa</u>na lakum \'aduwwun fattakhi<u>dz</u>uuhu \'aduwwan innam<u>aa</u> yad\'uu <u>h</u>izbahu liyakuunuu min a<u>sh</u>-<u>haa</u>bi ssa\'iir\r\r\n');
INSERT INTO quranLatin VALUES (3667, '35:7', 35, 7, '<B>al</b>la<u>dz</u>iina kafaruu lahum \'a<u>dzaa</u>bun syadiidun walla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti lahum maghfiratun wa-ajrun kabiir\r\r\n');
INSERT INTO quranLatin VALUES (3668, '35:8', 35, 8, 'afaman zuyyina lahu suu-u \'amalihi fara<u>aa</u>hu <u>h</u>asanan fa-innall<u>aa</u>ha yu<u>dh</u>illu man yasy<u>aa</u>u wayahdii man yasy<u>aa</u>u fal<u>aa</u> ta<u>dz</u>hab nafsuka \'alayhim <u>h</u>asar<u>aa</u>tin innall<u>aa</u>ha \'aliimun bim<u>aa</u> ya<u>sh</u>na\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3669, '35:9', 35, 9, 'wal<u>aa</u>hulla<u>dz</u>ii arsala rriy<u>aah</u>a fatutsiiru sa<u>haa</u>ban fasuqn<u>aa</u>hu il<u>aa</u> baladin mayyitin fa-a<u>h</u>yayn<u>aa</u> bihi l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> ka<u>dzaa</u>lika nnusyuur\r\r\n');
INSERT INTO quranLatin VALUES (3670, '35:10', 35, 10, 'man k<u>aa</u>na yuriidu l\'izzata falill<u>aa</u>hi l\'izzatu jamii\'an ilayhi ya<u>sh</u>\'adu lkalimu <u>ththh</u>ayyibu wal\'amalu <u>shshaa</u>li<u>h</u>u yarfa\'uhu walla<u>dz</u>iina yamkuruuna ssayyi-<u>aa</u>ti lahum \'a<u>dzaa</u>bun syadiidun wamakru ul<u>aa</u>-ika huwa yabuur\r\r\n');
INSERT INTO quranLatin VALUES (3671, '35:11', 35, 11, 'wal<u>aa</u>hu khalaqakum min tur<u>aa</u>bin tsumma min nu<u>th</u>fatin tsumma ja\'alakum azw<u>aa</u>jan wam<u>aa</u> ta<u>h</u>milu min unts<u>aa</u> wal<u>aa</u> ta<u>dh</u>a\'u ill<u>aa</u> bi\'ilmihi wam<u>aa</u> yu\'ammaru min mu\'ammarin wal<u>aa</u> yunqa<u>sh</u>u min \'umurihi ill<u>aa</u> fii kit<u>aa</u>bin inna <u>dzaa</u>lika \'al<u>aa</u>ll<u>aa</u>hi yasiir\r\r\n');
INSERT INTO quranLatin VALUES (3672, '35:12', 35, 12, 'wam<u>aa</u> yastawii lba<u>h</u>r<u>aa</u>ni h<u>aadzaa</u> \'a<u>dz</u>bun fur<u>aa</u>tun s<u>aa</u>-ighun syar<u>aa</u>buhu wah<u>aadzaa</u> mil<u>h</u>un uj<u>aa</u>jun wamin kullin ta/kuluuna la<u>h</u>man <u>th</u>ariyyan watastakhrijuuna <u>h</u>ilyatan talbasuunah<u>aa</u> watar<u>aa</u> lfulka fiihi maw<u>aa</u>khira litabtaghuu min fa<u>dh</u>lihi wala\'allakum tasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (3673, '35:13', 35, 13, 'yuulijullayla fii nnah<u>aa</u>ri wayuuliju nnah<u>aa</u>ra fiillayli wasakhkhara sysyamsa walqamara kullun yajrii li-ajalin musamman <u>dzaa</u>likumull<u>aa</u>hu rabbukum lahu lmulku walla<u>dz</u>iina tad\'uuna min duunihi m<u>aa</u> yamlikuuna min qi<u>th</u>miir\r\r\n');
INSERT INTO quranLatin VALUES (3674, '35:14', 35, 14, 'in tad\'uuhum l<u>aa</u> yasma\'uu du\'<u>aa</u>-akum walaw sami\'uu m<u>aa</u> istaj<u>aa</u>buu lakum wayawma lqiy<u>aa</u>mati yakfuruuna bisyirkikum wal<u>aa</u> yunabbi-uka mitslu khabiir\r\r\n');
INSERT INTO quranLatin VALUES (3675, '35:15', 35, 15, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su antumu lfuqar<u>aa</u>u il<u>aa</u>ll<u>aa</u>hi wal<u>aa</u>hu huwa lghaniyyu l<u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (3676, '35:16', 35, 16, 'in yasya/ yu<u>dz</u>hibkum waya/ti bikhalqin jadiid\r\r\n');
INSERT INTO quranLatin VALUES (3677, '35:17', 35, 17, 'wam<u>aa</u> <u>dzaa</u>lika \'al<u>aa</u>ll<u>aa</u>hi bi\'aziiz\r\r\n');
INSERT INTO quranLatin VALUES (3678, '35:18', 35, 18, 'wal<u>aa</u> taziru w<u>aa</u>ziratun wizra ukhr<u>aa</u> wa-in tad\'u mutsqalatun il<u>aa</u> <u>h</u>imlih<u>aa</u> l<u>aa</u> yu<u>h</u>mal minhu syay-un walaw k<u>aa</u>na <u>dzaa</u> qurb<u>aa</u> innam<u>aa</u> tun<u>dz</u>irulla<u>dz</u>iina yakhsyawna rabbahum bilghaybi wa-aq<u>aa</u>muu <u>shsh</u>al<u>aa</u>ta waman tazakk<u>aa</u> fa-innam<u>aa</u> yatazakk<u>aa</u> linafsihi wa-il<u>aa</u>ll<u>aa</u>hi lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (3679, '35:19', 35, 19, 'wam<u>aa</u> yastawii l-a\'m<u>aa</u> walba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (3680, '35:20', 35, 20, 'wal<u>aa</u> <u>zhzh</u>ulum<u>aa</u>tu wal<u>aa</u> nnuur\r\r\n');
INSERT INTO quranLatin VALUES (3681, '35:21', 35, 21, 'wal<u>aa</u> <u>zhzh</u>illu wal<u>aa</u> l<u>h</u>aruur\r\r\n');
INSERT INTO quranLatin VALUES (3682, '35:22', 35, 22, 'wam<u>aa</u> yastawii l-a<u>h</u>y<u>aa</u>u wal<u>aa</u> l-amw<u>aa</u>tu innall<u>aa</u>ha yusmi\'u man yasy<u>aa</u>u wam<u>aa</u> anta bimusmi\'in man fii lqubuur\r\r\n');
INSERT INTO quranLatin VALUES (3683, '35:23', 35, 23, 'in anta ill<u>aa</u> na<u>dz</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (3684, '35:24', 35, 24, 'inn<u>aa</u> arsaln<u>aa</u>ka bil<u>h</u>aqqi basyiiran wana<u>dz</u>iiran wa-in min ummatin ill<u>aa</u> khal<u>aa</u> fiih<u>aa</u> na<u>dz</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (3685, '35:25', 35, 25, 'wa-in yuka<u>dzdz</u>ibuuka faqad ka<u>dzdz</u>aballa<u>dz</u>iina min qablihim j<u>aa</u>-at-hum rusuluhum bilbayyin<u>aa</u>ti wabizzuburi wabilkit<u>aa</u>bi lmuniir\r\r\n');
INSERT INTO quranLatin VALUES (3686, '35:26', 35, 26, 'tsumma akha<u>dz</u>tulla<u>dz</u>iina kafaruu fakayfa k<u>aa</u>na nakiir\r\r\n');
INSERT INTO quranLatin VALUES (3687, '35:27', 35, 27, 'alam tara annall<u>aa</u>ha anzala mina ssam<u>aa</u>-i m<u>aa</u>-an fa-akhrajn<u>aa</u> bihi tsamar<u>aa</u>tin mukhtalifan lw<u>aa</u>nuh<u>aa</u> wamina ljib<u>aa</u>li judadun bii<u>dh</u>un wa<u>h</u>umrun mukhtalifun lw<u>aa</u>nuh<u>aa</u> waghar<u>aa</u>biibu suud\r\r\n');
INSERT INTO quranLatin VALUES (3688, '35:28', 35, 28, 'wamina nn<u>aa</u>si waddaw<u>aa</u>bbi wal-an\'<u>aa</u>mi mukhtalifun lw<u>aa</u>nuhu ka<u>dzaa</u>lika innam<u>aa</u> yakhsy<u>aa</u>ll<u>aa</u>ha min \'ib<u>aa</u>dihi l\'ulam<u>aa</u>u innall<u>aa</u>ha \'aziizun ghafuur\r\r\n');
INSERT INTO quranLatin VALUES (3689, '35:29', 35, 29, 'innalla<u>dz</u>iina yatluuna kit<u>aa</u>ball<u>aa</u>hi wa-aq<u>aa</u>muu <u>shsh</u>al<u>aa</u>ta wa-anfaquu mimm<u>aa</u> razaqn<u>aa</u>hum sirran wa\'al<u>aa</u>niyatan yarjuuna tij<u>aa</u>ratan lan tabuur\r\r\n');
INSERT INTO quranLatin VALUES (3690, '35:30', 35, 30, 'liyuwaffiyahum ujuurahum wayaziidahum min fa<u>dh</u>lihi innahu ghafuurun syakuur\r\r\n');
INSERT INTO quranLatin VALUES (3691, '35:31', 35, 31, 'walla<u>dz</u>ii aw<u>h</u>ayn<u>aa</u> ilayka mina lkit<u>aa</u>bi huwa l<u>h</u>aqqu mu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi innall<u>aa</u>ha bi\'ib<u>aa</u>dihi lakhabiirun ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (3692, '35:32', 35, 32, 'tsumma awratsn<u>aa</u> lkit<u>aa</u>balla<u>dz</u>iina i<u>sth</u>afayn<u>aa</u> min \'ib<u>aa</u>din<u>aa</u> faminhum <u>zhaa</u>limun linafsihi waminhum muqta<u>sh</u>idun waminhum s<u>aa</u>biqun bilkhayr<u>aa</u>ti bi-i<u>dz</u>nill<u>aa</u>hi <u>dzaa</u>lika huwa lfa<u>dh</u>lu lkabiir\r\r\n');
INSERT INTO quranLatin VALUES (3693, '35:33', 35, 33, 'jann<u>aa</u>tu \'adnin yadkhuluunah<u>aa</u> yu<u>h</u>allawna fiih<u>aa</u> min as<u>aa</u>wira min <u>dz</u>ahabin walu/lu-an walib<u>aa</u>suhum fiih<u>aa</u> <u>h</u>ariir\r\r\n');
INSERT INTO quranLatin VALUES (3694, '35:34', 35, 34, 'waq<u>aa</u>luu l<u>h</u>amdu lill<u>aa</u>hilla<u>dz</u>ii a<u>dz</u>haba \'ann<u>aa</u> l<u>h</u>azana inna rabban<u>aa</u> laghafuurun syakuur\r\r\n');
INSERT INTO quranLatin VALUES (3695, '35:35', 35, 35, '<B>al</b>la<u>dz</u>ii a<u>h</u>allan<u>aa</u> d<u>aa</u>ra lmuq<u>aa</u>mati min fa<u>dh</u>lihi l<u>aa</u> yamassun<u>aa</u> fiih<u>aa</u> na<u>sh</u>abun wal<u>aa</u> yamassun<u>aa</u> fiih<u>aa</u> lughuub\r\r\n');
INSERT INTO quranLatin VALUES (3696, '35:36', 35, 36, 'walla<u>dz</u>iina kafaruu lahum n<u>aa</u>ru jahannama l<u>aa</u> yuq<u>daa</u> \'alayhim fayamuutuu wal<u>aa</u> yukhaffafu \'anhum min \'a<u>dzaa</u>bih<u>aa</u> ka<u>dzaa</u>lika najzii kulla kafuur\r\r\n');
INSERT INTO quranLatin VALUES (3697, '35:37', 35, 37, 'wahum ya<u>sth</u>arikhuuna fiih<u>aa</u> rabban<u>aa</u> akhrijn<u>aa</u> na\'mal <u>shaa</u>li<u>h</u>an ghayralla<u>dz</u>ii kunn<u>aa</u> na\'malu awa lam nu\'ammirkum m<u>aa</u> yata<u>dz</u>akkaru fiihi man ta<u>dz</u>akkara waj<u>aa</u>-akumu nna<u>dz</u>iiru fa<u>dz</u>uuquu fam<u>aa</u> li<u>zhzhaa</u>limiina min na<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (3698, '35:38', 35, 38, 'innall<u>aa</u>ha \'<u>aa</u>limu ghaybi ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i innahu \'aliimun bi<u>dzaa</u>ti <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (3699, '35:39', 35, 39, 'huwalla<u>dz</u>ii ja\'alakum khal<u>aa</u>-ifa fii l-ar<u>dh</u>i faman kafara fa\'alayhi kufruhu wal<u>aa</u> yaziidu lk<u>aa</u>firiina kufruhum \'inda rabbihim ill<u>aa</u> maqtan wal<u>aa</u> yaziidu lk<u>aa</u>firiina kufruhum ill<u>aa</u> khas<u>aa</u>r<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3700, '35:40', 35, 40, 'qul ara-aytum syurak<u>aa</u>-akumulla<u>dz</u>iina tad\'uuna min duunill<u>aa</u>hi aruunii m<u>aatsaa</u> khalaquu mina l-ar<u>dh</u>i am lahum syirkun fii ssam<u>aa</u>w<u>aa</u>ti am <u>aa</u>tayn<u>aa</u>hum kit<u>aa</u>ban fahum \'al<u>aa</u> bayyinatin minhu bal in ya\'idu <u>zhzhaa</u>limuuna ba\'<u>dh</u>uhum ba\'<u>dh</u>an ill<u>aa</u> ghuruur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3701, '35:41', 35, 41, 'innall<u>aa</u>ha yumsiku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a an tazuul<u>aa</u> wala-in z<u>aa</u>lat<u>aa</u> in amsakahum<u>aa</u> min a<u>h</u>adin min ba\'dihi innahu k<u>aa</u>na <u>h</u>aliiman ghafuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3702, '35:42', 35, 42, 'wa-aqsamuu bil<u>aa</u>hi jahda aym<u>aa</u>nihim la-in j<u>aa</u>-ahum na<u>dz</u>iirun layakuununna ahd<u>aa</u> min i<u>h</u>d<u>aa</u> l-umami falamm<u>aa</u> j<u>aa</u>-ahum na<u>dz</u>iirun m<u>aa</u> z<u>aa</u>dahum ill<u>aa</u> nufuur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3703, '35:43', 35, 43, 'istikb<u>aa</u>ran fii l-ar<u>dh</u>i wamakra ssayyi-i wal<u>aa</u> ya<u>h</u>iiqu lmakru ssayyi-u ill<u>aa</u> bi-ahlihi fahal yan<u>zh</u>uruuna ill<u>aa</u> sunnata l-awwaliina falan tajida lisunnatill<u>aa</u>hi tabdiilan walan tajida lisunnatill<u>aa</u>hi ta<u>h</u>wiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3704, '35:44', 35, 44, 'awa lam yasiiruu fii l-ar<u>dh</u>i fayan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatulla<u>dz</u>iina min qablihim wak<u>aa</u>nuu asyadda minhum quwwatan wam<u>aa</u> k<u>aa</u>nall<u>aa</u>hu liyu\'jizahu min syay-in fii ssam<u>aa</u>w<u>aa</u>ti wal<u>aa</u> fii l-ar<u>dh</u>i innahu k<u>aa</u>na \'aliiman qadiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3705, '35:45', 35, 45, 'walaw yu-<u>aa</u>khi<u>dz</u>ull<u>aa</u>hu nn<u>aa</u>sa bim<u>aa</u> kasabuu m<u>aa</u> taraka \'al<u>aa</u> <u>zh</u>ahrih<u>aa</u> min d<u>aa</u>bbatin wal<u>aa</u>kin yu-akhkhiruhum il<u>aa</u> ajalin musamman fa-i<u>dzaa</u> j<u>aa</u>-a ajaluhum fa-innall<u>aa</u>ha k<u>aa</u>na bi\'ib<u>aa</u>dihi ba<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (3706, '36:1', 36, 1, 'y<u>aa</u>-siin</u>\r\r\n');
INSERT INTO quranLatin VALUES (3707, '36:2', 36, 2, 'walqur-<u>aa</u>ni l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (3708, '36:3', 36, 3, 'innaka lamina lmursaliin\r\r\n');
INSERT INTO quranLatin VALUES (3709, '36:4', 36, 4, '\'al<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (3710, '36:5', 36, 5, 'tanziila l\'aziizi rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (3711, '36:6', 36, 6, 'litun<u>dz</u>ira qawman m<u>aa</u> un<u>dz</u>ira <u>aa</u>b<u>aa</u>uhum fahum gh<u>aa</u>filuun\r\r\n');
INSERT INTO quranLatin VALUES (3712, '36:7', 36, 7, 'laqad <u>h</u>aqqa lqawlu \'al<u>aa</u> aktsarihim fahum l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (3713, '36:8', 36, 8, 'inn<u>aa</u> ja\'aln<u>aa</u> fii a\'n<u>aa</u>qihim aghl<u>aa</u>lan fahiya il<u>aa</u> l-a<u>dz</u>q<u>aa</u>ni fahum muqma<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (3714, '36:9', 36, 9, 'waja\'aln<u>aa</u> min bayni aydiihim saddan wamin khalfihim saddan fa-aghsyayn<u>aa</u>hum fahum l<u>aa</u> yub<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (3715, '36:10', 36, 10, 'wasaw<u>aa</u>un \'alayhim a-an<u>dz</u>artahum am lam tun<u>dz</u>irhum l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (3716, '36:11', 36, 11, 'innam<u>aa</u> tun<u>dz</u>iru mani ittaba\'a <u>dzdz</u>ikra wakhasyiya rra<u>h</u>m<u>aa</u>na bilghaybi fabasysyirhu bimaghfiratin wa-ajrin kariim\r\r\n');
INSERT INTO quranLatin VALUES (3717, '36:12', 36, 12, 'inn<u>aa</u> na<u>h</u>nu nu<u>h</u>yii lmawt<u>aa</u> wanaktubu m<u>aa</u> qaddamuu wa<u>aa</u>ts<u>aa</u>rahum wakulla syay-in a<u>hs</u>ayn<u>aa</u>hu fii im<u>aa</u>min mubiin\r\r\n');
INSERT INTO quranLatin VALUES (3718, '36:13', 36, 13, 'wa<u>dh</u>rib lahum matsalan a<u>sh</u>-<u>haa</u>ba lqaryati i<u>dz</u> j<u>aa</u>-ah<u>aa</u> lmursaluun\r\r\n');
INSERT INTO quranLatin VALUES (3719, '36:14', 36, 14, 'i<u>dz</u> arsaln<u>aa</u> ilayhimu itsnayni faka<u>dzdz</u>abuuhum<u>aa</u> fa\'azzazn<u>aa</u> bits<u>aa</u>litsin faq<u>aa</u>luu inn<u>aa</u> ilaykum mursaluun\r\r\n');
INSERT INTO quranLatin VALUES (3720, '36:15', 36, 15, 'q<u>aa</u>luu m<u>aa</u> antum ill<u>aa</u> basyarun mitslun<u>aa</u> wam<u>aa</u> anzala rra<u>h</u>m<u>aa</u>nu min syay-in in antum ill<u>aa</u> tak<u>dz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (3721, '36:16', 36, 16, 'q<u>aa</u>luu rabbun<u>aa</u> ya\'lamu inn<u>aa</u> ilaykum lamursaluun\r\r\n');
INSERT INTO quranLatin VALUES (3722, '36:17', 36, 17, 'wam<u>aa</u> \'alayn<u>aa</u> ill<u>aa</u> lbal<u>aa</u>ghu lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (3723, '36:18', 36, 18, 'q<u>aa</u>luu inn<u>aa</u> ta<u>th</u>ayyarn<u>aa</u> bikum la-in lam tantahuu lanarjumannakum walayamassannakum minn<u>aa</u> \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (3724, '36:19', 36, 19, 'q<u>aa</u>luu <u>th</u><u>aa</u>-irukum ma\'akum a-in <u>dz</u>ukkirtum bal antum qawmun musrifuun\r\r\n');
INSERT INTO quranLatin VALUES (3725, '36:20', 36, 20, 'waj<u>aa</u>-a min aq<u>shaa</u> lmadiinati rajulun yas\'<u>aa</u> q<u>aa</u>la y<u>aa</u> qawmi ittabi\'uu lmursaliin\r\r\n');
INSERT INTO quranLatin VALUES (3726, '36:21', 36, 21, 'ittabi\'uu man l<u>aa</u> yas-alukum ajran wahum muhtaduun\r\r\n');
INSERT INTO quranLatin VALUES (3727, '36:22', 36, 22, 'wam<u>aa</u> liya l<u>aa</u> a\'budulla<u>dz</u>ii fa<u>th</u>aranii wa-ilayhi turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (3728, '36:23', 36, 23, 'a-attakhi<u>dz</u>u min duunihi <u>aa</u>lihatan in yuridni rra<u>h</u>m<u>aa</u>nu bi<u>dh</u>urrin l<u>aa </u>tughni \'annii syaf<u>aa</u>\'atuhum syay-an wal<u>aa</u> yunqi<u>dz</u>uun\r\r\n');
